INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(1, 'Lần đầu ĐH FPT cấp học bổng tiến sĩ ', 'Lan-Dau-Dh-Fpt-Cap-Hoc-Bong-Tien-Si', 'Bên cạnh 400 suất học bổng Nguyễn Văn Đạo, ĐH FPT lần đầu tiên chọn ra 30 học sinh xuất sắc nhất để cấp học bổng toàn phần đào tạo từ cử nhân lên thẳng tiến sĩ, với tổng giá trị quỹ lên tới 5 triệu USD.', 'FPT-2.jpg', 22, '<p class=\"Normal\">Ng&agrave;y 18/1, ĐH FPT c&ocirc;ng bố chương tr&igrave;nh học bổng mang t&ecirc;n cố gi&aacute;o  sư Nguyễn Văn Đạo v&agrave; học bổng tiến sỹ cho m&ugrave;a tuyển sinh 2011.</p>\r\n<p class=\"Normal\">Trong số 400 học sinh xuất sắc nhất ở 100 trường THPT h&agrave;ng đầu  cả nước được cấp học bổng Nguyễn Văn Đạo, ĐH FPT sẽ chọn ra 30 em giỏi nhất để  cấp học bổng đ&agrave;o tạo bậc tiến sĩ chuy&ecirc;n ng&agrave;nh Khoa học m&aacute;y t&iacute;nh, với thời gian  đ&agrave;o tạo ở cả trong v&agrave; ngo&agrave;i nước.</p>\r\n<p class=\"Normal\">Để được cấp học bổng n&agrave;y, th&iacute; sinh phải c&oacute; th&agrave;nh t&iacute;ch học tập  xuất sắc, đam m&ecirc; ng&agrave;nh CNTT, v&agrave; đặc biệt đam m&ecirc; nghi&ecirc;n cứu khoa học. Ngo&agrave;i ra,  ĐH FPT tiếp tục d&agrave;nh nhiều suất học bổng to&agrave;n phần v&agrave; b&aacute;n phần cho c&aacute;c th&iacute; sinh  đạt kết quả cao trong kỳ thi tuyển sinh của trường, cũng như th&iacute; sinh đạt th&agrave;nh  t&iacute;ch trong c&aacute;c kỳ thi Olympic quốc gia v&agrave; quốc tế.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/58/D9/FPT.jpg\" alt=\"Ảnh: Ho&agrave;ng H&agrave;.\" width=\"480\" height=\"355\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">H&agrave;ng năm, sinh vi&ecirc;n của ĐH FPT đều c&oacute; nhiều đợt sang nước ngo&agrave;i học tập v&agrave; thực tập. Ảnh: <em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Theo Hiệu trưởng ĐH FPT L&ecirc; Trường T&ugrave;ng, \"đầu ra\" cho những tiến  sĩ CNTT tương lai n&agrave;y sẽ l&agrave; nguồn nh&acirc;n lực cho Viện nghi&ecirc;n cứu C&ocirc;ng nghệ FPT -  nơi tập trung nghi&ecirc;n cứu v&agrave; ứng dụng những dự &aacute;n, sản phẩm c&ocirc;ng nghệ mới c&oacute;  nhiều khả năng thương mại h&oacute;a v&agrave; &aacute;p dụng v&agrave;o thực tiễn. Đồng thời đ&acirc;y sẽ l&agrave;  nguồn cung cho đội ngũ giảng vi&ecirc;n của ĐH FPT, g&oacute;p phần v&agrave;o mục ti&ecirc;u trở th&agrave;nh  trường đại học đ&agrave;o tạo CNTT v&agrave; truyền th&ocirc;ng lớn nhất v&agrave; tốt nhất Việt Nam.</p>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; lần đầu ti&ecirc;n một trường đại học c&ocirc;ng bố cấp cả g&oacute;i học  bổng đ&agrave;o tạo từ bậc cử nh&acirc;n l&ecirc;n tiến sĩ cho sinh vi&ecirc;n đầu v&agrave;o.</p>\r\n<p class=\"Normal\">Trước đ&oacute;, năm 2010, ĐH FPT cấp gần 200 suất học bổng Nguyễn Văn  Đạo trị gi&aacute; 9.900 USD một suất cho c&aacute;c học sinh xuất sắc tr&ecirc;n cả nước.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nguyễn L&ecirc;</strong></p>', 1, 252, 0, 1, NULL, NULL),
(2, '300 tỷ đồng phát triển giáo dục mầm non ', '300-Ty-Dong-Phat-Trien-Giao-Duc-Mam-Non', 'Bộ Giáo dục và Đào tạo đang xây dựng chương trình, mục tiêu quốc gia về giáo dục giai đoạn 2011-2015, trong đó dự kiến chi 300 tỷ đồng để phát triển giáo dục mầm non năm 2011.\r\n', 'tre_em_set_sub.jpg', 22, '<div style=\"overflow: hidden;\">\r\n<h1 class=\"Title\">300 tỷ đồng ph&aacute;t triển gi&aacute;o dục mầm non</h1>\r\n<h2 class=\"Lead\">Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo đang x&acirc;y dựng chương tr&igrave;nh, mục ti&ecirc;u quốc  gia về gi&aacute;o dục giai đoạn 2011-2015, trong đ&oacute; dự kiến chi 300 tỷ đồng để ph&aacute;t  triển gi&aacute;o dục mầm non năm 2011.</h2>\r\n<p class=\"Normal\">Với số tiền n&agrave;y, Bộ Gi&aacute;o dục sẽ x&acirc;y dựng th&ecirc;m khoảng 16.400  ph&ograve;ng học phục vụ mục ti&ecirc;u phổ cập gi&aacute;o dục mầm non cho trẻ 5 tuổi, sau đ&oacute; từng  bước x&acirc;y dựng đủ số ph&ograve;ng học c&ograve;n thiếu cho gi&aacute;o dục mầm non.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/58/52/tre_em.jpg\" alt=\"\" width=\"495\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Năm 2011 dự kiến gi&aacute;o dục mầm non sẽ được chi 300 tỷ đồng. <em>Ảnh: Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&aacute;c thiết bị tối thiểu phục vụ c&ocirc;ng t&aacute;c giảng dạy cho c&aacute;c lớp  học mầm non sẽ được đầu tư; c&aacute;c lớp đ&agrave;o tạo, bồi dưỡng n&acirc;ng cao tr&igrave;nh độ cho  gi&aacute;o vi&ecirc;n mầm non sẽ được ch&uacute; trọng nhằm n&acirc;ng chuẩn đ&agrave;o tạo v&agrave; chuẩn nghề  nghiệp, đ&aacute;p ứng được y&ecirc;u cầu chương tr&igrave;nh gi&aacute;o dục mầm non mới.</p>\r\n<p class=\"Normal\">Hiện Bộ Gi&aacute;o dục cũng đ&atilde; phối hợp với Bộ Kế hoạch v&agrave; đầu tư, Bộ  T&agrave;i ch&iacute;nh x&acirc;y dựng dự &aacute;n gi&aacute;o dục mầm non cho trẻ em c&oacute; ho&agrave;n cảnh kh&oacute; khăn. Theo  đ&oacute;, trong giai đoạn 2011- 2015, khoảng 50 triệu USD sẽ được d&ugrave;ng để đầu tư cho  trẻ thiệt th&ograve;i. Đ&acirc;y l&agrave; kinh ph&iacute; được sử dụng từ nguồn vốn vay ưu đ&atilde;i của Ng&acirc;n  h&agrave;ng thế giới.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>\r\n</div>', 1, 2292, 0, 1, NULL, NULL),
(4, 'Nợ giáo viên tiền tỷ chi phí phổ cập giáo dục ', 'No-Giao-Vien-Tien-Ty-Chi-Phi-Pho-Cap-Giao-Duc', 'Ba năm qua, nhiều giáo viên ở Khánh Hòa bỏ công sức, kể cả tiền bạc để thực hiện phổ cập giáo dục cho học sinh trên địa bàn tỉnh, song đến nay vẫn chưa nhận được tiền chính quyền chi trả.\r\n', 'pho-cap-giao-duc-nho-2.jpg', 22, '<h1 class=\"Title\">Nợ gi&aacute;o vi&ecirc;n tiền tỷ chi ph&iacute; phổ cập gi&aacute;o dục</h1>\r\n<h2 class=\"Lead\">Ba năm qua, nhiều gi&aacute;o vi&ecirc;n ở Kh&aacute;nh H&ograve;a bỏ c&ocirc;ng sức, kể cả tiền  bạc để thực hiện phổ cập gi&aacute;o dục cho học sinh tr&ecirc;n địa b&agrave;n tỉnh, song đến nay  vẫn chưa nhận được tiền ch&iacute;nh quyền chi trả.</h2>\r\n<p class=\"Normal\">Thầy Nguyễn Ngọc Thạch, Ph&oacute; hiệu trưởng trường tiểu học Trịnh  Phong, thị trấn Di&ecirc;n Kh&aacute;nh (huyện Di&ecirc;n Kh&aacute;nh, Kh&aacute;nh H&ograve;a), cho biết: Nh&agrave; trường  vẫn c&ograve;n thiếu hơn 260 triệu đồng chi trả cho 66 lượt gi&aacute;o vi&ecirc;n trong 3 năm qua,  nhiều văn ph&ograve;ng phẩm d&ugrave;ng để giảng dạy cũng do gi&aacute;o vi&ecirc;n tự bỏ tiền t&uacute;i ra. Hiện  tại, nh&agrave; trường c&oacute; nhiều thầy c&ocirc; chưa được chi trả tiền đứng lớp phổ cập từ 10  đến 50 triệu đồng.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/pho-cap-giao-duc-to.jpg\" alt=\"\" width=\"490\" height=\"350\" /><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nhiều gi&aacute;o vi&ecirc;n Kh&aacute;nh H&ograve;a đang bị nợ tiền phổ cập gi&aacute;o dục. Ảnh  minh họa: <em>Nam Điền</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Chia sẻ kh&oacute; khăn tr&ecirc;n, c&ocirc; gi&aacute;o B&ugrave;i Thị Luyến, gi&aacute;o vi&ecirc;n dạy To&aacute;n  của trường tiểu học Trịnh Phong cho biết: Từ khi c&oacute; phong tr&agrave;o dạy phổ cập gi&aacute;o  dục c&ocirc; đ&atilde; tham gia giảng dạy c&aacute;c lớp phổ cập gi&aacute;o dục v&agrave;o ban đ&ecirc;m, mỗi tuần tới  5 tiết. Thời gian trước chế độ đứng lớp đều được thanh to&aacute;n r&otilde; r&agrave;ng. Từ năm 2007  đến nay chưa được thanh to&aacute;n tiền đứng lớp, c&ocirc; bị nợ đến hơn 20 triệu đồng.</p>\r\n<p class=\"Normal\">C&ocirc; Luyến ngậm ng&ugrave;i: &ldquo;Việc tham gia c&ocirc;ng t&aacute;c phổ cập gi&aacute;o dục l&agrave;  một ti&ecirc;u ch&iacute; b&igrave;nh x&eacute;t thi đua h&agrave;ng năm n&ecirc;n phải cố gắng. Bằng l&ograve;ng nhiệt t&igrave;nh,  t&ocirc;i cũng cố gắng bỏ c&ocirc;ng sức, xăng xe mấy năm liền đi dạy. Tuy nhi&ecirc;n, việc chậm  thanh to&aacute;n l&agrave; rất thiệt th&ograve;i, hiện gia đ&igrave;nh t&ocirc;i c&ograve;n một người mẹ v&agrave; nu&ocirc;i hai con  ăn học n&ecirc;n kinh tế kh&aacute; eo hẹp&rdquo;.</p>\r\n<p class=\"Normal\">&Ocirc;ng B&ugrave;i Hữu H&oacute;a, Trưởng ph&ograve;ng gi&aacute;o dục huyện miền n&uacute;i Kh&aacute;nh  Vĩnh, giải th&iacute;ch: \"Kh&aacute;nh Vĩnh l&agrave; địa b&agrave;n miền n&uacute;i, do đ&oacute; việc vận động con em  đồng b&agrave;o d&acirc;n tộc &iacute;t người đi học gặp nhiều kh&oacute; khăn, lại thiếu kinh ph&iacute; hỗ trợ  n&ecirc;n c&aacute;c hoạt động n&agrave;y li&ecirc;n tục nhiều năm đ&atilde; &iacute;t nhiều g&acirc;y th&ecirc;m kh&oacute; khăn, ức chế  cho c&ocirc;ng t&aacute;c giảng dạy của gi&aacute;o vi&ecirc;n\". Hiện tại, huyện Kh&aacute;nh Vĩnh đang thiếu của  gi&aacute;o vi&ecirc;n gần 400 triệu đồng phổ cập gi&aacute;o dục.</p>\r\n<p class=\"Normal\">Trao đổi về vấn đề tr&ecirc;n, &ocirc;ng Lưu Quốc Thanh, Gi&aacute;m đốc Sở Gi&aacute;o  dục v&agrave; Đ&agrave;o tạo tỉnh Kh&aacute;nh H&ograve;a x&aacute;c nhận, ng&agrave;nh gi&aacute;o dục Kh&aacute;nh H&ograve;a đang cần bổ  sung gần 7,5 tỷ đồng tiền phục vụ c&ocirc;ng t&aacute;c phổ cập gi&aacute;o dục. Đ&acirc;y l&agrave; số tiền c&ograve;n  thiếu của gi&aacute;o vi&ecirc;n trong 3 năm từ 2008 đến 2010 tr&ecirc;n địa b&agrave;n to&agrave;n tỉnh. Sở đ&atilde;  c&oacute; c&ocirc;ng văn gửi Sở T&agrave;i ch&iacute;nh đề nghị bổ sung ng&acirc;n s&aacute;ch để hỗ trợ cho địa phương  chi trả khoản tiền tr&ecirc;n cho c&aacute;c gi&aacute;o vi&ecirc;n.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nam Điền</strong></p>', 1, 34, 0, 1, NULL, NULL),
(5, 'Đón và chăm sóc trẻ sau giờ tan trường qua dịch vụ ', 'Don-Va-Cham-Soc-Tre-Sau-Gio-Tan-Truong-Qua-Dich-Vu', 'Các bé sẽ được chăm sóc bữa ăn, tắm rửa sạch sẽ, vui chơi và học tập cùng cô giáo theo các nội dung trong sổ báo bài, mở rộng hoặc đào sâu kiến thức theo yêu cầu của phụ huynh.\r\n \r\n', 'be-2.jpg', 22, '<h1 class=\"Title\">Đ&oacute;n v&agrave; chăm s&oacute;c trẻ sau giờ tan trường qua dịch  vụ</h1>\r\n<h2 class=\"Lead\">C&aacute;c b&eacute; sẽ được chăm s&oacute;c bữa ăn, tắm rửa sạch sẽ, vui  chơi v&agrave; học tập c&ugrave;ng c&ocirc; gi&aacute;o theo c&aacute;c nội dung trong sổ b&aacute;o b&agrave;i, mở rộng hoặc  đ&agrave;o s&acirc;u kiến thức theo y&ecirc;u cầu của phụ huynh.</h2>\r\n<p class=\"Normal\">Với dịch vụ &ldquo;Đưa - đ&oacute;n v&agrave; chăm s&oacute;c b&eacute; sau giờ tan  trường&rdquo;, Happy Back Home thay cha mẹ đ&oacute;n trẻ tại trường bằng &ocirc;t&ocirc; của trung t&acirc;m,  nắm bắt th&ocirc;ng tin học tập, th&ocirc;ng b&aacute;o mới của trường để cung cấp lại đầy đủ cho  phụ huynh.</p>\r\n<p class=\"Normal\">Lịch sinh hoạt của b&eacute; tại trung t&acirc;m được x&acirc;y dựng hợp  l&yacute; v&agrave; phong ph&uacute; dựa theo t&acirc;m sinh l&yacute; mỗi lứa tuổi. Mọi chương tr&igrave;nh đều được  thống nhất giữa gia đ&igrave;nh v&agrave; trung t&acirc;m, kh&ocirc;ng l&agrave;m x&aacute;o trộn cuộc sống thường nhật  ở nh&agrave; của trẻ. C&aacute;c b&eacute; được vui chơi sau một ng&agrave;y học, lấy lại năng lượng từ  những bữa ăn chất lượng, ph&ugrave; hợp sở th&iacute;ch. Ngo&agrave;i ra, trẻ được gi&aacute;o vi&ecirc;n k&egrave;m học  với phương ph&aacute;p sư phạm tiến bộ gi&uacute;p b&eacute; dễ hiểu v&agrave; nhớ b&agrave;i l&acirc;u.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/_DSC3170.jpg\" alt=\"\" width=\"448\" height=\"298\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">B&ecirc;n cạnh đ&oacute;, dịch vụ &ldquo;Du lịch sinh th&aacute;i - Từ trường  học đến cuộc sống&rdquo; sẽ gi&uacute;p trẻ củng cố kiến thức l&yacute; thuyết bằng những chuyến du  lịch sinh th&aacute;i được đ&uacute;c kết từ ch&iacute;nh c&aacute;c b&agrave;i học tại trường. Th&ocirc;ng qua buổi d&atilde;  ngoại, tham quan những địa điểm du lịch, mỗi chương tr&igrave;nh sẽ gi&uacute;p b&eacute; được tận  mắt v&agrave; chạm v&agrave;o điều đ&atilde; học. Đồng thời, gi&aacute;o vi&ecirc;n chuy&ecirc;n m&ocirc;n sẽ gi&uacute;p c&aacute;c con  hiểu r&otilde; v&agrave; s&acirc;u sắc hơn c&aacute;c điều đ&atilde; thấy.</p>\r\n<p class=\"Normal\">Với &ldquo;R&egrave;n luyện kỹ năng sống - B&eacute; với Cộng đồng&rdquo;,  Happy Back Home chủ trương kh&ocirc;ng d&ugrave;ng gi&aacute;o tr&igrave;nh phức tạp m&agrave; chỉ tổng hợp c&aacute;c  b&agrave;i học đạo đức trong chương tr&igrave;nh học của trẻ. Bằng những buổi hoạt động ngoại  kh&oacute;a c&ugrave;ng bạn b&egrave;, trẻ dễ d&agrave;ng tiếp nhận v&agrave; hiểu những gi&aacute; trị rất đời thường. Từ  đ&oacute;, b&eacute; tự x&acirc;y dựng v&agrave; n&acirc;ng dần c&aacute;c kỹ năng sống cơ bản nhưng quan trọng. Điều  n&agrave;y g&oacute;p phần h&igrave;nh th&agrave;nh nh&acirc;n c&aacute;ch v&agrave; tạo tiền đề ph&aacute;t triển tương lai cho b&eacute;.  Ngo&agrave;i ra, trung t&acirc;m c&ograve;n cung cấp dịch vụ chăm s&oacute;c b&eacute; ng&agrave;y lễ, ng&agrave;y nghỉ đột xuất  v&agrave; c&aacute;c chương tr&igrave;nh &ldquo;Ng&agrave;y thứ bảy của b&eacute;\" phong ph&uacute;.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"10\" height=\"22\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/P1000817.jpg\" alt=\"\" width=\"448\" height=\"336\" /><br /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Nh&acirc;n Tết Nguy&ecirc;n đ&aacute;n 2011, từ 24 đến 31/1, Happy Back  Home cung cấp dịch vụ chăm s&oacute;c b&eacute; ng&agrave;y cận Tết với mức gi&aacute; ưu đ&atilde;i 15%. C&aacute;c b&eacute; sẽ  được tham gia v&agrave;o c&aacute;c chương tr&igrave;nh sinh hoạt với chủ đề về Tết cổ truyền bằng  một chuỗi c&aacute;c c&acirc;u chuyện, tr&ograve; chơi li&ecirc;n quan đến Tết. Ngo&agrave;i ra, chương tr&igrave;nh &ldquo;Du  lịch sinh th&aacute;i - Từ trường học đến cuộc sống&rdquo; số đầu ti&ecirc;n nh&acirc;n dịp Tết Nguy&ecirc;n  Đ&aacute;n 2011 với chủ đề &ldquo;Về nguồn&rdquo; sẽ đưa b&eacute; đến thăm l&agrave;ng nghề truyền thống Việt  Nam.</p>\r\n<p class=\"Normal\">Dịch vụ dựa tr&ecirc;n m&ocirc; h&igrave;nh &ldquo;After school care&rdquo; đang thịnh h&agrave;nh tại  c&aacute;c quốc gia ph&aacute;t triển. Truy cập <a class=\"Normal\" href=\"http://www.happybackhome.com/\">http://www.happybackhome.com</a> hoặc li&ecirc;n  hệ 08-6296 6792 để c&oacute; th&ecirc;m th&ocirc;ng tin.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn: <em>Happy Back Home</em>)</p>', 1, 2279, 0, 1, NULL, NULL),
(6, '7 học sinh rơi từ tầng hai xuống đất vì gãy lan can ', '7-Hoc-Sinh-Roi-Tu-Tang-Hai-Xuong-Dat-Vi-Gay-Lan-Can', 'Đang giờ ra chơi, bất ngờ toàn bộ lan can tầng hai của Trường THCS thị trấn Chợ Rã (Bắc Kạn) gãy đổ ra ngoài, kéo theo 7 học sinh lớp 6A rơi xuống đất. \r\n', 'tai_nan_set_top.gif', 22, '<p>Đang giờ ra chơi, bất ngờ to&agrave;n bộ lan can tầng hai của Trường THCS thị trấn Chợ  R&atilde; (Bắc Kạn) g&atilde;y đổ ra ngo&agrave;i, k&eacute;o theo 7 học sinh lớp 6A rơi xuống đất.</p>\r\n<p class=\"Normal\">Theo l&atilde;nh đạo trường, l&uacute;c đ&oacute; l&agrave; giờ ra chơi chiều 10/1, 7 học  sinh đang đứng dựa v&agrave;o lan can. Sau tai nạn c&aacute;c em đ&atilde; được đưa v&agrave;o Bệnh viện  huyện Ba Bể cấp cứu. Tuy nhi&ecirc;n c&oacute; 4 em bị thương nặng gồm Nguyễn T&ugrave;ng L&acirc;m, N&ocirc;ng  Thị Ngọc, N&ocirc;ng Thị Thảo Trang v&agrave; Dương Thị H&agrave; Vy phải chuyển l&ecirc;n Bệnh vi&ecirc;n Đa  khoa tỉnh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/tai_nan.gif\" alt=\"\" width=\"400\" height=\"312\" /><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ch&aacute;u Nguyễn T&ugrave;ng L&acirc;m, 11 tuổi bị gẫy 02 xương cẳng tay phải, đa  vết thương răng h&agrave;m mặt đ&atilde; được b&oacute; bột v&agrave; chuyển chắm s&oacute;c  ri&ecirc;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">B&aacute;c sĩ L&ocirc; Văn Tường, Trưởng khoa Ngoại chấn thương của bệnh viện  cho biết, em Nguyễn T&ugrave;ng L&acirc;m bị g&atilde;y hai xương cẳng tay phải, đa vết thương răng  h&agrave;m mặt đ&atilde; được b&oacute; bột v&agrave; chăm s&oacute;c ri&ecirc;ng.</p>\r\n<p class=\"Normal\">Theo chuẩn đo&aacute;n ban đầu, em N&ocirc;ng Thị Ngọc bị gẫy k&iacute;n 2 xương  c&aacute;nh tay phải; N&ocirc;ng Thị Thảo Trang bị chấn thương cột sống, l&uacute;n L1, kh&ocirc;ng liệt;  Dương Thị H&agrave; Vy bị đa vết thương răng h&agrave;m mặt v&agrave; ch&acirc;n phải.</p>\r\n<p class=\"Normal\">Sau vụ tai nạn, l&atilde;nh đạo tỉnh Bắc Kạn đ&atilde; trực tiếp đến thăm c&aacute;c  học sinh gặp nạn. Chủ tịch UBND tỉnh đ&atilde; chỉ đạo c&aacute;c ng&agrave;nh chức năng l&agrave;m r&otilde;  nguy&ecirc;n nh&acirc;n vụ việc, nhất l&agrave; chất lượng c&ocirc;ng tr&igrave;nh trường THCS thị trấn Chợ R&atilde;  để xử l&yacute;.<br /><br />Trường THCS thị trấn Chợ R&atilde; do C&ocirc;ng ty Ph&uacute; Đạt (Th&aacute;i Nguy&ecirc;n)  thi c&ocirc;ng từ năm 2006 đến nay vẫn chưa ho&agrave;n th&agrave;nh. C&aacute;c lớp học đ&atilde; đưa v&agrave;o sử dụng  trước đ&oacute; cũng chưa c&oacute; lan can cầu thang.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Theo <em>B&aacute;o Bắc Kạn</em>)</p>', 1, 1081, 0, 1, NULL, NULL),
(7, 'Giáo viên TP HCM được thưởng tết tối thiểu 700.000 đồng ', 'Giao-Vien-Tp-Hcm-Duoc-Thuong-Tet-Toi-Thieu-700.000-Dong', 'Sở GD&ĐT TP HCM vừa có thông báo về việc UBND thành phố chấp thuận đề nghị hỗ trợ mức quà tết cho cán bộ công chức trong ngành tối thiểu là 700.000 đồng. Mức thưởng này cao hơn năm ngoái 100.000 đồng.\r\n', 'thuong-tet-3.jpg', 22, '<h1 class=\"Title\">Gi&aacute;o vi&ecirc;n TP HCM được thưởng tết tối thiểu 700.000 đồng</h1>\r\n<h2 class=\"Lead\">Sở GD&amp;ĐT TP HCM vừa c&oacute; th&ocirc;ng b&aacute;o về việc UBND th&agrave;nh phố chấp  thuận đề nghị hỗ trợ mức qu&agrave; tết cho c&aacute;n bộ c&ocirc;ng chức trong ng&agrave;nh tối thiểu l&agrave;  700.000 đồng. Mức thưởng n&agrave;y cao hơn năm ngo&aacute;i 100.000 đồng.</h2>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, đối với những đơn vị trường học tự chủ về t&agrave;i ch&iacute;nh  tự c&acirc;n đối thu chi th&igrave; mức thưởng d&agrave;nh cho gi&aacute;o vi&ecirc;n, c&aacute;n bộ, nh&acirc;n vi&ecirc;n c&oacute; thể  cao hơn.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/thuong-tet-1.jpg\" alt=\"\" width=\"480\" height=\"361\" /><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Gi&aacute;o vi&ecirc;n hướng dẫn học sinh trong kỳ thi tốt nghiệp 2010. Ảnh:  <em>Hải Duy&ecirc;n</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Thực tế ở một số trường học v&agrave; cấp học mức thưởng kh&ocirc;ng giống  nhau t&ugrave;y v&agrave;o nguồn thu của nh&agrave; trường. Ở một số trường PTTH, THCS, tiểu học gi&aacute;o  vi&ecirc;n cho biết, mức thưởng năm nay cũng tương tự năm ngo&aacute;i c&oacute; thể từ 500 đến v&agrave;i  triệu đồng t&ugrave;y v&agrave;o th&acirc;m ni&ecirc;n hoặc sự đ&aacute;nh gi&aacute; của nh&agrave; trường.</p>\r\n<p class=\"Normal\">C&ograve;n đối với những gi&aacute;o vi&ecirc;n mầm non ở c&aacute;c nh&oacute;m trường tư th&igrave; vẫn  chưa c&oacute; th&ocirc;ng tin g&igrave; về việc thưởng Tết. \"Năm n&agrave;o cũng vậy cả th&ocirc;i, gọi l&agrave;  thưởng cho đỡ tủi th&acirc;n chứ 2-3 trăm ng&agrave;n th&igrave; kh&ocirc;ng đủ đi chợ một bữa\", một nữ  gi&aacute;o vi&ecirc;n gi&atilde;i b&agrave;y.</p>\r\n<p class=\"Normal\">\"Những trường c&oacute; nguồn thu ri&ecirc;ng th&igrave; c&oacute; thể thưởng cho c&aacute;n bộ  gi&aacute;o vi&ecirc;n v&agrave;i triệu hoặc nhiều hơn, c&ograve;n những trường kh&ocirc;ng c&oacute; nguồn thu, học  sinh &iacute;t th&igrave; cũng đ&agrave;nh phải chịu chứ kh&ocirc;ng c&oacute; th&igrave; lấy đ&acirc;u ra m&agrave; thưởng. Việc  lương thưởng c&aacute;c c&aacute;n bộ gi&aacute;o vi&ecirc;n c&oacute; thể thỏa thuận với nh&agrave; trường theo thỏa ước  hay hợp đồng lao động\", &ocirc;ng Nguyễn Hữu H&ugrave;ng Chủ tịch c&ocirc;ng đo&agrave;n Sở GD&amp;ĐT TP  HCM n&oacute;i.</p>\r\n<p class=\"Normal\">&Ocirc;ng H&ugrave;ng cho biết th&ecirc;m, để hỗ trợ những c&aacute;n bộ gi&aacute;o vi&ecirc;n c&oacute; ho&agrave;n  cảnh kh&oacute; khăn ở c&aacute;c quận huyện ngoại th&agrave;nh ăn Tết, Sở đ&atilde; ph&aacute;t động phong tr&agrave;o  \"nội th&agrave;nh hỗ trợ ngoại th&agrave;nh\" v&agrave; sẽ đi thăm tặng qu&agrave; cho những gi&aacute;o vi&ecirc;n ở v&ugrave;ng  ven th&agrave;nh phố.</p>\r\n<p class=\"Normal\">Năm 2010 nhiều trường ở TP HCM gi&aacute;o vi&ecirc;n c&oacute; mức thưởng Tết l&ecirc;n  đến h&agrave;ng chục triệu đồng.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n</strong></p>', 1, 2811, 0, 1, NULL, NULL),
(8, 'Mức sinh hoạt phí tối đa cho lưu học sinh là 1.200 USD ', 'Muc-Sinh-Hoat-Phi-Toi-Da-Cho-Luu-Hoc-Sinh-La-1.200-Usd', 'Đối với lưu học sinh tại Ba Lan, Bungary, Nga..., mức sinh hoạt phí sẽ tăng từ 400 USD lên 480 USD; tại Australia, New Zealand tăng từ 860 USD lên 1.032 USD và tại Mỹ, Canada, Anh, Nhật Bản tăng từ 1.000 lên 1.200 USD một người một tháng... \r\n', 'du_hoc_sinh_set_sub.jpg', 22, '<h1 class=\"Title\">Mức sinh hoạt ph&iacute; tối đa cho lưu học sinh l&agrave; 1.200 USD</h1>\r\n<h2 class=\"Lead\">Đối với lưu học sinh tại Ba Lan, Bungary, Nga..., mức sinh hoạt  ph&iacute; sẽ tăng từ 400 USD l&ecirc;n 480 USD; tại Australia, New Zealand tăng từ 860 USD  l&ecirc;n 1.032 USD v&agrave; tại Mỹ, Canada, Anh, Nhật Bản tăng từ 1.000 l&ecirc;n 1.200 USD một  người một th&aacute;ng...</h2>\r\n<p class=\"Normal\">Li&ecirc;n Bộ T&agrave;i ch&iacute;nh, Bộ Ngoại giao, Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo vừa ban  h&agrave;nh th&ocirc;ng tư li&ecirc;n tịch hướng dẫn chế độ, cấp ph&aacute;t v&agrave; quản l&yacute; kinh ph&iacute; đ&agrave;o tạo  lưu học sinh Việt Nam ở nước ngo&agrave;i bằng ng&acirc;n s&aacute;ch nh&agrave; nước.</p>\r\n<p class=\"Normal\">Theo đ&oacute;, từ th&aacute;ng 2 mức sinh hoạt ph&iacute; cho học sinh du học bằng  ng&acirc;n s&aacute;ch nh&agrave; nước tại hơn 30 nước v&agrave; v&ugrave;ng l&atilde;nh thổ đều tăng đ&aacute;ng kể. Tăng cao  nhất l&agrave; tại Australia, New Zealand từ 860 USD l&ecirc;n 1.032 USD một người một  th&aacute;ng.</p>\r\n<p class=\"Normal\">Văn bản n&agrave;y cũng bổ sung lưu học sinh ở Đ&agrave;i Loan, Hong Kong,  M&ocirc;ng Cổ v&agrave;o danh mục c&aacute;c nước v&agrave; v&ugrave;ng l&atilde;nh thổ được hỗ trợ bảo hiểm y tế với số  tiền tương ứng l&agrave; 300 USD một người mỗi năm (ri&ecirc;ng ở M&ocirc;ng Cổ l&agrave; 150 USD).</p>\r\n<p class=\"Normal\">\"Ri&ecirc;ng tiền tạm ứng chi ph&iacute; đi đường, ph&iacute; l&agrave;m hộ chiếu, visa v&agrave;  sinh hoạt ph&iacute; (kh&ocirc;ng qu&aacute; 3 th&aacute;ng tiền sinh hoạt ph&iacute;) cho lưu học sinh trước khi  đi học thực hiện theo phương thức cấp bằng tiền mặt\", th&ocirc;ng tư n&ecirc;u r&otilde;.</p>\r\n<p class=\"Normal\">Theo Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo, khoảng 60.000 học sinh  Việt Nam đang theo học tại c&aacute;c cơ sở đ&agrave;o tạo nước ngo&agrave;i. Trong đ&oacute; hơn 4.000  người học bằng ng&acirc;n s&aacute;ch nh&agrave; nước theo Đề &aacute;n 322, v&agrave;i ngh&igrave;n người theo học bằng  c&aacute;c học bổng kh&aacute;c, số c&ograve;n lại đi bằng con đường tự t&uacute;c.</p>\r\n<p><strong>\r\n<p class=\"Normal\">Chi tiết sinh hoạt ph&iacute; ở c&aacute;c nước theo th&ocirc;ng tư  mới:</p>\r\n</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\" width=\"486\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Normal\" rowspan=\"2\" width=\"237\" align=\"left\">T&ecirc;n nước v&agrave; v&ugrave;ng l&atilde;nh thổ</td>\r\n<td class=\"BreakLine\" colspan=\"2\" width=\"159\"><br /></td>\r\n<td class=\"Normal\" rowspan=\"2\" width=\"90\" align=\"left\">Mức cấp b&ugrave; SHP diện Hiệp  định (USD/ LHS/ th&aacute;ng)</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"83\" align=\"left\">Bằng đồng đ&ocirc;la Mỹ</td>\r\n<td class=\"Normal\" width=\"76\" align=\"left\">Bằng đồng EURO</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Ấn Độ</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">420</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">300-420</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Trung Quốc</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">420</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">293</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Đ&agrave;i Loan (Trung Quốc)</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">420</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"BreakLine\" width=\"90\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Campuchia, L&agrave;o</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">204</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">84</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">M&ocirc;ng Cổ</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">204</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">144</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">H&agrave;n Quốc, Singapore</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">600</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"BreakLine\" width=\"90\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Hong Kong</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">600</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"BreakLine\" width=\"90\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Th&aacute;i Lan, Philippines, Malaysia, Indonesia</td>\r\n<td width=\"83\">\r\n<p class=\"Normal\">360</p>\r\n</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"BreakLine\" width=\"90\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Ba Lan</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">480</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">480</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Bungary</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">480</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">404</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Hungary</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">480</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">143-403</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Czech</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">480</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">84</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Slovakia</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">480</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">112</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Rumany</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">480</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">420</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Ucraina, Belarutsia</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">480</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">456</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Nga</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">480</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">420</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Cuba</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">204</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">198</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">C&aacute;c nước T&acirc;y Bắc &Acirc;u</td>\r\n<td class=\"BreakLine\" width=\"83\"><br /></td>\r\n<td class=\"Normal\" width=\"76\" align=\"middle\">888</td>\r\n<td class=\"BreakLine\" width=\"90\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Mỹ, Canada, Anh, Nhật Bản</td>\r\n<td width=\"83\">\r\n<p class=\"Normal\">1.200</p>\r\n</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"BreakLine\" width=\"90\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Australia, New Zealand</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">1.032</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"BreakLine\" width=\"90\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Ai Cập</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">540</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">480</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Maroc, Mozambique</td>\r\n<td class=\"BreakLine\" width=\"83\"><br /></td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">250</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong> </strong></p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 1, 2115, 0, 1, NULL, NULL),
(9, 'Học sinh Hà Nội được nghỉ 11 ngày Tết ', 'Hoc-Sinh-Ha-Noi-Duoc-Nghi-11-Ngay-Tet', 'UBND thành phố Hà Nội vừa đồng ý với đề xuất của Sở Giáo dục và Đào tạo về việc cho học sinh nghỉ tết Tết Nguyên đán Tân Mão 11 ngày. \r\n', 't2.jpg', 22, '<h1 class=\"Title\">Học sinh H&agrave; Nội được nghỉ 11 ng&agrave;y Tết</h1>\r\n<h2 class=\"Lead\">UBND th&agrave;nh phố H&agrave; Nội vừa đồng &yacute; với đề xuất của Sở Gi&aacute;o dục v&agrave;  Đ&agrave;o tạo về việc cho học sinh nghỉ tết Tết Nguy&ecirc;n đ&aacute;n T&acirc;n M&atilde;o 11 ng&agrave;y.</h2>\r\n<p class=\"Normal\">Cụ thể, c&aacute;n bộ, gi&aacute;o vi&ecirc;n, học sinh c&aacute;c trường mầm non, tiểu  học, THCS, THPT, bổ t&uacute;c văn h&oacute;a v&agrave; trung t&acirc;m gi&aacute;o dục kỹ thuật tổng hợp, trung  t&acirc;m gi&aacute;o dục thường xuy&ecirc;n được nghỉ từ ng&agrave;y 28/1 (ng&agrave;y 25 th&aacute;ng chạp) đến hết  ng&agrave;y 7/2 (m&ugrave;ng 5 th&aacute;ng gi&ecirc;ng năm T&acirc;n M&atilde;o).<br /><br />C&aacute;n bộ, gi&aacute;o vi&ecirc;n, học sinh  c&aacute;c trường trung cấp chuy&ecirc;n nghiệp được nghỉ 17 ng&agrave;y, từ 22/1 (ng&agrave;y 19 th&aacute;ng  chạp) đến hết ng&agrave;y 7/2 (m&ugrave;ng 5 th&aacute;ng gi&ecirc;ng).</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/hs-gialanh10.jpg\" alt=\"\" width=\"495\" height=\"330\" /><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Học sinh H&agrave; Nội đang chịu đợt r&eacute;t đậm. Ảnh: <em>L&ecirc;  Hiếu.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&ocirc;ng chức, vi&ecirc;n chức trong c&aacute;c cơ quan h&agrave;nh ch&iacute;nh sự nghiệp như  Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo v&agrave; c&aacute;c ph&ograve;ng gi&aacute;o dục sẽ đi l&agrave;m v&agrave;o thứ bảy, chủ nhật  ng&agrave;y 29-30/1, tức ng&agrave;y 26, 27 th&aacute;ng chạp v&agrave; nghỉ Tết bắt đầu từ 31/1 đến 7/2 (từ  28 th&aacute;ng chạp đến hết m&ugrave;ng 5 th&aacute;ng gi&ecirc;ng năm T&acirc;n M&atilde;o).</p>\r\n<p class=\"Normal\">UBND H&agrave; Nội y&ecirc;u cầu Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo th&ocirc;ng b&aacute;o cụ thể về  thời gian nghỉ Tết để nh&agrave; trường, phụ huynh, học sinh thực hiện.</p>\r\n<p class=\"Normal\">Trước đ&oacute;, Thủ tướng cũng đ&atilde; quyết định cho c&ocirc;ng chức, vi&ecirc;n chức  cả nước được nghỉ Tết nguy&ecirc;n đ&aacute;n trong 8 ng&agrave;y, đi l&agrave;m b&ugrave; v&agrave;o thứ bảy, chủ nhật  ng&agrave;y 29-30/1.</p>\r\n<p class=\"Normal\">H&agrave; Nội v&agrave; c&aacute;c tỉnh ph&iacute;a bắc đang g&aacute;nh chịu đợt r&eacute;t đậm, r&eacute;t hại  d&agrave;i, dự kiến đến s&aacute;t tết Nguy&ecirc;n đ&aacute;n.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đo&agrave;n Loan</strong></p>', 1, 2656, 0, 1, NULL, NULL),
(10, 'Hàng trăm nghìn học sinh nghỉ học vì giá rét ', 'Hang-Tram-Nghin-Hoc-Sinh-Nghi-Hoc-Vi-Gia-Ret', 'Sớm nay, các trường tiểu học ở Hà Nội đều trưng biển thông báo nghỉ học do nhiệt độ ở mức 8 độ C. Một vài phụ huynh không theo dõi dự báo thời tiết vẫn đưa con đến trường và ngậm ngùi quay xe ra về.\r\n', 'phu_huynh_xem_lich_nghi_hoc_set_sub.jpg', 22, '<h1 class=\"Title\">H&agrave;ng trăm ngh&igrave;n học sinh nghỉ học v&igrave; gi&aacute; r&eacute;t</h1>\r\n<h2 class=\"Lead\">Sớm nay, c&aacute;c trường tiểu học ở H&agrave; Nội đều trưng biển th&ocirc;ng b&aacute;o  nghỉ học do nhiệt độ ở mức 8 độ C. Một v&agrave;i phụ huynh kh&ocirc;ng theo d&otilde;i dự b&aacute;o thời  tiết vẫn đưa con đến trường v&agrave; ngậm ng&ugrave;i quay xe ra về.</h2>\r\n<p class=\"Normal\">Chị Cao Thị Hồng, Hiệu trưởng Trường tiểu học Mai Dịch (Cầu  Giấy) cho biết, từ s&aacute;ng sớm, chị v&agrave; c&aacute;c c&ocirc; li&ecirc;n tục nhận được điện thoại của phụ  huynh hỏi về việc nghỉ học của con em. Bản th&acirc;n chị đ&atilde; chỉ đạo c&aacute;c c&ocirc; gi&aacute;o chủ  nhiệm chủ động nhắn tin b&aacute;o nghỉ cho phụ huynh.</p>\r\n<p class=\"Normal\">\"Chỉ c&oacute; v&agrave;i ch&aacute;u kh&ocirc;ng theo d&otilde;i được dự b&aacute;o thời tiết v&agrave; chưa  kịp nhận tin nhắn n&ecirc;n vẫn đến lớp. Sau khi nghe th&ocirc;ng b&aacute;o, c&aacute;c ch&aacute;u được phụ  huynh đưa về nh&agrave;. Đến khoảng 8h kh&ocirc;ng c&ograve;n học sinh đến trường nữa\", chị Hồng cho  hay.</p>\r\n<p class=\"Normal\">Hiệu trưởng Hồng cũng chia sẻ, để đối ph&oacute; với thời tiết diễn  biến phức tạp, nh&agrave; trường đ&atilde; l&agrave;m sổ li&ecirc;n lạc điện tử, c&oacute; bất kỳ thay đổi n&agrave;o đều  th&ocirc;ng b&aacute;o nhanh ch&oacute;ng đến từng phụ huynh. Trường cũng qu&aacute;n triệt văn bản chỉ đạo  của Sở đến học sinh v&agrave; phụ huynh, nếu nhiệt độ dưới 10 độ C (theo đ&agrave;i HTV) th&igrave;  phụ huynh chủ động cho con nghỉ học.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/thong_bao_nghi_hoc.jpg\" alt=\"\" width=\"350\" height=\"480\" /><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">S&aacute;ng nay, hầu hết c&aacute;c trường tiểu học đều th&ocirc;ng b&aacute;o cho học sinh  nghỉ học v&igrave; qu&aacute; lạnh. <em>Ảnh: Ho&agrave;ng  Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&oacute; tr&ecirc;n 3.000 học sinh nhưng s&aacute;ng nay Trường tiểu học Nam Th&agrave;nh  C&ocirc;ng (Đống Đa) chỉ c&oacute; v&agrave;i em đến lớp. Số n&agrave;y được c&aacute;c c&ocirc; giải th&iacute;ch l&agrave; \"trời qu&aacute;  lạnh n&ecirc;n được nghỉ học\".</p>\r\n<p class=\"Normal\">Hiệu trưởng Phan Kim Anh cho biết, từ đầu m&ugrave;a đ&ocirc;ng, trường đ&atilde; c&oacute;  kế hoạch th&ocirc;ng b&aacute;o nghỉ học nếu nhiệt độ xuống dưới mức cho ph&eacute;p. C&aacute;c c&ocirc; chủ  nhiệm đ&atilde; trao đổi với phụ huynh, chủ động theo d&otilde;i dự b&aacute;o thời tiết tr&ecirc;n VTV1 để  l&agrave;m căn cứ.</p>\r\n<p class=\"Normal\">\"Ph&ograve;ng Gi&aacute;o dục quy định theo d&otilde;i chương tr&igrave;nh thời tiết tr&ecirc;n  VTV1 bởi chỉ c&oacute; một mốc nhiệt độ duy nhất để nh&agrave; trường v&agrave; phụ huynh c&ugrave;ng thống  nhất. Ở c&aacute;c đ&agrave;i kh&aacute;c nhiệt độ dự b&aacute;o c&oacute; sự ch&ecirc;nh lệch từ thấp nhất đến cao nhất  n&ecirc;n kh&ocirc;ng biết lựa chọn mốc n&agrave;o l&agrave;m chuẩn\", chị Kim Anh n&oacute;i.</p>\r\n<p class=\"Normal\">Theo chị Kim Anh, khi xem dự b&aacute;o thời tiết tr&ecirc;n VTV1 v&agrave;o l&uacute;c hơn  6h, nh&agrave; trường v&agrave; c&aacute;c c&ocirc; c&ugrave;ng thống nhất để c&oacute; quyết định, sau đ&oacute; th&ocirc;ng b&aacute;o đến  phụ huynh trước 6h30. Như vậy sẽ kh&ocirc;ng s&aacute;t thời gian c&aacute;c ch&aacute;u đi học. \"Từ đầu  m&ugrave;a đ&ocirc;ng đến nay, h&ocirc;m nay l&agrave; ng&agrave;y đầu ti&ecirc;n trường cho học sinh nghỉ. D&ugrave; vậy, c&aacute;c  c&ocirc; vẫn đến trường l&agrave;m điểm, l&agrave;m sổ s&aacute;ch chuẩn bị kết th&uacute;c học kỳ\", chị Kim Anh  cho hay.</p>\r\n<p class=\"Normal\">Chiều h&ocirc;m qua, học sinh Trường tiểu học Phương Mai (Đống Đa) đ&atilde;  nhận được th&ocirc;ng b&aacute;o kết th&uacute;c buổi học chiều sớm 30 ph&uacute;t để tr&aacute;nh r&eacute;t. Hiệu  trưởng Nguyễn Thu C&uacute;c cho biết đ&acirc;y l&agrave; phương &aacute;n của l&atilde;nh đạo trường nhằm hạn chế  việc c&aacute;c em phải chịu c&aacute;i lạnh v&agrave;o cuối ng&agrave;y. S&aacute;ng nay, khi nhiệt độ 8 độ C,  trường đ&atilde; th&ocirc;ng b&aacute;o đến phụ huynh v&agrave; ph&acirc;n c&ocirc;ng bảo vệ viết bảng th&ocirc;ng b&aacute;o nghỉ  đặt ở cổng trường từ s&aacute;ng sớm. C&oacute; v&agrave;i học sinh vẫn đến trường v&agrave; được bố mẹ đưa  về nh&agrave; ngay.</p>\r\n<p class=\"Normal\">\"Quy định nghỉ học khi thời tiết lạnh được Sở Gi&aacute;o dục đưa ra từ  nhiều năm nay, nh&agrave; trường cũng đ&atilde; th&ocirc;ng b&aacute;o rộng r&atilde;i đến phụ huynh n&ecirc;n t&ocirc;i thấy  kh&ocirc;ng qu&aacute; bất ngờ v&agrave; kh&oacute; giải quyết\", chị C&uacute;c n&oacute;i.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/phu_huynh_xem_lich_nghi_hoc.jpg\" alt=\"\" width=\"480\" height=\"350\" /><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Phụ huynh xem kỹ th&ocirc;ng b&aacute;o v&agrave; lịch học m&ugrave;a đ&ocirc;ng cho con. <em>Ảnh: Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Anh L&ecirc; Văn Ki&ecirc;n đưa con đến trường mới biết th&ocirc;ng tin được nghỉ  học, đ&agrave;nh quay xe đưa con về nh&agrave; &ocirc;ng b&agrave; ngoại gửi. Anh cho biết do ngủ dậy muộn  n&ecirc;n kh&ocirc;ng xem được chương tr&igrave;nh dự b&aacute;o thời tiết. \"Trời r&eacute;t, ch&aacute;u được nghỉ n&ecirc;n  cuộc sống gia đ&igrave;nh t&ocirc;i cũng x&aacute;o trộn. Con nghỉ học nhưng bố mẹ c&oacute; được nghỉ l&agrave;m  đ&acirc;u. Nh&agrave; t&ocirc;i c&ograve;n c&oacute; &ocirc;ng b&agrave; để gửi ch&aacute;u, chứ nhiều đồng nghiệp phải nghỉ l&agrave;m để  tr&ocirc;ng con\", anh Ki&ecirc;n n&oacute;i.</p>\r\n<p class=\"Normal\">Trao đổi với <em>VnExpress.net</em>, &ocirc;ng  Nguyễn Tr&iacute; Dũng, Ph&oacute; ph&ograve;ng Gi&aacute;o dục tiểu học (Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo) cho biết,  H&agrave; Nội rất rộng, chia l&agrave;m nhiều v&ugrave;ng, v&igrave; vậy theo chỉ đạo của Sở c&aacute;c trường được  giao quyền chủ động theo d&otilde;i thời tiết v&agrave; th&ocirc;ng b&aacute;o cho học sinh nghỉ học.</p>\r\n<p class=\"Normal\">\"Ch&uacute;ng t&ocirc;i biết c&oacute; nhiều trường v&agrave; phụ huynh cũng băn khoăn về  mức nhiệt độ để lấy l&agrave;m mốc quyết định cho học sinh nghỉ. Sở đ&atilde; th&ocirc;ng b&aacute;o l&agrave;  theo Đ&agrave;i truyền h&igrave;nh H&agrave; Nội v&agrave;o l&uacute;c 6h30. Nếu thấy trời qu&aacute; r&eacute;t, nhiệt độ dưới  10 độ C th&igrave; c&aacute;c trường tiểu học th&ocirc;ng b&aacute;o cho học sinh nghỉ để đảm bảo sức  khỏe\", &ocirc;ng Dũng n&oacute;i.</p>\r\n<p class=\"Normal\">Hiện H&agrave; Nội c&oacute; hơn 400.000 học sinh tiểu học.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 1, 1013, 0, 1, NULL, NULL),
(11, 'Phương pháp Mathnasium giúp trẻ yêu thích môn toán ', 'Phuong-Phap-Mathnasium-Giup-Tre-Yeu-Thich-Mon-Toan', 'Phương pháp dạy toán Mathnasium với 5 kỹ thuật giảng dạy bổ sung nhau, giúp trẻ em tiếp thu kiến thức toán hiệu quả, không cảm thấy áp lực và nhàm chán.\r\n', 'hinh_250x195[1]_JPG_thumb210x0_ns.jpg', 22, '<h1 class=\"Title\">Phương ph&aacute;p Mathnasium gi&uacute;p trẻ y&ecirc;u th&iacute;ch m&ocirc;n  to&aacute;n</h1>\r\n<h2 class=\"Lead\">Phương ph&aacute;p dạy to&aacute;n Mathnasium với 5 kỹ thuật giảng  dạy bổ sung nhau, gi&uacute;p trẻ em tiếp thu kiến thức to&aacute;n hiệu quả, kh&ocirc;ng cảm thấy  &aacute;p lực v&agrave; nh&agrave;m ch&aacute;n.</h2>\r\n<p class=\"Normal\">Phương ph&aacute;p dạy n&agrave;y được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi  &ocirc;ng Larry Martinek - gi&aacute;o sư to&aacute;n học xuất sắc của bang California, Mỹ. Trong  trung hạn, Mathnasium gi&uacute;p học sinh ph&aacute;t triển tư duy, n&acirc;ng cao chỉ số th&ocirc;ng  minh, đồng thời x&acirc;y dựng l&ograve;ng tin v&agrave;o ch&iacute;nh bản th&acirc;n m&igrave;nh. Mục ti&ecirc;u l&acirc;u d&agrave;i v&agrave;  cao nhất của n&oacute; l&agrave; gi&uacute;p từng c&aacute; nh&acirc;n trở th&agrave;nh người tự tin, năng động v&agrave; th&agrave;nh  c&ocirc;ng trong cuộc sống.</p>\r\n<p class=\"Normal\">Trong 5 kỹ thuật giảng dạy, kỹ thuật tư duy gi&uacute;p c&aacute;c  em tập th&oacute;i quen suy nghĩ, từ đ&oacute; x&acirc;y dựng sự nhạy b&eacute;n v&agrave; tự tin với c&aacute;c con số.  Kỹ thuật diễn đạt bằng ng&ocirc;n từ l&agrave;m học sinh tập th&oacute;i quen v&agrave; kỹ năng diễn giải  bằng lời những &yacute; tưởng, suy luận của m&igrave;nh. Ngo&agrave;i ra, kỹ thuật quan s&aacute;t h&igrave;nh ảnh  trực quan, sử dụng gi&aacute;o cụ, thực h&agrave;nh to&aacute;n viết cung cấp nguồn gi&aacute;o tr&igrave;nh v&agrave; b&agrave;i  tập phong ph&uacute;, đa dạng để trẻ c&oacute; thể thực h&agrave;nh v&agrave; ph&aacute;t triển kiến thức, tư duy  v&agrave; kỹ năng giải to&aacute;n.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/hinh.jpg\" alt=\"\" width=\"400\" height=\"269\" /><br /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Gi&aacute;o tr&igrave;nh Mathnasium gồm 3 loại. Loại thứ nhất l&agrave;  c&aacute;c b&agrave;i tập PK, l&agrave; b&agrave;i tập &ldquo;theo đơn&rdquo;. Đ&acirc;y l&agrave; hệ thống c&aacute;c b&agrave;i tập chuy&ecirc;n đề  được ph&acirc;n cho từng học sinh dựa tr&ecirc;n kết quả kiểm tra đầu kỳ của học sinh đ&oacute;.  Loại b&agrave;i tập n&agrave;y gi&uacute;p ho&agrave;n thiện những mặt c&ograve;n hạn chế, v&agrave; ph&aacute;t triển c&aacute;c mặt  mạnh của từng em. Từ đ&oacute;, phương ph&aacute;p x&acirc;y dựng nền tảng to&aacute;n vững chắc cho trẻ.  Loại thứ hai l&agrave; b&agrave;i tập Work Out Book - r&egrave;n luyện tư duy số. Đ&acirc;y l&agrave; loại được  thiết kế tổng hợp, với những kh&aacute;i niệm mở rộng, gi&uacute;p học sinh ph&aacute;t triển &ldquo;tư duy  số&rdquo;, từ đ&oacute; ph&aacute;t triển tr&iacute; tuệ tốt nhất. Loại thứ ba l&agrave; Focus-on, chuy&ecirc;n s&acirc;u về  những chủ đề nhất định hoặc củng cố c&aacute;c lỗ hổng v&agrave; điểm c&ograve;n hạn chế, n&acirc;ng cao  kiến thức theo chủ đề.</p>\r\n<p class=\"Normal\">Quy tr&igrave;nh đ&agrave;o tạo của phương ph&aacute;p gồm 3 bước. Đầu  ti&ecirc;n l&agrave; bước kiểm tra đầu kỳ. Học sinh sẽ được kiểm tra viết v&agrave; vấn đ&aacute;p để x&aacute;c  định kiến thức, tr&igrave;nh độ, những điểm mạnh v&agrave; điểm cần phải củng cố, ph&aacute;t triển  th&ecirc;m. Dựa v&agrave;o kết quả n&agrave;y, kế hoạch học tập sẽ được thiết kế cho từng em. Đ&acirc;y  c&ograve;n được gọi l&agrave; phương ph&aacute;p học to&aacute;n hướng c&aacute; nh&acirc;n. Bước 2, thực h&agrave;nh dạy v&agrave; học  theo kế hoạch học tập. Trong một buổi học, mỗi gi&aacute;o vi&ecirc;n sẽ giảng dạy v&agrave; k&egrave;m cặp  4-5 học sinh theo phương ph&aacute;p dạy từ học sinh n&agrave;y đến học sinh kh&aacute;c. B&ecirc;n cạnh  đ&oacute;, thầy, c&ocirc; vẫn quan s&aacute;t, hỗ trợ học sinh khi c&aacute;c em l&agrave;m b&agrave;i tập độc lập.  Phương ph&aacute;p đảm bảo t&iacute;nh độc lập trong học tập v&agrave; sự giảng dạy k&egrave;m cặp của gi&aacute;o  vi&ecirc;n đối với từng em. Bước tiếp theo l&agrave; đ&aacute;nh gi&aacute; sự tiến bộ của học sinh. Cứ sau  3 th&aacute;ng, c&aacute;c em sẽ được kiểm tra để đ&aacute;nh gi&aacute; sự tiến bộ. Từ đ&oacute; gi&aacute;o vi&ecirc;n đưa ra  một kế hoạch học tập mới cao hơn. Quy tr&igrave;nh n&agrave;y được lặp đi lặp lại trong suốt  qu&aacute; tr&igrave;nh trẻ học với Mathnasium v&agrave; từng bước x&acirc;y dựng v&agrave; n&acirc;ng cao tư duy số v&agrave;  kiến thức to&aacute;n của từng em.</p>\r\n<p class=\"Normal\">Hiện phương ph&aacute;p n&agrave;y được &aacute;p dụng tại gần 400 trung  t&acirc;m dạy to&aacute;n Mathnasium ở Mỹ v&agrave; c&aacute;c quốc gia kh&aacute;c tr&ecirc;n thế giới. Hai trung t&acirc;m  Mathnasium đầu ti&ecirc;n tại Việt Nam ở số 3 Ng&ocirc; Thời Nhiệm, quận 3 v&agrave; 26/41-43-45  Nguyễn Minh Ho&agrave;ng, T&acirc;n B&igrave;nh, TP HCM sẽ khai giảng c&aacute;c lớp dạy to&aacute;n theo phương  ph&aacute;p Mathnasium đầu ti&ecirc;n v&agrave;o th&aacute;ng 2 (d&agrave;nh cho học sinh từ 4 đến 10 tuổi).</p>\r\n<p class=\"Normal\">Đăng k&yacute; ghi danh cho học sinh hay t&igrave;m hiểu về phương  ph&aacute;p Mathnasium, phụ huynh li&ecirc;n lạc số điện thoại 08-39301038 (quận 3),  08-38112988 (quận T&acirc;n B&igrave;nh), hay email về địa chỉ: <a class=\"Normal\" href=\"mailto:lienhe@mathnasium.vn\">lienhe@mathnasium.vn</a></p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn: <em>C&ocirc;ng ty Ph&aacute;t triển Gi&aacute;o dục Mỹ</em>)</p>', 1, 1614, 0, 1, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(12, 'Những nụ hôn ngọt ngào trong đêm tình nhân ', 'Nhung-Nu-Hon-Ngot-Ngao-Trong-Dem-Tinh-Nhan', 'Tối 13/2, hàng nghìn bạn trẻ có mặt tại cầu Ánh Sao (quận 7, TP HCM) chứng kiến những lời tỏ tình cùng những nụ hôn ngọt ngào của 100 cặp tình nhân trong \"Đêm Valentine thế kỷ\".\r\n', '250h_jpg_thumb210x0_ns.jpg', 22, '<h1 class=\"Title\">Những nụ h&ocirc;n ngọt ng&agrave;o trong đ&ecirc;m t&igrave;nh nh&acirc;n</h1>\r\n<h2 class=\"Lead\">Tối 13/2, h&agrave;ng ngh&igrave;n bạn trẻ c&oacute; mặt tại cầu &Aacute;nh Sao (quận 7, TP  HCM) chứng kiến những lời tỏ t&igrave;nh c&ugrave;ng những nụ h&ocirc;n ngọt ng&agrave;o của 100 cặp t&igrave;nh  nh&acirc;n trong \"Đ&ecirc;m Valentine thế kỷ\".</h2>\r\n<p><img src=\"../upload/h7.jpg\" alt=\"\" width=\"495\" height=\"330\" /></p>\r\n<p>Cầu &Aacute;nh Sao (quận 7, TP HCM) tối 13/2 l&agrave; nơi ghi lại khoảnh khắc 100 đ&ocirc;i t&igrave;nh  nh&acirc;n trao nhau những nụ h&ocirc;n ngọt ng&agrave;o.</p>\r\n<p><img src=\"../upload/h.jpg\" alt=\"\" width=\"495\" height=\"374\" /></p>\r\n<p>Đ&ocirc;i t&igrave;nh nh&acirc;n may mắn duy nhất Thế Mẫn - Thu Hương trong \"Đ&ecirc;m Valentine thế kỷ\"  được thể hiện t&igrave;nh y&ecirc;u của m&igrave;nh tr&ecirc;n khinh kh&iacute; cầu. Cả hai đ&atilde; trao nhau nụ h&ocirc;n  nồng n&agrave;n.</p>\r\n<p><img src=\"../upload/h2.jpg\" alt=\"\" width=\"495\" height=\"356\" /></p>\r\n<p>Kinh kh&iacute; cầu bay bổng đưa đ&ocirc;i bạn trẻ Thế Mẫn - Thu Hương l&ecirc;n bầu trời mịt tối.  \"C&oacute; một nụ h&ocirc;n tr&ecirc;n cao v&agrave; trong kh&ocirc;ng kh&iacute; như thế n&agrave;y th&igrave; kh&ocirc;ng g&igrave; sung sướng  bằng\", Thu Hương t&acirc;m sự.</p>\r\n<p><img src=\"../upload/h4.jpg\" alt=\"\" width=\"495\" height=\"344\" /></p>\r\n<p>H&agrave;ng trăm bạn trẻ kh&ocirc;ng c&oacute; dịp h&ocirc;n tr&ecirc;n khinh kh&iacute; cầu chia sẻ niềm vui v&agrave; ch&uacute;c  mừng hai bạn trẻ.</p>\r\n<p><img src=\"../upload/h17.jpg\" alt=\"\" width=\"495\" height=\"352\" /></p>\r\n<p>Đ&ocirc;i t&igrave;nh nh&acirc;n Qu&acirc;n v&agrave; Thương c&oacute; được những gi&acirc;y ph&uacute;t l&atilde;ng mạn.</p>\r\n<p><img src=\"../upload/h16.jpg\" alt=\"\" width=\"495\" height=\"355\" /></p>\r\n<p>V&agrave; ng&agrave;y t&igrave;nh nh&acirc;n năm nay, Thương nhận được m&oacute;n qu&agrave; 999 nụ hồng xanh do người  y&ecirc;u d&agrave;nh tặng.</p>\r\n<p><img src=\"../upload/h11.jpg\" alt=\"\" width=\"495\" height=\"373\" /></p>\r\n<p>C&ograve;n với Ngọc Yến, ng&agrave;y Valentine năm nay, người y&ecirc;u Minh Tuấn tỏ lời cầu h&ocirc;n c&ocirc;  bằng đ&ocirc;i nhẫn cưới.</p>\r\n<p><img src=\"../upload/h9.jpg\" alt=\"\" width=\"495\" height=\"367\" /></p>\r\n<p>Ngọc Yến đ&atilde; nhận lời cầu h&ocirc;n v&agrave; cả hai trao nhau nụ h&ocirc;n trước sự chứng kiến của  rất đ&ocirc;ng bạn trẻ.</p>\r\n<p><img src=\"../upload/h12.jpg\" alt=\"\" width=\"495\" height=\"359\" /></p>\r\n<p>Kim Ph&uacute;c hạnh ph&uacute;c khi người y&ecirc;u l&agrave; ch&agrave;ng trai đến từ Ph&aacute;p c&oacute; mặt tại Việt Nam  trong đ&ecirc;m đặc biệt n&agrave;y.</p>\r\n<p><img src=\"../upload/h18.jpg\" alt=\"\" width=\"495\" height=\"342\" /></p>\r\n<p>C&ocirc; g&aacute;i hạnh ph&uacute;c khi nhận m&oacute;n qu&agrave; l&aacute; đ&oacute;a hoa từ người y&ecirc;u trong đ&ecirc;m t&igrave;nh nh&acirc;n.</p>\r\n<p><img src=\"../upload/h22.jpg\" alt=\"\" width=\"495\" height=\"371\" /></p>\r\n<p>Ghi lại gi&acirc;y ph&uacute;t l&atilde;ng mạn trong đ&ecirc;m t&igrave;nh nh&acirc;n.</p>\r\n<p><img src=\"../upload/h21.jpg\" alt=\"\" width=\"495\" height=\"351\" /></p>\r\n<p>H&agrave;ng trăm đ&ocirc;i c&oacute; mặt trong \"Đ&ecirc;m Valentine thế kỷ\".</p>\r\n<p style=\"text-align: right;\"><strong>An Nhơn</strong></p>', 2, 63, 0, 1, NULL, NULL),
(13, 'Hot girl tâm sự về ngày Valentine ', 'Hot-Girl-Tam-Su-Ve-Ngay-Valentine', 'Một bông hồng trắng bằng khăn giấy, chiếc xe đạp gắn đầy hoa, hay bài thơ của chàng \"thi sĩ\" vô danh gửi tặng… là những món quà đầy ấn tượng mà hot girl Midu từng nhận được trong các mùa Valentine.\r\n', 'hot-girl-valentine-2.jpg', 22, '<h1 class=\"Title\">Hot girl t&acirc;m sự về ng&agrave;y Valentine</h1>\r\n<h2 class=\"Lead\">Một b&ocirc;ng hồng trắng bằng khăn giấy, chiếc xe đạp gắn đầy hoa, hay  b&agrave;i thơ của ch&agrave;ng \"thi sĩ\" v&ocirc; danh gửi tặng&hellip; l&agrave; những m&oacute;n qu&agrave; đầy ấn tượng m&agrave;  hot girl Midu từng nhận được trong c&aacute;c m&ugrave;a Valentine.</h2>\r\n<p class=\"Normal\">Bao m&ugrave;a Valentine đ&atilde; qua nhưng đ&aacute;ng nhớ nhất đối với c&ocirc; g&aacute;i trẻ  vẫn l&agrave; kỷ niệm của mối t&igrave;nh đầu thời học sinh. Đ&oacute; l&agrave; v&agrave;o năm học cấp 3, <a class=\"Normal\" href=\"../../gl/xa-hoi/nhip-dieu-tre/2010/05/3ba1c040/?phpMyAdmin=a4c14c12d106f8f87a752ed51b37018f\">Midu</a> được bạn  trai hẹn đi chơi v&agrave; tặng cho hộp qu&agrave; h&igrave;nh tr&aacute;i tim m&agrave;u đỏ. H&ocirc;m đ&oacute;, c&ocirc; n&agrave;ng đ&atilde;  giận hờn, rồi nổi n&oacute;ng n&eacute;m m&oacute;n qu&agrave; xuống đường v&agrave; kh&oacute;c sướt mướt bỏ đi. L&yacute; do  chỉ v&igrave; ch&agrave;ng bắt phải chờ đợi qu&aacute; l&acirc;u, đến l&uacute;c \"t&agrave;n cuộc\" mới trao qu&agrave;.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"../upload/tintuc/midu21.jpg\" alt=\"Midu cho biết, Valentine đ&aacute;ng nhớ nhất của m&igrave;nh l&agrave; lần giỗi hờn ng&acirc;y thơ với bạn trai v&agrave; kh&oacute;c\" width=\"400\" height=\"598\" /></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"404\" height=\"12\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Image\" style=\"text-align: left;\">Midu cho biết, Valentine đ&aacute;ng nhớ nhất của m&igrave;nh l&agrave; lần dỗi hờn  ng&acirc;y thơ của mối t&igrave;nh đầu.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Midu b&agrave;y tỏ: \"Nghĩ lại m&agrave; thấy buồn cười. Em rất h&aacute;o hức, n&ecirc;n  khi gặp l&agrave; muốn nhận qu&agrave; ngay, nhưng m&atilde;i chẳng thấy đ&acirc;u. Đến khi chuẩn bị về mới  l&ocirc;i ra một chiếc hộp nhỏ, giận qu&aacute; n&eacute;m lu&ocirc;n xuống đất rồi kh&oacute;c như mưa. Mỗi đứa  đi một đường. Cuối c&ugrave;ng, em lặng lẽ quay lại lượm m&agrave; kh&ocirc;ng cho người ta biết. Về  nh&agrave; mở ra th&igrave; thấy một b&ocirc;ng hồng m&agrave;u trắng l&agrave;m bằng khăn giấy k&egrave;m lời nhắn. Sau  n&agrave;y, hễ cứ nghĩ đến kỷ niệm n&agrave;y lại thấy hối hận v&igrave; biết anh ấy mất rất nhiều  thời gian v&agrave; l&agrave;m hư rất nhiều giấy mới xếp được b&ocirc;ng hoa\".</p>\r\n<p class=\"Normal\">V&agrave;o đại học, <a class=\"Normal\" href=\"../../gl/xa-hoi/nhip-dieu-tre/2010/07/3ba1dc31/?phpMyAdmin=a4c14c12d106f8f87a752ed51b37018f\">Midu </a>v&agrave; anh bạn đ&oacute; mỗi  người một nơi. Những m&ugrave;a Valentine tiếp theo, số lượng qu&agrave; của c&ocirc; g&aacute;i trẻ tăng  nhiều hơn khi c&ocirc; trở th&agrave;nh Hot Vteen, được nhiều người biết đến. Midu cho biết,  c&oacute; năm c&ocirc; nhận được cả một chiếc xe đạp gắn đầy hoa hồng. Qu&agrave; c&oacute; ghi t&ecirc;n người  gửi nhưng c&ocirc; kh&ocirc;ng biết r&otilde; l&agrave; ai.</p>\r\n<p class=\"Normal\">C&ograve;n bất ngờ nhất lại l&agrave; Valentine năm rồi. Trước ng&agrave;y lễ t&igrave;nh  nh&acirc;n một ng&agrave;y, một thanh ni&ecirc;n đến shop b&aacute;n h&agrave;ng của Midu v&agrave; mua tất cả m&oacute;n đồ c&oacute;  m&agrave;u t&iacute;m. Chưa kịp l&yacute; giải về h&agrave;nh động kh&aacute;c thường của vị kh&aacute;ch h&agrave;ng đặc biệt  n&agrave;y th&igrave; đ&uacute;ng v&agrave;o ng&agrave;y t&igrave;nh nh&acirc;n, tất cả m&oacute;n đồ n&agrave;y được đem đến v&agrave; tặng hết cho  c&ocirc; chủ. \"D&ugrave; kh&ocirc;ng phải l&agrave; y&ecirc;u, nhưng t&igrave;nh cảm của nhiều người l&agrave;m m&igrave;nh cảm động.  Em xem như đ&oacute; l&agrave; t&igrave;nh cảm trong s&aacute;ng n&ecirc;n kh&ocirc;ng băn khoăn khi nhận\", c&ocirc; sinh vi&ecirc;n  chuy&ecirc;n ng&agrave;nh mỹ thuật chia sẻ.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/xuan-101.jpg\" alt=\"Bảo Ngọc th&iacute;ch người y&ecirc;u tạo cho m&igrave;nh sự bất ngờ trong ng&agrave;y lễ t&igrave;nh nh&acirc;n.\" width=\"400\" height=\"565\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Bảo Ngọc th&iacute;ch người y&ecirc;u tạo cho m&igrave;nh sự bất ngờ trong ng&agrave;y lễ  t&igrave;nh nh&acirc;n.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><span style=\"color: #3f3f3f;\"><strong>Với <a href=\"../../gl/xa-hoi/nhip-dieu-tre/2010/11/3ba22e81/?phpMyAdmin=a4c14c12d106f8f87a752ed51b37018f\">hot girl Bảo Ngọc</a>, ng&agrave;y lễ  t&igrave;nh nh&acirc;n n&agrave;o cũng tr&agrave;n ngập niềm vui bởi nhận được rất nhiều qu&agrave;, d&ugrave; c&ocirc; chưa c&oacute;  \"mối t&igrave;nh vắt vai\" n&agrave;o.</strong></span> Ngọc cho biết, v&igrave; chưa c&oacute; bạn trai n&ecirc;n  c&ocirc; v&agrave; nh&oacute;m bạn g&aacute;i lập ra hội những người độc th&acirc;n để đi chơi v&agrave; tặng qu&agrave; cho  nhau. Nhiều người c&oacute; t&igrave;nh cảm với người đẹp đ&atilde; lặng lẽ gửi hoa, qu&agrave; k&egrave;m theo  những lời ch&uacute;c dễ thương. Với những t&igrave;nh cảm n&agrave;y, Ngọc chia sẻ c&ocirc; cảm thấy c&oacute;  ch&uacute;t ngại ng&ugrave;ng v&igrave; sợ nhận qu&agrave; rồi sẽ l&agrave;m người ta hiểu nhầm.</p>\r\n<p class=\"Normal\">Tuy ngại nhưng Bảo Ngọc kh&ocirc;ng phủ nhận c&oacute; những m&oacute;n qu&agrave; tạo ấn  tượng cho c&ocirc;. Như chiếc gối &ocirc;m nhỏ x&iacute;u c&oacute; th&ecirc;u t&ecirc;n Bảo Ngọc m&agrave; c&ocirc; nhận được c&aacute;ch  đ&acirc;y v&agrave;i năm. \"Chắc chắn người gửi đ&atilde; dồn rất nhiều t&acirc;m huyết v&agrave; t&igrave;nh cảm của  m&igrave;nh v&agrave;o m&oacute;n qu&agrave; n&agrave;y. M&oacute;n qu&agrave; nhỏ nhưng lại rất &yacute; nghĩa với em\", Ngọc n&oacute;i.</p>\r\n<p class=\"Normal\">Ngọc chia sẻ, trong ng&agrave;y Valentine c&ocirc; th&iacute;ch nhất l&agrave; cuộc hẹn bất  ngờ l&atilde;ng mạn thay v&igrave; những m&oacute;n qu&agrave; xa xỉ. \"Nếu c&oacute; người y&ecirc;u, em ước g&igrave; người đ&oacute;  sẽ tạo cho em sự bất ngờ. Chỉ cần anh ấy đến đ&oacute;n v&agrave; dẫn em đến bữa tiệc đ&atilde; sắp  đặt sẵn. Cảm gi&aacute;c l&uacute;c đ&oacute; chắc rất giống như trong truyện cổ t&iacute;ch\", c&ocirc; chia sẻ.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/hotgirl3.jpg\" alt=\"Hot girl Elly chia sẻ đối với c&ocirc; ng&agrave;y Valentine cũng như bao ng&agrave;y kh&aacute;c bởi c&ocirc; vẫn chưa t&igrave;m được người m&igrave;nh y&ecirc;u.\" width=\"480\" height=\"356\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hot girl Elly chia sẻ, ng&agrave;y Valentine cũng như bao ng&agrave;y kh&aacute;c bởi  c&ocirc; vẫn chưa t&igrave;m được người y&ecirc;u.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><span style=\"color: #3f3f3f;\"><strong>Kh&aacute;c với cảm x&uacute;c của c&aacute;c bạn trang  lứa, <a href=\"../../gl/xa-hoi/nhip-dieu-tre/2010/03/3ba19118/?phpMyAdmin=a4c14c12d106f8f87a752ed51b37018f\">c&ocirc; n&agrave;ng n&oacute;ng  bỏng Elly Trần</a> lại cho rằng ng&agrave;y lễ t&igrave;nh nh&acirc;n cũng b&igrave;nh thường như bao ng&agrave;y  kh&aacute;c.</strong></span> D&ugrave; nhận được rất nhiều qu&agrave; nhưng Elly th&agrave;nh thật cho biết  kh&ocirc;ng c&oacute; cảm x&uacute;c g&igrave; đặc biệt. \"Chắc bởi em chưa c&oacute; người m&igrave;nh y&ecirc;u thực sự\", c&ocirc;  n&oacute;i.</p>\r\n<p class=\"Normal\">Những m&ugrave;a Valentine gần đ&acirc;y, Elly kh&ocirc;ng may mắn được hẹn h&ograve; l&atilde;ng  mạn với bạn b&egrave; bởi ng&agrave;y n&agrave;y đều tr&ugrave;ng v&agrave;o c&aacute;c dịp c&ocirc; phải đi nước ngo&agrave;i tham gia  chụp ảnh hay giao lưu kh&aacute;n giả.</p>\r\n<p class=\"Normal\">Ấn tượng nhất với Elly l&agrave; t&igrave;nh cảm của một fan Đ&agrave;i Loan. Anh  ch&agrave;ng cất c&ocirc;ng sưu tập cả chục m&oacute;n đặc sản xứ Đ&agrave;i để l&agrave;m qu&agrave; tặng người đẹp. Anh  c&ograve;n d&agrave;nh to&agrave;n bộ số tiền tiết kiệm để mua v&eacute; m&aacute;y bay đến Việt Nam trao qua tận  tay. \"Nghe anh ấy n&oacute;i mong muốn m&oacute;n qu&agrave; sẽ gi&uacute;p em hiểu th&ecirc;m về Đ&agrave;i Loan khiến  em thấy cảm động lắm\", c&ocirc; n&oacute;i.</p>\r\n<p class=\"Normal\">Elly Trần chia sẻ, c&ocirc; đang chờ đợi một Valentine thật &yacute; nghĩa v&agrave;  l&atilde;ng mạn khi t&igrave;m được người y&ecirc;u.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>B&igrave;nh Nguy&ecirc;n</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', 2, 2768, 0, 1, NULL, NULL),
(14, 'Nên duyên chồng vợ từ mạng mai mối ', 'Nen-Duyen-Chong-Vo-Tu-Mang-Mai-Moi', 'Quen nhau qua trang web kết bạn, để chiếm được tình cảm của cô nàng cao tới 1,71 m, chàng trai cao 1,58 m kiên trì tỏ tình tới 10 lần và hạnh phúc đã mỉm cười với họ. ', 'cap_doi_hoan_hao_set_sub.jpg', 22, '<h1 class=\"Title\">N&ecirc;n duy&ecirc;n chồng vợ từ mạng mai mối</h1>\r\n<h2 class=\"Lead\">Quen nhau qua trang web kết bạn, để chiếm được t&igrave;nh cảm của c&ocirc;  n&agrave;ng cao tới 1,71 m, ch&agrave;ng trai cao 1,58 m ki&ecirc;n tr&igrave; tỏ t&igrave;nh tới 10 lần v&agrave; hạnh  ph&uacute;c đ&atilde; mỉm cười với họ. <br />&gt; <a class=\"Lead\" href=\"../../gl/xa-hoi/2009/12/3ba171fa/?phpMyAdmin=a4c14c12d106f8f87a752ed51b37018f\">T&igrave;m chồng qua net</a></h2>\r\n<p class=\"Normal\">Hội những người bạn S&agrave;i G&ograve;n v&agrave; th&agrave;nh vi&ecirc;n tr&ecirc;n một mạng x&atilde; hội  vẫn chưa ngớt b&agrave;n t&aacute;n x&ocirc;n xao về cặp đ&ocirc;i được cho l&agrave; ho&agrave;n hảo nhất. Ch&agrave;ng chỉ  cao 1,58 m, c&ograve;n n&agrave;ng hơn anh tới một c&aacute;i đầu nhưng họ vẫn y&ecirc;u nhau say đắm. T&igrave;nh  y&ecirc;u ấy được đ&aacute;nh dấu bởi một đ&aacute;m cưới linh đ&igrave;nh.</p>\r\n<p class=\"Normal\">Vừa đưa vợ đi dạo chơi nh&acirc;n ng&agrave;y Valentine về, anh Khổng Đạt  Tường (nick l&agrave; Danny) cười tươi cho biết đ&atilde; kể c&acirc;u chuyện t&igrave;nh của hai người  nhiều lần, v&agrave; mỗi lần kể đều cảm thấy ấm &aacute;p, hạnh ph&uacute;c v&igrave; nhờ mạng mai mối m&agrave;  anh đ&atilde; gặp được Jet (L&ecirc; Thị Như Huyền, hiện đ&atilde; l&agrave; vợ anh).</p>\r\n<p class=\"Normal\">Anh Tường kể, gặp Huyền trong một buổi offline của hội, tr&ograve;  chuyện v&agrave; ấn tượng với c&ocirc; g&aacute;i dễ thương c&oacute; chiều cao \"khủng\", khu&ocirc;n mặt dễ  thương, anh đ&atilde; đem l&ograve;ng cảm mến. Số lần gặp nhau nhiều hơn v&igrave; hội hay tổ chức  gặp mặt, nhiều l&uacute;c anh muốn thổ lổ t&igrave;nh cảm nhưng kh&ocirc;ng d&aacute;m mở lời v&igrave; Huyền đang  chuẩn bị cho kỳ thi tốt nghiệp cao đẳng.</p>\r\n<p class=\"Normal\">\"Kh&ocirc;ng thể kiềm chế m&atilde;i, một lần lấy hết can đảm t&ocirc;i đ&atilde; nhắn tin  cho Huyền thổ lộ rằng m&igrave;nh th&iacute;ch em mất rồi. L&uacute;c nhận được hồi &acirc;m của Huyền, t&ocirc;i  ngạt thở v&igrave; hạnh ph&uacute;c bởi \'Jet cũng mến anh\'&rdquo;, Tường t&acirc;m sự.</p>\r\n<p class=\"Normal\">Lần đầu ti&ecirc;n tỏ t&igrave;nh b&ecirc;n hồ b&aacute;n nguyệt, tim Tường như tan ra  từng mảnh khi nhận được c&acirc;u trả lời: \"Jet mến anh nhưng chỉ coi anh như người  anh b&igrave;nh thường\".</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/cap_doi_hoan_hao_1.jpg\" alt=\"\" width=\"320\" height=\"450\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ch&uacute; rể 1,58 m v&agrave; c&ocirc; d&acirc;u 1,71 m hạnh ph&uacute;c b&ecirc;n nhau trong ng&agrave;y  cưới.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Lời từ chối kh&ocirc;ng l&agrave;m ch&agrave;ng quản l&yacute; của một c&ocirc;ng ty du lịch bỏ  cuộc. Sau mỗi chiều tan sở, anh đều gh&eacute; qua nh&agrave; trọ thăm c&ocirc;. C&oacute; l&uacute;c đưa Huyền đi  chơi, dạo phố, đi ăn lặt vặt v&agrave; gi&uacute;p c&ocirc; định hướng tương lai. Tường tin rằng,  thời gian sẽ gi&uacute;p Huyền hiểu v&agrave; y&ecirc;u anh.</p>\r\n<p class=\"Normal\">Th&ecirc;m những lần tỏ t&igrave;nh thứ hai, thứ ba v&agrave; đến lần thứ ch&iacute;n bị từ  chối, Tường vẫn ki&ecirc;n tr&igrave; bởi nhận ra rằng Huyền đ&atilde; chiếm một vị tr&iacute; rất quan  trọng trong tr&aacute;i tim anh. Cho rằng c&oacute; thể Huyền chưa sẵn s&agrave;ng v&igrave; c&ograve;n lo cho học  tập, Tường quyết t&acirc;m đợi đến l&uacute;c c&ocirc; ra trường.</p>\r\n<p class=\"Normal\">\"Hai ng&agrave;y trước kỳ thi tốt nghiệp của Huyền, m&igrave;nh đ&atilde; rủ hai  người bạn nữa đến nh&agrave; em. M&igrave;nh tự tay nấu nồi ch&egrave; đỗ xanh với hy vọng mang đến  may mắn cho n&agrave;ng trước khi thi\", Tường kể.</p>\r\n<p class=\"Normal\">Sau khi Huyền ho&agrave;n th&agrave;nh kh&oacute;a học, Tường mạnh dạn tỏ t&igrave;nh lần  thứ 10. Thật bất ngờ, c&ocirc; n&agrave;ng cao 1,71 m đ&atilde; nhận lời y&ecirc;u ch&agrave;ng 1,58 m bởi nhận  ra anh l&agrave; người đ&agrave;n &ocirc;ng đ&iacute;ch thực, lu&ocirc;n quan t&acirc;m, lo lắng, y&ecirc;u thương c&ocirc; thật  l&ograve;ng.</p>\r\n<p class=\"Normal\">Nhưng cửa ải tiếp theo m&agrave; hai người phải vượt qua ch&iacute;nh l&agrave; sự  phản đối của gia đ&igrave;nh hai b&ecirc;n. Bố mẹ Tường khuy&ecirc;n con \"Chọn đứa cao vừa th&ocirc;i chứ  như thế th&igrave; cao qu&aacute;\", c&ograve;n gia đ&igrave;nh Huyền th&igrave; cay nghiệt: \"Bộ hết người rồi hay  sao m&agrave; lại chọn n&oacute; hả con\".</p>\r\n<p class=\"Normal\">Hai người bắt đầu l&ecirc;n kế hoạch \"lấy l&ograve;ng\" hai họ. Phụ mẫu của  Tường th&igrave; kh&aacute; dễ d&agrave;ng để thuyết phục, nhưng bố mẹ Huyền cứ thấy mặt ch&agrave;ng l&agrave; nổi  n&oacute;ng. Tường ki&ecirc;n tr&igrave; thể hiện thiện ch&iacute; bằng c&aacute;ch năng về thăm &ocirc;ng b&agrave;, thấy việc  g&igrave; c&oacute; thể l&agrave;m l&agrave; x&ocirc;ng v&agrave;o l&agrave;m ngay.</p>\r\n<p class=\"Normal\">\"Mưa dầm thấm l&acirc;u, gần một năm sau th&igrave; hai cụ đ&atilde; y&ecirc;n t&acirc;m giao  con g&aacute;i cho m&igrave;nh. Ng&agrave;y 16/11/2010 bọn m&igrave;nh đ&atilde; l&agrave;m đ&aacute;m cưới\", Tường cười cho hay.  C&ograve;n Huyền th&igrave; bật m&iacute;: \"B&acirc;y giờ bố mẹ m&igrave;nh c&ograve;n cưng anh hơn cả con g&aacute;i\".</p>\r\n<p class=\"Normal\">Valentine năm trước l&agrave; kỷ niệm 99 ng&agrave;y y&ecirc;u của đ&ocirc;i t&igrave;nh nh&acirc;n Vũ  Tuấn Đạt v&agrave; Nguyễn Thị Quỳnh. Quỳnh rất tr&acirc;n trọng ng&agrave;y n&agrave;y nhưng n&oacute; lại rơi v&agrave;o  m&ugrave;ng 1 Tết khiến hai đứa kh&ocirc;ng thể gặp nhau. Lễ t&igrave;nh nh&acirc;n năm nay, c&ocirc; đ&atilde; l&ecirc;n kế  hoạch l&atilde;ng mạn để người y&ecirc;u của m&igrave;nh thấy ấm &aacute;p nhất.</p>\r\n<p class=\"Normal\">Quỳnh kể, ngay khi đăng k&yacute; v&agrave;o mạng mai mối, c&oacute; một người thường  xuy&ecirc;n v&agrave;o ng&ocirc;i nh&agrave; ảo của Quỳnh để comment, anh bảo c&ocirc; l&agrave; \"ph&aacute;t ra y&ecirc;u kh&iacute;\", rồi  th&igrave; \"Ai m&agrave; c&oacute; thể y&ecirc;u một c&ocirc; b&eacute; thế n&agrave;y nhỉ?\". Quỳnh gh&eacute;t anh<em> </em>v&igrave; lu&ocirc;n  ch&eacute;m gi&oacute;, hay cười đểu v&agrave; đ&aacute; xo&aacute;y c&ocirc;. \"Một h&ocirc;m đang online, hai đứa nảy ra &yacute;  định gặp nhau xem ngo&agrave;i đời liệu c&oacute; \"ch&eacute;m\" như tr&ecirc;n mạng kh&ocirc;ng, vậy l&agrave; gặp\", Đạt  kể.</p>\r\n<p class=\"Normal\">Ấn tượng đầu ti&ecirc;n của Quỳnh l&agrave; c&acirc;u n&oacute;i của Đạt khi vừa nh&igrave;n thấy  đ&atilde; gọi c&ocirc; l&agrave; \"Qu&aacute;i vật\", c&ograve;n Quỳnh gọi \"anh ch&eacute;m gi&oacute;\". Kh&ocirc;ng qu&aacute; ngại ng&ugrave;ng v&igrave;  đ&atilde; hiểu nhau qua những lần tr&ograve; chuyện, buổi hẹn đầu ti&ecirc;n tr&ecirc;n phố T&ocirc; Tịch (H&agrave;  Nội) kết th&uacute;c bằng việc hai đứa cuốc bộ khi xe bị xịt lốp v&igrave; d&iacute;nh phải đinh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/cap_doi_hoan_hao_2.jpg\" alt=\"\" width=\"320\" height=\"450\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Anh ch&agrave;ng ch&eacute;m gi&oacute; v&agrave; c&ocirc; bạn g&aacute;i hotmilk dễ thương đ&atilde; y&ecirc;u nhau  được hơn một năm nhờ t&igrave;nh cờ quen nhau tr&ecirc;n mạng mai mối. Ảnh: <em>Ho&agrave;ng  Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Như c&oacute; sự sắp đặt, Quỳnh lại được ph&acirc;n c&ocirc;ng l&agrave;m một chương tr&igrave;nh  quảng c&aacute;o ngay tại ĐH B&aacute;ch khoa H&agrave; Nội, nơi Đạt đang theo học. Hai người gặp lại  nhau, Đạt trở th&agrave;nh hướng dẫn vi&ecirc;n giới thiệu về ng&ocirc;i trường của m&igrave;nh cho Huyền  nghe v&agrave; đưa c&ocirc; đi ăn cơm sinh vi&ecirc;n.</p>\r\n<p class=\"Normal\">Những lần đi chơi của hai người d&agrave;y l&ecirc;n, địa điểm được lựa chọn  cũng l&atilde;ng mạn. \"Một lần đi chơi, Đạt kh&ocirc;ng như mọi ng&agrave;y, n&oacute;i &iacute;t v&agrave; chỉ nh&igrave;n xa  xăm để rồi đột ngột đặt l&ecirc;n m&ocirc;i m&igrave;nh một nụ h&ocirc;n. Đ&uacute;ng 0 giờ đ&ecirc;m đ&oacute;, anh nhắn tin  tỏ t&igrave;nh\", Quỳnh chia sẻ.</p>\r\n<p class=\"Normal\">Vẫn thường gọi nhau l&agrave; \"qu&aacute;i vật\", l&agrave; \"ch&eacute;m gi&oacute;\", nhưng hai \"kẻ  th&ugrave; kh&ocirc;ng đội trời chung\" ấy đ&atilde; y&ecirc;u nhau l&uacute;c n&agrave;o kh&ocirc;ng biết. Họ c&ugrave;ng nhau đi ăn  b&uacute;n b&ograve; Huế cay x&egrave;, c&ugrave;ng nhau nấu những bữa cơm ấm c&uacute;ng, c&ugrave;ng lang thang chụp ảnh  hay c&ugrave;ng x&ograve;e tay xem b&oacute;i cho nhau.</p>\r\n<p class=\"Normal\">Khi mới nhận lời y&ecirc;u, ch&agrave;ng trai B&aacute;ch khoa đ&atilde; phải l&ecirc;n Ph&uacute; Thọ  thực tập, c&ograve;n c&ocirc; sinh vi&ecirc;n ĐH Phương Đ&ocirc;ng ở lại H&agrave; Nội với nỗi nhớ nhung. Gọi  điện, nhắn tin như chẳng đủ n&oacute;i l&ecirc;n t&igrave;nh cảm của hai người, họ c&ograve;n hẹn nhau l&ecirc;n  mạng để được \"ch&eacute;m gi&oacute;\" như ng&agrave;y n&agrave;o.</p>\r\n<p class=\"Normal\">Hơn một năm y&ecirc;u nhau, kỷ niệm t&igrave;nh y&ecirc;u của đ&ocirc;i bạn trẻ n&agrave;y c&oacute;  thể ghi đầy một cuốn sổ. Lần đầu ti&ecirc;n về nh&agrave; Quỳnh, kh&ocirc;ng hiểu sao \"con ngựa  sắt\" của Đạt giở chứng kh&ocirc;ng chịu nổ m&aacute;y. \"Chẳng hiểu g&igrave; nhưng m&igrave;nh thử lấy dao  chọc ngo&aacute;y v&agrave; n&oacute; chạy thật, vậy m&agrave; bố mẹ Quỳnh kh&ocirc;ng ngớt lời khen\", Đạt  cười.</p>\r\n<p class=\"Normal\">C&ograve;n Quỳnh th&igrave; ngay lần đầu ra mắt, trong l&uacute;c nấu ăn đ&atilde; l&agrave;m vỡ  chiếc đĩa lớn. C&ocirc; đ&atilde; nhanh ch&oacute;ng \"phi tang\" chiếc đĩa v&igrave; lo lắng bố mẹ anh ph&aacute;t  hiện v&agrave; quy kết c&ocirc; vụng về. Sau đ&oacute;, Đạt cũng đứng ra nhận hộ việc l&agrave;m vỡ đĩa cho  c&ocirc;.</p>\r\n<p class=\"Normal\">\"Thật ấm &aacute;p biết bao khi mỗi s&aacute;ng m&ugrave;a đ&ocirc;ng lại nhận được tin  nhắn \"trời lạnh rồi đấy, em mặc th&ecirc;m &aacute;o ấm nh&eacute;\". M&igrave;nh lu&ocirc;n cảm ơn mạng x&atilde; hội v&igrave;  nhờ n&oacute; m&agrave; m&igrave;nh v&agrave; anh đ&atilde; y&ecirc;u nhau\", Quỳnh t&acirc;m sự.</p>\r\n<p class=\"Normal\">Hiện tại, Đạt v&agrave; Quỳnh đ&atilde; ra trường v&agrave; c&oacute; việc l&agrave;m ổn định. Hai  người dự định sẽ l&agrave;m đ&aacute;m cưới v&agrave;o cuối năm nay.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 2, 2206, 0, 1, NULL, NULL),
(15, 'Những món quà Valentine làm từ tình yêu ', 'Nhung-Mon-Qua-Valentine-Lam-Tu-Tinh-Yeu', 'Cặm cụi cả tuần để thêu móc chìa khóa bằng len tặng người yêu, làm tranh bằng chính những hạt đỗ \"kỷ niệm ngày quen nhau\" của hai đứa là cách mà giới trẻ đang làm để tặng người yêu dịp lễ Valentine.\r\n', 'socola_set_sub.jpg', 22, '<h1 class=\"Title\">Những m&oacute;n qu&agrave; Valentine l&agrave;m từ t&igrave;nh y&ecirc;u</h1>\r\n<h2 class=\"Lead\">Cặm cụi cả tuần để th&ecirc;u m&oacute;c ch&igrave;a kh&oacute;a bằng len tặng người y&ecirc;u,  l&agrave;m tranh bằng ch&iacute;nh những hạt đỗ \"kỷ niệm ng&agrave;y quen nhau\" của hai đứa l&agrave; c&aacute;ch  m&agrave; giới trẻ đang l&agrave;m để tặng người y&ecirc;u dịp lễ Valentine.</h2>\r\n<p class=\"Normal\">Y&ecirc;u nhau được 2 năm, cả hai đều đ&atilde; c&oacute; c&ocirc;ng việc ổn định v&agrave; chỉ  chờ ng&agrave;y tốt, giờ tốt để l&agrave;m đ&aacute;m cưới, Trịnh Thị V&acirc;n (quận Hai B&agrave; Trưng, H&agrave; Nội)  rất tr&acirc;n trọng ng&agrave;y lễ t&igrave;nh nh&acirc;n h&agrave;ng năm. Với c&ocirc;, đ&acirc;y vừa l&agrave; ng&agrave;y gặp mặt đầu  ti&ecirc;n, vừa l&agrave; ng&agrave;y kỷ niệm t&igrave;nh y&ecirc;u của hai người.</p>\r\n<p class=\"Normal\">Ba năm trước, c&ocirc; v&agrave; nh&oacute;m bạn độc th&acirc;n thường rủ nhau đi c&agrave; ph&ecirc;  để ch&uacute;c mừng lẫn nhau ng&agrave;y lễ t&igrave;nh y&ecirc;u. Tại đ&acirc;y c&ocirc; gặp một ch&agrave;ng sinh vi&ecirc;n năm  cuối đang chơi ghi ta. M&ecirc; mẩn trước tiếng đ&agrave;n của anh, c&ocirc; đ&atilde; viết giấy nhờ anh  đ&aacute;nh hộ b&agrave;i \"C&acirc;y đ&agrave;n sinh vi&ecirc;n\". Đ&oacute; l&agrave; ng&agrave;y đ&aacute;nh dấu cho những lần gặp bất ngờ  về sau.</p>\r\n<p class=\"Normal\">\"Bọn m&igrave;nh gặp nhau tại qu&aacute;n, rồi hẹn nhau sau giờ tan trường.  Một năm sau, đ&uacute;ng ng&agrave;y Valentine th&igrave; ch&uacute;ng m&igrave;nh nhận lời y&ecirc;u nhau\", V&acirc;n kể.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/valentine.jpg\" alt=\"\" width=\"320\" height=\"450\" /><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nhiều c&ocirc; g&aacute;i d&agrave;nh thời gian th&ecirc;u tặng người y&ecirc;u những m&oacute;n qu&agrave;  đặc biệt. Ảnh: <em>Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Năm n&agrave;o c&ocirc; cũng d&agrave;nh những m&oacute;n qu&agrave; bất ngờ cho anh. C&oacute; năm l&agrave;  chiếc gối b&ocirc;ng, c&oacute; năm l&agrave; chiếc khăn... tất cả đều do c&ocirc; tự l&agrave;m. Năm nay, V&acirc;n  quyết định th&ecirc;u một chiếc m&oacute;c ch&igrave;a kh&oacute;a xinh xắn cho người y&ecirc;u v&igrave; \"m&oacute;c ch&igrave;a kh&oacute;a  c&oacute; thể lu&ocirc;n ở b&ecirc;n cạnh anh\".</p>\r\n<p class=\"Normal\">Bỏ 20.000 đồng mua nguy&ecirc;n liệu, tối n&agrave;o sau giờ ăn cơm c&ocirc; cũng  chạy t&oacute;t l&ecirc;n ph&ograve;ng th&ecirc;u bức tranh tr&ecirc;n mặt m&oacute;c ch&igrave;a kh&oacute;a. C&ocirc; cho hay, mất khoảng  một tuần để ho&agrave;n thiện sản phẩm.</p>\r\n<p class=\"Normal\">\"Mất nhiều thời gian nhưng m&igrave;nh thấy rất vui. Mỗi n&eacute;t chỉ th&ecirc;u  l&agrave; t&igrave;nh cảm của m&igrave;nh được gửi gắm trong đ&oacute;\", V&acirc;n tủm tỉm cười.</p>\r\n<p class=\"Normal\">Y&ecirc;u nhau 360 ng&agrave;y, B&ugrave;i Lan Huệ đ&atilde; d&ugrave;ng 360 hạt đỗ để đ&aacute;nh dấu kỷ  niệm t&igrave;nh cảm của hai người. Mua một chiếc b&igrave;nh thủy tinh lớn đặt tr&ecirc;n b&agrave;n, từ  ng&agrave;y y&ecirc;u anh, mỗi ng&agrave;y Huệ đều cho v&agrave;o b&igrave;nh một hạt đỗ. Ng&agrave;y vui c&ocirc; cho một hạt  đỗ đỏ, ng&agrave;y buồn, x&iacute;ch m&iacute;ch với nhau th&igrave; c&ocirc; bỏ một hạt đỗ đen.</p>\r\n<p class=\"Normal\">V&agrave; Valentine n&agrave;y Huệ sẽ d&ugrave;ng k&eacute;o d&aacute;n những hạt đỗ n&agrave;y th&agrave;nh bức  tranh h&igrave;nh tr&aacute;i tim, những b&ocirc;ng hoa v&agrave; một ng&ocirc;i nh&agrave; để tặng người y&ecirc;u.</p>\r\n<p class=\"Normal\">\"Nh&igrave;n v&agrave;o tranh anh sẽ nhớ đến tất cả những vui, buồn trong suốt  thời gian b&ecirc;n cạnh m&igrave;nh. Đ&oacute; l&agrave; chứng nh&acirc;n của t&igrave;nh y&ecirc;u m&agrave; hai đứa d&agrave;nh cho  nhau\", Huệ chia sẻ.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/socola.jpg\" alt=\"\" width=\"450\" height=\"320\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tự l&agrave;m chocolate để tặng người y&ecirc;u l&agrave; lựa chọn của rất nhiều bạn  trẻ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Năm n&agrave;o cũng thế, trước Valentine một tuần l&agrave; Anh Tuấn (ĐH B&aacute;ch  khoa) lại l&ecirc;n kế hoạch đi chơi v&agrave; chọn qu&agrave; cho bạn g&aacute;i. Năm nay, ng&agrave;y lễ t&igrave;nh  y&ecirc;u tr&uacute;ng v&agrave;o ng&agrave;y thứ Hai n&ecirc;n Tuấn sẽ rủ bạn g&aacute;i đi chơi v&agrave;o ng&agrave;y Chủ nhật. Sau  chuyến đi c&ocirc;ng vi&ecirc;n, hai người sẽ kết th&uacute;c ng&agrave;y Valentine bằng bữa tối l&atilde;ng  mạn.</p>\r\n<p class=\"Normal\">\"M&igrave;nh sẽ vẫn chọn những m&oacute;n qu&agrave; truyền thống l&agrave; hoa hồng v&agrave;  chocolate để tặng người y&ecirc;u. Nhưng chocolate sẽ do m&igrave;nh tự l&agrave;m với những h&igrave;nh  th&ugrave; đặc biệt. Chắc chắn người y&ecirc;u m&igrave;nh sẽ cảm động lắm\", Tuấn cười.</p>\r\n<p class=\"Normal\">Mua chocolate th&ocirc;, sữa đặc v&agrave; chiếc khu&ocirc;n với những h&igrave;nh xinh  xắn. Tuấn đun c&aacute;ch thủy chocolate, quấy đều với dầu ăn v&agrave; sữa rồi đổ v&agrave;o khu&ocirc;n  đợi kh&ocirc;. Vậy l&agrave; cậu đ&atilde; c&oacute; m&oacute;n qu&agrave; như mong muốn cho bạn g&aacute;i.</p>\r\n<p class=\"Normal\">\"Valentine l&agrave; cơ hội để ch&uacute;ng m&igrave;nh thể hiện t&igrave;nh y&ecirc;u đối với  nhau, h&acirc;m n&oacute;ng lại t&igrave;nh y&ecirc;u sau m&ugrave;a đ&ocirc;ng lạnh gi&aacute;. Sẽ thật hạnh ph&uacute;c khi được  c&ugrave;ng người y&ecirc;u thưởng thức chocolate ngọt ng&agrave;o do ch&iacute;nh tay m&igrave;nh l&agrave;m ra\", Tuấn  t&acirc;m sự.</p>\r\n<p class=\"Normal\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 2, 2731, 0, 1, NULL, NULL),
(16, '100 cặp tình nhân hôn nhau trên khinh khí cầu ', '100-Cap-Tinh-Nhan-Hon-Nhau-Tren-Khinh-Khi-Cau', '100 cặp tình nhân sẽ trao nhau nụ hôn trên khinh khí cầu và được tặng một bó hoa với 999 nụ hồng xanh, nhận \"lời cầu hôn của thần Cupid\"... trong lễ hội Valentine sẽ được tổ chức tại cầu Ánh Sao (quận 7, TP HCM) tối 13/2.\r\n', 'valentine22.jpg', 22, '<h2 class=\"Lead\">100 cặp t&igrave;nh nh&acirc;n sẽ trao nhau nụ h&ocirc;n tr&ecirc;n khinh kh&iacute; cầu v&agrave; được  tặng một b&oacute; hoa với 999 nụ hồng xanh, nhận \"lời cầu h&ocirc;n của thần Cupid\"... trong  lễ hội Valentine sẽ được tổ chức tại cầu &Aacute;nh Sao (quận 7, TP HCM) tối 13/2.</h2>\r\n<p class=\"Normal\">C&oacute; xuất xứ từ văn h&oacute;a phương T&acirc;y, lễ t&igrave;nh nh&acirc;n đ&atilde; trở th&agrave;nh một  trong những ng&agrave;y hội lớn của giới trẻ khắp nơi tr&ecirc;n thế giới. Ở Việt Nam,  Valentine ng&agrave;y c&agrave;ng c&oacute; &yacute; nghĩa quan trọng trong giới trẻ, đặc biệt l&agrave; những cặp  đ&ocirc;i đang y&ecirc;u.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img style=\"border: 1px solid black;\" src=\"../upload/valentine-ngot-ngao.jpg\" alt=\"\" width=\"380\" height=\"499\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c cặp đ&ocirc;i sẽ trao nhau nụ h&ocirc;n tr&ecirc;n khinh kh&iacute;  cầu.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Đ&ecirc;m Valentine thế kỷ lần đầu ti&ecirc;n được tổ chức tr&ecirc;n cầu &Aacute;nh Sao  (quận 7, TP HCM) sẽ đưa c&aacute;c bạn trẻ v&agrave;o thế giới t&igrave;nh y&ecirc;u với mưa bong b&oacute;ng,  vườn hoa hồng xanh được thiết kế như cổ t&iacute;ch.</p>\r\n<p class=\"Normal\">Tại lễ hội T&igrave;nh nh&acirc;n n&agrave;y, 100 cặp t&igrave;nh nh&acirc;n may mắn khắp cả nước  sẽ được tặng 5 m&oacute;n qu&agrave; th&uacute; vị. Theo đ&oacute;, c&aacute;c c&ocirc; g&aacute;i sẽ được bạn trai trao một b&oacute;  hồng xanh với 999 b&ocirc;ng. Từng cặp t&igrave;nh nh&acirc;n sẽ được ban nhạc Flamenco h&aacute;t tặng  ri&ecirc;ng bản t&igrave;nh ca họ y&ecirc;u th&iacute;ch. Đặc biệt, c&aacute;c ch&agrave;ng trai sẽ trao \"người t&igrave;nh  trăm năm\" nụ h&ocirc;n tr&ecirc;n khinh kh&iacute; cầu... C&ocirc; g&aacute;i may mắn nhất trong đ&ecirc;m Valentine  sẽ nhận được chiếc nhẫn cầu h&ocirc;n lấp l&aacute;nh tr&ecirc;n tay \"vị thần Cupid\".</p>\r\n<p class=\"Normal\">Để c&oacute; mặt trong lễ hội 100 cặp t&igrave;nh nh&acirc;n, 99 cặp t&igrave;nh nh&acirc;n phải  thể hiện \"c&acirc;u chuyện t&igrave;nh y&ecirc;u\" ấn tượng nhất trong c&aacute;c phần thi trước đ&oacute; do một  c&ocirc;ng ty tổ chức. Một cặp v&eacute; c&ograve;n lại được đưa ra đấu gi&aacute; v&agrave; đ&atilde; c&oacute; một đ&ocirc;i t&igrave;nh  nh&acirc;n mua với mức 9 triệu đồng.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/anhsao.jpg\" alt=\"\" width=\"480\" height=\"360\" /><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cầu &Aacute;nh Sao (quận 7, TP HCM). <em>Nguồn  Internet.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Song song đ&oacute;, cặp đ&ocirc;i Wanbi Tuấn Anh v&agrave; Trương Quỳnh Anh sẽ lần  đầu ti&ecirc;n chia sẻ c&acirc;u chuyện t&igrave;nh y&ecirc;u của họ sau lần chạm tr&aacute;n trong bộ phim 3D  \"B&oacute;ng ma học đường\". V&agrave; lần đầu ti&ecirc;n,10 nhiếp ảnh gia của Việt Nam sẽ &ldquo;hội tụ&rdquo;  tại Đ&ecirc;m Valentine thế kỷ để ghi dấu những khoảnh khắc t&igrave;nh y&ecirc;u đẹp nhất c&ugrave;ng  những nụ h&ocirc;n thế kỷ dưới bầu trời đ&ecirc;m. Những t&aacute;c phẩm nhiếp ảnh n&agrave;y sẽ được chọn  lọc v&agrave; triển l&atilde;m tại Bức tường đ&ecirc;m Valentine thế kỷ v&agrave;o ng&agrave;y Valentine tại Nh&agrave;  Văn h&oacute;a Thanh ni&ecirc;n.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>T&aacute; L&acirc;m</strong></p>', 2, 1287, 0, 1, NULL, NULL),
(17, 'Nhà thám hiểm 9 tuổi đặt chân tới Nam Cực ', 'Nha-Tham-Hiem-9-Tuoi-Dat-Chan-Toi-Nam-Cuc', 'Vượt qua hành trình dài nhiều ngày, Phạm Vũ Thiều Quang, cậu bé 9 tuổi đã cùng bố đặt chân tới Nam Cực vào chiều mùng 1 Tết. Cậu bé đã trở thành người châu Á trẻ tuổi nhất đặt chân tới vùng đất này.\r\n', 'top-2.jpg', 22, '<h1 class=\"Title\">Nh&agrave; th&aacute;m hiểm 9 tuổi đặt ch&acirc;n tới Nam Cực</h1>\r\n<h2 class=\"Lead\">Vượt qua h&agrave;nh tr&igrave;nh d&agrave;i nhiều ng&agrave;y, Phạm Vũ Thiều Quang, cậu b&eacute; 9  tuổi đ&atilde; c&ugrave;ng bố đặt ch&acirc;n tới Nam Cực v&agrave;o chiều m&ugrave;ng 1 Tết. Cậu b&eacute; đ&atilde; trở th&agrave;nh  người ch&acirc;u &Aacute; trẻ tuổi nhất đặt ch&acirc;n tới v&ugrave;ng đất n&agrave;y.</h2>\r\n<p class=\"Normal\">Chia sẻ tr&ecirc;n facebook của m&igrave;nh, anh Phạm Quang Vinh (bố của  Thiều Quang) cho biết, thời điểm Thiều Quang bước ch&acirc;n l&ecirc;n Nam Cực l&agrave; v&agrave;o l&uacute;c  15h15 ng&agrave;y 3/2. Tuy nhi&ecirc;n, chỉ trước đ&oacute; &iacute;t giờ, khả năng n&agrave;y vẫn c&ograve;n để ngỏ v&igrave;  phụ thuộc ho&agrave;n to&agrave;n v&agrave;o điều kiện thời tiết.</p>\r\n<p class=\"Normal\">Chỉ v&agrave;i giờ trong lần đầu đổ bộ, họ đ&atilde; nhận đủ cả mưa, tuyết  rơi, nắng, thậm ch&iacute; cả trời xanh trong vắt, rồi lại mưa, lại sương m&ugrave;... Theo  anh Vinh, đ&acirc;y l&agrave; lần đầu ti&ecirc;n Thiều Quang được đưa tay hứng những b&ocirc;ng  tuyết.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/13.jpg\" alt=\"Ảnh: Phạm Quang Vinh.\" width=\"490\" height=\"356\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Bố con Thiều Quang trong gi&acirc;y ph&uacute;t đặt ch&acirc;n l&ecirc;n Nam Cực. Ảnh:  <em>Phạm Quang Vinh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Qua những h&igrave;nh ảnh v&agrave; chia sẻ của &ocirc;ng bố n&agrave;y, Thiều Quang đ&atilde;  vượt qua những cơn say s&oacute;ng, nhanh ch&oacute;ng tươi tỉnh khi bước ch&acirc;n l&ecirc;n mảnh đất  Nam Cực, h&aacute;o hức d&ugrave;ng chiếc m&aacute;y ảnh của m&igrave;nh ghi lại h&igrave;nh ảnh ở nơi đ&acirc;y.</p>\r\n<p class=\"Normal\">\"Thiều Quang đ&atilde; được thấy cả sư tử biển v&agrave; chim c&aacute;nh cụt nhảy  s&oacute;ng từ tr&ecirc;n t&agrave;u, v&agrave; gặp những con chim c&aacute;nh cụt Gentoo đầu ti&ecirc;n tr&ecirc;n đảo  Deception, cả mấy con sư tử biển đang l&ecirc;n bờ nằm nghỉ nữa&hellip;\", anh Vinh kể. Đ&acirc;y sẽ  l&agrave; những nh&acirc;n vật trong bộ phim ngắn m&agrave; cậu b&eacute; dự định thực hiện từ trước khi  khởi h&agrave;nh.</p>\r\n<p class=\"Normal\">L&agrave; th&agrave;nh vi&ecirc;n nhỏ tuổi nhất trong chuyến đi, Thiều Quang được cả  đo&agrave;n th&aacute;m hiểm rất y&ecirc;u qu&yacute;. Em thậm ch&iacute; c&ograve;n trở th&agrave;nh vị kh&aacute;ch đặc biệt của  thuyền trưởng con t&agrave;u MS Fram.</p>\r\n<p class=\"Normal\">Với việc tới Nam Cực, cậu b&eacute; lớp 4 Thiều Quang đ&atilde; trở th&agrave;nh  nguời ch&acirc;u &Aacute; trẻ tuổi nhất đặt ch&acirc;n l&ecirc;n mảnh đất n&agrave;y.</p>\r\n<p class=\"Normal\">Sau khi đặt ch&acirc;n tới đảo Deception v&agrave;o ng&agrave;y 3/2, đo&agrave;n th&aacute;m hiểm  đ&atilde; lần lượt tới c&aacute;c trạm nghi&ecirc;n cứu (Base) kh&aacute;c nhau. Đặc biệt tại Base W (tr&ecirc;n  đảo Detaille), Thiều Quang lại được ưu ti&ecirc;n một lần nữa, trở th&agrave;nh người đầu  ti&ecirc;n đặt ch&acirc;n l&ecirc;n đảo Detaille. Em vinh dự được mời k&yacute; v&agrave;o sổ của Base W v&igrave; l&agrave;  trẻ em đầu ti&ecirc;n tr&ecirc;n thế giới tới được địa điểm n&agrave;y.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/10.jpg\" alt=\"Ảnh: Phạm Quang Vinh.\" width=\"490\" height=\"325\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tr&ecirc;n chuyến t&agrave;u tới mảnh đất địa cực, cậu b&eacute; n&agrave;y đ&atilde; trải qua  những cơn say s&oacute;ng. Ảnh: <em>Phạm Quang Vinh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Theo anh Vinh, đo&agrave;n th&aacute;m hiểm thật sự may mắn v&igrave; kh&ocirc;ng phải  chuyến du h&agrave;nh n&agrave;o đến Nam Cực cũng c&oacute; thể đến được Base W. Ngay cả trong thời  tiết rất tốt như trong ng&agrave;y 7/2, t&agrave;u MS Fram cũng phải neo ở kh&aacute; xa bờ, gần một  km để mọi người d&ugrave;ng xuồng chuy&ecirc;n dụng đi v&agrave;o bờ.</p>\r\n<p class=\"Normal\">Sau Base W, Thiều Quang c&ugrave;ng bố sẽ tiếp tục đặt ch&acirc;n tới Base E  v&agrave; Base Y... Chuyến th&aacute;m hiểm dự kiến k&eacute;o d&agrave;i trong 2 tuần qua c&aacute;c v&ugrave;ng đất của  ch&acirc;u lục lạnh gi&aacute; n&agrave;y.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#ffffce\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p class=\"Normal\">\"Trong giờ học buổi s&aacute;ng nay về an to&agrave;n v&agrave; c&aacute;c quy định của  IAATO v&agrave; Hiệp ước Antarctica ch&uacute;ng t&ocirc;i được nhắc lại l&agrave; c&oacute; 3 c&acirc;u hỏi sẽ kh&ocirc;ng c&oacute;  c&acirc;u trả lời trong những chuyến đi Nam Cực, đ&oacute; l&agrave; &ldquo;Khi n&agrave;o ch&uacute;ng ta đến đấy&rdquo;;  &ldquo;Ch&uacute;ng ta sẽ ở đấy bao l&acirc;u&rdquo; v&agrave; &ldquo;Ch&uacute;ng ta c&oacute; chắc chắn sẽ xuống đấy kh&ocirc;ng&rdquo;. Tất  cả mọi thứ phụ thuộc v&agrave;o thời tiết v&agrave; t&igrave;nh trạng của băng. Những g&igrave; ch&uacute;ng t&ocirc;i  biết h&agrave;ng ng&agrave;y l&agrave; kế hoạch A, v&agrave; c&ograve;n c&oacute; kế hoạch B, C v&agrave; D&hellip; cho từng ng&agrave;y, v&agrave;  cho cả những ng&agrave;y kế tiếp...\"</p>\r\n<p class=\"Normal\">\"M&ugrave;a h&egrave; ở Nam Cực khiến đảo Deception kh&ocirc;ng giống một v&ugrave;ng đất  Nam Cực lắm, khi bang tuyết chỉ che phủ những đỉnh n&uacute;i, c&ograve;n ch&acirc;n n&uacute;i th&igrave; lộ ra  rải r&aacute;c những bộ xương c&aacute; voi khổng lổ, những con thuyền gỗ đ&atilde; ở đ&acirc;y từ đầu thế  kỷ 20, v&agrave; cả những ng&ocirc;i mộ của c&aacute;c chiến binh v&agrave; những người đ&atilde; hy sinh ở  đ&acirc;y&hellip;\"</p>\r\n<p class=\"Normal\">\"Ch&uacute;ng t&ocirc;i rời đảo Deception trong trạng th&aacute;i l&acirc;ng l&acirc;ng, trong  đ&oacute; c&oacute; cả việc m&igrave;nh cảm thấy thật may mắn, khi ở tr&ecirc;n bờ đ&atilde; c&oacute; những l&uacute;c nắng rất  đẹp, c&oacute; tuyết rơi&hellip; V&agrave; hơn cả, với t&ocirc;i, l&agrave; khi nghe con trai m&igrave;nh n&oacute;i &ldquo;Small step  of a child, but a big step of the children&rdquo;. Con trai t&ocirc;i đ&atilde; đến Nam Cực v&agrave;o  ng&agrave;y mồng một Tết như t&ocirc;i hứa, v&agrave; thời tiết đ&atilde; ủng hộ cho lời hứa đấy của  t&ocirc;i...\"</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(<em>Tr&iacute;ch facebook của anh Phạm Quang  Vinh</em>)</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', 2, 2898, 0, 1, NULL, NULL),
(18, 'Giới trẻ nô nức xin chữ đầu năm ', 'Gioi-Tre-No-Nuc-Xin-Chu-Dau-Nam', '9h sáng mùng 4 Tết dòng người kéo đến Văn miếu Quốc Tử Giám đông nghẹt. Nhiều bạn trẻ đứng chen chân hàng tiếng đồng hồ để xin được chữ: Trạng nguyên, Đỗ đạt, Trí, Nhân... khi xuân về.\r\n', 'tre-1.jpg', 22, '<h1 class=\"Title\">Giới trẻ n&ocirc; nức xin chữ đầu năm</h1>\r\n<h2 class=\"Lead\">9h s&aacute;ng m&ugrave;ng 4 Tết d&ograve;ng người k&eacute;o đến Văn miếu Quốc Tử Gi&aacute;m đ&ocirc;ng  nghẹt. Nhiều bạn trẻ đứng chen ch&acirc;n h&agrave;ng tiếng đồng hồ để xin được chữ: Trạng  nguy&ecirc;n, Đỗ đạt, Tr&iacute;, Nh&acirc;n... khi xu&acirc;n về.</h2>\r\n<p><img src=\"../upload/giam-1.jpg\" alt=\"\" width=\"490\" height=\"364\" /></p>\r\n<p>Cả trăm người xếp h&agrave;ng tại Văn miếu Quốc Tử Gi&aacute;m để mua v&eacute; l&uacute;c 9h s&aacute;ng.</p>\r\n<p><img src=\"../upload/giam-2.jpg\" alt=\"\" width=\"490\" height=\"343\" /></p>\r\n<p>Nh&oacute;m học sinh trường Thanh Oai B phải bắt xe bu&yacute;t đi từ s&aacute;ng sớm. Họ tỏ ra h&aacute;o  hức khi sở hữu được những tấm v&eacute; v&agrave;o cửa.</p>\r\n<p><img src=\"../upload/giam-4.jpg\" alt=\"\" width=\"490\" height=\"331\" /></p>\r\n<p><img src=\"../upload/giam-6.jpg\" alt=\"\" width=\"490\" height=\"351\" /></p>\r\n<p>B&ecirc;n trong s&acirc;n, cả trăm người chen ch&acirc;n để xin chữ.</p>\r\n<p><img src=\"../upload/giam-7.jpg\" alt=\"\" width=\"490\" height=\"331\" /></p>\r\n<p>C&aacute;c &ocirc;ng đồ phải k&ecirc; 8 b&agrave;n l&agrave;m việc nhưng vẫn kh&ocirc;ng đ&aacute;p ứng kịp nhu cầu của kh&aacute;ch.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/giam-5.jpg\" alt=\"\" width=\"490\" height=\"326\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nguyễn B&aacute; Hiệp (&aacute;o trắng, học sinh lớp 7 trường THCS Nam Trung  Y&ecirc;n, H&agrave; Nội) c&ugrave;ng Nguyễn B&aacute; Hiếu khoe nhau những chữ t&acirc;m đắc m&agrave; họ xin được.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><img src=\"../upload/giam-8.jpg\" alt=\"\" width=\"490\" height=\"348\" /></p>\r\n<p>Ho&agrave;ng Minh Diệp, học sinh lớp 12 trường THPT Thăng Long bảo sau nhiều năm sống ở  H&agrave; Nội nhưng đ&acirc;y l&agrave; lần đầu c&ocirc; đặt ch&acirc;n đến Văn miếu để xin chữ. \"Năm nay em dự  định thi Đại học Kinh tế Quốc d&acirc;n n&ecirc;n đến đ&acirc;y để xin chữ Tr&iacute;. Mong rằng, kết quả  thi sẽ được như mong muốn\", Diệp n&oacute;i.</p>\r\n<p><img src=\"../upload/giam-9.jpg\" alt=\"\" width=\"490\" height=\"341\" /></p>\r\n<p>C&ograve;n với Nguyễn Trung Ki&ecirc;n, cậu học sinh lớp 9 th&igrave; vui mừng cho biết sau 2 tiếng  chen ch&acirc;n cậu cũng xin được chữ \"Trạng Nguy&ecirc;n\". Ki&ecirc;n khoe sang đ&acirc;y kh&ocirc;ng chỉ c&oacute;  m&igrave;nh c&oacute; k&eacute;o th&ecirc;m v&agrave;i người bạn nữa đi c&ugrave;ng.</p>\r\n<p><img src=\"../upload/giam-23.jpg\" alt=\"\" width=\"490\" height=\"357\" /></p>\r\n<p>Nhiều bạn trẻ t&acirc;m sự với <em>VnExpress.net</em>, họ đến Văn miếu kh&ocirc;ng chỉ xin  chữ Tr&iacute;, Dũng m&agrave; cũng cố xin cho được chữ Nh&acirc;n. Họ giải th&iacute;ch, nếu kh&ocirc;ng c&oacute;  \"Nh&acirc;n\", d&ugrave; t&agrave;i giỏi mấy cũng kh&ocirc;ng trở th&agrave;nh được một con người ho&agrave;n thiện.</p>\r\n<p><img src=\"../upload/giam-10.jpg\" alt=\"\" width=\"490\" height=\"349\" /></p>\r\n<p><img src=\"../upload/giam-11.jpg\" alt=\"\" width=\"490\" height=\"303\" /></p>\r\n<p>Những kiểu hong chữ cho ch&oacute;ng kh&ocirc; của c&aacute;c bạn trẻ.</p>\r\n<p style=\"text-align: right;\"><strong>Ho&agrave;ng Anh</strong></p>', 2, 2314, 0, 1, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(19, 'Mong ước đầu năm của giới trẻ ', 'Mong-Uoc-Dau-Nam-Cua-Gioi-Tre', 'Trong năm mới, chàng thủ khoa ĐH Ngoại thương Tăng Văn Bình quyết tâm trau dồi ngoại ngữ để thực hiện ước mơ du học, còn Miss Teen Diễm Trang sẽ dành nhiều thời gian hơn cho hoạt động xã hội, giao lưu quốc tế.\r\n', 'diem-trang-2.jpg', 22, '<h1 class=\"Title\">Mong ước đầu năm của giới trẻ</h1>\r\n<h2 class=\"Lead\">Trong năm mới, ch&agrave;ng thủ khoa ĐH Ngoại thương Tăng Văn B&igrave;nh quyết  t&acirc;m trau dồi ngoại ngữ để thực hiện ước mơ du học, c&ograve;n Miss Teen Diễm Trang sẽ  d&agrave;nh nhiều thời gian hơn cho hoạt động x&atilde; hội, giao lưu quốc tế.</h2>\r\n<h2 class=\"Lead\">Tăng Văn B&igrave;nh, thủ khoa ĐH Ngoại thương H&agrave; Nội: Trau dồi ngoại  ngữ để thực hiện ước mơ du học</h2>\r\n<div class=\"Normal\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/Tang_Van_Binh.jpg\" alt=\"Tăng Văn B&igrave;nh.\" width=\"250\" height=\"195\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Thủ khoa Tăng Văn B&igrave;nh. <em>Ảnh: Ho&agrave;ng  Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">L&agrave; học sinh duy nhất đạt điểm tuyệt đối  trong kỳ thi đại học 2010, Tăng Văn B&igrave;nh (Đ&ocirc; Lương, Nghệ An) được mệnh danh l&agrave;  thủ khoa của c&aacute;c thủ khoa. Sau bốn th&aacute;ng theo học ĐH Ngoại thương, B&igrave;nh đ&atilde; từng  bước l&agrave;m quen với cuộc sống mới, phương ph&aacute;p học mới v&agrave; c&oacute; th&ecirc;m nhiều người  bạn.</p>\r\n</div>\r\n<p class=\"Normal\">\"Em đ&atilde; trưởng th&agrave;nh l&ecirc;n rất nhiều, kh&ocirc;ng c&ograve;n \"g&agrave; g&ocirc;\" như ng&agrave;y  xưa nữa. Đại học Ngoại thương kh&ocirc;ng chỉ là m&ocirc;̣t đi&ecirc;̉m đ&ecirc;́n tr&ecirc;n chặng đường  học t&acirc;̣p m&agrave; c&ograve;n là m&ocirc;̣t gia đình mới, m&ocirc;̣t t&ocirc;̉ &acirc;́m thực sự trong em\", B&igrave;nh  t&acirc;m sự.</p>\r\n<p class=\"Normal\">Ch&agrave;ng thủ khoa đang cố gắng học th&ecirc;m ngoại ngữ để c&oacute; thể thực  hiện ước mơ du học trong một ng&agrave;y gần nhất. B&igrave;nh đ&atilde; l&ecirc;n một kế hoạch chi tiết  cho việc học, v&agrave; đặt những c&aacute;i mốc, những mục ti&ecirc;u để cố gắng. B&ecirc;n cạnh trau dồi  kiến thức, cậu cũng học hỏi th&ecirc;m từ thầy c&ocirc; v&agrave; c&aacute;c bạn nhiều kỹ năng sống để  ho&agrave;n thiện bản th&acirc;n.</p>\r\n<p class=\"Normal\">B&igrave;nh chia sẻ, trong suốt những ng&agrave;y diễn ra Đại hội Đảng XI vừa  qua, cậu lu&ocirc;n theo d&otilde;i th&ocirc;ng tin qua b&aacute;o, đ&agrave;i. B&igrave;nh sung sướng khi đọc những  d&ograve;ng th&ocirc;ng tin th&ocirc;ng b&aacute;o sự th&agrave;nh c&ocirc;ng của Đại hội. Cậu mong rằng, sau mỗi kỳ  đại hội, đất nước ta sẽ c&oacute; một chiến lược mới cho sự ph&aacute;t triển kinh tế.</p>\r\n<p class=\"Normal\">B&igrave;nh cũng hy vọng, những nh&agrave; l&atilde;nh đạo đất nước sẽ đưa ra được  nhiều ch&iacute;nh s&aacute;ch để ph&aacute;t hiện, bồi dưỡng, sử dụng nh&acirc;n t&agrave;i. \"Sự quan t&acirc;m của c&aacute;c  cấp l&atilde;nh đạo ch&iacute;nh l&agrave; động lực để cho những người trẻ ch&uacute;ng em quyết t&acirc;m phấn  đấu v&agrave; cố gắng. B&ecirc;n cạnh đ&oacute;, em mong rằng, giới trẻ cũng sẽ được tạo mọi điều  kiện, cơ hội để cống hiến sức trẻ, nhiệt huyết v&agrave; t&agrave;i năng của m&igrave;nh, g&oacute;p phần  x&acirc;y dựng đất nước\", B&igrave;nh n&oacute;i.</p>\r\n<h2 class=\"Lead\">Thu Nga, Hoa kh&ocirc;i sinh vi&ecirc;n H&agrave; Nội: Ưu ti&ecirc;n h&agrave;ng đầu cho việc học  tập</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/Thu_Nga.jpg\" alt=\"\" width=\"250\" height=\"195\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hoa kh&ocirc;i Thu Nga. <em>Ảnh: Ho&agrave;ng  Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Bước v&agrave;o năm thứ ba đại học, Thu Nga bận rộn hơn với việc học  tập bởi c&ocirc; phải học cả ng&agrave;y ở trường, về nh&agrave; lại b&ugrave; đầu với c&aacute;c b&agrave;i tập chuy&ecirc;n  ng&agrave;nh. X&aacute;c định việc quan trọng nhất thời điểm n&agrave;y l&agrave; việc học, Nga ki&ecirc;n quyết  từ chối những việc l&agrave;m th&ecirc;m ảnh hưởng đến học tập.</p>\r\n<p class=\"Normal\">\"Em gần như kh&ocirc;ng c&oacute; thời gian rảnh v&igrave; ngo&agrave;i việc học ở trường,  em c&ograve;n tham gia học th&ecirc;m c&aacute;c m&ocirc;n học kh&oacute;, học th&ecirc;m c&aacute;c kỹ năng mềm để chuẩn bị  h&agrave;nh trang cho ng&agrave;y ra trường, đi l&agrave;m được tốt\", Nga chia sẻ.</p>\r\n<p class=\"Normal\">Mặc d&ugrave; vậy, Thu Nga vẫn h&agrave;o hứng với những chuyến đi t&igrave;nh  nguyện, từ thiện v&igrave; cộng đồng. \"Em vừa đến với đồng b&agrave;o miền Trung dịp mưa lũ.  Em rất đau l&ograve;ng v&igrave; b&agrave; con m&igrave;nh c&ograve;n rất nhiều người đ&oacute;i khổ. Nếu em c&oacute; thể l&agrave;m g&igrave;  để g&oacute;p ch&uacute;t sức nhỏ, nhất định em sẽ cố gắng hết sức, xứng đ&aacute;ng l&agrave; h&igrave;nh ảnh đại  diện của sinh vi&ecirc;n thủ đ&ocirc;\", Nga n&oacute;i.</p>\r\n<p class=\"Normal\">Dưới con mắt của một sinh vi&ecirc;n chuy&ecirc;n ng&agrave;nh t&agrave;i ch&iacute;nh ng&acirc;n h&agrave;ng,  Thu Nga kỳ vọng trong năm mới, nền kinh tế của đất nước sẽ c&oacute; sự tăng trưởng  tốt, phục hồi sau khủng hoảng, từ đ&oacute; cải thiện được đời sống của người d&acirc;n v&agrave;  tạo th&ecirc;m nhiều việc l&agrave;m cho người lao động.</p>\r\n<p class=\"Normal\">B&ecirc;n cạnh đ&oacute;, c&ocirc; hy vọng, nh&agrave; nước, Bộ Gi&aacute;o dục cũng c&oacute; những cải  c&aacute;ch mạnh mẽ, tạo n&ecirc;n một nền gi&aacute;o dục mạnh v&agrave; sạch, kh&ocirc;ng c&ograve;n bạo lực trong học  đường, để nh&agrave; trường l&agrave; nơi ươm mầm những chủ nh&acirc;n cho tương lai của đất nước.  Nga tin rằng c&aacute;c bạn trẻ ng&agrave;y nay đủ bản lĩnh, lu&ocirc;n khao kh&aacute;t s&aacute;ng tạo, học hỏi  v&agrave; trải nghiệm. Ch&iacute;nh họ sẽ l&agrave;m n&ecirc;n nhiều kỳ t&iacute;ch.</p>\r\n<p class=\"Normal\">\"Nước ta c&ograve;n rất nhiều ho&agrave;n cảnh kh&oacute; khăn, bất hạnh, em mong  rằng giới trẻ sẽ l&agrave; những người đầu ti&ecirc;n sẵn l&ograve;ng sẻ chia, chung tay g&oacute;p sức từ  những h&agrave;nh động b&eacute; nhỏ của m&igrave;nh\", Nga n&oacute;i.</p>\r\n<h2 class=\"Lead\">Miss Teen Diễm Trang: D&agrave;nh nhiều thời gian hơn cho c&aacute;c hoạt động  x&atilde; hội</h2>\r\n<div class=\"Normal\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/Diem-Trang.jpg\" alt=\"Diễm Trang\" width=\"250\" height=\"195\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Diễm Trang. Ảnh: <em>H.S.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">2010 l&agrave; một năm nhiều  bất ngờ v&agrave; đ&aacute;ng nhớ với Diễm Trang khi c&ocirc; vừa đạt danh hiệu &Aacute; kh&ocirc;i của ĐH Quốc  tế ĐH Quốc gia TP HCM vừa trở th&agrave;nh Miss Teen năm 2010. Trang tỏ ra h&agrave;i l&ograve;ng v&igrave;  những th&agrave;nh quả m&igrave;nh đ&atilde; đạt được trong năm qua v&agrave; cảm thấy bản th&acirc;n c&oacute; sự thay  đổi lớn trong suy nghĩ v&agrave; t&iacute;nh c&aacute;ch.</p>\r\n</div>\r\n<p class=\"Normal\">&ldquo;Từ một c&ocirc; g&aacute;i lu&ocirc;n tự ti giờ em đ&atilde; t&igrave;m thấy được gi&aacute; trị bản  th&acirc;n, từ một người nh&uacute;t nh&aacute;t giờ trở n&ecirc;n mạnh dạn hơn, từ một c&ocirc; b&eacute; hay c&oacute; c&aacute;i  nh&igrave;n ti&ecirc;u cực em đ&atilde; biết c&aacute;ch sống lạc quan hơn... v&agrave; từ một c&ocirc; sinh vi&ecirc;n b&igrave;nh  thường giờ lại l&agrave; Miss Teen Việt Nam. Nhưng hơn hết l&agrave; em vui v&igrave; c&oacute; thể l&agrave;m cho  gia đ&igrave;nh tự h&agrave;o về m&igrave;nh&rdquo;, Trang n&oacute;i.</p>\r\n<p class=\"Normal\">Nữ sinh cho biết trong năm mới c&oacute; rất nhiều dự định. Trước mắt  l&agrave; hưởng một c&aacute;i Tết thật vui b&ecirc;n gia đ&igrave;nh v&agrave; ngay sau đ&oacute; Trang sẽ tập trung  nhiều hơn v&agrave;o việc tham gia c&aacute;c hoạt động x&atilde; hội. Gần nhất l&agrave; việc tham gia v&agrave;o  ban tổ chức sự kiện &ldquo;Hội nghị ph&aacute;t triển c&aacute;c nh&agrave; l&atilde;nh đạo v&agrave; trao đổi văn h&oacute;a&rdquo;  với quy m&ocirc; ch&acirc;u &Aacute; Th&aacute;i B&igrave;nh dương diễn ra 7 ng&agrave;y v&agrave;o th&aacute;ng 2. Trong đ&oacute; c&oacute; nhiều  chương tr&igrave;nh giao lưu d&agrave;nh cho sinh vi&ecirc;n của 20 nước ch&acirc;u &Aacute; với sinh vi&ecirc;n Việt  Nam. Trang b&agrave;y tỏ mong muốn sự kiện n&agrave;y sẽ l&agrave; cơ hội cho c&aacute;c bạn trẻ Việt Nam  học hỏi v&agrave; trao đổi văn h&oacute;a với c&aacute;c nước kh&aacute;c.</p>\r\n<p class=\"Normal\">Diễm Trang cũng nu&ocirc;i tham vọng học giỏi hơn nữa trong năm mới v&agrave;  h&igrave;nh ảnh của m&igrave;nh sẽ trở n&ecirc;n gần gũi th&acirc;n thiết hơn với c&aacute;c bạn học sinh, sinh  vi&ecirc;n cả nước v&agrave; c&oacute; thể mang lại những ảnh hưởng t&iacute;ch cực cho c&aacute;c bạn trẻ.</p>\r\n<p class=\"Normal\">Nữ sinh n&agrave;y cho rằng giới trẻ cần cố gắng học hỏi nhiều hơn v&agrave;  c&oacute; chọn lọc, phải qu&ecirc;n đi c&aacute;i t&ocirc;i c&aacute; nhận để nh&igrave;n nhận được những điểm chưa tốt  của bản th&acirc;n từ đ&oacute; c&oacute; thể khắc phục v&agrave; ho&agrave;n thiện m&igrave;nh từng ng&agrave;y. Mạo hiểm chấp  nhận th&aacute;ch thức v&agrave; rủi ro để nắm bắt những cơ hội học hỏi, hơn hết tạo cho bản  th&acirc;n một niềm tin v&agrave;o cuộc sống để c&oacute; thể sống v&agrave; cố gắng hết m&igrave;nh từng  ng&agrave;y.</p>\r\n<h2 class=\"Lead\">Lưu Nguyễn Hồng Quang: Tiếp tục theo đuổi đam m&ecirc; nghi&ecirc;n cứu h&oacute;a  học.</h2>\r\n<p class=\"Normal\">Hai năm li&ecirc;n tiếp gi&agrave;nh huy chương bạc Olympic H&oacute;a quốc tế, Hồng  Quang được tuyển thẳng v&agrave;o đại học, hiện l&agrave; sinh vi&ecirc;n năm nhất khoa h&oacute;a ĐH Khoa  học tự nhi&ecirc;n ĐHQG TP HCM. Mặc d&ugrave; đ&atilde; cố gắng v&agrave; đạt nhiều th&agrave;nh t&iacute;ch trong học  tập, xong Quang vẫn cảm thấy cần phải r&egrave;n luyện sự cẩn thận v&agrave; t&acirc;m l&yacute; thoải m&aacute;i  trước những bước ngoặt lớn trong cuộc sống v&agrave; c&ocirc;ng việc để đạt kết quả cao  hơn.</p>\r\n<p class=\"Normal\">Trong khi nhiều bạn b&egrave; lựa chọn c&aacute;c ng&agrave;nh kinh tế, ngoại thương,  c&ocirc;ng nghệ Quang vẫn quyết định theo đuổi theo đam m&ecirc; của m&igrave;nh để được học v&agrave;  nghi&ecirc;n cứu về H&oacute;a. \"Khi m&igrave;nh đ&atilde; kh&ocirc;ng th&iacute;ch c&ocirc;ng việc n&agrave;o đ&oacute; th&igrave; kh&ocirc;ng thể to&agrave;n  t&acirc;m, to&agrave;n &yacute; với n&oacute;. Điều đ&oacute; c&oacute; thể l&agrave;m ảnh hưởng đến cả những người xung quanh.  Với em bất cứ l&agrave; ng&agrave;nh n&agrave;o m&agrave; m&igrave;nh c&oacute; niềm đam m&ecirc; th&igrave; n&oacute; cũng th&agrave;nh c&ocirc;ng\", Quang  n&oacute;i.</p>\r\n<p class=\"Normal\">Nửa học kỳ l&agrave;m sinh vi&ecirc;n c&oacute; nhiều thay đổi, nhưng Quang kh&ocirc;ng  cảm thấy qu&aacute; bỡ ngỡ v&igrave; may mắn khi l&agrave; học sinh của trường phổ th&ocirc;ng Năng khiếu  thuộc ĐHQG TP HCM, Quang đ&atilde; được l&agrave;m quen với m&ocirc;i trường đại học. Điều kh&aacute;c biệt  r&otilde; rệt hơn đối với em l&agrave; c&oacute; nhiều bạn b&egrave; từ khắp nơi trong nước v&agrave; việc học theo  phương ph&aacute;p nghi&ecirc;n cứu l&agrave; chủ yếu, thầy c&ocirc; chỉ đ&oacute;ng vai tr&ograve; l&agrave; người định hướng.  Chia sẻ về kế hoạch trong năm mới, Quang cho biết đ&atilde; chuẩn bị tinh thần để đi du  học ng&agrave;nh H&oacute;a tại Mỹ v&agrave; trong tương lai xa mong muốn trở th&agrave;nh một nh&agrave; nghi&ecirc;n  cứu khoa học về lĩnh vực n&agrave;y.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n - Ho&agrave;ng  Th&ugrave;y</strong></p>', 2, 2694, 0, 1, NULL, NULL),
(20, 'Giám đốc tuổi mèo và thành tích đáng nể ', 'Giam-Doc-Tuoi-Meo-Va-Thanh-Tich-Dang-Ne', 'Học hết lớp 9, Nguyễn Hữu Năm phải nghỉ học vì nhà nghèo lại đông con, nhưng chàng trai đất Chương Mỹ (Hà Nội) đã xuất sắc giành nhiều giải thưởng sáng tạo trẻ và hiện là chủ công ty chuyên về chế tạo máy.\r\n', 'Nguyen_Huu_Nam_set_sub.jpg', 22, '<h1 class=\"Title\">Gi&aacute;m đốc tuổi m&egrave;o v&agrave; th&agrave;nh t&iacute;ch đ&aacute;ng nể</h1>\r\n<h2 class=\"Lead\">Học hết lớp 9, Nguyễn Hữu Năm phải nghỉ học v&igrave; nh&agrave; ngh&egrave;o lại đ&ocirc;ng  con, nhưng ch&agrave;ng trai đất Chương Mỹ (H&agrave; Nội) đ&atilde; xuất sắc gi&agrave;nh nhiều giải thưởng  s&aacute;ng tạo trẻ v&agrave; hiện l&agrave; chủ c&ocirc;ng ty chuy&ecirc;n về chế tạo m&aacute;y.</h2>\r\n<p class=\"Normal\">Tuổi Đinh M&atilde;o, Nguyễn Hữu Năm lu&ocirc;n thể hiện l&agrave; \"ch&uacute; m&egrave;o\" chăm  chỉ. Ngay từ nhỏ, gi&uacute;p đỡ bố mẹ việc đồng &aacute;ng cũng như l&agrave;m th&ecirc;m m&acirc;y tre đan, Năm  lu&ocirc;n thắc mắc tại sao người n&ocirc;ng d&acirc;n phải bỏ nhiều sức lực. \"C&oacute; c&aacute;ch n&agrave;o để giảm  bớt vất vả\" l&agrave; c&acirc;u hỏi cậu học tr&ograve; ngh&egrave;o lu&ocirc;n trăn trở.</p>\r\n<p class=\"Normal\">Th&ocirc;ng minh, ham học, nhưng v&igrave; nh&agrave; đ&ocirc;ng con (tới 6 anh chị em)  n&ecirc;n tới năm lớp 9 Năm phải nghỉ học để c&ugrave;ng bố mẹ lao động nu&ocirc;i sống gia đ&igrave;nh,  phụ c&aacute;c em ăn học. Kh&ocirc;ng được đến trường, Năm kh&ocirc;ng th&ocirc;i t&igrave;m t&ograve;i. C&aacute;i ước mơ  gi&uacute;p n&ocirc;ng d&acirc;n bớt khổ lu&ocirc;n &acirc;m ỉ trong cậu. Bu&ocirc;ng tay việc đồng &aacute;ng, ch&agrave;ng trai  l&agrave;ng lại lấy đồ hỏng trong nh&agrave; ra th&aacute;o, lắp, t&igrave;m hiểu cấu tạo, quy tr&igrave;nh hoạt  động của n&oacute;.</p>\r\n<p class=\"Normal\">\"Những l&uacute;c kh&ocirc;ng c&oacute; đồ cũ, m&igrave;nh lấy đồ d&ugrave;ng ra th&aacute;o lắp khiến cả  nh&agrave; k&ecirc;u ầm. Vậy l&agrave; đ&agrave;nh gom tiền, ra chợ trời mua đồ về m&agrave;y m&ograve; tự học\", Năm  kể.</p>\r\n<p class=\"Normal\">Năm 2005, con robot đầu tay của Năm với t&ecirc;n gọi DN5 được gửi dự  thi \"&Yacute; tưởng s&aacute;ng tạo d&agrave;nh cho thanh thiếu ni&ecirc;n to&agrave;n quốc lần thứ nhất\". Thật  bất ngờ, robot với khả năng cẩu trục l&agrave;m bằng phế liệu v&agrave; c&aacute;c linh kiện điện tử  mua ở chợ trời đ&atilde; vượt qua 227 s&aacute;ng chế gi&agrave;nh giải nhất. Sản phẩm của Năm sau đ&oacute;  c&ograve;n được lựa chọn tham dự triển l&atilde;m s&aacute;ng tạo quốc tế tổ chức tại Ấn Độ.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/Nguyen_Huu_Nam.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Năm 2010, Năm được Th&agrave;nh Đo&agrave;n H&agrave; Nội tuy&ecirc;n dương gương mặt trẻ  thủ đ&ocirc; ti&ecirc;u biểu. Ảnh: <em>Ho&agrave;ng  Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Với th&agrave;nh t&iacute;ch n&agrave;y, Năm vinh dự nhận được bằng khen của Hội đồng  Đội trung ương c&ugrave;ng Quỹ s&aacute;ng tạo kỹ thuật Việt Nam (VIFOTEC). \"Vinh dự nhất l&agrave;  sau đ&oacute; m&igrave;nh được gặp Đại tướng V&otilde; Nguy&ecirc;n Gi&aacute;p\", Năm n&oacute;i.</p>\r\n<p class=\"Normal\">Gi&agrave;nh giải thưởng l&agrave; động lực tiếp th&ecirc;m sức mạnh để Năm c&agrave;ng  ki&ecirc;n tr&igrave;, m&agrave;y m&ograve; s&aacute;ng tạo những &yacute; tưởng mới. Năm 2006, cậu chế tạo robot c&oacute; chức  năng chống lật cho &ocirc;t&ocirc; mang t&ecirc;n DC26. Được l&agrave;m từ đầu video hỏng của h&agrave;ng x&oacute;m,  mấy mảnh gỗ thừa của gia đ&igrave;nh, sản phẩm n&agrave;y đ&atilde; được nhận giải khuyến kh&iacute;ch.</p>\r\n<p class=\"Normal\">Kh&ocirc;ng dừng lại ở đ&oacute;, Năm quan s&aacute;t việc l&agrave;m gỗ của những người  thợ mộc ở qu&ecirc;, thấy họ phải vất vả d&ugrave;ng sức b&agrave;o gỗ, vậy l&agrave; cậu m&agrave;y m&ograve; t&igrave;m c&aacute;ch  gi&uacute;p đỡ. Sau nhiều lần th&aacute;o lắp, năm 2006, chiếc m&aacute;y b&agrave;o gỗ ra đời. Người thợ  mộc thay v&igrave; cặm cụi l&agrave;m bằng tay đ&atilde; được m&aacute;y hỗ trợ từ b&agrave;o đến xẻ gỗ. Cậu đ&atilde; b&aacute;n  được 5 sản phẩm với gi&aacute; 750.000 đồng mỗi chiếc.</p>\r\n<p class=\"Normal\">Đầu năm 2007, nghĩ rằng c&ocirc;ng việc của m&igrave;nh cần phải \"đưa v&agrave;o quy  củ\", cậu trai l&agrave;ng 19 tuổi thuyết phục gia đ&igrave;nh cho vay vốn để mở xưởng cơ kh&iacute;.  Thương con, bố mẹ đ&atilde; phải b&aacute;n ruộng, thu được 28 triệu đồng giao cho Năm. \"Cầm  số tiền trong tay, vừa mừng, vừa tủi, t&ocirc;i quyết t&acirc;m phải cố gắng để kh&ocirc;ng phụ  l&ograve;ng bố mẹ\", Năm b&ugrave;i ng&ugrave;i.</p>\r\n<p class=\"Normal\">Năm lấy t&ecirc;n m&igrave;nh đặt cho xưởng, một mặt xoay vốn, một mặt tập  hợp thanh ni&ecirc;n trong l&agrave;ng đ&agrave;o tạo cho họ th&agrave;nh thợ h&agrave;n, thợ lắp r&aacute;p, thợ tiện...  C&ograve;n cậu tiếp tục nghi&ecirc;n cứu chiếc m&aacute;y th&aacute;i sắn gi&uacute;p b&agrave; con n&ocirc;ng d&acirc;n. Sau hơn một  năm lắp r&aacute;p, m&aacute;y đ&atilde; ra đời với c&ocirc;ng suất th&aacute;i 2 tấn một giờ. Hai cơ sở chế biến  n&ocirc;ng phẩm đặt mua với gi&aacute; 20 triệu đồng mỗi m&aacute;y. Về sau, m&aacute;y được cải tiến với  c&ocirc;ng suất cao hơn.</p>\r\n<p class=\"Normal\">Quen tay, quen nghề, Năm từng bước thiết kế m&aacute;y đ&ograve;i hỏi kỹ thuật  cao như m&aacute;y đục thủy lực, soi trục đứng... V&agrave; chỉ một năm sau khi ra đời, xưởng  cơ kh&iacute; của ch&agrave;ng trai qu&ecirc; Chương Mỹ đ&atilde; sản xuất th&agrave;nh c&ocirc;ng hơn 40 loại m&aacute;y cơ  kh&iacute;. So với m&aacute;y m&oacute;c c&oacute; c&ugrave;ng chức năng nhập khẩu từ nước ngo&agrave;i, sản phẩm của Năm  c&oacute; gi&aacute; th&agrave;nh rẻ hơn nhiều lần.</p>\r\n<p class=\"Normal\">Đầu năm nay, khi đ&atilde; mở rộng được thị trường, Năm quyết định mở  rộng xưởng, th&agrave;nh lập C&ocirc;ng ty CTM (chế tạo m&aacute;y). Cậu cũng đi học th&ecirc;m đồ họa v&agrave;  tiếng Anh để c&oacute; thể thiết kế sản phẩm tr&ecirc;n m&aacute;y t&iacute;nh.</p>\r\n<p class=\"Normal\">Với những th&agrave;nh c&ocirc;ng của m&igrave;nh, Năm sở hữu rất nhiều bằng, giấy  khen. Năm 2009, cậu được Ban chấp h&agrave;nh trung ương Đo&agrave;n trao tặng danh hiệu người  thợ trẻ giỏi to&agrave;n quốc, được chủ tịch UBND huyện Chương Mỹ tặng giấy khen v&agrave; l&agrave;  một trong 10 gương mặt trẻ thủ đ&ocirc; ti&ecirc;u biểu năm 2010.</p>\r\n<p class=\"Normal\">Bật m&iacute; về b&iacute; quyết th&agrave;nh c&ocirc;ng, Năm chia sẻ: &ldquo;M&igrave;nh kh&ocirc;ng được học  nhiều n&ecirc;n thường đến c&aacute;c xưởng cơ kh&iacute; xem họ sản xuất v&agrave; vận h&agrave;nh m&aacute;y m&oacute;c, từ đ&oacute;  về m&agrave;y m&ograve; l&agrave;m theo. M&igrave;nh nghĩ l&agrave;m việc g&igrave; cũng cần đam m&ecirc; v&agrave; cố gắng&rdquo;.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 2, 2694, 0, 1, NULL, NULL),
(21, 'Học sinh vùng cao nghỉ Tết kéo dài vì giá rét', 'Hoc-Sinh-Vung-Cao-Nghi-Tet-Keo-Dai-Vi-Gia-Ret', 'Học sinh Hà Giang có thể nghỉ Tết Tân Mão gần 20 ngày còn học sinh Lào Cai được nghỉ Tết hơn các vùng khác 3 ngày để tránh giá rét.\r\n', 'sapa9.jpg', 22, '<h1 class=\"Title\">Học sinh v&ugrave;ng cao nghỉ Tết k&eacute;o d&agrave;i v&igrave; gi&aacute; r&eacute;t</h1>\r\n<h2 class=\"Lead\">Học sinh H&agrave; Giang c&oacute; thể nghỉ Tết T&acirc;n M&atilde;o gần 20 ng&agrave;y c&ograve;n học  sinh L&agrave;o Cai được nghỉ Tết hơn c&aacute;c v&ugrave;ng kh&aacute;c 3 ng&agrave;y để tr&aacute;nh gi&aacute; r&eacute;t.</h2>\r\n<p class=\"Normal\">S&aacute;ng 8/1, trao đổi với <em>VnExpress.net</em>, &ocirc;ng Lương Văn  So&ograve;ng, Gi&aacute;m đốc Sở GD&amp;ĐT H&agrave; Giang cho biết, tỉnh n&agrave;y chưa c&oacute; lịch nghỉ Tết  cụ thể v&igrave; thời tiết chưa ổn định. Tuy nhi&ecirc;n so với mọi năm, năm nay r&eacute;t hơn,  nhất l&agrave; v&ugrave;ng n&uacute;i đ&aacute; Đồng Văn, M&egrave;o Vạc. V&igrave; thế, thời gian nghỉ Tết của học sinh  c&oacute; thể được k&eacute;o d&agrave;i gần 20 ng&agrave;y.</p>\r\n<p class=\"Normal\">Theo &ocirc;ng So&ograve;ng, ngay từ đầu năm học, Sở đ&atilde; c&oacute; kế hoạch cụ thể  cho ng&agrave;y bắt đầu v&agrave; kết th&uacute;c năm học. Học sinh H&agrave; Giang khai giảng sớm hơn cả  nước hai tuần. Nếu r&eacute;t hại k&eacute;o d&agrave;i th&igrave; c&aacute;c Ph&ograve;ng Gi&aacute;o dục tự xem x&eacute;t, đề nghị  với UBND huyện cho học sinh được nghỉ.</p>\r\n<p class=\"Normal\">\"Do học trước n&ecirc;n ch&uacute;ng t&ocirc;i kh&ocirc;ng lo chậm chương tr&igrave;nh. Mặt  kh&aacute;c, trong điều kiện gi&aacute; r&eacute;t th&igrave; việc giữ sức khỏe cho học sinh phải được xem  l&agrave; trọng t&acirc;m\", &ocirc;ng So&ograve;ng n&oacute;i.</p>\r\n<p class=\"Normal\"><img src=\"../upload/sapa9.jpg\" alt=\"\" width=\"494\" height=\"349\" /></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Image\">Học sinh ở Sapa đi học trong gi&aacute; r&eacute;t. Ảnh: <em>M&atilde; Anh  L&acirc;m.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Gi&aacute;m đốc Sở GD&amp;ĐT L&agrave;o Cai Trương Kim Minh cho hay d&ugrave; đ&atilde; c&oacute;  kế hoạch cho học sinh tiểu học nghỉ Tết 10 ng&agrave;y, trung học một tuần, bắt đầu từ  28 Tết, nhưng để đối ph&oacute; với thời tiết biến đổi thất thường Sở kh&ocirc;ng quy định  ng&agrave;y nghỉ cứng. C&aacute;c trường đều được trang bị biểu đo nhiệt độ ri&ecirc;ng, căn cứ v&agrave;o  nhiệt độ chiều h&ocirc;m trước, nếu thấy dưới 10 độ C th&igrave; c&oacute; thể b&aacute;o c&aacute;o cho Ph&ograve;ng  Gi&aacute;o dục để xin chỉ đạo của UBND huyện, từ đ&oacute; cho học sinh nghỉ.</p>\r\n<p class=\"Normal\">\"Ch&uacute;ng t&ocirc;i giao quyền chủ động cho c&aacute;c trường bởi chỉ dựa v&agrave;o dự  b&aacute;o thời tiết của tỉnh th&igrave; chưa đủ. V&igrave; khu vực Sapa hay &Ocirc; Qu&yacute; Hồ c&oacute; thể dưới 5  độ C, nhưng v&ugrave;ng Thanh Kim, Thanh Qu&yacute; lại tr&ecirc;n 10 độ C\", &ocirc;ng Minh n&oacute;i v&agrave; cho  biết sau Tết học sinh L&agrave;o Cai được nghỉ k&eacute;o d&agrave;i hơn nơi kh&aacute;c khoảng 3 ng&agrave;y.</p>\r\n<p class=\"Normal\">Gi&aacute;m đốc Sở khẳng định việc chống r&eacute;t t&iacute;ch cực kh&ocirc;ng phải l&agrave; cho  học sinh nghỉ học bởi L&agrave;o Cai c&oacute; những v&ugrave;ng quanh năm r&eacute;t, nếu nghỉ d&agrave;i th&igrave;  kh&ocirc;ng thể ho&agrave;n th&agrave;nh chương tr&igrave;nh. Ch&iacute;nh v&igrave; vậy, ngay từ th&aacute;ng 10/2010, Sở đ&atilde; c&oacute;  kế hoạch đ&oacute;n r&eacute;t v&agrave; r&egrave;n cho học sinh chống chọi với thời tiết.</p>\r\n<p class=\"Normal\">Việc đầu ti&ecirc;n l&agrave; c&aacute;c trường sửa chữa ph&ecirc;n, v&aacute;ch, mua bao tải về  bịt khe hở. Sở GD&amp;ĐT H&agrave; Giang c&ograve;n li&ecirc;n hệ với H&agrave; Nội k&ecirc;u gọi ủng hộ quần &aacute;o  để học sinh giữ ấm. \"Mới đ&acirc;y, Sở đ&atilde; nhận 200 khối quần &aacute;o, đồ d&ugrave;ng cho m&ugrave;a r&eacute;t  từ H&agrave; Nội gửi l&ecirc;n. Số quần &aacute;o n&agrave;y đ&atilde; được ph&acirc;n ph&aacute;t đến tận trường cho học  sinh\", &ocirc;ng Minh cho hay.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 1, 1827, 0, 1, NULL, NULL),
(22, 'Không để thí sinh đăng ký đại học như chơi xổ số', '', '\"Chúng ta không thể để học sinh đăng ký nguyện vọng như chơi sổ xố, đến cuối cùng mới biết kết quả. Hãy để các em thấy rõ cơ hội của mình\", Bộ trưởng GD&ĐT Phạm Vũ Luận phát biểu tại Hội nghị tuyển sinh sáng 18/2. ', 'Pham_Vu_Luan.jpg', 22, '<h1 class=\"Title\">\'Kh&ocirc;ng để th&iacute; sinh đăng k&yacute; đại học như chơi xổ số\'</h1>\r\n<h2 class=\"Lead\">\"Ch&uacute;ng ta kh&ocirc;ng thể để học sinh đăng k&yacute; nguyện vọng như chơi sổ xố, đến cuối c&ugrave;ng mới biết kết quả. H&atilde;y để c&aacute;c em thấy r&otilde; cơ hội của m&igrave;nh\", Bộ trưởng GD&amp;ĐT Phạm Vũ Luận ph&aacute;t biểu tại Hội nghị tuyển sinh s&aacute;ng 18/2.&nbsp;<br /><span style=\"font-size: 10px; font-weight: normal;\">Tại hội nghị tuyển sinh đại học, cao đẳng s&aacute;ng 17/2, đại diện nhiều trường cho biết, c&oacute; nhiều học sinh bằng v&agrave; tr&ecirc;n mức điểm s&agrave;n vẫn trượt đại học, trong khi rất nhiều trường lại kh&ocirc;ng tuyển đủ chỉ ti&ecirc;u như ĐH Đồng Th&aacute;p, ĐH T&acirc;y Nguy&ecirc;n v&agrave; trường ngo&agrave;i c&ocirc;ng lập.</span></h2>\r\n<p class=\"Normal\">Bộ trưởng Gi&aacute;o dục Đ&agrave;o tạo Phạm Vũ Luận khẳng định, năm nay c&aacute;c trường cần thực hiện tuyển nguyện vọng 2, nguyện vọng 3 theo tinh thần c&ocirc;ng khai minh bạch. Th&ocirc;ng tin tuyển sinh phải được cập nhật li&ecirc;n tục tr&ecirc;n Internet để phụ huynh v&agrave; học sinh theo d&otilde;i, nắm được t&igrave;nh h&igrave;nh từ đ&oacute; quyết định chờ kết quả hay r&uacute;t ra đăng k&yacute; nguyện vọng ở trường kh&aacute;c.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/68/fe/Pham_Vu_Luan.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">\"Năm nay ch&uacute;ng ta sẽ tiến h&agrave;nh c&ocirc;ng khai, minh bạch để th&iacute; sinh c&oacute; thể lựa chọn c&aacute;c trường theo nguyện vọng 2, 3 thoải m&aacute;i, kh&ocirc;ng để t&igrave;nh trạng \"cơm treo, m&egrave;o đ&oacute;i\", Bộ trưởng Luận n&oacute;i. Ảnh:<em>Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">\"Ch&uacute;ng ta kh&ocirc;ng thể để học sinh đăng k&yacute; nguyện vọng như chơi sổ xố, đến cuối c&ugrave;ng mở ra mới biết kết quả m&agrave; h&atilde;y để c&aacute;c em thấy r&otilde; cơ hội của m&igrave;nh, phải chọn trường ph&ugrave; hợp với kết quả cho đến khi n&agrave;o kh&ocirc;ng thể lựa chọn được nữa. Ch&uacute;ng ta sẽ kh&ocirc;ng hạn chế cơ hội của c&aacute;c em\", Bộ trưởng Luận n&oacute;i.</p>\r\n<p class=\"Normal\">Bộ trưởng cũng cho biết, thời gian x&eacute;t tuyển kh&ocirc;ng qu&aacute; cứng nhắc, những trường chưa tuyển đủ c&oacute; thể được tuyển th&ecirc;m thời gian khoảng một th&aacute;ng, nhất quyết kh&ocirc;ng để t&igrave;nh trạng \"cơm treo m&egrave;o nhịn đ&oacute;i\".</p>\r\n<p class=\"Normal\">TS Nguyễn Tấn Vui, Ph&oacute; hiệu trưởng ĐH T&acirc;y nguy&ecirc;n đưa ra thực tế những ng&agrave;nh rất quan trọng với đất nước, với v&ugrave;ng như N&ocirc;ng - L&acirc;m - Ngư rất kh&oacute; tuyển sinh v&agrave; hầu như năm n&agrave;o cũng thiếu chỉ ti&ecirc;u trầm trọng. &Ocirc;ng Nguyễn Văn Bản, Ph&oacute; hiệu trưởng ĐH Đồng Th&aacute;p v&agrave; đại diện ĐH H&ugrave;ng Vương cũng n&ecirc;u những kh&oacute; khăn tương tự.</p>\r\n<p class=\"Normal\">Th&ocirc;ng cảm với c&aacute;c trường, Thứ trưởng Bộ Gi&aacute;o dục B&ugrave;i Văn Ga cho rằng, trong trường hợp cần thiết sẽ hạ điểm s&agrave;n đối với những ng&agrave;nh kh&oacute; tuyển tr&ugrave;ng với những ng&agrave;nh m&agrave; đất nước đang ưu ti&ecirc;n để vừa giải quyết kh&oacute; khăn cho c&aacute;c trường, vừa tạo th&ecirc;m điều kiện cho th&iacute; sinh.</p>\r\n<p class=\"Normal\">Tại hội nghị, Bộ trưởng Phạm Vũ Luận đ&atilde; chỉ đạo một số việc cần l&agrave;m để chuẩn bị cho kỳ thi đại học, cao đẳng sắp tới như giao nhiệm vụ cho Cục khảo th&iacute; v&agrave; c&aacute;c cơ quan li&ecirc;n quan sớm ho&agrave;n thiện quy chế thi trắc nghiệm. Đối với th&iacute; sinh l&agrave; người nước ngo&agrave;i, sẽ giao quyền tuyển sinh về cho hiệu trưởng. Ri&ecirc;ng th&iacute; sinh khuyết tật, Bộ trưởng sẽ cố gắng để giao quyền cho hiệu trưởng c&aacute;c trường quyết định cho th&iacute; sinh được miễn thi hay kh&ocirc;ng.</p>\r\n<p class=\"Normal\">\"Vấn đề hậu kiểm rất quan trọng. Sau kỳ tuyển sinh, c&aacute;c trường phải kiểm tra lại b&agrave;i thi v&agrave; Bộ sẽ kiểm tra một lần cuối. Những trường n&agrave;o vi phạm sẽ bị xử l&yacute; nghi&ecirc;m khắc. Sai phạm của năm nay nhưng năm sau mới ph&aacute;t hiện cũng bị xử l&yacute;\", Bộ trưởng nhấn mạnh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/68/fe/thi_dai_hoc.jpg\" border=\"1\" alt=\"Th&iacute; sinh dự thi đại học 2010. Ảnh: Tiến Dũng\" width=\"480\" height=\"339\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Th&iacute; sinh dự thi đại học 2010. Ảnh:&nbsp;<em>Tiến Dũng</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Kỳ thi đại học, cao đẳng 2011 sẽ vẫn thực hiện 3 chung: chung đề, chung đợt, chung kết quả. Đợt một di&ecirc;̃n ra trong hai ng&agrave;y 4 v&agrave; 5, đợt 2 di&ecirc;̃n ra trong hai ng&agrave;y 9 v&agrave; 10 của th&aacute;ng 7. Đợt 3 thi cao đẳng diễn ra trong hai ng&agrave;y 15 v&agrave; 16/7; c&aacute;c m&ocirc;n năng khiếu k&eacute;o d&agrave;i thời gian thi đến 22/7.</p>\r\n<p class=\"Normal\">Dự kiến thời gian nộp hồ sơ theo tuyến Sở gi&aacute;o dục sẽ bắt đầu từ ng&agrave;y 14/3 đến 14/4. C&ograve;n c&aacute;c trường đại học, cao đẳng sẽ bắt đầu nhận từ ng&agrave;y 15/4 đến hết ng&agrave;y 21/4. Để đơn giản h&oacute;a c&aacute;c thủ tục h&agrave;nh ch&iacute;nh đối với th&iacute; sinh tr&uacute;ng tuyển quy định \"nộp hồ sơ tr&uacute;ng tuyển\" khi học sinh nhập học được b&atilde;i bỏ.</p>\r\n<p class=\"Normal\">Quy chế tuyển sinh, những điều cần biết về kỳ thi đại học, cao đẳng sẽ được th&ocirc;ng b&aacute;o tr&ecirc;n website của Bộ trước ng&agrave;y 14/3. Chỉ ti&ecirc;u tuyển sinh của c&aacute;c trường đại học, cao đẳng, trung cấp chuy&ecirc;n nghiệp hệ ch&iacute;nh quy năm 2011 sẽ được x&aacute;c định tr&ecirc;n 2 ti&ecirc;u ch&iacute;: diện t&iacute;ch s&agrave;n x&acirc;y dựng tr&ecirc;n 1 sinh vi&ecirc;n (Diện t&iacute;ch s&agrave;n x&acirc;y dựng phục vụ đ&agrave;o tạo v&agrave; nghi&ecirc;n cứu khoa học d&agrave;nh cho mỗi sinh vi&ecirc;n tối thiểu l&agrave; 2m2) v&agrave; số sinh vi&ecirc;n, học sinh quy đổi tr&ecirc;n 1 giảng vi&ecirc;n, gi&aacute;o vi&ecirc;n (đối với mỗi nh&oacute;m ng&agrave;nh kh&aacute;c nhau tỷ lệ sinh vi&ecirc;n tr&ecirc;n giảng vi&ecirc;n kh&aacute;c nhau). Đối với những cơ sở đ&agrave;o tạo c&oacute; kết quả tuyển sinh năm 2009 v&agrave; 2010 thấp hơn 50% số chỉ ti&ecirc;u được Bộ giao th&ocirc;ng b&aacute;o th&igrave; chỉ ti&ecirc;u tuyển sinh ch&iacute;nh quy năm 2011 được x&aacute;c định tối đa l&agrave; 50%.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#cab5ca\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Normal\">Những trường bị xử phạt vi phạm h&agrave;nh ch&iacute;nh trong tuyển sinh năm 2010 bao gồm: ĐH Kinh doanh v&agrave; c&ocirc;ng nghệ H&agrave; Nội, ĐH Kỹ thuật Y tế Hải Dương, ĐH Thăng Long, CĐ Kinh tế c&ocirc;ng nghiệp H&agrave; Nội, CĐ Viễn Đ&ocirc;ng, CĐ Giao th&ocirc;ng vận tải 2, CĐ X&acirc;y dựng Nam Định, CĐ B&igrave;nh Định, CĐ Y tế B&igrave;nh Thuận, CĐ H&agrave;ng Hải, CĐ X&acirc;y dựng số 2, CĐ Cộng Đồng H&agrave; Nội, CĐ T&agrave;i ch&iacute;nh hải quan, CĐ B&aacute;ch Việt v&agrave; CĐ Kinh tế, kỹ thuật thương mại.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y - Hải Duy&ecirc;n</strong></p>', 1, 3, 0, 1, NULL, NULL),
(23, 'Chụp ảnh cưới tại Flamingo Đại Lải Resort ', 'Chup-Anh-Cuoi-Tai-Flamingo-Dai-Lai-Resort', 'Với hệ sinh thái độc đáo gồm đảo với đàn chim hàng nghìn con cùng nhiều hồ, suối, bán đảo… Flamingo Đại Lải Resort là điểm đến cho các đôi uyên ương muốn tìm nơi lưu giữ khoảnh khắc trăm năm và nghỉ dưỡng cuối tuần.\r\n', 'New_Imagesp2.jpg', 22, '<h1 class=\"Title\">Chụp ảnh cưới tại Flamingo Đại Lải Resort</h1>\r\n<h2 class=\"Lead\">Với hệ sinh th&aacute;i độc đ&aacute;o gồm đảo với đ&agrave;n chim h&agrave;ng ngh&igrave;n con c&ugrave;ng  nhiều hồ, suối, b&aacute;n đảo&hellip; Flamingo Đại Lải Resort l&agrave; điểm đến cho c&aacute;c đ&ocirc;i uy&ecirc;n  ương muốn t&igrave;m nơi lưu giữ khoảnh khắc trăm năm v&agrave; nghỉ dưỡng cuối tuần.</h2>\r\n<p class=\"Normal\">Flamingo Đại Lải Resort tọa lạc tr&ecirc;n c&aacute;c b&aacute;n đảo đẹp ph&iacute;a bắc hồ  Đại Lải, Vĩnh Ph&uacute;c. Nơi đ&acirc;y được bao quanh bởi n&uacute;i, hồ nước, suối v&agrave; rừng c&oacute;  diện t&iacute;ch h&agrave;ng ngh&igrave;n ha, tạo n&ecirc;n một cảnh quan thơ mộng. Kh&ocirc;ng gian xanh rộng,  tầm nh&igrave;n trải tr&ecirc;n mặt nước lớn đến tận c&aacute;c điểm nhấn cảnh quan của cả v&ugrave;ng hồ  n&uacute;i c&ugrave;ng thi&ecirc;n nhi&ecirc;n hoang sơ, gi&uacute;p c&aacute;c đ&ocirc;i bạn trẻ dễ d&agrave;ng t&igrave;m cho m&igrave;nh những  g&oacute;c ảnh đẹp v&agrave; lạ.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img style=\"border: 1px solid black;\" src=\"../upload/New_Image1.jpg\" alt=\"\" width=\"448\" height=\"298\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">B&ecirc;n cạnh những c&aacute;nh rừng th&ocirc;ng, mảng c&acirc;y hoa m&agrave;u, thảm cỏ v&agrave;  rừng cọ, bạn c&oacute; thể ghi lại h&igrave;nh ảnh đ&ocirc;i lứa trước kh&ocirc;ng gian của b&atilde;i cỏ v&agrave; b&atilde;i  tắm của Flamingo Beach Club. Xuy&ecirc;n qua c&aacute;nh rừng th&ocirc;ng, thảm cỏ v&agrave; rừng cọ,  Flamingo Beach Club đem đến cho du kh&aacute;ch kh&ocirc;ng gian m&ecirc;nh m&ocirc;ng của hồ, b&atilde;i c&aacute;t  uốn lượn hướng ra b&aacute;n đảo xanh m&aacute;t. Biển xanh, c&aacute;t trắng, kh&ocirc;ng giang tĩnh lặng  sẽ l&agrave; nền cho những tấm ảnh cưới v&agrave; những gi&acirc;y ph&uacute;t thư gi&atilde;n của đ&ocirc;i uy&ecirc;n ương.  Ngo&agrave;i ra, hai bạn c&ograve;n c&oacute; thể dạo chơi tr&ecirc;n hồ bằng du thuyền, ngắm chim bay về  tổ v&agrave; lưu giữ c&aacute;c bức ảnh hạnh ph&uacute;c l&uacute;c ho&agrave;ng h&ocirc;n.</p>\r\n<p class=\"Normal\">Ngo&agrave;i giờ chụp ảnh, cặp vợ chồng sắp cưới cũng c&oacute; thể tận hưởng  cuộc sống v&agrave;ng son tại Flamingo Đại Lải Resort với đồi cỏ, b&atilde;i tắm tự nhi&ecirc;n,  vườn c&acirc;y ăn quả thư gi&atilde;n, con đường thơ v&agrave; thưởng thức c&aacute;c giai điệu từ hệ thống  đ&agrave;n gi&oacute; cao nhất Việt Nam.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img style=\"border: 1px solid black;\" src=\"../upload/New-Image02.jpg\" alt=\"\" width=\"448\" height=\"169\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&aacute;c đ&ocirc;i uy&ecirc;n ương tới nơi đ&acirc;y cũng c&oacute; thể tham gia chơi golf tại  c&acirc;u lạc bộ Golf &amp; Thể thao hay thả m&igrave;nh tr&ecirc;n mặt hồ với c&acirc;u lạc bộ Du  thuyền. C&acirc;u lạc bộ Chăm s&oacute;c sức khỏe l&agrave; lựa chọn để đắm m&igrave;nh trong kh&ocirc;ng gian  spa hiện đại v&agrave; sang trọng. Những ai y&ecirc;u th&iacute;ch nghệ thuật c&oacute; thể thưởng thức  kh&ocirc;ng gian nghệ thuật cổ điển tại C&acirc;u lạc bộ Văn h&oacute;a &amp; Nghệ thuật hay một bộ  phim hay tại c&acirc;u lạc bộ Giải tr&iacute;.</p>\r\n<p class=\"Normal\">Ngo&agrave;i ra, bạn kh&ocirc;ng thể kh&ocirc;ng đến Flamingo Club v&agrave; Flamingo  Beach Club với c&aacute;c dịch vụ v&agrave; hoạt động giải tr&iacute; cao cấp bao gồm: ẩm thực, bơi,  ch&egrave;o thuyền kayak, xe đạp, tổ chức sự kiện, nghệ thuật, spa&hellip;</p>\r\n<p class=\"Normal\">Để đăng k&yacute; trở th&agrave;nh hội vi&ecirc;n v&agrave; sử dụng dịch vụ với nhiều ưu  đ&atilde;i tại Flamingo Club, li&ecirc;n hệ:</p>\r\n<p class=\"Normal\">Flamingo Club - Flamingo Đại Lải Resort</p>\r\n<p class=\"Normal\">Trụ sở ch&iacute;nh: X&atilde; Ngọc Thanh, Ph&uacute;c Y&ecirc;n, Vĩnh Ph&uacute;c<br />Điện thoại:  02116268689 - Fax: 02116268679<br />Website: <a class=\"Normal\" href=\"http://www.flamingodailai.com/\">http://www.flamingodailai.com/</a></p>\r\n<p class=\"Normal\">Văn ph&ograve;ng tại H&agrave; Nội: 63 H&agrave;ng Trống, Ho&agrave;n Kiếm</p>\r\n<p class=\"Normal\">Điện thoại: 0439285786 - Fax: 0439286388<br />Email: <a class=\"Normal\" href=\"mailto:sales@hvg.com.vn\">sales@hvg.com.vn</a></p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn: <em>Flamingo Đại Lải Resort</em>)</p>', 1, 2534, 0, 1, NULL, NULL),
(24, 'Hỗ trợ gần 3.000 vé xe tết cho sinh viên', 'Ho-Tro-Gan-3.000-Ve-Xe-Tet-Cho-Sinh-Vien', 'Mỗi khi máy bay sắp hạ cánh xuống sân bay Suvarnabhumi, tôi đều có cảm giác mình sắp được trở về nhà, về quê hương thân thương thứ hai của mình. Bạn Đặng Quốc Chí chia sẻ.Khoảng 2.700 sinh viên ngoại tỉnh có hoàn cảnh khó khăn, đang học tập tại TP HCM sẽ được tặng vé xe miễn phí về quê trong dịp tết Tân Mão.', 'sinhvien[1]_1.jpg', 22, '<h1 class=\"Title\">Hỗ trợ gần 3.000 v&eacute; xe tết cho sinh vi&ecirc;n</h1>\r\n<h2 class=\"Lead\">Khoảng 2.700 sinh vi&ecirc;n ngoại tỉnh c&oacute; ho&agrave;n cảnh kh&oacute; khăn, đang học tập tại TP HCM sẽ được tặng v&eacute; xe miễn ph&iacute; về qu&ecirc; trong dịp tết T&acirc;n M&atilde;o.</h2>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; một trong chuỗi c&aacute;c hoạt động hỗ trợ sinh vi&ecirc;n dịp tết của Trung t&acirc;m hỗ trợ sinh vi&ecirc;n TP HCM. Trong đ&oacute;, dự kiến sẽ d&agrave;nh 1.800 v&eacute; cho sinh vi&ecirc;n ngh&egrave;o c&oacute; hộ khẩu thường tr&uacute; tại c&aacute;c tỉnh miền Trung bị thiệt hại nặng nề trong c&aacute;c đợt b&atilde;o lũ vừa qua. 900 v&eacute; c&ograve;n lại sẽ tặng cho c&aacute;c sinh vi&ecirc;n ngh&egrave;o, sinh vi&ecirc;n khuyết tật, sinh vi&ecirc;n d&acirc;n tộc c&aacute;c tỉnh th&agrave;nh kh&aacute;c trong cả nước.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/45/10/sinhvien[1].jpg\" border=\"1\" alt=\"Sinh vi&ecirc;n được hỗ trợ v&eacute; xe trong ng&agrave;y về qu&ecirc; đ&oacute;n tết\" width=\"480\" height=\"328\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Sinh vi&ecirc;n được hỗ trợ v&eacute; xe trong ng&agrave;y về qu&ecirc; đ&oacute;n tết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Ph&oacute; gi&aacute;m đốc trung t&acirc;m hỗ trợ sinh vi&ecirc;n Dương Trọng Ph&uacute;c cho biết, so với c&aacute;c năm trước số lượng v&eacute; hỗ trợ năm nay &iacute;t hơn nhiều v&igrave; kh&ocirc;ng c&oacute; c&aacute;c đơn vị v&agrave; doanh nghiệp đồng h&agrave;nh. Đa phần kinh ph&iacute; trong hoạt động n&agrave;y đều do Ủy ban Mặt trận tổ quốc Việt Nam TP HCM hỗ trợ.</p>\r\n<p class=\"Normal\">Số lượng v&eacute; tặng sẽ đưa xuống c&aacute;c trường theo chỉ ti&ecirc;u. Sinh vi&ecirc;n kh&oacute; khăn trong c&aacute;c diện được x&eacute;t tặng v&eacute; xe c&oacute; thể li&ecirc;n hệ trực tiếp tại văn ph&ograve;ng Đo&agrave;n thanh ni&ecirc;n - Hội sinh vi&ecirc;n, Trung t&acirc;m hỗ trợ sinh vi&ecirc;n của trường trước ng&agrave;y 31/12 để tiến h&agrave;nh l&agrave;m hồ sơ.</p>\r\n<p class=\"Normal\">Ngo&agrave;i tặng v&eacute; xe, Trung t&acirc;m hỗ trợ sinh vi&ecirc;n sẽ cung cấp khoảng 3.500 đầu việc với nhiều loại h&igrave;nh theo tuần, ng&agrave;y, giờ... cho c&aacute;c bạn ở lại c&oacute; nhu cầu kiếm th&ecirc;m thu nhập. Chương tr&igrave;nh vui tết xa nh&agrave; với nhiều hoạt động th&uacute; vị cũng sẽ được tổ chức cho những sinh vi&ecirc;n kh&ocirc;ng c&oacute; điều kiện về qu&ecirc;. Đại diện Th&agrave;nh Đo&agrave;n v&agrave; Trung t&acirc;m hỗ trợ sinh vi&ecirc;n cũng sẽ đi thăm hỏi, giao lưu v&agrave; tặng qu&agrave; cho sinh vi&ecirc;n trong KTX của c&aacute;c trường.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n</strong></p>', 1, 2687, 0, 1, NULL, NULL),
(25, 'Một mình ở Thái Lan ', 'Mot-Minh-O-Thai-Lan', 'Không quá ồn ã tấp nập như Hong Kong, hay quá yên bình như Hội An, Bangkok khiến cho tôi cảm thấy vô cùng phấn khích, tựa hồ như vừa thức giấc sau một cơn ngủ say. Bạn Nguyễn Anh Ngọc viết.\r\n', 'top1.jpg', 22, '<h1 class=\"Title\">Một m&igrave;nh ở Th&aacute;i Lan</h1>\r\n<h2 class=\"Lead\">Kh&ocirc;ng qu&aacute; ồn &atilde; tấp nập như Hong Kong, hay qu&aacute; y&ecirc;n b&igrave;nh như Hội  An, Bangkok khiến cho t&ocirc;i cảm thấy v&ocirc; c&ugrave;ng phấn kh&iacute;ch, tựa hồ như vừa thức giấc  sau một cơn ngủ say. Bạn Nguyễn Anh Ngọc viết.</h2>\r\n<p class=\"Normal\">\"Với nhiều người, du lịch một m&igrave;nh c&oacute; lẽ l&agrave; chuyện đi&ecirc;n rồ.  Nhưng với t&ocirc;i, du lịch một m&igrave;nh lu&ocirc;n mang lại những trải nghiệm tuyệt vời v&agrave; mới  mẻ, nhất l&agrave; tại Th&aacute;i Lan &ldquo;v&ugrave;ng đất của nụ cười&rdquo;.</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"3\" width=\"180\" align=\"center\" bgcolor=\"#eaeaff\">\r\n<tbody>\r\n<tr>\r\n<td><em>*Ảnh:</em> <strong><a href=\"../../gl/xa-hoi/du-lich/2011/02/mot-minh-o-thai-lan/page_1.asp?phpMyAdmin=a4c14c12d106f8f87a752ed51b37018f\">Vẻ đẹp Th&aacute;i  Lan</a></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">T&ocirc;i đặt ch&acirc;n đến Bangkok trong một s&aacute;ng h&egrave; oi nồng với tất cả sự  h&aacute;o hức v&agrave; mong chờ. Th&aacute;i Lan lu&ocirc;n l&agrave; điểm đến số một trong tr&aacute;i tim của t&ocirc;i, v&agrave;  chắc hẳn, của rất nhiều người nữa. Cơ duy&ecirc;n của t&ocirc;i với mảnh đất n&agrave;y dường như  l&agrave; đ&atilde; định trước, Bangkok ch&agrave;o đ&oacute;n t&ocirc;i bằng một bầu kh&ocirc;ng kh&iacute; s&ocirc;i động chưa từng  thấy. Đ&oacute; l&agrave; bầu kh&ocirc;ng kh&iacute; m&agrave; bạn chẳng thể bắt gặp ở bất kỳ một nơi n&agrave;o kh&aacute;c.</p>\r\n<p class=\"Normal\">N&agrave;y l&agrave; khu quảng trường Siam s&ocirc;i động, cửa hiệu san s&aacute;t, những  con hẻm b&aacute;t ng&aacute;t đồ v&agrave; những nhịp cầu thang cuốn lấy nhau đến ch&oacute;ng mặt. N&agrave;y l&agrave;  Ho&agrave;ng Cung lộng lẫy, uy nghi&ecirc;m nhưng cũng thật gần gũi, thanh b&igrave;nh. Những con  đường tr&agrave;n ngập những g&aacute;nh h&agrave;ng rong với mực nướng, tr&aacute;i c&acirc;y, nước quất, ch&egrave; hoa  quả&hellip; rất nhiều, rất ngon, v&agrave; rất rẻ.</p>\r\n<p class=\"Normal\">T&ocirc;i vẫn nhớ nguy&ecirc;n c&aacute;i cảm gi&aacute;c đầu ti&ecirc;n được thưởng thức b&aacute;t mỳ  Tom yum đặc trưng của xứ ấy. Vị cay nồng của ớt, vị ngọt tươi của hải sản, vị  thanh của nước d&ugrave;ng, tất cả h&ograve;a quyện để rồi tạo n&ecirc;n một hương vị độc đ&aacute;o m&agrave;  chẳng nơi n&agrave;o, chẳng m&oacute;n n&agrave;o c&oacute; được. Mọi gi&aacute;c quan của bạn đều được đ&aacute;nh thức ở  đ&acirc;y. Mắt bạn bận t&acirc;m với những cảnh th&uacute; mới mẻ, tai bạn lắng nghe những thanh &acirc;m  rộn r&atilde;&hellip; Chẳng thể c&ograve;n t&acirc;m tr&iacute; để nghĩ &ldquo;&ocirc;i, h&oacute;a ra t&ocirc;i đang một m&igrave;nh ở Bangkok&rdquo;.  Đấy l&agrave; l&uacute;c t&acirc;m người đ&atilde; h&ograve;a với hồn của xứ sở.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/3.jpg\" alt=\"ảnh\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tượng Phật nằm ở Ayutthaya. Ảnh: <em>Nguyễn Anh  Ngọc.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Ayutthaya l&agrave; vương quốc cổ của người Th&aacute;i, nằm c&aacute;ch Bangkok  chừng 70 km. Một m&igrave;nh t&ocirc;i len lỏi giữa quảng trường Chiến Thắng ở Bangkok để t&igrave;m  xe kh&aacute;ch. Một m&igrave;nh t&ocirc;i lang thang suốt khu phố Ayutthaya để t&igrave;m h&agrave;ng cho thu&ecirc; xe  đạp. Rồi một m&igrave;nh t&ocirc;i rong ruổi tr&ecirc;n những con đường kinh đ&ocirc; cổ, lang thang khắp  c&aacute;c ng&ocirc;i ch&ugrave;a cổ ở nơi đ&acirc;y, giữa c&aacute;i nắng chang chang như đổ lửa của một trưa h&egrave;  xứ nhiệt đới.</p>\r\n<p class=\"Normal\">Đ&oacute; c&oacute; lẽ l&agrave; một trong những trải nghiệm th&uacute; vị nhất trong cuộc  đời. Ayutthaya dễ d&agrave;ng m&ecirc; hoặc bất cứ ai bởi vẻ cổ k&iacute;nh đan xen chốn thị th&agrave;nh,  giữa những con đường mới l&agrave; những ng&ocirc;i ch&ugrave;a đ&atilde; mấy thế kỷ. Kh&aacute;i niệm thời gian  v&agrave; kh&ocirc;ng gian bỗng chốc bị x&oacute;a nh&ograve;a, thực tại h&ograve;a lẫn với qu&aacute; khứ.</p>\r\n<p class=\"Normal\">Mỗi ng&ocirc;i đền, ng&ocirc;i ch&ugrave;a đi qua mang lại cho t&ocirc;i những trải  nghiệm kh&aacute;c lạ. N&agrave;y l&agrave; Wat Mahatat với tượng Phật nằm trong gốc c&acirc;y, kia l&agrave; ch&ugrave;a  Wat Phra Si Sanphet như một minh chứng sống động về thời kỳ ho&agrave;ng kim của vương  quốc Ayutthaya xa xưa, v&agrave; kh&ocirc;ng thể qu&ecirc;n Wat Lokaya Suttha với tượng Phật nằm  khổng lồ. V&agrave; c&ograve;n đến h&agrave;ng chục ng&ocirc;i ch&ugrave;a nữa tr&ecirc;n mảnh đất ấy, thật độc đ&aacute;o,  thật kh&oacute; qu&ecirc;n.</p>\r\n<p class=\"Normal\">Điều để lại cho t&ocirc;i nhiều ấn tượng nhất ở Th&aacute;i Lan, kh&ocirc;ng phải  cảnh đẹp, ẩm thực hay chuyện mua sắm, m&agrave; ch&iacute;nh l&agrave; ở người Th&aacute;i. Từng được nghe  nhiều về sự th&acirc;n thiện, dễ mến của con người nơi đ&acirc;y nhưng quả thực, phải gặp gỡ  tận nơi, t&ocirc;i mới thực sự cảm phục. C&ocirc; chủ nh&agrave; trọ nơi t&ocirc;i ở, một người chu đ&aacute;o  chưa từng thấy, tận t&igrave;nh chỉ cho t&ocirc;i từng ng&oacute;c ng&aacute;ch, đ&acirc;u l&agrave; nơi t&ocirc;i c&oacute; thể mua  đồ rẻ nhất ở Bangkok, c&aacute;ch di chuyển bằng thuyền tới Ho&agrave;ng Cung, c&aacute;ch sử dụng  t&agrave;u điện tr&ecirc;n kh&ocirc;ng, những g&igrave; cần tr&aacute;nh v&agrave; kh&ocirc;ng được l&agrave;m&hellip;</p>\r\n<p class=\"Normal\">Rồi c&ocirc; chủ tiệm thu&ecirc; xe ở Ayutthaya, người cho t&ocirc;i thu&ecirc; xe đạp  cả ng&agrave;y với gi&aacute; 30 baht (khoảng 20.000 đồng), nhưng đ&atilde; cho t&ocirc;i rất rất nhiều  thứ, bưu thiếp, bưu ảnh, m&oacute;c đeo... m&agrave; t&ocirc;i tin, lớn hơn con số 30 baht rất  nhiều. Rồi những người bu&ocirc;n b&aacute;n ở chợ Chatuchak, họ hồn nhi&ecirc;n v&agrave; chất ph&aacute;c đến  kh&ocirc;ng ngờ, lu&ocirc;n miệng cười với kh&aacute;ch du lịch, d&ugrave; họ chỉ gh&eacute; qua chỉ để ngắm  ngh&iacute;a, chụp h&igrave;nh. Tận t&igrave;nh gi&uacute;p t&ocirc;i thử từng m&oacute;n đồ, lựa từng thứ m&agrave; vẫn lu&ocirc;n  tươi cười, rạng rỡ.</p>\r\n<p class=\"Normal\">C&oacute; thể bạn cho rằng, đ&oacute; l&agrave; v&igrave; du lịch, n&ecirc;n họ mới như vậy. Cũng  c&oacute; thể, nhưng t&ocirc;i hiểu rằng, nếu như c&aacute;i t&acirc;m kh&ocirc;ng ch&acirc;n th&agrave;nh, th&igrave; sẽ th&agrave;nh đ&oacute;ng  kịch. C&ograve;n những người Th&aacute;i m&agrave; t&ocirc;i đ&atilde; gặp, họ giản dị, ch&acirc;n th&agrave;nh trong từng &aacute;nh  mắt, từng c&aacute;i đưa tay, từng động t&aacute;c, v&agrave; từng lời n&oacute;i. Họ mang đến cho t&ocirc;i một  cảm gi&aacute;c m&agrave; kh&ocirc;ng mảnh đất du lịch n&agrave;o c&oacute; thể l&agrave;m được, sự an t&acirc;m tuyệt đối.</p>\r\n<p class=\"Normal\">Chuyến đi Th&aacute;i Lan đầu ti&ecirc;n của t&ocirc;i đ&atilde; kh&eacute;p lại với đầy dư &acirc;m v&agrave;  &aacute;m ảnh. T&ocirc;i tự đặt cho m&igrave;nh một lời thề, rằng mỗi năm phải quay trở lại nơi ấy  một lần. Để đến những nơi chưa từng đến, để t&igrave;m lại cảm gi&aacute;c ở những nơi đ&atilde; qua,  v&agrave; để t&igrave;m lại con tim m&igrave;nh sau những th&aacute;ng ng&agrave;y vất vả giữa cuộc đời thực. N&oacute;i  một c&aacute;ch giản đơn: T&ocirc;i y&ecirc;u Th&aacute;i Lan.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nguyễn Anh Ngọc</strong></p>', 3, 402, 0, 1, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(26, 'Trai Hà thành trổ tài vật cầu đầu xuân', 'Trai-Ha-Thanh-Tro-Tai-Vat-Cau-Dau-Xuan', 'Những pha tranh cướp quyết liệt cùng những tiếng cười vui là hình ảnh về lễ hội vật cầu đầu xuân của các thanh niên làng Thúy Lĩnh, quận Hoàng Mai (Hà Nội), diễn ra chiều 8/2 (6 Tết).\r\n', '130.jpg', 22, '<h1 class=\"Title\">Trai H&agrave; th&agrave;nh trổ t&agrave;i vật cầu đầu xu&acirc;n</h1>\r\n<h2 class=\"Lead\">Những pha tranh cướp quyết liệt c&ugrave;ng những tiếng cười vui l&agrave; h&igrave;nh  ảnh về lễ hội vật cầu đầu xu&acirc;n của c&aacute;c thanh ni&ecirc;n l&agrave;ng Th&uacute;y Lĩnh, quận Ho&agrave;ng Mai  (H&agrave; Nội), diễn ra chiều 8/2 (6 Tết).</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"../upload/vat-cau-5.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">V&agrave;o mỗi m&ugrave;ng 6 Tết h&agrave;ng năm, thanh ni&ecirc;n trong l&agrave;ng  Th&uacute;y Lĩnh, phường Lĩnh Nam, quận Ho&agrave;ng Mai (H&agrave; Nội) lại trổ t&agrave;i với lễ hội vật  cầu cổ truyền đầu xu&acirc;n.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"../upload/vat-cau-6.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Địa điểm thi đấu nằm ngay b&ecirc;n đ&igrave;nh l&agrave;ng cổ nơi thờ  Linh Lang Đại vương. Tương truyền, Linh Lang l&agrave; hiện th&acirc;n của Th&aacute;i tử Hoằng  Ch&acirc;n, con trai thứ tư của vua L&yacute; Th&aacute;nh T&ocirc;ng (1054-1072).</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"../upload/vat-cau-21.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Một cụ gi&agrave; trong l&agrave;ng kể, tục lệ thi vật cầu đ&atilde; c&oacute; từ  nhiều đời. Ng&agrave;y xưa c&aacute;c trai tr&aacute;ng trong l&agrave;ng tuổi khoảng 18 - 20 mới được thi  đấu. Trang phục của c&aacute;c VĐV n&agrave;y gồm quần trắng, thắt lưng đỏ, m&igrave;nh trần, tượng  trưng cho 8 &ocirc;ng m&atilde;nh hổ cướp b&oacute;ng, cướp cầu. B&oacute;ng gồm bốn quả, người chơi lần  lượt cướp từng quả một, hết bốn quả b&oacute;ng mới tới cướp quả cầu.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"../upload/vat-cau-15.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Tuy nhi&ecirc;n, ng&agrave;y nay c&oacute; đơn giản hơn một ch&uacute;t. Một quả  cầu được sơn son nặng 20kg d&agrave;nh cho thanh ni&ecirc;n, 10kg d&agrave;nh cho thiếu nhi. Hai lứa  tuổi sẽ tham gia tranh t&agrave;i ri&ecirc;ng ở hai phần thi.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"../upload/vat-cau-22.jpg\" alt=\"\" width=\"400\" height=\"603\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Tr&ecirc;n một s&acirc;n rộng chừng 300m2, VĐV phải thật khỏe mới  c&oacute; thể chơi. Thời gian k&eacute;o d&agrave;i v&agrave;o khoảng 2 giờ đồng hồ.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"../upload/vat-cau-2.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Tr&ecirc;n s&acirc;n c&oacute; 4 c&aacute;i hố ở bốn g&oacute;c với 4 m&agrave;u kh&aacute;c nhau. 8  VĐV chia l&agrave;m 4 đội đeo đai theo m&agrave;u hố của m&igrave;nh v&agrave; tham gia cướp cầu.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"../upload/vat-cau-4.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">4 đội sẽ sử dụng chiến thuật l&agrave;m sao để tranh cướp  được quả cầu bằng gỗ 20kg, đường k&iacute;nh hơn 40cm mang về hố của đội m&igrave;nh.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"../upload/vat-cau-20.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">C&aacute;c đấu thủ cố luồn l&aacute;ch, lừa trước đ&oacute;n sau, c&oacute; l&uacute;c  cả 8 người c&ugrave;ng đ&egrave; lấn l&ecirc;n nhau cố cướp bằng được quả cầu trong tay một người  đang &ocirc;m chặt. C&oacute; khi quả cầu ở ngay tr&ecirc;n đầu, hơn chục b&agrave;n tay giơ l&ecirc;n, họ  kiễng, họ nhảy l&ecirc;n cao để đ&oacute;n bằng được tr&aacute;i cầu.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"../upload/vat-cau-9.jpg\" alt=\"\" width=\"495\" height=\"328\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Với mỗi lần mang được cầu về hố người chơi sẽ được  phần thưởng 20.000 đồng, v&agrave; giải cao nhất sẽ thuộc về đội 3 lần li&ecirc;n tiếp để  được quả cầu v&agrave;o hố. Giải thưởng c&oacute; thể l&ecirc;n tới h&agrave;ng triệu đồng cho giải n&agrave;y nếu  c&oacute; nhiều nh&agrave; t&agrave;i trợ l&agrave; c&aacute;c kh&aacute;n giả hảo t&acirc;m.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"../upload/vat-cau-11.jpg\" alt=\"\" width=\"495\" height=\"328\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">H&agrave;ng trăm kh&aacute;n giả v&acirc;y k&iacute;n s&acirc;n đ&igrave;nh cổ vũ bằng những  tiếng cười v&agrave; tr&agrave;ng ph&aacute;o tay kh&ocirc;ng ngớt.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"../upload/vat-cau-17.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Mặc d&ugrave; giải thưởng trong ng&agrave;y thi đấu m&ugrave;ng 6 Tết T&acirc;n  M&atilde;o kh&aacute; lớn, l&ecirc;n tới hơn 3 triệu đồng nhưng c&aacute;c thanh ni&ecirc;n vẫn chơi rất đẹp v&agrave;  quyết liệt, để lại nhiều tiếng cười vui cho người xem. Nhiều lần, c&oacute; thanh ni&ecirc;n  nằm lăn ra nghỉ v&igrave; mệt, trong khi ngay cạnh đ&oacute;, c&aacute;c đấu thủ vẫn hăng h&aacute;i cướp  cho được quả cầu.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"../upload/vat-cau-16.jpg\" alt=\"\" width=\"495\" height=\"328\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">V&agrave; giải cao nhất đ&atilde; thuộc về hai vận động vi&ecirc;n mang  đai đỏ. C&aacute;c anh thở hổn hển v&agrave; kh&ocirc;ng n&oacute;i n&ecirc;n lời trong khi nhận giải từ &ocirc;ng cụ  cao tuổi trong l&agrave;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng H&agrave;</strong></p>', 3, 1782, 0, 1, NULL, NULL),
(27, 'Các bãi biển Đà Nẵng đồng loạt thả chim bồ câu ', 'Cac-Bai-Bien-Da-Nang-Dong-Loat-Tha-Chim-Bo-Cau', 'Sáng nay, Ban quản lý bán đảo Sơn Trà và các bãi biển du lịch thành phố Đà Nẵng đã tổ chức thả chim bồ câu, phát động xây dựng vườn chim hòa bình tại công viên biển Đông.\r\n', 'thachim2.jpg', 22, '<h1 class=\"Title\">C&aacute;c b&atilde;i biển Đ&agrave; Nẵng đồng loạt thả chim bồ c&acirc;u</h1>\r\n<h2 class=\"Lead\">S&aacute;ng nay, Ban quản l&yacute; b&aacute;n đảo Sơn Tr&agrave; v&agrave; c&aacute;c b&atilde;i biển  du lịch th&agrave;nh phố Đ&agrave; Nẵng đ&atilde; tổ chức thả chim bồ c&acirc;u, ph&aacute;t động x&acirc;y dựng vườn  chim h&ograve;a b&igrave;nh tại c&ocirc;ng vi&ecirc;n biển Đ&ocirc;ng.</h2>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; hoạt động nằm trong chương tr&igrave;nh khai cuộc du  xu&acirc;n Sơn Tr&agrave; đầu năm T&acirc;n M&atilde;o. Hơn 1.200 con chim bồ c&acirc;u được thả bay l&ecirc;n bầu  trời tại C&ocirc;ng vi&ecirc;n biển Đ&ocirc;ng tạo n&ecirc;n cảnh quan hấp dẫn cho du kh&aacute;ch trong v&agrave;  ngo&agrave;i nước.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/thachim.jpg\" alt=\"\" width=\"495\" height=\"344\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hơn 1.200 chim bồ c&acirc;u đ&atilde; được thả. Ảnh: <em>Minh  Nhật.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Cũng trong dịp n&agrave;y, c&aacute;c tour tham quan b&aacute;n đảo Sơn Tr&agrave; gồm: lặn  biển ngắm san h&ocirc;, c&acirc;u c&aacute; c&ugrave;ng ngư d&acirc;n, c&acirc;u mực, tham quan v&ograve;ng quanh b&aacute;n đảo Sơn  Tr&agrave; bằng đường thủy v&agrave; đường bộ sẽ được khai trương.</p>\r\n<p class=\"Normal\">B&ecirc;n cạnh đ&oacute;, c&aacute;c hoạt động thể thao tr&ecirc;n biển như lướt s&oacute;ng c&ugrave;ng  Jetski, cưỡi phao chuối, d&ugrave; lượn tr&ecirc;n kh&ocirc;ng, Kayaking&hellip; cũng được khởi động phục  vụ du kh&aacute;ch.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Minh Nhật</strong></p>', 3, 1175, 0, 1, NULL, NULL),
(28, 'Đi tìm cây đa đại thụ hơn nghìn năm tuổi ', 'Di-Tim-Cay-Da-Dai-Thu-Hon-Nghin-Nam-Tuoi', 'Chúng tôi thẳng tiến về Bán đảo Sơn Trà (Đà Nẵng) nơi được mạnh danh là “Mắt thần Đông Dương”. Mục tiêu của chúng tôi là khám phá cây đa cổ thụ hơn nghìn năm tuổi. Đường dốc và ngoằn nghèo khiến du khách có cảm giác như bay lên thiên giới. Bạn Triệu Hòa chia sẻ.\r\n', 'cayda2.jpg', 22, '<h1 class=\"Title\">Đi t&igrave;m c&acirc;y đa đại thụ hơn ngh&igrave;n năm tuổi</h1>\r\n<h2 class=\"Lead\">Ch&uacute;ng t&ocirc;i thẳng tiến về B&aacute;n đảo Sơn Tr&agrave; (Đ&agrave; Nẵng) nơi được mạnh  danh l&agrave; &ldquo;Mắt thần Đ&ocirc;ng Dương&rdquo;. Mục ti&ecirc;u của ch&uacute;ng t&ocirc;i l&agrave; kh&aacute;m ph&aacute; c&acirc;y đa cổ thụ  hơn ngh&igrave;n năm tuổi. Đường dốc v&agrave; ngoằn ngh&egrave;o khiến du kh&aacute;ch c&oacute; cảm gi&aacute;c như bay  l&ecirc;n thi&ecirc;n giới. Bạn Triệu H&ograve;a chia sẻ.</h2>\r\n<p class=\"Normal\">Con đường chạy ngược l&ecirc;n l&ecirc;n đỉnh n&uacute;i Sơn Tr&agrave; (cao 696 m) c&oacute;  nhiều ng&atilde; rẽ để đi, ch&uacute;ng t&ocirc;i chọn hướng dọc c&aacute;c khu biệt thự nơi m&agrave; m&ugrave;a n&agrave;y  sương mờ v&agrave; con người c&oacute; thể ho&agrave; quyện v&agrave;o nhau, b&ecirc;n biển r&igrave; r&agrave;o với những ngọn  s&oacute;ng lăn tăn đập v&agrave;o c&aacute;c thuyền th&uacute;ng của ngư d&acirc;n tụ về để l&ecirc;n bờ chuẩn bị cho  những ng&agrave;y T&ecirc;t ấm c&uacute;ng b&ecirc;n gia đ&igrave;nh.</p>\r\n<p class=\"Normal\">Tr&ecirc;n đường đi c&oacute; 4 đến 5 c&acirc;y cổ thụ nhưng tuổi đời nhỏ hơn, độ  ho&agrave;n thiện kh&ocirc;ng c&oacute; hoặc nằm s&aacute;t khu vực biển n&ecirc;n chưa được bảo tồn chu đ&aacute;o.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img style=\"border: 1px solid black;\" src=\"../upload/cay-da.jpg\" alt=\"c&acirc;y đa\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&acirc;y đa ngh&igrave;n năm tuổi ở Đ&agrave; Nẵng. Ảnh: <em>Triệu H&ograve;a. </em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&acirc;y đa hiện ra trước mắt ch&uacute;ng t&ocirc;i sừng sững hi&ecirc;n ngang với  những t&aacute;n l&aacute; xum xu&ecirc;, chằng chịt. C&acirc;y cao khoảng 20m với nhiều rễ cọc nh&aacute;nh mọc  ra từ t&aacute;n l&aacute; hướng xuống đất khoảng 12m, gồm 9 rễ như vậy. C&oacute; bộ rễ c&oacute; chu vi  l&ecirc;n đến 1 m. H&igrave;nh thế c&acirc;y như c&oacute; b&agrave;n tay con người t&aacute;c động, c&ugrave;ng với sự ph&acirc;n bố  của c&aacute;c c&acirc;y nhỏ hơn ở khu vực xung quanh ch&uacute;ng t&ocirc;i thấy th&iacute;ch th&uacute; về sự huyền b&iacute;  tại khu vực n&agrave;y v&agrave; muốn kh&aacute;m ph&aacute; với &yacute; nghĩ đ&acirc;y c&oacute; thể l&agrave; nơi để giấu kho b&aacute;u v&agrave;  được đ&aacute;nh dấu cẩn thận của c&aacute;c bậc tiền nh&acirc;n!</p>\r\n<p class=\"Normal\">Sư thầy, sau khi c&ugrave;ng đo&agrave;n ch&uacute;ng t&ocirc;i gom r&aacute;c khu vực ph&iacute;a trước  c&acirc;y cổ thụ, đ&atilde; giải th&iacute;ch cho ch&uacute;ng t&ocirc;i biết th&ecirc;m: &rdquo;C&acirc;y đa c&oacute; từ đời n&agrave;o kh&ocirc;ng  ai hay biết, c&oacute; thể tuổi đời hơn cả 1.000 năm. C&acirc;y c&oacute; tr&aacute;i nhỏ m&agrave;u xanh, khi lớn  m&agrave;u v&agrave;ng v&agrave; khi ch&iacute;n m&agrave;u đ&agrave;&rdquo;.</p>\r\n<p class=\"Normal\">Nơi đ&acirc;y c&ograve;n c&oacute; tượng Phật tổ để ch&uacute;ng sinh đi du ngoạn chắp tay  khấn b&aacute;i cầu may cầu phước, được hương kh&oacute;i suốt ng&agrave;y bởi c&aacute;c sư thầy đến từ  Ch&ugrave;a Linh Ứng c&aacute;ch khoảng 4 km.</p>\r\n<p class=\"Normal\">Cảm gi&aacute;c huyền ảo v&agrave; linh thi&ecirc;ng c&ograve;n khiến ch&uacute;ng t&ocirc;i vỡ o&agrave; trong  ngạc nhi&ecirc;n khi từ gốc c&acirc;y đa c&oacute; thể nh&igrave;n ngắm C&ugrave; L&agrave;o Ch&agrave;m quyện mờ trong  m&acirc;y.</p>\r\n<p class=\"Normal\">Điều khiến ch&uacute;ng t&ocirc;i phật l&ograve;ng nhất l&agrave; c&aacute;i th&ugrave;ng r&aacute;c nằm ngay  gốc đa, b&ecirc;n cạnh bức tượng Phật, r&aacute;c rơi vung v&atilde;i. Buồn hơn nữa khi kh&aacute;ch tham  quan đến đ&acirc;y chỉ biết chụp h&igrave;nh, thắp nhang m&agrave; kh&ocirc;ng biết mỗi người mỗi tay &yacute;  thức bỏ r&aacute;c v&agrave;o th&ugrave;ng. Sư thầy đ&atilde; ph&aacute;t động c&ugrave;ng ch&uacute;ng t&ocirc;i gom tất cả c&aacute;c loại  r&aacute;c xung quanh bỏ v&agrave;o th&ugrave;ng, sau một tho&aacute;ng đ&atilde; sạch đẹp v&agrave; dễ nh&igrave;n hơn nhiều.  Mọi người trong đo&agrave;n đều kiến nghị n&ecirc;n dịch th&ugrave;ng r&aacute;c n&agrave;y ra xa hơn khu vực tham  quan v&agrave; Tượng Phật để đảm bảo một kh&ocirc;ng gian kh&aacute;m ph&aacute; l&yacute; tưởng v&agrave; ho&agrave;i niệm chốn  linh thi&ecirc;ng.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Triệu Ho&agrave;</strong></p>', 3, 1706, 0, 1, NULL, NULL),
(30, 'Rêu nảy lộc xuân trên phố cổ Hội An', 'Reu-Nay-Loc-Xuan-Tren-Pho-Co-Hoi-An', 'Trong màn sương lãng đãng, phố cổ Hội An hiện ra như bức tranh bình dị mà sâu lắng. Rêu nảy lộc trên từng ngõ phố, bật mầm trên từng mái ngói cũ xưa gieo nhớ thương, neo lòng du khách trong khoảnh khắc mùa xuân đang về. \r\n\r\n', 'a-top-2-reu-xuan-HA.jpg', 22, '<h1 class=\"Title\">R&ecirc;u nảy lộc xu&acirc;n tr&ecirc;n phố cổ Hội An</h1>\r\n<h2 class=\"Lead\">Trong m&agrave;n sương l&atilde;ng đ&atilde;ng, phố cổ Hội An hiện ra như bức tranh  b&igrave;nh dị m&agrave; s&acirc;u lắng. R&ecirc;u nảy lộc tr&ecirc;n từng ng&otilde; phố, bật mầm tr&ecirc;n từng m&aacute;i ng&oacute;i  cũ xưa gieo nhớ thương, neo l&ograve;ng du kh&aacute;ch trong khoảnh khắc m&ugrave;a xu&acirc;n đang về.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img style=\"border: 1px solid black;\" src=\"../upload/4-2_Anh_1_Reu_xuan_o_pho_co_Hoi_An_gui_VNE_.jpg\" alt=\"\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đ&egrave;n lồng truyền thống phố cổ Hội An đủ sắc m&agrave;u nổi bật trong ng&ocirc;i nh&agrave; cổ m&aacute;i  ng&oacute;i xanh r&ecirc;u.</td>\r\n</tr>\r\n<tr>\r\n<td><img style=\"border: 1px solid black;\" src=\"../upload/4-2_Anh_2_Reu_xuan_o_pho_co_Hoi_An_gui_VNE_.jpg\" alt=\"\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Du kh&aacute;ch mua sắm mặt h&agrave;ng thủ c&ocirc;ng mỹ nghệ trong ng&ocirc;i nh&agrave; cổ ở đường Trần Ph&uacute;,  TP.Hội An.</td>\r\n</tr>\r\n<tr>\r\n<td><img style=\"border: 1px solid black;\" src=\"../upload/4-2_Anh_3_Reu_xuan_o_pho_co_Hoi_An_gui_VNE_.jpg\" alt=\"\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">hay ngắm những ng&ocirc;i nh&agrave; cổ tr&ecirc;n x&iacute;ch l&ocirc;.</td>\r\n</tr>\r\n<tr>\r\n<td align=\"middle\"><img style=\"border: 1px solid black;\" src=\"../upload/4-2_Anh_4_Reu_xuan_o_pho_co_Hoi_An_gui_VNE_.jpg\" alt=\"\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một con hẻm xanh r&ecirc;u nối đường Trần Ph&uacute; với Nguyễn Th&aacute;i Học, TP Hội An.</td>\r\n</tr>\r\n<tr>\r\n<td><img style=\"border: 1px solid black;\" src=\"../upload/4-2_Anh_5_Reu_xuan_o_pho_co_Hoi_An_gui_VNE_.jpg\" alt=\"\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Du kh&aacute;ch thư thả đọc s&aacute;ch dưới ng&ocirc;i nh&agrave; cổ.</td>\r\n</tr>\r\n<tr>\r\n<td align=\"middle\"><img style=\"border: 1px solid black;\" src=\"../upload/4-2_Anh_6_Reu_xuan_o_pho_co_Hoi_An_gui_VNE_.jpg\" alt=\"\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">G&aacute;nh đậu hủ đi b&aacute;n trong một ng&otilde; phố xanh r&ecirc;u ở phố cổ Hội An.</td>\r\n</tr>\r\n<tr>\r\n<td><img style=\"border: 1px solid black;\" src=\"../upload/4-2_Anh_7_Reu_xuan_o_pho_co_Hoi_An_gui_VNE_.jpg\" alt=\"\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">R&ecirc;u nảy lộc chi ch&iacute;t tr&ecirc;n m&aacute;i ng&oacute;i những ng&ocirc;i nh&agrave; cổ ở TP.Hội An.</td>\r\n</tr>\r\n<tr>\r\n<td><img style=\"border: 1px solid black;\" src=\"../upload/4-2_Anh_8_Reu_xuan_o_pho_co_Hoi_An_gui_VNE_.jpg\" alt=\"\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Du kh&aacute;ch ghi lại tấm ảnh kỷ niệm b&ecirc;n bức tường xanh r&ecirc;u ở phố cổ Hội An.</td>\r\n</tr>\r\n<tr>\r\n<td><img style=\"border: 1px solid black;\" src=\"../upload/4-2_Anh_10_Reu_xuan_o_pho_co_Hoi_An_gui_VNE_.jpg\" alt=\"\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một nh&agrave; h&agrave;ng chuy&ecirc;n b&aacute;n đặc sản Cao Lầu ở đường phố Trần Ph&uacute;,TP Hội An.</td>\r\n</tr>\r\n<tr>\r\n<td><img style=\"border: 1px solid black;\" src=\"../upload/4-2_Anh_11_Reu_xuan_o_pho_co_Hoi_An_gui_VNE_.jpg\" alt=\"\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">R&ecirc;u xu&acirc;n nảy lộc tr&ecirc;n khe ng&oacute;i của m&aacute;i nh&agrave; cổ ở Hội An.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tr&iacute; T&iacute;n</strong></p>', 3, 1197, 0, 1, NULL, NULL),
(31, '50.000 du khách đến Huế ăn Tết ', '50.000-Du-Khach-Den-Hue-An-Tet', 'Lượng khách du lịch đến tỉnh Thừa Thiên - Huế ăn Tết năm nay tăng mạnh. Dự kiến sẽ có khoảng 50.000 du khách trong và ngoài nước đến cố đô, tăng khoảng 20.000 khách so với Tết năm trước\r\n', 'set.jpg', 22, '<div style=\"overflow: hidden;\">\r\n<h1 class=\"Title\">50.000 du kh&aacute;ch đến Huế ăn Tết</h1>\r\n<h2 class=\"Lead\">Lượng kh&aacute;ch du lịch đến tỉnh Thừa Thi&ecirc;n - Huế ăn Tết  năm nay tăng mạnh. Dự kiến sẽ c&oacute; khoảng 50.000 du kh&aacute;ch trong v&agrave; ngo&agrave;i  nước đến cố đ&ocirc;, tăng khoảng 20.000 kh&aacute;ch so với Tết năm trước</h2>\r\n<p class=\"Normal\">Ng&agrave;y 4/2, Sở Văn h&oacute;a Thể thao v&agrave; Du lịch tỉnh Thừa  Thi&ecirc;n - Huế cho biết, đ&atilde; c&oacute; rất nhiều c&ocirc;ng ty du lịch đ&atilde; đặt ph&ograve;ng kh&aacute;ch  sạn trước cho du kh&aacute;ch, tập trung nhiều nhất v&agrave;o c&aacute;c ng&agrave;y từ mồng 2 đến  mồng 10 Tết.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/61/cc/bai.jpg\" border=\"1\" alt=\"Du kh&aacute;ch nước ngo&agrave;i x&ocirc;ng đất Đại nội Huế đầu năm mới\" width=\"480\" height=\"308\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Du kh&aacute;ch nước ngo&agrave;i x&ocirc;ng đất Đại nội Huế đầu năm mới. Ảnh: <em>Trần An.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Nguy&ecirc;n nh&acirc;n khiến du kh&aacute;ch đến Huế ăn Tết tăng đột  biến l&agrave; do năm nay địa phương c&oacute; th&ecirc;m nhiều lễ hội, đặc biệt tỉnh tổ  chức khai hội văn h&oacute;a, thể thao v&agrave; du lịch Thừa Thi&ecirc;n - Huế năm 2011,  v&agrave;o ng&agrave;y 10/2, tức mồng 8 Tết.</p>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; hoạt động khởi đầu quảng b&aacute;, tuy&ecirc;n truyền cho  chuỗi c&aacute;c sự kiện văn h&oacute;a, thể thao v&agrave; du lịch trong năm được tổ chức  tại tỉnh, hướng tới năm du lịch quốc gia 2012 do tỉnh đăng cai. Nằm  trong chương tr&igrave;nh khai hội sẽ c&oacute; c&aacute;c chương tr&igrave;nh nghệ thuật, c&aacute;c tr&ograve;  chơi d&acirc;n gian đầu xu&acirc;n, hoạt động carnaval du lịch...</p>\r\n<p class=\"Normal\">Mở đầu cho chương tr&igrave;nh n&agrave;y, từ mồng 2 Tết cho đến rằm  th&aacute;ng Gi&ecirc;ng, ở Thừa Thi&ecirc;n - Huế c&oacute; h&agrave;ng loạt lễ hội như: lễ hội đu ti&ecirc;n  ở Điền H&ograve;a (huyện Phong Điền) v&agrave; thị trấn Sịa, x&atilde; Quảng Thọ (huyện  Quảng Điền); lễ hội đua ghe truyền thống v&agrave; lễ hội vật l&agrave;ng Thủ Lễ ở thị  trấn Sịa; lễ hội cầu ngư ở thị trấn Lăng C&ocirc; (huyện Ph&uacute; Lộc) v&agrave; ở Thuận  An (huyện Ph&uacute; Vang); hội vật l&agrave;ng S&igrave;nh (huyện Ph&uacute; Vang); lễ hội đền  Huyền Tr&acirc;n ở phường An T&acirc;y, TP Huế&hellip;</p>\r\n<p class=\"Normal\">Từ mồng 1 đến mồng 3 Tết, tất cả c&aacute;c di t&iacute;ch tr&ecirc;n địa b&agrave;n tỉnh Thừa Thi&ecirc;n - Huế mở cửa miễn ph&iacute; phục vụ người d&acirc;n.</p>\r\n<p class=\"Normal\"><strong>Trần An</strong></p>\r\n</div>', 3, 1470, 0, 1, NULL, NULL),
(32, '4 độc giả viết về du lịch Thái Lan được tặng quà', '4-Doc-Gia-Viet-Ve-Du-Lich-Thai-Lan-Duoc-Tang-Qua', 'Trong số nhiều độc giả gửi bài tham dự cuộc thi viết \"Trải nghiệm kỳ thú khi du lịch Thái Lan\", Ban biên tập đã lựa chọn 4 bài viết để trao quà tặng cho tháng 1. ', 'a3.jpg', 22, '<div style=\"overflow: hidden;\">\r\n<h1 class=\"Title\">4 độc giả viết về du lịch Th&aacute;i Lan được tặng qu&agrave;</h1>\r\n<h2 class=\"Lead\">Trong số nhiều độc giả gửi b&agrave;i tham dự cuộc thi viết  \"Trải nghiệm kỳ th&uacute; khi du lịch Th&aacute;i Lan\", Ban bi&ecirc;n tập đ&atilde; lựa chọn 4  b&agrave;i viết để trao qu&agrave; tặng cho th&aacute;ng 1.</h2>\r\n<p class=\"Normal\">*Giải nhất l&agrave; một v&eacute; m&aacute;y bay khứ hồi đến Th&aacute;i Lan:</p>\r\n<p class=\"Normal\">- Độc giả bạn Th&ugrave;y Linh với b&agrave;i viết <a class=\"Normal\" href=\"http://vnexpress.net/gl/xa-hoi/du-lich/2011/01/3ba252f8/\">Bangkok, điểm đến của những người th&iacute;ch kh&aacute;m ph&aacute;</a></p>\r\n<p class=\"Normal\">*3 giải khuyến kh&iacute;ch l&agrave; c&aacute;c phần qu&agrave; lưu niệm:</p>\r\n<p class=\"Normal\">- Độc giả Di Linh với b&agrave;i viết <a class=\"Normal\" href=\"http://vnexpress.net/gl/xa-hoi/du-lich/2011/01/3ba25546/\">Tản mạn về Th&aacute;i Lan</a></p>\r\n<p class=\"Normal\">- Độc giả L&ecirc; Trực với b&agrave;i viết <a class=\"Normal\" href=\"http://vnexpress.net/gl/xa-hoi/du-lich/2011/01/3ba2587a/\">Cầu s&ocirc;ng Kwai, nơi g&igrave;n giữ thời gian</a></p>\r\n<p class=\"Normal\">- Độc giả Thuc Nga với b&agrave;i viết <a class=\"Normal\" href=\"http://vnexpress.net/gl/xa-hoi/du-lich/2011/01/3ba25dce/\">Bangkok v&agrave; Pattaya, gi&oacute; v&agrave; những đ&ecirc;m kh&ocirc;ng ngủ </a></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/61/31/a3.jpg\" border=\"1\" alt=\"Ho&agrave;ng Gia\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cung điện Ho&agrave;ng Gia Th&aacute;i Lan lu&ocirc;n đ&ocirc;ng kh&aacute;ch du lịch. Ảnh: <em>Th&ugrave;y Linh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&aacute;c t&aacute;c giả trong danh s&aacute;ch nhận qu&agrave; mời li&ecirc;n hệ với  Anh Khoa, Tổng cục du lịch Th&aacute;i Lan, Lầu 5 - T&ograve;a nh&agrave; Empire - 26 - 28  H&agrave;m Nghi - quận 1, TP HCM, điện thoại: 08.62913885, 0913883685 để nhận  qu&agrave; tặng.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đo&agrave;n Loan</strong></p>\r\n</div>', 3, 2564, 0, 1, NULL, NULL),
(33, 'Lễ hội thổ dân nơi tận cùng thế giới ', 'Le-Hoi-Tho-Dan-Noi-Tan-Cung-The-Gioi', 'Lễ hội thổ dân ở thung lũng Baliem, trên hòn đảo West Papua New Guinea thuộc Indonesia là một điểm nhấn văn hóa mang đậm hơi thở của những bộ tộc hoang dã nhất hành tinh. Bạn Trần Thu Thủy chia sẻ.', '7.jpg', 22, '<h1 class=\"Title\">Lễ hội thổ d&acirc;n nơi tận c&ugrave;ng thế giới</h1>\r\n<h2 class=\"Lead\">Lễ hội thổ d&acirc;n ở thung lũng Baliem, tr&ecirc;n h&ograve;n đảo West  Papua New Guinea thuộc Indonesia l&agrave; một điểm nhấn văn h&oacute;a mang đậm hơi  thở của những bộ tộc hoang d&atilde; nhất h&agrave;nh tinh. Bạn Trần Thu Thủy chia sẻ.</h2>\r\n<p class=\"Normal\">Lễ hội văn h&oacute;a thổ d&acirc;n ở thung lũng Baliem l&agrave; một  trong những t&agrave;i sản văn h&oacute;a phong ph&uacute; v&agrave; độc đ&aacute;o nhất ở Indonesia. Bản  th&acirc;n lễ hội đ&atilde; l&agrave; một sự h&aacute;o hức với ch&iacute;nh thổ d&acirc;n địa phương chứ kh&ocirc;ng  chỉ hấp dẫn với kh&aacute;ch phương xa. Th&aacute;ng 8 l&agrave; thời điểm khắp nơi tr&ecirc;n h&ograve;n  đảo West Papua New Guinea thuộc Indonesia diễn ra c&aacute;c lễ hội.</p>\r\n<p class=\"Normal\">Tham dự v&agrave;o lễ hội thung lũng Baliem, du kh&aacute;ch sẽ c&oacute;  cơ hội t&igrave;m hiểu v&agrave; trải nghiệm trực tiếp những hoạt động mang đậm văn  h&oacute;a thổ d&acirc;n c&ograve;n như thuở sơ khai với sự hiện diện của h&agrave;ng ngh&igrave;n thổ  d&acirc;n. Lễ hội t&aacute;i hiện từ những trận đ&aacute;nh giữa c&aacute;c bộ lạc, những vũ điệu  của chiến binh h&acirc;n hoan chiến thắng c&aacute;c cuộc đi săn đầu người, những  điệu m&uacute;a của thiếu nữ ngực trần, những người đ&agrave;n &ocirc;ng t&oacute;c xoăn da đen lực  lưỡng trong c&aacute;c cuộc đi săn th&uacute;&hellip;</p>\r\n<p class=\"Normal\">Những điệu m&uacute;a xuất ph&aacute;t từ trong cuộc sống rất đỗi  b&igrave;nh dị của họ, từ việc nh&oacute;m lửa, ca h&aacute;t, &acirc;n &aacute;i hay những tranh chấp,  ghen tu&ocirc;ng, c&atilde;i lộn, đ&aacute;nh nhau, giảng h&ograve;a cho đến tục lệ linh thi&ecirc;ng như  ướp x&aacute;c t&ugrave; trưởng. Một điều dễ nhận thấy l&agrave; họ tham gia v&agrave;o lễ hội, họ  nhảy m&uacute;a, họ ca h&aacute;t một c&aacute;ch say sưa, rất đỗi tự nhi&ecirc;n v&agrave; đầy l&ograve;ng nhiệt  t&igrave;nh. Cứ như thể trong m&aacute;u họ đ&atilde; sẵn c&oacute; những nhịp điệu v&agrave; những lời ca  tiếng h&aacute;t.</p>\r\n<p class=\"Normal\">Thổ d&acirc;n đến với lễ hội ai ai cũng muốn ph&ocirc; trương sự  đẹp đẽ của h&igrave;nh thể v&agrave; sự uy nghi của khu&ocirc;n mặt. M&agrave;u trắng ng&agrave; của những  c&aacute;i nanh lợn như t&uacute;a ra từ hốc mũi khiến cho c&aacute;c chiến binh Dani c&agrave;ng  th&ecirc;m vẻ oai phong dữ tợn. Họ lấy đất s&eacute;t c&aacute;c m&agrave;u để vẽ l&ecirc;n mặt, lấy bồ  h&oacute;ng để b&ocirc;i l&ecirc;n l&agrave;n da đ&atilde; sẵn đen b&oacute;ng căng tr&agrave;n sức sống. Họ vẽ c&aacute;c  mảng m&agrave;u lớn, c&aacute;c chấm hoặc c&aacute;c h&igrave;nh tr&ograve;n, lục gi&aacute;c quanh cơ thể, đầu  đội mũ l&ocirc;ng chim sặc sỡ &aacute;nh l&ecirc;n rực rỡ trong nắng v&agrave;ng v&agrave; tr&ecirc;n nền trời  xanh ngăn ngắt&hellip; &Aacute;nh mắt của những người thổ d&acirc;n c&ograve;n nguy&ecirc;n c&aacute;i vẻ hoang  dại nhưng khi họ cười, bạn sẽ nhận thấy ngay th&ocirc;ng điệp th&acirc;n thiện từ  &aacute;nh mắt đ&oacute;.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/60/51/7.jpg\" alt=\"thổ d&acirc;n\" width=\"500\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nanh lợn l&agrave;m tăng vẻ dữ tợn cho đ&agrave;n &ocirc;ng Dani. Ảnh: <em>Thu Thủy.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&oacute; ba bộ lạc ch&iacute;nh sinh sống trong l&ograve;ng thung lũng  Baliem. Tộc người Dani chiếm đa số, người Lai sống ở ph&iacute;a t&acirc;y thung lũng  v&agrave; người Yali ở ph&iacute;a đ&ocirc;ng nam. Mỗi bộ lạc c&oacute; một nền văn h&oacute;a kh&aacute;c biệt.  Một c&aacute;ch chắc chắn v&agrave; th&uacute; vị để ph&acirc;n biệt giữa c&aacute;c bộ lạc ch&iacute;nh l&agrave; quả  bầu (penis gourd) che phần k&iacute;n của người đ&agrave;n &ocirc;ng hay c&ograve;n gọi l&agrave; Koteka.</p>\r\n<p class=\"Normal\">Người Dani sử dụng loại bầu d&agrave;i, nhọn, vỏ mỏng. Ph&iacute;a  b&ecirc;n tr&ecirc;n quả bầu được trang tr&iacute; bằng một nh&uacute;m l&ocirc;ng chim hoặc một mảnh da  th&uacute;. Ngo&agrave;i mục đ&iacute;ch sử dụng koteka để che dương vật, người Lani lại  d&ugrave;ng quả bầu c&oacute; k&iacute;ch thước kh&aacute; to như một nơi để đựng những thứ ri&ecirc;ng tư  của m&igrave;nh v&agrave; thậm ch&iacute; họ đựng cả thực phẩm ở trong đ&oacute;. C&ograve;n người Yali  lại sử dụng Koteka vừa nhỏ, vừa d&agrave;i, thậm ch&iacute; đủ d&agrave;i để n&acirc;ng đỡ những  v&ograve;ng m&acirc;y đeo quanh bụng.</p>\r\n<p class=\"Normal\">Ngo&agrave;i những thứ phụ trang tr&ecirc;n đầu, tr&ecirc;n cổ, phụ nữ  c&aacute;c bộ lạc đều mặc v&aacute;y cỏ. Đặc biệt phụ nữ Yali mặc v&aacute;y ph&acirc;n theo tầng  theo lớp, mỗi lớp tương đương với 4 tuổi. C&ocirc; n&agrave;o v&aacute;y c&oacute; 4 lớp trở l&ecirc;n l&agrave;  đến tuổi lấy chồng. Tr&ecirc;n dọc đường đi s&acirc;u v&agrave;o thung lũng, đ&acirc;u đ&acirc;u t&ocirc;i  cũng thấy những phụ nữ Dani đeo một c&aacute;i t&uacute;i tự đan từ sợi cỏ gọi l&agrave;  &ldquo;noken&rdquo;, d&acirc;y t&uacute;i vắt ngang qua đầu v&agrave; bu&ocirc;ng xuống qu&aacute; lưng, n&oacute; như l&agrave;  một phần thể thiếu trong trang phục của họ vậy. Nhiều năm trước đ&acirc;y, khi  x&atilde; hội văn minh c&ograve;n chưa chạm tới nơi n&agrave;y, vỏ ốc, nanh ch&oacute; v&agrave; lợn c&ograve;n  l&agrave; phương tiện c&oacute; gi&aacute; trị cao để trao đổi h&agrave;ng h&oacute;a, thậm ch&iacute; l&agrave; để đổi  lấy vợ.</p>\r\n<p class=\"Normal\">Đến thung lũng Baliem, tham gia lễ hội thổ d&acirc;n, d&otilde;i  theo những trận đ&aacute;nh giả, h&ograve;a nhịp ch&acirc;n v&agrave;o những điệu m&uacute;a m&ecirc; mải... tất  cả hoạt động lễ hội như dần được hiển thị qua một &ldquo;m&agrave;n h&igrave;nh lớn&rdquo; về văn  h&oacute;a bản địa độc đ&aacute;o v&agrave; đầy m&agrave;u sắc. Nhiều l&uacute;c t&ocirc;i thấy m&igrave;nh bị ngợp như  đang được sống trong kh&ocirc;ng gian của thời kỳ sơ khai nguy&ecirc;n thủy. Ở đ&oacute;  t&ocirc;i t&igrave;m thấy những gi&aacute; trị ch&acirc;n thực về cuộc sống, về kh&aacute;i niệm hạnh  ph&uacute;c rất đỗi giản dị v&agrave; mộc mạc.</p>\r\n<p class=\"Normal\">Dẫu biết cuộc sống kh&ocirc;ng ngừng vận động để hướng về  những điều kiện tốt đẹp hơn cho con người, nhưng vẫn mong sao những n&eacute;t  đẹp văn h&oacute;a của lễ hội thổ d&acirc;n v&ugrave;ng thung lũng Baliem sẽ được giữ lại v&agrave;  c&ograve;n m&atilde;i với thời gian. Mong sao những n&eacute;t đẹp trong đời sống văn h&oacute;a  của họ sẽ lu&ocirc;n l&agrave; những trải nghiệm mới mẻ cho ai từng một lần đến, rồi  tr&oacute;t y&ecirc;u mến những con người hồn hậu, y&ecirc;u cuộc sống tựa thủa hồng hoang  v&agrave; y&ecirc;u mảnh đất c&ograve;n nhiều điều b&iacute; ẩn n&agrave;y.</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#ececff\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p class=\"Normal\">H&agrave;nh tr&igrave;nh bay tới thung lũng Balierm được biết đến l&agrave;  h&agrave;nh tr&igrave;nh bay xa nhất v&agrave; d&agrave;i nhất trong c&aacute;c nước khu vực Đ&ocirc;ng Nam &Aacute;  với 24 giờ bay li&ecirc;n tục v&agrave; với 5 chặng bay: H&agrave; Nội &ndash; Kulalumpur&ndash; Jakatar  &ndash;Ujung Pandang &ndash; Jayapura v&agrave; Wamena.</p>\r\n<p class=\"Normal\">Thị trấn Wamena l&agrave; nơi trung chuyển duy nhất bằng  đường h&agrave;ng kh&ocirc;ng để tiếp cận được với thung lũng Baliem. Chi ph&iacute; ở đ&acirc;y  cao gấp 5 lần so với Jakarta bởi tất cả h&agrave;ng h&oacute;a v&agrave;o được thung lũng đều  bằng đường h&agrave;ng kh&ocirc;ng. Chi ph&iacute; tối thiểu để tổ chức một chuyến đi từ 4  đến 5 ng&agrave;y khoảng 1.700 USD.</p>\r\n<p class=\"Normal\">Từ năm 2010, điều kiện để v&agrave;o thung lũng Baliem v&agrave; tới  thăm c&aacute;c bộ lạc ở đ&acirc;y bắt buộc phải th&ocirc;ng qua một đơn vị tổ chức du  lịch v&agrave; xin giấy ph&eacute;p của cảnh s&aacute;t địa phương.</p>\r\n<p class=\"Normal\">Lễ hội Baliem được tổ chức v&agrave;o những ng&agrave;y kh&ocirc;ng cố  định trong nửa đầu th&aacute;ng 8. Năm 2011, lễ hội được dự kiến tổ chức v&agrave;o  dịp m&ugrave;ng 8 v&agrave; 9 th&aacute;ng 8.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Trần Thu Thủy v&agrave; Nguyễn Lưu Triều Dương</strong></p>', 3, 1719, 0, 1, NULL, NULL),
(34, 'Người Hà Nội du xuân phương Nam tránh rét', 'Nguoi-Ha-Noi-Du-Xuan-Phuong-Nam-Tranh-Ret', 'Lo ngại giá lạnh trong ngày Tết, nhiều gia đình ở Hà Nội đã lên kế hoạch du xuân phương Nam. Một số công ty du lịch cũng tung ra chương trình tour đáp ứng nhu cầu du khách, đặc biệt là người lớn tuổi.', 'thanh-pho1.jpg', 22, '<h1 class=\"Title\">Người H&agrave; Nội du xu&acirc;n phương Nam tr&aacute;nh r&eacute;t</h1>\r\n<h2 class=\"Lead\">Lo ngại gi&aacute; lạnh trong ng&agrave;y Tết, nhiều gia đ&igrave;nh ở H&agrave;  Nội đ&atilde; l&ecirc;n kế hoạch du xu&acirc;n phương Nam. Một số c&ocirc;ng ty du lịch cũng tung  ra chương tr&igrave;nh tour đ&aacute;p ứng nhu cầu du kh&aacute;ch, đặc biệt l&agrave; người lớn  tuổi.</h2>\r\n<p class=\"Normal\">H&agrave;ng năm cứ qua ng&agrave;y m&ugrave;ng 1 Tết l&agrave; gia đ&igrave;nh chị Hương  Linh (Ho&agrave;ng Mai, H&agrave; Nội) lại du lịch, kh&aacute;m ph&aacute; c&aacute;c miền đất nước n&ecirc;n gia  đ&igrave;nh chị đi chơi xa thay v&igrave; thăm bạn b&egrave;, họ h&agrave;ng. Tết n&agrave;y, khi tiết  trời miền Bắc gi&aacute; buốt chị liền đặt v&eacute; m&aacute;y bay cho cả gia đ&igrave;nh du xu&acirc;n  phương Nam, vừa kết hợp thăm họ h&agrave;ng vừa cho bọn trẻ đến c&aacute;c điểm vui  chơi s&ocirc;i động của TP HCM.</p>\r\n<p class=\"Normal\">\"T&ocirc;i từng đến TP HCM v&agrave;o dịp Tết, th&agrave;nh phố kh&ocirc;ng qu&aacute;  đ&ocirc;ng người, c&aacute;c h&agrave;ng qu&aacute;n mở cửa nhộn nhịp, điểm vui chơi nhiều, rất  th&iacute;ch hợp cho tụi trẻ. T&ocirc;i th&iacute;ch nhất l&agrave; dạo chơi v&agrave; ngắm hoa xu&acirc;n ở  đường hoa Nguyễn Huệ v&agrave; c&ocirc;ng vi&ecirc;n Tao Đ&agrave;n. Nếu ở H&agrave; Nội, cho bọn trẻ đi  chơi nhiều trong c&aacute;i gi&aacute; lạnh, ch&uacute;ng rất dễ ốm\", chị Linh n&oacute;i.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5f/5a/thanh-pho1.jpg\" alt=\"th&agrave;nh phố HCM\" width=\"300\" height=\"450\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Du kh&aacute;ch tham quan nh&agrave; thờ lớn ở TP HCM. Ảnh: <em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Kh&ocirc;ng chỉ chị Linh, gia đ&igrave;nh anh Ho&agrave;ng L&ecirc;, quận Long  Bi&ecirc;n, cũng đang chuẩn bị h&agrave;nh l&yacute; cho chuyến du xu&acirc;n phương Nam. Anh cho  biết, bố anh vừa trải qua đợt ốm v&igrave; gi&aacute; r&eacute;t n&ecirc;n cần cho cụ thay đổi  kh&ocirc;ng kh&iacute;. Cả gia đ&igrave;nh 5 người gi&agrave; trẻ c&ugrave;ng khởi h&agrave;nh v&agrave;o m&ugrave;ng 2 Tết cho  chuyến đi TP HCM v&agrave; Cần Thơ.</p>\r\n<p class=\"Normal\">\"Đ&acirc;y l&agrave; lần đầu ti&ecirc;n đi chơi xa nh&agrave; dịp Tết n&ecirc;n ch&uacute;ng  t&ocirc;i phải mua tour từ c&ocirc;ng ty lữ h&agrave;nh, kh&ocirc;ng phải tự lo việc đi lại, ăn  nghỉ. Hy vọng sẽ c&oacute; chuyến đi vui vẻ, ấn tượng đầu năm mới\", anh L&ecirc; n&oacute;i.</p>\r\n<p class=\"Normal\">Theo &ocirc;ng Trần Văn Long, Gi&aacute;m đốc c&ocirc;ng ty Du lịch Việt,  năm nay du kh&aacute;ch đặt tour từ H&agrave; Nội đi du lịch TP HCM kh&aacute; đ&ocirc;ng, đa số  l&agrave; người gi&agrave;. Từ đợt r&eacute;t đậm, r&eacute;t hại, mỗi tuần đơn vị tổ chức cho một  đo&agrave;n kh&aacute;ch 25-30 người v&agrave;o phương Nam vui chơi. Trong dịp Tết, mỗi ng&agrave;y  c&oacute; một đo&agrave;n du kh&aacute;ch khởi h&agrave;nh, ri&ecirc;ng ng&agrave;y m&ugrave;ng 2 c&oacute; 2 đo&agrave;n.</p>\r\n<p class=\"Normal\">&Ocirc;ng Long cho biết, do miền Bắc r&eacute;t triền mi&ecirc;n n&ecirc;n  trước Tết gia đ&igrave;nh &ocirc;ng đ&atilde; đ&oacute;n c&aacute;c cụ lớn tuổi v&agrave;o ph&iacute;a nam tr&aacute;nh r&eacute;t. Từ  việc của gia đ&igrave;nh, &ocirc;ng nảy sinh kế hoạch tổ chức tour \"Nắng ấm phương  Nam\" cho người ph&iacute;a Bắc. Năm nay, số lượng kh&aacute;ch đặt tour chưa lớn, song  c&aacute;c năm tới c&oacute; thể sẽ tăng cao khi nhiều người c&oacute; th&oacute;i quen đi chơi xa  dịp Tết.</p>\r\n<p class=\"Normal\">Tour \"Nắng ấm phương Nam\" đi bằng m&aacute;y bay từ H&agrave; Nội  đến TP HCM hoặc Cần Thơ, trong 7-8 ng&agrave;y, du kh&aacute;ch sẽ được tham quan TP  HCM, sau đ&oacute; xuống miền T&acirc;y, hoặc nghỉ ngơi ở c&aacute;c resort tại Phan Thiết.  &Ocirc;ng Long cho biết, c&aacute;c dịch vụ du lịch như nh&agrave; h&agrave;ng, kh&aacute;ch sạn tại ph&iacute;a  Nam đều sẵn s&agrave;ng, kh&ocirc;ng c&oacute; t&igrave;nh trạng đ&oacute;ng cửa như ở H&agrave; Nội, n&ecirc;n du  kh&aacute;ch ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m.</p>\r\n<p class=\"Normal\">Theo chị Đo&agrave;n Thanh Tr&agrave;, đại diện Saigontourist, đơn  vị sẽ phục vụ hơn 500 kh&aacute;ch từ c&aacute;c tỉnh th&agrave;nh trong cả nước với c&aacute;c  chương tr&igrave;nh Tết tại TP HCM. Tham gia tour \"Tết c&ugrave;ng người S&agrave;i G&ograve;n\", du  kh&aacute;ch c&oacute; dịp thưởng ngoạn hội hoa xu&acirc;n tại c&ocirc;ng vi&ecirc;n văn h&oacute;a Tao Đ&agrave;n,  đến ch&ugrave;a cầu may, xin lộc, tận hưởng kh&ocirc;ng kh&iacute; đ&oacute;n xu&acirc;n tưng bừng tại  khu vực Chợ Lớn.</p>\r\n<p class=\"Normal\">Đặc biệt, du kh&aacute;ch được thưởng thức bữa cơm ấm c&uacute;ng  với nhiều m&oacute;n đặc sản ng&agrave;y Tết, nhấm nh&aacute;p b&aacute;nh mứt c&ugrave;ng ấm tr&agrave; xanh đ&oacute;n  xu&acirc;n đồng thời đ&oacute;n nhận từ tay gia chủ m&oacute;n qu&agrave; l&igrave; x&igrave; may mắn. Tour Tết ở  đồng bằng s&ocirc;ng Cửu Long lại mang kh&ocirc;ng kh&iacute; chuẩn bị Tết đặc trưng của  b&agrave; con v&ugrave;ng s&ocirc;ng nước: s&ocirc;i động chợ nổi, tưng bừng chợ hoa; viếng ch&ugrave;a;  nghe đờn ca t&agrave;i tử&hellip;</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đo&agrave;n Loan</strong></p>', 3, 530, 0, 1, NULL, NULL),
(35, 'Tôi đi trốn rét ở Thái Lan', 'Toi-Di-Tron-Ret-O-Thai-Lan', 'Với tour tự do đi trốn rét này, được bạn người Thái hướng dẫn, chúng tôi thăm thú mấy đô thị nhỏ lân cận Pattaya, đều trong tỉnh Chonburi, Đông Nam nước Thái. Bạn An Bình chia sẻ.', 'thai1.jpg', 22, '<h1 class=\"Title\">T&ocirc;i đi trốn r&eacute;t ở Th&aacute;i Lan</h1>\r\n<h2 class=\"Lead\">Với tour tự do đi trốn r&eacute;t n&agrave;y, được bạn người Th&aacute;i  hướng dẫn, ch&uacute;ng t&ocirc;i thăm th&uacute; mấy đ&ocirc; thị nhỏ l&acirc;n cận Pattaya, đều trong  tỉnh Chonburi, Đ&ocirc;ng Nam nước Th&aacute;i. Bạn An B&igrave;nh chia sẻ.</h2>\r\n<p class=\"Normal\">Lần đầu đến Th&aacute;i của t&ocirc;i cũng đ&atilde; kh&aacute; l&acirc;u. Hồi đ&oacute;, mới  xuống m&aacute;y bay đ&atilde; thấy ngộp. S&acirc;n bay Đ&ocirc;n Mường qu&aacute; rộng v&agrave; b&acirc;y giờ l&agrave; s&acirc;n  bay Suvarnabhumi c&ograve;n lớn v&agrave; hiện đại hơn nhiều. Từ c&aacute;c khu vực l&agrave;m thủ  tục đến khu vực chuẩn bị l&ecirc;n m&aacute;y bay đi bộ &ldquo;tướt bơ&rdquo;, nhưng kh&ocirc;ng &ldquo;to&aacute;t  mồ h&ocirc;i hột&rdquo;, v&igrave; trong nh&agrave; rất m&aacute;t mặc d&ugrave; ngo&agrave;i trời n&oacute;ng như m&ugrave;a h&egrave; ở  ta.</p>\r\n<p class=\"Normal\">Đầu ti&ecirc;n l&agrave; quận Chonburi, nghỉ trưa tr&ecirc;n b&atilde;i biển.  H&agrave;ng loạt lều qu&aacute;n san s&aacute;t chạy dọc bờ biển xấp xỉ c&acirc;y số, với c&ugrave;ng một  kiểu bạt che, ghế gỗ sofa. V&eacute; ngồi/nằm nghỉ chỉ 30 baht/người (chưa bằng  b&aacute;t phở b&igrave;nh d&acirc;n ở H&agrave; Nội), c&oacute; thể ngồi cả ng&agrave;y lu&ocirc;n, nếu bạn th&iacute;ch.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5f/c4/thai1.jpg\" alt=\"Th&aacute;i Lan\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">S&ocirc;ng Chao Phraya chảy qua trung t&acirc;m BKK. Ảnh: <em>An B&igrave;nh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Pattaya đ&uacute;ng l&agrave; th&agrave;nh phố du lịch. Chao &ocirc;i c&aacute;c qu&aacute;n  bar chật k&iacute;n bởi du kh&aacute;ch, m&agrave; chủ yếu l&agrave; người T&acirc;y. Khu vực  Centrralfestival v&agrave; kh&aacute;ch sạn Hilton s&aacute;t ngay bờ biển c&ograve;n đ&ocirc;ng hơn nữa.  To&agrave; nh&agrave; gửi &ocirc;t&ocirc; k&iacute;n đặc xe n&ecirc;n ch&uacute;ng t&ocirc;i phải l&ecirc;n tầng ch&oacute;t v&oacute;t 11, may  qu&aacute; c&oacute; xe ra. Si&ecirc;u thị thật l&agrave; lộng lẫy, nườm nượp người mua h&agrave;ng hoặc  kết hợp đi chơi hoặc chỉ đi chơi. Ở trung t&acirc;m thương mại n&agrave;y c&oacute; một h&agrave;ng  phở, trương biển &ldquo;Saigon Bistro&rdquo;.</p>\r\n<p class=\"Normal\">Buổi tối, tr&ecirc;n bờ biển ngay s&aacute;t đ&oacute;, cơ man n&agrave;o l&agrave; c&aacute;c bộ b&agrave;n ghế v&agrave; du kh&aacute;ch ngồi ăn nhậu hoặc chỉ uống.</p>\r\n<p class=\"Normal\">Ở chợ nổi Pattaya<strong> </strong>t&ocirc;i nh&igrave;n thấy một  c&ocirc; g&aacute;i nhỏ mặt mũi h&iacute; hửng với chiếc gối tr&ecirc;n tay. Đi du lịch m&agrave; mang  gối l&agrave;m g&igrave;? T&eacute; ra đ&oacute; l&agrave; qu&agrave; tr&uacute;ng thưởng tr&ograve; chơi giải tr&iacute; ở chợ. N&oacute; sẽ  được k&ecirc; đầu tr&ecirc;n xe b&aacute;n tải - loại xe được sử dụng rất nhiều ở Th&aacute;i,  thật l&agrave; tiện lợi.</p>\r\n<p class=\"Normal\">Cả khu chợ nổi nằm tr&ecirc;n mặt hồ, nh&agrave; nổi, cầu nổi, cứ  san s&aacute;t. Người ng&oacute; nghi&ecirc;ng xem h&agrave;ng, người tay x&aacute;ch n&aacute;ch mang, tạo ra  c&aacute;c d&ograve;ng người chen vai s&aacute;t c&aacute;nh c&ugrave;ng đi. C&oacute; h&igrave;nh tượng ch&uacute; g&agrave; trống đặc  trưng rất Th&aacute;i, đang d&otilde;ng dạc ng&acirc;n vang tr&ecirc;n mặt nước. Bạn thử cảm gi&aacute;c  đu người tr&ecirc;n d&acirc;y c&aacute;p chưa. Độ cao, khoảng c&aacute;ch như trong ảnh, b&ecirc;n dưới  l&agrave; m&ecirc;nh m&ocirc;ng nước. Ho&agrave;n to&agrave;n kh&aacute;c ngồi cabin c&aacute;p treo đấy bạn ạ.</p>\r\n<p class=\"Normal\">Cũng gần Pattaya c&oacute; một trang trại trồng nho &ldquo;nho nhỏ&rdquo;  m&agrave; thu h&uacute;t du kh&aacute;ch nhiều kh&ocirc;ng ngờ. Sản phẩm của trang trại đ&ocirc;ng nghịt  người mua, n&agrave;o l&agrave; nước &eacute;p nho c&oacute; ga, kem vị nho, kem c&oacute; nho kh&ocirc;, b&aacute;nh  ngọt c&oacute; nho&hellip;</p>\r\n<p class=\"Normal\">Sattahip - một th&agrave;nh phố nhỏ s&aacute;t biển, phong c&aacute;ch  Th&aacute;i, thanh b&igrave;nh, c&aacute;ch Pattaya chỉ khoảng 30 km th&ocirc;i. Rất đ&ocirc;ng du kh&aacute;ch  nước ngo&agrave;i gh&eacute; tới đ&acirc;y.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5f/c4/thai2.jpg\" alt=\"Khu phố T&agrave;u ở BKK Ảnh: An B&igrave;nh\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Khu phố T&agrave;u ở Bang kok. Ảnh: <em>An B&igrave;nh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Sớm tinh mơ t&ocirc;i được rủ đi chợ Chong, s&aacute;t ngay cảng  c&aacute;, thật sảng kho&aacute;i. Chợ c&aacute;, chợ rau ngo&agrave;i trời m&agrave; xen kẽ l&agrave; c&aacute;c sạp  h&agrave;ng đơn sơ b&agrave;y b&aacute;n cả quần &aacute;o, mỹ phẩm&hellip; Nhiều thứ c&ograve;n rẻ hơn ở VN.</p>\r\n<p class=\"Normal\">B&atilde;i tắm Sai Keaw Beach nhỏ, hẹp, đ&ocirc;ng du kh&aacute;ch. Bạn  biết kh&ocirc;ng, địa điểm du lịch n&agrave;y thuộc khu vực qu&acirc;n sự đấy, thế m&agrave; vẫn  được l&agrave;m du lịch.</p>\r\n<p class=\"Normal\">Chỗ ch&uacute;ng t&ocirc;i nghỉ lại ở Bangkok l&agrave; một v&ugrave;ng ven đ&ocirc;  c&aacute;ch s&acirc;n bay Suvarnabhumi khoảng 12 km. Độ d&agrave;i đường đi n&agrave;y chả l&agrave; g&igrave; so  với loại đ&ocirc; thị cực lớn như BKK. Muốn v&agrave;o trung t&acirc;m th&agrave;nh phố, người  d&acirc;n v&ugrave;ng ven thường chọn c&aacute;ch đi mấy chặng: xe hơi, t&agrave;u điện ngầm, t&agrave;u  điện tr&ecirc;n cao rồi xe bu&yacute;t l&agrave; nhanh v&agrave; rẻ nhất.</p>\r\n<p class=\"Normal\">Nếu kh&ocirc;ng muốn đi đường bộ, mời bạn đi taxi đường s&ocirc;ng  tr&ecirc;n chiếc t&agrave;u nhỏ, cũng v&agrave;o loại rẻ. M&agrave;u nước s&ocirc;ng Chao Phraya gần  bằng c&aacute;c s&ocirc;ng T&ocirc; Lịch, Kim Ngưu, Nhuệ của H&agrave; Nội vậy m&agrave; người Th&aacute;i vẫn  l&agrave;m được du lịch tr&ecirc;n s&ocirc;ng.</p>\r\n<p class=\"Normal\">Ch&uacute;ng t&ocirc;i gh&eacute; ch&ugrave;a Mangkon Kamalawat ở khu vực n&agrave;y,  cũng thắp hương, khấn mong Đức Phật ph&ugrave; hộ như rất đ&ocirc;ng người Th&aacute;i đang  l&agrave;m quanh ch&uacute;ng t&ocirc;i.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>An B&igrave;nh</strong></p>\r\n<div class=\"fl\"><img src=\"http://vnexpress.net/Images/Point/TopLeft.gif\" alt=\"\" /></div>', 3, 1128, 0, 1, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(36, '3 tiêu đề và biểu tượng du lịch Việt Nam đoạt giải cao', '3-Tieu-De-Va-Bieu-Tuong-Du-Lich-Viet-Nam-Doat-Giai-Cao', 'Tối 26/1, Tổng cục Du lịch đã công bố và trao giải cuộc thi \"Tiêu đề - Biểu tượng\" cho chương trình xúc tiến du lịch Việt Nam giai đoạn 2011 - 2015.', 'logo6.jpg', 22, '<div style=\"overflow: hidden;\">\r\n<h1 class=\"Title\">3 ti&ecirc;u đề v&agrave; biểu tượng du lịch Việt Nam đoạt giải cao</h1>\r\n<h2 class=\"Lead\">Tối 26/1, Tổng cục Du lịch đ&atilde; c&ocirc;ng bố v&agrave; trao giải cuộc  thi \"Ti&ecirc;u đề - Biểu tượng\" cho chương tr&igrave;nh x&uacute;c tiến du lịch Việt Nam  giai đoạn 2011 - 2015.</h2>\r\n<p class=\"Normal\">Ban tổ chức đ&atilde; trao giải nhất cho ti&ecirc;u đề v&agrave; biểu  tượng \"Việt Nam - Sự kh&aacute;c biệt &Aacute; Đ&ocirc;ng\", trị gi&aacute; 50 triệu đồng; giải nh&igrave;  l&agrave; \"Đất nước của những nụ cười\" trị gi&aacute; 30 triệu đồng v&agrave; giải ba \"Việt  Nam - Niềm tự h&agrave;o ch&acirc;u &Aacute;\" với 20 triệu đồng. Ngo&agrave;i ra, Ban tổ chức c&ograve;n  trao 7 giải khuyến kh&iacute;ch cho c&aacute;c t&aacute;c phẩm, trị gi&aacute; 10 triệu đồng.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5e/39/logo6.jpg\" alt=\"Logo v&agrave; slogan được giải nhất\" width=\"450\" height=\"200\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Biều tượng v&agrave; ti&ecirc;u đề giải nhất. Ảnh: <em>Đo&agrave;n Loan.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Sau gần 4 th&aacute;ng ph&aacute;t động, Ban tổ chức đ&atilde; nhận được  413 t&aacute;c phẩm của 223 t&aacute;c giả, trong đ&oacute; c&oacute; nhiều t&aacute;c phẩm của c&aacute; c&ocirc;ng ty  chuy&ecirc;n nghiệp trong lĩnh vực tư vấn, x&acirc;y dựng thương hiệu trong nước v&agrave;  quốc tế. Ban gi&aacute;m khảo đ&atilde; chọn được 10 t&aacute;c phẩm v&agrave;o chung kết.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5e/39/logo5.jpg\" alt=\"\" width=\"450\" height=\"200\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Biểu tượng v&agrave; ti&ecirc;u đề đạt giải nh&igrave;. Ảnh:<em> Đo&agrave;n Loan.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">&Ocirc;ng Nguyễn Mạnh Cường, Ph&oacute; tổng cục trưởng Tổng cục Du  lịch Việt Nam, cho biết c&aacute;c t&aacute;c phẩm lọt v&agrave;o chung kết đều đạt chất  lượng, c&oacute; nhiều &yacute; tưởng v&agrave; c&oacute; thể ph&aacute;t triển thương hiệu x&uacute;c tiến cho du  lịch Việt Nam.</p>\r\n<p class=\"Normal\">Giai đoạn 2010-2020, ng&agrave;nh du lịch phấn đấu đạt tốc độ  tăng trưởng kh&aacute;ch quốc tế 10-15% v&agrave; kh&aacute;ch nội địa đạt 15-18% mỗi năm.  Để thu h&uacute;t kh&aacute;ch như mục ti&ecirc;u đề ra, ng&agrave;nh sẽ đẩy mạnh ph&aacute;t triển thương  hiệu v&agrave; chương tr&igrave;nh x&uacute;c tiến du lịch.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5e/39/logo4.jpg\" alt=\"\" width=\"450\" height=\"200\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Biều tượng v&agrave; ti&ecirc;u đề giải ba. Ảnh: <em>Đo&agrave;n Loan</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Dự kiến trong qu&yacute; 1 năm nay, ng&agrave;nh du lịch sẽ lựa chọn  ti&ecirc;u đề v&agrave; biểu tượng ch&iacute;nh thức cho chương tr&igrave;nh x&uacute;c tiến giai đoạn  2011-2015.</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"3\" width=\"190\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<form method=\"POST\"><input type=\"hidden\" name=\"phpMyAdmin\" value=\"a4c14c12d106f8f87a752ed51b37018f\" /></form> \r\n<tbody>\r\n<tr style=\"vertical-align: top;\">\r\n<td style=\"height: 3px; background: url(../../images/vote/bg-top.gif) repeat-x scroll center top #ffffff;\" colspan=\"2\"><img src=\"http://vnexpress.net/images/vote/left-top.gif\" alt=\"\" /></td>\r\n<td class=\"txtr\" style=\"height: 3px; background: url(../../images/vote/bg-top.gif) repeat-x scroll center top #ffffff;\"><img src=\"http://vnexpress.net/images/vote/right-top.gif\" alt=\"\" /></td>\r\n</tr>\r\n<tr style=\"vertical-align: top;\">\r\n<td style=\"width: 4px; height: 21px; background: url(../../images/vote/bg-left.gif) repeat-y scroll center top #ffffff;\"><br /></td>\r\n<td style=\"height: 21px; vertical-align: top; padding-left: 8px; padding-top: 1px; padding-bottom: 2px; background-color: #ffffff;\"><label class=\"link-folder\">Thăm d&ograve; &yacute; kiến</label></td>\r\n<td style=\"width: 4px; height: 21px; background: url(../../images/vote/bg-right.gif) repeat-y scroll center top #ffffff;\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td style=\"background: url(../../images/vote/bg-left-c.gif) repeat-y scroll center top #ffffff;\"><br /></td>\r\n<td style=\"background-color: #eeeedd;\" valign=\"top\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" height=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td width=\"100%\" valign=\"top\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" height=\"100%\">\r\n<tbody>\r\n<tr valign=\"top\">\r\n<td style=\"padding: 10px 0px 10px 8px; background-color: #eeeedd;\"><label style=\"font: bold 12px arial; color: #4d4d4d;\">Bạn th&iacute;ch ti&ecirc;u đề v&agrave; biểu tượng n&agrave;o nhất?</label></td>\r\n</tr>\r\n<tr height=\"100%\" valign=\"top\">\r\n<td style=\"height: 100%; padding-left: 7px; background-color: #eeeedd;\" valign=\"top\"><input name=\"fsubjectid\" type=\"hidden\" value=\"1000496697\" /> <input name=\"fpageid\" type=\"hidden\" value=\"0\" /> <input name=\"fvoteid\" type=\"hidden\" value=\"349442519\" /> <input name=\"fvotetitle\" type=\"hidden\" value=\"Bạn th&iacute;ch ti&ecirc;u đề v&agrave; biểu tượng n&agrave;o nhất?\" /> <input name=\"fvotefor\" type=\"hidden\" /> <input name=\"faction\" type=\"hidden\" value=\"0\" /> <input name=\"fDescription\" type=\"hidden\" /> <input name=\"fnumitem\" type=\"hidden\" value=\"3\" /> \r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" height=\"100%\">\r\n<tbody>\r\n<tr>\r\n<input name=\"fT_0\" type=\"hidden\" value=\"Giải nhất\" /> <input name=\"fI_0\" type=\"hidden\" value=\"0\" /> <input name=\"fN_0\" type=\"hidden\" value=\"0\" />\r\n<td width=\"20px\" align=\"right\" valign=\"top\"><input style=\"font: 12px arial; color: #000000;\" name=\"fC_0\" type=\"checkbox\" value=\"0\" /></td>\r\n<td valign=\"top\"><label style=\"font: 12px arial; color: #000000; margin-left: 2px; margin-right: 2px;\">Giải nhất</label></td>\r\n</tr>\r\n<tr>\r\n<input name=\"fT_1\" type=\"hidden\" value=\"Giải nh&igrave;\" /> <input name=\"fI_1\" type=\"hidden\" value=\"0\" /> <input name=\"fN_1\" type=\"hidden\" value=\"1\" />\r\n<td width=\"20px\" align=\"right\" valign=\"top\"><input style=\"font: 12px arial; color: #000000;\" name=\"fC_1\" type=\"checkbox\" value=\"1\" /></td>\r\n<td valign=\"top\"><label style=\"font: 12px arial; color: #000000; margin-left: 2px; margin-right: 2px;\">Giải nh&igrave;</label></td>\r\n</tr>\r\n<tr>\r\n<input name=\"fT_2\" type=\"hidden\" value=\"Giải ba\" /> <input name=\"fI_2\" type=\"hidden\" value=\"0\" /> <input name=\"fN_2\" type=\"hidden\" value=\"2\" />\r\n<td width=\"20px\" align=\"right\" valign=\"top\"><input style=\"font: 12px arial; color: #000000;\" name=\"fC_2\" type=\"checkbox\" value=\"2\" /></td>\r\n<td valign=\"top\"><label style=\"font: 12px arial; color: #000000; margin-left: 2px; margin-right: 2px;\">Giải ba</label></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr height=\"10px\" valign=\"top\">\r\n<td style=\"background-color: #eeeedd; text-align: right;\" align=\"center\" valign=\"top\"><br /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n<td style=\"width: 4px; background: url(../../images/vote/bg-right-c.gif) repeat-y scroll center top #ffffff;\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td style=\"width: 4px; height: 25px; background: url(../../images/vote/bg-left.gif) repeat-y scroll center top #ffffff;\"><br /></td>\r\n<td style=\"height: 25px; padding: 5px 5px 0px; background-color: #ffffff;\"><input style=\"cursor: pointer;\" src=\"http://vnexpress.net/Images/Vote/submit.gif\" type=\"image\" />&nbsp;&nbsp;<input style=\"cursor: pointer;\" src=\"http://vnexpress.net/Images/Vote/view.gif\" type=\"image\" /></td>\r\n<td style=\"height: 25px; background: url(../../images/vote/bg-right.gif) repeat-y scroll center top transparent;\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td style=\"width: 4px; height: 5px; background: url(../../images/vote/left-bottom.gif) no-repeat scroll center bottom #ffffff;\"><br /></td>\r\n<td style=\"height: 5px; background: url(../../images/vote/bg-bottom.gif) repeat-x scroll center bottom #ffffff; font-size: 1px;\">&nbsp;</td>\r\n<td style=\"width: 4px; height: 5px; background: url(../../images/vote/right-bottom.gif) no-repeat scroll center bottom #ffffff;\"><br /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đo&agrave;n Loan</strong></p>\r\n</div>', 3, 758, 0, 1, NULL, NULL),
(37, 'Bangkok và Pattaya, gió và những đêm không ngủ', 'Bangkok-Va-Pattaya,-Gio-Va-Nhung-Dem-Khong-Ngu', '\"Ngày ở Pattaya, tôi được thử cảm giác đi xe nghịch hướng. Ngồi sau xe máy, làn gió làm bay tóc, những ánh đèn pha ngược chiều, ngược cả hướng làm tôi hơi lo sợ, tôi thấy bờ vai che chở lúc này thật là... hữu dụng\", bạn Thuc Nga chia sẻ.', 'Cho-Noi-Pattaya1.jpg', 22, '<h1 class=\"Title\">Bangkok v&agrave; Pattaya, gi&oacute; v&agrave; những đ&ecirc;m kh&ocirc;ng ngủ</h1>\r\n<h2 class=\"Lead\">\"Ng&agrave;y ở Pattaya, t&ocirc;i được thử cảm gi&aacute;c đi xe nghịch  hướng. Ngồi sau xe m&aacute;y, l&agrave;n gi&oacute; l&agrave;m bay t&oacute;c, những &aacute;nh đ&egrave;n pha ngược  chiều, ngược cả hướng l&agrave;m t&ocirc;i hơi lo sợ, t&ocirc;i thấy bờ vai che chở l&uacute;c n&agrave;y  thật l&agrave;... hữu dụng\", bạn Thuc Nga chia sẻ.</h2>\r\n<p class=\"Normal\">T&ocirc;i gặp anh giữa s&acirc;n bay Survanabuhmi một trưa nắng  nhẹ v&agrave; hanh hao gi&oacute;, trong h&agrave;nh tr&igrave;nh Bangkok - Pattaya. Những cử chỉ  th&acirc;n thiện đầu ti&ecirc;n đ&atilde; thể hiện sự mến kh&aacute;ch của con người nơi đ&acirc;y. Một  c&aacute;ch v&ocirc; tư v&agrave; x&atilde; giao, t&ocirc;i đ&aacute;p lại bằng một c&aacute;i &ocirc;m người bạn hướng dẫn  vi&ecirc;n của m&igrave;nh tr&ograve;n v&ograve;ng tay!</p>\r\n<p class=\"Normal\">Dường như một h&agrave;nh động ấy th&ocirc;i đ&atilde; th&agrave;nh nguồn cảm  hứng cho cả t&ocirc;i v&agrave; anh trong suốt h&agrave;nh tr&igrave;nh. D&ugrave; mệt sau cả ng&agrave;y đưa  đo&agrave;n đi tham quan, anh vẫn nhiệt t&igrave;nh đưa t&ocirc;i đến những nơi m&agrave; anh muốn  giới thiệu với ri&ecirc;ng t&ocirc;i. Ng&agrave;y ở Pattaya, t&ocirc;i được thử cảm gi&aacute;c đi xe  nghịch hướng. Ngồi sau xe m&aacute;y, l&agrave;n gi&oacute; l&agrave;m bay t&oacute;c, những &aacute;nh đ&egrave;n pha  ngược chiều, ngược cả hướng l&agrave;m t&ocirc;i hơi lo sợ, t&ocirc;i thấy bờ vai che chở  l&uacute;c n&agrave;y thật l&agrave;... hữu dụng. Cảm gi&aacute;c được ngồi sau một bờ vai rộng, với  m&ugrave;i hương lạ lẫm nhưng lại rất th&acirc;n quen l&agrave;m t&ocirc;i mơ hồ thấy như m&igrave;nh  đang ở một nơi rất gần gũi v&agrave; quen thuộc, t&ocirc;i thấy l&ograve;ng m&igrave;nh b&igrave;nh y&ecirc;n...  T&ocirc;i thấy Pattaya thật đẹp...</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5d/ce/Chua-Binh-Minh.jpg\" border=\"1\" alt=\"ch&ugrave;a\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một ngồi ch&ugrave;a b&ecirc;n bờ s&ocirc;ng. Ảnh: <em>Thuc Nga.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">T&ocirc;i thật may mắn, v&igrave; được đ&iacute;ch th&acirc;n hướng dẫn vi&ecirc;n bản  địa đưa đi ngắm th&agrave;nh phố - những nơi kh&ocirc;ng c&oacute; trong lịch tr&igrave;nh. Anh  bạn dường như muốn giới thiệu cho t&ocirc;i tất cả những g&igrave; anh cho l&agrave; n&ecirc;n  biết đến nhất. Từ những ban c&ocirc;ng đầy hoa t&iacute;m đẹp m&ecirc; hồn của kh&aacute;ch sạn  Mariot, đến những con phố ngang dọc ngập đầy hoa đủ sắc m&agrave;u bao quanh  những khu kh&aacute;ch sạn xinh đẹp, con đường bờ biển r&igrave; r&agrave;o gi&oacute; l&agrave;m t&ocirc;i nhớ  đến biển Việt Nam v&agrave; bớt nhớ nh&agrave;, anh ph&oacute;ng xe đưa t&ocirc;i l&ecirc;n đỉnh ngọn đồi  cao nhất Pattaya để ngắm to&agrave;n cảnh th&agrave;nh phố về đ&ecirc;m, đo&aacute;n xem đo&agrave;n  kh&aacute;ch của m&igrave;nh đang dạo chơi ở đ&acirc;u giữa những &aacute;nh đ&egrave;n lung linh đ&oacute;, hay  dẫn t&ocirc;i lang thang trong chợ nổi Pattaya để ngắm những ng&ocirc;i nh&agrave; gỗ,  những đồ thủ c&ocirc;ng v&agrave; thử những m&oacute;n ăn vặt đặc trưng. T&ocirc;i h&aacute;o hức ăn no  căng bụng những m&oacute;n ăn vặt ấy, rồi cay ch&aacute;y lưỡi bởi một quả ớt xanh b&eacute;  t&iacute; v&agrave; thơm lừng!</p>\r\n<p class=\"Normal\">Về Bangkok, anh lại c&ugrave;ng t&ocirc;i shopping, v&agrave; đưa t&ocirc;i đi  ngắm cảnh ngoại &ocirc; Bangkok về đ&ecirc;m. Bỏ sau lưng những ồn &agrave;o ban ng&agrave;y, kẹt  xe v&agrave; hối hả, ch&uacute;ng t&ocirc;i cảm nhận sự tĩnh lặng v&agrave; &ecirc;m ả của đ&ecirc;m miền nhiệt  đới. C&ugrave;ng ăn ch&egrave; Th&aacute;i, ăn những m&oacute;n ăn đ&ecirc;m lạ với t&ocirc;i v&agrave; qu&aacute; quen với  anh. V&agrave; c&ugrave;ng cảm nhận sự b&igrave;nh y&ecirc;n trong t&acirc;m hồn... Anh đ&atilde; kể cho t&ocirc;i  nghe về đạo Phật, về triết l&yacute; cuộc sống m&agrave; anh vẫn lu&ocirc;n t&acirc;m niệm, anh kể  về c&aacute;c đời vua Rama, t&ocirc;i nhận thấy ở anh một t&acirc;m hồn nhạy cảm v&agrave; s&acirc;u  sắc, dường như c&aacute;ch gi&aacute;o dục đặc trưng Th&aacute;i Lan từ thời ni&ecirc;n thiếu đ&atilde;  cho anh những suy ngẫm rất ch&acirc;n thực nhưng đầy t&iacute;nh triết l&yacute; về cuộc  sống, về con người. T&ocirc;i học hỏi anh...</p>\r\n<p class=\"Normal\">Ch&uacute;ng t&ocirc;i đ&atilde; c&oacute; những đ&ecirc;m kh&ocirc;ng ngủ, để chia sẻ những  c&acirc;u chuyện về cuộc sống, những buồn vui, quan niệm, v&agrave; để c&ugrave;ng lang  thang dạo chơi...</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5d/ce/Cho-Noi-Pattaya1.jpg\" border=\"1\" alt=\"chợ nổi\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Chợ nổi ở Pattaya. Ảnh: <em>Thuc Nga.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Hai th&agrave;nh phố, hai nơi kh&aacute;c nhau. Một nơi gi&oacute; biển r&igrave;  r&agrave;o, quang đ&atilde;ng v&agrave; y&ecirc;n b&igrave;nh, đặc trưng bởi dải bờ biển v&agrave; những dịch vụ  chủ yếu d&agrave;nh cho du kh&aacute;ch nước ngo&agrave;i, với kh&ocirc;ng kh&iacute; tấp nập v&agrave; n&oacute;ng  bỏng, những qu&aacute;n bar v&agrave; cửa h&agrave;ng mở cửa th&acirc;u đ&ecirc;m. Một nơi đặc trưng bởi  những m&aacute;i ch&ugrave;a, những cung điện nguy nga của chốn kinh đ&ocirc;, với nhịp sống  c&ocirc;ng nghiệp hối hả nhộn nhịp, với nạn kẹt xe nổi tiếng thế giới, đ&ocirc;ng  đ&uacute;c v&agrave; s&ocirc;i động. Nhưng t&ocirc;i y&ecirc;u cả hai nơi! V&agrave; tạm biệt Th&aacute;i Lan trong  lưu luyến!</p>\r\n<p class=\"Normal\">Mới đ&acirc;y, t&ocirc;i lại c&oacute; dịp đến Th&aacute;i Lan. Vẫn h&agrave;nh tr&igrave;nh  quen thuộc đ&oacute;. Đối với t&ocirc;i, mỗi một điểm tr&ecirc;n h&agrave;nh tr&igrave;nh thật sự th&acirc;n  thương v&agrave; quen thuộc. Cũng l&agrave; những đ&ecirc;m trở gi&oacute;! Anh giữ lời hứa như đ&atilde;  hẹn trong lần gặp trước, anh đưa t&ocirc;i đi ăn ở nh&agrave; h&agrave;ng đẹp nhất Pattaya  lung linh &aacute;nh đ&egrave;n soi b&oacute;ng tr&ecirc;n mặt hồ nước, thấp tho&aacute;ng trong v&ograve;m c&acirc;y  l&aacute;, đi ăn lẩu b&ograve; phong vị Trung Hoa ở khu Ratchada - Bangkok. Anh đưa  t&ocirc;i l&ecirc;n Bayoke Sky ăn tối v&agrave; ngắm Bangkok về đ&ecirc;m, nghe gi&oacute; h&ograve;a c&ugrave;ng  tiếng đập của con tim...</p>\r\n<p class=\"Normal\">Nhớ lắm, Th&aacute;i Lan, t&ocirc;i muốn quay trở lại!</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Thuc Nga</strong></p>', 3, 1655, 0, 1, NULL, NULL),
(38, 'Ngôi đền cổ Phimai', 'Ngoi-Den-Co-Phimai', 'Phimai xưa kia từng được xem là một trong những thành phố quan trọng của người Khmer. Kiến trúc và điêu khắc của đền được xem là cùng thời kỳ với kiến trúc của Bayon, Baphuon và Angkor Wat.', 'den4.jpg', 22, '<h1 class=\"Title\">Ng&ocirc;i đền cổ Phimai</h1>\r\n<h2 class=\"Lead\">Phimai xưa kia từng được xem l&agrave; một trong những th&agrave;nh  phố quan trọng của người Khmer. Kiến tr&uacute;c v&agrave; đi&ecirc;u khắc của đền được xem  l&agrave; c&ugrave;ng thời kỳ với kiến tr&uacute;c của Bayon, Baphuon v&agrave; Angkor Wat.</h2>\r\n<p class=\"Normal\">Sau 5 giờ đi xe bu&yacute;t từ Bangkok, th&agrave;nh phố đầu ti&ecirc;n m&agrave;  đo&agrave;n ch&uacute;ng t&ocirc;i đến l&agrave; Korat, tỉnh cửa ng&otilde; v&agrave;o v&ugrave;ng Isan, Isarn hay  Esarn, được xem như v&ugrave;ng Đ&ocirc;ng Bắc Th&aacute;i Lan, v&ugrave;ng của sự giao thoa nhiều  nền văn h&oacute;a - nơi m&agrave; nền văn h&oacute;a của L&agrave;o, Campuchia v&agrave; Th&aacute;i Lan gặp  nhau.</p>\r\n<p class=\"Normal\">Do đ&oacute; m&agrave; ng&ocirc;n ngữ ở đ&acirc;y ho&agrave;n to&agrave;n kh&aacute;c biệt. Tiếng L&agrave;o  l&agrave; ng&ocirc;n ngữ được phổ biến nhất b&ecirc;n cạnh tiếng Khmer cũng được sử dụng  rộng r&atilde;i. Nhiều người d&acirc;n trong v&ugrave;ng c&oacute; thể biết hoặc kh&ocirc;ng biết tiếng  Anh nhưng họ c&oacute; thể biết nhiều thứ tiếng địa phương như L&agrave;o, Th&aacute;i hay  Khmer. Thức ăn ở v&ugrave;ng n&agrave;y nặng khẩu vị L&agrave;o. M&oacute;n ăn đặc trưng v&ugrave;ng n&agrave;y l&agrave;  gỏi đủ đủ (tiếng Th&aacute;i gọi l&agrave; Som Tam hay Tam Mak Hong theo tiếng L&agrave;o),  g&agrave; nướng, gạo dẻo v&agrave; lạp Korat giữ vị tr&iacute; đặc biệt tr&ecirc;n tuyến đường từ  Bangkok đến v&ugrave;ng Đ&ocirc;ng Bắc.</p>\r\n<p class=\"Normal\">Hướng dẫn vi&ecirc;n địa phương c&ograve;n cho ch&uacute;ng t&ocirc;i biết mỗi  khi c&oacute; lễ hội, nhất l&agrave; Tết của Th&aacute;i Lan, Korat v&agrave; con đường ch&iacute;nh v&agrave;o  th&agrave;nh phố, c&aacute;c qu&aacute;n ăn, nh&agrave; h&agrave;ng sẽ tổ chức c&aacute;c hoạt động thu h&uacute;t kh&aacute;ch  bằng việc miễn ph&iacute; hay giảm gi&aacute; đặc biệt đối với m&oacute;n ăn đặc sản ch&iacute;nh  của th&agrave;nh phố n&agrave;y. C&aacute;ch l&agrave;m n&agrave;y d&ugrave;ng để quảng b&aacute; thương hiệu ri&ecirc;ng của  c&aacute;c đơn vị kinh doanh v&agrave; l&agrave;m nổi bật đặc sản ri&ecirc;ng c&oacute; của địa phương.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/5C/43/den.jpg\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đền Phimai c&oacute; kiến tr&uacute;c giống đền Angko của Campuchia. Ảnh: <em>L&acirc;m Văn Sơn.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Sau bữa cơm trưa Th&aacute;i, ch&uacute;ng t&ocirc;i được đưa đến tham  quan c&ocirc;ng vi&ecirc;n lịch sử Phimai, l&agrave; một khu phức hợp di t&iacute;ch gồm nhiều đền  v&agrave; những bức tượng Phật nằm trong một diện t&iacute;ch lớn đang được bảo vệ.  Trong đ&oacute; c&oacute; hai c&ocirc;ng tr&igrave;nh c&oacute; gi&aacute; trị lớn về mặt kiến tr&uacute;c đ&oacute; l&agrave; c&ocirc;ng  vi&ecirc;n lịch sử Phanom Rung v&agrave; Pra Sat Hin Phimai.</p>\r\n<p class=\"Normal\">C&ocirc;ng vi&ecirc;n lịch sử Phimai tọa lạc tại quận Phimai,  th&agrave;nh phố Korat, thủ phủ tỉnh Nakhon Ratchasima - một tỉnh cửa ng&otilde; của  v&ugrave;ng Đ&ocirc;ng Bắc Th&aacute;i Lan. Korat cũng l&agrave; t&ecirc;n gọi của cao nguy&ecirc;n Korat. Từ  quốc lộ số 2 rẽ phải theo đường 206 qua một c&acirc;y cầu đo&agrave;n ch&uacute;ng t&ocirc;i đến  c&ocirc;ng vi&ecirc;n. Ph&iacute;a ngo&agrave;i c&ocirc;ng vi&ecirc;n l&agrave; bảo t&agrave;ng lịch sử Phimai v&agrave; tiếp b&ecirc;n  trong l&agrave; khu phức hợp nhiều ng&ocirc;i đền linh thi&ecirc;ng.</p>\r\n<p class=\"Normal\">Thời kỳ Khmer, Phimai từng l&agrave; một th&agrave;nh phố quan  trọng. Trong đ&oacute;, Pra Sat Hin Phimai l&agrave; ng&ocirc;i đền Khmer lớn nhất tại Th&aacute;i  Lan v&agrave; l&agrave; ng&ocirc;i đền c&oacute; kiến tr&uacute;c độc đ&aacute;o nhất trong quần thể Phimai nằm ở  trung t&acirc;m th&agrave;nh phố.</p>\r\n<p class=\"Normal\">Trước khi đến được ng&ocirc;i đền thờ trung t&acirc;m ch&uacute;ng t&ocirc;i  phải đi qua một c&acirc;y cầu người ta gọi l&agrave; cầu Naga, cầu c&oacute; đầu rắn thần.  Naga được x&acirc;y dựng quay theo hướng Nam. Trước cầu Naga l&agrave; bục trung t&acirc;m  h&igrave;nh chữ thập trước c&oacute; 2 con sư tử.</p>\r\n<p class=\"Normal\">Đền được x&acirc;y dựng trong suốt thời kỳ vua Jayavaraman  VI trị v&igrave; từ năm 1080 đến năm 1107. Sau đ&oacute; ng&ocirc;i đền lại được tiếp tục  ho&agrave;n thiện bởi vua Jayavarman VII từ năm 1181 đến năm 1219, l&agrave; vị vua c&oacute;  vị tr&iacute; rất quan trọng trong việc củng cố v&agrave; ph&aacute;t triển ng&ocirc;i đền. Nếu  như trước đ&oacute;, ng&ocirc;i đền ảnh hưởng của kiến tr&uacute;c đạo Hindu th&igrave; sau khi vua  Jayavaraman 6 l&ecirc;n ng&ocirc;i, ng&ocirc;i đền lại chịu ảnh hưởng của kiến tr&uacute;c đạo  B&agrave; La M&ocirc;n.</p>\r\n<p class=\"Normal\">Ng&ocirc;i đền được người d&acirc;n ở đ&acirc;y cho l&agrave; c&oacute; một con đường  b&iacute; mật dẫn đến đền Angkor Wat của Campuchia. Theo giả thuyết của c&aacute;c nh&agrave;  khoa học th&igrave; con đường đ&aacute; trực tiếp đến đền Angkor từ cầu Naga theo  hướng Đ&ocirc;ng Bắc l&agrave; khoảng 225 km. Hầu như con đường n&agrave;y hiện bị bao phủ  bởi rừng c&acirc;y v&agrave; chỉ c&oacute; thể nh&igrave;n thấy được từ tr&ecirc;n kh&ocirc;ng. Phần c&ograve;n lại  của con đường duy nhất hiện nay l&agrave; đường v&agrave;o cổng Phimai. Phần n&agrave;y đ&atilde;  chứng thực sự tồn tại của con đường đ&atilde; c&oacute; từ trước thế kỷ 12-13 từ thời  kỳ vua Jayavarman VII.</p>\r\n<p class=\"Normal\">Mặc d&ugrave; đền Angkor được x&acirc;y dựng trước Phimai, tuy  nhi&ecirc;n về cơ bản cả hai ng&ocirc;i đền đều kh&ocirc;ng mấy thay đổi. Đền Angkor huy  ho&agrave;ng, đặc sắc v&agrave; bề thế hơn Phimai bởi năm ngọn th&aacute;p lớn như năm ngọn  n&uacute;i lớn vươn l&ecirc;n cao, trong khi c&aacute;c th&aacute;p thờ của đền Phimai sắp xếp một  c&aacute;ch đơn lẻ.</p>\r\n<p class=\"Normal\">To&agrave;n bộ ng&ocirc;i đền x&acirc;y dựng theo h&igrave;nh vu&ocirc;ng v&agrave; bức tường  th&agrave;nh bao quanh đền c&oacute; chiều rộng l&agrave; 580 m v&agrave; chiều d&agrave;i l&agrave; 1020 m. Về  mặt địa l&yacute;, người d&acirc;n địa phương cho rằng n&oacute; đ&atilde; được x&acirc;y dựng tr&ecirc;n một  ngọn đồi cao v&agrave; được bao bọc bởi 3 con k&ecirc;nh đ&agrave;o.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/5C/43/den4.jpg\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những h&igrave;nh tượng tại đền Phimai. Ảnh: <em>L&acirc;m Văn Sơn.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Phimai xưa kia từng được xem l&agrave; một trong những th&agrave;nh  phố quan trọng của người Khmer. Kiến tr&uacute;c v&agrave; đi&ecirc;u khắc của đền được xem  l&agrave; c&ugrave;ng thời kỳ với kiến tr&uacute;c của Bayon, Baphuon v&agrave; Angkor Wat. Chất  liệu x&acirc;y dựng chủ yếu l&agrave; sa thạch, đ&aacute; ong v&agrave; l&agrave; sự kết hợp của đ&aacute; sa  thạch đỏ c&ugrave;ng với c&aacute;t trộn tạo th&agrave;nh chất kết d&iacute;nh vững chắc.</p>\r\n<p class=\"Normal\">Hiện, ng&ocirc;i đền bị b&agrave;o m&ograve;n đến tận phần đ&aacute; b&ecirc;n trong  n&ecirc;n v&aacute;ch đ&aacute; ở đ&acirc;y nhiều chỗ đ&atilde; để lộ những m&agrave;u sắc đỏ ch&oacute;i của sa thạch  đỏ. Bộ phận bảo vệ di sản ở tỉnh n&agrave;y đ&atilde; cố tr&ugrave;ng tu kh&ocirc;i phục lại đền  bằng những khung nẹp sắt v&agrave; c&aacute;c vữa hồ xi măng.</p>\r\n<p class=\"Normal\">Gần ng&ocirc;i đền c&ograve;n c&oacute; bảo t&agrave;ng quốc gia Phimai nơi lưu  giữ những cổ vật thời kỳ Angkor v&agrave; đặc biệt hơn nữa l&agrave; c&aacute;ch c&ocirc;ng vi&ecirc;n  quốc gia Phimai khoảng 1,5 km c&oacute; c&acirc;y Sai Ngam l&agrave; giống c&acirc;y bany khổng  lồ. C&acirc;y Sai Ngam thật ra chỉ l&agrave; một c&acirc;y duy nhất nhưng nếu nh&igrave;n từ xa  c&acirc;y Sai Ngam tr&ocirc;ng giống như một rừng c&acirc;y lớn. Người địa phương c&ograve;n cho  biết c&oacute; thể n&oacute;i Sai Ngam l&agrave; c&acirc;y lớn nhất Đ&ocirc;ng Nam &Aacute;, c&oacute; tuổi thọ tr&ecirc;n  350 năm v&agrave; người Th&aacute;i xem c&acirc;y n&agrave;y như l&agrave; &ldquo;c&acirc;y thần&rdquo;.</p>\r\n<p class=\"Normal\">C&oacute; cơ hội đến tham quan được Phimai ở Nakhon  Ratchashima Th&aacute;i Lan, Watphou ở Nam L&agrave;o v&agrave; Angkor ở Campuchia, t&ocirc;i mới  nhận ra về sự to lớn v&agrave; bao tr&ugrave;m của thời kỳ đế chế Angkor h&ugrave;ng vĩ.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>L&acirc;m Văn Sơn</strong></p>', 3, 562, 0, 1, NULL, NULL),
(39, 'Tour Tết Tân Mão giá ưu đãi', 'Tour-Tet-Tan-Mao-Gia-Uu-Dai', 'Công ty cổ phần Truyền thông Du Lịch Việt đưa ra những chùm tour \"Tết Tân Mão\" ưu đãi như Singapore - Malaysia 7 ngày 6 đêm giá từ 699 USD, miền Trung 5 ngày 4 đêm giá đặc biệt từ 4,9 triệu đồng...', 'hinh_1_400x300.JPG', 22, '<h1 class=\"Title\">Tour Tết T&acirc;n M&atilde;o gi&aacute; ưu đ&atilde;i</h1>\r\n<h2 class=\"Lead\">C&ocirc;ng ty cổ phần Truyền th&ocirc;ng Du Lịch Việt đưa ra những ch&ugrave;m tour \"Tết T&acirc;n M&atilde;o\" ưu đ&atilde;i như Singapore - Malaysia 7 ng&agrave;y 6 đ&ecirc;m gi&aacute; từ 699 USD, miền Trung 5 ng&agrave;y 4 đ&ecirc;m gi&aacute; đặc biệt từ 4,9 triệu đồng...</h2>\r\n<p class=\"Normal\">Từ TP HCM, đối với chương tr&igrave;nh tour nước ngo&agrave;i, tuyến Singapore - Malaysia 7 ng&agrave;y 6 đ&ecirc;m gi&aacute; từ 14.700.000 đồng, Th&aacute;i Lan 6 ng&agrave;y 5 đ&ecirc;m gi&aacute; từ 10.900.000 đồng, Trung Quốc 7 ng&agrave;y 6 đ&ecirc;m gi&aacute; từ 19.000.000 đồng. Ngo&agrave;i ra, c&ocirc;ng ty c&ograve;n c&oacute; những ch&ugrave;m tour đặc sắc đến Mỹ v&agrave; Australia.</p>\r\n<p class=\"Normal\">B&ecirc;n cạnh đ&oacute;, c&aacute;c chương tr&igrave;nh tour trong nước cũng được du kh&aacute;ch ưu &aacute;i như ch&ugrave;m tour miền Bắc (H&agrave; Nội - Hạ Long - Tuần Ch&acirc;u - Sapa - H&agrave; Khẩu) 6 ng&agrave;y 5 đ&ecirc;m gi&aacute; từ 8.699.000 đồng hay ch&ugrave;m tour miền Trung ( Huế - Đ&agrave; Nẵng - B&agrave; N&agrave; - Hội An - Phong Nha - La Vang) gi&aacute; từ 6.399.000 đồng. Đặc biệt, Du lịch Việt c&oacute; ch&ugrave;m tour miền Trung 5 ng&agrave;y 4 đ&ecirc;m với gi&aacute; chỉ từ 4.999.000 đồng, khởi h&agrave;nh bằng m&aacute;y bay v&agrave; về bằng xe du lịch giường nằm chất lượng cao.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/51/CA/hinh_1_400x300.JPG\" border=\"1\" alt=\"\" width=\"400\" height=\"300\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Nếu muốn tận dụng thời gian để nghỉ ngơi thư gi&atilde;n c&ugrave;ng gia đ&igrave;nh, bạn c&oacute; thể chọn cho m&igrave;nh một kỳ nghỉ tại Ph&uacute; Quốc (từ 4.099.000 đồng) hay C&ocirc;n Đảo (từ 4.699.000 đồng), kh&aacute;m ph&aacute; Vinpearl Land tại Nha Trang gi&aacute; từ 4.399.000 đồng. Đặc biệt trong dịp Tết n&agrave;y, Du lịch Việt tổ chức tour mới đi Ninh Chữ - Vịnh Vĩnh Hy - Cổ Thạch 3 ng&agrave;y 2 đ&ecirc;m (gi&aacute; từ 2.399.000 đồng) với phương tiện đi v&agrave; về bằng xe d&agrave;nh cho đối tượng du kh&aacute;ch kh&ocirc;ng c&oacute; nhiều thời gian v&agrave; đối tượng gia đ&igrave;nh muốn tổ chức tour ri&ecirc;ng.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/51/CA/hinh_2_400x300.JPG\" border=\"1\" alt=\"\" width=\"400\" height=\"300\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Khởi h&agrave;nh từ H&agrave; Nội, đối với tour nước ngo&agrave;i, c&ocirc;ng ty c&oacute; chương tr&igrave;nh Th&aacute;i Lan 5 ng&agrave;y 4 đ&ecirc;m khởi h&agrave;nh m&ugrave;ng 1, 2 v&agrave; 3 (gi&aacute; từ 9.680.000 đồng), Singapore 4 ng&agrave;y 3 đ&ecirc;m khởi h&agrave;nh m&ugrave;ng 1 (gi&aacute; 12.850.000 đồng), Trung Quốc 5 ng&agrave;y 4 đ&ecirc;m khởi h&agrave;nh m&ugrave;ng 2 (gi&aacute; 12.220.000 đồng), Trung Quốc 7 ng&agrave;y 6 đ&ecirc;m khởi h&agrave;nh m&ugrave;ng 2 (gi&aacute; 15.380.000 đồng).</p>\r\n<p class=\"Normal\">Chương tr&igrave;nh tour trong nước c&oacute; c&aacute;c tour ngắn ng&agrave;y, th&iacute;ch hợp với nhiều đối tượng du kh&aacute;ch như du lịch Sapa ngắm hoa anh đ&agrave;o 2 ng&agrave;y 1 đ&ecirc;m (gi&aacute; từ 1.899.000 đồng); Ph&uacute; Quốc 3 ng&agrave;y 2 đ&ecirc;m (gi&aacute; từ 3.950.000 đồng, chưa bao gồm v&eacute; m&aacute;y bay); lễ hội Ch&ugrave;a Hương 1 ng&agrave;y (gi&aacute; từ 750.000 đồng); B&aacute;i Đ&iacute;nh - Tr&agrave;ng An (từ 850.000 đồng); Tam Cốc - B&iacute;ch Động (từ 777.000 đồng); lặn biển Nha Trang 4 ng&agrave;y 3 đ&ecirc;m (gi&aacute; từ 2.850.000 đồng, chưa bao gồm v&eacute; m&aacute;y bay); đi Vinpearl đ&oacute;n ch&agrave;o năm mới 4 ng&agrave;y 3 đ&ecirc;m, khởi h&agrave;nh 5/2 (gi&aacute; từ 7.499.000 đồng).</p>\r\n<p class=\"Normal\">Kh&aacute;ch c&oacute; nhu cầu đặt tour, li&ecirc;n hệ C&ocirc;ng ty cổ phần Truyền th&ocirc;ng Du Lịch Việt</p>\r\n<p class=\"Normal\">Văn ph&ograve;ng ch&iacute;nh tại TP HCM: 175 Nguyễn Th&aacute;i B&igrave;nh, quận 1<br />Điện thoại: (08) 39 14 66 88 &ndash; Fax: (08) 39 15 22 35<br />Hotlines: 0909 189 888 (ph&ograve;ng nội địa) &ndash; 0938 30 44 88 (ph&ograve;ng Outbound)</p>\r\n<p class=\"Normal\">Chi nh&aacute;nh H&agrave; Nội: 21 Văn Miếu, phường Văn Miếu, quận Đống Đa - Điện thoại: (04) 3512 33 88 &ndash; Fax: (04) 3512 2769</p>\r\n<p class=\"Normal\">Website:&nbsp;<a class=\"Normal\" href=\"http://www.dulichviet.com.vn/\">www.dulichviet.com.vn</a>&nbsp;<br />Email:&nbsp;<a class=\"Normal\" href=\"mailto:info@dulichviet.com.vn\">info@dulichviet.com.vn</a></p>\r\n<p class=\"Normal\">Tổng đ&agrave;i li&ecirc;n hệ to&agrave;n quốc: 1900 1518</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>C&ocirc;ng ty CP Truyền th&ocirc;ng DL Việt</em>)</p>', 3, 1788, 0, 1, NULL, NULL),
(40, 'Làng đào Hà Nội \'nín thở\' vì giá rét', '', 'Những người trồng hoa Nhật Tân, Tứ Liên (Hà Nội) như ngồi trên đống lửa vì lo giá rét có nguy cơ kéo dài. Một tháng trước Tết nhưng đào vẫn chưa có dấu hiệu gì đón xuân.', 'HHH_9967.jpg', 22, '<h1 class=\"Title\">L&agrave;ng đ&agrave;o H&agrave; Nội \'n&iacute;n thở\' v&igrave; gi&aacute; r&eacute;t</h1>\r\n<h2 class=\"Lead\">Những người trồng hoa Nhật T&acirc;n, Tứ Li&ecirc;n (H&agrave; Nội) như ngồi tr&ecirc;n đống lửa v&igrave; lo gi&aacute; r&eacute;t c&oacute; nguy cơ k&eacute;o d&agrave;i. Một th&aacute;ng trước Tết nhưng đ&agrave;o vẫn chưa c&oacute; dấu hiệu g&igrave; đ&oacute;n xu&acirc;n.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/51/84/HHH_9967.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Quất tại khu vực phường Tứ Li&ecirc;n đ&atilde; ch&iacute;n v&agrave;ng chuẩn bị đ&oacute;n năm mới xu&acirc;n T&acirc;n M&atilde;o. Nhiều người d&acirc;n đ&atilde; sớm đi đặt v&agrave; chọn c&acirc;y ưng &yacute;.</td>\r\n</tr>\r\n<tr>\r\n<td align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/51/84/HHH_9876.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">C&aacute;c chủ vườn đang tạo thế, tỉa t&oacute;t kỹ c&agrave;ng cho c&acirc;y để chuẩn bị đ&oacute;n kh&aacute;ch.</td>\r\n</tr>\r\n<tr>\r\n<td align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/51/84/HHH_9940.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Giữa trời gi&aacute; r&eacute;t, b&agrave; l&atilde;o ở l&agrave;ng Nhật T&acirc;n vẫn ra vườn đ&agrave;o đang đợi ng&agrave;y thu hoạch.</td>\r\n</tr>\r\n<tr>\r\n<td align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/51/84/HHH_9920.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">L&agrave;ng hoa chỉ mới ở giai đoạn đầu rộn r&atilde; hương sắc xu&acirc;n. Người trồng quất vui, nh&agrave; trồng đ&agrave;o hồi hộp tr&ocirc;ng ng&oacute;ng v&agrave;o thời tiết.</td>\r\n</tr>\r\n<tr>\r\n<td align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/51/84/HHH_9913.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Một người trồng đ&agrave;o nhiều năm kinh nghiệm cho biết, từ nay đến m&ugrave;ng 8 th&aacute;ng Chạp m&agrave; thời tiết H&agrave; Nội vẫn lạnh thế n&agrave;y, vỏ trấu đ&agrave;o kh&ocirc;ng bật được ra th&igrave; nguy to.</td>\r\n</tr>\r\n<tr>\r\n<td align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/51/84/HHH_9945.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Những ng&agrave;y cận Tết, với c&aacute;c hộ trồng v&agrave; kinh doanh hoa, c&ocirc;ng việc chăm s&oacute;c được đặt l&ecirc;n h&agrave;ng đầu.</td>\r\n</tr>\r\n<tr>\r\n<td align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/51/84/HHH_9932.jpg\" alt=\"\" width=\"400\" height=\"601\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Từ trước tới nay mới chỉ trồng hoa tươi, chưa trồng đ&agrave;o lần n&agrave;o, anh Đang thử cấy gh&eacute;p ph&ocirc;i đ&agrave;o rừng với hy vọng năm tới vườn hoa Nhật T&acirc;n sẽ kh&ocirc;ng chỉ c&oacute; đ&agrave;o b&iacute;ch, đ&agrave;o phai, đ&agrave;o thế...</td>\r\n</tr>\r\n<tr>\r\n<td align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/51/84/HHH_9916.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Tuy vậy, trong cả khu vườn vẫn c&oacute; một v&agrave;i c&acirc;y nở hoa đỏ thắm.</td>\r\n</tr>\r\n<tr>\r\n<td align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/51/84/HHH_9938.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Xen giữa vườn đ&agrave;o l&agrave; những luống hoa thược dược l&aacute;c đ&aacute;c nở hoa.</td>\r\n</tr>\r\n<tr>\r\n<td align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/51/84/HHH_9900.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Những chậu hoa ng&agrave;y Tết cũng được sơn mới chờ đ&oacute;n xu&acirc;n.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng H&agrave;</strong></p>', 3, 3, 0, 1, NULL, NULL),
(41, '\'Gift Voucher\' ưu đãi tại Vinpearl Nha Trang', '', 'Vinpearl Nha Trang giới thiệu chương trình “Gift Voucher” - thẻ quà tặng dành cho khách. Theo đó, bạn sẽ được hưởng trực tiếp ưu đãi tại Vinpearl Nha Trang Resort & Spa từ 18% đến 40%, tùy từng thời điểm đặt phòng.', '', 22, '<h1 class=\"Title\">\'Gift Voucher\' ưu đ&atilde;i tại Vinpearl Nha Trang</h1>\r\n<h2 class=\"Lead\">Vinpearl Nha Trang giới thiệu chương tr&igrave;nh &ldquo;Gift Voucher&rdquo; - thẻ qu&agrave; tặng d&agrave;nh cho kh&aacute;ch. Theo đ&oacute;, bạn sẽ được hưởng trực tiếp ưu đ&atilde;i tại Vinpearl Nha Trang Resort &amp; Spa từ 18% đến 40%, t&ugrave;y từng thời điểm đặt ph&ograve;ng.</h2>\r\n<p class=\"Normal\">Chương tr&igrave;nh &aacute;p dụng từ 16/11/2010 đến hết ng&agrave;y 30/11/2011 với 2 mệnh gi&aacute; kh&aacute;ch nhau: Deluxe Gift Voucher, gi&aacute; 399 USD v&agrave; VIP Gift Voucher, gi&aacute; 490 USD. Kh&aacute;ch h&agrave;ng sẽ chủ động lựa chọn c&aacute;c hạn mức ph&ugrave; hợp với kế hoạch t&agrave;i ch&iacute;nh của m&igrave;nh. Sử dụng thẻ VIP Gift Voucher, kỳ nghỉ k&eacute;o d&agrave;i 3 ng&agrave;y 2 đ&ecirc;m d&agrave;nh cho 2 người sẽ được bố tr&iacute; tại ph&ograve;ng hướng biển, Grand Deluxe Ocean View. Với thẻ Deluxe Gift Voucher, kỳ nghỉ k&eacute;o d&agrave;i 3 ng&agrave;y 2 đ&ecirc;m d&agrave;nh cho 2 người sẽ được bố tr&iacute; tại ph&ograve;ng Deluxe hướng đồi l&atilde;ng mạn.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/50/BB/du-lich1.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"303\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">D&ugrave;ng &ldquo;Gift Voucher&rdquo;, ngay khi đặt ch&acirc;n đến s&acirc;n bay Cam Ranh, bạn sẽ được đ&oacute;n tiếp v&agrave; đưa đến &ldquo;thi&ecirc;n đường miền nhiệt đới&rdquo; miễn ph&iacute; bằng xe đưa đ&oacute;n ri&ecirc;ng của Vinpearl. Tại kh&aacute;ch sạn Vinpearl Resort &amp; Spa, kh&aacute;ch h&agrave;ng được nh&acirc;n vi&ecirc;n gi&uacute;p l&agrave;m thủ tục đăng k&yacute; ph&ograve;ng theo quy tr&igrave;nh chuy&ecirc;n nghiệp v&agrave; thuận tiện, v&agrave; nghỉ tại ph&ograve;ng kh&aacute;ch sạn ti&ecirc;u chuẩn 5 sao với nội thất sang trọng, kh&ocirc;ng gian ấm &aacute;p. Ngo&agrave;i ra, bạn c&ograve;n được miễn ph&iacute; 3 bữa ăn buffet trong ng&agrave;y với c&aacute;c m&oacute;n ăn &Aacute; &ndash; &Acirc;u chế biến hấp dẫn, bắt mắt bởi c&aacute;c đầu bếp chuy&ecirc;n nghiệp.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/50/BB/du-lich2.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"193\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trong thời gian nghỉ ngơi tại đ&acirc;y, du kh&aacute;ch kh&ocirc;ng chỉ được chi&ecirc;m ngưỡng vẻ đẹp hoang sơ của biển xanh c&aacute;t trắng m&agrave; c&ograve;n được tận hưởng mọi dịch vụ nghỉ dưỡng cao cấp v&agrave; sang trọng của Vinpearl Resort &amp; Spa như sử dụng b&atilde;i biển ri&ecirc;ng của kh&aacute;ch sạn, tham gia miễn ph&iacute; ph&ograve;ng tập thể h&igrave;nh v&agrave; tất cả hoạt động thể thao h&agrave;ng ng&agrave;y: aerobic, yoga, b&oacute;ng chuyền b&atilde;i biển, b&oacute;ng b&agrave;n&hellip; B&ecirc;n cạnh đ&oacute;, bạn c&oacute; thể vui chơi kh&ocirc;ng giới hạn tại C&ocirc;ng vi&ecirc;n giải tr&iacute; Vinpearl Land; kh&aacute;m ph&aacute; vẻ đẹp của th&agrave;nh phố biển Nha Trang bằng tour du lịch &ldquo;Nha trang City Tour&rdquo; d&agrave;nh cho 2 người; miễn ph&iacute; c&aacute;c phương tiện đi lại tr&ecirc;n đảo (tuk tuk) v&agrave; giữa đất liền &ndash; đảo (c&aacute;p treo, ph&agrave;, can&ocirc;)&hellip; c&ugrave;ng nhiều ưu đ&atilde;i kh&aacute;c.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/50/BB/du-lich3.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"246\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">B&ecirc;n cạnh việc mua Gift Voucher v&agrave; đăng k&yacute; thụ hưởng trực tiếp những ưu đ&atilde;i lớn từ Vinpearl, bạn c&ograve;n c&oacute; thể sử dụng &ldquo;Gift Voucher&rdquo; như một m&oacute;n qu&agrave; d&agrave;nh tặng đối t&aacute;c, bạn b&egrave;, người th&acirc;n.</p>\r\n<p class=\"Normal\">Tọa lạc tr&ecirc;n đảo H&ograve;n Tre xinh đẹp giữa vịnh Nha Trang với biển xanh, c&aacute;t trắng, nắng v&agrave;ng, khu du lịch v&agrave; giải tr&iacute; Vinpearl Nha Trang như &ldquo;thi&ecirc;n đường của miền nhiệt đới&rdquo;. Đến Vinpearl Nha Trang, bạn sẽ được cảm nhận vẻ đẹp hoang sơ, quyến rũ của một h&ograve;n đảo ngọc.</p>\r\n<p class=\"Normal\">Th&ocirc;ng tin t&igrave;m hiểu qua website:&nbsp;<a class=\"Normal\" href=\"http://www.vinpearlland.com/\">http://www.vinpearlland.com</a></p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Vinpearl Nha Trang</em>)</p>', 3, 3, 0, 1, NULL, NULL),
(42, 'Mai Châu mờ sương ngày đầu năm', 'Mai-Chau-Mo-Suong-Ngay-Dau-Nam', 'Trong ngày đầu của năm mới 2011, khung cảnh ở bản Hang Kia, Mai Châu (Hòa Bình) hiện lên huyền ảo trong làn sương mịt mù.\r\n\r\n\r\nSương mù kèm theo thời tiết giá lạnh 13 độ C tại Mai Châu sáng mùng 1 Tết Dương lịch.\r\n\r\nMột trong những ngôi nhà tại bản Hang Kia chìm trong sương.\r\n\r\nXe máy đi lại khó khăn.\r\n\r\nThời điểm giáp xuân, những cây hoa mận đã nở nhưng khó có thể nhận ra trong làn sương mờ ảo.\r\n\r\nMột người phụ nữ Mông cho gia cầm ăn.\r\n\r\nNhững đứa trẻ cùng nhau chơi quay.\r\n\r\nMột em bé thiếu quần mặc trong giá rét.\r\n\r\nHai mẹ con người dân tộc Mông sưởi ấm trong căn nhà gỗ.\r\n\r\nNhững người đàn ông đang quây quần trong bữa cơm chào năm mới.\r\n\r\nMột người đàn ông trở về nhà cùng chiếc khèn.', 'maichau1.jpg', 22, '<h1 class=\"Title\">Mai Ch&acirc;u mờ sương ng&agrave;y đầu năm</h1>\r\n<h2 class=\"Lead\">Trong ng&agrave;y đầu của năm mới 2011, khung cảnh ở bản Hang Kia, Mai Ch&acirc;u (H&ograve;a B&igrave;nh) hiện l&ecirc;n huyền ảo trong l&agrave;n sương mịt m&ugrave;.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4E/FA/maichau1.jpg\" alt=\"\" width=\"400\" height=\"601\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Sương m&ugrave; k&egrave;m theo thời tiết gi&aacute; lạnh 13 độ C tại Mai Ch&acirc;u s&aacute;ng m&ugrave;ng 1 Tết Dương lịch.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4E/FA/1a.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Một trong những ng&ocirc;i nh&agrave; tại bản Hang Kia ch&igrave;m trong sương.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4E/FA/2.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Xe m&aacute;y đi lại kh&oacute; khăn.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4E/FA/maichau2.jpg\" alt=\"\" width=\"400\" height=\"601\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Thời điểm gi&aacute;p xu&acirc;n, những c&acirc;y hoa mận đ&atilde; nở nhưng kh&oacute; c&oacute; thể nhận ra trong l&agrave;n sương mờ ảo.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4E/FA/4.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Một người phụ nữ M&ocirc;ng cho gia cầm ăn.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4E/FA/5.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Những đứa trẻ c&ugrave;ng nhau chơi quay.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4E/FA/maichau3.jpg\" alt=\"\" width=\"400\" height=\"601\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Một em b&eacute; thiếu quần mặc trong gi&aacute; r&eacute;t.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4E/FA/7.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Hai mẹ con người d&acirc;n tộc M&ocirc;ng sưởi ấm trong căn nh&agrave; gỗ.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4E/FA/8.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Những người đ&agrave;n &ocirc;ng đang qu&acirc;y quần trong bữa cơm ch&agrave;o năm mới.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4E/FA/9.jpg\" alt=\"\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Một người đ&agrave;n &ocirc;ng trở về nh&agrave; c&ugrave;ng chiếc kh&egrave;n.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng H&agrave;</strong></p>', 3, 1948, 0, 1, NULL, NULL),
(43, 'Thi viết về những trải nghiệm kỳ thú ở Thái Lan', 'Thi-Viet-Ve-Nhung-Trai-Nghiem-Ky-Thu-O-Thai-Lan', 'Mỗi tháng VnExpress.net sẽ tặng một cặp vé máy bay khứ hồi cho độc giả gửi bài viết hay nhất về du lịch trên đất nước Thái Lan. ', 'chua-1.jpg', 22, '<h1 class=\"Title\">Thi viết về những trải nghiệm kỳ th&uacute; ở Th&aacute;i Lan</h1>\r\n<h2 class=\"Lead\">Mỗi th&aacute;ng&nbsp;<em>VnExpress.net</em>&nbsp;sẽ tặng một cặp v&eacute; m&aacute;y bay khứ hồi cho độc giả gửi b&agrave;i viết hay nhất về du lịch tr&ecirc;n đất nước Th&aacute;i Lan.&nbsp;</h2>\r\n<p class=\"Normal\">Từ th&aacute;ng 1,&nbsp;<em>VnExpress.net</em>&nbsp;nhận b&agrave;i viết v&agrave; ch&ugrave;m ảnh chia sẻ những trải nghiệm du lịch tr&ecirc;n đất nước Th&aacute;i Lan, mỗi b&agrave;i viết tối đa 1.000 chữ v&agrave; 10 ảnh, gửi về<a class=\"Normal\" href=\"mailto:dulich@vnexpres.net\">dulich@vnexpres.net</a>.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4D/CB/chua-1.jpg\" alt=\"\" width=\"330\" height=\"420\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những c&ocirc;ng tr&igrave;nh t&ocirc;n gi&aacute;o d&aacute;t v&agrave;ng độc đ&aacute;o ở Th&aacute;i Lan. Ảnh:&nbsp;<em>Đo&agrave;n Loan.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Ban bi&ecirc;n tập sẽ lựa chọn 4 b&agrave;i viết v&agrave; ch&ugrave;m ảnh dự thi c&oacute; chất lượng cao, được b&igrave;nh chọn nhiều nhất trong th&aacute;ng để trao giải.</p>\r\n<p class=\"Normal\">Giải đặc biệt l&agrave; một v&eacute; m&aacute;y bay khứ hồi đi Bangkok, 3 giải khuyến kh&iacute;ch l&agrave; những phần qu&agrave; lưu niệm ấn tượng. Danh s&aacute;ch độc giả tr&uacute;ng giải sẽ được c&ocirc;ng bố h&agrave;ng th&aacute;ng tr&ecirc;n chuy&ecirc;n mục Du lịch.</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"3\" width=\"497\" bgcolor=\"#ecf2ff\">\r\n<tbody>\r\n<tr>\r\n<td>* Cuộc thi viết về những trải nghiệm kỳ th&uacute; ở Th&aacute;i Lan do Tổng cục Du lịch Th&aacute;i Lan t&agrave;i trợ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đo&agrave;n Loan</strong></p>', 3, 2982, 0, 1, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(44, 'Trải nghiệm cảm giác sống chậm tại Côn Đảo', 'Trai-Nghiem-Cam-Giac-Song-Cham-Tai-Con-Dao', 'Sự vắng vẻ, phong cảnh tự nhiên cùng sự ra đời của khu nghỉ dưỡng cao cấp giúp Côn Đảo trở thành địa điểm lý tưởng cho người muốn trải nghiệm cảm giác sống chậm.', 'New_Image1.JPG', 22, '<h1 class=\"Title\">Trải nghiệm cảm gi&aacute;c sống chậm tại C&ocirc;n Đảo</h1>\r\n<h2 class=\"Lead\">Sự vắng vẻ, phong cảnh tự nhi&ecirc;n c&ugrave;ng sự ra đời của khu nghỉ dưỡng cao cấp gi&uacute;p C&ocirc;n Đảo trở th&agrave;nh địa điểm l&yacute; tưởng cho người muốn trải nghiệm cảm gi&aacute;c sống chậm.</h2>\r\n<p class=\"Normal\">Nằm c&aacute;ch bờ biển ph&iacute;a đ&ocirc;ng nam Việt Nam 230 km, C&ocirc;n Đảo &ndash; hay c&ograve;n gọi l&agrave; C&ocirc;n Sơn nằm trong quần thể 16 h&ograve;n đảo. Nơi đ&acirc;y mang vẻ đẹp hoang sơ với 80% diện t&iacute;ch l&agrave; rừng quốc gia gồm khu bảo tồn biển rộng 140 km2. Từng l&agrave; nơi tọa lạc của một trong những nh&agrave; t&ugrave; nổi tiếng dưới thời thực d&acirc;n Ph&aacute;p, hiện C&ocirc;n Đảo c&ograve;n được biết đến l&agrave; một thi&ecirc;n đường nghỉ dưỡng. H&ograve;n đảo xinh đẹp n&agrave;y c&oacute; tổng số d&acirc;n khoảng 6.000, c&aacute;ch TP HCM 45 ph&uacute;t đi m&aacute;y bay.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4E/32/New_Image1.JPG\" border=\"1\" alt=\"\" width=\"400\" height=\"266\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Sự y&ecirc;n b&igrave;nh v&agrave; vẻ đẹp thi&ecirc;n nhi&ecirc;n nơi đ&acirc;y dễ l&agrave;m cho du kh&aacute;ch c&oacute; cảm gi&aacute;c muốn ở lại d&agrave;i ng&agrave;y. Đặc biệt, C&ocirc;n Đảo ph&ugrave; hợp để trở th&agrave;nh h&ograve;n đảo nghỉ dưỡng cao cấp, đặc biệt d&agrave;nh cho người muốn trải nghiệm cảm gi&aacute;c sống chậm, t&aacute;ch biệt cuộc sống hiện đại. Hiện C&ocirc;n Đảo c&oacute; resort 5 sao đầu ti&ecirc;n - Resort Six Senses C&ocirc;n Đảo (do C&ocirc;ng ty Indochina Land đầu tư v&agrave; tập đo&agrave;n quốc tế Six Senses Resorts and Spas quản l&yacute;). Điểm đặc biệt của resort n&agrave;y l&agrave; nội thất bằng gỗ. Lối kiến tr&uacute;c mang vẻ đẹp hiện đại với chất liệu v&agrave; m&agrave;u sắc h&agrave;i h&ograve;a nhằm t&ocirc;n l&ecirc;n n&eacute;t đẹp tự nhi&ecirc;n.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4E/32/New_Image3.JPG\" border=\"1\" alt=\"\" width=\"400\" height=\"287\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&aacute;c kiểu biệt thự tại Resort Six Senses C&ocirc;n Đảo đa dạng từ một đến hai tầng với một, ba hoặc bốn ph&ograve;ng ngủ. Tất cả c&aacute;c căn biệt thự đều c&oacute; ph&ograve;ng tắm trong nh&agrave; lẫn ngo&agrave;i trời. Phong c&aacute;ch thiết kế hiệu quả gi&uacute;p giảm kh&ocirc;ng gian sử dụng điều h&ograve;a nhiệt độ, bổ sung th&ocirc;ng gi&oacute; tự nhi&ecirc;n. Điều n&agrave;y tạo n&ecirc;n sự tho&aacute;ng đ&atilde;ng cho ph&ograve;ng tắm v&agrave; sảnh tiếp kh&aacute;ch (chỉ ph&ograve;ng ngủ được lắp điều h&ograve;a nhiệt độ nhằm mang lại sự tiện nghi).</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4E/32/New_Image2.JPG\" border=\"1\" alt=\"\" width=\"400\" height=\"269\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Ngo&agrave;i ra, trung t&acirc;m của khu nghỉ dưỡng c&ograve;n được x&acirc;y dựng m&ocirc; phỏng ng&ocirc;i l&agrave;ng Việt Nam truyền thống. Với tổng diện t&iacute;ch l&agrave; 12 ha, Six Senses C&ocirc;n Đảo gồm c&oacute; 50 biệt thự trải dọc bờ biển bao quanh bởi phong cảnh n&uacute;i non. Tất cả những biệt thự ở đ&acirc;y đều c&oacute; hồ bơi ri&ecirc;ng, đ&aacute;p ứng được kh&ocirc;ng gian ri&ecirc;ng tư v&agrave; sự cao cấp. Đặc biệt, đến nơi đ&acirc;y, du kh&aacute;ch c&ograve;n được trải nghiệm nhiều hoạt động vui chơi giải tr&iacute; như đi thuyền th&uacute;ng, lặn dọc bờ biển san h&ocirc;, ch&egrave;o thuyền kayak hay du ngoạn đảo r&ugrave;a, đảo chim... B&ecirc;n cạnh đ&oacute;, du kh&aacute;ch cũng được hưởng c&aacute;c dịch vụ spa, nh&agrave; h&agrave;ng cao cấp với thực đơn phong ph&uacute; v&agrave; chất lượng.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><em>(</em>Nguồn<em>: Six Senses C&ocirc;n Đảo)</em></p>', 3, 693, 0, 1, NULL, NULL),
(45, 'Du lịch kết hợp mua sắm Tết tại Trung Quốc', 'Du-Lich-Ket-Hop-Mua-Sam-Tet-Tai-Trung-Quoc', 'Hai tour du lịch kết hợp mua sắm Tết tại Trung Quốc là Nam Ninh - Quảng Châu và Thượng Hải - Hàng Châu - Tô Châu phục vụ du khách có nhu cầu mua sắm trong mùa siêu giảm giá trước Tết Nguyên đán.', 'Hang-Chau--Trung-Quoc.jpg', 22, '<h1 class=\"Title\">Du lịch kết hợp mua sắm Tết tại Trung Quốc</h1>\r\n<h2 class=\"Lead\">Hai tour du lịch kết hợp mua sắm Tết tại Trung Quốc l&agrave; Nam Ninh - Quảng Ch&acirc;u v&agrave; Thượng Hải - H&agrave;ng Ch&acirc;u - T&ocirc; Ch&acirc;u phục vụ du kh&aacute;ch c&oacute; nhu cầu mua sắm trong m&ugrave;a si&ecirc;u giảm gi&aacute; trước Tết Nguy&ecirc;n đ&aacute;n.</h2>\r\n<p class=\"Normal\">Trong đ&oacute;, tour Nam Ninh - Quảng Ch&acirc;u (5 ng&agrave;y 4 đ&ecirc;m, gi&aacute; trọn g&oacute;i 229 USD một kh&aacute;ch) sẽ mang đến cơ hội lựa chọn nhiều mặt h&agrave;ng ưa th&iacute;ch với gi&aacute; hấp dẫn. Đặc biệt, khi mua sắm tại Quảng Ch&acirc;u, du kh&aacute;ch c&oacute; thể t&igrave;m thấy nhiều loại h&agrave;ng thời trang Ph&aacute;p, Anh, Italy, &ocirc;t&ocirc; Mỹ, đồ điện tử Nhật s&acirc;m Cao Ly, gấm Thượng Hải... cao cấp, nổi tiếng thế giới hoặc loại đồ b&igrave;nh d&acirc;n, gi&aacute; rẻ.</p>\r\n<p class=\"Normal\">Đến Quảng Ch&acirc;u, du kh&aacute;ch c&ograve;n được thưởng ngoạn những thắng cảnh đẹp nổi tiếng như: ch&ugrave;a Lục Dung. Bạn cũng c&oacute; thể shopping tại trung t&acirc;m thương mại Thi&ecirc;n H&agrave; lớn nhất Quảng Ch&acirc;u, phố mua sắm Bắc Kinh lộ, trung t&acirc;m thương mại h&agrave;ng may mặc quần &aacute;o gi&aacute; rẻ sầm uất nhất Quảng Ch&acirc;u - Thi&ecirc;n M&atilde; v&agrave; Bạch M&atilde;, trung t&acirc;m h&agrave;ng điện tử, điện m&aacute;y&hellip; Quảng Ch&acirc;u c&ograve;n l&agrave; nơi c&oacute; thuốc Trung y tốt của Trung Quốc. Nơi đ&acirc;y c&oacute; trường đại học Trung y. Nếu lo lắng hay quan t&acirc;m tới t&igrave;nh trạng sức khỏe, bạn c&oacute; thể tới bắt mạch v&agrave; tư vấn sức khỏe miễn ph&iacute; tại hiệu thuốc Bảo Thụ Đường. Tại Nam Ninh - địa danh được v&iacute; l&agrave; &ldquo;Nơi c&oacute; m&ugrave;a đ&ocirc;ng kh&ocirc;ng kh&ocirc; h&eacute;o, hoa kh&ocirc;ng phải m&ugrave;a xu&acirc;n cũng nở rộ&rdquo;. Kh&aacute;ch du lịch cũng sẽ được thăm c&ocirc;ng vi&ecirc;n Thanh T&uacute; Sơn, quảng trường D&acirc;n tộc, khu triển l&atilde;m quốc tế Trung Quốc ASEAN, c&ocirc;ng vi&ecirc;n Nam Hồ, cửa h&agrave;ng thuốc bỏng nổi tiếng Trung Quốc...</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4D/B6/Hang-Chau--Trung-Quoc.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"338\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">B&ecirc;n cạnh tour Nam Ninh - Quảng Ch&acirc;u, tour Thượng Hải - H&agrave;ng Ch&acirc;u - T&ocirc; Ch&acirc;u (4 ng&agrave;y 4 đ&ecirc;m, gi&aacute; trọn g&oacute;i 499 USD một kh&aacute;ch) sẽ gi&uacute;p kh&aacute;ch h&agrave;ng s&agrave;nh điệu h&agrave;nh l&ograve;ng. Thượng Hải được xem l&agrave; c&aacute;i n&ocirc;i của thời trang với những thương hiệu nổi tiếng thế giới như Chanel, Cartier, Prada, Gucci, Hugo Boss, Tod&rsquo;s, Omega, Rado, Longines, Pierre Cardin, Giorgio Armani, Louis Vuitton&hellip; Mua sắm tại đ&acirc;y rẻ hơn so với ch&acirc;u &Acirc;u.</p>\r\n<p class=\"Normal\">Đến thăm th&agrave;nh phố của b&ecirc;n kia s&ocirc;ng Ho&agrave;ng Phố, du kh&aacute;ch c&ograve;n c&oacute; thể ngắm c&aacute;c danh lam thắng cảnh nổi tiếng: H&agrave;ng Ch&acirc;u, T&ocirc; Ch&acirc;u, ch&ugrave;a Ngọc Phật, miếu Th&agrave;nh Ho&agrave;ng, cửa h&agrave;ng ngọc trai Th&aacute;i Hồ, Khu Phố Đ&ocirc;ng, ng&ocirc;i ch&ugrave;a cổ nổi tiếng Trung Quốc- H&agrave;n Sơn Tự... Nếu H&agrave;ng Ch&acirc;u nổi tiếng l&agrave; một trong bảy kinh đ&ocirc; cổ của Trung Hoa cổ th&igrave; T&ocirc; Ch&acirc;u lại nổi tiếng v&igrave; ng&agrave;nh dệt lụa tơ tằm. Nơi đ&acirc;y sở hữu hơn 100 vườn hoa c&acirc;y cảnh xanh tươi, những con k&ecirc;nh lượn lờ, b&ecirc;n tr&ecirc;n l&agrave; cầu đ&aacute; h&igrave;nh v&ograve;ng cung v&agrave; những căn nh&agrave; cổ vẫn vững v&agrave;ng trước mưa gi&oacute;, thời gian. H&agrave;ng Ch&acirc;u, T&ocirc; Ch&acirc;u l&agrave; hai trong số những danh lam đẹp nhất Trung Hoa.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4D/B6/shanghai---harbor.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"324\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Nh&acirc;n dịp năm mới, C&ocirc;ng ty Du lịch v&agrave; Thể thao Việt Nam, Vietran Tour sẽ triển khai ph&aacute;t h&agrave;nh h&agrave;ng ngh&igrave;n thẻ mua h&agrave;ng v&agrave; thẻ tặng qu&agrave; tới kh&aacute;ch h&agrave;ng, &aacute;p dụng cho c&aacute;c đo&agrave;n kh&aacute;ch đăng k&yacute; đi tour c&oacute; ng&agrave;y khởi h&agrave;nh từ 1/1 đến hết 31/3/2011.</p>\r\n<p class=\"Normal\">Theo đ&oacute;, kh&aacute;ch đăng k&yacute; theo đo&agrave;n ri&ecirc;ng đi du lịch trong nước, c&aacute;c nước trong khu vực Đ&ocirc;ng Nam &Aacute; v&agrave; tour tới Trung Quốc (bằng đường bộ) sẽ được tặng ngay một thẻ mua h&agrave;ng hoặc tiền mặt trị gi&aacute; 500.000 đồng cho mỗi đo&agrave;n kh&aacute;ch. Đối với c&aacute;c tour ch&acirc;u &Acirc;u v&agrave; c&aacute;c điểm du lịch kh&aacute;c, đo&agrave;n sẽ được tặng thẻ mua h&agrave;ng hoặc tiền mặt với trị gi&aacute; 1.000.000 đồng. Ngo&agrave;i ra, mỗi du kh&aacute;ch trong đo&agrave;n c&ograve;n được tặng một m&oacute;n qu&agrave; &yacute; nghĩa trước chuyến đi.</p>\r\n<p class=\"Normal\">Để biết th&ecirc;m th&ocirc;ng tin chi tiết về c&aacute;c tour du lịch mua sắm Tết v&agrave; chương tr&igrave;nh khuyến m&atilde;i của Vietran Tour, kh&aacute;ch h&agrave;ng li&ecirc;n hệ Vietran Tour</p>\r\n<p class=\"Normal\">Địa chỉ: 49 Quang Trung - H&agrave; Nội<br />Điện thoại: 04 3943 8777<br />Hotline: 0943121318&nbsp;<br />Web:&nbsp;<a class=\"Normal\" href=\"http://www.vietrantour.com.vn/\">www.vietrantour.com.vn</a></p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Vietrantour</em>)</p>', 3, 925, 0, 1, NULL, NULL),
(46, 'Ngăn chặn hiện tượng tiêu cực mùa lễ hội', 'Ngan-Chan-Hien-Tuong-Tieu-Cuc-Mua-Le-Hoi', 'Bộ Văn hóa Thể thao và Du lịch vừa chỉ đạo các khách sạn, cơ sở lưu trú du lịch đảm bảo an toàn vệ sinh thực phẩm, không tăng giá, ép giá đối với du khách dịp tết Tân Mão.', 'a4.jpg', 22, '<h1 class=\"Title\">Ngăn chặn hiện tượng ti&ecirc;u cực m&ugrave;a lễ hội</h1>\r\n<h2 class=\"Lead\">Bộ Văn h&oacute;a Thể thao v&agrave; Du lịch vừa chỉ đạo c&aacute;c kh&aacute;ch sạn, cơ sở lưu tr&uacute; du lịch đảm bảo an to&agrave;n vệ sinh thực phẩm, kh&ocirc;ng tăng gi&aacute;, &eacute;p gi&aacute; đối với du kh&aacute;ch dịp tết T&acirc;n M&atilde;o.</h2>\r\n<p class=\"Normal\">Với c&ocirc;ng t&aacute;c lễ hội, Bộ Văn h&oacute;a Thể thao v&agrave; Du lịch chỉ đạo c&aacute;c cơ quan trực thuộc tăng cường thanh kiểm tra ngăn chặn c&aacute;c biểu hiện ti&ecirc;u cực, kh&ocirc;ng l&agrave;nh mạnh trong dịp Tết v&agrave; m&ugrave;a lễ hội. Tại c&aacute;c di t&iacute;ch, nơi thờ tự phải c&oacute; quy định việc đặt h&ograve;m c&ocirc;ng đức, kh&ocirc;ng để xảy ra t&igrave;nh trạng để tiền c&ocirc;ng đức, tiền cầu lộc g&acirc;y phản cảm. C&aacute;c h&agrave;ng qu&aacute;n, b&atilde;i để xe tại khu du lịch v&agrave; khu vực lễ hội phải được quản l&yacute;.</p>\r\n<p class=\"Normal\">Ngo&agrave;i ra, c&aacute;c đơn vị trực thuộc cần chủ động trao đổi th&ocirc;ng tin để c&aacute;c lễ hội tr&ecirc;n địa b&agrave;n ph&ugrave; hợp với truyền thống, tổ chức khoa học về thời gian, giảm tải người tham gia tại một khu vực trong một thời gian nhất định; thực hiện nghi&ecirc;m chỉ đạo của Thủ tướng về cấm sản xuất, bu&ocirc;n b&aacute;n, đốt ph&aacute;o nổ v&agrave; thả đ&egrave;n trời.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/49/29/a4.jpg\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Người d&acirc;n thường đổ đi lễ ch&ugrave;a dịp năm mới. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Bộ Văn h&oacute;a cũng y&ecirc;u cầu c&aacute;c địa phương tăng cường hoạt động văn ho&aacute;, thể thao v&agrave; du lịch, biểu diễn nghệ thuật phục vụ nh&acirc;n d&acirc;n trước, trong v&agrave; sau Tết, ưu ti&ecirc;n biểu diễn phục vụ đồng b&agrave;o, chiến sĩ v&ugrave;ng bi&ecirc;n giới, hải đảo, n&ocirc;ng th&ocirc;n v&ugrave;ng xa.</p>\r\n<p class=\"Normal\">Đối với việc tổ chức ch&uacute;c Tết trong ng&agrave;nh, Bộ trưởng Văn h&oacute;a thể thao v&agrave; Du lịch chỉ đạo c&aacute;c đơn vị ch&uacute;c Tết c&aacute;n bộ, c&ocirc;ng chức, vi&ecirc;n chức v&agrave; người lao động tại nơi l&agrave;m việc v&agrave; thăm hỏi c&aacute;n bộ l&atilde;o th&agrave;nh, c&aacute;n bộ đ&atilde; nghỉ hưu; kh&ocirc;ng tổ chức đi ch&uacute;c Tết l&atilde;nh đạo c&aacute;c cấp; nghi&ecirc;m cấm sử dụng tiền ng&acirc;n s&aacute;ch nh&agrave; nước để li&ecirc;n hoan, chi&ecirc;u đ&atilde;i, thưởng, biếu; kh&ocirc;ng d&ugrave;ng rượu ngoại để chi&ecirc;u đ&atilde;i, tiếp kh&aacute;ch.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đo&agrave;n Loan</strong></p>', 3, 843, 0, 1, NULL, NULL),
(47, '14 ngày trải nghiệm tại 3 quốc gia châu Âu', '14-Ngay-Trai-Nghiem-Tai-3-Quoc-Gia-Chau-Au', 'Với chuyến đi 14 ngày, bạn sẽ được tới thăm 3 quốc gia châu Âu gồm kinh đô ánh sáng - Pháp, ngã tư của các nền văn minh châu Âu - Thụy Sĩ và cái nôi của nền nghệ thuật thế giới - Italy.', 'chau-au.jpg', 22, '<h1 class=\"Title\">14 ng&agrave;y trải nghiệm tại 3 quốc gia ch&acirc;u &Acirc;u</h1>\r\n<h2 class=\"Lead\">Với chuyến đi 14 ng&agrave;y, bạn sẽ được tới thăm 3 quốc gia ch&acirc;u &Acirc;u gồm kinh đ&ocirc; &aacute;nh s&aacute;ng - Ph&aacute;p, ng&atilde; tư của c&aacute;c nền văn minh ch&acirc;u &Acirc;u - Thụy Sĩ v&agrave; c&aacute;i n&ocirc;i của nền nghệ thuật thế giới - Italy.</h2>\r\n<p class=\"Normal\">Tại Ph&aacute;p, du kh&aacute;ch sẽ được đến cổng chiến thắng Arc de Triomphe, nh&agrave; thờ Đức b&agrave; Paris, th&aacute;p Eiffel, cung điện Versailles, điện Louvre v&agrave; ngắm to&agrave;n cảnh Paris bằng du thuyền tr&ecirc;n s&ocirc;ng Seine. Ngo&agrave;i ra, du kh&aacute;ch c&oacute; cơ hội tới tham quan v&agrave; mua sắm tại La Fayette.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/48/16/chau-au.jpg\" border=\"1\" alt=\"Ch&acirc;u &acirc;u\" width=\"400\" height=\"400\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Geneva, Thụy Sĩ l&agrave; nơi c&oacute; những t&ograve;a nh&agrave; với lối thiết kế tao nh&atilde;, cổ k&iacute;nh với nhiều cửa hiệu đồng hồ như Tissot, Omega, Raymond... C&ograve;n tại th&agrave;nh phố miền trung Thụy Sĩ, Lucerne, c&aacute;ch Geneva 276 km l&agrave; những con phố cổ l&aacute;t đ&aacute; d&agrave;nh cho người đi bộ, những ng&ocirc;i nh&agrave; x&acirc;y dựng c&aacute;ch đ&acirc;y 4-5 thế kỷ. Nơi đ&acirc;y c&ograve;n c&oacute; nhiều bảo t&agrave;ng lớn, nơi c&aacute;c nghệ sĩ đường phố mặc trang phục truyền thống chơi c&aacute;c nhạc cụ cổ. Tại Lucerne, kh&aacute;ch du lịch được đến thăm c&acirc;y cầu gỗ cổ nhất ch&acirc;u &Acirc;u vắt qua s&ocirc;ng Reuss x&acirc;y dựng v&agrave;o khoảng năm 1333, tượng đ&agrave;i sư tử (Lion Monument) được tạc v&agrave;o một v&aacute;ch đ&aacute; in b&oacute;ng xuống nước.</p>\r\n<p class=\"Normal\">Đỉnh Titlis ở \"n&uacute;i Thi&ecirc;n thần\" Engelberg cũng l&agrave; một điểm đến tại Lucerne. Nơi đ&acirc;y, du kh&aacute;ch sẽ đi bằng xe điện từ thị trấn Engelberg, sau đ&oacute; đi bằng c&aacute;p treo l&ecirc;n độ cao 3.020 m tới n&uacute;i Alpes.</p>\r\n<p class=\"Normal\">Nằm c&aacute;ch Lucerne 242 km l&agrave; th&agrave;nh phố Milan \"kinh đ&ocirc; thời trang\" của Italy. Từ Milan, du kh&aacute;ch tới Venice - th&agrave;nh phố c&oacute; nhiều cầu v&agrave; đảo nhất thế giới. Cả th&agrave;nh phố nằm tr&ecirc;n 118 h&ograve;n đảo trong một vịnh k&eacute;o d&agrave;i 60 km, rộng 4 km, tất cả được nối với đất liền bằng những c&ocirc;ng tr&igrave;nh nghệ thuật độc đ&aacute;o. Đảo San Marco v&agrave; c&ocirc;ng nghệ thổi thuỷ tinh, cầu Sighs, th&aacute;nh đường San Marco... l&agrave; những điểm đến th&uacute; vị nơi đ&acirc;y.</p>\r\n<p class=\"Normal\">Tại Italy, bạn kh&ocirc;ng thể bỏ qua th&aacute;p nghi&ecirc;ng Pisa, h&ograve;n ngọc kiến tr&uacute;c v&agrave; l&agrave; một trong những c&ocirc;ng tr&igrave;nh tưởng niệm quan trọng nhất của ch&acirc;u &Acirc;u thời Trung cổ v&agrave; Di sản Văn h&oacute;a thế giới Florence. Điểm cuối của của h&agrave;nh tr&igrave;nh l&agrave; thủ đ&ocirc; Rome với nhiều c&ocirc;ng tr&igrave;nh kiến tr&uacute;c, nghệ thuật, t&ocirc;n gi&aacute;o như bảo t&agrave;ng Vatican (Vatican Museum), th&aacute;nh đường St.Peter, đấu trường La M&atilde; cổ Colosseum, di t&iacute;ch Trung t&acirc;m văn h&oacute;a ch&iacute;nh trị cổ Roman Forum, đ&agrave;i phun nước Trevi...</p>\r\n<p class=\"Normal\">Ch&agrave;o đ&oacute;n Gi&aacute;ng sinh v&agrave; năm mới 2011, C&ocirc;ng ty cổ phần Du lịch Việt Nam - H&agrave; Nội tiếp tục ho&agrave;n thiện v&agrave; đưa ra thị trường tour du lịch ch&acirc;u &Acirc;u. Du kh&aacute;ch sẽ được tận hưởng những dịch vụ cao cấp, nghỉ đ&ecirc;m tại những kh&aacute;ch sạn sang trọng v&agrave; thưởng thức hương vị ẩm thực đặc trưng của nhiều v&ugrave;ng miền của ch&acirc;u &Acirc;u. Ngo&agrave;i ra, chương tr&igrave;nh sẽ đem lại những kiến thức hữu &iacute;ch về đời sống x&atilde; hội, sự hiểu biết về lịch sử, tinh hoa kiến tr&uacute;c, nghệ thuật... ch&acirc;u &Acirc;u v&agrave; những cơ hội được trải nghiệm sự th&acirc;n thiện, cởi mở c&ugrave;ng n&eacute;t văn h&oacute;a truyền thống của người d&acirc;n.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>C&ocirc;ng ty Du lịch Việt Nam &ndash; H&agrave; Nội</em>)</p>', 3, 2740, 0, 1, NULL, NULL),
(48, 'Noel và năm mới tại Vinpearl Resort & Spa', 'Noel-Va-Nam-Moi-Tai-Vinpearl-Resort---Spa', 'Ngày 24, 25/12, khách du lịch ở tại Vinpearl Resort & Spa sẽ được tham gia các hoạt động vui chơi ngoài trời vui nhộn. Vào ngày 31/12, tiệc đón năm mới sẽ được tổ chức trong không gian ấm cúng với nến, rượu vang, chocolate.', '8.jpg', 22, '<h1 class=\"Title\">Noel v&agrave; năm mới tại Vinpearl Resort &amp; Spa</h1>\r\n<h2 class=\"Lead\">Ng&agrave;y 24, 25/12, kh&aacute;ch du lịch ở tại Vinpearl Resort &amp; Spa sẽ được tham gia c&aacute;c hoạt động vui chơi ngo&agrave;i trời vui nhộn. V&agrave;o ng&agrave;y 31/12, tiệc đ&oacute;n năm mới sẽ được tổ chức trong kh&ocirc;ng gian ấm c&uacute;ng với nến, rượu vang, chocolate.</h2>\r\n<p class=\"Normal\">Du kh&aacute;ch chọn Vinpearl Nha Trang l&agrave;m điểm đến trong m&ugrave;a Gi&aacute;ng sinh v&agrave; năm mới chỉ phải trả th&ecirc;m từ 100 USD để tham dự chương tr&igrave;nh tiệc được thiết kế d&agrave;nh ri&ecirc;ng cho m&ugrave;a lễ hội.</p>\r\n<p class=\"Normal\">Người lớn sẽ c&oacute; nhiều game vận động được tổ chức tại bể bơi trong kh&ocirc;ng kh&iacute; s&ocirc;i động bởi sự cộng hưởng của &acirc;m thanh, hoạt n&aacute;o vi&ecirc;n v&agrave; c&ugrave;ng tham gia v&agrave;o cuộc thi cải trang &ocirc;ng gi&agrave; Noel. C&ograve;n c&aacute;c b&eacute; sẽ được tham gia nhiều game mini với &ocirc;ng gi&agrave; Noel như t&ocirc; tượng, vẽ tranh, thi dẫn b&oacute;ng, thi chuyển qu&agrave; c&ugrave;ng &ocirc;ng gi&agrave; Noel v&agrave; sẽ nhận được c&aacute;c phần thưởng ngộ nghĩnh. Điểm nhấn trong c&aacute;c hoạt động đ&oacute;n Noel ch&iacute;nh l&agrave; bữa tiệc đ&ecirc;m Gi&aacute;ng sinh tr&agrave;n ngập kh&ocirc;ng kh&iacute; từ c&aacute;ch trang tr&iacute;, b&agrave;i tr&iacute; m&oacute;n ăn, &acirc;m nhạc&hellip; Tiệc cocktail được phục vụ trước bữa tối ch&iacute;nh, tiệc buffet với c&aacute;c m&oacute;n ăn mang hương vị đặc trưng Noel với đồ uống trong tiệc ch&iacute;nh miễn ph&iacute;.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/47/E4/8.jpg\" border=\"1\" alt=\"Gi&aacute;ng sinh tr&ecirc;n biển.\" width=\"400\" height=\"267\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Gi&aacute;ng sinh tại Vinpearl Nha Trang .</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Tiệc đ&oacute;n năm mới tại Vinpearl Nha Trang Resort &amp; Spa được tổ chức từ 17h ng&agrave;y 31/12 trong kh&ocirc;ng gian ấm c&uacute;ng với ph&aacute;o b&ocirc;ng, nến, rượu vang v&agrave; chocolate. Kh&aacute;ch h&agrave;ng sẽ được tham dự \"Gala dinner new year\" với tiệc buffet c&oacute; hơn 60 m&oacute;n ăn hương vị bốn phương c&ugrave;ng đồ uống miễn ph&iacute;, thưởng thức m&agrave;n tr&igrave;nh diễn của ban nhạc, c&ugrave;ng tham gia c&aacute;c tr&ograve; chơi vui nhộn, c&ugrave;ng đếm ngược đ&oacute;n ch&agrave;o năm mới v&agrave; khi&ecirc;u vũ trước khi kết th&uacute;c bữa tiệc. Về ph&ograve;ng, mỗi kh&aacute;ch sẽ nhận được một m&oacute;n qu&agrave; ch&uacute;c mừng năm mới &yacute; nghĩa. Sau đ&oacute;, trong ng&agrave;y đầu ti&ecirc;n của năm mới 2010 c&aacute;c em nhỏ sẽ được tiếp tục tham gia một số hoạt động vui chơi tại khu Kid Club trong buổi s&aacute;ng.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/47/E4/3.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"269\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">\"Gala dinner new year\" với tiệc buffet c&oacute; hơn 60 m&oacute;n ăn hương vị bốn phương.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Ngo&agrave;i ra nh&acirc;n dịp n&agrave;y, c&aacute;c cửa h&agrave;ng thời trang, đồ lưu niệm tại Vinpearl &hellip; đều đang &aacute;p dụng chương tr&igrave;nh &ldquo;crazy sales&rdquo; để &ldquo;mừng tuổi&rdquo; du kh&aacute;ch với c&aacute;c mức từ 10 đến 70% t&ugrave;y mặt h&agrave;ng.</p>\r\n<p class=\"Normal\">Vinpearl Nha Trang tọa lạc tr&ecirc;n đảo H&ograve;n Tre, l&agrave; khu li&ecirc;n hợp du lịch giải tr&iacute; biển đảo ti&ecirc;u chuẩn 5 sao, đẳng cấp quốc tế nhưng vẫn g&igrave;n giữ vẻ đẹp hoang sơ. Tại đ&acirc;y, c&ograve;n c&oacute; b&atilde;i tắm biển tự nhi&ecirc;n d&agrave;i 700 m, được xem l&agrave; đẹp nhất Nha Trang, khu kh&aacute;ch sạn c&oacute; kiến tr&uacute;c đẹp với 485 ph&ograve;ng nghỉ, trong đ&oacute; c&oacute; 17 ph&ograve;ng hạng sang, 2 ph&ograve;ng tổng thống v&agrave; nhiều dịch vụ giải tr&iacute;, chăm s&oacute;c sức khỏe.</p>\r\n<p class=\"Normal\">Xem th&ecirc;m h&igrave;nh ảnh lễ hội của Vinpearl Nha Trang&nbsp;<a class=\"Normal\" href=\"http://vnexpress.net/GL/Xa-hoi/Du-lich/2010/12/3BA247E4/page_1.asp\">tại đ&acirc;y</a>.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"472\" align=\"center\" bgcolor=\"#dfdfdf\">\r\n<tbody>\r\n<tr>\r\n<td>C&ocirc;ng ty cổ phần Vinpearl &aacute;p dụng chương tr&igrave;nh &ldquo;Gift Voucher&rdquo;<strong>&nbsp;</strong>(thẻ qu&agrave; tặng) với 2 mệnh gi&aacute;: Deluxe Gift Voucher gi&aacute; 399 USD v&agrave; VIP Gift Voucher gi&aacute; 490 USD. Ngo&agrave;i việc đăng k&yacute; thụ hưởng trực tiếp những ưu đ&atilde;i tại Vinpearl Nha Trang Resort v&agrave; Spa, kh&aacute;ch c&ograve;n c&oacute; thể sử dụng &ldquo;Gift Voucher&rdquo; như m&oacute;n qu&agrave; d&agrave;nh tặng bạn b&egrave;, người th&acirc;n. Sử dụng một trong hai loại thẻ đặc biệt tr&ecirc;n, kh&aacute;ch h&agrave;ng sẽ được nhận những m&oacute;n qu&agrave; lưu niệm từ Vinpearl. Đặc biệt, kh&aacute;ch sử dụng Voucher được ưu ti&ecirc;n khi đặt ph&ograve;ng v&agrave; đợi đầu ti&ecirc;n khi kh&ocirc;ng c&ograve;n ph&ograve;ng trống.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>C&ocirc;ng ty cổ phần Vinpearl</em>)</p>', 3, 745, 0, 1, NULL, NULL),
(49, 'Huế lung linh sắc màu đón Giáng sinh', 'Hue-Lung-Linh-Sac-Mau-Don-Giang-Sinh', 'Không khí Giáng sinh đến Huế muộn hơn Sài Gòn, Hà Nội, nhưng không vì thế mà làm giảm đi sắc màu lung linh. Khắp các ngả đường của cố đô ngập tràn ánh đèn điện trang hoàng cho đêm Noel đang cận kề.', 'doi.jpg', 22, '<h1 class=\"Title\">Huế lung linh sắc m&agrave;u đ&oacute;n Gi&aacute;ng sinh</h1>\r\n<h2 class=\"Lead\">Kh&ocirc;ng kh&iacute; Gi&aacute;ng sinh đến Huế muộn hơn S&agrave;i G&ograve;n, H&agrave; Nội, nhưng kh&ocirc;ng v&igrave; thế m&agrave; l&agrave;m giảm đi sắc m&agrave;u lung linh. Khắp c&aacute;c ngả đường của cố đ&ocirc; ngập tr&agrave;n &aacute;nh đ&egrave;n điện trang ho&agrave;ng cho đ&ecirc;m Noel đang cận kề.<br />&gt;&nbsp;<a class=\"Lead\" href=\"http://vnexpress.net/GL/Xa-hoi/2010/12/3BA245BD/\">Kh&ocirc;ng kh&iacute; Gi&aacute;ng sinh trong gi&aacute; lạnh ở H&agrave; Nội</a>/&nbsp;<a class=\"Lead\" href=\"http://vnexpress.net/GL/Xa-hoi/2010/12/3BA242C9/\">S&agrave;i G&ograve;n lung linh đ&oacute;n Gi&aacute;ng sinh</a></h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/48/6B/doi.jpg\" alt=\"\" width=\"495\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/48/6B/hue12.jpg\" alt=\"\" width=\"495\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Khắp nẻo đường của th&agrave;nh phố Huế được trang tr&iacute; đ&egrave;n điện v&agrave; d&ograve;ng chữ quen thuộc ch&agrave;o đ&oacute;n Gi&aacute;ng sinh v&agrave; năm mới.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/48/6B/hue11.jpg\" alt=\"\" width=\"495\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tr&ecirc;n đường phố đến c&aacute;c cửa hiệu, tr&agrave;n ngập quần &aacute;o, đồ trang tr&iacute; Gi&aacute;ng sinh.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/48/6B/hue16.jpg\" alt=\"\" width=\"495\" height=\"341\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hang đ&aacute; với m&aacute;ng cỏ đơn sơ l&agrave; nơi h&agrave;i đồng Gi&ecirc;su sinh ra trong đ&ecirc;m Noel. Cảnh tượng n&agrave;y xuất hiện tại nhiều cửa hiệu lớn.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/48/6B/hue2.jpg\" alt=\"\" width=\"494\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Trẻ nhỏ th&iacute;ch th&uacute; với c&aacute;c gian trang tr&iacute; đ&oacute;n Gi&aacute;ng sinh.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/48/6B/hue6.jpg\" alt=\"\" width=\"495\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nh&agrave; s&aacute;ch Lạc Việt tr&ecirc;n đường H&ugrave;ng Vương d&agrave;nh hẳn một diện t&iacute;ch kh&aacute; lớn để trang tr&iacute;.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/48/6B/hue7.jpg\" alt=\"\" width=\"495\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Thiếu nữ tạo d&aacute;ng cho bạn trai chụp ảnh kỷ niệm trước kh&aacute;ch sạn H&ugrave;ng Vương.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/48/6B/hue8.jpg\" alt=\"\" width=\"495\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Noel cũng l&agrave; dịp để c&aacute;c &ldquo;người mẫu&rdquo; kh&ocirc;ng chuy&ecirc;n thỏa sức tạo d&aacute;ng trước m&aacute;y ảnh.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/48/6B/hue13.jpg\" alt=\"\" width=\"495\" height=\"365\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nh&agrave; thờ xứ đạo Bến Ngự tọa lạc tr&ecirc;n đường Phan Đ&igrave;nh Ph&ugrave;ng được trang ho&agrave;ng đ&egrave;n điện soi b&oacute;ng xuống d&ograve;ng An Cựu.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/48/6B/hue14.jpg\" alt=\"\" width=\"361\" height=\"500\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Th&aacute;p đ&ocirc;i theo kiến tr&uacute;c Ph&aacute;p của gi&aacute;o xứ ch&iacute;nh t&ograve;a Phủ Cam Huế lung linh đ&egrave;n điện.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\">Văn Nguyễn</p>', 3, 2321, 0, 1, NULL, NULL),
(50, 'Ana Mandara Villas Dalat lưu giữ kiến trúc Pháp cổ', 'Ana-Mandara-Villas-Dalat-Luu-Giu-Kien-Truc-Phap-Co', 'Khu nghỉ Ana Mandara Villas Dalat được trùng tu nguyên vẹn kiến trúc cổ biệt thự Pháp, đan xen trang trí vật dụng xưa làm toát lên vẻ quyền uy của giới thượng lưu.', 'du-lich1 (1).jpg', 22, '<h1 class=\"Title\">Ana Mandara Villas Dalat lưu giữ kiến tr&uacute;c Ph&aacute;p cổ</h1>\r\n<h2 class=\"Lead\">Khu nghỉ Ana Mandara Villas Dalat được tr&ugrave;ng tu nguy&ecirc;n vẹn kiến tr&uacute;c cổ biệt thự Ph&aacute;p, đan xen trang tr&iacute; vật dụng xưa l&agrave;m to&aacute;t l&ecirc;n vẻ quyền uy của giới thượng lưu.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/3F/32/du-lich1.jpg\" border=\"1\" alt=\"Kiến tr&uacute;c cổ của biệt thự Ph&aacute;p lu&ocirc;n hấp dẫn c&aacute;c du kh&aacute;ch trong v&agrave; ngo&agrave;i nước.\" width=\"480\" height=\"322\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Kiến tr&uacute;c cổ của biệt thự Ph&aacute;p lu&ocirc;n hấp dẫn c&aacute;c du kh&aacute;ch trong v&agrave; ngo&agrave;i nước.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/3F/32/du-lich2.jpg\" border=\"1\" alt=\"Nh&agrave; h&agrave;ng Le Petit Dalat mang phong c&aacute;ch ch&acirc;u &Acirc;u.\" width=\"480\" height=\"360\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nh&agrave; h&agrave;ng Le Petit Dalat mang phong c&aacute;ch ch&acirc;u &Acirc;u.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/3F/32/du-lich3.jpg\" border=\"1\" alt=\"Khung cảnh l&atilde;ng mạn.\" width=\"480\" height=\"322\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Khung cảnh l&atilde;ng mạn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/3F/32/du-lich4.jpg\" border=\"1\" alt=\"Kiến tr&uacute;c từ cổng đến to&agrave;n bộ villa được phục hồi nguy&ecirc;n vẹn.\" width=\"480\" height=\"322\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Kiến tr&uacute;c từ cổng đến to&agrave;n bộ villa được phục hồi nguy&ecirc;n vẹn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/3F/32/du-lich5.jpg\" border=\"1\" alt=\"N&eacute;t đẹp quyến rũ l&agrave; sự kết hợp h&agrave;i ho&agrave; giữa cảnh quan v&agrave; sự cổ k&iacute;nh.\" width=\"480\" height=\"322\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">N&eacute;t đẹp quyến rũ l&agrave; sự kết hợp h&agrave;i ho&agrave; giữa cảnh quan v&agrave; sự cổ k&iacute;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/3F/32/du-lich6.jpg\" border=\"1\" alt=\"Ph&ograve;ng kh&aacute;ch của Villa 3 cho du kh&aacute;ch cảm gi&aacute;c sống với phong th&aacute;i tầng lớp thượng lưu xa xưa.\" width=\"480\" height=\"324\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ph&ograve;ng kh&aacute;ch của Villa 3 cho du kh&aacute;ch cảm gi&aacute;c sống với phong th&aacute;i tầng lớp thượng lưu xa xưa.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/3F/32/du-lich7.jpg\" border=\"1\" alt=\"Tr&agrave; chiều mang phong c&aacute;ch ch&acirc;u &Acirc;u với c&aacute;c loại b&aacute;nh.\" width=\"400\" height=\"533\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tr&agrave; chiều mang phong c&aacute;ch ch&acirc;u &Acirc;u với c&aacute;c loại b&aacute;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/3F/32/du-lich9.jpg\" border=\"1\" alt=\"Bữa tối l&atilde;ng mạn tại hầm rượu.\" width=\"480\" height=\"322\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Bữa tối l&atilde;ng mạn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/3F/32/du-lich11.jpg\" border=\"1\" alt=\"N&eacute;t hiện đại của khu biệt thự cổ được t&ocirc; điểm bởi hồ bơi nước n&oacute;ng ngo&agrave;i trời.\" width=\"480\" height=\"360\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">N&eacute;t hiện đại của khu biệt thự cổ được t&ocirc; điểm bởi hồ bơi nước n&oacute;ng ngo&agrave;i trời.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:<em>&nbsp;Ninh Van Bay Holiday Club</em>)</p>', 3, 904, 0, 1, NULL, NULL),
(51, 'Khám phá Đài Loan', 'Kham-Pha-Dai-Loan', 'Tòa tháp cao thứ hai thế giới, sông tình yêu, hội chợ hoa quốc tế, Nhật Nguyệt Đàm hay những điểm vui chơi được xem là hiện đại bậc nhất trong khu vực đã hút một lượng khách khá lớn đến Đài Loan.', 'canh.jpg', 22, '<h1 class=\"Title\">Kh&aacute;m ph&aacute; Đ&agrave;i Loan</h1>\r\n<h2 class=\"Lead\">T&ograve;a th&aacute;p cao thứ hai thế giới, s&ocirc;ng t&igrave;nh y&ecirc;u, hội chợ hoa quốc tế, Nhật Nguyệt Đ&agrave;m hay những điểm vui chơi được xem l&agrave; hiện đại bậc nhất trong khu vực đ&atilde; h&uacute;t một lượng kh&aacute;ch kh&aacute; lớn đến Đ&agrave;i Loan.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/38/92/canh.jpg\" alt=\"Ch&uacute; th&iacute;ch ảnh:\" width=\"350\" height=\"523\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">2/3 diện t&iacute;ch Đ&agrave;i Loan l&agrave; đồi n&uacute;i v&agrave; rừng c&acirc;y rậm rạp. Đặt ch&acirc;n đến Đ&agrave;i Loan, du kh&aacute;ch c&oacute; thể được l&ecirc;n t&ograve;a th&aacute;p Taipei 101 tầng, cao thứ hai thế giới, đi thang m&aacute;y nhanh nhất thế giới (37 gi&acirc;y l&ecirc;n được tầng thứ 85).</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/38/92/canh-24.jpg\" alt=\"\" width=\"490\" height=\"343\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nhiều h&agrave;ng hiệu nổi tiếng về quần &aacute;o, giầy d&eacute;p, mỹ phẩm... cũng c&oacute; mặt tại đ&acirc;y. Ngo&agrave;i ra ở t&ograve;a nh&agrave; cao 101 tầng n&agrave;y c&ograve;n c&oacute; nhiều loại đ&aacute; được cho l&agrave; mang lại vận may cho người kinh doanh. Nh&acirc;n dịp Noel, nhiều shop giảm gi&aacute; bất ngờ để thu h&uacute;t Thượng đế.</td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/38/92/canh-7.jpg\" alt=\"\" width=\"490\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/38/92/canh-9.jpg\" alt=\"\" width=\"490\" height=\"334\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\">Hội chợ hoa Flora với chủ đề \"Sắc hoa, Lưu Thủy, Tần Thị Giới\" rộng hơn 90 ha diễn ra tại Đ&agrave;i Bắc, thủ phủ của Đ&agrave;i Loan từ th&aacute;ng 11/2010 tới th&aacute;ng 4/2011. Hội chợ thu h&uacute;t 23 quốc gia như Nhật Bản, Trung Quốc, Mỹ, Ph&aacute;p, Italia, T&acirc;y Ba Nha, Đức... v&agrave; cả Việt Nam.</td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/38/92/canh-6.jpg\" alt=\"\" width=\"490\" height=\"326\" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/38/92/canh-5.jpg\" alt=\"\" width=\"490\" height=\"351\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\">Tại khu triển l&atilde;m hoa quốc tế ở Đ&agrave;i Bắc c&oacute; t&ograve;a nh&agrave; Eco Ark cao 3 tầng, được x&acirc;y dựng bằng 1,5 triệu chai nhựa t&aacute;i chế, đủ chống chọi với động đất, mưa b&atilde;o. Kinh ph&iacute; x&acirc;y dựng khoảng 3,8 triệu USD. Những ng&agrave;y qu&aacute; n&oacute;ng, t&ograve;a nh&agrave; c&oacute; hệ thống tự phun nước l&agrave;m m&aacute;t để c&aacute;c chai kh&ocirc;ng bị biến dạng.</td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/38/92/canh-10.jpg\" alt=\"\" width=\"490\" height=\"328\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Viện bảo t&agrave;ng Cố Cung (National Palace Museum) được c&ocirc;ng nhận l&agrave; một trong bốn viện bảo t&agrave;ng lớn nhất thế giới. Nơi đ&acirc;y c&oacute; hơn 600.000 t&aacute;c phẩm nghệ thuật qu&yacute; gi&aacute;.</td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/38/92/canh-12.jpg\" alt=\"\" width=\"490\" height=\"311\" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/38/92/canh-11.jpg\" alt=\"\" width=\"490\" height=\"306\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Bảo t&agrave;ng gốm ở thị trấn Yingge cũng được du kh&aacute;ch h&aacute;o hức đến thăm trong tour du lịch.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Anh</strong></p>', 3, 593, 0, 1, NULL, NULL),
(52, 'Nghỉ dưỡng tại Evason Ana Mandara Nha Trang', 'Nghi-Duong-Tai-Evason-Ana-Mandara-Nha-Trang', 'Khu du lịch Evason Ana Mandara Nha Trang được xây dựng trong khuôn viên 26.000 m2 bao quanh bởi khu vườn nhiệt đới riêng biệt nhìn ra biển.', 'nha-trang-2.jpg', 22, '<h1 class=\"Title\">Nghỉ dưỡng tại Evason Ana Mandara Nha Trang</h1>\r\n<h2 class=\"Lead\">Khu du lịch Evason Ana Mandara Nha Trang được x&acirc;y dựng trong khu&ocirc;n vi&ecirc;n 26.000 m2 bao quanh bởi khu vườn nhiệt đới ri&ecirc;ng biệt nh&igrave;n ra biển.</h2>\r\n<p class=\"Normal\">Evason Ana Mandara gồm 17 khu biệt thự v&agrave; 74 ph&ograve;ng. C&aacute;c ph&ograve;ng kết hợp giữa kiến tr&uacute;c truyền thống Việt Nam với n&eacute;t ri&ecirc;ng địa phương mang lại sự tiện nghi v&agrave; sang trọng. Khu nghỉ được trang bị nội thất l&agrave;m từ gỗ, m&acirc;y tre v&agrave; đồ gốm tiện nghi, ấn tượng.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/34/89/nha-trang-2.jpg\" border=\"1\" alt=\"Evason Ana Mandara &amp; Six Senses Spa &ndash; Nha Trang.\" width=\"450\" height=\"338\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Evason Ana Mandara &amp; Six Senses Spa &ndash; Nha Trang.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trong 74 ph&ograve;ng kh&aacute;ch, 36 ph&ograve;ng hướng ra phong cảnh vườn, 10 ph&ograve;ng quay ra hướng biển, 24 ph&ograve;ng deluxe v&agrave; 4 ph&ograve;ng suite. Mỗi ph&ograve;ng đều nằm trong khu vườn nhiệt đới sang trọng hoặc quay ra phong cảnh biển của Vịnh Nha Trang. Đặc biệt, tất cả c&aacute;c ph&ograve;ng của Evason Ana Mandara Nha Trang đều c&oacute; s&acirc;n hi&ecirc;n ri&ecirc;ng biệt c&ugrave;ng trang thiết bị hiện đại đạt ti&ecirc;u chuẩn khu du lịch 5 sao.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/34/89/nha-trang-3.jpg\" border=\"1\" alt=\"Nh&agrave; h&agrave;ng với c&aacute;c m&oacute;n ăn đặc sản Nha Trang v&agrave; c&aacute;c m&oacute;n truyền thống Việt Nam.\" width=\"450\" height=\"338\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nh&agrave; h&agrave;ng với c&aacute;c m&oacute;n ăn đặc sản Nha Trang v&agrave; c&aacute;c m&oacute;n truyền thống Việt Nam.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Khu vực nh&agrave; h&agrave;ng c&oacute; c&aacute;c m&oacute;n đặc trưng &Acirc;u &ndash; &Aacute; c&ugrave;ng đặc sản của v&ugrave;ng biển Nha Trang. Đặc biệt, khu spa cao cấp &ndash; Six Senses Spa với bể bơi ngo&agrave;i trời, ph&ograve;ng họp, trung t&acirc;m thương vụ d&agrave;nh cho c&aacute;c doanh nh&acirc;n c&ugrave;ng nhiều hoạt động ngo&agrave;i trời v&agrave; thể thao biển sẽ đ&aacute;p ứng c&aacute;c nhu cầu giải tr&iacute; của du kh&aacute;ch.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/34/89/nha-trang-4.jpg\" border=\"1\" alt=\"Ph&ograve;ng ở khu nghỉ c&oacute; kiến tr&uacute;c h&agrave;i ho&agrave; với n&eacute;t truyền thống Việt Nam.\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ph&ograve;ng ở khu nghỉ c&oacute; kiến tr&uacute;c h&agrave;i ho&agrave; với n&eacute;t truyền thống Việt Nam.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Evason Ana Mandara &amp; Six Senses Spa, Nha Trang xếp vị tr&iacute; thứ 8 trong top 20 khu nghỉ dưỡng h&agrave;ng đầu ch&acirc;u &Aacute; do độc giả Conde Nast Traveler, tạp ch&iacute; du lịch uy t&iacute;n của Mỹ vừa c&ocirc;ng bố danh s&aacute;ch top những điểm đến đ&aacute;ng mơ ước do độc giả b&igrave;nh chọn.</p>\r\n<p class=\"Normal\">Evason Ana Mandara &amp; Six Senses Spa &ndash; Nha Trang</p>\r\n<p class=\"Normal\">Đại lộ Trần Ph&uacute;, TP Nha Trang</p>\r\n<p class=\"Normal\">ĐT: (058) 3522 222 - Fax: (058) 3525 828</p>\r\n<p class=\"Normal\">Email:&nbsp;<a class=\"Normal\" href=\"mailto:reservations-nhatrang@sixsenses.com\">reservations-nhatrang@sixsenses.com</a></p>\r\n<p class=\"Normal\">Website:&nbsp;<a class=\"Normal\" href=\"http://www.sixsenses.com/evason-ana-mandara-nha-trang/\">www.sixsenses.com/evason-ana-mandara-nha-trang/</a></p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Ninh Van Bay Holiday Club</em>)</p>', 3, 229, 0, 1, NULL, NULL),
(53, 'Lên mái nhà của châu Âu', 'Len-Mai-Nha-Cua-Chau-Au', 'Cao 3.454 m, ngọn núi Jungfrau cao nhất thuộc dãy Alps (Thụy Sĩ) quanh năm tuyết phủ. Du khách như lạc trong thế giới truyện cổ tích với những ngôi nhà thơ mộng phủ tuyết trắng xóa bên sườn núi, trong cảnh thiên nhiên hùng vĩ. VnExpress.net ghi nhận một số hình ảnh trên mái nhà châu Âu.', '14.jpg', 22, '<h1 class=\"Title\">L&ecirc;n m&aacute;i nh&agrave; của ch&acirc;u &Acirc;u</h1>\r\n<h2 class=\"Lead\">Cao 3.454 m, ngọn n&uacute;i Jungfrau cao nhất thuộc d&atilde;y Alps (Thụy Sĩ) quanh năm tuyết phủ. Du kh&aacute;ch như lạc trong thế giới truyện cổ t&iacute;ch với những ng&ocirc;i nh&agrave; thơ mộng phủ tuyết trắng x&oacute;a b&ecirc;n sườn n&uacute;i, trong cảnh thi&ecirc;n nhi&ecirc;n h&ugrave;ng vĩ.&nbsp;<em>VnExpress.net</em>&nbsp;ghi nhận một số h&igrave;nh ảnh tr&ecirc;n m&aacute;i nh&agrave; ch&acirc;u &Acirc;u.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/31/5E/18.jpg\" alt=\"\" width=\"495\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Từ thị trấn dưới thung lũng mang t&ecirc;n Interlaken, du kh&aacute;ch đi l&ecirc;n đỉnh n&uacute;i Jungfrau qua hệ thống t&agrave;u b&aacute;nh x&iacute;ch. Chuyến t&agrave;u đặc biệt n&agrave;y đi l&ecirc;n nh&agrave; ga Jungfrau ở độ cao hơn 3.000 m, l&agrave; nh&agrave; ga cao nhất ch&acirc;u &Acirc;u.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/31/5E/23.jpg\" alt=\"\" width=\"495\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một nh&agrave; ga tr&ecirc;n đường l&ecirc;n n&uacute;i phủ tuyết trắng. Mỗi ng&agrave;y, c&aacute;c chuyến t&agrave;u đ&oacute;n khoảng 5.000 du kh&aacute;ch l&ecirc;n đỉnh n&uacute;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/31/5E/21.jpg\" alt=\"\" width=\"495\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những ng&ocirc;i nh&agrave; &ecirc;m đềm b&ecirc;n sườn n&uacute;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/31/5E/19.jpg\" alt=\"\" width=\"495\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">B&ecirc;n những h&agrave;ng th&ocirc;ng trắng x&oacute;a.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/31/5E/22.jpg\" alt=\"\" width=\"495\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cảnh thi&ecirc;n nhi&ecirc;n h&ugrave;ng vĩ v&agrave; thơ mộng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/31/5E/14.jpg\" alt=\"\" width=\"495\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">L&ecirc;n cao, m&acirc;y trắng bao phủ quanh những ngọn n&uacute;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/31/5E/13.jpg\" alt=\"\" width=\"495\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tuyết phủ d&agrave;y.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/31/5E/11.jpg\" alt=\"\" width=\"495\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ngọn n&uacute;i Jungfrau cao nhất ch&acirc;u &Acirc;u thuộc d&atilde;y Alps.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/31/5E/10.jpg\" alt=\"\" width=\"495\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Du kh&aacute;ch b&ecirc;n cộc mốc tr&ecirc;n n&uacute;i tuyết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/31/5E/9.jpg\" alt=\"\" width=\"495\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">L&aacute; cờ Thụy Sĩ tung bay trong nắng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/31/5E/8.jpg\" alt=\"\" width=\"495\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Thời tiết -7 độ C kh&ocirc;ng l&agrave;m giảm nhiệt t&igrave;nh của du kh&aacute;ch miền nhiệt đới tr&ecirc;n n&uacute;i tuyết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/31/5E/12.jpg\" alt=\"\" width=\"495\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Vui th&iacute;ch v&ugrave;i m&igrave;nh trong tuyết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đo&agrave;n Loan</strong></p>', 3, 1765, 0, 1, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(54, 'Sắc màu văn hóa Ấn Độ trên đất Singapore', 'Sac-Mau-Van-Hoa-An-Do-Tren-Dat-Singapore', 'Các thiếu nữ say sưa trong điệu múa truyền thống, người đàn ông bước đi trên than nóng..., lễ hội Ánh sáng của người Ấn trên đảo quốc sư tử đã thu hút sự chú ý của đông đảo người dân bản địa và du khách nước ngoài.', 'anh-sang8.jpg', 22, '<h1 class=\"Title\">Sắc m&agrave;u văn h&oacute;a Ấn Độ tr&ecirc;n đất Singapore</h1>\r\n<h2 class=\"Lead\">C&aacute;c thiếu nữ say sưa trong điệu m&uacute;a truyền thống, người đ&agrave;n &ocirc;ng bước đi tr&ecirc;n than n&oacute;ng..., lễ hội &Aacute;nh s&aacute;ng của người Ấn tr&ecirc;n đảo quốc sư tử đ&atilde; thu h&uacute;t sự ch&uacute; &yacute; của đ&ocirc;ng đảo người d&acirc;n bản địa v&agrave; du kh&aacute;ch nước ngo&agrave;i.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/28/7D/anh-sang8.jpg\" border=\"1\" alt=\"Cổng ch&agrave;o của lễ hội được được trang tr&iacute; lộng lẫy với biểu tượng những ch&uacute; voi v&agrave;ng.\" width=\"480\" height=\"347\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Lễ hội &Aacute;nh s&aacute;ng (Deepavali), một lễ hội lớn trong năm của người Ấn Độ, l&agrave; dịp để mọi người vui chơi v&agrave; phục hồi năng lượng. Hằng năm, lễ hội n&agrave;y cũng được tổ chức rất quy m&ocirc; lớn từ đầu th&aacute;ng 10 đến đầu th&aacute;ng 11 tại khu Tiểu Ấn (Little India) thuộc Singapore, nơi tập trung đ&ocirc;ng đảo cộng đồng người Ấn. Cổng ch&agrave;o của lễ hội được được trang tr&iacute; lộng lẫy với biểu tượng hai ch&uacute; voi v&agrave;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/28/7D/anh-sang5.jpg\" border=\"1\" alt=\"Người Ấn Độ th&iacute;ch được vẽ h&igrave;nh săm l&ecirc;n cơ thể.\" width=\"480\" height=\"319\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hội tụ những n&eacute;t văn h&oacute;a truyền thống của người theo đạo Hin du, đ&acirc;y c&ograve;n l&agrave; nơi thể hiện những đặc trưng về đời sống t&acirc;m linh, x&atilde; hội của người Ấn. Đường phố, nơi diễn ra lễ hội được trang ho&agrave;ng lộng lẫy với h&igrave;nh ảnh chim c&ocirc;ng sặc sỡ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/28/7D/anh-sang1.jpg\" border=\"1\" alt=\"Em b&eacute; Ấn Độ h&oacute;a trang giống một nữ thần.\" width=\"400\" height=\"602\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Em b&eacute; Ấn Độ h&oacute;a trang giống một nữ thần.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/28/7D/anh-sang2.jpg\" border=\"1\" alt=\"Thiếu nữ m&uacute;a điệu truyền thống.\" width=\"480\" height=\"360\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Thiếu nữ uyển chuyển với điệu m&uacute;a truyền thống.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/28/7D/anh-sang9.jpg\" border=\"1\" alt=\"Nghệ sĩ xay m&ecirc; điệu nhảy truyền thống\" width=\"480\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Lễ hội thu h&uacute;t nhiều nghệ sỹ từ Ấn Độ sang biểu diễn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/28/7D/anh-sang12.jpg\" border=\"1\" alt=\"Trẻ em cũng vui tuoi nhay mua trong le hoi.\" width=\"480\" height=\"325\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c em nhỏ cũng vui tươi nhảy m&uacute;a trong lễ hội.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/28/7D/anh-sang3.jpg\" border=\"1\" alt=\"Người đ&agrave;n &ocirc;ng bế con tr&ecirc;n tay đứng trước nh&agrave; thờ v&agrave; cầu nguyện cho gia đ&igrave;nh.\" width=\"400\" height=\"582\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Người đ&agrave;n &ocirc;ng bế con tr&ecirc;n tay đứng trước vị thần v&agrave; cầu nguyện.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/28/7D/anh-sang11.jpg\" border=\"1\" alt=\"Người Ấn Độ th&iacute;ch vẽ những h&igrave;nh săm l&ecirc;n cơ thể.\" width=\"400\" height=\"560\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Người Ấn Độ th&iacute;ch vẽ những h&igrave;nh trang tr&iacute; l&ecirc;n cơ thể.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/28/7D/anh-sang4.jpg\" border=\"1\" alt=\"Những người đ&agrave;n &ocirc;ng thực hiện nghi lễ bước đi tr&ecirc;n lớp than n&oacute;ng.\" width=\"480\" height=\"379\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những người đ&agrave;n &ocirc;ng thực hiện nghi lễ bước đi tr&ecirc;n lớp than n&oacute;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/28/7D/anh-sang6.jpg\" border=\"1\" alt=\"Những v&ograve;ng hoa nhiều m&agrave;u sắc thường d&ugrave;ng trong lễ hội của ngưới Ấn.\" width=\"480\" height=\"349\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những v&ograve;ng hoa treo b&aacute;n cho người đi lễ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/28/7D/anh-sang7.jpg\" border=\"1\" alt=\"Những bức tượng truyền thống kh&aacute; bắt mắt được trưng b&agrave;y trong lễ hội.\" width=\"480\" height=\"334\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những bức tượng thủ c&ocirc;ng mỹ nghệ truyền thống kh&aacute; bắt mắt được trưng b&agrave;y trong lễ hội.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n</strong></p>', 3, 1288, 0, 1, NULL, NULL),
(55, 'Disneyland - nơi giấc mơ cổ tích trở thành sự thực', 'Disneyland---Noi-Giac-Mo-Co-Tich-Tro-Thanh-Su-Thuc', 'Đây là địa điểm tôi thích nhất trong chuyến du ngoạn Hong Kong mùa thu năm nay vì những cảm xúc có được ở đây là những cảm xúc tuyệt vời nhất mà tôi không thể nào quên được. Bạn Nguyễn Thu Hà chia sẻ', '3.jpg', 22, '<p class=\"Title\">Disneyland - nơi gi&acirc;́c mơ c&ocirc;̉ tích trở thành sự thực</p>\r\n<p class=\"Lead\">Đ&acirc;y là địa đi&ecirc;̉m t&ocirc;i thích nh&acirc;́t trong chuy&ecirc;́n du ngoạn Hong Kong mùa thu năm nay vì những cảm xúc có được ở đ&acirc;y là những cảm xúc tuy&ecirc;̣t vời nh&acirc;́t mà t&ocirc;i kh&ocirc;ng th&ecirc;̉ nào qu&ecirc;n được. Bạn Nguyễn Thu H&agrave; chia sẻ.</p>\r\n<p class=\"Normal\">T&ocirc;i bị cu&ocirc;́n hút ngay từ đi&ecirc;̣u nhạc du dương th&acirc;̀n ti&ecirc;n khi bước vào c&ocirc;̉ng c&ocirc;ng vi&ecirc;n. C&ocirc;ng vi&ecirc;n này có 4 khu chính, Fantasy Land là nơi dành cho các em nhỏ gặp gỡ với các nh&acirc;n v&acirc;̣t d&ecirc;̃ thương như chu&ocirc;̣t Mickey, vịt Donald cùng những trò chơi dành cho trẻ con. Hình ảnh đặc trưng cho khu này là l&acirc;u đài của c&ocirc;ng chúa ngủ trong rừng &ldquo;Sleeping beauty castle&rdquo;, nó cũng đ&ocirc;̀ng thời là hình ảnh đặc trưng khi ta nói đ&ecirc;́n Disneyland park.</p>\r\n<p class=\"Normal\">Thứ hai là khu Adventure Land g&ocirc;̀m những trò chơi dành cho thanh thi&ecirc;́u ni&ecirc;n, đặc sắc nh&acirc;́t là hành trình khám phá s&ocirc;ng Amazon kỳ̀ thú và ng&ocirc;i nhà tr&ecirc;n c&acirc;y của Tarzan. Khu Tomorrow Land là những trò chơi khám phá vũ trụ, th&ecirc;́ giới tương lai mang tính khoa học vi&ecirc;̃n tưởng, cũng là nơi chúng ta có th&ecirc;̉ gặp hình ảnh của nh&acirc;n v&acirc;̣t Stich đáng y&ecirc;u trong Lilo v&agrave; Stich. Cu&ocirc;́i cùng là khu Main Street Land được x&acirc;y m&ocirc; phỏng theo khung cảnh thành ph&ocirc;́ ở Mỹ đ&acirc;̀u th&ecirc;́ kỷ 20.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1C/B2/3.jpg\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đại nhạc hội đường phố. Ảnh:&nbsp;<em>Nguyễn Thu H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Các trò chơi thì dĩ nhi&ecirc;n là đặc sắc r&ocirc;̀i, nhưng cái mà t&ocirc;i say m&ecirc; Disneyland nh&acirc;́t chính là được đắm mình trong khung cảnh của th&ecirc;́ giới th&acirc;̀n ti&ecirc;n qua các đại nhạc h&ocirc;̣i s&ocirc;i đ&ocirc;̣ng, những tòa l&acirc;u đài tráng l&ecirc;̣, từ những shop đ&ocirc;̀ lưu ni&ecirc;̣m d&ecirc;̃ thương cho đ&ecirc;́n nhà hàng và cả khách sạn được thi&ecirc;́t k&ecirc;́ theo ki&ecirc;̉u c&ocirc;̉ tích.</p>\r\n<p class=\"Normal\">Có th&ecirc;̉ nói các show di&ecirc;̃n chính là &ldquo;đặc sản&rdquo; của Disneyland, n&ocirc;̉i ti&ecirc;́ng nh&acirc;́t là đại nhạc h&ocirc;̣i đường ph&ocirc;́ &ldquo;Disney on Parade&rdquo; và màn pháo hoa bu&ocirc;̉i t&ocirc;́i mang t&ecirc;n &ldquo;Disney in the Stars&rdquo; tại Main Street Land, hay &ldquo;Lion King Show&rdquo; tại Advanture Land. Chúng t&ocirc;i đ&ecirc;́n g&acirc;̀n 3h chi&ecirc;̀u, là lúc sắp đ&ecirc;́n giờ bi&ecirc;̉u di&ecirc;̃n đại nhạc h&ocirc;̣i đường ph&ocirc;́ &ldquo;Disney on Parade&rdquo;. Vì đã bi&ecirc;́t trước n&ecirc;n t&ocirc;i r&acirc;́t háo hức đứng x&ecirc;́p hàng trước đ&ecirc;̉ chọn ch&ocirc;̃ đứng xem cho đẹp.</p>\r\n<p class=\"Normal\">Th&acirc;̣t khó di&ecirc;̃n tả được cảm xúc khi được gặp Alice cùng chú thỏ trắng đãng trí, cả bà hoàng h&acirc;̣u trong &ldquo;Alice in Wonderland&rdquo;. Khi đọc truy&ecirc;̣n này t&ocirc;i th&acirc;́y cái bà hoàng h&acirc;̣u này vừa đáng ghét lại vừa nh&ocirc;̣n. R&ocirc;̀i thì Cinderalla, nàng ti&ecirc;n cá và nàng Bạch Tuy&ecirc;́t xinh đẹp tuy&ecirc;̣t tr&acirc;̀n đúng ki&ecirc;̉u c&ocirc;̉ tích thoát tục, cũng nhảy múa tung tăng tr&ecirc;n các tòa l&acirc;u đài tráng l&ecirc;̣, kh&ocirc;ng th&ecirc;̉ tin là tr&ecirc;n đời lại có những c&ocirc; c&ocirc;ng chúa đẹp như th&ecirc;́.</p>\r\n<p class=\"Normal\">Ti&ecirc;́p sau đ&acirc;́y là Mickey, Minnie, Donal, Daisy và Pluto nữa. Cả các nh&acirc;n v&acirc;̣t trong Toy Story cũng ra v&acirc;̃y chào khán giả như th&acirc;̣t. Đám đ&ocirc;ng xung quanh thì ph&acirc;́n khích v&ocirc;̃ tay &acirc;̀m ĩ. Hi&ecirc;̣u ứng &acirc;m thanh được sử dụng r&acirc;́t thành c&ocirc;ng khi&ecirc;́n khán giả chỉ mu&ocirc;́n nhảy múa lắc lư cùng các nh&acirc;n v&acirc;̣t. Trong bài hát có đoạn &ldquo;Yes, you believe that dream comes true&hellip;&rdquo;.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1C/B2/4.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Alice in Wonderland. Ảnh:&nbsp;<em>Nguyễn Thu H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Cả khi ng&ocirc;̀i trong phòng phim 3D cũng v&acirc;̣y, cảm xúc d&acirc;ng trào mãnh li&ecirc;̣t khi được ng&ocirc;̀i tr&ecirc;n thảm bay cùng Aladin và c&ocirc;ng chúa trong giai đi&ecirc;̣u ngọt ngào hứng khởi của bài &ldquo;The whole new world&rdquo;. N&ecirc;́u là xem m&ocirc;̣t b&ocirc;̣ phim 3D bom t&acirc;́n nào đó thì sẽ kh&ocirc;ng có cảm giác đặc bi&ecirc;̣t đ&ecirc;́n v&acirc;̣y.</p>\r\n<p class=\"Normal\">Vì những nh&acirc;n v&acirc;̣t hoạt hình của Walt Disney đã là m&ocirc;̣t ph&acirc;̀n của tu&ocirc;̉i thơ t&ocirc;i, từ Bạch Tuy&ecirc;́t đ&ecirc;́n Cinderalla, Ariel, Belle &amp; The beast, r&ocirc;̀i Aladin, Lion King&hellip; Phút gi&acirc;y đó đưa t&acirc;m thức t&ocirc;i ngược dòng thời gian trở lại những ngày xưa, cái h&ocirc;̀i bé thơ v&acirc;̃n còn m&ecirc; m&acirc;̉n với truy&ecirc;̣n c&ocirc;̉ tích v&agrave; phim hoạt hình, được bi&ecirc;́t đ&ecirc;́n thảm bay và ước ao mình cũng được bay như th&ecirc;́.</p>\r\n<p class=\"Normal\">H&ocirc;̀i bé, trong những khả năng si&ecirc;u nhi&ecirc;n mà t&ocirc;i được bi&ecirc;́t đ&ecirc;́n, t&ocirc;i v&acirc;̃n thích bay nh&acirc;́t, và khi ngủ t&ocirc;i r&acirc;́t hay mơ là mình bay đ&ecirc;́n những nơi v&ocirc; cùng đẹp đẽ, có thung lũng và đ&ocirc;̀ng cỏ xanh mướt. Lớn l&ecirc;n t&ocirc;i kh&ocirc;ng còn có những gi&acirc;́c mơ kỳ̀ thú trẻ con đó nữa, và thỉnh thoảng t&ocirc;i v&acirc;̃n ti&ecirc;́c nhớ những gi&acirc;́c mơ &acirc;́y. Với m&ocirc;̃i chúng ta, ngoài h&ocirc;̀i ức v&ecirc;̀ m&ocirc;̣t cu&ocirc;̣c tình ra, thì h&ocirc;̀i ức v&ecirc;̀ tu&ocirc;̉i thơ là m&ocirc;̣t mi&ecirc;̀n k&yacute; ức nhạy cảm và thi&ecirc;ng li&ecirc;ng nh&acirc;́t, chỉ c&acirc;̀n chạm nhẹ vào là đã đủ khi&ecirc;́n t&acirc;m h&ocirc;̀n chúng ta rung đ&ocirc;̣ng x&ocirc;́n xang.</p>\r\n<p class=\"Normal\">T&ocirc;i đ&ecirc;́n với Disneyland ở tu&ocirc;̉i 21, và đ&acirc;y chính là nơi mà gi&acirc;́c mơ tu&ocirc;̉i thơ t&ocirc;i trở thành sự thực. Kh&ocirc;ng hi&ecirc;̉u sao sau khi đ&ecirc;́n đ&acirc;y, t&ocirc;i cảm th&acirc;́y có nhi&ecirc;̀u ni&ecirc;̀m tin hơn, y&ecirc;u cu&ocirc;̣c s&ocirc;́ng hơn, gi&ocirc;́ng như cảm giác m&ocirc;̣t t&acirc;m h&ocirc;̀n đang trưởng thành còn vương chút trẻ thơ vừa tìm được nơi nương tựa. Đ&ocirc;i lúc t&ocirc;i v&acirc;̃n cảm th&acirc;́y mình gi&ocirc;́ng Holden Caufield, anh chàng mới lớn lu&ocirc;n mu&ocirc;́n níu giữ tu&ocirc;̉i thơ, lu&ocirc;n l&acirc;̉m nh&acirc;̉m bài hát &ldquo;bắt trẻ đ&ocirc;̀ng xanh&rdquo; và chỉ có m&ocirc;̣t ước mơ là sẽ đứng ở m&ocirc;̣t đ&ocirc;̀ng cỏ tr&ecirc;n núi đ&ecirc;̉ làm nhi&ecirc;̣m vụ đỡ những đứa trẻ bị rơi ra khỏi cánh đ&ocirc;̀ng xanh mướt tu&ocirc;̉i thơ của chúng. Và t&acirc;m h&ocirc;̀n Holden dường như đã tìm được nơi nương tựa khi anh dắt tay đứa em gái nhỏ đi tr&ecirc;n con đường trải dài trước mắt. Thì đ&acirc;y, cảm giác của t&ocirc;i khi đắm mình trong kh&ocirc;ng gian của Disneyland cũng g&acirc;̀n gi&ocirc;́ng như v&acirc;̣y.</p>\r\n<p class=\"Normal\">M&ocirc;̣t ngày nào đó n&ecirc;́u như tr&ecirc;n đường đời, t&ocirc;i có gặp đi&ecirc;̀u gì đau kh&ocirc;̉ hay đ&ocirc;̉ vỡ, nh&acirc;́t định t&ocirc;i sẽ nghĩ đ&ecirc;́n Disneyland như là m&ocirc;̣t đi&ecirc;̉m đ&ecirc;́n đ&ecirc;̉ tìm lại ni&ecirc;̀m hứng khởi. Hoặc n&ecirc;́u t&ocirc;i có con, t&ocirc;i cũng sẽ c&ocirc;́ gắng cho chúng tới đ&acirc;y, tu&ocirc;̉i đẹp nh&acirc;́t đ&ecirc;̉ vào đ&acirc;y theo t&ocirc;i là khoảng 16-17. Hoặc n&ecirc;́u kh&ocirc;ng phải cả hai trường hợp tr&ecirc;n, chỉ là tình cờ t&ocirc;i được trở lại đ&acirc;y, thì t&ocirc;i cũng sẽ v&ocirc; cùng hạnh phúc, khi l&acirc;̀n thứ hai được trở v&ecirc;̀ với gi&acirc;́c mơ xưa cũ đẹp đẽ và v&ocirc; tư.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nguyễn Thu H&agrave;</strong></p>', 3, 574, 0, 1, NULL, NULL),
(56, 'Thắng cảnh suối nước Vàng', 'Thang-Canh-Suoi-Nuoc-Vang', 'Cách thành phố Bắc Giang chừng 60 km về phía đông bắc, thắng cảnh nước Vàng ở xã Lục Sơn (Lục Nam, Bắc Giang) thuộc khu bảo tồn thiên nhiên Tây Yên Tử là điểm đến đầy kỳ thú cho các chuyến dã ngoại của những du khách “bụi” đi tìm cảm giác mạnh. Độc giả Nguyễn Văn Hưởng viết.', '1.jpg', 22, '<p class=\"Title\">Thắng cảnh suối nước V&agrave;ng</p>\r\n<p class=\"Lead\">C&aacute;ch th&agrave;nh phố Bắc Giang chừng 60 km về ph&iacute;a đ&ocirc;ng bắc, thắng cảnh nước V&agrave;ng ở x&atilde; Lục Sơn (Lục Nam, Bắc Giang) thuộc khu bảo tồn thi&ecirc;n nhi&ecirc;n T&acirc;y Y&ecirc;n Tử l&agrave; điểm đến đầy kỳ th&uacute; cho c&aacute;c chuyến d&atilde; ngoại của những du kh&aacute;ch &ldquo;bụi&rdquo; đi t&igrave;m cảm gi&aacute;c mạnh. Độc giả Nguyễn Văn Hưởng viết.</p>\r\n<p class=\"Normal\">Đường v&agrave;o suối nước V&agrave;ng quanh co, uốn lượn, những cung đường đất đ&aacute; ch&ecirc;nh v&ecirc;nh, mỗi khi c&oacute; trận lũ đi qua mọi thứ đ&aacute; sỏi, c&agrave;nh c&acirc;y, l&aacute; rừng lại chặn ngang khiến đường đi c&agrave;ng th&ecirc;m kh&oacute;. Những con suối vắt ngang đường l&agrave;m xe kh&ocirc;ng thể lội qua, người d&acirc;n Lục Sơn dựng những chiếc cầu tạm bằng gỗ th&igrave; mới c&oacute; thể đi được.</p>\r\n<p class=\"Normal\">Từ UBND x&atilde; Lục Sơn đến suối nước V&agrave;ng chừng 5 km, đường đ&aacute; sỏi l&ocirc; nh&ocirc;, những con &ldquo;tuấn m&atilde;&rdquo; mang nh&atilde;n hiệu Mink của Nga được nhiều du kh&aacute;ch chọn l&agrave;m phương tiện tr&ecirc;n h&agrave;nh tr&igrave;nh đầy gian nan n&agrave;y.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1C/A1/1.jpg\" alt=\"\" width=\"480\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">N&uacute;i rừng T&acirc;y Y&ecirc;n Tử. Ảnh:&nbsp;<em>Nguyễn Văn Hưởng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Nước V&agrave;ng l&agrave; t&ecirc;n gọi của d&ograve;ng suối chảy từ tr&ecirc;n đỉnh d&atilde;y n&uacute;i Phật Sơn thuộc khu bảo tồn thi&ecirc;n nhi&ecirc;n T&acirc;y Y&ecirc;n Tử, (n&uacute;i Phật Sơn), t&ecirc;n gọi của con suối cũng ch&iacute;nh l&agrave; m&agrave;u nước rất độc đ&aacute;o v&agrave; kỳ lạ ở đ&acirc;y. Quanh năm nước chỉ c&oacute; một m&agrave;u duy nhất đ&oacute; l&agrave; m&agrave;u v&agrave;ng &oacute;ng như m&agrave;u mật ong rừng, những du kh&aacute;ch mới đến đ&acirc;y lần đầu đều kh&ocirc;ng khỏi ngạc nhi&ecirc;n về m&agrave;u nước độc đ&aacute;o v&agrave; đặc trưng c&oacute; một kh&ocirc;ng hai tr&ecirc;n đất nước ta n&agrave;y.</p>\r\n<p class=\"Normal\">Đến suối nước V&agrave;ng l&agrave; đến một v&ugrave;ng đại ng&agrave;n h&ugrave;ng vĩ, bao la rộng lớn, khu bảo tồn rừng nguy&ecirc;n sinh T&acirc;y Y&ecirc;n Tử với hệ thảm thực vật, động vật phong ph&uacute;, đa dạng, c&aacute;c lo&agrave;i lan rừng, thảo dược qu&yacute; hiếm như: trầm hương, ba k&iacute;ch, sa nh&acirc;n, Pơ mu&hellip; c&ugrave;ng c&aacute;c lo&agrave;i động vật như: vọc đen m&aacute; trắng, gấu ngựa, hươu v&agrave;ng, s&oacute;c, cầy hương c&ugrave;ng c&aacute;c lo&agrave;i chim rừng&hellip; Kh&iacute; hậu nơi đ&acirc;y &ocirc;n h&ograve;a, m&aacute;t mẻ c&aacute;c th&aacute;ng cuối năm thường c&oacute; m&acirc;y m&ugrave; bao phủ rất th&iacute;ch hợp cho việc du lịch nghỉ dưỡng.</p>\r\n<p class=\"Normal\">Du kh&aacute;ch trải qua những khoảnh khắc vượt th&aacute;c, băng rừng, lội suối đầy th&uacute; vị v&agrave; đ&aacute;ng nhớ. Để kh&aacute;m ph&aacute; v&agrave; chinh phục con suối từ ph&iacute;a hạ nguồn l&ecirc;n miền thượng mất qu&aacute; nửa ng&agrave;y, sau khi băng qua gần 20 th&aacute;c ghềnh lớn nhỏ, một số th&aacute;c đẹp như th&aacute;c Anh Vũ, th&aacute;c M&acirc;y, Th&aacute;c Gi&oacute;t, Th&aacute;c Nước V&agrave;ng&hellip; Th&aacute;c n&agrave;o cũng đẹp, cũng hấp dẫn v&agrave; lạ mắt, ph&iacute;a dưới mỗi th&aacute;c đều c&oacute; những bồn tắm thi&ecirc;n tạo c&oacute; thể chứa đến v&agrave;i chục người, l&agrave; nơi để bạn c&oacute; thể thỏa sức v&ugrave;ng vẫy, giải nhiệt c&ugrave;ng d&ograve;ng nước m&aacute;t lạnh từ tr&ecirc;n cao dội l&ecirc;n cơ thể ta.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1C/A1/2.jpg\" alt=\"\" width=\"480\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đường đến nước V&agrave;ng. Ảnh:&nbsp;<em>Nguyễn Văn Hưởng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Đỉnh suối nước V&agrave;ng c&aacute;ch ch&ugrave;a Đồng - Y&ecirc;n Tử v&agrave; am Ngọa V&acirc;n (nơi Điều Ngự Gi&aacute;c Ho&agrave;ng - vua Trần Nh&acirc;n T&ocirc;ng tu ẩn v&agrave; vi&ecirc;n tịch tại đ&acirc;y) kh&ocirc;ng xa, đứng b&ecirc;n n&agrave;y c&oacute; thể tr&ocirc;ng thấy rất r&otilde; Y&ecirc;n Tử b&ecirc;n sườn đ&ocirc;ng. Từ đ&acirc;y du kh&aacute;ch c&oacute; thể vượt n&uacute;i Phật Sơn để l&ecirc;n thiền viện Hồ Thi&ecirc;n (nơi tổ đệ nhất Trần Nh&acirc;n T&ocirc;ng tu h&agrave;nh, giảng đạo).</p>\r\n<p class=\"Normal\">Danh thắng nước V&agrave;ng được UBND tỉnh Bắc Giang khoanh v&ugrave;ng bảo vệ v&agrave; ra quyết định c&ocirc;ng nhận l&agrave; danh lam thắng cảnh của tỉnh.</p>\r\n<p class=\"Normal\">Danh thắng nước V&agrave;ng, n&uacute;i Phật Sơn - Y&ecirc;n Tử với những c&aacute;nh rừng nguy&ecirc;n sinh xanh thẳm v&agrave; nhiều thảo mộc, mu&ocirc;ng th&uacute;, lại vắt l&ecirc;n m&igrave;nh một d&ograve;ng suối c&oacute; sắc v&agrave;ng lung linh v&agrave; bồn tắm thi&ecirc;n tạo, chắc chắn sẽ l&agrave;m h&agrave;i l&ograve;ng du kh&aacute;ch khi đến đ&acirc;y.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nguyễn Văn Hưởng</strong></p>', 3, 551, 0, 1, NULL, NULL),
(57, 'Nghỉ mát ở thành phố Cebu ', 'Nghi-Mat-O-Thanh-Pho-Cebu', 'Cách Manila 365 dặm về phía nam, Cebu được coi là một trung tâm hành chính thứ hai của Philippines. Cebu còn nổi tiếng bởi những bãi cát trắng sạch và nước biển trong xanh như pha lê. Bạn Kenji Chen chia sẻ. ', '2.jpg', 22, '<p class=\"Title\"><span style=\"font-size: large;\">Nghỉ m&aacute;t ở th&agrave;nh phố Cebu</span></p>\r\n<p class=\"Lead\"><strong><span style=\"font-size: small;\">C&aacute;ch Manila 365 dặm về ph&iacute;a nam, Cebu được coi l&agrave; một trung t&acirc;m h&agrave;nh ch&iacute;nh thứ hai của Philippines. Cebu c&ograve;n nổi tiếng bởi những b&atilde;i c&aacute;t trắng sạch v&agrave; nước biển trong xanh như pha l&ecirc;. Bạn Kenji Chen chia sẻ.&nbsp;</span></strong><br /><br /></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/19/09/2.jpg\" alt=\"\" width=\"480\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">B&atilde;i biển trong xanh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/19/09/3.jpg\" alt=\"\" width=\"480\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cebu c&ograve;n l&agrave; th&agrave;nh phố của resort.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/19/09/4.jpg\" alt=\"\" width=\"480\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nơi đ&acirc;y th&iacute;ch hợp cho nghỉ dưỡng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/19/09/1.jpg\" alt=\"\" width=\"480\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&oacute; nhiều khu nghỉ dưỡng với những dịch vụ chuy&ecirc;n nghiệp.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Kenji Chen</strong></p>', 3, 2540, 0, 1, NULL, NULL),
(58, 'Nhiều tour khám phá Hà Nội dịp đại lễ', 'Nhieu-Tour-Kham-Pha-Ha-Noi-Dip-Dai-Le', 'Các công ty du lịch ở Hà Nội ồ ạt tung ra nhiều tour khám phá phố cổ, thăm các võ đường hay đến kinh đô Việt cổ để phục vụ du khách muốn tìm hiểu thắng cảnh, văn hóa đất Tràng An.', '9.jpg', 22, '<p class=\"Title\">Nhiều tour kh&aacute;m ph&aacute; H&agrave; Nội dịp đại lễ</p>\r\n<p class=\"Lead\">C&aacute;c c&ocirc;ng ty du lịch ở H&agrave; Nội ồ ạt tung ra nhiều tour kh&aacute;m ph&aacute; phố cổ, thăm c&aacute;c v&otilde; đường hay đến kinh đ&ocirc; Việt cổ để phục vụ du kh&aacute;ch muốn t&igrave;m hiểu thắng cảnh, văn h&oacute;a đất Tr&agrave;ng An.</p>\r\n<p class=\"Normal\">C&ocirc;ng ty Cổ phần Đồng Xu&acirc;n đưa ra 5 tour kh&aacute;m ph&aacute; phố cổ H&agrave; Nội bằng xe điện. Du kh&aacute;ch được kh&aacute;m ph&aacute; những c&ocirc;ng tr&igrave;nh đặc trưng trong l&ograve;ng phố cổ; thăm c&aacute;c nh&agrave; nghề v&agrave; nghệ nh&acirc;n trong phố nghề ti&ecirc;u biểu; đến c&aacute;c đ&igrave;nh, đền, ch&ugrave;a cổ để t&igrave;m hiểu văn h&oacute;a t&acirc;m linh; t&igrave;m hiểu văn h&oacute;a ẩm thực nổi tiếng của đất Kinh kỳ; tham quan tuyến phố thương mại sầm uất của 36 phố phường H&agrave; Nội xưa v&agrave; mua sắm tại chợ Đồng Xu&acirc;n.&nbsp;<br /><br />Đặc sắc nhất phải kể tới chương tr&igrave;nh kh&aacute;m ph&aacute; n&eacute;t đặc trưng trong l&ograve;ng phố cổ. Xe điện sẽ đưa du kh&aacute;ch đi qua 19 tuyến phố, trong đ&oacute; kh&aacute;ch c&oacute; thể lựa chọn ba trong số c&aacute;c điểm di t&iacute;ch 48 H&agrave;ng Ngang, chợ Đồng Xu&acirc;n, ch&ugrave;a Huyền Thuy&ecirc;n, đền Bạch M&atilde;, &Ocirc; Quan Chưởng, Nh&agrave; cổ 87 M&atilde; M&acirc;y, Nh&agrave; thờ Lớn, tượng đ&agrave;i vua L&ecirc; Th&aacute;i Tổ, tượng đ&agrave;i Vua L&yacute; C&ocirc;ng Uẩn, đền Ngọc Sơn để tham quan trong v&ograve;ng 30 ph&uacute;t mỗi điểm.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/10/DB/9.jpg\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Du kh&aacute;ch tham quan phố cổ H&agrave; Nội dịp đại lễ. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&ocirc;ng ty Du lịch Vietran Tour đưa ra chương tr&igrave;nh \"Truy t&igrave;m b&iacute; k&iacute;p L&yacute; Triều\". Bằng tr&ograve; chơi phối hợp đồng đội, du kh&aacute;ch sẽ được kh&aacute;m ph&aacute; bề d&agrave;y kh&ocirc;ng gian - thời gian lịch sử của văn hiến Thăng Long. Ngo&agrave;i ra du kh&aacute;ch sẽ được tham quan những l&agrave;ng cổ phụ cận, danh lam thắng cảnh mang phong vị, hương sắc, học thuật của H&agrave; Nội.</p>\r\n<p class=\"Normal\">C&ocirc;ng ty Vietran Tour c&ograve;n c&oacute; chương tr&igrave;nh thăm c&aacute;c v&otilde; đường, đưa du kh&aacute;ch trải nghiệm v&agrave; kh&aacute;m ph&aacute; lại v&otilde; sử Việt Nam m&agrave; dấu t&iacute;ch n&oacute; đang được lưu lại ở ch&iacute;nh đất Thăng Long.</p>\r\n<p class=\"Normal\">Tại ch&ugrave;a Trầm, du kh&aacute;ch sẽ xem c&aacute;c m&ocirc;n sinh biểu diễn ph&aacute;i v&otilde; B&igrave;nh Định Gia. Đo&agrave;n c&oacute; thể c&ugrave;ng tham gia tập thử v&agrave; biểu diễn. B&igrave;nh Định gia l&agrave; một m&ocirc;n ph&aacute;i v&otilde; thuật cổ truyền Việt Nam thuộc hệ ph&aacute;i v&otilde; B&igrave;nh Định. S&aacute;ng tổ của m&ocirc;n ph&aacute;i n&agrave;y l&agrave; Trần Đại Ch&iacute;, một v&otilde; tướng dưới triều Thanh (Trung Quốc), sau n&agrave;y sang định cư ở B&igrave;nh Định.</p>\r\n<p class=\"Normal\">Tại ch&ugrave;a Ph&aacute;p V&acirc;n - Y&ecirc;n Sở, v&otilde; đường của m&ocirc;n ph&aacute;i V&otilde; Đang Th&aacute;i Cực Ch&iacute;nh T&ocirc;ng, du kh&aacute;ch sẽ tham quan ch&ugrave;a, xem biểu diễn nghệ thuật của m&ocirc;n ph&aacute;i n&agrave;y. Tham gia tour n&agrave;y, kh&aacute;ch cũng sẽ được đến v&otilde; đường Bảo Long, nơi đang đ&agrave;o tạo v&otilde; thuật theo gi&aacute;o tr&igrave;nh của học viện V&otilde; thuật Tiểu Long Thiếu L&acirc;m Tự (Trung Quốc)...</p>\r\n<p class=\"Normal\">&ldquo;H&agrave;nh tr&igrave;nh 1000 năm Thăng Long - Đ&ocirc;ng Đ&ocirc; - H&agrave; Nội&rdquo; l&agrave; bộ sưu tập tour do Vietravel thực hiện, kh&aacute; th&iacute;ch hợp với những du kh&aacute;ch từ c&aacute;c tỉnh th&agrave;nh, bao gồm 4 chương tr&igrave;nh như sau: H&agrave; Nội - du thuyền Hạ Long - Tr&agrave;ng An - Cố đ&ocirc; Hoa Lư (4 ng&agrave;y); H&agrave; Nội - Y&ecirc;n Tử - Hạ Long - Ninh B&igrave;nh (4 ng&agrave;y); H&agrave; Nội - Ph&uacute; Thọ - Ninh B&igrave;nh - Bắc Ninh - Hạ Long (6 ng&agrave;y); H&agrave; Nội - Y&ecirc;n Tử - Hạ Long (Tuần Ch&acirc;u) - Sapa (6 ng&agrave;y).</p>\r\n<p class=\"Normal\">Du kh&aacute;ch kh&ocirc;ng chỉ tham quan thắng cảnh lịch sử, những điểm đến mang dấu t&iacute;ch ng&agrave;n năm như: Bắc Ninh, Ph&uacute; Thọ, Ninh B&igrave;nh, Hoa Lư, H&agrave; Nội&hellip; m&agrave; c&ograve;n được tham dự c&aacute;c hoạt động trong chương tr&igrave;nh đại lễ kỷ niệm 1000 năm Thăng Long &ndash; H&agrave; Nội như: Li&ecirc;n hoan ẩm thực H&agrave; Th&agrave;nh, chương tr&igrave;nh nghệ thuật &ldquo;Thăng Long - H&agrave; Nội, thời đại Hồ Ch&iacute; Minh&rdquo;, triển l&atilde;m &ldquo;Thăng Long - H&agrave; Nội xưa&rdquo;, lễ hội đường phố, li&ecirc;n hoan thả diều, biểu diễn của c&aacute;c đo&agrave;n nghệ thuật quốc tế...</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đo&agrave;n Loan</strong></p>', 3, 2650, 0, 1, NULL, NULL),
(59, 'Du lịch các nước châu Á - Thái Bình Dương bằng tàu biển', 'Du-Lich-Cac-Nuoc-Chau-A---Thai-Binh-Duong-Bang-Tau-Bien', 'Bạn có thể tới thăm Singapore hay Hong Kong - Nhật Bản bằng du thuyền cao cấp với giá hấp dẫn 16.030.000 đồng và 49.700.000 đồng.', '9 (1).jpg', 22, '<p class=\"Title\">Du lịch c&aacute;c nước ch&acirc;u &Aacute; - Th&aacute;i B&igrave;nh Dương bằng t&agrave;u biển</p>\r\n<p class=\"Lead\">Bạn c&oacute; thể tới thăm Singapore hay Hong Kong - Nhật Bản bằng du thuyền cao cấp với gi&aacute; hấp dẫn 16.030.000 đồng v&agrave; 49.700.000 đồng.</p>\r\n<p class=\"Normal\">Từ cảng Ti&ecirc;n Sa - Đ&agrave; Nẵng, bạn sẽ cập bến tại thi&ecirc;n đường mua sắm - Singapore. Tại đ&acirc;y, du kh&aacute;ch c&oacute; thể chi&ecirc;m ngưỡng c&ocirc;ng vi&ecirc;n Sư tử biển, bảo t&agrave;ng S&aacute;p, vườn chim Jurong, t&ograve;a Thị ch&iacute;nh, t&ograve;a nh&agrave; Quốc hội, nh&agrave; h&aacute;t &ldquo;Con Nh&iacute;m&rdquo;, thưởng thức chương tr&igrave;nh nhạc nước m&agrave;u kỳ ảo nổi tiếng...</p>\r\n<p class=\"Normal\">Bạn c&ograve;n c&oacute; thể thoải m&aacute;i thưởng thức c&aacute;c chương tr&igrave;nh vui chơi giải tr&iacute; phong ph&uacute;, đa dạng như khi&ecirc;u vũ, tập thể dục thẩm mỹ, &acirc;m nhạc, phim h&agrave;i, ảo thuật, tr&agrave; đạo... ngay tr&ecirc;n t&agrave;u. Đồng thời, du kh&aacute;ch được phục vụ những m&oacute;n ăn phương Đ&ocirc;ng đa dạng v&agrave; ẩm thực hiện đại phương T&acirc;y.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/0D/CC/9.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"301\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Điểm dừng ch&acirc;n ở Nhật Bản gi&uacute;p du kh&aacute;ch kh&aacute;m ph&aacute; danh lam thắng cảnh đất nước mặt trời mọc với ng&ocirc;i đền cổ Sensoji, Cung điện Ho&agrave;ng gia, ch&ugrave;a V&agrave;ng, đền Nijo v&agrave; Ho&agrave;ng cung Nhật Bản lộng lẫy... Bạn c&ograve;n c&oacute; thể chinh phục ngọn n&uacute;i Ph&uacute; Sĩ bởi những chỏm tuyết bạc trong &aacute;nh nắng ho&agrave;ng h&ocirc;n; h&ograve;a m&igrave;nh trong thung lũng Owakudani; mua sắm v&agrave; thưởng thức m&oacute;n ăn mang đậm m&agrave;u sắc Nhật Bản.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/0D/CC/8.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"337\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trong khi đ&oacute;, Hong Kong lại mang vẻ đẹp hiện đại. Vịnh nước Cạn Repulse bay, Trung t&acirc;m b&agrave;n giao Hong Kong, Quảng trường Golden Bauhina, đền Wong Tai Sin l&agrave; những điểm du lịch th&uacute; vị tại đ&acirc;y. Du kh&aacute;ch c&oacute; thể lựa chọn những m&oacute;n đồ ưng &yacute; ở c&aacute;c khu mua sắm cao tầng sang trọng, xen lẫn với c&aacute;c khu phố b&aacute;n đồ rẻ.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/0D/CC/2.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"299\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">B&agrave; Vĩnh Ho&agrave;ng, Gi&aacute;m đốc điều h&agrave;nh C&ocirc;ng ty ICC Travel cho biết, đ&acirc;y l&agrave; một kiểu du lịch hấp dẫn. Trong tương lai, nhiều kh&aacute;ch h&agrave;ng sẽ quan t&acirc;m hơn đến loại h&igrave;nh du lịch t&agrave;u biển n&agrave;y. C&ocirc;ng ty ICC Travel c&oacute; nhiều chương tr&igrave;nh khuyến m&atilde;i nhằm thu h&uacute;t kh&aacute;ch h&agrave;ng đến với loại h&igrave;nh du lịch độc đ&aacute;o n&agrave;y. Với 16.030.000 đồng (tour trọn g&oacute;i), bạn sẽ được du ngoại tr&ecirc;n biển từ đảo Ti&ecirc;n Sa - Đ&agrave; Nẵng đến Singapore trong 6 ng&agrave;y đ&ecirc;m (từ ng&agrave;y 1 đến 6/11). Ngo&agrave;i ra, bạn sẽ c&oacute; 12 ng&agrave;y đ&ecirc;m du lịch Nhật Bản v&agrave; Hong Kong bằng du thuyền 5 sao Peace Boat với gi&aacute; 49.700.000 đồng.</p>\r\n<p class=\"Normal\">Để biết th&ecirc;m th&ocirc;ng tin chi tiết:</p>\r\n<p class=\"Normal\">C&ocirc;ng ty ICC Travel: Địa chỉ: 15 Đ&ocirc;ng Du - phường Bến Ngh&eacute; - quận 1 - TP HCM</p>\r\n<p class=\"Normal\">Điện thoại: 08- 38220452 - Fax: 08-3 8226822</p>\r\n<p class=\"Normal\">Email:&nbsp;<a class=\"Normal\" href=\"mailto:sales&amp;marketing@icctours.com\">sales&amp;marketing@icctours.com</a></p>\r\n<p class=\"Normal\">Web:&nbsp;<a class=\"Normal\" href=\"http://icctour.1co.cc/\">http://icctour.1co.cc/</a></p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>C&ocirc;ng ty ICC Travel)</em></p>', 3, 2213, 0, 1, NULL, NULL),
(60, 'Team building - tour du lịch khám phá TP HCM', 'Team-Building---Tour-Du-Lich-Kham-Pha-Tp-Hcm', 'Chương trình giúp các nhóm khách lần đầu đặt chân đến TP HCM có thể tự mình khám phá, cảm nhận, hiểu khái quát nét văn hóa, ẩm thực, con người nơi đây... chỉ trong vài giờ tham quan.', 'DSC_4142 400x266.jpg', 22, '<p class=\"Title\"><span style=\"font-size: small;\">Team building - tour du lịch kh&aacute;m ph&aacute; TP HCM</span></p>\r\n<p class=\"Lead\">Chương tr&igrave;nh gi&uacute;p c&aacute;c nh&oacute;m kh&aacute;ch lần đầu đặt ch&acirc;n đến TP HCM c&oacute; thể tự m&igrave;nh kh&aacute;m ph&aacute;, cảm nhận, hiểu kh&aacute;i qu&aacute;t n&eacute;t văn h&oacute;a, ẩm thực, con người nơi đ&acirc;y... chỉ trong v&agrave;i giờ tham quan.</p>\r\n<p class=\"Normal\">C&ocirc;ng ty Du lịch Vietmark đ&atilde; tổ chức chuyến Team building kh&aacute;m ph&aacute; TP HCM cho đo&agrave;n kh&aacute;ch nước ngo&agrave;i của một tập đo&agrave;n đa quốc gia v&agrave;o ng&agrave;y 16/9 vừa qua.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/0A/F4/DSC_4142%20400x266.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"266\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Chương tr&igrave;nh chọn những địa danh quen thuộc tại khu trung t&acirc;m. H&agrave;nh tr&igrave;nh đi v&agrave; kh&aacute;m ph&aacute; hấp dẫn đối với du kh&aacute;ch tham gia Team buiding. Những con người xa lạ, kh&aacute;c biệt về ng&ocirc;n ngữ, văn h&oacute;a, phải kết nối lại với nhau để t&igrave;m ra tiếng n&oacute;i tập thể.</p>\r\n<p class=\"Normal\">Qua đ&oacute;, họ c&oacute; thể &ldquo;vẽ&rdquo; được ch&acirc;n dung TP HCM xưa v&agrave; nay - một l&aacute;t cắt của th&agrave;nh phố tại khu trung t&acirc;m. Đ&oacute; l&agrave; chợ Bến Th&agrave;nh sầm uất v&agrave; cổ k&iacute;nh; những c&ocirc;ng tr&igrave;nh vẫn giữ nguy&ecirc;n kiến tr&uacute;c cổ như bưu điện th&agrave;nh phố, nh&agrave; thờ Đức B&agrave;, nh&agrave; h&aacute;t th&agrave;nh phố; Dinh Độc Lập uy nghi; nh&agrave; s&aacute;ch hiện đại...</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/0A/F4/DSC_4145%20400x266.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"266\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Lịch sử, văn h&oacute;a, kiến tr&uacute;c, &acirc;m nhạc... được t&aacute;i hiện qua những bước ch&acirc;n du kh&aacute;ch. Kết th&uacute;c chương tr&igrave;nh, chiến thắng gi&agrave;nh cho đội đầu ti&ecirc;n về đ&iacute;ch l&agrave; những hiểu biết bất ngờ về th&agrave;nh phố. Team building thực tế đ&atilde; gi&uacute;p họ hiểu th&ecirc;m về một TP HCM với hơn 300 năm lịch sử, hiện đại nhưng cũng cổ k&iacute;nh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/0A/F4/IMG_0008%20400x300.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"300\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">&Ocirc;ng Đỗ Tuấn Anh, Gi&aacute;m đốc C&ocirc;ng ty Vietmark, chia sẻ: &ldquo;Team building kh&aacute;m ph&aacute; TP HCM gi&uacute;p du kh&aacute;ch nước ngo&agrave;i v&agrave; d&acirc;n địa phương hiểu th&ecirc;m về th&agrave;nh phố mang t&ecirc;n B&aacute;c&rdquo;.</p>\r\n<p class=\"Normal\">Li&ecirc;n hệ: C&ocirc;ng ty Du lịch Vietmark</p>\r\n<p class=\"Normal\">166D, lầu 1, Trần Hưng Đạo, quận 1, TP HCM. ĐT: 84 (8) 5404 5404</p>\r\n<p class=\"Normal\">Website:&nbsp;<a class=\"Normal\" href=\"http://www.vietnamteambuilding.com/\">http://www.vietnamteambuilding.com/</a></p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Vietmark</em>)</p>', 3, 1617, 0, 1, NULL, NULL),
(61, 'Nhớ hương Fanxifan', 'Nho-Huong-Fanxifan', 'Trong mỗi người trẻ tuổi, luôn có một khát khao. Với tôi, đỉnh Fanxifan, nóc nhà Đông Dương chính là trường dạy cho giới trẻ khát vọng chinh phục. Bạn Phạm Thị Tốt chia sẻ.', '1 (2).jpg', 22, '<p class=\"Title\">Nhớ hương Fanxifan</p>\r\n<p class=\"Lead\">Trong mỗi người trẻ tuổi, lu&ocirc;n c&oacute; một kh&aacute;t khao. Với t&ocirc;i, đỉnh Fanxifan, n&oacute;c nh&agrave; Đ&ocirc;ng Dương ch&iacute;nh l&agrave; trường dạy cho giới trẻ kh&aacute;t vọng chinh phục. Bạn Phạm Thị Tốt chia sẻ.</p>\r\n<p class=\"Normal\">Buổi s&aacute;ng, trời l&agrave;nh lạnh v&agrave; đầy sương m&ugrave;, t&ocirc;i c&ugrave;ng một người bạn chờ đ&oacute;n đo&agrave;n từ H&agrave; Nội l&ecirc;n Sapa để l&ecirc;n Trạm T&ocirc;n leo Fanxifan. Cả đo&agrave;n gồm 15 th&agrave;nh vi&ecirc;n c&ugrave;ng 7 anh khu&acirc;n v&aacute;c người M&ocirc;ng đen. Đi độ khoảng 45 ph&uacute;t l&agrave; tới Trạm T&ocirc;n, ở độ cao 2.047 m, trời nắng r&aacute;o, gi&oacute; thổi phần phật, thời tiết kh&aacute; tốt để leo Fanxifan. Cả đo&agrave;n xuất ph&aacute;t để đến l&aacute;n ở độ cao 2.400 m.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"210\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td>*<a href=\"http://vnexpress.net/GL/Xa-hoi/Du-lich/2010/09/3BA20972/page_2.asp\"><strong>Ảnh n&uacute;i rừng Fanxifan</strong></a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Đoạn đường n&agrave;y dễ đi, dốc kh&ocirc;ng cao lắm. Thế m&agrave; đi khoảng nửa tiếng đ&atilde; thấy mệt. V&aacute;c c&aacute;i balo khoảng 5 kg đi được một đoạn đ&atilde; thấy mồ h&ocirc;i to&aacute;t ra ướt cả lưng &aacute;o. Anh hướng dẫn li&ecirc;n tục động vi&ecirc;n: &ldquo;Gần tới chỗ ăn trưa rồi, đi hết con dốc n&agrave;y l&agrave; đến&hellip; con dốc kh&aacute;c&rdquo;. Vậy m&agrave; mấy anh khu&acirc;n v&aacute;c g&ugrave;i 20 kg cứ đi phăm phăm, ch&acirc;n đi đ&ocirc;i d&eacute;p tổ ong, mặt vẫn tươi cười.&nbsp;<br /><br />Từ độ cao 2.400 đến 2.800 m, đường đi kh&oacute; hơn, gi&oacute; thổi mạnh hơn, nhưng b&ugrave; lại cảnh đẹp hơn v&agrave; c&oacute; nhiều lo&agrave;i hoa: hoa bảo xu&acirc;n, hoa đỗ quy&ecirc;n đỏ, hoa mận&hellip; nở rực rỡ trong nắng. C&oacute; 5 đoạn cầu thang tay vịn, b&ecirc;n kia l&agrave; v&aacute;ch n&uacute;i cheo leo. Cứ đi hết ngọn đồi n&agrave;y, lại leo tiếp ngọn đồi kh&aacute;c. L&uacute;c nh&igrave;n lại chỉ thấy con đường b&eacute; t&yacute; x&iacute;u, l&uacute;c ẩn l&uacute;c hiện dưới t&aacute;n c&acirc;y rừng. C&oacute; những đoạn đường chỉ to&agrave;n bậc đ&aacute;, ng&oacute; ph&iacute;a trước kh&ocirc;ng thấy ai, ng&oacute; ph&iacute;a sau cũng kh&ocirc;ng ai thấy m&igrave;nh. Vậy l&agrave; lại h&uacute; l&ecirc;n thật to... để gọi bạn.</p>\r\n<p class=\"Normal\">Khi ho&agrave;ng h&ocirc;n bu&ocirc;ng xuống, cũng l&agrave; l&uacute;c đo&agrave;n tới l&aacute;n 2.800 m, nơi cả đo&agrave;n nghỉ đ&ecirc;m. D&ugrave; l&aacute;n khuất ở thung lũng nhưng gi&oacute; thổi v&ugrave; v&ugrave;, v&agrave; sương đ&ecirc;m lạnh bắt đầu bu&ocirc;ng xuống. Cả đo&agrave;n ngồi quanh bếp lửa hơ tay cho ấm. Ăn tối rồi ngủ sớm, dưỡng sức cho ng&agrave;y mai. S&agrave;n l&aacute;n bằng tre, c&aacute;ch mặt đất 50 cm, gi&oacute; l&ugrave;a m&aacute;t rười rượi. Đ&ecirc;m đ&oacute; nhiệt kế chỉ 5 độ C, cả đo&agrave;n co ro chập chờn trong giấc mơ chinh phục n&oacute;c nh&agrave; Đ&ocirc;ng Dương.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/09/72/1.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">N&uacute;i rừng Ho&agrave;ng Li&ecirc;n Sơn h&ugrave;ng vĩ. Ảnh:&nbsp;<em>Phạm Thị Tốt.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Buổi s&aacute;ng, sau khi nạp năng lượng từ bữa s&aacute;ng, cả đo&agrave;n xuất ph&aacute;t. Nghe b&aacute;c Hải n&oacute;i từ l&aacute;n n&agrave;y l&ecirc;n đỉnh đi t&yacute; x&iacute;u nữa l&agrave; tới, thế m&agrave; đoạn đầu đi chỉ to&agrave;n thấy xuống dốc, hết con dốc n&agrave;y lại xuống nữa. Xuống khoảng 200 m, rồi mới leo l&ecirc;n lại. Đoạn đường n&agrave;y bậc dốc cao hơn, đi mất sức hơn. Cũng may mắn l&agrave; trời nắng r&aacute;o, kh&ocirc;ng trơn trượt. L&uacute;c n&agrave;y th&igrave; chỉ to&agrave;n nghe tiếng m&igrave;nh thở ph&igrave; ph&ograve;, lồng ngực căng hết cỡ. Cứ leo được một đoạn lại dừng, thở dồn dập như bị thiếu oxy.</p>\r\n<p class=\"Normal\">Đi được khoảng một giờ, anh porter bảo sắp tới rồi, nhưng cứ đi m&atilde;i, đi m&atilde;i, th&ecirc;m mấy lần sắp tới nữa m&agrave; chẳng thấy đỉnh đ&acirc;u. Th&ocirc;i th&igrave; cứ đi, đi rồi sẽ tới. Đi hết đoạn đường đất, c&acirc;y thấp l&egrave; t&egrave;, lại đến tiếp rừng tr&uacute;c. Bỗng nghe chị V&acirc;n Anh la l&ecirc;n: &ldquo;Ơ, tới rồi &agrave;? Tới rồi&rdquo;. Cả nh&oacute;m h&eacute;t h&ograve;, tới rồi, tới rồi, kh&ocirc;ng ngờ đến đỉnh rồi. Vậy l&agrave; nhao nhao l&ecirc;n chạm v&agrave;o n&oacute;c nh&agrave; Đ&ocirc;ng Dương. Sướng ngất ng&acirc;y. Từ tr&ecirc;n cao ngắm biển m&acirc;y v&agrave; n&uacute;i m&agrave; l&ograve;ng cứ l&acirc;ng l&acirc;ng. L&uacute;c đ&oacute; l&agrave; 9h35 s&aacute;ng.</p>\r\n<p class=\"Normal\">Tinh thần phấn chấn n&ecirc;n việc đi xuống diễn ra nhanh hơn. Tuy nhi&ecirc;n, bước xuống ch&acirc;n phải trụ nhiều hơn v&agrave; sử dụng tay để giữ thăng bằng n&ecirc;n cơ bắp mỏi hơn. L&uacute;c n&agrave;y coi như đ&iacute;ch đ&atilde; đạt được, tha hồ c&oacute; thời gian m&agrave; cưỡi ngựa xem hoa, vừa đi vừa ngắm cảnh. Sướng nhất l&agrave; l&uacute;c đi qua cầu thang vịn thứ 3, gi&oacute; thổi m&acirc;y bay l&ecirc;n trời, bay cả ngang qua trước mặt, chỉ cần giơ tay ra l&agrave; chạm v&agrave;o.</p>\r\n<p class=\"Normal\">Cảnh trời chiều tr&ecirc;n d&atilde;y Ho&agrave;ng Li&ecirc;n Sơn đầy m&agrave;u sắc. C&oacute; sắc nắng v&agrave;ng rực, sắc xanh lam, tim t&iacute;m dệt th&agrave;nh từng vạt chạy ngang qua sườn n&uacute;i. Nheo mắt nh&igrave;n, thấy mặt trời như b&oacute;ng đ&egrave;n s&aacute;ng l&ograve;a, treo lơ lửng trong khoảng kh&ocirc;ng của gi&oacute; v&agrave; m&acirc;y.</p>\r\n<p class=\"Normal\">Đ&acirc;y cũng l&agrave; l&uacute;c cảm nhận được con người xứ m&igrave;nh đ&atilde; đối xử chưa đ&uacute;ng với rừng. Rất nhiều nh&agrave;nh hoa đỗ quy&ecirc;n bị bẻ, vất vương v&atilde;i tr&ecirc;n đường. Nhiều gốc c&acirc;y trơ trọi, nhiều c&acirc;y rừng bị ng&atilde;, v&agrave; rất nhiều c&acirc;y bị chặt xước th&acirc;n. X&oacute;t lắm, x&oacute;t cho th&acirc;n c&acirc;y bị chảy m&aacute;u, x&oacute;t cho cả thế hệ con ch&aacute;u mai sau kh&ocirc;ng biết c&oacute; được thưởng thức rừng như vậy kh&ocirc;ng.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Phạm Thị Tốt</strong></p>', 3, 425, 0, 1, NULL, NULL),
(62, 'Phố phường Hà Nội rực rỡ trước đại lễ', 'Pho-Phuong-Ha-Noi-Ruc-Ro-Truoc-Dai-Le', '20 ngày trước đại lễ nghìn năm, đường phố Hà Nội rực rỡ ánh đèn cùng những hình tượng hoa văn màu sắc. Hồ Gươm như lung linh hơn.', 'dienbienphu.jpg', 22, '<p class=\"Title\">Phố phường H&agrave; Nội rực rỡ trước đại lễ</p>\r\n<p class=\"Lead\">20 ng&agrave;y trước đại lễ ngh&igrave;n năm, đường phố H&agrave; Nội rực rỡ &aacute;nh đ&egrave;n c&ugrave;ng những h&igrave;nh tượng hoa văn m&agrave;u sắc. Hồ Gươm như lung linh hơn.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/09/2E/dienbienphu.jpg\" alt=\"\" width=\"492\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Đường Điện Bi&ecirc;n Phủ rực rỡ, lung linh nhất thủ đ&ocirc; những ng&agrave;y n&agrave;y.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/09/2E/thaprua.jpg\" alt=\"\" width=\"492\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Hồ Ho&agrave;n Kiếm lung linh.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/09/2E/dinhtienhoang.jpg\" alt=\"\" width=\"360\" height=\"541\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Chạy dọc đường Đinh Ti&ecirc;n Ho&agrave;ng c&oacute; thể ngắm biểu tượng Khu&ecirc; Văn C&aacute;c.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/09/2E/hoguom.jpg\" alt=\"\" width=\"492\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Cầu Th&ecirc; H&uacute;c v&agrave; Đền Ngọc Sơn.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/09/2E/le-duan.jpg\" alt=\"\" width=\"492\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Biểu tượng trống đồng Đ&ocirc;ng Sơn tr&ecirc;n đường L&ecirc; Duẩn.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/09/2E/langthuong.jpg\" alt=\"\" width=\"492\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Những b&ocirc;ng hoa đổi mầu tại ng&atilde; tư Nguyễn Ch&iacute; Thanh - đường L&aacute;ng.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/09/2E/phamhung.jpg\" alt=\"\" width=\"492\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">C&aacute;nh chim h&ograve;a b&igrave;nh v&agrave; Khu&ecirc; Văn C&aacute;c tr&ecirc;n đường Phạm H&ugrave;ng.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/09/2E/trannhantong.jpg\" alt=\"\" width=\"492\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Phố Trần Nh&acirc;n T&ocirc;ng, trước cửa c&ocirc;ng vi&ecirc;n Thống Nhất rự rỡ mầu sắc.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/09/2E/tranduyhung.jpg\" alt=\"\" width=\"492\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Những ch&ugrave;m &aacute;nh s&aacute;ng như những tia nước chảy tr&ecirc;n đường Trần Duy Hưng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng H&agrave;</strong></p>', 3, 1146, 0, 1, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(63, 'Một chuyến đi Hà Giang', 'Mot-Chuyen-Di-Ha-Giang', 'Ngày 30 cả đoàn được đi trên con đường đèo uốn lượn, những vòng cua nhỏ sát ngay mép vực khiến các xế thả sức nghiêng mình nằm trọn trong vòng ôm của đèo. Bạn Đỗ Quang Trung viết về chuyến đi Hà Giang.', 'a21.jpg', 22, '<p class=\"Title\">Một chuyến đi H&agrave; Giang</p>\r\n<p class=\"Lead\">Ng&agrave;y 30 cả đo&agrave;n được đi tr&ecirc;n con đường đ&egrave;o uốn lượn, những v&ograve;ng cua nhỏ s&aacute;t ngay m&eacute;p vực khiến c&aacute;c xế thả sức nghi&ecirc;ng m&igrave;nh nằm trọn trong v&ograve;ng &ocirc;m của đ&egrave;o. Bạn Đỗ Quang Trung viết về chuyến đi H&agrave; Giang.</p>\r\n<p class=\"Normal\">Nắng cứ ch&ecirc;nh chếch xi&ecirc;n ngang, đ&egrave;o với những con dốc cao v&uacute;t giờ chỉ c&ograve;n lại tiếng động cơ vang l&ecirc;n vỗ vồn v&atilde; v&agrave;o đ&aacute;.</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"3\" width=\"243\" align=\"center\" bgcolor=\"#ecf7ff\">\r\n<tbody>\r\n<tr>\r\n<td><a href=\"http://vnexpress.net/GL/Xa-hoi/Du-lich/2010/09/3BA2067F/page_1.asp\">* Ảnh những gương mặt v&ugrave;ng cao</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&aacute;i buổi trưa h&ocirc;m ấy, t&ocirc;i đi ra trước cửa nh&agrave; trọ v&agrave; thấy một cậu b&eacute;. T&ocirc;i lại gần n&oacute;i chuyện với em. T&ocirc;i hỏi em &ldquo;Nh&agrave; em ở đ&acirc;u? Trưa thế n&agrave;y sao kh&ocirc;ng về ngủ?&ldquo;. Em trả lời &ldquo;Nh&agrave; em ở kia&rdquo; rồi chỉ vu vơ về ph&iacute;a trước mặt. T&ocirc;i dẫn em v&agrave;o trong nh&agrave;, thấy những người chủ nh&agrave; nh&igrave;n em một c&aacute;ch &aacute;i ngại. C&ocirc; chủ nh&agrave; lại gần t&ocirc;i kể: &ldquo;Bố mẹ n&oacute; bỏ nhau, mẹ bỏ v&agrave;o Nam, bố đi lang thang. N&oacute; đi quanh x&oacute;m ai c&oacute; g&igrave; cho th&igrave; ăn, n&oacute; thường hay lấy đồ của kh&aacute;ch du lịch\".</p>\r\n<p class=\"Normal\">T&ocirc;i thấy em &ocirc;m đầu ngồi bất động. Ở tuổi ấy, ai bắt em phải suy nghĩ nhiều đến thế? V&agrave; t&ocirc;i hỏi em c&oacute; muốn ăn kẹo kh&ocirc;ng? Em trả lời c&oacute;. T&ocirc;i liền chạy l&ecirc;n g&aacute;c v&agrave; đưa cho em cả 3 que kẹo. Nh&igrave;n em ăn, t&ocirc;i hỏi &ldquo;C&oacute; ngon kh&ocirc;ng?&rdquo;. T&ocirc;i thấy em cười. Em bảo em muốn uống nước. T&ocirc;i quay v&agrave;o nh&agrave;. Trở ra với ly nước. Em uống nhanh, dường như sợ c&oacute; ai đ&oacute; cướp thứ đ&oacute; từ tr&ecirc;n tay em vậy. T&ocirc;i hỏi em c&oacute; muốn g&igrave; nữa kh&ocirc;ng? Em lắc đầu.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/06/7F/a21.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Trồng trọt tr&ecirc;n n&uacute;i đ&aacute;. Ảnh:&nbsp;<em>Đỗ Quang Trung.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Khi t&ocirc;i v&agrave;o ăn cơm, trở ra đ&atilde; thấy em đi mất. T&ocirc;i kh&ocirc;ng c&oacute; g&igrave; để cho em ngoại trừ 3 thanh kẹo. Liệu t&ocirc;i c&ograve;n c&oacute; thể l&agrave;m g&igrave; cho em hơn thế khi m&agrave; người tạo ra em đ&atilde; kh&ocirc;ng thể cho em một cuộc sống đ&uacute;ng nghĩa?</p>\r\n<p class=\"Normal\">Rời xa nơi ấy c&ugrave;ng với những h&igrave;nh ảnh về em, t&ocirc;i l&ecirc;n đường đến M&egrave;o Vạc, nơi t&ocirc;i từng nghe trong b&agrave;i h&aacute;t&nbsp;<em>Cướp vợ</em>&nbsp;của Ngũ Cung. Ng&agrave;y h&ocirc;m ấy l&agrave; một chuyến đi vất vả. Một chiếc xe trong đo&agrave;n hỏng xăm v&agrave; t&ocirc;i xuất ph&aacute;t chậm 3h so với đo&agrave;n. Ch&uacute;ng t&ocirc;i đ&aacute;nh v&agrave;o những con đường đ&egrave;o quanh co &aacute;nh đ&egrave;n pha yếu ớt. C&oacute; những đoạn đường đang l&agrave;m, t&ocirc;i ngồi tr&ecirc;n xe m&agrave; cảm gi&aacute;c lơ lửng tận nơi n&agrave;o. Chạy suốt từ 7h s&aacute;ng tới tận 10h đ&ecirc;m mới tới M&egrave;o Vạc, người r&atilde; ra v&igrave; mệt. Cũng may, vừa hay đến M&egrave;o Vạc th&igrave; xe t&ocirc;i kh&ocirc;ng c&ograve;n một giọt xăng.</p>\r\n<p class=\"Normal\">Cả đo&agrave;n đang đợi ch&uacute;ng t&ocirc;i tr&ecirc;n Kh&acirc;u Vai v&agrave; ch&uacute;ng t&ocirc;i lại trở l&ecirc;n tr&ecirc;n đ&oacute;. Tắc đường, bởi h&ocirc;m đ&oacute; tr&ecirc;n Kh&acirc;u Vai tổ chức chợ t&igrave;nh. T&ocirc;i đến nơi ngồi c&ugrave;ng mọi người để biết rằng đo&agrave;n đ&atilde; c&oacute; mặt đ&ocirc;ng đủ. Rồi t&ocirc;i lại đi để &ldquo;xem thế n&agrave;o l&agrave; chợ t&igrave;nh?&ldquo;. Hơi thất vọng bởi người Kinh l&ecirc;n đ&acirc;y nhiều qu&aacute;, t&ocirc;i cứ ngồi m&atilde;i ở một chỗ để c&oacute; thể lưu lại được một bức ảnh về phi&ecirc;n chợ v&agrave; cũng để nghe về c&aacute;i phong tục của lễ hội n&agrave;y.</p>\r\n<p class=\"Normal\">Cuối c&ugrave;ng t&ocirc;i đem nỗi thất vọng trở về, t&ocirc;i về khi cả đo&agrave;n đ&atilde; gần ngủ hết v&agrave; nhớ về m&oacute;n thắng cố. T&ocirc;i lại dậy đi t&igrave;m, một qu&aacute;n c&oacute; cả thắng cố lẫn m&egrave;n m&eacute;n. V&agrave;o qu&aacute;n, t&ocirc;i xin ph&eacute;p nhập b&agrave;n c&ugrave;ng với hai người ngồi trước ở đ&oacute; v&igrave; t&ocirc;i đi c&oacute; một m&igrave;nh, hỏi ra họ l&agrave; người Tuy&ecirc;n Quang. T&ocirc;i n&oacute;i chuyện với họ xung quanh m&oacute;n ăn v&agrave; về chợ t&igrave;nh. Thắng cố nơi đ&acirc;y nấu kh&ocirc;ng giống như những g&igrave; t&ocirc;i đọc, t&ocirc;i ăn thấy n&oacute; giống như giả cầy, c&ograve;n m&egrave;n m&eacute;n l&agrave;m từ ng&ocirc; chan nước thắng cố v&agrave;o ăn, n&oacute; nh&agrave;n nhạt vữa vụn.</p>\r\n<p class=\"Normal\">Sau bữa rượu, t&ocirc;i định trở về ph&ograve;ng, tr&ecirc;n đường về t&ocirc;i thấy một chiếc chiếu vẫn đang trải v&agrave; thắp những chiếc nến tr&ecirc;n ấy. T&ocirc;i xin ph&eacute;p được ngồi c&ugrave;ng. T&ocirc;i hỏi anh về chợ t&igrave;nh. Anh bảo ng&agrave;y n&agrave;y con trai con g&aacute;i đi với một chiếc đ&egrave;n pin, người con trai chủ động l&agrave;m quen. Khi người con g&aacute;i đồng &yacute;, người con g&aacute;i sẽ dẫn người con trai đi đến một nơi n&agrave;o đ&oacute;, họ n&oacute;i chuyện với nhau. Nếu một trong hai đ&atilde; c&oacute; vợ hoặc c&oacute; chồng th&igrave; họ sẽ l&agrave; bạn, c&ograve;n nếu kh&ocirc;ng họ sẽ tiếp tục l&agrave;m quen với nhau.</p>\r\n<p class=\"Normal\">Khi anh bảo sắp dọn chiếu, t&ocirc;i trở l&ecirc;n khu nghỉ thiếp đi với giấc mơ mộng mị đ&ecirc;m chợ t&igrave;nh.</p>\r\n<p class=\"Normal\">\"Đ&acirc;u rồi thời rung reng v&ograve;ng bạc lắc đồng?<br />Em k&igrave;a, những c&acirc;y sa mộc lặng lẽ trong thung<br />Ch&uacute;ng đang nghĩ g&igrave;?<br />Em đang nghĩ g&igrave;?\".<br /><br />(Chợ t&igrave;nh Kh&acirc;u Vai - Trần H&ograve;a B&igrave;nh)</p>\r\n<p class=\"Normal\">Đến Đồng Văn, t&ocirc;i v&agrave;o một khu chợ, dựng xe v&agrave; t&ocirc;i thấy một v&agrave;i người đ&agrave;n &ocirc;ng đang uống rượu. T&ocirc;i hỏi người đang chế biến m&oacute;n ăn v&agrave; được biết đ&acirc;y l&agrave; m&oacute;n thắng cố. T&ocirc;i v&agrave;o v&agrave; gọi 2 lạng thắng cố. Như t&ocirc;i được biết thắng cố l&agrave;m trong dịp lễ bảo vệ thần rừng của d&acirc;n tộc H&rsquo;M&ocirc;ng, người ta cắt lấy đầu để thờ v&agrave; cho nội tạng của những con vật như tr&acirc;u, b&ograve;, lợn, ngựa v&agrave;o chung trong một nồi. Nhưng thắng cố ở đ&acirc;y kh&aacute;c, thắng được chế biến từ một con duy nhất. H&ocirc;m đ&oacute;, m&oacute;n t&ocirc;i ăn được chế biến từ thịt b&ograve;, họ nướng sau đ&oacute; để tr&ecirc;n một chiếc b&agrave;n. Ai ăn, họ cắt ra c&acirc;n, cho v&agrave;o nồi trần qua, m&uacute;c &iacute;t nước đổ v&agrave;o b&aacute;t. Đ&oacute; l&agrave; thắng cố ở Đồng Văn.</p>\r\n<p class=\"Normal\">&ldquo;Vị n&uacute;i, hương đ&egrave;o, xu&acirc;n gia vị<br />Kh&iacute; trời, nghĩa đất, thắng cố đ&acirc;y!&rdquo;.</p>\r\n<p class=\"Normal\">T&ocirc;i biết sẽ l&agrave; kh&ocirc;ng bao giờ đủ khi n&oacute;i về một chuyến đi như thế. Nhưng quan trọng l&agrave; tất cả ch&uacute;ng ta đ&atilde; quay trở về. Ch&uacute;ng ta đ&atilde; gặp nhau, đ&atilde; cười, đ&atilde; vui vẻ qu&acirc;y quần b&ecirc;n những bữa ăn v&agrave; ngủ chung trong những gian nh&agrave; như thế.</p>\r\n<p class=\"Normal\">Bởi vậy, t&ocirc;i v&agrave; bạn h&atilde;y c&ugrave;ng nhau mơ về những chuyến đi&hellip;</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đỗ Quang Trung</strong></p>', 3, 541, 0, 1, NULL, NULL),
(64, 'Simacai hoang vắng', 'Simacai-Hoang-Vang', 'Simacai (Lào Cai) nhiệt độ trung bình ngày luôn 15 độ C, cảnh sắc thiên nhiên rực rỡ và lòng người nồng nhiệt sẽ làm dịu lòng du khách sau chuyến vượt rừng vất vả để tới đây. Bạn Nguyễn Thị Giang chia sẻ hình ảnh vùng cao.', '5 (1).jpg', 22, '<p class=\"Title\">Simacai hoang vắng</p>\r\n<p class=\"Lead\">Simacai (L&agrave;o Cai) nhiệt độ trung b&igrave;nh ng&agrave;y lu&ocirc;n 15 độ C, cảnh sắc thi&ecirc;n nhi&ecirc;n rực rỡ v&agrave; l&ograve;ng người nồng nhiệt sẽ l&agrave;m dịu l&ograve;ng du kh&aacute;ch sau chuyến vượt rừng vất vả để tới đ&acirc;y. Bạn Nguyễn Thị Giang chia sẻ h&igrave;nh ảnh v&ugrave;ng cao.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/07/FC/5.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đứa b&eacute; nằm chờ bố mẹ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/07/FC/9.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Trẻ em tắm suối.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/07/FC/7.jpg\" border=\"1\" alt=\"\" width=\"300\" height=\"450\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Mẹ v&agrave; con.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/07/FC/6.jpg\" border=\"1\" alt=\"\" width=\"300\" height=\"450\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">B&aacute;n h&agrave;ng trong chợ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/07/FC/10.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">N&uacute;i rừng T&acirc;y Bắc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nguyễn Thị Giang</strong></p>', 3, 1834, 0, 1, NULL, NULL),
(65, 'Tư vấn du lịch Cửu Trại Câu', 'Tu-Van-Du-Lich-Cuu-Trai-Cau', 'ửu Trại Câu là một địa danh khá đẹp và nổi tiếng nằm ở phía bắc, tỉnh Tứ Xuyên - Trung Quốc. Hành trình đi tới đó cũng khá phức tạp và vận chuyển dưới nhiều hình thức khá phức tạp, do vậy du lịch “bụi” chỉ phù hợp với các bạn trẻ ưa thích khám phá, nếu bạn ở tuổi trung tuổi thì nên đi theo tour do các hãng lữ hành tổ chức. Hiện có rất nhiều hãng lữ hành đã triển khai bán sản phẩm này có tham quan Thành Đô - Nga My Sơn và Cửu Trại Câu.', '_nh-91.jpg', 22, '<p class=\"Title\">Tư vấn du lịch Cửu Trại C&acirc;u</p>\r\n<p class=\"Normal\">Cửu Trại C&acirc;u l&agrave; một địa danh kh&aacute; đẹp v&agrave; nổi tiếng nằm ở ph&iacute;a bắc, tỉnh Tứ Xuy&ecirc;n - Trung Quốc. H&agrave;nh tr&igrave;nh đi tới đ&oacute; cũng kh&aacute; phức tạp v&agrave; vận chuyển dưới nhiều h&igrave;nh thức kh&aacute; phức tạp, do vậy du lịch &ldquo;bụi&rdquo; chỉ ph&ugrave; hợp với c&aacute;c bạn trẻ ưa th&iacute;ch kh&aacute;m ph&aacute;, nếu bạn ở tuổi trung tuổi th&igrave; n&ecirc;n đi theo tour do c&aacute;c h&atilde;ng lữ h&agrave;nh tổ chức. Hiện c&oacute; rất nhiều h&atilde;ng lữ h&agrave;nh đ&atilde; triển khai b&aacute;n sản phẩm n&agrave;y c&oacute; tham quan Th&agrave;nh Đ&ocirc; - Nga My Sơn v&agrave; Cửu Trại C&acirc;u.</p>\r\n<p class=\"Normal\">Nếu đi du lịch &ldquo;bụi&rdquo; bạn c&oacute; thể đi theo hướng đi tham quan Nam Ninh theo đường Lạng Sơn, hiện một số nơi ở H&agrave; Nội c&oacute; b&aacute;n v&eacute; tuyến n&agrave;y l&agrave; 300.000đồng/v&eacute; tới thẳng Nam Ninh rồi. Tuy nhi&ecirc;n bạn phải tự chuẩn bị hộ chiếu v&agrave; l&agrave;m thủ tục xin visa, tiếng Trung cũng l&agrave; một trở ngại. Tới Nam Ninh, bạn c&oacute; thể tham quan tại Nam Ninh v&agrave;i ng&agrave;y rồi bay đi Th&agrave;nh Đ&ocirc; - thủ phủ của tỉnh Tứ Xuy&ecirc;n - chi ph&iacute; khoảng 1.000 NDT. Th&agrave;nh Đ&ocirc; cũng l&agrave; một địa danh tuyệt vời để bạn kh&aacute;m ph&aacute; v&agrave; trải nghiệm 1- 2 ng&agrave;y, sau đ&oacute; l&ecirc;n đường đi Cửu Trại C&acirc;u.</p>\r\n<p class=\"Normal\">Từ Th&agrave;nh Đ&ocirc; l&ecirc;n Cửu Trại C&acirc;u c&oacute; 2 c&aacute;ch, đi đường bộ qua Songpan d&agrave;i 430km, đi mất 10-12 giờ, s&aacute;ng tại bến xe Chandianzi (từ trung t&acirc;m đi ra mất khoảng 30 ph&uacute;t) c&oacute; chuyến 8h v&agrave; 8h30, bạn xe đi xe kh&aacute;ch đến 18h-20h th&igrave; đến nơi. Gi&aacute; xe bus giao động trong khoảng 120 &ndash; 140 NDT. Đi đường n&agrave;y cũng th&iacute;ch hợp nếu bạn muốn c&oacute; thể gh&eacute; qua thăm d&atilde;y n&uacute;i Ho&agrave;ng Long.</p>\r\n<p class=\"Normal\">C&aacute;ch thứ hai l&agrave; bay từ Th&agrave;nh Đ&ocirc; đến Cửu Trại C&acirc;u, s&acirc;n bay c&aacute;ch Cửu Trại C&acirc;u khoảng 80km. C&oacute; nhiều h&atilde;ng h&agrave;ng kh&ocirc;ng c&oacute; đường bay đến Cửu Trại C&acirc;u như: China Airlines, China Southern Airlines. Tuy nhi&ecirc;n nếu đi kh&aacute;m ph&aacute; th&igrave; bạn n&ecirc;n đi bằng đường bộ sẽ trải nghiệm được rất nhiều vẻ đẹp của v&ugrave;ng n&agrave;y.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/06/F9/_nh-91.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"290\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cửu Trại C&acirc;u rực rỡ v&agrave;o m&ugrave;a thu. Ảnh:&nbsp;<em>Asiana Travel</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Tr&ecirc;n đường từ Th&agrave;nh Đ&ocirc; đến Cửu Trại C&acirc;u v&agrave;o m&ugrave;a thu (th&aacute;ng 9, 10) sẽ c&oacute; tuyết n&ecirc;n đường đ&egrave;o rất nguy hiểm, kh&oacute; đi v&igrave; thế bạn n&ecirc;n thu&ecirc; xe của người d&acirc;n địa phương để thuận lợi việc tham quan hoặc đăng k&yacute; qua c&aacute;c tour trọn g&oacute;i tại c&aacute;c c&ocirc;ng ty du lịch uy t&iacute;n nhằm đảm bảo chất lượng tham quan v&agrave; sự an to&agrave;n.</p>\r\n<p class=\"Normal\">Điểm lưu tr&uacute;: Trong khu bảo tồn Cửu Trại C&acirc;u kh&ocirc;ng c&oacute; điểm kh&aacute;ch sạn ch&iacute;nh thức n&agrave;o. C&aacute;c kh&aacute;ch sạn đều nằm ngo&agrave;i khu bảo tồn bao gồm: Kh&aacute;ch sạn ti&ecirc;u chuẩn 5 sao Jiuzhaigou Xilaideng International, Sheraton. Kh&aacute;ch sạn ti&ecirc;u chuẩn 4 sao Chang Qing Binguan, The Geshang, The Chinese Travel, Golden Harbour. Kh&aacute;ch sạn ti&ecirc;u chuẩn 3 sao Xing Long Binguan, Qianhe Hotel. Ngo&agrave;i ra c&ograve;n rất nhiều hệ thống nh&agrave; nghỉ homestay của d&acirc;n địa phương chỉ c&aacute;ch cổng ch&agrave;o 15 ph&uacute;t xe.</p>\r\n<p class=\"Normal\">Mua sắm: V&igrave; nơi đ&acirc;y phần lớn l&agrave; d&acirc;n tộc Tạng A Ba v&agrave; Khương n&ecirc;n c&aacute;c sản vật hầu hết l&agrave; đồ thủ c&ocirc;ng mỹ nghệ. Đối với sản phẩm của người Tạng A Ba l&agrave; c&aacute;c sản phẩm đan l&aacute;t, th&ecirc;u, tranh vẽ. C&aacute;c sản phẩm th&ecirc;u bằng tay của d&acirc;n tộc Khương nổi tiếng khắp tỉnh Tứ Xuy&ecirc;n v&igrave; sự kh&eacute;o l&eacute;o v&agrave; đặc sắc.</p>\r\n<p class=\"Normal\">Ngo&agrave;i ra, qu&yacute; kh&aacute;ch c&ograve;n c&oacute; thể t&igrave;m mua c&aacute;c loại thuốc bắc, nấm, thảo dược của người Tạng, v&agrave; cả thịt b&ograve; Yak rất nhiều đạm v&agrave; ngon.</p>\r\n<p class=\"Normal\">Nếu qu&yacute; kh&aacute;ch muốn ngắm b&igrave;nh minh sớm, th&igrave; n&ecirc;n lưu tr&uacute; ở c&aacute;c nh&agrave; người Tạng nằm trong khu vực Cửu Trại C&acirc;u, vừa thưởng thức đ&ecirc;m tối đầy th&uacute; vị ngập tr&agrave;n truyền thống văn h&oacute;a người Tạng, vừa được ngắm b&igrave;nh minh ngay ở trước nh&agrave;.</p>\r\n<p class=\"Normal\">Du kh&aacute;ch n&ecirc;n mang theo đồ ấm, v&igrave; khu vực n&agrave;y ban đ&ecirc;m rất lạnh.</p>\r\n<p class=\"Normal\">Ti&ecirc;u chuẩn homestay, kh&aacute;ch sạn ở khu vực Cửu Trại C&acirc;u vẫn chưa đạt đến mức tiện nghi cao nhất so với c&aacute;c điểm du lịch kh&aacute;c tại Trung Quốc nhưng vẫn đ&aacute;p ứng tốt nhu cầu nghỉ ngơi của du kh&aacute;ch sau chuyến tham quan. Việc mặc cả khi mua sản vật địa phương ở đ&acirc;y l&agrave; điều cần thiết.</p>\r\n<p class=\"Normal\">Cửu Trại C&acirc;u l&agrave; khu bảo tồn thi&ecirc;n nhi&ecirc;n, nằm ở độ cao 4.000m so với mực nước biển, thuộc v&ugrave;ng tự trị của d&acirc;n tộc Khương v&agrave; d&acirc;n tộc Tạng A B&aacute;, nằm ở ph&iacute;a bắc tỉnh Tứ Xuy&ecirc;n, Trung Quốc, diện t&iacute;ch hơn 60.000 ha. Nơi đ&acirc;y c&oacute; 9 ng&ocirc;i l&agrave;ng của người Tạng n&ecirc;n được gọi l&agrave; Cửu Trại.</p>\r\n<p class=\"Normal\">Theo truyền thuyết kể rằng, Cửu Trại C&acirc;u ch&iacute;nh l&agrave; những mảnh vỡ từ chiếc gương được l&agrave;m từ gi&oacute; v&agrave; trăng của nam thần Đạt Qua tặng nữ thần Y&ecirc;u Lạc Sắc M&ocirc;. Để rồi khi chạm ch&acirc;n đến, khắp kh&ocirc;ng gian ngập tr&agrave;n tiếng gi&oacute; ng&acirc;n vang, tiếng chim lảnh l&oacute;t v&agrave; cứ thế suốt dọc đường đi cảnh nối cảnh như trong một bức tranh thủy mặc.</p>\r\n<p class=\"Normal\">M&ugrave;a đẹp nhất trong năm ở Cửu Trại C&acirc;u l&agrave; m&ugrave;a thu, thời điểm cụ thể từ 15/10 đến 25/10 hằng năm. V&agrave;o m&ugrave;a thu, những thảm l&aacute; đỏ, l&aacute; v&agrave;ng ở khu bảo tồn đ&atilde; tạo n&ecirc;n một bức tranh thi&ecirc;n nhi&ecirc;u tuyệt mỹ v&agrave; trong l&ograve;ng c&aacute;c hồ xanh trong vắt phản chiếu bầu trời thu xanh trong vời vợi. C&aacute;c hệ thống hồ, s&ocirc;ng, suối nơi đ&acirc;y c&oacute; l&agrave;n nước xanh tuyệt đẹp đến mức khiến người ta nghĩ rằng kh&ocirc;ng c&ograve;n nơi n&agrave;o c&oacute; thể đẹp hơn. Những gam m&agrave;u tự nhi&ecirc;n của đất trời, s&ocirc;ng suối, cỏ c&acirc;y cứ h&ograve;a quyện v&agrave;o nhau v&agrave; thay đổi theo từng thời điểm trong ng&agrave;y. Nếu kh&ocirc;ng được tận mắt ngắm nh&igrave;n những hồ nước trong suốt như pha l&ecirc;, m&agrave; chỉ xem qua c&aacute;c s&aacute;ch ảnh, hẳn du kh&aacute;ch c&oacute; thể nghĩ rằng người ta t&ocirc; vẽ m&agrave;u sắc v&agrave;o trong đ&oacute;.</p>\r\n<p class=\"Normal\">Khu du lịch Cửu Trại C&acirc;u l&agrave; tập hợp của 114 hồ lớn nhỏ, v&agrave; hơn 17 th&aacute;c nước. Trong đ&oacute; những điểm nhấn bao gồm :</p>\r\n<p class=\"Normal\">Ngũ Sắc tr&igrave; l&agrave; hồ s&acirc;u 6,6m, rộng gần 3.000m v&agrave; theo tương truyền l&agrave; hồ Ngọc Thạch đa sắc từng tồn tại ở cung điện của T&acirc;y Vương Mẫu năm xưa. Cảnh tr&iacute;, n&uacute;i non, động vật nơi đ&acirc;y vẫn c&ograve;n giữ nguy&ecirc;n vẹn vẻ đẹp hoang sơ l&agrave;m say đắm l&ograve;ng người.</p>\r\n<p class=\"Normal\">Trần Ch&acirc;u Than l&agrave; th&aacute;c nước ngọc trai, l&agrave; khu vực th&aacute;c Tr&acirc;n Ch&acirc;u cao 28m với những l&agrave;n nước bạc cuồn cuộn tu&ocirc;n chảy, k&eacute;o d&agrave;i 310m, ph&ocirc; trương sức mạnh kỳ vĩ của thi&ecirc;n nhi&ecirc;n.</p>\r\n<p class=\"Normal\">Th&aacute;c Thụ Ch&iacute;nh tuy kh&ocirc;ng rộng nhưng cao hơn 30m, d&ograve;ng th&aacute;c chia th&agrave;nh hai d&ograve;ng đổ xuống dưới vực s&acirc;u tung bọt trắng x&oacute;a.</p>\r\n<p class=\"Normal\">Th&aacute;c Gấu Tr&uacute;c cao 78m, l&agrave; th&aacute;c nước ở độ cao nhất của khe Cửu Trại.</p>\r\n<p class=\"Normal\">Th&aacute;c Nhược Nhật rộng 100m, cao hơn 20m, nước đổ ầm ầm cả ng&agrave;y lẫn đ&ecirc;m.</p>\r\n<p class=\"Normal\">Ngo&agrave;i ra nơi n&agrave;y c&ograve;n c&oacute; rất nhiều c&aacute;nh rừng nguy&ecirc;n sinh với hơn 200 loại c&acirc;y chủ yếu như v&acirc;n sam, hồng sam, th&ocirc;ng dầu, hồng đu&ocirc;i ngựa, song tử diệp, phong, liễu đỏ, đỗ quy&ecirc;n. Ngo&agrave;i ra c&ograve;n c&oacute; rất nhiều lo&agrave;i qu&yacute; hiếm, trong đ&oacute; qu&yacute; nhất l&agrave; lo&agrave;i gấu tr&uacute;c, quốc th&uacute; của Trung Hoa Đại Lục.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(<em>C&ocirc;ng ty Du lịch Vietravel</em>)</p>', 3, 1704, 0, 1, NULL, NULL),
(66, 'Shangri-La thung lũng huyền thoại', 'Shangri-La-Thung-Lung-Huyen-Thoai', 'Thành phố Shangri-La thuộc tỉnh Vân Nam, Trung Quốc là điểm du lịch hấp dẫn với cảnh quan thiên nhiên tươi đẹp, những ngôi chùa theo kiến trúc Phật giáo Tây Tạng…', 'dulich2.jpg', 22, '<p class=\"Title\">Shangri-La thung lũng huyền thoại</p>\r\n<p class=\"Lead\">Th&agrave;nh phố Shangri-La thuộc tỉnh V&acirc;n Nam, Trung Quốc l&agrave; điểm du lịch hấp dẫn với cảnh quan thi&ecirc;n nhi&ecirc;n tươi đẹp, những ng&ocirc;i ch&ugrave;a theo kiến tr&uacute;c Phật gi&aacute;o T&acirc;y Tạng&hellip;</p>\r\n<p class=\"Normal\">M&ugrave;a hạ nơi đ&acirc;y đẹp hơn khi cỏ c&acirc;y căng tr&agrave;n sức sống, những triền đồi trải đầy hoa nở, m&acirc;y n&uacute;i trập tr&ugrave;ng như tranh thủy mặc. Shangri-La nằm trong một g&oacute;c phố nhỏ, ẩn m&igrave;nh s&acirc;u trong phố ch&iacute;nh với những con đường l&aacute;t đ&aacute; đậm phong c&aacute;ch kiến tr&uacute;c T&acirc;y Tạng. Th&agrave;nh phố ch&agrave;o đ&oacute;n kh&aacute;ch du lịch với sự thanh b&igrave;nh v&agrave; y&ecirc;n ả.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/FE/26/dulich2.jpg\" border=\"1\" alt=\"Những hồ nước ngọt phẳng lặng, nằm phơi m&igrave;nh b&ecirc;n những d&atilde;y n&uacute;i cao tạo cảm gi&aacute;c y&ecirc;n b&igrave;nh cho Shangrila.\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những hồ nước ngọt phẳng lặng, nằm phơi m&igrave;nh b&ecirc;n d&atilde;y n&uacute;i cao tạo cảm gi&aacute;c y&ecirc;n b&igrave;nh cho thị trấn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&aacute;c gian h&agrave;ng b&agrave;y nhiều sản phẩm d&acirc;n gian của người Tạng. Những chiếc v&ograve;ng đeo tay v&agrave; khuy&ecirc;n tai đủ m&agrave;u sắc; chiếc chu&ocirc;ng c&oacute; tiếng k&ecirc;u leng keng vui tai; b&uacute;p b&ecirc; mang h&igrave;nh ảnh những c&ocirc; b&eacute; d&acirc;n tộc Nạp T&acirc;y Tạng hoặc Moshua; c&aacute;c con b&ograve; Yak nhồi b&ocirc;ng nhỏ xinh. Ngo&agrave;i ra, cặp, t&uacute;i l&agrave;m từ da thuộc l&agrave; những m&oacute;n qu&agrave; th&uacute; vị cho kh&aacute;ch du lịch.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/FE/26/dulich04.jpg\" border=\"1\" alt=\"Những m&oacute;n qu&agrave; lưu niệm đặc trưng khi đến Shangri-La\" width=\"400\" height=\"366\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những m&oacute;n qu&agrave; lưu niệm đặc trưng Shangri-La.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Đến với Shangri-La, một địa danh kh&ocirc;ng thể bỏ qua l&agrave; ng&ocirc;i ch&ugrave;a Đại Phật Tự nằm tr&ecirc;n ngọn đồi trong khu cổ trấn. Bạn c&oacute; thể nh&igrave;n to&agrave;n cảnh khu th&agrave;nh cổ khi đến đ&acirc;y. &ldquo;Ph&aacute;p lu&acirc;n kinh quay&rdquo; khổng lồ cao 24 m (c&ograve;n gọi l&agrave; v&ograve;ng quay tụng ch&uacute;) trong s&acirc;n ch&ugrave;a được cho l&agrave; lớn nhất thế giới.</p>\r\n<p class=\"Normal\">Người d&acirc;n nơi đ&acirc;y tin rằng vừa niệm Phật vừa d&ugrave;ng tay k&eacute;o &ldquo;Ph&aacute;p lu&acirc;n kinh quay&rdquo; nhiều v&ograve;ng sẽ đem lại những điều an l&agrave;nh, như &yacute;. Bởi vậy, h&igrave;nh ảnh &ldquo;Ph&aacute;p lu&acirc;n kinh quay&rdquo; được mạ v&agrave;ng, chạm trổ h&igrave;nh Phật với những v&ograve;ng quay kh&ocirc;ng dứt gi&uacute;p tăng th&ecirc;m vẻ uy nghi&ecirc;m, thanh khiết của ng&ocirc;i ch&ugrave;a.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/FE/26/dulich4.jpg\" border=\"1\" alt=\"Ch&ugrave;a Đại Phật Tự nằm tr&ecirc;n một ngọn đồi.\" width=\"450\" height=\"309\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ch&ugrave;a Đại Phật Tự nằm tr&ecirc;n một ngọn đồi.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Chi tiết chương tr&igrave;nh v&agrave; gi&aacute; xem tại Website:&nbsp;<a class=\"Normal\" href=\"http://www.travel365.com.vn/\">http://www.travel365.com.vn/</a></p>\r\n<p class=\"Normal\">Hoặc li&ecirc;n hệ trực tiếp Asiana Travel</p>\r\n<p class=\"Normal\">Địa chỉ: 159 Phương Mai, Đống Đa, H&agrave; Nội</p>\r\n<p class=\"Normal\">Tel: (84 - 4) 35 736 748 &ndash; 49/ Fax: (84 - 4) 35 736 750 - Hotline: 0983.388.387 (Ms. Mỹ); 0983.000.448 (Ms. Nh&acirc;n)</p>\r\n<p class=\"Normal\">Email:&nbsp;<a class=\"Normal\" href=\"mailto:info@travel365.com.vn\">info@travel365.com.vn</a></p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Asiana Travel</em>)</p>', 3, 434, 0, 1, NULL, NULL),
(67, 'Công viên đá ở Côn Minh', 'Cong-Vien-Da-O-Con-Minh', 'Tạo hóa đã ưu ái cho Thạch Lâm (Côn Minh, Vân Nam, Trung Quốc) một phong cảnh thần tiên của đá và nước. Những cột đá nằm trong một khu vực rộng tới 350 km2, tập trung nhất là khu công viên đá Thạch Lâm.', '1 (3).jpg', 22, '<p class=\"Title\">C&ocirc;ng vi&ecirc;n đ&aacute; ở C&ocirc;n Minh</p>\r\n<p class=\"Lead\">Tạo h&oacute;a đ&atilde; ưu &aacute;i cho Thạch L&acirc;m (C&ocirc;n Minh, V&acirc;n Nam, Trung Quốc) một phong cảnh thần ti&ecirc;n của đ&aacute; v&agrave; nước. Những cột đ&aacute; nằm trong một khu vực rộng tới 350 km2, tập trung nhất l&agrave; khu c&ocirc;ng vi&ecirc;n đ&aacute; Thạch L&acirc;m.</p>\r\n<p class=\"Normal\">Kh&aacute;ch du lịch Việt Nam đi theo tuyến H&agrave; Nội &ndash; Alư Cổ Động &ndash; Thạch L&acirc;m &ndash; C&ocirc;n Minh &ndash; H&agrave; Nội đều c&oacute; cảm nhận: Thạch L&acirc;m l&agrave; điểm tham quan l&yacute; tưởng với vẻ đẹp kỳ diệu, một sự kết hợp h&agrave;i h&ograve;a giữa thi&ecirc;n nhi&ecirc;n v&agrave; con người.</p>\r\n<p class=\"Normal\">Ngắm nh&igrave;n bạn sẽ thấy c&aacute;c khối đ&aacute; như một ng&ocirc;i ch&ugrave;a, h&igrave;nh th&ugrave; giống một rừng kiếm, một d&atilde;y cột, một con voi, một con phượng ho&agrave;ng x&ograve;e c&aacute;nh, rồi h&igrave;nh tượng của H&ograve;a thượng Đường Tăng, của một thiếu nữ h&oacute;a đ&aacute;, lại c&oacute; cả giường Ti&ecirc;n, những hồ nước trong xanh với c&aacute;c đảo đ&aacute; nhấp nh&ocirc;... Đi trong rừng đ&aacute; với mu&ocirc;n v&agrave;n những h&igrave;nh dạng kỳ diệu ấy du kh&aacute;ch như lạc v&agrave;o một thế giới ri&ecirc;ng, chỉ c&oacute; trong chuyện thần ti&ecirc;n.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/FB/65/1.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Rừng đ&aacute; h&ugrave;ng vĩ. Ảnh:&nbsp;<em>Hồng Phương.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Nơi đ&acirc;y đ&atilde; được chọn l&agrave; một trong những trường quay của bộ phim &ldquo;T&acirc;y du k&yacute;&rdquo;, với những điểm ghi dấu ấn của T&ocirc;n Ngộ Kh&ocirc;ng v&agrave; c&aacute;c sư đệ đ&atilde; thể hiện t&agrave;i tr&iacute; trong việc th&aacute;p t&ugrave;ng Đường Tăng đi T&acirc;y Tr&uacute;c lấy kinh. Đ&oacute; l&agrave; lỗ đ&aacute; h&igrave;nh tr&ograve;n, l&agrave; khe đ&aacute; hẹp chỉ cho lọt cổ.</p>\r\n<p class=\"Normal\">Cảnh đẹp thần ti&ecirc;n của Thạch L&acirc;m c&agrave;ng được du kh&aacute;ch h&agrave;i l&ograve;ng khi đến tham quan ch&iacute;nh bởi những con người nơi đ&acirc;y: những c&aacute;n bộ, nh&acirc;n vi&ecirc;n quản l&yacute; c&ocirc;ng vi&ecirc;n. Nhiều người trong số họ thuộc d&acirc;n tộc Yi, một trong những d&acirc;n tộc thiểu số đ&ocirc;ng nhất ở V&acirc;n Nam. Những c&ocirc; g&aacute;i d&acirc;n tộc Yi chăm chỉ v&agrave; hiếu kh&aacute;ch, trong trang phục đặc sắc, l&aacute;i &ocirc;t&ocirc; điện đưa du kh&aacute;ch đến c&aacute;c điểm tham quan trong &ldquo;rừng đ&aacute;&rdquo;. Đ&oacute; l&agrave; loại xe rất tiện lợi, đặc biệt l&agrave; giữ cho m&ocirc;i trường sạch sẽ, kh&ocirc;ng bị &ocirc; nhiễm, kh&ocirc;ng ồn &agrave;o.</p>\r\n<p class=\"Normal\">Những c&ocirc;ng nh&acirc;n chuy&ecirc;n sửa sang, bảo dưỡng c&ocirc;ng vi&ecirc;n một c&aacute;ch rất tỉ mỉ, rất khoa học đối với từng c&acirc;y hoa, ngọn cỏ... để b&ecirc;n đường đi l&agrave; những thảm cỏ xanh mượt m&agrave; v&agrave; l&agrave; những vườn hoa đẹp rực rỡ như đ&oacute;n ch&agrave;o du kh&aacute;ch. Tất cả đều lu&ocirc;n nở nụ cười th&acirc;n thiện với kh&aacute;ch. Nhiều người giao tiếp được bằng tiếng Việt c&agrave;ng l&agrave;m cho quan hệ chủ, kh&aacute;ch th&ecirc;m th&acirc;n mật.</p>\r\n<p class=\"Normal\">Ch&iacute;nh v&igrave; thế m&agrave; nơi đ&acirc;y &ndash; một c&ocirc;ng vi&ecirc;n, một di sản thi&ecirc;n nhi&ecirc;n thế giới &ndash; đ&atilde; được giữ g&igrave;n một c&aacute;ch ho&agrave;n hảo, hết sức sạch sẽ. Bản th&acirc;n điều đ&oacute; như một lời nhắc nhở, tạo cho du kh&aacute;ch một th&oacute;i quen rất văn minh: kh&ocirc;ng dẫm l&ecirc;n cỏ, kh&ocirc;ng h&aacute;i một b&ocirc;ng hoa, vứt r&aacute;c đ&uacute;ng nơi quy định...</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/FB/65/5.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cảnh quan trữ t&igrave;nh hiếm c&oacute;. Ảnh:&nbsp;<em>Hồng Phương.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Ch&uacute;ng t&ocirc;i đến Thạch L&acirc;m đ&uacute;ng dịp mở đầu tuần Lễ hội của người Sani, một nh&aacute;nh của d&acirc;n tộc Yi, được chứng kiến điệu nhảy tập thể, giản dị nhưng đầy mầu sắc. Những ch&agrave;ng trai, c&ocirc; g&aacute;i người d&acirc;n tộc trong trang phục truyền thống nhảy trong tiếng nhạc d&acirc;n gian, tay trong tay, nhảy theo một v&ograve;ng tr&ograve;n, sẵn s&agrave;ng mời v&agrave; nhảy rất vui vẻ c&ugrave;ng du kh&aacute;ch.</p>\r\n<p class=\"Normal\">L&uacute;c n&agrave;y kh&ocirc;ng c&ograve;n ph&acirc;n biệt chủ - kh&aacute;ch, tất cả h&ograve;a trong kh&ocirc;ng kh&iacute; lễ hội, trong t&igrave;nh hữu nghị giữa c&aacute;c d&acirc;n tộc, c&aacute;c quốc gia. Lễ hội của người Sani diễn ra giữa khung cảnh h&ugrave;ng tr&aacute;ng, thơ mộng của đ&aacute;, của nước c&agrave;ng l&agrave;m cho Thạch L&acirc;m th&ecirc;m quyến rũ, để lại cho du kh&aacute;ch một ấn tượng kh&ocirc;ng thể n&agrave;o qu&ecirc;n.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hồng Phương</strong></p>', 3, 1090, 0, 1, NULL, NULL),
(68, 'Học sinh Hà Nội dự kiến nghỉ Tết 11 ngày', 'Hoc-Sinh-Ha-Noi-Du-Kien-Nghi-Tet-11-Ngay', 'Ngày 6/1, Sở Giáo dục Đào tạo đã có tờ trình gửi UBND Hà Nội đề xuất thời gian nghỉ Tết Tân Mão của ngành giáo dục là 11 ngày. ', 'hs-gialanh13.jpg', 22, '<h1 class=\"Title\">Học sinh H&agrave; Nội dự kiến nghỉ Tết 11 ng&agrave;y</h1>\r\n<h2 class=\"Lead\">Ng&agrave;y 6/1, Sở Gi&aacute;o dục Đ&agrave;o tạo đ&atilde; c&oacute; tờ tr&igrave;nh gửi UBND H&agrave; Nội đề xuất thời gian nghỉ Tết T&acirc;n M&atilde;o của ng&agrave;nh gi&aacute;o dục l&agrave; 11 ng&agrave;y.&nbsp;</h2>\r\n<p class=\"Normal\">Theo tờ tr&igrave;nh, c&aacute;n bộ, gi&aacute;o vi&ecirc;n, học sinh c&aacute;c trường mầm non, tiểu học, THCS, THPT, bổ t&uacute;c văn h&oacute;a, trung t&acirc;m gi&aacute;o dục thường xuy&ecirc;n, trung t&acirc;m gi&aacute;o dục kỹ thuật tổng hợp tr&ecirc;n địa b&agrave;n H&agrave; Nội sẽ được nghỉ Tết 11 ng&agrave;y, từ 28/1 (25 th&aacute;ng Chạp) đến hết ng&agrave;y 7/2 (m&ugrave;ng 5 th&aacute;ng Gi&ecirc;ng).</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/51/C8/hs-gialanh13.jpg\" border=\"1\" alt=\"\" width=\"495\" height=\"340\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Học sinh thủ đ&ocirc; đang chịu cảnh gi&aacute; lạnh. Ảnh:&nbsp;<em>L&ecirc; Hiếu.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Ri&ecirc;ng c&aacute;c trường trung cấp chuy&ecirc;n nghiệp được nghỉ 17 ng&agrave;y, từ ng&agrave;y 22/1 (19 th&aacute;ng Chạp) đến hết ng&agrave;y 7/2 (m&ugrave;ng 5 th&aacute;ng Gi&ecirc;ng). C&aacute;n bộ c&ocirc;ng chức, vi&ecirc;n chức của Sở GD&amp;ĐT, c&aacute;c ph&ograve;ng GD&amp;ĐT quận, huyện, thị x&atilde; sẽ đi l&agrave;m v&agrave;o ng&agrave;y thứ bảy (29/1), chủ nhật (30/1) v&agrave; nghỉ Tết từ ng&agrave;y 31/1 đến hết ng&agrave;y 7/2 (8 ng&agrave;y).&nbsp;<br /><br />Sau khi c&oacute; quyết định ph&ecirc; duyệt của UBND H&agrave; Nội, Sở GD&amp;ĐT H&agrave; Nội sẽ c&oacute; th&ocirc;ng b&aacute;o cụ thể về thời gian nghỉ Tết để c&aacute;c nh&agrave; trường, phụ huynh, học sinh thực hiện.</p>\r\n<p class=\"Normal\">Trước đ&oacute;, Thủ tướng cũng đ&atilde; quyết định cho c&ocirc;ng chức, vi&ecirc;n chức cả nước được nghỉ Tết nguy&ecirc;n đ&aacute;n trong 8 ng&agrave;y.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đo&agrave;n Loan</strong></p>', 1, 1107, 0, 1, NULL, NULL),
(69, 'Tổ hợp hỗ trợ giáo dục ở THPT hiện đại nhất thủ đô', 'To-Hop-Ho-Tro-Giao-Duc-O-Thpt-Hien-Dai-Nhat-Thu-Do', 'Sáng 5/1, tổ hợp hỗ trợ giáo dục tiên tiến Amsers’ Town đã được khai trương tại trường THPT chuyên Hà Nội – Amsterdam trong sự hào hứng của học sinh.', 'thu-vien.jpg', 22, '<h1 class=\"Title\">Tổ hợp hỗ trợ gi&aacute;o dục ở THPT hiện đại nhất thủ đ&ocirc;</h1>\r\n<h2 class=\"Lead\">S&aacute;ng 5/1, tổ hợp hỗ trợ gi&aacute;o dục ti&ecirc;n tiến Amsers&rsquo; Town đ&atilde; được khai trương tại trường THPT chuy&ecirc;n H&agrave; Nội &ndash; Amsterdam trong sự h&agrave;o hứng của học sinh.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/50/88/thu-vien.jpg\" border=\"1\" alt=\"To&agrave;n cảnh\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Amsers&rsquo; Town được đặt trong khu&ocirc;n vi&ecirc;n trường H&agrave; Nội &ndash; Amsterdam. Ảnh:&nbsp;<em>Ho&agrave;ng Th&ugrave;y</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; nơi cung cấp c&aacute;c dịch vụ phục vụ việc dạy v&agrave; học đối với gi&aacute;o vi&ecirc;n v&agrave; học sinh của nh&agrave; trường như: s&aacute;ch tham khảo, đồ d&ugrave;ng học tập, photocoppy, văn ph&ograve;ng phẩm...</p>\r\n<p class=\"Normal\">Amsers&rsquo; Town được xem như điểm kết nối giữa nh&agrave; trường v&agrave; c&aacute;c nh&agrave; xuất bản lớn, c&oacute; uy t&iacute;n. Được đặt ngay trong khu&ocirc;n vi&ecirc;n trường n&ecirc;n tổ hợp đ&aacute;p ứng nhanh y&ecirc;u cầu của học sinh v&agrave; gi&aacute;o vi&ecirc;n, tăng sự y&ecirc;n t&acirc;m cho phụ huynh.</p>\r\n<p class=\"Normal\">Tổ hợp Amsers&rsquo; Town kh&ocirc;ng chỉ cung cấp c&aacute;c dịch vụ gi&aacute;o dục m&agrave; trong kế hoạch ph&aacute;t triển của m&igrave;nh, sẽ trở th&agrave;nh nơi giao lưu, học tập. Trong tương lai gần, Amsers&rsquo; Town sẽ l&agrave; nơi gặp gỡ giữa c&aacute;c cựu học sinh th&agrave;nh đạt với học sinh trong trường, đồng thời cũng l&agrave; nơi diễn ra c&aacute;c buổi giao lưu với c&aacute;c học giả, chuy&ecirc;n vi&ecirc;n v&agrave; doanh nh&acirc;n.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/50/88/thu-vien-1.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Học sinh h&agrave;o hứng với m&ocirc; h&igrave;nh hỗ trợ gi&aacute;o dục mới Amsers&rsquo; Town. Ảnh:&nbsp;<em>Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Ph&aacute;t biểu tại buổi lễ, thầy L&ecirc; Trọng Tuấn, Ph&oacute; hiệu trưởng nh&agrave; trường cho biết: &ldquo;M&ocirc; h&igrave;nh tổ hợp như Amsers&rsquo; Town l&agrave; một phần thiết yếu của c&aacute;c trường ở những nước ti&ecirc;n tiến nhưng lại l&agrave; lần đầu tại trường phổ th&ocirc;ng ở H&agrave; Nội. THPT chuy&ecirc;n H&agrave; Nội &ndash; Amsterdam tự h&agrave;o l&agrave; đơn vị đầu ti&ecirc;n tạo dựng những gi&aacute; trị gi&aacute;o dục gia tăng cho học sinh v&agrave; mong muốn m&ocirc; h&igrave;nh n&agrave;y c&oacute; thể được nh&acirc;n rộng trong khối c&aacute;c trường phổ th&ocirc;ng ở H&agrave; Nội v&agrave; c&aacute;c địa phương kh&aacute;c...&rdquo;.</p>\r\n<p class=\"Normal\">Trong buổi ra mắt, c&aacute;c đơn vị hợp t&aacute;c đ&atilde; trao 64 m&oacute;n qu&agrave;, bao gồm s&aacute;ch v&agrave; đồ d&ugrave;ng học tập cho c&aacute;n bộ c&aacute;c lớp của trường. NXB Kim Đồng v&agrave; Nh&agrave; s&aacute;ch Long Minh trao tặng thư viện 150 đầu s&aacute;ch.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/50/88/amser.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Rất đ&ocirc;ng học sinh v&agrave; gi&aacute;o vi&ecirc;n đ&atilde; đến dự buổi khai trương tổ hợp Amsers&rsquo; Town. Ảnh:<em>&nbsp;Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Tổ hợp Amsers&rsquo; Town l&agrave; kết quả hợp t&aacute;c của THPT chuy&ecirc;n H&agrave; Nội &ndash; Amsterdam với NXB Kim Đồng, NXB Trẻ, Nh&agrave; s&aacute;ch Long Minh v&agrave; c&ocirc;ng ty An Sinh. Trong tương lai, tổ hợp n&agrave;y sẽ tiếp tục đ&oacute;n nhận sự hợp t&aacute;c của c&aacute;c nh&agrave; xuất bản lớn, c&oacute; uy t&iacute;n ở cả trong v&agrave; ngo&agrave;i nước.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 1, 2156, 0, 1, NULL, NULL),
(70, 'Hỗ trợ gần 3.000 vé xe tết cho sinh viên', 'Ho-Tro-Gan-3.000-Ve-Xe-Tet-Cho-Sinh-Vien', 'Khoảng 2.700 sinh viên ngoại tỉnh có hoàn cảnh khó khăn, đang học tập tại TP HCM sẽ được tặng vé xe miễn phí về quê trong dịp tết Tân Mão.', 'sinhvien[1].jpg', 22, '<h1 class=\"Title\">Hỗ trợ gần 3.000 v&eacute; xe tết cho sinh vi&ecirc;n</h1>\r\n<h2 class=\"Lead\">Khoảng 2.700 sinh vi&ecirc;n ngoại tỉnh c&oacute; ho&agrave;n cảnh kh&oacute; khăn, đang học tập tại TP HCM sẽ được tặng v&eacute; xe miễn ph&iacute; về qu&ecirc; trong dịp tết T&acirc;n M&atilde;o.</h2>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; một trong chuỗi c&aacute;c hoạt động hỗ trợ sinh vi&ecirc;n dịp tết của Trung t&acirc;m hỗ trợ sinh vi&ecirc;n TP HCM. Trong đ&oacute;, dự kiến sẽ d&agrave;nh 1.800 v&eacute; cho sinh vi&ecirc;n ngh&egrave;o c&oacute; hộ khẩu thường tr&uacute; tại c&aacute;c tỉnh miền Trung bị thiệt hại nặng nề trong c&aacute;c đợt b&atilde;o lũ vừa qua. 900 v&eacute; c&ograve;n lại sẽ tặng cho c&aacute;c sinh vi&ecirc;n ngh&egrave;o, sinh vi&ecirc;n khuyết tật, sinh vi&ecirc;n d&acirc;n tộc c&aacute;c tỉnh th&agrave;nh kh&aacute;c trong cả nước.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/45/10/sinhvien[1].jpg\" border=\"1\" alt=\"Sinh vi&ecirc;n được hỗ trợ v&eacute; xe trong ng&agrave;y về qu&ecirc; đ&oacute;n tết\" width=\"480\" height=\"328\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Sinh vi&ecirc;n được hỗ trợ v&eacute; xe trong ng&agrave;y về qu&ecirc; đ&oacute;n tết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Ph&oacute; gi&aacute;m đốc trung t&acirc;m hỗ trợ sinh vi&ecirc;n Dương Trọng Ph&uacute;c cho biết, so với c&aacute;c năm trước số lượng v&eacute; hỗ trợ năm nay &iacute;t hơn nhiều v&igrave; kh&ocirc;ng c&oacute; c&aacute;c đơn vị v&agrave; doanh nghiệp đồng h&agrave;nh. Đa phần kinh ph&iacute; trong hoạt động n&agrave;y đều do Ủy ban Mặt trận tổ quốc Việt Nam TP HCM hỗ trợ.</p>\r\n<p class=\"Normal\">Số lượng v&eacute; tặng sẽ đưa xuống c&aacute;c trường theo chỉ ti&ecirc;u. Sinh vi&ecirc;n kh&oacute; khăn trong c&aacute;c diện được x&eacute;t tặng v&eacute; xe c&oacute; thể li&ecirc;n hệ trực tiếp tại văn ph&ograve;ng Đo&agrave;n thanh ni&ecirc;n - Hội sinh vi&ecirc;n, Trung t&acirc;m hỗ trợ sinh vi&ecirc;n của trường trước ng&agrave;y 31/12 để tiến h&agrave;nh l&agrave;m hồ sơ.</p>\r\n<p class=\"Normal\">Ngo&agrave;i tặng v&eacute; xe, Trung t&acirc;m hỗ trợ sinh vi&ecirc;n sẽ cung cấp khoảng 3.500 đầu việc với nhiều loại h&igrave;nh theo tuần, ng&agrave;y, giờ... cho c&aacute;c bạn ở lại c&oacute; nhu cầu kiếm th&ecirc;m thu nhập. Chương tr&igrave;nh vui tết xa nh&agrave; với nhiều hoạt động th&uacute; vị cũng sẽ được tổ chức cho những sinh vi&ecirc;n kh&ocirc;ng c&oacute; điều kiện về qu&ecirc;. Đại diện Th&agrave;nh Đo&agrave;n v&agrave; Trung t&acirc;m hỗ trợ sinh vi&ecirc;n cũng sẽ đi thăm hỏi, giao lưu v&agrave; tặng qu&agrave; cho sinh vi&ecirc;n trong KTX của c&aacute;c trường.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n</strong></p>', 1, 2669, 0, 1, NULL, NULL),
(71, '\'Mái nhà xanh\' giúp trẻ giảm sốc lần đầu đi học', '', 'Nằm nép trong góc sân trường Măng non 1, quận 10, TP HCM là căn phòng nhỏ được bày trí bắt mắt với đủ loại tranh vẽ, vật dụng, đồ chơi hình con vật ngộ nghĩnh. Đây là không gian dành cho bé tập làm quen trước khi đi học.', 'mai-nha-xanh-1.jpg', 22, '<h1 class=\"Title\">\'M&aacute;i nh&agrave; xanh\' gi&uacute;p trẻ giảm sốc lần đầu đi học</h1>\r\n<h2 class=\"Lead\">Nằm n&eacute;p trong g&oacute;c s&acirc;n trường Măng non 1, quận 10, TP HCM l&agrave; căn ph&ograve;ng nhỏ được b&agrave;y tr&iacute; bắt mắt với đủ loại tranh vẽ, vật dụng, đồ chơi h&igrave;nh con vật ngộ nghĩnh. Đ&acirc;y l&agrave; kh&ocirc;ng gian d&agrave;nh cho b&eacute; tập l&agrave;m quen trước khi đi học.</h2>\r\n<p class=\"Normal\">M&ocirc; h&igrave;nh n&agrave;y c&oacute; t&ecirc;n \"M&aacute;i nh&agrave; xanh\", nơi vui chơi d&agrave;nh cho c&aacute;c b&eacute; đến l&agrave;m quen trường lớp, gi&uacute;p b&eacute; kh&ocirc;ng bỡ ngỡ m&agrave; c&oacute; thể nhanh ch&oacute;ng h&ograve;a nhập trong lần đầu đi học.</p>\r\n<p class=\"Normal\">Hiệu trưởng trường Măng non 1 Đặng Thị &Aacute;nh Mai cho biết, \"m&aacute;i nh&agrave; xanh\" c&ograve;n được gọi l&agrave; ng&ocirc;i nh&agrave; \"giảm sốc\" - bước trung gian chuyển c&aacute;c b&eacute; từ m&ocirc;i trường gia đ&igrave;nh v&agrave;o trường học. Ở đ&acirc;y, b&eacute; được vui chơi trong ng&ocirc;i nh&agrave; nhỏ c&oacute; b&agrave;n ghế, g&oacute;c đọc truyện tranh v&agrave; đủ loại đồ chơi: b&uacute;p b&ecirc;, bộ xếp h&igrave;nh... c&ugrave;ng nhiều tranh vẽ m&agrave;u sắc tr&ecirc;n tường. Kh&ocirc;ng gian n&agrave;y sẽ tạo cho b&eacute; cảm gi&aacute;c vừa gần gũi, vừa được kh&aacute;m ph&aacute; những điều mới mẻ, th&uacute; vị.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/36/ED/mai-nha-xanh-7.jpg\" alt=\"C&aacute;c b&eacute; mầm non vui chơi tại s&acirc;n trường.\" width=\"480\" height=\"385\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c b&eacute; vui chơi tại s&acirc;n trường mầm non Hoa Lư. Ảnh:&nbsp;<em>Hải Duy&ecirc;n.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trong thời gian b&eacute; vui chơi tại căn ph&ograve;ng n&agrave;y, gi&aacute;o vi&ecirc;n v&agrave; phụ huynh sẽ quan s&aacute;t v&agrave; nắm bắt mức độ th&iacute;ch nghi của b&eacute;. Từ đ&oacute; c&oacute; những biện ph&aacute;p, chế độ chăm s&oacute;c th&iacute;ch hợp để b&eacute; dễ h&ograve;a nhập với m&ocirc;i trường b&ecirc;n ngo&agrave;i khi t&aacute;ch khỏi gia đ&igrave;nh. Bố mẹ bận rộn, kh&ocirc;ng c&oacute; thời gian theo con đến \"m&aacute;i nh&agrave; xanh\", c&oacute; thể nhờ &ocirc;ng b&agrave;, người th&acirc;n thay thế.</p>\r\n<p class=\"Normal\">Nhiều trường mầm non tr&ecirc;n địa b&agrave;n th&agrave;nh phố cũng lập \"M&aacute;i nh&agrave; xanh\" như: Hoa Lư (quận 1), Tuổi thơ 7 (quận 3), Rạng Đ&ocirc;ng 9 (quận 6)... với c&aacute;ch trang tr&iacute; kh&aacute;c nhau. Một số trường kh&ocirc;ng c&oacute; khu&ocirc;n vi&ecirc;n rộng th&igrave; tận dụng cả g&oacute;c cầu thang, m&aacute;i hi&ecirc;n để tạo n&ecirc;n kh&ocirc;ng gian cho c&aacute;c b&eacute;.</p>\r\n<p class=\"Normal\">Theo Hiệu trưởng trường mầm non Hoa Lư Nguyễn Thị Kim V&acirc;n, c&aacute;c phụ huynh c&oacute; thể cho con chơi tại \"m&aacute;i nh&agrave; xanh\" với thời lượng tăng dần, từ 2 giờ đến 3 giờ hoặc nửa ng&agrave;y. Sau khi b&eacute; đ&atilde; quen th&igrave; cho đi tham quan lớp học của c&aacute;c anh chị lớp lớn. Bố mẹ cũng n&ecirc;n v&agrave;o trong lớp c&ugrave;ng chơi với con. Đến khi b&eacute; ho&agrave;n to&agrave;n ổn định về mặt t&acirc;m l&yacute; mới cho đi học.</p>\r\n<p class=\"Normal\">C&ocirc; hiệu trưởng giải th&iacute;ch: \"Nếu chưa l&agrave;m quen với trường lớp m&agrave; đường đột đi học, b&eacute; sẽ bị sốc v&agrave; rối loạn t&acirc;m l&yacute;, từ đ&oacute; dẫn đến sợ đi học, gh&eacute;t c&ocirc; gi&aacute;o hoặc rơi v&agrave;o trạng th&aacute;i rối loạn quan hệ với gia đ&igrave;nh, trầm cảm. C&aacute;c bậc cha mẹ đừng nghĩ rằng, c&aacute;c b&eacute; sẽ vui khi đến trường mầm non v&igrave; c&oacute; bạn v&agrave; c&ocirc; gi&aacute;o. Bởi đ&acirc;y l&agrave; m&ocirc;i trường ho&agrave;n to&agrave;n xa lạ, kh&ocirc;ng kh&aacute;c g&igrave; c&ocirc;ng vi&ecirc;n, si&ecirc;u thị... v&agrave; lu&ocirc;n cần c&oacute; người th&acirc;n b&ecirc;n cạnh b&eacute; thời gian đầu\".</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/36/ED/mai-nha-xanh-1.jpg\" alt=\"kh&ocirc;ng gian b&ecirc;n trong\" width=\"480\" height=\"355\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Kh&ocirc;ng gian b&ecirc;n trong \"m&aacute;i nh&agrave; xanh\" của trường măng non 1, quận 10 được trang tr&iacute; với nhiều h&igrave;nh ảnh ngộ nghĩnh. Ảnh:&nbsp;<em>Hải Duy&ecirc;n</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Đồng quan điểm tr&ecirc;n, b&agrave; Kim Thanh, Trưởng ph&ograve;ng gi&aacute;o dục mầm non Sở Gi&aacute;o dục TP HCM cho biết, c&aacute;c b&eacute; khi bắt đầu đi học thường c&oacute; biểu hiện kh&oacute;c nhiều, kh&oacute; ăn, hay buồn n&ocirc;n, sụt c&acirc;n... Những việc n&agrave;y đều xuất ph&aacute;t từ t&acirc;m l&yacute; kh&ocirc;ng ổn định của b&eacute;, sợ bị cha mẹ bỏ rơi. Do đ&oacute;, đưa b&eacute; đến chơi tại \"m&aacute;i nh&agrave; xanh\" l&agrave; c&aacute;ch tốt để b&eacute; l&agrave;m quen với m&ocirc;i trường b&ecirc;n ngo&agrave;i, gi&uacute;p ổn định tinh thần để dễ d&agrave;ng h&ograve;a nhập với bạn b&egrave; v&agrave; th&oacute;i quen sinh hoạt tr&ecirc;n lớp. Điều n&agrave;y c&ograve;n g&oacute;p phần quan trọng v&agrave;o việc giảm &aacute;p lực cho gi&aacute;o vi&ecirc;n trong thời gian nhận c&aacute;c b&eacute; mới.</p>\r\n<p class=\"Normal\">\"Trẻ dưới 3 tuổi chưa biết c&aacute;ch tự thiết lập mối quan hệ với người xung quanh. Mọi thứ đều phụ thuộc v&agrave;o c&ocirc; gi&aacute;o hoặc người th&acirc;n. V&igrave; vậy việc giảm sốc cho trẻ cũng phải thực hiện từ từ, cần tập cho b&eacute; những kỹ năng tự phục vụ cơ bản khi đi học ch&iacute;nh thức\", b&agrave; Thanh n&oacute;i.</p>\r\n<p class=\"Normal\">\"M&aacute;i nh&agrave; xanh\" được triển khai th&iacute; điểm tại c&aacute;c trường ở TP HCM từ năm 2007. Cho đến nay, m&ocirc; h&igrave;nh n&agrave;y được thực hiện ở một số trường v&agrave; đem lại hiệu quả ngo&agrave;i mong đợi. Theo c&aacute;c gi&aacute;o vi&ecirc;n mầm non, t&igrave;nh trạng kh&oacute;c nhiều, sợ đi học của những trẻ lần đầy tới trường giảm hẳn. C&aacute;c em cũng h&ograve;a nhập nhanh hơn với nề nếp tr&ecirc;n lớp. Đ&acirc;y l&agrave; một h&igrave;nh thức \"giảm sốc\" được c&aacute;c chuy&ecirc;n gia t&acirc;m l&yacute; người Ph&aacute;p nghi&ecirc;n cứu v&agrave; hỗ trợ triển khai tại Việt Nam.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n</strong></p>', 1, 3, 0, 1, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(72, 'Bộ Giáo dục tri ân nhà giáo ưu tú', 'Bo-Giao-Duc-Tri-An-Nha-Giao-Uu-Tu', 'Sáng nay tại Hà Nội, Bộ Giáo dục và Đào tạo đã tổ chức kỷ niệm 28 năm ngày nhà giáo Việt Nam và gặp gỡ các nhà giáo nhân dân, nhà giáo ưu tú năm 2010.', 'gap_mat_nha_giao_nhan_dan.JPG', 22, '<h1 class=\"Title\">Bộ Gi&aacute;o dục tri &acirc;n nh&agrave; gi&aacute;o ưu t&uacute;</h1>\r\n<h2 class=\"Lead\">S&aacute;ng nay tại H&agrave; Nội, Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo đ&atilde; tổ chức kỷ niệm 28 năm ng&agrave;y nh&agrave; gi&aacute;o Việt Nam v&agrave; gặp gỡ c&aacute;c nh&agrave; gi&aacute;o nh&acirc;n d&acirc;n, nh&agrave; gi&aacute;o ưu t&uacute; năm 2010.</h2>\r\n<p class=\"Normal\">&Ocirc;ng Trần C&ocirc;ng Phong, Chủ tịch c&ocirc;ng đo&agrave;n gi&aacute;o dục Việt Nam khẳng định, nh&agrave; gi&aacute;o lu&ocirc;n lu&ocirc;n được Đảng, nh&agrave; nước v&agrave; nh&acirc;n d&acirc;n t&ocirc;n vinh. Những nh&agrave; gi&aacute;o, c&aacute;n bộ quản l&yacute; gi&aacute;o dục c&oacute; th&agrave;nh t&iacute;ch xuất sắc trong phong tr&agrave;o thi đua y&ecirc;u nước v&agrave; c&aacute;c cuộc vận động của ng&agrave;nh c&agrave;ng được trọng vọng.</p>\r\n<p class=\"Normal\">\"Nh&agrave; gi&aacute;o nh&acirc;n d&acirc;n, nh&agrave; gi&aacute;o ưu t&uacute; lu&ocirc;n đi đầu trong c&aacute;c phong tr&agrave;o thi đua dạy tốt, học tốt, chăm lo sự nghiệp gi&aacute;o dục, l&agrave; người c&oacute; c&ocirc;ng lao to lớn trong việc n&acirc;ng cao d&acirc;n tr&iacute;, ph&aacute;t triển nguồn nh&acirc;n lực, ph&aacute;t hiện v&agrave; bồi dưỡng nh&acirc;n t&agrave;i cho đất nước\", &ocirc;ng Phong n&oacute;i.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/32/27/gap_mat_nha_giao_nhan_dan.JPG\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nh&agrave; gi&aacute;o lu&ocirc;n được Đảng, nh&agrave; nước v&agrave; nh&acirc;n d&acirc;n t&ocirc;n vinh.&nbsp;<em>Ảnh: Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Tham gia buổi gặp mặt, nhiều nh&agrave; gi&aacute;o đ&atilde; nhớ lại những ng&agrave;y đầu gian kh&oacute; trong sự nghiệp trồng người. Nh&agrave; gi&aacute;o nh&acirc;n d&acirc;n Đặng Quang Việt, Hiệu trưởng ĐH T&acirc;y Bắc cho biết, những ng&agrave;y đầu về Cao đẳng T&acirc;y Bắc nhận c&ocirc;ng t&aacute;c, &ocirc;ng gần như ng&atilde; quỵ bởi phải chịu c&aacute;i gi&aacute; lạnh của v&ugrave;ng rừng n&uacute;i trong căn ph&ograve;ng trống hơ trống ho&aacute;c v&agrave; c&aacute;i đ&oacute;i triền mi&ecirc;n.</p>\r\n<p class=\"Normal\">\"D&ugrave; vất vả nhưng đổi lại, sinh vi&ecirc;n của t&ocirc;i rất ham học. C&aacute;c em nghe giảng h&aacute;o hức như muốn nuốt lấy lời thầy. Ch&iacute;nh điều đ&oacute; đ&atilde; l&agrave;m t&ocirc;i qu&ecirc;n hết kh&oacute; khăn, cố gắng truyền tải cho c&aacute;c em thật nhiều kiến thức trong mỗi tiết học\", thầy Việt n&oacute;i.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#efefef\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Normal\">Từ năm 1988 đến nay đ&atilde; c&oacute; 11 đợt phong tặng, ghi nhận v&agrave; tuy&ecirc;n dương th&agrave;nh t&iacute;ch xuất sắc cho 488 nh&agrave; gi&aacute;o nh&acirc;n d&acirc;n v&agrave; 6.165 nh&agrave; gi&aacute;o ưu t&uacute; ở tất cả c&aacute;c bậc học tr&ecirc;n cả nước. Số gi&aacute;o vi&ecirc;n đứng lớp tr&ecirc;n cả nước l&agrave; hơn một triệu, trong đ&oacute; gi&aacute;o vi&ecirc;n mầm non 195.852, gi&aacute;o vi&ecirc;n phổ th&ocirc;ng 804.183, gi&aacute;o vi&ecirc;n trung cấp chuy&ecirc;n nghiệp 17.488 người. Giảng vi&ecirc;n trong c&aacute;c trường đại học, cao đẳng c&oacute; 70.558 người, trong đ&oacute; 277 GS, 1.925 PGS, 7.104 TS v&agrave; 26.715 thạc sĩ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 1, 843, 0, 1, NULL, NULL),
(73, 'Có \'phao\' của Bộ, trường ngoài công lập vẫn đuối', '', 'Kết thúc thời gian nới hạn tuyển sinh, các trường ngoài công lập vẫn thiếu chỉ tiêu trầm trọng. Có trường tuyển chưa được 1/3 chỉ tiêu được giao.', 'dai_hoc_1.jpg', 22, '<h1 class=\"Title\">C&oacute; \'phao\' của Bộ, trường ngo&agrave;i c&ocirc;ng lập vẫn đuối</h1>\r\n<h2 class=\"Lead\">Kết th&uacute;c thời gian nới hạn tuyển sinh, c&aacute;c trường ngo&agrave;i c&ocirc;ng lập vẫn thiếu chỉ ti&ecirc;u trầm trọng. C&oacute; trường tuyển chưa được 1/3 chỉ ti&ecirc;u được giao.</h2>\r\n<p class=\"Normal\">Đầu th&aacute;ng 11, Bộ GD&amp;ĐT c&oacute; quyết định k&eacute;o d&agrave;i thời gian tuyển sinh cho c&aacute;c trường ngo&agrave;i c&ocirc;ng lập. Theo đ&oacute;, c&aacute;c trường được ph&eacute;p k&eacute;o d&agrave;i thời gian x&eacute;t tuyển đến hết ng&agrave;y 15/11. Tuy nhi&ecirc;n, trao đổi với&nbsp;<em>VnExpress</em>, hầu hết trường đều cho rằng việc gia hạn kh&ocirc;ng gi&uacute;p c&oacute; th&ecirc;m chỉ ti&ecirc;u v&igrave; đ&atilde; qu&aacute; muộn v&agrave; nguồn tuyển cũng kh&ocirc;ng c&ograve;n.</p>\r\n<p class=\"Normal\">&Ocirc;ng Đặng Văn Định, Hiệu trưởng ĐH Chu Văn An cho biết, từ khi nhận quyết định của Bộ, trường đ&atilde; d&ugrave;ng nhiều biện ph&aacute;p, song đến 15/11 vẫn kh&ocirc;ng nhận th&ecirc;m hồ sơ n&agrave;o đăng k&yacute;. Hiện chỉ ti&ecirc;u tuyển sinh của trường l&agrave; 1.500 gồm cả đại học v&agrave; cao đẳng, nhưng đến khi kết th&uacute;c tuyển sinh nguyện vọng 3 (30/9), trường mới c&oacute; 250 chỉ ti&ecirc;u đại học v&agrave; hơn 150 chỉ ti&ecirc;u cao đẳng.</p>\r\n<p class=\"Normal\">\"Rất hoan ngh&ecirc;nh v&agrave; cảm ơn Bộ đ&atilde; quan t&acirc;m đến c&aacute;c trường ngo&agrave;i c&ocirc;ng lập, nhưng c&oacute; lẽ c&aacute;c em đ&atilde; c&oacute; quyết định về con đường đi từ trước, c&oacute; thể lựa chọn học nghề hoặc quyết định thi lại\", &ocirc;ng Định n&oacute;i v&agrave; cho biết sẽ cố gắng tăng chỉ ti&ecirc;u đ&agrave;o tạo li&ecirc;n th&ocirc;ng, li&ecirc;n kết, văn bằng 2... để tăng số lượng đ&agrave;o tạo, từ đ&oacute; mới c&acirc;n đối được thu chi, đảm bảo quy m&ocirc; đ&agrave;o tạo của trường.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/30/A8/dai_hoc_1.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">D&ugrave; được Bộ Gi&aacute;o dục gia hạn, c&aacute;c trường ngo&agrave;i c&ocirc;ng lập vẫn thiếu chỉ ti&ecirc;u trầm trọng.&nbsp;<em>Ảnh: Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Cũng kh&ocirc;ng tuyển th&ecirc;m được chỉ ti&ecirc;u n&agrave;o trong thời gian Bộ gia hạn, &ocirc;ng Trần Hữu Nghị, Hiệu trưởng ĐH d&acirc;n lập Hải Ph&ograve;ng cho biết, ngay từ khi nhận được quyết định của Bộ, Ban gi&aacute;m đốc nh&agrave; trường cũng kh&ocirc;ng hy vọng g&igrave; ở \"phao cứu sinh\" n&agrave;y bởi đ&atilde; qu&aacute; muộn, kh&ocirc;ng c&ograve;n th&iacute; sinh để tuyển. Giả định nếu tuyển được, &ocirc;ng cũng kh&ocirc;ng biết sẽ giải quyết việc học cho c&aacute;c em như thế n&agrave;o khi năm học mới đ&atilde; bắt đầu được hơn một th&aacute;ng.</p>\r\n<p class=\"Normal\">\"Số lượng th&iacute; sinh tr&ecirc;n điểm s&agrave;n kh&ocirc;ng nhiều trong khi c&aacute;c trường c&ocirc;ng lập ở Hải Ph&ograve;ng đ&atilde; lấy s&aacute;t mức điểm ấy. Thế n&ecirc;n ch&uacute;ng t&ocirc;i rất kh&oacute; khăn trong việc thu h&uacute;t sinh vi&ecirc;n. C&oacute; chăng, chỉ c&ograve;n c&aacute;ch hạ điểm s&agrave;n mới giải được b&agrave;i to&aacute;n thiếu nguồn tuyển của trường ngo&agrave;i c&ocirc;ng lập\", &ocirc;ng Nghị đề xuất.</p>\r\n<p class=\"Normal\">&Ocirc;ng Ho&agrave;ng Hữu Nguy&ecirc;n, Hiệu ph&oacute; ĐH Th&agrave;nh T&acirc;y cho biết, Bộ k&yacute; quyết định điều chỉnh chỉ ti&ecirc;u tuyển sinh v&agrave; k&eacute;o d&agrave;i thời gian x&eacute;t tuyển hệ ch&iacute;nh quy năm 2010 từ ng&agrave;y 1/11, nhưng đến 8/11 nh&agrave; trường mới nhận được văn bản. Mặt kh&aacute;c, hạn cuối x&eacute;t tuyển theo quy chế tuyển sinh từ 30/9, nhưng đến tận đầu th&aacute;ng 11 Bộ mới c&oacute; quyết định gia hạn tuyển sinh cho c&aacute;c trường, như vậy l&agrave; qu&aacute; muộn.</p>\r\n<p class=\"Normal\">\"Hiện nay ch&uacute;ng t&ocirc;i mới tuyển được 1/3 chỉ ti&ecirc;u, kh&ocirc;ng tuyển th&ecirc;m được th&iacute; sinh n&agrave;o trong đợt gia hạn. Gi&aacute; như Bộ cho ph&eacute;p sớm hơn th&igrave; ch&uacute;ng t&ocirc;i c&oacute; thể thay đổi được t&igrave;nh trạng n&agrave;y\", &ocirc;ng Nguy&ecirc;n n&oacute;i.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 1, 3, 0, 1, NULL, NULL),
(74, 'Vinh danh 121 nhà giáo trẻ tiêu biểu TP HCM', 'Vinh-Danh-121-Nha-Giao-Tre-Tieu-Bieu-Tp-Hcm', 'Những nhà giáo điển hình về đạo đức, xuất sắc trong chuyên môn, nghiên cứu và nhiệt tình cống hiến cho xã hội được Thành đoàn TP HCM tuyên dương đêm 14/11, tại Nhà văn hóa thanh niên.', 'giang-vien-tre-1.jpg', 22, '<h1 class=\"Title\">Vinh danh 121 nh&agrave; gi&aacute;o trẻ ti&ecirc;u biểu TP HCM</h1>\r\n<h2 class=\"Lead\">Những nh&agrave; gi&aacute;o điển h&igrave;nh về đạo đức, xuất sắc trong chuy&ecirc;n m&ocirc;n, nghi&ecirc;n cứu v&agrave; nhiệt t&igrave;nh cống hiến cho x&atilde; hội được Th&agrave;nh đo&agrave;n TP HCM tuy&ecirc;n dương đ&ecirc;m 14/11, tại Nh&agrave; văn h&oacute;a thanh ni&ecirc;n.</h2>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; một hoạt động nhằm ch&agrave;o mừng ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam 20/11 được Th&agrave;nh đo&agrave;n TP HCM thực hiện thường ni&ecirc;n từ năm 2008.</p>\r\n<p class=\"Normal\">Để khuyến kh&iacute;ch sự nỗ lực phấn đấu của c&aacute;c nh&agrave; gi&aacute;o trẻ, năm nay Ban thường vụ th&agrave;nh đo&agrave;n ch&iacute;nh thức ban h&agrave;nh quy chế, n&acirc;ng danh hiệu \"Nh&agrave; gi&aacute;o trẻ ti&ecirc;u biểu\" l&ecirc;n th&agrave;nh \"Giải thưởng Nh&agrave; gi&aacute;o trẻ ti&ecirc;u biểu\".</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/2F/3B/giang-vien-tre-1.jpg\" border=\"1\" alt=\"C&aacute;c Nh&agrave; gi&aacute;o trẻ được vinh danh\" width=\"480\" height=\"335\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Gương c&aacute;c \"Nh&agrave; gi&aacute;o trẻ ti&ecirc;u biểu\" được vinh danh. Ảnh:&nbsp;<em>Hải Duy&ecirc;n</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Từ 369 hồ sơ đề cử của c&aacute;c trường, ph&ograve;ng gi&aacute;o dục v&agrave; c&aacute;c cơ sở đo&agrave;n tr&ecirc;n địa b&agrave;n TP HCM, sau khi x&eacute;t chọn theo quy chế, Hội đồng giải thưởng chọn ra 121 gương nh&agrave; gi&aacute;o trẻ ti&ecirc;u biểu để tuy&ecirc;n dương. Trong đ&oacute; c&oacute; nhiều nh&agrave; gi&aacute;o đ&atilde; ba năm liền đạt danh hiệu n&agrave;y như: thầy Đặng Tất Dũng, thầy V&otilde; Trung T&iacute;n (giảng vi&ecirc;n ĐH Luật TP HCM), c&ocirc; Trần Thị Nguyệt Sương (giảng vi&ecirc;n ĐH T&ocirc;n Đức Thắng)...</p>\r\n<p class=\"Normal\">Đặc biệt trong c&aacute;c gương nh&agrave; gi&aacute;o trẻ năm nay c&ograve;n c&oacute; thầy Phan Thanh Sơn Nam, 33 tuổi l&agrave; một trong những Ph&oacute; gi&aacute;o sư trẻ tuổi nhất của Việt Nam.</p>\r\n<p class=\"Normal\">Kh&ocirc;ng chỉ l&agrave; những gi&aacute;o vi&ecirc;n ti&ecirc;u biểu về đạo đức lối sống trong trường học, l&agrave; người c&oacute; nhiều s&aacute;ng kiến trong giảng dạy, c&aacute;c nh&agrave; gi&aacute;o trẻ c&ograve;n l&agrave; những chiến sĩ t&igrave;nh nguyện, c&aacute;n bộ đo&agrave;n giỏi, tham gia nhiều hoạt động x&atilde; hội như: m&ugrave;a h&egrave; xanh, lập học bổng hỗ trợ cho c&aacute;c em học sinh ngh&egrave;o hiếu học...</p>\r\n<p class=\"Normal\">\"Nh&agrave; gi&aacute;o trẻ ti&ecirc;u biểu\" l&agrave; danh hiệu chỉ d&agrave;nh cho c&aacute;c thầy c&ocirc; gi&aacute;o c&oacute; tuổi đời kh&ocirc;ng qu&aacute; 35.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n</strong></p>', 1, 2508, 0, 1, NULL, NULL),
(75, '2.000 tỷ đồng phát triển trường chuyên', '2.000-Ty-Dong-Phat-Trien-Truong-Chuyen', 'Số tiền này dành để tăng cường đầu tư cơ sở vật chất, trang thiết bị dạy học, đồng thời phát triển đội ngũ cán bộ quản lý, giáo viên trong các trường chuyên.', 'chuyen1.jpg', 22, '<h1 class=\"Title\">2.000 tỷ đồng ph&aacute;t triển trường chuy&ecirc;n</h1>\r\n<h2 class=\"Lead\">Số tiền n&agrave;y d&agrave;nh để tăng cường đầu tư cơ sở vật chất, trang thiết bị dạy học, đồng thời ph&aacute;t triển đội ngũ c&aacute;n bộ quản l&yacute;, gi&aacute;o vi&ecirc;n trong c&aacute;c trường chuy&ecirc;n.</h2>\r\n<p class=\"Normal\">&Ocirc;ng Vũ Đ&igrave;nh Chuẩn, Vụ trưởng Gi&aacute;o dục trung học (Bộ GD&amp;ĐT) cho biết, đề &aacute;n ph&aacute;t triển hệ thống trường THPT chuy&ecirc;n giai đoạn 2010-2020 đặt ra mục ti&ecirc;u mỗi tỉnh, th&agrave;nh phố sẽ c&oacute; &iacute;t nhất một trường THPT chuy&ecirc;n, tổng số học sinh chuy&ecirc;n chiếm khoảng 2% số học sinh phổ th&ocirc;ng của từng tỉnh th&agrave;nh.</p>\r\n<p class=\"Normal\">Đến năm 2015, 100% trường THPT chuy&ecirc;n đạt chuẩn quốc gia, 100% c&aacute;n bộ quản l&yacute;, gi&aacute;o vi&ecirc;n giỏi về chuy&ecirc;n m&ocirc;n, nghiệp vụ, sử dụng th&ocirc;ng thạo tin học v&agrave; thiết bị dạy học hiện đại...</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/26/EF/chuyen1.jpg\" alt=\"\" width=\"495\" height=\"362\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Mỗi tỉnh th&agrave;nh sẽ c&oacute; &iacute;t nhất một trường chuy&ecirc;n.&nbsp;<em>Ảnh: Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Để đạt được mục ti&ecirc;u, đề &aacute;n đ&atilde; đặt ra 6 nhiệm vụ v&agrave; giải ph&aacute;p trong đ&oacute; tăng cường c&ocirc;ng t&aacute;c quản l&yacute; v&agrave; hợp t&aacute;c quốc tế trong việc ph&aacute;t triển hệ thống trường THPT chuy&ecirc;n. Đề &aacute;n sẽ thực hiện theo 2 giai đoạn. Giai đoạn 1 (2010-2015) tập trung tạo cơ sở, nền tảng ph&aacute;t triển hệ thống trường THPT chuy&ecirc;n. Giai đoạn 2 (2015-2020) ch&uacute; trọng ph&aacute;t triển hệ thống trường THPT chuy&ecirc;n một c&aacute;ch vững chắc.</p>\r\n<p class=\"Normal\">Thứ trưởng Bộ GD&amp;ĐT Nguyễn Vinh Hiển l&agrave; trưởng ban điều h&agrave;nh đề &aacute;n.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 1, 2475, 0, 1, NULL, NULL),
(76, 'Học viện đầu tiên công khai tuyển phó giám đốc', 'Hoc-Vien-Dau-Tien-Cong-Khai-Tuyen-Pho-Giam-Doc', 'Học viện Công nghệ Bưu chính Viễn thông (Tập đoàn Bưu chính Viễn thông Việt Nam), vừa thông báo mời giới thiệu hoặc ứng cử 2 chức danh Phó giám đốc làm việc tại Hà Nội, nhằm đáp ứng sự phát triển mạnh của học viện.', 'Hoc-vien.jpg', 22, '<p class=\"Title\">Học viện đầu ti&ecirc;n c&ocirc;ng khai tuyển ph&oacute; gi&aacute;m đốc</p>\r\n<p class=\"Lead\">Học viện C&ocirc;ng nghệ Bưu ch&iacute;nh Viễn th&ocirc;ng (Tập đo&agrave;n Bưu ch&iacute;nh Viễn th&ocirc;ng Việt Nam), vừa th&ocirc;ng b&aacute;o mời giới thiệu hoặc ứng cử 2 chức danh Ph&oacute; gi&aacute;m đốc l&agrave;m việc tại H&agrave; Nội, nhằm đ&aacute;p ứng sự ph&aacute;t triển mạnh của học viện.</p>\r\n<p class=\"Normal\">Theo đ&oacute;, một Ph&oacute; gi&aacute;m đốc sẽ đảm nhiệm việc quản l&yacute; đ&agrave;o tạo, một Ph&oacute; gi&aacute;m đốc quản l&yacute; nghi&ecirc;n cứu khoa học v&agrave; chuyển giao c&ocirc;ng nghệ của học viện. Y&ecirc;u cầu của trường l&agrave; ứng vi&ecirc;n nữ kh&ocirc;ng qu&aacute; 40 tuổi v&agrave; ứng vi&ecirc;n nam kh&ocirc;ng qu&aacute; 50 tuổi, trường hợp đặc biệt cũng kh&ocirc;ng được qu&aacute; 45 (nữ) v&agrave; 55 tuổi (nam).</p>\r\n<p class=\"Normal\">Những ứng vi&ecirc;n đang l&agrave;m Hiệu trưởng, Hiệu ph&oacute;, Trưởng khoa đ&agrave;o tạo đại học hoặc sau đại học c&aacute;c trường; Gi&aacute;m đốc, Ph&oacute; gi&aacute;m đốc Học viện (c&oacute; đ&agrave;o tạo đại học); Viện trưởng, Viện ph&oacute; Viện nghi&ecirc;n cứu trực thuộc cấp Bộ trở l&ecirc;n; Vụ trưởng, Vụ ph&oacute; c&aacute;c vụ thuộc Bộ; Trưởng ban, Ph&oacute; trưởng ban chức năng thuộc VNPT; Gi&aacute;m đốc, Ph&oacute; gi&aacute;m đốc c&aacute;c doanh nghiệp hoạt động trong lĩnh vực khoa học c&ocirc;ng nghệ v&agrave; đ&agrave;o tạo... đủ ti&ecirc;u chuẩn ứng cử chức danh Ph&oacute; gi&aacute;m đốc học viện.<br /><br />B&ecirc;n cạnh y&ecirc;u cầu phải c&oacute; học vị Tiến sĩ trở l&ecirc;n về khoa học kỹ thuật (đ&atilde; qua bồi dưỡng về quản l&yacute; kinh tế) hoặc quản l&yacute; kinh tế hoặc quản l&yacute; đ&agrave;o tạo, sử dụng th&agrave;nh thạo &iacute;t nhất một ngoại ngữ th&ocirc;ng dụng (ưu ti&ecirc;n tiếng Anh), c&aacute;c ứng vi&ecirc;n phải c&oacute; phẩm chất ch&iacute;nh trị, đạo đức tốt; c&oacute; uy t&iacute;n trong giới khoa học, gi&aacute;o dục; T&acirc;m huyết với sự nghiệp gi&aacute;o dục, đ&agrave;o tạo; c&oacute; tầm nh&igrave;n v&agrave; năng lực triển vọng đ&oacute;ng g&oacute;p được tốt ở cương vị Ph&oacute; gi&aacute;m đốc học viện...</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1A/FE/Hoc-vien.jpg\" alt=\"Ảnh:\" width=\"480\" height=\"348\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cơ sở của Học viện C&ocirc;ng nghệ Bưu ch&iacute;nh Viễn th&ocirc;ng ở H&agrave; Nội. Ảnh:&nbsp;<em>Mai Hạnh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Theo PGS TS Ho&agrave;ng Minh, Gi&aacute;m đốc học viện, việc th&ocirc;ng b&aacute;o rộng r&atilde;i n&agrave;y vừa để mở rộng cơ hội lựa chọn, t&igrave;m kiếm c&aacute;n bộ, vừa g&oacute;p phần n&acirc;ng cao h&igrave;nh ảnh v&agrave; n&acirc;ng cao năng lực cạnh tranh nguồn nh&acirc;n lực của học viện. Đồng thời, học viện cũng cần được bổ sung th&ecirc;m c&aacute;c nh&acirc;n tố mới trong đội ngũ c&aacute;n bộ quản l&yacute; khi chuyển sang hoạt động theo cơ chế tự chủ, tự chịu tr&aacute;ch nhiệm.</p>\r\n<p class=\"Normal\">\"Ch&uacute;ng t&ocirc;i quan t&acirc;m v&agrave; ưu ti&ecirc;n c&aacute;c ứng vi&ecirc;n c&oacute; chức danh gi&aacute;o sư, ph&oacute; gi&aacute;o sư v&agrave; khuyến kh&iacute;ch c&aacute;c nh&agrave; gi&aacute;o, nh&agrave; khoa học gốc Việt ứng cử. C&aacute;c ứng vi&ecirc;n sẽ được l&agrave;m việc trong m&ocirc;i trường năng động, s&aacute;ng tạo, tr&acirc;n trọng c&aacute;n bộ tr&iacute; thức, với nhiều cơ hội để cống hiến v&agrave; ph&aacute;t triển sự nghiệp\", &ocirc;ng Minh nhấn mạnh.</p>\r\n<p class=\"Normal\">Học viện C&ocirc;ng nghệ Bưu ch&iacute;nh Viễn th&ocirc;ng nhận hồ sơ giới thiệu ứng vi&ecirc;n hoặc tự ứng cử đến hết ng&agrave;y 30/11. Đ&acirc;y l&agrave; cơ sở gi&aacute;o dục đại học c&ocirc;ng lập đầu ti&ecirc;n c&ocirc;ng bố \"tuyển\" chức danh l&atilde;nh đạo. Trước đ&oacute;, Bộ GD&amp;ĐT cũng c&ocirc;ng bố \"tuyển\" ứng vi&ecirc;n cho chức danh Thứ trưởng, Vụ trưởng. V&agrave; Sở GD&amp;ĐT Đ&agrave; Nẵng, Bắc Giang, Ph&uacute; Thọ cũng đ&atilde; tổ chức thi tuyển c&aacute;n bộ chức danh hiệu trưởng, hiệu ph&oacute; của h&agrave;ng chục trường.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"99%\">\r\n<tbody>\r\n<tr>\r\n<td bgcolor=\"#dcdcba\">Học viện C&ocirc;ng nghệ Bưu ch&iacute;nh Viễn th&ocirc;ng hiện c&oacute; 2 cơ sở đ&agrave;o tạo tại H&agrave; Nội v&agrave; TP HCM với 12 khoa đ&agrave;o tạo đại học v&agrave; sau đại học c&aacute;c ng&agrave;nh: Điện tử - Viễn th&ocirc;ng, Điện - Điện tử, C&ocirc;ng nghệ th&ocirc;ng tin, Quản trị Kinh doanh, T&agrave;i ch&iacute;nh Kế to&aacute;n c&ugrave;ng gần 26 ng&agrave;n sinh vi&ecirc;n c&aacute;c hệ đang theo học. Ban gi&aacute;m đốc Học viện hiện gồm một Gi&aacute;m đốc v&agrave; 2 Ph&oacute; gi&aacute;m đốc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tiến Dũng</strong></p>', 1, 948, 0, 1, NULL, NULL),
(77, 'Những tân sinh viên mồ côi, hiếu học', 'Nhung-Tan-Sinh-Vien-Mo-Coi,-Hieu-Hoc', 'Mẹ mất năm 2007, 2 năm sau bố cũng ra đi, khiến 6 anh chị em của Nguyễn Huy Chiến (Hà Tĩnh) sau giờ học lại phải đi làm để kiếm sống. Nhờ nỗ lực vươn lên, Chiến vừa trở thành tân sinh viên của ĐH Điện lực Hà Nội.', 'Sinh-vien-ngheo.jpg', 22, '<p class=\"Title\">Những t&acirc;n sinh vi&ecirc;n mồ c&ocirc;i, hiếu học</p>\r\n<p class=\"Lead\">Mẹ mất năm 2007, 2 năm sau bố cũng ra đi, khiến 6 anh chị em của Nguyễn Huy Chiến (H&agrave; Tĩnh) sau giờ học lại phải đi l&agrave;m để kiếm sống. Nhờ nỗ lực vươn l&ecirc;n, Chiến vừa trở th&agrave;nh t&acirc;n sinh vi&ecirc;n của ĐH Điện lực H&agrave; Nội.</p>\r\n<p class=\"Normal\">Mồ c&ocirc; cả cha lẫn mẹ, Nguyễn Th&agrave;nh Sơn v&agrave; em g&aacute;i (qu&ecirc; H&agrave; Tĩnh) lớn l&ecirc;n nhờ sự chăm s&oacute;c của &ocirc;ng b&agrave; ngoại đ&atilde; ngo&agrave;i 70 tuổi. Cả gia đ&igrave;nh sống nhờ v&agrave;o mấy s&agrave;o ruộng n&ecirc;n cuộc sống gặp nhiều kh&oacute; khăn, vất vả. Hiểu được điều đ&oacute;, cộng với ước mơ trở th&agrave;nh lập tr&igrave;nh vi&ecirc;n giỏi, Sơn đ&atilde; cố gắng học tập tốt v&agrave; đỗ v&agrave;o khoa C&ocirc;ng nghệ Th&ocirc;ng tin (ĐH Thủy lợi) trong kỳ tuyển sinh vừa qua.</p>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, niềm hạnh ph&uacute;c kh&ocirc;ng trọn vẹn v&igrave; hai &ocirc;ng b&agrave; ở tuổi gần đất xa trời v&agrave; cậu t&acirc;n sinh vi&ecirc;n đang phải đối mặt với g&aacute;nh nặng cơm &aacute;o gạo tiền khi Sơn l&ecirc;n đường ra H&agrave; Nội học tập, nu&ocirc;i dưỡng ước mơ trở th&agrave;nh lập tr&igrave;nh vi&ecirc;n.</p>\r\n<p class=\"Normal\">Cũng mồ c&ocirc;i từ nhỏ, cuộc sống của Đỗ Mạnh Tiến (Thanh H&oacute;a) dựa chủ yếu v&agrave;o sự gi&uacute;p đỡ, cưu mang của họ h&agrave;ng v&agrave; người th&acirc;n. Kh&ocirc;ng đầu h&agrave;ng trước số phận, Tiến đ&atilde; nỗ lực học tập v&agrave; thi đỗ v&agrave;o khoa C&ocirc;ng nghệ Th&ocirc;ng tin (ĐH Thủy lợi). Cậu t&acirc;n sinh vi&ecirc;n cho hay, sẽ cố gắng học thật tốt để kh&ocirc;ng phụ c&ocirc;ng chăm s&oacute;c của mọi người, để bố mẹ em y&ecirc;n l&ograve;ng, đồng thời để cuộc sống sau n&agrave;y đỡ vất vả.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1A/A8/Sinh-vien-ngheo.jpg\" alt=\"Ảnh: Tiến Dũng.\" width=\"480\" height=\"318\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c t&acirc;n sinh vi&ecirc;n chia sẻ ho&agrave;n cảnh kh&oacute; khăn của m&igrave;nh. Ảnh:&nbsp;<em>Tiến Dũng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Gia đ&igrave;nh c&oacute; 6 anh chị em, mẹ mất năm 2007, 2 năm sau bố cũng ra đi, tất cả anh chị em của Nguyễn Huy Chiến (H&agrave; Tĩnh) đều đang trong độ tuổi đi học, chưa c&oacute; việc l&agrave;m. Những buổi kh&ocirc;ng đi học, c&aacute;c anh chị em đều phải đi l&agrave;m để kiếm sống v&agrave; chăm s&oacute;c đứa em bị dị tật bẩm sinh. Cuộc sống của gia đ&igrave;nh cậu t&acirc;n sinh vi&ecirc;n ng&agrave;nh Hệ thống điện - ĐH Điện lực hiện chủ yếu nhờ v&agrave;o sự gi&uacute;p đỡ của b&agrave; con l&agrave;ng x&oacute;m n&ecirc;n ho&agrave;n cảnh v&ocirc; c&ugrave;ng kh&oacute; khăn.</p>\r\n<p class=\"Normal\">Mồ c&ocirc;i cha mẹ từ khi c&ograve;n nhỏ, Đinh Văn Ngọc (Cao Bằng) được nu&ocirc;i dưỡng v&agrave; học tập tại Trung t&acirc;m Bảo trợ x&atilde; hội tỉnh. D&ugrave; ho&agrave;n cảnh kh&oacute; khăn nhưng Ngọc vẫn nỗ lực v&agrave; thi đỗ ng&agrave;nh Chăn nu&ocirc;i của ĐH Th&aacute;i Nguy&ecirc;n.</p>\r\n<p class=\"Normal\">Kh&ocirc;ng c&oacute; cha, mẹ t&acirc;m thần bất ổn, l&agrave;m việc tự do thu nhập thấp, n&ecirc;n năm 10 tuổi, B&ugrave;i Văn Hải (Từ Li&ecirc;m, H&agrave; Nội) được đưa v&agrave;o nu&ocirc;i dưỡng trong l&agrave;ng trẻ mồ c&ocirc;i Birla. Nhờ cố gắng học tập, 8 năm sau Hải trở th&agrave;nh cậu học sinh duy nhất của trường đỗ đại học năm 2009- 2010. Hiện, cậu sinh vi&ecirc;n ng&agrave;nh Cơ kh&iacute; (ĐH Giao th&ocirc;ng Vận tải) trở về nh&agrave; để tiện chăm s&oacute;c &ocirc;ng b&agrave; ngoại tuổi đ&atilde; cao.</p>\r\n<p class=\"Normal\">Vắng sự chăm s&oacute;c của cha mẹ từ khi c&ograve;n nhỏ nhưng 4 anh chị em của Nguyễn Đức Hưng (Thanh H&oacute;a) vẫn đ&ugrave;m bọc, nu&ocirc;i nhau kh&ocirc;n lớn. Nỗ lực n&agrave;y đ&atilde; gi&uacute;p Hưng được 21 điểm v&agrave; đỗ v&agrave;o ng&agrave;nh B&aacute;c sĩ Đa khoa của ĐH Y H&agrave; Nội.</p>\r\n<p class=\"Normal\">May mắn hơn nhiều bạn kh&aacute;c, d&ugrave; thiếu bố nhưng Nguyễn Mộng Dung vẫn c&ograve;n c&oacute; mẹ v&agrave; &ocirc;ng b&agrave; ngoại. Ng&agrave;y ng&agrave;y người mẹ bị bệnh v&agrave; dị tật bẩm sinh miệt m&agrave;i ngồi b&ecirc;n chiếc m&aacute;y kh&acirc;u kiếm ch&uacute;t tiền trang trải cuộc sống cho gia đ&igrave;nh 4 người. Tuy nhi&ecirc;n, gần đ&acirc;y mẹ Dung gần như kh&ocirc;ng đi lại được, vận động cũng kh&oacute; khăn khiến kinh tế gia đ&igrave;nh c&agrave;ng th&ecirc;m eo hẹp.</p>\r\n<p class=\"Normal\">Để phụ gi&uacute;p mẹ v&agrave; &ocirc;ng b&agrave; ngoại gi&agrave; yếu, c&ocirc; t&acirc;n sinh vi&ecirc;n ng&agrave;nh Sư phạm Địa l&yacute; (ĐH Th&aacute;i Nguy&ecirc;n) vừa đi học vừa đi l&agrave;m th&ecirc;m để kiếm tiền ăn học.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"494\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Normal\" bgcolor=\"#dfdfdf\">Tr&ecirc;n đ&acirc;y l&agrave; 7 trong số 101 t&acirc;n sinh vi&ecirc;n ph&iacute;a Bắc c&oacute; ho&agrave;n cảnh kh&oacute; khăn vừa được b&aacute;o Tuổi trẻ TP HCM c&ugrave;ng ng&acirc;n h&agrave;ng HSBC trao học bổng \"Tiếp sức đến trường\" (trị gi&aacute; 5 triệu đồng một suất). Năm nay, 1.000 học sinh, sinh vi&ecirc;n sẽ được nhận học bổng \"Tiếp sức đến trường\".</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tiến Dũng</strong></p>', 1, 207, 0, 1, NULL, NULL),
(78, '130.000 học sinh vùng lũ trở lại trường  ', '130.000-Hoc-Sinh-Vung-Lu-Tro-Lai-Truong', 'Hôm nay gần 100.000 học sinh ở Quảng Bình và hơn 30.000 em ở huyện Hương Khê (Hà Tĩnh) đã đi học trở lại sau một tuần nghỉ tránh lũ. Hiện nơi này vẫn còn 24.000 em chưa thể đến lớp do trường hư hỏng quá nặng.', 'truong.jpg', 22, '<p class=\"Title\">130.000 học sinh v&ugrave;ng lũ trở lại trường</p>\r\n<p class=\"Lead\">H&ocirc;m nay gần 100.000 học sinh ở Quảng B&igrave;nh v&agrave; hơn 30.000 em ở huyện Hương Kh&ecirc; (H&agrave; Tĩnh) đ&atilde; đi học trở lại sau một tuần nghỉ tr&aacute;nh lũ. Hiện nơi n&agrave;y vẫn c&ograve;n 24.000 em chưa thể đến lớp do trường hư hỏng qu&aacute; nặng.</p>\r\n<p class=\"Normal\">Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo Quảng B&igrave;nh cho biết, trong đợt lũ vừa qua, to&agrave;n bộ trường học của tỉnh bị ngập, 5 em bị lũ cuốn chết, một số em bị thương. Khi nước r&uacute;t, c&aacute;c trường đ&atilde; huy động học sinh, thanh ni&ecirc;n t&igrave;nh nguyện dọn dẹp b&ugrave;n đất, lau dọn b&agrave;n ghế, lớp học&hellip; Đến s&aacute;ng 12/10, gần 100.000 học sinh c&aacute;c cấp bắt đầu đi học trở lại.</p>\r\n<p class=\"Normal\">Mặc d&ugrave; vậy, hiện nay Quảng B&igrave;nh c&ograve;n hơn 50 trường với 14.000 em, chủ yếu l&agrave; học sinh mầm non v&agrave; tiểu học vẫn chưa thể đến lớp do trường bị hư hỏng qu&aacute; nặng, cần thời gian sửa chữa.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/17/E9/truong.jpg\" alt=\"\" width=\"495\" height=\"337\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Học sinh v&ugrave;ng rốn lũ x&atilde; H&agrave; Linh, huyện Hương Kh&ecirc;, đến lớp trong ngập ngụa b&ugrave;n đất. Ảnh:&nbsp;<em>Nguy&ecirc;n Khoa.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Tại rốn lũ Hương Kh&ecirc; (H&agrave; Tĩnh), sau một tuần nghỉ học, từ h&ocirc;m qua, hơn 30.000 học sinh THPT của huyện n&agrave;y đ&atilde; trở lại trường. Hiện c&ograve;n gần 10.000 em mầm non v&agrave; tiểu học kh&aacute;c vẫn chưa thể đến lớp.</p>\r\n<p class=\"Normal\">Lũ r&uacute;t, song ng&agrave;nh gi&aacute;o dục hai tỉnh H&agrave; Tĩnh, Quảng B&igrave;nh đang gặp rất nhiều kh&oacute; khăn. Do bị ng&acirc;m nước qu&aacute; l&acirc;u, trường lớp, b&agrave;n ghế, sổ s&aacute;ch, s&aacute;ch gi&aacute;o khoa, thiết bị dạy học, tủ hồ sơ, nh&agrave; c&ocirc;ng vụ của gi&aacute;o vi&ecirc;n&hellip; đều bị hư hỏng nghi&ecirc;m trọng. Ước t&iacute;nh, đợt lũ đ&atilde; khiến ng&agrave;nh gi&aacute;o dục Quảng B&igrave;nh thiệt hại 84 tỷ đồng, H&agrave; Tĩnh hơn 50 tỷ đồng...</p>\r\n<p class=\"Normal\">Điều khiến ng&agrave;nh gi&aacute;o dục hai tỉnh lo ngại nhất hiện nay l&agrave; t&igrave;nh trạng học sinh bỏ học. Sau lũ h&agrave;ng chục ngh&igrave;n gia đ&igrave;nh đ&atilde; bị mất nh&agrave; cửa, t&agrave;i sản v&agrave; kế sinh nhai, việc cho con đến trường trở n&ecirc;n v&ocirc; c&ugrave;ng kh&oacute; khăn.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#fff2e6\">\r\n<tbody>\r\n<tr>\r\n<td>Theo Ban chỉ đạo ph&ograve;ng chống lụt b&atilde;o trung ương, đợt mưa lũ từ ng&agrave;y 1 đến 5/10 tại miền Trung đ&atilde; cướp đi 66 sinh mạng, l&agrave;m 18 người mất t&iacute;ch, thiệt hại vật chất ước t&iacute;nh 2.750 tỷ đồng. Trong đ&oacute;, ri&ecirc;ng Quảng B&igrave;nh c&oacute; 45 người chết, 16 người mất t&iacute;ch, thiệt hại 1.580 tỷ đồng; H&agrave; Tĩnh c&oacute; 12 người chết, thiệt hại 845 tỷ đồng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nguy&ecirc;n Khoa</strong></p>', 1, 2106, 0, 1, NULL, NULL),
(79, 'Chính phủ đầu tư 200 triệu USD cho ĐH Việt Đức', 'Chinh-Phu-Dau-Tu-200-Trieu-Usd-Cho-Dh-Viet-Duc', 'Sáng 6/10, Hiệu trưởng ĐH Việt Đức Wolf Rieck công bố số tiền khởi điểm mà chính phủ Việt Nam đầu tư cho trường là 200 triệu USD.', 'vietduc1.jpg', 22, '<p class=\"Title\">Ch&iacute;nh phủ đầu tư 200 triệu USD cho ĐH Việt Đức</p>\r\n<p class=\"Lead\">S&aacute;ng 6/10, Hiệu trưởng ĐH Việt Đức Wolf Rieck c&ocirc;ng bố số tiền khởi điểm m&agrave; ch&iacute;nh phủ Việt Nam đầu tư cho trường l&agrave; 200 triệu USD.</p>\r\n<p class=\"Normal\">Hiệu trưởng Rieck n&oacute;i: \"Số tiền n&agrave;y ho&agrave;n to&agrave;n do ch&iacute;nh phủ Việt Nam chịu tr&aacute;ch nhiệm. Về ph&iacute;a Đức sẽ kh&ocirc;ng c&oacute; mối li&ecirc;n quan n&agrave;o đến khoản tiền n&agrave;y m&agrave; chỉ hỗ trợ về đội ngũ giảng vi&ecirc;n cũng như chương tr&igrave;nh đ&agrave;o tạo cho ĐH Việt Đức\".</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/14/13/vietduc1.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"377\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Giảng vi&ecirc;n, v&agrave; đo&agrave;n đại biểu bang Hessen tham quan ph&ograve;ng nghi&ecirc;n cứu của sinh vi&ecirc;n Việt Đức. Ảnh:&nbsp;<em>Hải Duy&ecirc;n</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trong tổng số tiền đầu tư c&oacute; 180 triệu USD do Ch&iacute;nh phủ Việt Nam vay từ ng&acirc;n h&agrave;ng thế giới. 150 triệu USD trong số tiền vay kh&ocirc;ng cần phải trả l&atilde;i v&agrave; được trả dần trong v&ograve;ng 40 năm.</p>\r\n<p class=\"Normal\">Một nửa số tiền đầu tư sẽ được sử dụng v&agrave;o việc x&acirc;y dựng khu&ocirc;n vi&ecirc;n trường tại tỉnh B&igrave;nh Dương. Khoảng 50 triệu USD d&ugrave;ng để trang bị c&ocirc;ng cụ nghi&ecirc;n cứu, ph&ograve;ng m&aacute;y, thiết bị. Phần tiền c&ograve;n lại d&ugrave;ng để đ&agrave;o tạo v&agrave; ph&aacute;t triển đội ngũ giảng vi&ecirc;n, nh&acirc;n vi&ecirc;n trong trường.</p>\r\n<p class=\"Normal\">Từ những năm đầu th&agrave;nh lập đến nay, ĐH Việt Đức hoạt động với sự hỗ trợ của Ch&iacute;nh phủ Cộng h&ograve;a li&ecirc;n bang Đức, đặc biệt l&agrave; ch&iacute;nh quyền bang Hessen. Gần đ&acirc;y, bang n&agrave;y hỗ trợ x&acirc;y dựng chương tr&igrave;nh trọng điểm \"Trung t&acirc;m nghi&ecirc;n cứu\", gi&uacute;p c&aacute;c nghi&ecirc;n cứu sinh theo học bậc tiến sĩ của nước Đức về c&aacute;c lĩnh vực giao th&ocirc;ng, thủy lợi, m&ocirc;i trường.</p>\r\n<p class=\"Normal\">Đến nay, ĐH Việt Đức c&oacute; 200 sinh vi&ecirc;n, được giảng dạy bằng tiếng Anh, đồng thời học th&ecirc;m tiếng Đức. Hiện trường c&oacute; 30 giảng vi&ecirc;n bao gồm c&aacute;c gi&aacute;o sư, tiến sĩ của Đức. Trong tương lai c&aacute;c giảng vi&ecirc;n của Việt Nam sẽ được gửi đi học tại Đức v&agrave; dần dần thay thế cho c&aacute;c giảng vi&ecirc;n nước ngo&agrave;i.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/14/13/vietduc2.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"380\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Sinh vi&ecirc;n Việt Đức trong giờ học thực h&agrave;nh. Ảnh: Hải Duy&ecirc;n.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">ĐH Việt Đức được th&agrave;nh lập từ năm 2008, dựa tr&ecirc;n sự k&yacute; kết hợp t&aacute;c của ch&iacute;nh phủ hai nước Việt - Đức, l&agrave; một đại học c&ocirc;ng lập Việt Nam theo chuẩn quốc tế. Trường được x&acirc;y dựng theo m&ocirc; h&igrave;nh quản l&yacute; v&agrave; giảng dạy của Đức, l&agrave; m&ocirc; h&igrave;nh th&iacute; nghiệm cho c&aacute;c trường đại học của Việt Nam trong tương lai.</p>\r\n<p class=\"Normal\">Dự kiến trong th&aacute;ng 11, ch&iacute;nh phủ hai b&ecirc;n sẽ đ&agrave;m ph&aacute;n với Hội đồng nh&agrave; trường về việc mở th&ecirc;m 2 chuy&ecirc;n ng&agrave;nh mới C&ocirc;ng nghệ th&ocirc;ng tin v&agrave; Quản trị t&agrave;i ch&iacute;nh. Trường đặt mục ti&ecirc;u tuyển 1.000 sinh vi&ecirc;n v&agrave;o năm 2014.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n</strong></p>', 1, 873, 0, 1, NULL, NULL),
(80, 'Tuyển 80 chỉ tiêu liên thông đại học ngành tiếng Anh', 'Tuyen-80-Chi-Tieu-Lien-Thong-Dai-Hoc-Nganh-Tieng-Anh', 'Đại học Hoa Sen công bố 80 chỉ tiêu ngành tiếng Anh (chuyên ngành Anh văn thương mại) dành cho chương trình liên thông từ cao đẳng lên đại học khóa 10.2 của niên học 2010 - 2011.', '1509_400280.jpg', 22, '<p class=\"Title\">Tuyển 80 chỉ ti&ecirc;u li&ecirc;n th&ocirc;ng đại học ng&agrave;nh tiếng Anh</p>\r\n<p class=\"Lead\">Đại học Hoa Sen c&ocirc;ng bố 80 chỉ ti&ecirc;u ng&agrave;nh tiếng Anh (chuy&ecirc;n ng&agrave;nh Anh văn thương mại) d&agrave;nh cho chương tr&igrave;nh li&ecirc;n th&ocirc;ng từ cao đẳng l&ecirc;n đại học kh&oacute;a 10.2 của ni&ecirc;n học 2010 - 2011.</p>\r\n<p class=\"Normal\">Chương tr&igrave;nh d&agrave;nh cho c&aacute;c bạn đ&atilde; c&oacute; bằng cao đẳng ch&iacute;nh quy c&ugrave;ng ng&agrave;nh đ&agrave;o tạo, th&acirc;m ni&ecirc;n c&ocirc;ng t&aacute;c từ một năm trở l&ecirc;n t&iacute;nh đến ng&agrave;y dự thi. Sinh vi&ecirc;n tốt nghiệp loại kh&aacute;, giỏi được miễn quy định th&acirc;m ni&ecirc;n c&ocirc;ng t&aacute;c.</p>\r\n<p class=\"Normal\">Chương tr&igrave;nh học theo lộ tr&igrave;nh học chế t&iacute;n chỉ k&eacute;o d&agrave;i 3-4 học kỳ, thời gian linh động (c&oacute; thể vừa l&agrave;m vừa học). Giảng vi&ecirc;n được đ&agrave;o tạo từ nước ngo&agrave;i, học h&agrave;m từ thạc sĩ trở l&ecirc;n, c&oacute; kỹ năng sư phạm lẫn kinh nghiệm thực tế. Ngo&agrave;i ra, chương tr&igrave;nh cũng hỗ trợ vay vốn học tập v&agrave; tạo cơ hội việc l&agrave;m sau khi ra trường.</p>\r\n<p class=\"Normal\">Sinh vi&ecirc;n tốt nghiệp được nhận bằng cử nh&acirc;n tiếng Anh, nằm trong hệ thống văn bằng quốc gia, c&oacute; thể học l&ecirc;n c&aacute;c bậc cao hơn hoặc chuyển tiếp sang chương tr&igrave;nh cử nh&acirc;n kinh doanh quốc tế (hợp t&aacute;c giữa ĐH Hoa Sen v&agrave; ĐH Paris Est &ndash; Ph&aacute;p) để nhận bằng gi&aacute; trị thuộc chuẩn LMD (Licence &ndash; Master &ndash; Doctorat), c&ocirc;ng nhận khắp ch&acirc;u &Acirc;u.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/06/3B/1509_400280.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"280\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Thời gian nhận hồ sơ dự tuyển của kỳ thi li&ecirc;n th&ocirc;ng kh&oacute;a 10.2 k&eacute;o d&agrave;i đến hết ng&agrave;y 20/11. Th&iacute; sinh phải tham dự thi tuyển sinh đầu v&agrave;o với hai m&ocirc;n, bao gồm Anh văn cơ sở (90 ph&uacute;t) v&agrave; Anh văn chuy&ecirc;n ng&agrave;nh kinh doanh (120 ph&uacute;t) v&agrave;o ng&agrave;y thứ bảy (27/11). Để tạo nhiều cơ hội cho th&iacute; sinh, đại học Hoa Sen sẽ tổ chức lớp &ocirc;n tập v&agrave;o c&aacute;c buổi tối trong tuần v&agrave; ng&agrave;y thứ bảy, chủ nhật. Hạn ch&oacute;t nhận đăng k&yacute; lớp &ocirc;n tập l&agrave; ng&agrave;y 25/9.</p>\r\n<p class=\"Normal\">Th&ocirc;ng tin chi tiết, li&ecirc;n hệ ph&ograve;ng tuyển sinh, ĐH Hoa Sen &ndash; 93 Cao Thắng, quận 3, TP HCM; điện thoại (08) 3830.1877 (số nội bộ 131,154) hoặc tr&ecirc;n website<a class=\"Normal\" href=\"http://www.hoasen.edu.vn/\">http://www.hoasen.edu.vn/</a>.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"489\" align=\"center\" bgcolor=\"#dfdfdf\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Normal\">Hội thảo giới thiệu chương tr&igrave;nh li&ecirc;n th&ocirc;ng cao đẳng - đại học 2010 (các ngành: quản trị kinh doanh, k&ecirc;́ toán, Anh văn thương mại, c&ocirc;ng ngh&ecirc;̣ th&ocirc;ng tin và Quản trị Du lịch Nh&agrave; h&agrave;ng Kh&aacute;ch sạn) được tổ chức v&agrave;o thứ tư (ng&agrave;y 22/9) tại 93 Cao Thắng, quận 3, TP HCM. Đăng k&yacute; tại&nbsp;<a class=\"Normal\" href=\"mailto:nntanh@hoasen.edu.vn\">nntanh@hoasen.edu.vn</a>&nbsp;hoặc điện thoại: 08 3830 1877 Ext. 114, 141, 154.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Đại học Hoa Sen</em>)</p>', 1, 2829, 0, 1, NULL, NULL),
(81, 'Ngô Bảo Châu: Toán học cần kết hợp các khoa học khác', 'Ngo-Bao-Chau:-Toan-Hoc-Can-Ket-Hop-Cac-Khoa-Hoc-Khac', 'GS Ngô Bảo Châu đề xuất cần cơ chế đặc biệt cả về điều hành và kinh phí cho Viện cao cấp Toán, và cho biết hằng năm anh sẽ về nước làm việc vào tháng 6 đến tháng 8.', 'NBC1.jpg', 22, '<p class=\"Title\">Ng&ocirc; Bảo Ch&acirc;u: To&aacute;n học cần kết hợp c&aacute;c khoa học kh&aacute;c</p>\r\n<p class=\"Lead\">GS Ng&ocirc; Bảo Ch&acirc;u đề xuất cần cơ chế đặc biệt cả về điều h&agrave;nh v&agrave; kinh ph&iacute; cho Viện cao cấp To&aacute;n, v&agrave; cho biết hằng năm anh sẽ về nước l&agrave;m việc v&agrave;o th&aacute;ng 6 đến th&aacute;ng 8.</p>\r\n<p class=\"Normal\">S&aacute;ng 1/9 Viện To&aacute;n học tổ chức buổi gặp gỡ một số ph&oacute;ng vi&ecirc;n để chia sẻ kế hoạch th&agrave;nh lập Viện Nghi&ecirc;n cứu cao cấp về To&aacute;n, với sự tham dự của GS Ng&ocirc; Việt Trung - Viện trưởng Viện To&aacute;n học, GS L&ecirc; Tuấn Hoa - Viện ph&oacute; ki&ecirc;m Chủ tịch Hội To&aacute;n học, GS Ng&ocirc; Bảo Ch&acirc;u c&ugrave;ng nhiều nh&agrave; to&aacute;n học kh&aacute;c.</p>\r\n<p class=\"Normal\">Theo GS Ng&ocirc; Việt Trung, Viện To&aacute;n học hiện nằm trong Viện Khoa học v&agrave; C&ocirc;ng nghệ Việt Nam - một đơn vị h&agrave;nh ch&iacute;nh sự nghiệp n&ecirc;n phải hoạt động v&agrave; thực hiện theo c&aacute;c quy định quản l&yacute; hiện h&agrave;nh, c&oacute; nhiều r&agrave;ng buộc. Viện nghi&ecirc;n cứu cao cấp về To&aacute;n học cần c&oacute; cơ chế đặc biệt, hoạt động theo quy chế đặc th&ugrave;, với cơ cấu điều h&agrave;nh về chuy&ecirc;n m&ocirc;n kh&aacute;c hơn.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/FF/2F/NBC1.jpg\" border=\"1\" alt=\"Ch&uacute; th&iacute;ch ảnh:\" width=\"480\" height=\"331\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Viện ph&oacute; Viện To&aacute;n học L&ecirc; Tuấn Hoa (giơ tay). Ảnh:&nbsp;<em>Phương L&ecirc;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">GS L&ecirc; Tuấn Hoa cho hay, Viện To&aacute;n c&oacute; đội ngũ c&aacute;n bộ nghi&ecirc;n cứu l&agrave;m việc thường xuy&ecirc;n, được trả lương từ ng&acirc;n s&aacute;ch. C&ograve;n ở Viện To&aacute;n cao cấp, c&aacute;c nh&agrave; nghi&ecirc;n cứu, giảng vi&ecirc;n l&agrave;m việc tại Viện sẽ kh&ocirc;ng được nhận lương m&agrave; nhận một khoản t&agrave;i trợ nghi&ecirc;n cứu. Thay v&igrave; đi c&ocirc;ng t&aacute;c hay nghi&ecirc;n cứu ở nước ngo&agrave;i, c&aacute;c nh&agrave; to&aacute;n học c&oacute; thể đến đ&acirc;y nghi&ecirc;n cứu.</p>\r\n<p class=\"Normal\">Cho rằng chủ trương th&agrave;nh lập Viện nghi&ecirc;n cứu To&aacute;n cao cấp l&agrave; m&ocirc; h&igrave;nh mới ở Việt Nam nhưng đ&atilde; được thử nghiệm v&agrave; th&agrave;nh c&ocirc;ng ở nhiều nước, GS Ng&ocirc; Bảo Ch&acirc;u mong mỏi, Viện sẽ c&oacute; nhiệm vụ quan trọng l&agrave; chấn hưng việc nghi&ecirc;n cứu to&aacute;n, tạo ra m&ocirc;i trường, kh&ocirc;ng gian nghi&ecirc;n cứu mới cho c&aacute;c nh&agrave; to&aacute;n học.</p>\r\n<p class=\"Normal\">\"Những nh&agrave; to&aacute;n học muốn ti&ecirc;n phong thử nghiệm trước m&ocirc; h&igrave;nh Viện nghi&ecirc;n cứu n&agrave;y kh&ocirc;ng phải v&igrave; muốn được d&agrave;nh ri&ecirc;ng sự ưu đ&atilde;i, t&agrave;i trợ m&agrave; v&igrave; ch&uacute;ng t&ocirc;i x&eacute;t thấy đ&atilde; c&oacute; những điều kiện thuận lợi để thực hiện\", GS Ch&acirc;u n&oacute;i.</p>\r\n<p class=\"Normal\">Cũng theo GS Ch&acirc;u, Viện sẽ c&oacute; cơ chế hoạt động đặc biệt, độc lập v&agrave; chủ yếu do l&atilde;nh đạo Viện (3-5 người) điều h&agrave;nh, với sự tham gia của c&aacute;c nh&agrave; to&aacute;n học nước ngo&agrave;i v&agrave; nh&agrave; to&aacute;n học Việt Nam ở nước ngo&agrave;i. B&ecirc;n cạnh việc phục vụ chủ yếu cho c&aacute;c nh&agrave; to&aacute;n học trong nước đang giảng dạy ở c&aacute;c trường đại học, Viện cũng sẽ đ&oacute;n cả những người nghi&ecirc;n cứu về Vật l&yacute;, Sinh học&hellip; c&oacute; đề t&agrave;i nghi&ecirc;n cứu li&ecirc;n quan đến to&aacute;n.</p>\r\n<p class=\"Normal\">&ldquo;Động lực ch&iacute;nh để ph&aacute;t triển to&aacute;n học cũng như c&aacute;c ng&agrave;nh khoa học kh&aacute;c l&agrave; tạo ra sự kết hợp giữa c&aacute;c nh&agrave; khoa học với nhau. V&igrave; thế tư tưởng then chốt của Viện Nghi&ecirc;n cứu cao cấp về to&aacute;n l&agrave; tạo ra s&acirc;n chơi để c&aacute;c nh&agrave; to&aacute;n học, nh&agrave; nghi&ecirc;n cứu trong v&agrave; ngo&agrave;i nước c&oacute; thể l&agrave;m việc với nhau&rdquo;, GS Ng&ocirc; Bảo Ch&acirc;u nhấn mạnh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/FF/2F/NBC2.jpg\" border=\"1\" alt=\"Ch&uacute; th&iacute;ch ảnh:\" width=\"480\" height=\"331\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">GS Ng&ocirc; Bảo Ch&acirc;u. Ảnh:&nbsp;<em>Phương L&ecirc;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Đề cập về dự định trong thời gian tới, gi&aacute;o sư của ĐH Chicago cho biết hằng năm sẽ d&agrave;nh thời gian từ th&aacute;ng 6 đến th&aacute;ng 8 (nghỉ h&egrave;) về nước l&agrave;m việc. C&ograve;n trong năm học, b&ecirc;n cạnh c&ocirc;ng việc ch&iacute;nh ở ĐH Chicago, gi&aacute;o sư Ch&acirc;u cũng sẽ thu xếp để về l&agrave;m việc ở Việt Nam nhiều hơn, thường xuy&ecirc;n hơn.</p>\r\n<p class=\"Normal\">Theo GS L&ecirc; Tuấn Hoa, kh&ocirc;ng thể th&agrave;nh lập được Viện Nghi&ecirc;n cứu cao cấp về To&aacute;n nếu Ch&iacute;nh phủ kh&ocirc;ng c&oacute; sự đầu tư. &ldquo;Viện KIAS của H&agrave;n Quốc c&oacute; kinh ph&iacute; hoạt động l&agrave; 18 triệu USD một năm, trong đ&oacute; 15 triệu USD do Ch&iacute;nh phủ cấp, 3 triệu USD c&ograve;n lại do Viện tự vận động t&agrave;i trợ&rdquo;, &ocirc;ng Hoa dẫn chứng.</p>\r\n<p class=\"Normal\">Tương tự, GS Ng&ocirc; Bảo Ch&acirc;u cũng dẫn chứng, Viện Nghi&ecirc;n cứu cao cấp Princeton nơi &ocirc;ng từng l&agrave;m việc c&oacute; khoản vốn l&ecirc;n tới 500 triệu USD v&agrave; được d&ugrave;ng cho vay để lấy l&atilde;i l&agrave;m kinh ph&iacute; hoạt động. V&agrave; mỗi năm c&oacute; h&agrave;ng trăm nh&agrave; khoa học đến đ&acirc;y l&agrave;m việc. &ldquo;Ch&uacute;ng ta mới th&agrave;nh lập n&ecirc;n kh&ocirc;ng thể l&agrave;m giống như vậy, m&agrave; cần c&oacute; một cơ chế đặc biệt cho hoạt động của Viện Nghi&ecirc;n cứu cao cấp về To&aacute;n&rdquo;.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#f2f9f9\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p class=\"Normal\">Chiều 1/9, Chủ tịch nước Nguyễn Minh Triết đ&atilde; tiếp th&acirc;n mật GS Ng&ocirc; Bảo Ch&acirc;u v&agrave; gia đ&igrave;nh tại Phủ Chủ tịch. Tham dự buổi gặp mặt c&ograve;n c&oacute; Thứ trưởng GD&amp;ĐT B&ugrave;i Văn Ga, Thứ trưởng KH&amp;CN L&ecirc; Đ&igrave;nh Tiến, l&atilde;nh đạo Viện To&aacute;n học, ĐH Quốc gia H&agrave; Nội&hellip;</p>\r\n<p class=\"Normal\">Chủ tịch nước nhấn mạnh, sự kiện GS Ng&ocirc; Bảo Ch&acirc;u được nhận giải thưởng Fields thực sự l&agrave; niềm vinh dự lớn, niềm tự h&agrave;o với đất nước v&agrave; con người Việt Nam.</p>\r\n<p class=\"Normal\">Sau khi biểu dương th&agrave;nh t&iacute;ch của ng&agrave;nh gi&aacute;o dục v&agrave; lắng nghe, trao đổi &yacute; kiến với l&atilde;nh đạo c&aacute;c bộ, ng&agrave;nh, Chủ tịch nước cũng hoan ngh&ecirc;nh gi&aacute;o sư Ng&ocirc; Bảo Ch&acirc;u, d&ugrave; th&agrave;nh đạt nhưng vẫn b&igrave;nh dị, khi&ecirc;m tốn v&agrave; c&oacute; những ph&aacute;t biểu được Chủ tịch nước rất quan t&acirc;m. Mong rằng, gi&aacute;o sư d&ugrave; l&agrave;m việc ở nước ngo&agrave;i nhưng nếu c&oacute; điều kiện, h&atilde;y d&agrave;nh thời gian về Việt Nam.</p>\r\n<p class=\"Normal\">Gi&aacute;o sư Ng&ocirc; Bảo Ch&acirc;u cho biết, sẽ c&ugrave;ng cộng đồng To&aacute;n học Việt Nam cố gắng trong khả năng của m&igrave;nh, để ng&agrave;nh To&aacute;n học ng&agrave;y c&agrave;ng tốt l&ecirc;n, nhất l&agrave; sau khi Thủ tướng quyết định Chương tr&igrave;nh trọng điểm Quốc gia ph&aacute;t triển to&aacute;n học đến năm 2020.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tiến Dũng</strong></p>', 1, 2869, 0, 1, NULL, NULL),
(82, 'Phát hành sách về các nhà Toán học và Ngô Bảo Châu', 'Phat-Hanh-Sach-Ve-Cac-Nha-Toan-Hoc-Va-Ngo-Bao-Chau', 'Cuốn sách đầu tiên tổng hợp thông tin đầy đủ nhất về các nhà Toán học nổi tiếng, trong đó, có 12 trang viết về giáo sư Ngô Bảo Châu sẽ được phát hành tại buổi đón tiếp GS Ngô Bảo Châu tối 29/8.', '2GS-Ngo.jpg', 22, '<p class=\"Title\">Ph&aacute;t h&agrave;nh s&aacute;ch về c&aacute;c nh&agrave; To&aacute;n học v&agrave; Ng&ocirc; Bảo Ch&acirc;u</p>\r\n<p class=\"Lead\">Cuốn s&aacute;ch đầu ti&ecirc;n tổng hợp th&ocirc;ng tin đầy đủ nhất về c&aacute;c nh&agrave; To&aacute;n học nổi tiếng, trong đ&oacute;, c&oacute; 12 trang viết về gi&aacute;o sư Ng&ocirc; Bảo Ch&acirc;u sẽ được ph&aacute;t h&agrave;nh tại buổi đ&oacute;n tiếp GS Ng&ocirc; Bảo Ch&acirc;u tối 29/8.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"right\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/FC/26/2GS-Ngo.jpg\" alt=\"\" width=\"250\" height=\"183\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">GS Ng&ocirc; Việt Trung - Viện trưởng Viện To&aacute;n học v&agrave; GS Ng&ocirc; Bảo Ch&acirc;u.Ảnh:&nbsp;<em>Nguyễn Việt Dũng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Chiều 27/8, Ph&oacute; tổng gi&aacute;m đốc NXB Gi&aacute;o dục Trần Qu&yacute; Thao cho<em>VnExpress.net</em>&nbsp;biết, ng&agrave;y 29/8 sẽ ph&aacute;t h&agrave;nh cuốn s&aacute;ch \"Kể chuyện về To&aacute;n v&agrave; c&aacute;c nh&agrave; To&aacute;n học\" của Việt Nam cũng như thế giới, d&agrave;y hơn 300 trang với những mẩu chuyện d&iacute; dỏm về c&aacute;c nh&agrave; To&aacute;n học.</p>\r\n<p class=\"Normal\">\"Đ&acirc;y l&agrave; cuốn s&aacute;ch đầu ti&ecirc;n tổng hợp th&ocirc;ng tin đầy đủ nhất về c&aacute;c nh&agrave; To&aacute;n học nổi tiếng, trong đ&oacute;, c&oacute; 12 trang viết về gi&aacute;o sư Ng&ocirc; Bảo Ch&acirc;u để tặng những người y&ecirc;u th&iacute;ch To&aacute;n v&agrave; Ng&ocirc; Bảo Ch&acirc;u\", &ocirc;ng Thao n&oacute;i.</p>\r\n<p class=\"Normal\">Cũng theo &ocirc;ng Ph&oacute; tổng gi&aacute;m đốc, cuốn s&aacute;ch n&agrave;y được GS Nguyễn Duy Tiến - giảng vi&ecirc;n ĐH Khoa học Tự nhi&ecirc;n (ĐH Quốc gia H&agrave; Nội) chuẩn bị từ 6 th&aacute;ng trước v&agrave; được GS TSKH Trần Văn Nhung (Nguy&ecirc;n Thứ trưởng Bộ GD&amp;ĐT), GS TSKH L&ecirc; Tuấn Hoa (Viện ph&oacute; Viện To&aacute;n học) viết lời giới thiệu.</p>\r\n<p class=\"Normal\">Tối 29/8, Ch&iacute;nh phủ sẽ tổ chức ch&agrave;o mừng GS Ng&ocirc; Bảo Ch&acirc;u với sự tham dự của l&atilde;nh đạo Đảng, Nh&agrave; nước, Bộ KH&amp;CN, Bộ Gi&aacute;o dục, Viện Khoa học v&agrave; C&ocirc;ng nghệ Việt Nam, Viện To&aacute;n học, ĐH Quốc gia H&agrave; Nội... Tổng số kh&aacute;ch mời tới dự chương tr&igrave;nh l&agrave; khoảng 3.000 người. V&agrave; lễ ch&agrave;o mừng sẽ được truyền h&igrave;nh trực tiếp tr&ecirc;n VTV1 từ 20h.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tiến Dũng</strong></p>', 1, 2120, 0, 1, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(83, '300 chỉ tiêu NV2 vào CĐ Nghề quốc tế', '300-Chi-Tieu-Nv2-Vao-Cd-Nghe-Quoc-Te', 'Học viện Công nghệ Thông tin Bách khoa (BKACAD) - ĐH Bách Khoa Hà Nội tuyển sinh 300 chỉ tiêu NV2 hệ Cử nhân Cao đẳng nghề quốc tế dành cho thí sinh dự thi khối A, B, D có điểm thi từ 10 trở lên.', 'Bkad.jpg', 22, '<div class=\"content\">\r\n<div>\r\n<p class=\"Title\">300 chỉ ti&ecirc;u NV2 v&agrave;o CĐ Nghề quốc tế</p>\r\n<p class=\"Lead\"><strong>Học viện C&ocirc;ng nghệ Th&ocirc;ng tin B&aacute;ch khoa (BKACAD) - ĐH B&aacute;ch Khoa H&agrave; Nội tuyển sinh 300 chỉ ti&ecirc;u NV2 hệ Cử nh&acirc;n Cao đẳng nghề quốc tế d&agrave;nh cho th&iacute; sinh dự thi khối A, B, D c&oacute; điểm thi từ 10 trở l&ecirc;n.<br /></strong></p>\r\n<p class=\"Normal\">Năm nay, được sự cho ph&eacute;p của ĐH B&aacute;ch khoa H&agrave; Nội, Học viện C&ocirc;ng nghệ Th&ocirc;ng tin B&aacute;ch khoa tuyển sinh theo h&igrave;nh thức x&eacute;t tuyển thẳng v&agrave; thi tuyển trực tiếp hệ cao đẳng nghề quốc tế, chuy&ecirc;n ng&agrave;nh Chuy&ecirc;n gia cao cấp Quản trị mạng v&agrave; Bảo mật hệ thống l&agrave;<strong>&nbsp;</strong>chương tr&igrave;nh đ&agrave;o tạo tập trung d&agrave;i hạn 3 năm.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/F6/3B/Bkad.jpg\" alt=\"Ảnh: Trần Phương.\" width=\"480\" height=\"326\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Giờ học tại CĐ Nghề B&aacute;ch khoa. Ảnh:&nbsp;<em>Trần Phương.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Kết th&uacute;c kh&oacute;a học, sinh vi&ecirc;n được nhận bằng cử nh&acirc;n CĐ Nghề B&aacute;ch khoa H&agrave; Nội, chứng chỉ của ĐH B&aacute;ch khoa H&agrave; Nội v&agrave; c&aacute;c chứng chỉ quốc tế theo ti&ecirc;u chuẩn Mỹ.</p>\r\n<p class=\"Normal\">Th&iacute; sinh dự thi khối A, B, D đạt điểm thi từ 10 đều c&oacute; thể nộp hồ sơ tham gia x&eacute;t tuyển. Thời gian nhận hồ sơ đến ng&agrave;y 30/10.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tiến Dũng</strong></p>\r\n</div>\r\n</div>', 1, 2618, 0, 1, NULL, NULL),
(84, 'Đại học đầu tiên khởi kiện vì bị trùng tên', 'Dai-Hoc-Dau-Tien-Khoi-Kien-Vi-Bi-Trung-Ten', 'Hiệu phó ĐH Đông Á (Đà Nẵng) cho biết, sau nhiều lần thương thảo bất thành, HĐQT và Ban giám hiệu đã thông qua quyết định khởi kiện ĐH Công nghệ Đông Á (Bắc Ninh) vì đã vi phạm bản quyền nhãn hiệu. ', 'dh.jpg', 22, '<p class=\"Title\">Đại học đầu ti&ecirc;n khởi kiện v&igrave; bị tr&ugrave;ng t&ecirc;n</p>\r\n<p class=\"Lead\">Hiệu ph&oacute; ĐH Đ&ocirc;ng &Aacute; (Đ&agrave; Nẵng) cho biết, sau nhiều lần thương thảo bất th&agrave;nh, HĐQT v&agrave; Ban gi&aacute;m hiệu đ&atilde; th&ocirc;ng qua quyết định khởi kiện ĐH C&ocirc;ng nghệ Đ&ocirc;ng &Aacute; (Bắc Ninh) v&igrave; đ&atilde; vi phạm bản quyền nh&atilde;n hiệu.&nbsp;</p>\r\n<p class=\"Normal\">&Ocirc;ng Đỗ Thế, Ph&oacute; hiệu trưởng cho biết, ĐH Đ&ocirc;ng &Aacute; được th&agrave;nh lập v&agrave;o năm 2009 tr&ecirc;n cơ sở Cao đẳng Đ&ocirc;ng &Aacute;. Từ th&aacute;ng 4/2005, HĐQT nh&agrave; trường đ&atilde; lập hồ sơ gửi l&ecirc;n Cục Sở hữu Tr&iacute; tuệ Việt Nam đăng k&yacute; 12 nh&atilde;n hiệu &ldquo;Đ&ocirc;ng &Aacute;&rdquo; trong nh&oacute;m dịch vụ gi&aacute;o dục, đ&agrave;o tạo. Đến nay Cục Sở hữu Tr&iacute; tuệ Việt Nam đ&atilde; cấp chứng nhận 12 nh&atilde;n hiệu, trong đ&oacute; c&oacute; hai nh&atilde;n hiệu \"ĐH Đ&ocirc;ng &Aacute;\".</p>\r\n<p class=\"Normal\">Nhưng một trường kh&aacute;c tồn tại từ năm 2008 với t&ecirc;n &ldquo;ĐH C&ocirc;ng nghệ Đ&ocirc;ng &Aacute;&rdquo; tại Bắc Ninh đ&atilde; g&acirc;y nhầm lẫn với ĐH Đ&ocirc;ng &Aacute;. Ng&agrave;y 3/8 vừa qua, khi Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo quyết định tạm ngừng tuyển sinh ĐH, CĐ, Trung học chuy&ecirc;n nghiệp năm 2010 đối với ĐH C&ocirc;ng nghệ Đ&ocirc;ng &Aacute;, th&igrave; do nhầm lẫn, h&agrave;ng ngh&igrave;n học sinh, phụ huynh c&oacute; con dự định theo học ĐH Đ&ocirc;ng &Aacute; (Đ&agrave; Nẵng) lo lắng.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/F0/10/dh.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"357\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">ĐH Đ&ocirc;ng &Aacute; đi&ecirc;u đứng trong m&ugrave;a tuyển sinh v&igrave; bị vi phạm nh&atilde;n hiệu đ&atilde; được bảo hộ. Ảnh:&nbsp;<em>Chi&ecirc;u Anh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">\"ĐH Đ&ocirc;ng &Aacute; đ&atilde; bị l&acirc;y tiếng xấu. Trường quyết định dừng mọi hoạt động, tập trung v&agrave;o xử l&yacute; th&ocirc;ng tin để trả lời đối t&aacute;c, phụ huynh v&agrave; học sinh. Ch&uacute;ng t&ocirc;i rất bức x&uacute;c v&igrave; vừa tốn thời gian, tiền bạc, nhưng lo nhất vẫn l&agrave; ảnh hưởng đến c&ocirc;ng t&aacute;c tuyển sinh năm 2010-2011 sắp tới\", &ocirc;ng Thế n&oacute;i.</p>\r\n<p class=\"Normal\">B&agrave; Nguyễn Thị Anh Đ&agrave;o, Chủ tịch HĐQT ĐH Đ&ocirc;ng &Aacute; cho biết th&ecirc;m, li&ecirc;n tiếp nhiều ng&agrave;y qua phải nhận điện thoại, giải th&iacute;ch cho đồng nghiệp, đối t&aacute;c, bạn b&egrave;, phụ huynh v&agrave; cả học sinh... trước &ldquo;th&ocirc;ng tin oan&rdquo; trường bị đ&igrave;nh chỉ tuyển sinh. \"Nhiều người c&ograve;n thương hại, chia sẻ. C&oacute; đối t&aacute;c nghi ngờ, thất vọng, phụ huynh th&igrave; lo lắng, học sinh th&igrave; chần chừ trước khi lựa chọn...\", b&agrave; Đ&agrave;o n&oacute;i.</p>\r\n<p class=\"Normal\">B&agrave; Đ&agrave;o cho biết th&ecirc;m, từ năm 2008, ngay khi biết c&oacute; trường tr&ugrave;ng t&ecirc;n, ĐH Đ&ocirc;ng &Aacute; (l&uacute;c bấy giờ l&agrave; Cao đẳng Đ&ocirc;ng &Aacute;) đ&atilde; gọi điện thoại cho l&atilde;nh đạo ĐH C&ocirc;ng nghệ Đ&ocirc;ng &Aacute; để cảnh b&aacute;o việc đặt t&ecirc;n n&agrave;y sẽ x&acirc;m hại đến quyền Sở hữu Tr&iacute; tuệ trong nh&oacute;m lĩnh vực &ldquo;dịch vụ gi&aacute;o dục, đ&agrave;o tạo&rdquo;, nhưng trường ở Bắc Ninh vẫn giữ t&ecirc;n.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/F0/10/dh4.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"359\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">B&agrave; Nguyễn Thị Anh Đ&agrave;o, Chủ tịch HĐQT ĐH Đ&ocirc;ng &Aacute; trao đổi với b&aacute;o ch&iacute;. Ảnh:&nbsp;<em>Chi&ecirc;u Anh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">L&atilde;nh đạo ĐH Đ&ocirc;ng &Aacute; cho biết, trước mắt đ&atilde; gửi đơn khiếu nại l&ecirc;n Cục sở hữu tr&iacute; tuệ, Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo, đề nghị can thiệp, buộc ĐH C&ocirc;ng nghệ Đ&ocirc;ng &Aacute; (Bắc Ninh) phải chấm dứt ngay việc sử dụng nh&atilde;n hiệu Đ&ocirc;ng &Aacute;, g&acirc;y nhầm lẫn. Bước tiếp theo, ĐH Đ&ocirc;ng &Aacute; sẽ kiện ĐH C&ocirc;ng Nghệ Đ&ocirc;ng &Aacute; ra to&agrave;, đ&ograve;i bồi thường thiệt hại về uy t&iacute;n thương hiệu, t&agrave;i sản vật chất sau khi đ&atilde; thống k&ecirc;, nhất l&agrave; sau kết quả tuyển sinh năm học 2010-2011.</p>\r\n<p class=\"Normal\">Chỉ ti&ecirc;u tuyển sinh trung b&igrave;nh mỗi năm của ĐH Đ&ocirc;ng &Aacute; 3.200-3.500 th&iacute; sinh. Hiện trường n&agrave;y c&oacute; 19 ng&agrave;nh đ&agrave;o tạo ở c&aacute;c cấp từ Trung học chuy&ecirc;n nghiệp, CĐ v&agrave; ĐH.</p>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; lần đầu ti&ecirc;n tại Việt Nam xảy ra tranh chấp nh&atilde;n hiệu quyền sở hữu tr&iacute; tuệ tr&ecirc;n lĩnh vực gi&aacute;o dục. Trước đ&acirc;y phần lớn tranh chấp trong lĩnh vực n&agrave;y l&agrave; quyền t&aacute;c giả li&ecirc;n quan đến gi&aacute;o tr&igrave;nh, b&agrave;i giảng.</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#ffebd7\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p class=\"Normal\">Theo điều 129 của Luật sở hữu tr&iacute; tuệ, c&aacute;c h&agrave;nh vi sau đ&acirc;y được thực hiện m&agrave; kh&ocirc;ng được ph&eacute;p của chủ sở hữu nh&atilde;n hiệu th&igrave; bị coi l&agrave; x&acirc;m phạm quyền đối với nh&atilde;n hiệu:</p>\r\n<p class=\"Normal\">a) Sử dụng dấu hiệu tr&ugrave;ng với nh&atilde;n hiệu được bảo hộ cho h&agrave;ng ho&aacute;, dịch vụ tr&ugrave;ng với h&agrave;ng ho&aacute;, dịch vụ thuộc danh mục đăng k&yacute; k&egrave;m theo nh&atilde;n hiệu đ&oacute;;</p>\r\n<p class=\"Normal\">b) Sử dụng dấu hiệu tr&ugrave;ng với nh&atilde;n hiệu được bảo hộ cho h&agrave;ng ho&aacute;, dịch vụ tương tự hoặc li&ecirc;n quan tới h&agrave;ng ho&aacute;, dịch vụ thuộc danh mục đăng k&yacute; k&egrave;m theo nh&atilde;n hiệu đ&oacute;, nếu việc sử dụng c&oacute; khả năng g&acirc;y nhầm lẫn về nguồn gốc h&agrave;ng ho&aacute;, dịch vụ;</p>\r\n<p class=\"Normal\">c) Sử dụng dấu hiệu tương tự với nh&atilde;n hiệu được bảo hộ cho h&agrave;ng ho&aacute;, dịch vụ tr&ugrave;ng, tương tự hoặc li&ecirc;n quan tới h&agrave;ng ho&aacute;, dịch vụ thuộc danh mục đăng k&yacute; k&egrave;m theo nh&atilde;n hiệu đ&oacute;, nếu việc sử dụng c&oacute; khả năng g&acirc;y nhầm lẫn về nguồn gốc h&agrave;ng ho&aacute;, dịch vụ;</p>\r\n<p class=\"Normal\">d) Sử dụng dấu hiệu tr&ugrave;ng hoặc tương tự với nh&atilde;n hiệu nổi tiếng hoặc dấu hiệu dưới dạng dịch nghĩa, phi&ecirc;n &acirc;m từ nh&atilde;n hiệu nổi tiếng cho h&agrave;ng ho&aacute;, dịch vụ bất kỳ, kể cả h&agrave;ng ho&aacute;, dịch vụ kh&ocirc;ng tr&ugrave;ng, kh&ocirc;ng tương tự v&agrave; kh&ocirc;ng li&ecirc;n quan tới h&agrave;ng ho&aacute;, dịch vụ thuộc danh mục h&agrave;ng ho&aacute;, dịch vụ mang nh&atilde;n hiệu nổi tiếng, nếu việc sử dụng c&oacute; khả năng g&acirc;y nhầm lẫn về nguồn gốc h&agrave;ng ho&aacute; hoặc g&acirc;y ấn tượng sai lệch về mối quan hệ giữa người sử dụng dấu hiệu đ&oacute; với chủ sở hữu nh&atilde;n hiệu nổi tiếng.</p>\r\n<p class=\"Normal\">Điều 198. Quyền tự bảo vệ</p>\r\n<p class=\"Normal\">1. Chủ thể quyền sở hữu tr&iacute; tuệ c&oacute; quyền &aacute;p dụng c&aacute;c biện ph&aacute;p sau đ&acirc;y để bảo vệ quyền sở hữu tr&iacute; tuệ của m&igrave;nh:</p>\r\n<p class=\"Normal\">a) &Aacute;p dụng biện ph&aacute;p c&ocirc;ng nghệ nhằm ngăn ngừa h&agrave;nh vi x&acirc;m phạm quyền sở hữu tr&iacute; tuệ;</p>\r\n<p class=\"Normal\">b) Y&ecirc;u cầu tổ chức, c&aacute; nh&acirc;n c&oacute; h&agrave;nh vi x&acirc;m phạm quyền sở hữu tr&iacute; tuệ phải chấm dứt h&agrave;nh vi x&acirc;m phạm, xin lỗi, cải ch&iacute;nh c&ocirc;ng khai, bồi thường thiệt hại;</p>\r\n<p class=\"Normal\">c) Y&ecirc;u cầu cơ quan nh&agrave; nước c&oacute; thẩm quyền xử l&yacute; h&agrave;nh vi x&acirc;m phạm quyền sở hữu tr&iacute; tuệ theo quy định của Luật n&agrave;y v&agrave; c&aacute;c quy định kh&aacute;c của ph&aacute;p luật c&oacute; li&ecirc;n quan;</p>\r\n<p class=\"Normal\">d) Khởi kiện ra t&ograve;a &aacute;n hoặc trọng t&agrave;i để bảo vệ quyền, lợi &iacute;ch hợp ph&aacute;p của m&igrave;nh.</p>\r\n<p class=\"Normal\">2. Tổ chức, c&aacute; nh&acirc;n bị thiệt hại do h&agrave;nh vi x&acirc;m phạm quyền sở hữu tr&iacute; tuệ hoặc ph&aacute;t hiện h&agrave;nh vi x&acirc;m phạm quyền sở hữu tr&iacute; tuệ g&acirc;y thiệt hại cho người ti&ecirc;u d&ugrave;ng hoặc cho x&atilde; hội c&oacute; quyền y&ecirc;u cầu cơ quan nh&agrave; nước c&oacute; thẩm quyền xử l&yacute; h&agrave;nh vi x&acirc;m phạm quyền sở hữu tr&iacute; tuệ theo quy định của Luật n&agrave;y v&agrave; c&aacute;c quy định kh&aacute;c của ph&aacute;p luật c&oacute; li&ecirc;n quan.</p>\r\n<p class=\"Normal\">3. Tổ chức, c&aacute; nh&acirc;n bị thiệt hại hoặc c&oacute; khả năng bị thiệt hại do h&agrave;nh vi cạnh tranh kh&ocirc;ng l&agrave;nh mạnh c&oacute; quyền y&ecirc;u cầu cơ quan nh&agrave; nước c&oacute; thẩm quyền &aacute;p dụng c&aacute;c biện ph&aacute;p d&acirc;n sự quy định tại Điều 202 của Luật n&agrave;y v&agrave; c&aacute;c biện ph&aacute;p h&agrave;nh ch&iacute;nh theo quy định của ph&aacute;p luật về cạnh tranh.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tr&agrave; Bang - Chi&ecirc;u An</strong></p>', 1, 390, 0, 1, NULL, NULL),
(85, 'Đại học đầu tiên khởi kiện vì bị trùng tên', 'Dai-Hoc-Dau-Tien-Khoi-Kien-Vi-Bi-Trung-Ten', 'Hiệu phó ĐH Đông Á (Đà Nẵng) cho biết, sau nhiều lần thương thảo bất thành, HĐQT và Ban giám hiệu đã thông qua quyết định khởi kiện ĐH Công nghệ Đông Á (Bắc Ninh) vì đã vi phạm bản quyền nhãn hiệu. ', 'dh_1.jpg', 22, '<p class=\"Title\">Đại học đầu ti&ecirc;n khởi kiện v&igrave; bị tr&ugrave;ng t&ecirc;n</p>\r\n<p class=\"Lead\">Hiệu ph&oacute; ĐH Đ&ocirc;ng &Aacute; (Đ&agrave; Nẵng) cho biết, sau nhiều lần thương thảo bất th&agrave;nh, HĐQT v&agrave; Ban gi&aacute;m hiệu đ&atilde; th&ocirc;ng qua quyết định khởi kiện ĐH C&ocirc;ng nghệ Đ&ocirc;ng &Aacute; (Bắc Ninh) v&igrave; đ&atilde; vi phạm bản quyền nh&atilde;n hiệu.&nbsp;</p>\r\n<p class=\"Normal\">&Ocirc;ng Đỗ Thế, Ph&oacute; hiệu trưởng cho biết, ĐH Đ&ocirc;ng &Aacute; được th&agrave;nh lập v&agrave;o năm 2009 tr&ecirc;n cơ sở Cao đẳng Đ&ocirc;ng &Aacute;. Từ th&aacute;ng 4/2005, HĐQT nh&agrave; trường đ&atilde; lập hồ sơ gửi l&ecirc;n Cục Sở hữu Tr&iacute; tuệ Việt Nam đăng k&yacute; 12 nh&atilde;n hiệu &ldquo;Đ&ocirc;ng &Aacute;&rdquo; trong nh&oacute;m dịch vụ gi&aacute;o dục, đ&agrave;o tạo. Đến nay Cục Sở hữu Tr&iacute; tuệ Việt Nam đ&atilde; cấp chứng nhận 12 nh&atilde;n hiệu, trong đ&oacute; c&oacute; hai nh&atilde;n hiệu \"ĐH Đ&ocirc;ng &Aacute;\".</p>\r\n<p class=\"Normal\">Nhưng một trường kh&aacute;c tồn tại từ năm 2008 với t&ecirc;n &ldquo;ĐH C&ocirc;ng nghệ Đ&ocirc;ng &Aacute;&rdquo; tại Bắc Ninh đ&atilde; g&acirc;y nhầm lẫn với ĐH Đ&ocirc;ng &Aacute;. Ng&agrave;y 3/8 vừa qua, khi Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo quyết định tạm ngừng tuyển sinh ĐH, CĐ, Trung học chuy&ecirc;n nghiệp năm 2010 đối với ĐH C&ocirc;ng nghệ Đ&ocirc;ng &Aacute;, th&igrave; do nhầm lẫn, h&agrave;ng ngh&igrave;n học sinh, phụ huynh c&oacute; con dự định theo học ĐH Đ&ocirc;ng &Aacute; (Đ&agrave; Nẵng) lo lắng.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/F0/10/dh.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"357\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">ĐH Đ&ocirc;ng &Aacute; đi&ecirc;u đứng trong m&ugrave;a tuyển sinh v&igrave; bị vi phạm nh&atilde;n hiệu đ&atilde; được bảo hộ. Ảnh:&nbsp;<em>Chi&ecirc;u Anh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">\"ĐH Đ&ocirc;ng &Aacute; đ&atilde; bị l&acirc;y tiếng xấu. Trường quyết định dừng mọi hoạt động, tập trung v&agrave;o xử l&yacute; th&ocirc;ng tin để trả lời đối t&aacute;c, phụ huynh v&agrave; học sinh. Ch&uacute;ng t&ocirc;i rất bức x&uacute;c v&igrave; vừa tốn thời gian, tiền bạc, nhưng lo nhất vẫn l&agrave; ảnh hưởng đến c&ocirc;ng t&aacute;c tuyển sinh năm 2010-2011 sắp tới\", &ocirc;ng Thế n&oacute;i.</p>\r\n<p class=\"Normal\">B&agrave; Nguyễn Thị Anh Đ&agrave;o, Chủ tịch HĐQT ĐH Đ&ocirc;ng &Aacute; cho biết th&ecirc;m, li&ecirc;n tiếp nhiều ng&agrave;y qua phải nhận điện thoại, giải th&iacute;ch cho đồng nghiệp, đối t&aacute;c, bạn b&egrave;, phụ huynh v&agrave; cả học sinh... trước &ldquo;th&ocirc;ng tin oan&rdquo; trường bị đ&igrave;nh chỉ tuyển sinh. \"Nhiều người c&ograve;n thương hại, chia sẻ. C&oacute; đối t&aacute;c nghi ngờ, thất vọng, phụ huynh th&igrave; lo lắng, học sinh th&igrave; chần chừ trước khi lựa chọn...\", b&agrave; Đ&agrave;o n&oacute;i.</p>\r\n<p class=\"Normal\">B&agrave; Đ&agrave;o cho biết th&ecirc;m, từ năm 2008, ngay khi biết c&oacute; trường tr&ugrave;ng t&ecirc;n, ĐH Đ&ocirc;ng &Aacute; (l&uacute;c bấy giờ l&agrave; Cao đẳng Đ&ocirc;ng &Aacute;) đ&atilde; gọi điện thoại cho l&atilde;nh đạo ĐH C&ocirc;ng nghệ Đ&ocirc;ng &Aacute; để cảnh b&aacute;o việc đặt t&ecirc;n n&agrave;y sẽ x&acirc;m hại đến quyền Sở hữu Tr&iacute; tuệ trong nh&oacute;m lĩnh vực &ldquo;dịch vụ gi&aacute;o dục, đ&agrave;o tạo&rdquo;, nhưng trường ở Bắc Ninh vẫn giữ t&ecirc;n.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/F0/10/dh4.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"359\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">B&agrave; Nguyễn Thị Anh Đ&agrave;o, Chủ tịch HĐQT ĐH Đ&ocirc;ng &Aacute; trao đổi với b&aacute;o ch&iacute;. Ảnh:&nbsp;<em>Chi&ecirc;u Anh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">L&atilde;nh đạo ĐH Đ&ocirc;ng &Aacute; cho biết, trước mắt đ&atilde; gửi đơn khiếu nại l&ecirc;n Cục sở hữu tr&iacute; tuệ, Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo, đề nghị can thiệp, buộc ĐH C&ocirc;ng nghệ Đ&ocirc;ng &Aacute; (Bắc Ninh) phải chấm dứt ngay việc sử dụng nh&atilde;n hiệu Đ&ocirc;ng &Aacute;, g&acirc;y nhầm lẫn. Bước tiếp theo, ĐH Đ&ocirc;ng &Aacute; sẽ kiện ĐH C&ocirc;ng Nghệ Đ&ocirc;ng &Aacute; ra to&agrave;, đ&ograve;i bồi thường thiệt hại về uy t&iacute;n thương hiệu, t&agrave;i sản vật chất sau khi đ&atilde; thống k&ecirc;, nhất l&agrave; sau kết quả tuyển sinh năm học 2010-2011.</p>\r\n<p class=\"Normal\">Chỉ ti&ecirc;u tuyển sinh trung b&igrave;nh mỗi năm của ĐH Đ&ocirc;ng &Aacute; 3.200-3.500 th&iacute; sinh. Hiện trường n&agrave;y c&oacute; 19 ng&agrave;nh đ&agrave;o tạo ở c&aacute;c cấp từ Trung học chuy&ecirc;n nghiệp, CĐ v&agrave; ĐH.</p>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; lần đầu ti&ecirc;n tại Việt Nam xảy ra tranh chấp nh&atilde;n hiệu quyền sở hữu tr&iacute; tuệ tr&ecirc;n lĩnh vực gi&aacute;o dục. Trước đ&acirc;y phần lớn tranh chấp trong lĩnh vực n&agrave;y l&agrave; quyền t&aacute;c giả li&ecirc;n quan đến gi&aacute;o tr&igrave;nh, b&agrave;i giảng.</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#ffebd7\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p class=\"Normal\">Theo điều 129 của Luật sở hữu tr&iacute; tuệ, c&aacute;c h&agrave;nh vi sau đ&acirc;y được thực hiện m&agrave; kh&ocirc;ng được ph&eacute;p của chủ sở hữu nh&atilde;n hiệu th&igrave; bị coi l&agrave; x&acirc;m phạm quyền đối với nh&atilde;n hiệu:</p>\r\n<p class=\"Normal\">a) Sử dụng dấu hiệu tr&ugrave;ng với nh&atilde;n hiệu được bảo hộ cho h&agrave;ng ho&aacute;, dịch vụ tr&ugrave;ng với h&agrave;ng ho&aacute;, dịch vụ thuộc danh mục đăng k&yacute; k&egrave;m theo nh&atilde;n hiệu đ&oacute;;</p>\r\n<p class=\"Normal\">b) Sử dụng dấu hiệu tr&ugrave;ng với nh&atilde;n hiệu được bảo hộ cho h&agrave;ng ho&aacute;, dịch vụ tương tự hoặc li&ecirc;n quan tới h&agrave;ng ho&aacute;, dịch vụ thuộc danh mục đăng k&yacute; k&egrave;m theo nh&atilde;n hiệu đ&oacute;, nếu việc sử dụng c&oacute; khả năng g&acirc;y nhầm lẫn về nguồn gốc h&agrave;ng ho&aacute;, dịch vụ;</p>\r\n<p class=\"Normal\">c) Sử dụng dấu hiệu tương tự với nh&atilde;n hiệu được bảo hộ cho h&agrave;ng ho&aacute;, dịch vụ tr&ugrave;ng, tương tự hoặc li&ecirc;n quan tới h&agrave;ng ho&aacute;, dịch vụ thuộc danh mục đăng k&yacute; k&egrave;m theo nh&atilde;n hiệu đ&oacute;, nếu việc sử dụng c&oacute; khả năng g&acirc;y nhầm lẫn về nguồn gốc h&agrave;ng ho&aacute;, dịch vụ;</p>\r\n<p class=\"Normal\">d) Sử dụng dấu hiệu tr&ugrave;ng hoặc tương tự với nh&atilde;n hiệu nổi tiếng hoặc dấu hiệu dưới dạng dịch nghĩa, phi&ecirc;n &acirc;m từ nh&atilde;n hiệu nổi tiếng cho h&agrave;ng ho&aacute;, dịch vụ bất kỳ, kể cả h&agrave;ng ho&aacute;, dịch vụ kh&ocirc;ng tr&ugrave;ng, kh&ocirc;ng tương tự v&agrave; kh&ocirc;ng li&ecirc;n quan tới h&agrave;ng ho&aacute;, dịch vụ thuộc danh mục h&agrave;ng ho&aacute;, dịch vụ mang nh&atilde;n hiệu nổi tiếng, nếu việc sử dụng c&oacute; khả năng g&acirc;y nhầm lẫn về nguồn gốc h&agrave;ng ho&aacute; hoặc g&acirc;y ấn tượng sai lệch về mối quan hệ giữa người sử dụng dấu hiệu đ&oacute; với chủ sở hữu nh&atilde;n hiệu nổi tiếng.</p>\r\n<p class=\"Normal\">Điều 198. Quyền tự bảo vệ</p>\r\n<p class=\"Normal\">1. Chủ thể quyền sở hữu tr&iacute; tuệ c&oacute; quyền &aacute;p dụng c&aacute;c biện ph&aacute;p sau đ&acirc;y để bảo vệ quyền sở hữu tr&iacute; tuệ của m&igrave;nh:</p>\r\n<p class=\"Normal\">a) &Aacute;p dụng biện ph&aacute;p c&ocirc;ng nghệ nhằm ngăn ngừa h&agrave;nh vi x&acirc;m phạm quyền sở hữu tr&iacute; tuệ;</p>\r\n<p class=\"Normal\">b) Y&ecirc;u cầu tổ chức, c&aacute; nh&acirc;n c&oacute; h&agrave;nh vi x&acirc;m phạm quyền sở hữu tr&iacute; tuệ phải chấm dứt h&agrave;nh vi x&acirc;m phạm, xin lỗi, cải ch&iacute;nh c&ocirc;ng khai, bồi thường thiệt hại;</p>\r\n<p class=\"Normal\">c) Y&ecirc;u cầu cơ quan nh&agrave; nước c&oacute; thẩm quyền xử l&yacute; h&agrave;nh vi x&acirc;m phạm quyền sở hữu tr&iacute; tuệ theo quy định của Luật n&agrave;y v&agrave; c&aacute;c quy định kh&aacute;c của ph&aacute;p luật c&oacute; li&ecirc;n quan;</p>\r\n<p class=\"Normal\">d) Khởi kiện ra t&ograve;a &aacute;n hoặc trọng t&agrave;i để bảo vệ quyền, lợi &iacute;ch hợp ph&aacute;p của m&igrave;nh.</p>\r\n<p class=\"Normal\">2. Tổ chức, c&aacute; nh&acirc;n bị thiệt hại do h&agrave;nh vi x&acirc;m phạm quyền sở hữu tr&iacute; tuệ hoặc ph&aacute;t hiện h&agrave;nh vi x&acirc;m phạm quyền sở hữu tr&iacute; tuệ g&acirc;y thiệt hại cho người ti&ecirc;u d&ugrave;ng hoặc cho x&atilde; hội c&oacute; quyền y&ecirc;u cầu cơ quan nh&agrave; nước c&oacute; thẩm quyền xử l&yacute; h&agrave;nh vi x&acirc;m phạm quyền sở hữu tr&iacute; tuệ theo quy định của Luật n&agrave;y v&agrave; c&aacute;c quy định kh&aacute;c của ph&aacute;p luật c&oacute; li&ecirc;n quan.</p>\r\n<p class=\"Normal\">3. Tổ chức, c&aacute; nh&acirc;n bị thiệt hại hoặc c&oacute; khả năng bị thiệt hại do h&agrave;nh vi cạnh tranh kh&ocirc;ng l&agrave;nh mạnh c&oacute; quyền y&ecirc;u cầu cơ quan nh&agrave; nước c&oacute; thẩm quyền &aacute;p dụng c&aacute;c biện ph&aacute;p d&acirc;n sự quy định tại Điều 202 của Luật n&agrave;y v&agrave; c&aacute;c biện ph&aacute;p h&agrave;nh ch&iacute;nh theo quy định của ph&aacute;p luật về cạnh tranh.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tr&agrave; Bang - Chi&ecirc;u Anh</strong></p>', 1, 2829, 0, 1, NULL, NULL),
(86, 'Hai đại học đầu tiên bị tạm ngừng tuyển sinh', 'Hai-Dai-Hoc-Dau-Tien-Bi-Tam-Ngung-Tuyen-Sinh', 'Ngày 5/8, Thứ trưởng Bộ GD&ĐT Nguyễn Thị Nghĩa đã ký quyết định tạm ngừng tuyển sinh năm 2010 đối với ĐH Công nghệ Đông Á (Bắc Ninh) và ĐH Phan Châu Trinh (Quảng Nam) do nhiều vi phạm.', 'Phu-huynh.jpg', 22, '<p class=\"Title\">Hai đại học đầu ti&ecirc;n bị tạm ngừng tuyển sinh</p>\r\n<p class=\"Lead\">Ng&agrave;y 5/8, Thứ trưởng Bộ GD&amp;ĐT Nguyễn Thị Nghĩa đ&atilde; k&yacute; quyết định tạm ngừng tuyển sinh năm 2010 đối với ĐH C&ocirc;ng nghệ Đ&ocirc;ng &Aacute; (Bắc Ninh) v&agrave; ĐH Phan Ch&acirc;u Trinh (Quảng Nam) do nhiều vi phạm.</p>\r\n<p class=\"Normal\">Ng&agrave;y 5/8, Thứ trưởng Bộ GD&amp;ĐT Nguyễn Thị Nghĩa vừa k&yacute; quyết định tạm ngừng tuyển sinh năm 2010 đối với ĐH C&ocirc;ng nghệ Đ&ocirc;ng &Aacute;. L&yacute; do bị tạm ngừng tuyển sinh l&agrave; v&igrave; trường vi phạm Quy chế tổ chức v&agrave; hoạt động của đại học tư thục.</p>\r\n<p class=\"Normal\">Theo đ&oacute;, trường được th&agrave;nh lập từ cuối năm 2008 theo quyết định của Thủ tướng, hơn một th&aacute;ng sau Hội đồng quản trị của trường được Bộ GD&amp;ĐT c&ocirc;ng nhận. Năm 2009, trường bắt đầu tuyển sinh v&agrave; đ&agrave;o tạo tr&igrave;nh độ đại học nhưng đến nay vẫn chưa ban h&agrave;nh được Quy chế tổ chức v&agrave; hoạt động. Hội đồng quản trị mất đo&agrave;n kết, ảnh hưởng đến uy t&iacute;n của ng&agrave;nh, địa phương v&agrave; nh&agrave; trường.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/EE/0F/Phu-huynh.jpg\" alt=\"Ảnh: Ho&agrave;ng H&agrave;.\" width=\"480\" height=\"319\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Phụ huynh, học sinh vẫn l&agrave; những người thiệt th&ograve;i nhất khi lỡ theo học những trường đại học k&eacute;m chất lượng. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Th&aacute;ng 5 vừa qua, ĐH C&ocirc;ng nghệ Đ&ocirc;ng &Aacute; đ&atilde; khởi c&ocirc;ng x&acirc;y dựng 2 khối nh&agrave; đầu ti&ecirc;n tại l&agrave;ng đại học ở th&agrave;nh phố Bắc Ninh phục vụ 4.000 sinh vi&ecirc;n. C&aacute;c ng&agrave;nh học của trường gồm: Kinh tế, Ngoại ngữ, C&ocirc;ng nghệ th&ocirc;ng tin, Kiến tr&uacute;c, Cơ kh&iacute; v&agrave; c&aacute;c kh&oacute;a li&ecirc;n kết với c&aacute;c trường đại học tại Ph&aacute;p, Mỹ, Australia...</p>\r\n<p class=\"Normal\">Năm 2009 - 2010, trường tuyển sinh kh&oacute;a đầu ti&ecirc;n với 500 sinh vi&ecirc;n cho 3 ng&agrave;nh Kế to&aacute;n, T&agrave;i ch&iacute;nh Ng&acirc;n h&agrave;ng v&agrave; Quản trị kinh doanh. Năm nay, trường được giao 1.400 chỉ ti&ecirc;u ĐH, CĐ; hơn 1.000 chỉ ti&ecirc;u vừa học vừa l&agrave;m, li&ecirc;n th&ocirc;ng; v&agrave; 700 chỉ ti&ecirc;u TCCN.</p>\r\n<p class=\"Normal\"><strong><span style=\"color: #4f4f4f;\">C&ugrave;ng ng&agrave;y, Bộ GD&amp;ĐT đ&atilde; c&oacute; buổi l&agrave;m việc với l&atilde;nh đạo ĐH Phan Ch&acirc;u Trinh</span></strong>, c&oacute; sự tham gia của l&atilde;nh đạo tỉnh Quảng Nam v&agrave; TP Hội An. Ph&oacute; thanh tra Phạm Ngọc Tr&uacute;c đ&atilde; đọc quyết định tạm ngừng tuyển sinh đối với ĐH Phan Ch&acirc;u Trinh trong năm học n&agrave;y.</p>\r\n<p class=\"Normal\">Đại diện Bộ GD&amp;ĐT cho biết, năm 2007, ĐH Phan Ch&acirc;u Trinh đ&atilde; tuyển sinh khi chưa được sự đồng &yacute; của Bộ, c&oacute; nhiều sai s&oacute;t trong kh&acirc;u ra đề, chấm thi, x&eacute;t tuyển; tự &yacute; tăng điểm gi&atilde;n c&aacute;ch giữa c&aacute;c khu vực ưu ti&ecirc;n kh&ocirc;ng đ&uacute;ng quy định; một số ng&agrave;nh x&eacute;t tuyển th&iacute; sinh kh&ocirc;ng đ&uacute;ng khối thi v&agrave;o học. Sau hơn 2 năm th&agrave;nh lập, hiện trường chưa c&oacute; Hội đồng cổ đ&ocirc;ng, kh&ocirc;ng họp Hội đồng quản trị, chưa c&oacute; Ban kiểm so&aacute;t...</p>\r\n<p class=\"Normal\">Để đảm bảo chất lượng đ&agrave;o tạo đối với kh&oacute;a tuyển sinh năm 2007 của ĐH Phan Ch&acirc;u Trinh, Bộ GD&amp;ĐT đ&atilde; gửi c&ocirc;ng văn tới ĐH Huế v&agrave; ĐH Đ&agrave; Nẵng đề nghị hỗ trợ giảng vi&ecirc;n, t&agrave;i liệu học tập, điều kiện vật chất phục vụ th&iacute; nghiệm thực h&agrave;nh, hướng dẫn luận văn, thi tốt nghiệp v&agrave; đ&aacute;nh gi&aacute; tốt nghiệp cho sinh vi&ecirc;n trường n&agrave;y.</p>\r\n<p class=\"Normal\">Ph&oacute; chủ tịch tỉnh Quảng Nam Trần Minh Cả cho rằng, những vấn đề ri&ecirc;ng tư nội bộ của trường cần sớm giải quyết dứt điểm, c&aacute;c th&agrave;nh vi&ecirc;n trong Hội đồng quản trị của trường cần nh&oacute;m họp v&agrave; t&igrave;m c&aacute;ch th&aacute;o gỡ những vướng mắc.</p>\r\n<p class=\"Normal\">Trong khi đ&oacute;, đại diện ĐH Phan Ch&acirc;u Trinh đ&atilde; l&ecirc;n tiếng phản đối v&agrave; trả lại quyết định cho đo&agrave;n c&ocirc;ng t&aacute;c của Bộ, đồng thời đề nghị được l&agrave;m việc gặp trực tiếp Bộ trưởng GD&amp;ĐT Phạm Vũ Luận.</p>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; lần đầu ti&ecirc;n Bộ GD&amp;ĐT quyết định tạm ngừng tuyển sinh đối với trường đại học, nhằm n&acirc;ng cao chất lượng đ&agrave;o tạo, siết chặt kỷ cương của ng&agrave;nh.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tiến Dũng - Tr&agrave; Bang</strong></p>', 1, 1707, 0, 1, NULL, NULL),
(87, 'Thí sinh cao đẳng đầu tiên được 30 điểm', 'Thi-Sinh-Cao-Dang-Dau-Tien-Duoc-30-Diem', 'Trương Thanh Hà thủ khoa CĐ Kinh tế Đối ngoại là thí sinh thi cao đẳng đầu tiên được 30 điểm. Á khoa khối A của trường này là Hà Quốc Thi được 29,75 điểm. ', 'Thi_1.jpg', 22, '<p class=\"Title\">Th&iacute; sinh cao đẳng đầu ti&ecirc;n được 30 điểm</p>\r\n<p class=\"Lead\">Trương Thanh H&agrave; thủ khoa CĐ Kinh tế Đối ngoại l&agrave; th&iacute; sinh thi cao đẳng đầu ti&ecirc;n được 30 điểm. &Aacute; khoa khối A của trường n&agrave;y l&agrave; H&agrave; Quốc Thi được 29,75 điểm.&nbsp;</p>\r\n<p class=\"Normal\">Ở khối D1, Phan Thị Thảo (TP HCM) được tổng điểm cao nhất, 26 điểm (đ&atilde; l&agrave;m tr&ograve;n).</p>\r\n<p class=\"Normal\">Ở hệ cao đẳng, thủ khoa Huỳnh Văn Trung T&iacute;n của CĐ C&ocirc;ng nghiệp Thực phẩm TP HCM cũng c&oacute; điểm số kh&aacute; cao: 29,5. Hai &aacute; khoa Trần Trung Hiếu v&agrave; Ninh Khiết Ho&agrave;ng c&ugrave;ng được 29,5 điểm.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/ED/92/Thi_1.jpg\" alt=\"Ảnh: Ho&agrave;ng H&agrave;.\" width=\"480\" height=\"333\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Th&iacute; sinh dự thi đại học năm 2010. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Theo thống k&ecirc; của Cục C&ocirc;ng nghệ Th&ocirc;ng tin (Bộ GD&amp;ĐT), kh&aacute; nhiều trong số 100 th&iacute; sinh c&oacute; điểm thi cao nhất hiện nay thuộc về c&aacute;c trường cao đẳng. Điển h&igrave;nh, Đ&agrave;o Xu&acirc;n Ph&aacute;t (CĐ Sư phạm Nha Trang) được 29,5 điểm; Nguyễn Thị Ka Ly (CĐ X&acirc;y dựng 3) được 29,5 điểm; Huynh Tr&uacute;c Thanh (CĐ C&ocirc;ng thương TP HCM) được 29 điểm&hellip;</p>\r\n<p class=\"Normal\">Thống k&ecirc; n&agrave;y cũng cho thấy, ngo&agrave;i Tăng Văn B&igrave;nh (ĐH Ngoại thương) v&agrave; Trương Thanh H&agrave; (CĐ Kinh tế Đối ngoại) được tr&ograve;n 30 điểm, Lưu Thanh Xu&acirc;n (ĐH Sư phạm H&agrave; Nội 2) v&agrave; H&agrave; Quốc Thi (CĐ Kinh tế Đối ngoại) cũng được 30 điểm sau khi đ&atilde; l&agrave;m tr&ograve;n. 100 th&iacute; sinh c&oacute; điểm cao nhất đều được 28,25 điểm trở l&ecirc;n.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tiến Dũng</strong></p>', 1, 2739, 0, 1, NULL, NULL),
(88, 'Hơn 2 triệu học sinh thi giải Toán trên ViOlympic', 'Hon-2-Trieu-Hoc-Sinh-Thi-Giai-Toan-Tren-Violympic', 'Ngày 30/7, Bộ GD&ĐT và Tập đoàn FPT đã trao giải cuộc thi Giải Toán đầu tiên qua mạng dành cho học sinh tiểu học và THCS. Thái Bình dẫn đầu cả nước về số thí sinh đoạt giải ở cả hai bậc học.', '1_GS-Nhung.jpg', 22, '<p class=\"Title\">Hơn 2 triệu học sinh thi giải To&aacute;n tr&ecirc;n ViOlympic</p>\r\n<p class=\"Lead\">Ng&agrave;y 30/7, Bộ GD&amp;ĐT v&agrave; Tập đo&agrave;n FPT đ&atilde; trao giải cuộc thi Giải To&aacute;n đầu ti&ecirc;n qua mạng d&agrave;nh cho học sinh tiểu học v&agrave; THCS. Th&aacute;i B&igrave;nh dẫn đầu cả nước về số th&iacute; sinh đoạt giải ở cả hai bậc học.</p>\r\n<p class=\"Normal\">Theo Bộ GD&amp;ĐT, sau 2 năm tổ chức cuộc thi, ViOlympic.vn đ&atilde; gi&uacute;p học sinh nhanh nhạy trong t&iacute;nh to&aacute;n, xử l&yacute; th&ocirc;ng số. Đ&acirc;y l&agrave; s&acirc;n chơi tr&iacute; tuệ trực tuyến, hướng dẫn c&aacute;c em sử dụng Internet như một phương tiện học tập hữu hiệu.</p>\r\n<p class=\"Normal\"><strong><span style=\"color: #2f2f2f;\">Ở bậc tiểu học</span></strong>, 7 tỉnh, th&agrave;nh đoạt c&uacute;p V&agrave;ng gồm: Th&aacute;i B&igrave;nh, Hải Ph&ograve;ng, H&agrave; Nội, Bắc Giang, Vĩnh Ph&uacute;c, Bắc Ninh, Thanh H&oacute;a. Với 13 HCV, 1 HCB v&agrave; 3 HCĐ, Th&aacute;i B&igrave;nh dẫn đầu cả nước về số th&iacute; sinh đoạt huy chương.</p>\r\n<p class=\"Normal\">Quảng Ninh, Thừa Thi&ecirc;n Huế, Quảng B&igrave;nh, Đ&agrave; Nẵng, Ninh B&igrave;nh, H&agrave; Nam, Th&aacute;i Nguy&ecirc;n, Ph&uacute; Thọ, B&igrave;nh Định, H&agrave; Tĩnh, L&agrave;o Cai, Nam Định, Đồng Nai v&agrave; Ph&uacute; Y&ecirc;n đoạt giải Bạc. 15 tỉnh, th&agrave;nh được trao giải Đồng gồm: Quảng Trị, Nghệ An, Vĩnh Long, Gia Lai, H&agrave; Giang, TP HCM, Cần Thơ, Tuy&ecirc;n Quang, B&igrave;nh Thuận, Kon Tum, Đăk Lăk, B&igrave;nh Dương, B&agrave; Rịa - Vũng T&agrave;u, Y&ecirc;n B&aacute;i v&agrave; Hưng Y&ecirc;n.</p>\r\n<p class=\"Normal\">Theo thống k&ecirc;, 41 em đạt điểm tối đa (300 điểm), 194 em được 280 điểm trở l&ecirc;n, 212 em được tr&ecirc;n 250 điểm, v&agrave; 224 em được từ 230 điểm. Đ&aacute;ng lưu &yacute;, c&oacute; 216 th&iacute; sinh ho&agrave;n th&agrave;nh b&agrave;i thi trong thời gian dưới 30 ph&uacute;t v&agrave; đạt từ 270 điểm trở l&ecirc;n.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/EA/8D/1_GS-Nhung.jpg\" alt=\"Ảnh: Tiến Dũng.\" width=\"490\" height=\"348\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">GS. TSKH Trần Văn Nhung - nguy&ecirc;n Thứ trưởng GD&amp;ĐT trao giải cho c&aacute;c tỉnh. Ảnh:&nbsp;<em>Tiến Dũng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><strong><span style=\"color: #2f2f2f;\">Ở bậc THCS</span></strong>, 7 tỉnh, th&agrave;nh: Th&aacute;i B&igrave;nh, Bắc Ninh, Đồng Nai, Ph&uacute; Thọ, H&agrave; Tĩnh, Vĩnh Ph&uacute;c, Hải Ph&ograve;ng vinh dự được nhận c&uacute;p V&agrave;ng. Th&aacute;i B&igrave;nh vẫn dẫn đầu cả nước về số lượng học sinh đoạt giải (15 HCV, 2 HCĐ).</p>\r\n<p class=\"Normal\">Thừa Thi&ecirc;n Huế, Ph&uacute; Y&ecirc;n, T&acirc;y Ninh, H&agrave; Nam, Nghệ An, Ninh B&igrave;nh, Thanh H&oacute;a, Tiền Giang, Đ&agrave; Nẵng, TP HCM, Th&aacute;i Nguy&ecirc;n, An Giang, Quảng Ninh, Nam Định được trao giải Bạc.</p>\r\n<p class=\"Normal\">Y&ecirc;n B&aacute;i, Tuy&ecirc;n Quang, L&agrave;o Cai, Tr&agrave; Vinh, Vĩnh Long, B&igrave;nh Định, L&acirc;m Đồng, Hậu Giang, B&agrave; Rịa - Vũng T&agrave;u, Đăk Lăk, Cao Bằng, Kh&aacute;nh H&ograve;a, Long An, Cần Thơ, Kon Tum, Quảng B&igrave;nh, Bắc Giang, H&agrave; Nội, Quảng Trị được trao giải Đồng.</p>\r\n<p class=\"Normal\">Theo thống k&ecirc;, c&oacute; 14 em đạt điểm tối đa (296 điểm), 32 em được 280 điểm trở l&ecirc;n, 306 em được từ 250 điểm, 614 em được 230 điểm trở l&ecirc;n.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/EA/8D/1_Thu-truong-GD.jpg\" alt=\"Ảnh: Tiến Dũng.\" width=\"490\" height=\"295\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Thứ trưởng GD&amp;ĐT Trần Quang Qu&yacute; (thứ tư từ phải sang) v&agrave; nguy&ecirc;n Thứ trưởng Trần Văn Nhung (ngo&agrave;i c&ugrave;ng b&ecirc;n tr&aacute;i) chụp ảnh lưu niệm c&ugrave;ng đại diện c&aacute;c tỉnh đoạt giải V&agrave;ng. Ảnh:&nbsp;<em>Tiến Dũng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Hiện, ViOlympic.vn c&oacute; gần 1,2 triệu học sinh tiểu học v&agrave; hơn 900.000 học sinh THCS ở 63 tỉnh, th&agrave;nh phố cũng như c&aacute;c em ở nước ngo&agrave;i tham gia thi trực tuyến. Trong đ&oacute;, đ&ocirc;ng nhất l&agrave; H&agrave; Nội với hơn 200.000 học sinh, Nghệ An gần 170.000 em, Thanh H&oacute;a gần 140.000 em, Đồng Nai gần 130.000 em, TP HCM hơn 120.000 em, Thừa Thi&ecirc;n Huế hơn 100.000 em...</p>\r\n<p class=\"Normal\">Những tỉnh kh&oacute; khăn như Ninh Thuận cũng c&oacute; gần 1.200 học sinh tham dự, Bến Tre c&oacute; hơn 1.000 em, Điện Bi&ecirc;n gần 1.000 em, Lạng Sơn 800 em, Lai Ch&acirc;u hơn 600 em... Đ&aacute;ng lưu &yacute;, cuộc thi cũng thu h&uacute;t được hơn 4.000 học sinh Việt Nam ở nước ngo&agrave;i tham dự.</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"3\" width=\"497\" bgcolor=\"#97ff97\">\r\n<tbody>\r\n<tr>\r\n<td bgcolor=\"#f3f3e9\">\r\n<p class=\"Normal\">Khai sinh ng&agrave;y 15/12/2008, hiện ViOlympic đ&atilde; thu h&uacute;t hơn 2 triệu th&agrave;nh vi&ecirc;n v&agrave; trở th&agrave;nh s&acirc;n chơi tr&iacute; tuệ th&uacute; vị, bổ &iacute;ch cho học sinh tiểu học v&agrave; THCS tr&ecirc;n to&agrave;n quốc với b&igrave;nh qu&acirc;n tăng trưởng 5.000 th&agrave;nh vi&ecirc;n mới mỗi ng&agrave;y.</p>\r\n<p class=\"Normal\">ViOlympic đ&atilde; nhận giải thưởng C&ocirc;ng nghệ Th&ocirc;ng tin - Truyền th&ocirc;ng Việt Nam 2009 (Vietnam ICT Awards 2009) cho sản phẩm nội dung số mang t&iacute;nh văn h&oacute;a, gi&aacute;o dục tốt nhất.</p>\r\n<p class=\"Normal\">Sắp tới, ViOlympic dự kiến mở rộng th&ecirc;m cuộc thi cho c&aacute;c m&ocirc;n tiếng Việt, tiếng Anh... v&agrave; c&aacute;c lớp đầu cấp THPT, đồng thời x&acirc;y dựng phi&ecirc;n bản tiếng Anh để c&oacute; thể tổ chức cuộc thi giải To&aacute;n qua Internet cấp khu vực v&agrave; quốc tế.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tiến Dũng</strong></p>', 1, 1040, 0, 1, NULL, NULL),
(89, 'ĐH Kinh tế Quốc dân dự kiến điểm chuẩn 18-28,5', 'Dh-Kinh-Te-Quoc-Dan-Du-Kien-Diem-Chuan-18-28,5', 'Năm nay điểm chuẩn khối A vào trường là 21, khối D là 20 (tiếng Anh hệ số 1), riêng 7 ngành lấy điểm khối A, D là 18. Bốn ngành dạy bằng tiếng Anh lấy điểm chuẩn 21-28,5 (tiếng Anh hệ số 2).\r\n', 'DSC_6904.jpg', 22, '<p class=\"Title\">ĐH Kinh tế Quốc d&acirc;n dự kiến điểm chuẩn 18-28,5</p>\r\n<p class=\"Lead\">Năm nay điểm chuẩn khối A v&agrave;o trường l&agrave; 21, khối D l&agrave; 20 (tiếng Anh hệ số 1), ri&ecirc;ng 7 ng&agrave;nh lấy điểm khối A, D l&agrave; 18. Bốn ng&agrave;nh dạy bằng tiếng Anh lấy điểm chuẩn 21-28,5 (tiếng Anh hệ số 2).<br /><br /></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/EA/0A/DSC_6904.jpg\" alt=\"Ảnh: Ho&agrave;ng H&agrave;.\" width=\"495\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">M&ugrave;a tuyển sinh n&agrave;y, điểm chuẩn của nhiều trường giảm hơn so với năm 2009. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Chiều nay, ĐH Kinh tế Quốc d&acirc;n đưa ra mức điểm chuẩn dự kiến của kh&oacute;a K52. Theo Trưởng ph&ograve;ng Đ&agrave;o tạo Nguyễn Quang Dong, trường đ&atilde; b&aacute;o c&aacute;o Bộ Gi&aacute;o dục mức điểm dự kiến n&agrave;y. Đ&acirc;y l&agrave; điểm chuẩn dự kiến cho học sinh KV3, mức điểm chuẩn ch&ecirc;nh lệch giữa 2 nh&oacute;m đối tượng kế tiếp l&agrave; 1 điểm v&agrave; giữa 2 khu vực kế tiếp l&agrave; 0,5 điểm.</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\" width=\"490\" bgcolor=\"#f1f1e2\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Normal\" width=\"261\" valign=\"top\">- Kinh tế n&ocirc;ng nghiệp v&agrave; ph&aacute;t triển n&ocirc;ng th&ocirc;n</td>\r\n<td class=\"Normal\" width=\"74\" align=\"center\">417</td>\r\n<td class=\"Normal\" width=\"77\" align=\"center\">A/D<sub>1</sub></td>\r\n<td class=\"Normal\" width=\"78\" align=\"center\">18</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"261\" valign=\"top\">- Thống k&ecirc; kinh tế x&atilde; hội</td>\r\n<td class=\"Normal\" width=\"74\" align=\"center\">424</td>\r\n<td class=\"Normal\" width=\"77\" align=\"center\">A/D<sub>1</sub></td>\r\n<td class=\"Normal\" width=\"78\" align=\"center\">18</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"261\" valign=\"top\">- Tin học kinh tế</td>\r\n<td class=\"Normal\" width=\"74\" align=\"center\">444</td>\r\n<td class=\"Normal\" width=\"77\" align=\"center\">A/D<sub>1</sub></td>\r\n<td class=\"Normal\" width=\"78\" align=\"center\">18</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"261\" valign=\"top\">- Hệ thống th&ocirc;ng tin quản l&iacute;</td>\r\n<td class=\"Normal\" width=\"74\" align=\"center\">453</td>\r\n<td class=\"Normal\" width=\"77\" align=\"center\">A/D<sub>1</sub></td>\r\n<td class=\"Normal\" width=\"78\" align=\"center\">18</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"261\" valign=\"top\">- Luật kinh doanh</td>\r\n<td class=\"Normal\" width=\"74\" align=\"center\">545</td>\r\n<td class=\"Normal\" width=\"77\" align=\"center\">A/D<sub>1</sub></td>\r\n<td class=\"Normal\" width=\"78\" align=\"center\">18</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"261\" valign=\"top\">- Luật kinh doanh quốc tế</td>\r\n<td class=\"Normal\" width=\"74\" align=\"center\">546</td>\r\n<td class=\"Normal\" width=\"77\" align=\"center\">A/D<sub>1</sub></td>\r\n<td class=\"Normal\" width=\"78\" align=\"center\">18</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"261\" valign=\"top\">Chuy&ecirc;n ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin</td>\r\n<td class=\"Normal\" width=\"74\" align=\"center\">146</td>\r\n<td class=\"Normal\" width=\"77\" align=\"center\">A</td>\r\n<td class=\"Normal\" width=\"78\" align=\"center\">18</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"261\" valign=\"top\">Chuy&ecirc;n ng&agrave;nh Tiếng Anh Thương mại (tiếng Anh hệ số 2)</td>\r\n<td class=\"Normal\" width=\"74\" align=\"center\">701</td>\r\n<td class=\"Normal\" width=\"77\" align=\"center\">D<sub>1</sub></td>\r\n<td class=\"Normal\" width=\"78\" align=\"center\">28,5</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"261\" valign=\"top\"><strong>C&aacute;c lớp tuyển sinh theo đặc th&ugrave;:</strong></td>\r\n<td class=\"BreakLine\" width=\"74\">&nbsp;</td>\r\n<td class=\"BreakLine\" width=\"77\">&nbsp;</td>\r\n<td class=\"BreakLine\" width=\"78\">&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"261\" valign=\"top\">- Quản trị lữ h&agrave;nh (tiếng Anh hệ số 2)</td>\r\n<td class=\"Normal\" width=\"74\" align=\"center\">454</td>\r\n<td class=\"Normal\" width=\"77\" align=\"center\">D<sub>1</sub></td>\r\n<td class=\"Normal\" width=\"78\" align=\"center\">21</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"261\" valign=\"top\">- Quản trị kh&aacute;ch sạn (tiếng Anh hệ số 2)</td>\r\n<td class=\"Normal\" width=\"74\" align=\"center\">455</td>\r\n<td class=\"Normal\" width=\"77\" align=\"center\">D<sub>1</sub></td>\r\n<td class=\"Normal\" width=\"78\" align=\"center\">21</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"261\" valign=\"top\">- QTKD bằng tiếng Anh (E-BBA)</td>\r\n<td class=\"Normal\" rowspan=\"2\" width=\"74\" align=\"center\">457</td>\r\n<td class=\"Normal\" width=\"77\" align=\"center\">A</td>\r\n<td class=\"Normal\" width=\"78\" align=\"center\">21</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"261\" valign=\"top\">- QTKD bằng tiếng Anh (tiếng Anh hệ số 2)</td>\r\n<td class=\"Normal\" width=\"77\" align=\"center\">D1</td>\r\n<td class=\"Normal\" width=\"78\" align=\"center\">28,5</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tiến Dũng</strong></p>', 1, 368, 0, 1, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(90, 'Hai nữ sinh được điểm 10 môn Địa lý', 'Hai-Nu-Sinh-Duoc-Diem-10-Mon-Dia-Ly', 'Tại ĐH Khoa học Xã hội và Nhân văn Hà Nội, điểm thi 9,75 (làm tròn thành 10) môn Địa lý thuộc về thí sinh Nguyễn Thị Hữu và Hoàng Thị Thu Trang. Điểm 9,5 môn Lịch sử là của thí sinh Trần Thị Cẩm.', 'Gio-thi.jpg', 22, '<p class=\"Title\">Hai nữ sinh được điểm 10 m&ocirc;n Địa l&yacute;</p>\r\n<p class=\"Lead\">Tại ĐH Khoa học X&atilde; hội v&agrave; Nh&acirc;n văn H&agrave; Nội, điểm thi 9,75 (l&agrave;m tr&ograve;n th&agrave;nh 10) m&ocirc;n Địa l&yacute; thuộc về th&iacute; sinh Nguyễn Thị Hữu v&agrave; Ho&agrave;ng Thị Thu Trang. Điểm 9,5 m&ocirc;n Lịch sử l&agrave; của th&iacute; sinh Trần Thị Cẩm.<br />Trưa nay, ĐH Khoa học X&atilde; hội v&agrave; Nh&acirc;n văn (ĐH Quốc gia H&agrave; Nội) c&ocirc;ng bố điểm thi khối C, với hai thủ khoa c&ugrave;ng được 26,5 điểm l&agrave; Phạm Thị Minh Duy&ecirc;n - ng&agrave;nh C&ocirc;ng t&aacute;c x&atilde; hội (Văn 8,5; Sử 8,5; Địa 9,25) v&agrave; Nguyễn Thị B&iacute;ch Ngọc - ng&agrave;nh Đ&ocirc;ng phương học (Văn 8; Sử 9; Địa 9,25).</p>\r\n<p class=\"Normal\">Điểm thi 9,75 (l&agrave;m tr&ograve;n th&agrave;nh 10) m&ocirc;n Địa l&yacute; thuộc về th&iacute; sinh Nguyễn Thị Hữu v&agrave; Ho&agrave;ng Thị Thu Trang. Điểm 9,5 m&ocirc;n Lịch sử l&agrave; của th&iacute; sinh Trần Thị Cẩm. C&ograve;n điểm 9 m&ocirc;n Văn thuộc về th&iacute; sinh Trịnh Ngọc Gi&aacute;p.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/E9/E7/Gio-thi.jpg\" alt=\"Ảnh: Ho&agrave;ng H&agrave;.\" width=\"480\" height=\"317\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Th&iacute; sinh dự thi đại học đợt 2 tại H&agrave; Nội. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Theo thống k&ecirc; của trường, năm nay c&aacute;c ng&agrave;nh T&acirc;m l&yacute; học, Khoa học Quản l&yacute; v&agrave; Văn học c&oacute; kh&aacute; đ&ocirc;ng th&iacute; sinh đạt mức điểm x&eacute;t tuyển của năm 2009. Cao nhất l&agrave; ng&agrave;nh Văn học, với 205 th&iacute; sinh đạt từ 18 điểm trở l&ecirc;n, trong khi chỉ ti&ecirc;u tuyển sinh chỉ l&agrave; 100 (gồm cả khối C v&agrave; D).</p>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; năm đầu ti&ecirc;n trường tuyển sinh ng&agrave;nh Việt Nam học, c&oacute; 32 th&iacute; sinh đạt từ 18 điểm (mức điểm chuẩn thấp nhất năm 2009) trở l&ecirc;n, trong khi chỉ ti&ecirc;u l&agrave; 60.</p>\r\n<p class=\"Normal\">Dưới đ&acirc;y l&agrave; bảng thống k&ecirc; số th&iacute; sinh dự thi khối C năm 2010 c&oacute; tổng điểm lớn hơn hoặc bằng mức điểm x&eacute;t tuyển năm 2009. Điểm số n&agrave;y đ&atilde; bao gồm cả điểm ưu ti&ecirc;n.</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"3\" width=\"492\" bgcolor=\"#f3f3e9\">\r\n<tbody>\r\n<tr>\r\n<td class=\"BoxTitle\" width=\"45%\" align=\"center\" bgcolor=\"#c6c68c\">Ng&agrave;nh</td>\r\n<td class=\"BoxTitle\" width=\"18%\" align=\"center\" bgcolor=\"#c6c68c\">Điểm XT&nbsp;<br />năm 2009</td>\r\n<td class=\"BoxTitle\" width=\"18%\" align=\"center\" bgcolor=\"#c6c68c\">Số th&iacute; sinh năm 2010&nbsp;<br />đạt điểm XT 2009</td>\r\n<td class=\"BoxTitle\" width=\"18%\" align=\"center\" bgcolor=\"#c6c68c\">Chỉ ti&ecirc;u 2010</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">T&acirc;m l&yacute; học</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">18</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">92</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">80</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">Khoa học quản l&yacute;</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">18,5</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">189</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">110</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">X&atilde; hội học</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">18</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">59</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">70</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">Triết học</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">18</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">18</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">70</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">Ch&iacute;nh trị học</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">18</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">35</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">70</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">C&ocirc;ng t&aacute;c x&atilde; hội</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">18</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">80</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">70</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">Văn học</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">18</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">205</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">100</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">Ng&ocirc;n ngữ học</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">18</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">35</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">50</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">Lịch sử</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">19,5</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">92</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">100</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">B&aacute;o ch&iacute;</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">21</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">40</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">100</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">Th&ocirc;ng tin &ndash; Thư viện</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">18</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">13</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">70</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">Lưu trữ học v&agrave; QTVP</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">20</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">37</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">70</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">Đ&ocirc;ng phương học</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">20</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">144</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">110</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">Quốc tế học</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">18,5</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">93</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">80</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">Du lịch học</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">19,5</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">77</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">90</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">H&aacute;n N&ocirc;m</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">18</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">25</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">30</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">Nh&acirc;n học</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">18</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">17</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">70</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"45%\">Việt Nam học</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">-</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">-</td>\r\n<td class=\"Normal\" width=\"18%\" align=\"center\">60</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><em>Lưu &yacute;: Số th&iacute; sinh tr&ecirc;n chỉ t&iacute;nh ri&ecirc;ng khối C, kh&ocirc;ng bao gồm khối A v&agrave; D.</em></p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tiến Dũng</strong></p>', 1, 1522, 0, 1, NULL, NULL),
(91, 'Được 1,5 điểm Văn vẫn đỗ đại học', 'Duoc-1,5-Diem-Van-Van-Do-Dai-Hoc', 'Chiều 28/7, ĐH Sân khấu Điện ảnh công bố danh sách thí sinh trúng tuyển. Nhiều thí sinh dù chỉ được 1,5 điểm Văn nhưng môn năng khiếu đạt điểm cao, vẫn trúng tuyển vào trường này.', '4.jpg', 22, '<p class=\"Title\">Được 1,5 điểm Văn vẫn đỗ đại học</p>\r\n<p class=\"Lead\">Chiều 28/7, ĐH S&acirc;n khấu Điện ảnh c&ocirc;ng bố danh s&aacute;ch th&iacute; sinh tr&uacute;ng tuyển. Nhiều th&iacute; sinh d&ugrave; chỉ được 1,5 điểm Văn nhưng m&ocirc;n năng khiếu đạt điểm cao, vẫn tr&uacute;ng tuyển v&agrave;o trường n&agrave;y.<br /><br /></p>\r\n<p class=\"Normal\">Theo t&igrave;m hiểu của&nbsp;<em>VnExpress.net</em>, ở khoa Quay phim Điện ảnh, điểm tr&uacute;ng tuyển lấy thấp nhất l&agrave; 14,5 (đối với th&iacute; sinh KV3), với điều kiện m&ocirc;n chuy&ecirc;n ng&agrave;nh phải được từ 12 điểm trở l&ecirc;n. Ở mức điểm n&agrave;y, c&oacute; 2 th&iacute; sinh được 1,5 điểm Văn vẫn nằm trong danh s&aacute;ch tr&uacute;ng tuyển.</p>\r\n<p class=\"Normal\">Khoa Quay phim Truyền h&igrave;nh, điểm chuẩn từ 16 trở l&ecirc;n. Một số th&iacute; sinh được 2-3 điểm Văn cũng tr&uacute;ng tuyển.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/E9/72/4.jpg\" alt=\"Ảnh: Ho&agrave;ng Th&ugrave;y.\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Th&iacute; sinh dự thi ng&agrave;nh nghệ thuật ở H&agrave; Nội. Ảnh:&nbsp;<em>Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Năm nay, khoa Đạo diễn truyền h&igrave;nh lấy từ 18 trở l&ecirc;n (điểm chuy&ecirc;n ng&agrave;nhđược từ 13). Khoa Diễn vi&ecirc;n S&acirc;n khấu lấy 17,5 điểm; Khoa Đạo diễn Truyền h&igrave;nh lấy từ 16 điểm; Khoa Bi&ecirc;n kịch Điện ảnh lấy từ 18 điểm. Với điểm chuẩn 20, khoa L&yacute; luận Ph&ecirc; b&igrave;nh điện ảnh ch&iacute; c&oacute; 4 th&iacute; sinh tr&uacute;ng tuyển...</p>\r\n<p class=\"Normal\">Theo quy định, sau khi vượt qua v&ograve;ng sơ tuyển, th&iacute; sinh dự thi v&agrave;o ĐH S&acirc;n khấu Điện ảnh phải dự thi m&ocirc;n Văn v&agrave; m&ocirc;n năng khiếu (hệ số 2). Tổng chỉ ti&ecirc;u năm 2010 của trường l&agrave; 358 sinh vi&ecirc;n.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tiến Dũng</strong></p>', 1, 768, 0, 1, NULL, NULL),
(92, 'Hơn 100 trường công bố điểm tuyển sinh', 'Hon-100-Truong-Cong-Bo-Diem-Tuyen-Sinh', 'Bên cạnh các trường lớn như ĐH Hà Nội, Y Hà Nội, Kinh tế TP HCM... nhiều trường cao đẳng cũng công bố điểm thi. Nhiều trường cho hay dự kiến điểm chuẩn sẽ thấp hơn năm 2009.', 'Thi-TP-HCM.jpg', 22, '<p class=\"Title\">Hơn 100 trường c&ocirc;ng bố điểm tuyển sinh</p>\r\n<p class=\"Lead\">B&ecirc;n cạnh c&aacute;c trường lớn như ĐH H&agrave; Nội, Y H&agrave; Nội, Kinh tế TP HCM... nhiều trường cao đẳng cũng c&ocirc;ng bố điểm thi. Nhiều trường cho hay dự kiến điểm chuẩn sẽ thấp hơn năm 2009.</p>\r\n<p class=\"Lead\">Chiều 28/7, ĐH H&agrave; Nội c&ocirc;ng bố điểm thi. Với 38 điểm (ngoại ngữ nh&acirc;n đ&ocirc;i), th&iacute; sinh thi khoa tiếng Nhật Phạm Thị Quỳnh Trang trở th&agrave;nh thủ khoa của trường. C&ograve;n thủ khoa khối A đạt 25,5 điểm</p>\r\n<p class=\"Normal\">Trao đổi với&nbsp;<em>VnExpress.net</em>, Trưởng ph&ograve;ng Đ&agrave;o tạo ĐH H&agrave; Nội L&ecirc; Quốc Hạnh cho biết, trường đang l&ecirc;n phương &aacute;n điểm x&eacute;t tuyển nhưng \"chắc chắn điểm chuẩn sẽ thấp hơn năm 2009 v&agrave; hạ đều ở c&aacute;c ng&agrave;nh, c&aacute;c khối\", &ocirc;ng Hạnh n&oacute;i.</p>\r\n<p class=\"Normal\">Cũng ng&agrave;y, ĐH Y H&agrave; Nội c&ocirc;ng bố điểm thi, với thủ khoa L&ecirc; Thị Minh Vượng được 30 điểm (sau khi đ&atilde; l&agrave;m tr&ograve;n v&agrave; cộng th&ecirc;m 1 điểm ưu ti&ecirc;n). &Aacute; khoa Trần Văn Minh được 29 điểm. Cuối tuần n&agrave;y, trường sẽ c&ocirc;ng bố điểm tr&uacute;ng tuyển.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/E9/5B/Thi-TP-HCM.jpg\" alt=\"Th&iacute; sinh tập chuẩn bị l&agrave;m b&agrave;i thi đại học. Ảnh: Hải Duy&ecirc;n.\" width=\"480\" height=\"341\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Th&iacute; sinh chuẩn bị l&agrave;m b&agrave;i thi đại học. Ảnh:&nbsp;<em>Hải Duy&ecirc;n</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Với 28,5 điểm, Phan Thiện T&ocirc;n (trường Phổ th&ocirc;ng Năng khiếu TP HCM) trở th&agrave;nh thủ khoa ĐH Kinh tế TP HCM. Đồng &aacute; khoa l&agrave; 3 th&iacute; sinh c&oacute; chung mức điểm 28,25 l&agrave; Trần Mỹ Đạt, Dư Th&acirc;n Duy, v&agrave; Triệu Ngọc Diệp.</p>\r\n<p class=\"Normal\">Trong số gần 20.000 hồ sơ dự thi th&igrave; c&oacute; khoảng 11.000 th&iacute; sinh đạt mức điểm s&agrave;n của năm ngo&aacute;i trở l&ecirc;n (13 điểm). C&oacute; tới 3.800 th&iacute; sinh đạt 19 điểm trở l&ecirc;n (chưa t&iacute;nh điểm ưu ti&ecirc;n), trong khi năm nay ĐH Kinh tế lấy 4.000 chỉ ti&ecirc;u. Ch&iacute;nh v&igrave; vậy, theo đại diện nh&agrave; trường, mức điểm tr&uacute;ng tuyển sẽ khoảng 19.</p>\r\n<p class=\"Normal\">Theo đ&aacute;nh gi&aacute; của l&atilde;nh đạo ĐH Khoa học X&atilde; hội v&agrave; Nh&acirc;n văn TP HCM, điểm thi v&agrave; điểm tr&uacute;ng tuyển năm nay cũng tương đương năm ngo&aacute;i. Th&iacute; sinh đạt thủ khoa khối C l&agrave; Nguyễn Huỳnh Lu&acirc;n, với 25,5 điểm v&agrave; Ng&ocirc; Phan Bảo Tr&acirc;n đứng đầu khối D với 25 điểm.</p>\r\n<p class=\"Normal\">ĐH Nha Trang cũng c&ocirc;ng bố kết quả thi đai học ở cả ba ph&acirc;n hiệu. Theo thống k&ecirc;, điểm thi kh&aacute; thấp chỉ c&oacute; khoảng 1.200 th&iacute; sinh c&oacute; điểm từ 13 trở l&ecirc;n trong số gần 13.000 bạn dự thi. Chỉ ti&ecirc;u năm nay của trường l&agrave; 2.200 ở hệ đại học v&agrave; 800 hệ cao đẳng. Theo đại diện trường, v&igrave; điểm thi tương đối thấp n&ecirc;n nếu Bộ Gi&aacute;o dục hạ điểm s&agrave;n th&igrave; trường cũng sẽ phải hạ điểm tr&uacute;ng tuyển mới lấy đủ chỉ ti&ecirc;u. Dự kiến, trường sẽ phải d&agrave;nh hơn 1.000 chỉ ti&ecirc;u cho nguyện vọng 2.</p>\r\n<p class=\"Normal\">Cả nước c&oacute; hơn 100 ĐH, CĐ c&ocirc;ng bố điểm thi. Đ&atilde; c&oacute; 2 thủ khoa được 30 điểm, trong đ&oacute; 1 thủ khoa đạt điểm tuyệt đối.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n - Tiến Dũng</strong></p>', 1, 1685, 0, 1, NULL, NULL),
(93, 'Việt Nam đoạt 4 huy chương Olympic Hóa học', 'Viet-Nam-Doat-4-Huy-Chuong-Olympic-Hoa-Hoc', 'Sáng nay, Cục phó Khảo thí (Bộ GD&ĐT) Trần Văn Nghĩa cho hay, 4 học sinh Việt Nam dự Olympic Hóa học tại Nhật Bản đã giành được 2 huy chương Bạc và 2 huy chương Đồng', 'IChO.jpg', 22, '<p class=\"Title\">Việt Nam đoạt 4 huy chương Olympic H&oacute;a học</p>\r\n<p class=\"Lead\">S&aacute;ng nay, Cục ph&oacute; Khảo th&iacute; (Bộ GD&amp;ĐT) Trần Văn Nghĩa cho hay, 4 học sinh Việt Nam dự Olympic H&oacute;a học tại Nhật Bản đ&atilde; gi&agrave;nh được 2 huy chương Bạc v&agrave; 2 huy chương Đồng</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/E8/DF/IChO.jpg\" alt=\"\" width=\"450\" height=\"325\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Trong ph&ograve;ng thi l&yacute; thuyết H&oacute;a học. Ảnh:&nbsp;<em>Icho.org.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trong đ&oacute;, Nguyễn Đức B&igrave;nh (THPT chuy&ecirc;n Lam Sơn, Thanh H&oacute;a) v&agrave; Lưu Nguyễn Hồng Anh (THPT Năng khiếu, ĐH Quốc gia TP HCM) được huy chương Bạc. C&ograve;n L&ecirc; Anh Tuấn (THPT chuy&ecirc;n Trần Ph&uacute;, Hải Ph&ograve;ng) v&agrave; Mai Thu C&uacute;c (THPT chuy&ecirc;n Nguyễn Tr&atilde;i, Hải Dương) được huy chương Đồng.</p>\r\n<p class=\"Normal\">C&oacute; khoảng 280 học sinh đến từ hơn 70 quốc gia v&agrave; v&ugrave;ng l&atilde;nh thổ dự thi Olympic H&oacute;a học lần thứ 42 tại Nhật Bản.</p>\r\n<p class=\"Normal\">Năm 2009, tại kỳ thi Olympic H&oacute;a học quốc tế lần thứ 41 ở Anh, đo&agrave;n Việt Nam gi&agrave;nh được 1 huy chương V&agrave;ng, 2 huy chương Bạc v&agrave; 1 huy chương Đồng.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tiến Dũng</strong></p>', 1, 270, 0, 1, NULL, NULL),
(94, 'Chàng trai mồ côi trở thành thủ khoa 30 điểm', 'Chang-Trai-Mo-Coi-Tro-Thanh-Thu-Khoa-30-Diem', 'Ngay khi nhận kết quả thủ khoa ĐH Ngoại thương với số điểm tuyệt đối, ngôi nhà nhỏ ở xã Yên Sơn (Đô Lương, Nghệ An) của Bình luôn chật kín khách. \r\nNgười ở gần thì đến chúc mừng cậu học trò cưng của xóm làng, người làng xa đến “xem mặt” thủ khoa của một trường danh tiếng. Còn Bình thì vừa bận tiếp khách, vừa tranh thủ nghe điện thoại chúc mừng, khuôn mặt đỏ ửng ngượng nghịu.', 'binh.jpg', 22, '<p class=\"Title\">Ch&agrave;ng trai mồ c&ocirc;i trở th&agrave;nh thủ khoa 30 điểm</p>\r\n<p class=\"Lead\">Ngay khi nhận kết quả thủ khoa ĐH Ngoại thương với số điểm tuyệt đối, ng&ocirc;i nh&agrave; nhỏ ở x&atilde; Y&ecirc;n Sơn (Đ&ocirc; Lương, Nghệ An) của B&igrave;nh lu&ocirc;n chật k&iacute;n kh&aacute;ch.&nbsp;<br />Người ở gần th&igrave; đến ch&uacute;c mừng cậu học tr&ograve; cưng của x&oacute;m l&agrave;ng, người l&agrave;ng xa đến &ldquo;xem mặt&rdquo; thủ khoa của một trường danh tiếng. C&ograve;n B&igrave;nh th&igrave; vừa bận tiếp kh&aacute;ch, vừa tranh thủ nghe điện thoại ch&uacute;c mừng, khu&ocirc;n mặt đỏ ửng ngượng nghịu.</p>\r\n<p class=\"Normal\">8 th&aacute;ng sau khi lọt l&ograve;ng mẹ, Tăng Văn B&igrave;nh đ&atilde; phải sống cảnh mồ c&ocirc;i bố. L&uacute;c ấy, chị g&aacute;i cậu mới bi b&ocirc; l&ecirc;n 3. Một n&aacute;ch hai con nhỏ, với phụ cấp &iacute;t ỏi của c&ocirc; gi&aacute;o mầm non trường l&agrave;ng, chị Trần Thị Dung phải tất bật sớm h&ocirc;m để vừa lo việc đồng &aacute;ng, vừa lo việc nh&agrave; trường mới tạm đủ c&aacute;i ăn, c&aacute;i mặc cho c&aacute;c con.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/E8/44/binh.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">B&igrave;nh v&agrave; mẹ. Ảnh:&nbsp;<em>Trường Long.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">&ldquo;Từ nhỏ, hai chị em đ&atilde; rất thương mẹ, lại si&ecirc;ng học. T&ocirc;i thường động vi&ecirc;n muốn mẹ hết khổ th&igrave; phải cố gắng học n&ecirc;n hai đứa c&agrave;ng thi đua nhau. Những khi ấy, mệt nhọc trong t&ocirc;i tan biến hết, v&agrave; t&ocirc;i lu&ocirc;n tin rằng hai con của m&igrave;nh sẽ th&agrave;nh c&ocirc;ng&rdquo;, chị Dung cười n&oacute;i vui vẻ.</p>\r\n<p class=\"Normal\">Ngay từ nhỏ, B&igrave;nh đ&atilde; sớm thể hiện khả năng To&aacute;n học. Năm học lớp 5, B&igrave;nh l&agrave; học sinh duy nhất của huyện Đ&ocirc; Lương đạt giải nhất m&ocirc;n To&aacute;n của tỉnh Nghệ An. Cuối năm học lớp 9, cậu đậu thủ khoa Trường chuy&ecirc;n THPT Phan Bội Ch&acirc;u của tỉnh Nghệ An, &aacute; khoa thi v&agrave;o lớp 10 chuy&ecirc;n To&aacute;n ĐH Khoa học tự nhi&ecirc;n H&agrave; Nội. Năm lớp 12 B&igrave;nh đạt giải nhất m&ocirc;n To&aacute;n của tỉnh v&agrave; giải nh&igrave; m&ocirc;n To&aacute;n kỳ thi học sinh giỏi quốc gia&hellip;</p>\r\n<p class=\"Normal\">Kh&ocirc;ng chỉ c&oacute; th&agrave;nh t&iacute;ch đ&aacute;ng nể trong c&aacute;c kỳ thi m&agrave; điểm số những m&ocirc;n học kh&aacute;c như Văn, Sử, Địa, Ngoại ngữ&hellip; của B&igrave;nh từ nhỏ đến nay cũng lu&ocirc;n ở tốp đầu. Đặc biệt, điểm tổng kết m&ocirc;n To&aacute;n của B&igrave;nh lu&ocirc;n l&agrave; 10, tổng kết c&aacute;c năm học lu&ocirc;n tr&ecirc;n ch&iacute;n phẩy.</p>\r\n<p class=\"Normal\">N&oacute;i về kinh nghiệm cũng như phương ph&aacute;p học, ch&agrave;ng trai xứ Nghệ chỉ cười bẽn lẽn: &ldquo;Em rất &iacute;t đi học th&ecirc;m v&igrave; nh&agrave; ngh&egrave;o, kh&ocirc;ng c&oacute; tiền. Chủ yếu em học v&agrave; hiểu b&agrave;i khi thầy c&ocirc; gi&aacute;o giảng tr&ecirc;n lớp rồi về nh&agrave; t&igrave;m kiếm s&aacute;ch vở, t&agrave;i liệu tham khảo, chỗ n&agrave;o kh&ocirc;ng biết th&igrave; hỏi bạn b&egrave;, thầy c&ocirc;&rdquo;.</p>\r\n<p class=\"Normal\">Say m&ecirc; To&aacute;n học nhưng lại th&iacute;ch c&aacute;c ng&agrave;nh kinh tế, sau khi kết th&uacute;c đợt thi khối A v&agrave;o ĐH Ngoại thương, biết chắc m&igrave;nh sẽ đậu v&igrave; l&agrave;m b&agrave;i tốt, nhưng B&igrave;nh kh&ocirc;ng d&aacute;m nghĩ rằng sẽ đạt điểm tuyệt đối. &ldquo;Chắc do em may mắn hơn c&aacute;c bạn, chứ thi đậu v&agrave;o ĐH Ngoại thương th&igrave; ai cũng xuất sắc&rdquo;, t&acirc;n thủ khoa khi&ecirc;m tốn n&oacute;i.</p>\r\n<p class=\"Normal\">Đạt được nguyện vọng trở th&agrave;nh sinh vi&ecirc;n kinh tế, ước mơ lớn nhất của B&igrave;nh hiện nay l&agrave; được đi du học, v&agrave; trở th&agrave;nh một CEO nổi tiếng. &ldquo;D&ugrave; rất y&ecirc;u m&ocirc;n To&aacute;n nhưng trở th&agrave;nh một gi&aacute;m đốc điều h&agrave;nh, một nh&agrave; l&atilde;nh đạo kinh tế mới l&agrave; ước mơ của em&rdquo;, t&acirc;n thủ khoa b&agrave;y tỏ.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Trường Long</strong></p>', 1, 721, 0, 1, NULL, NULL),
(95, 'Thủ khoa đại học Khoa học tự nhiên TP HCM đạt 28,5 điểm', 'Thu-Khoa-Dai-Hoc-Khoa-Hoc-Tu-Nhien-Tp-Hcm-Dat-28,5-Diem', 'hiều 26/7, Đại học Khoa học tự nhiên TP HCM công bố kết quả điểm thi và điểm chuẩn dự kiến đối với các ngành.\r\n\r\nThủ khoa khối A là Nguyễn Mạnh Tiến đạt 28,5 điểm (Toán 9,75; Lý 9; Hóa 9,5). Thí sinh Phạm Thị Lộc Xuân dẫn đầu khối B với 27,5 điểm (Sinh 9; Toán 9; Hóa 9,5).', 'diem1.jpg', 22, '<p class=\"Title\">Thủ khoa đại học Khoa học tự nhi&ecirc;n TP HCM đạt 28,5 điểm</p>\r\n<p class=\"Lead\">Chiều 26/7, Đại học Khoa học tự nhi&ecirc;n TP HCM c&ocirc;ng bố kết quả điểm thi v&agrave; điểm chuẩn dự kiến đối với c&aacute;c ng&agrave;nh.</p>\r\n<p class=\"Normal\">Thủ khoa khối A l&agrave;&nbsp;<span style=\"color: #000000;\">Nguyễn Mạnh Tiến đạt 28,5 điểm (To&aacute;n 9,75; L&yacute; 9; H&oacute;a 9,5). Th&iacute; sinh Phạm Thị Lộc Xu&acirc;n dẫn đầu khối B với 27,5 điểm (Sinh 9; To&aacute;n 9; H&oacute;a 9,5).</span></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/E7/EA/diem1.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"357\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Th&iacute; sinh dự thi đại học 2010. Ảnh:&nbsp;<em>Hải Duy&ecirc;n.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">C&oacute; ba b&agrave;i thi đạt điểm 10 đều thuộc m&ocirc;n To&aacute;n, trong đ&oacute; khối A một b&agrave;i v&agrave; khối B hai b&agrave;i. Nhằm khuyến kh&iacute;ch tinh thần học tập, những th&iacute; sinh c&oacute; điểm thi từ 27 trở l&ecirc;n đối với khối A v&agrave; 26 trở l&ecirc;n đối với khối B đều được nh&agrave; trường tặng một suất học bổng tương đương một năm học miễn ph&iacute;. Trong suốt thời gian học, nếu th&iacute; sinh đạt kết quả tốt sẽ tiếp tục được nhận c&aacute;c học bổng kh&aacute;c của trường.</span></p>\r\n<p><span style=\"color: #000000;\"> </span></p>\r\n<p class=\"Normal\">Một số ng&agrave;nh dự kiến tuyển sinh nguyện vọng 2 của ĐH Khoa học tự nhi&ecirc;n gồm: To&aacute;n - Tin, Vật l&yacute;, C&ocirc;ng nghệ th&ocirc;ng tin, Hải dương học - Kh&iacute; tượng v&agrave; thủy văn, Khoa học vật liệu, Sinh học.</p>\r\n<p class=\"Normal\">Điểm chuẩn dự kiến của c&aacute;c ng&agrave;nh:&nbsp;<br />(Điểm c&aacute;ch biệt với c&aacute;c nh&oacute;m đối tượng l&agrave; 1 v&agrave; khu vực ưu ti&ecirc;n l&agrave; 0,5)</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">M&atilde; ng&agrave;nh</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">T&ecirc;n ng&agrave;nh</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">Khối thi</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">Chi ti&ecirc;u</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">Điểm</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">101</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">To&aacute;n - Tin</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">300</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">15</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">104</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">Vật L&yacute;</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">250</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">14.5</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">105</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">Điện tử viễn th&ocirc;ng</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">200</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">17</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">107</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">C&ocirc;ng nghệ th&ocirc;ng tin</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">550</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">18</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">109</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">Hải Dương học, Kh&iacute; tượng thủy văn</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A, B</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">50/50</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">14.5/15</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">201</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">H&oacute;a học</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">250</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">16</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">203</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">Đại chất</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A, B</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">75/75</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">14/17</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">205</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">Khoa học m&ocirc;i trường</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A, B</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">75/75</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">15.5/18</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">206</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">C&ocirc;ng nghệ m&ocirc;i trường</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A, B</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">60/60</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">16/19</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">207</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">Khoa học vật liệu</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">180</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">14</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">301</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">Sinh học</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">B</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">300</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">16</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">312</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">C&ocirc;ng nghệ sinh học</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A, B</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">70/130</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">17/21</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"5\" width=\"415\" valign=\"top\">&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" colspan=\"5\" width=\"415\" valign=\"top\">Hệ cao đẳng ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin: 800 chỉ ti&ecirc;u, lấy bằng điểm s&agrave;n.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n</strong></p>\r\n<p>&nbsp;</p>', 1, 737, 0, 1, NULL, NULL),
(96, 'Hai học sinh lớp 11 đoạt huy chương Olympic Vật lý', 'Hai-Hoc-Sinh-Lop-11-Doat-Huy-Chuong-Olympic-Vat-Ly', 'Trong số 5 học sinh Việt Nam đoạt huy chương Olympic Vật lý Quốc tế (IPHO) 41 tại Croatia, Phan Văn Trung (THPT Hà Nội - Amsterdam) và Nguyễn Hoành Đạo (Chuyên Lam Sơn, Thanh Hóa) là 2 học sinh trẻ nhất.\r\n', 'HCV.jpg', 22, '<p class=\"Title\">Hai học sinh lớp 11 đoạt huy chương Olympic Vật l&yacute;</p>\r\n<p class=\"Lead\">Trong số 5 học sinh Việt Nam đoạt huy chương Olympic Vật l&yacute; Quốc tế (IPHO) 41 tại Croatia, Phan Văn Trung (THPT H&agrave; Nội - Amsterdam) v&agrave; Nguyễn Ho&agrave;nh Đạo (Chuy&ecirc;n Lam Sơn, Thanh H&oacute;a) l&agrave; 2 học sinh trẻ nhất.</p>\r\n<p class=\"Lead\"><br />Theo th&ocirc;ng tin từ Bộ GD&amp;ĐT, em Đinh Anh Minh (lớp 12 THPT chuy&ecirc;n Quốc học Huế) đoạt huy chương V&agrave;ng, Phan Minh Trung (lớp 11, THPT Chuy&ecirc;n H&agrave; Nội - Amsterdam), Phạm B&igrave;nh Minh (lớp 12, THPT chuy&ecirc;n Trần Ph&uacute; - Hải Ph&ograve;ng) v&agrave; Phạm Văn Quyền (lớp 12, THPT chuy&ecirc;n L&ecirc; Hồng Phong - Nam Định) c&ugrave;ng đoạt huy chương Bạc. C&ograve;n Nguyễn Ho&agrave;nh Đạo (lớp 11, THPT chuy&ecirc;n Lam Sơn - Thanh Ho&aacute;) đoạt huy chương Đồng.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/E7/7D/HCV.jpg\" alt=\"Ảnh: TTXVN.\" width=\"450\" height=\"338\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đinh Anh Minh - huy chương V&agrave;ng Olympic Vật l&yacute; 41. Ảnh:&nbsp;<em>TTXVN.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Olympic Vật l&yacute; Quốc tế (IPHO) 41 tại Croatia (ng&agrave;y 17-25/7) c&oacute; 80 nước v&agrave; v&ugrave;ng l&atilde;nh thổ tham dự. Đo&agrave;n Việt Nam c&oacute; 5 học sinh tham dự. Chiều tối 27/7 đo&agrave;n sẽ về tới s&acirc;n bay Nội B&agrave;i.</p>\r\n<p class=\"Normal\">Năm nay, đo&agrave;n Việt Nam cũng gi&agrave;nh được 2 HCB v&agrave; 1 HCĐ&nbsp;<a class=\"Normal\" href=\"http://vnexpress.net/GL/Xa-hoi/Giao-duc/2010/07/3BA1E33F/\">Olympic Sinh học Quốc tế</a>&nbsp;(IBO 21, H&agrave;n Quốc), v&agrave; 1 HCV, 4 HCB, 1 HCĐ tại&nbsp;<a class=\"Normal\" href=\"http://vnexpress.net/GL/Xa-hoi/Giao-duc/2010/07/3BA1DF50/\">Olympic To&aacute;n học Quốc tế</a>&nbsp;(IMO 51, Kazakhstan).</p>\r\n<p class=\"Normal\"><strong>Tiến Dũng</strong></p>', 1, 729, 0, 1, NULL, NULL),
(97, 'Sinh viên thể thao khỏe khoắn trên sàn diễn', 'Sinh-Vien-The-Thao-Khoe-Khoan-Tren-San-Dien', 'Những sinh viên Đại học Thể dục thể thao II, TP HCM thướt tha trong những bộ áo dài hay trang phục khỏe khoắn trình diễn như người mẫu trên sàn catwalk.', 'thethao2.jpg', 22, '<h1 class=\"Title\">Sinh vi&ecirc;n thể thao khỏe khoắn tr&ecirc;n s&agrave;n diễn</h1>\r\n<h2 class=\"Lead\">Những sinh vi&ecirc;n Đại học Thể dục thể thao II, TP HCM thướt tha trong những bộ &aacute;o d&agrave;i hay trang phục khỏe khoắn tr&igrave;nh diễn như người mẫu tr&ecirc;n s&agrave;n catwalk.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao2.jpg\" border=\"1\" alt=\"Cuộc thi Miss v&agrave; Mr của trường Đại học Thể dục thể thao II, quận Thủ Đức, TP HCM, thu h&uacute;t c&aacute;c bạn sinh vi&ecirc;n tham gia nồng nhiệt. Sau v&ograve;ng loại đ&atilde; chọn ra 14 bạn v&agrave;o chung kết đ&ecirc;m 18/1.\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Cuộc thi Miss v&agrave; Mr của trường Đại học Thể dục thể thao II, quận Thủ Đức, TP HCM, thu h&uacute;t c&aacute;c bạn sinh vi&ecirc;n tham gia nồng nhiệt. Sau v&ograve;ng loại đ&atilde; chọn ra 14 bạn v&agrave;o chung kết đ&ecirc;m 18/1.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao3.jpg\" border=\"1\" alt=\"C&aacute;c th&iacute; sinh tham gia thi với trang phục &aacute;o d&agrave;i, thể thao v&agrave; phần thi năng khiếu, ứng xử.\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">C&aacute;c th&iacute; sinh tham gia thi với trang phục &aacute;o d&agrave;i, thể thao v&agrave; phần thi năng khiếu, ứng xử.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao4.jpg\" border=\"1\" alt=\"Tự tin tr&igrave;nh diễn như c&aacute;c người mẫu thực sự.\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Tự tin tr&igrave;nh diễn như c&aacute;c người mẫu thực sự.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao1.jpg\" border=\"1\" alt=\"E ấp trong trang phục &aacute;o d&agrave;i.\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">E ấp trong trang phục &aacute;o d&agrave;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao6.jpg\" border=\"1\" alt=\"Trang phục thể thao đ&uacute;ng sở trường n&ecirc;n được c&aacute;c đ&ocirc;i thể hiện rất tự tin.\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Trang phục thể thao đ&uacute;ng sở trường n&ecirc;n được c&aacute;c đ&ocirc;i thể hiện rất tự tin. Nhưng đ&ocirc;i khi ch&agrave;ng e ngại n&ecirc;n nhắm mắt trước bạn diễn nữ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao7.jpg\" border=\"1\" alt=\"Phong c&aacute;ch tr&igrave;nh diễn mang vẻ đẹp khỏe khoắn đ&uacute;ng chất sinh vi&ecirc;n ng&agrave;nh thể thao.\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Phong c&aacute;ch tr&igrave;nh diễn mang vẻ đẹp khỏe khoắn đ&uacute;ng chất sinh vi&ecirc;n ng&agrave;nh thể thao.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao8.jpg\" border=\"1\" alt=\"Tr&igrave;nh diễn c&aacute;c ca kh&uacute;c m&igrave;nh y&ecirc;u th&iacute;ch trong phần thi năng khiếu.\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Tr&igrave;nh diễn c&aacute;c ca kh&uacute;c m&igrave;nh y&ecirc;u th&iacute;ch trong phần thi năng khiếu.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao9.jpg\" border=\"1\" alt=\"Tất nhi&ecirc;n kh&ocirc;ng thiếu phần năng khiếu thể thao.\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Tất nhi&ecirc;n kh&ocirc;ng thiếu phần năng khiếu thể thao.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao10.jpg\" border=\"1\" alt=\"Nữ sinh vi&ecirc;n xinh xắn thể hiện kỹ năng b&oacute;ng rổ của m&igrave;nh.\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Nữ sinh vi&ecirc;n xinh xắn thể hiện kỹ năng b&oacute;ng rổ của m&igrave;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao13.jpg\" border=\"1\" alt=\"14 th&iacute; sinh tr&igrave;nh diễn trong khi đợi ban gi&aacute;m khảo th&ocirc;ng b&aacute;o kết quả.\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">14 th&iacute; sinh tr&igrave;nh diễn trong khi đợi ban gi&aacute;m khảo th&ocirc;ng b&aacute;o kết quả.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao11.jpg\" border=\"1\" alt=\"Danh hiệu Miss Thể thao cuối c&ugrave;ng được trao cho Lệ Hằng (khoa Y sinh), Mr thể thao d&agrave;nh cho Trung Hiếu (khoa thể thao giải tr&iacute;).\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Danh hiệu Miss Thể thao cuối c&ugrave;ng được trao cho Lệ Hằng (khoa Y sinh), Mr thể thao d&agrave;nh cho Trung Hiếu (khoa thể thao giải tr&iacute;).</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao12.jpg\" border=\"1\" alt=\"Đ&ecirc;m diễn thu h&uacute;t h&agrave;ng trăm sinh vi&ecirc;n thể thao tham dự.\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Đ&ecirc;m diễn thu h&uacute;t h&agrave;ng trăm sinh vi&ecirc;n thể thao. Chương tr&igrave;nh được t&agrave;i trợ bởi c&ocirc;ng ty Codad v&agrave; Shytex.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ki&ecirc;n Cường</strong></p>', 2, 2815, 0, 1, NULL, NULL),
(98, 'Nữ sinh báo chí đoạt giải đặc biệt cuộc thi hát tiếng Anh', 'Nu-Sinh-Bao-Chi-Doat-Giai-Dac-Biet-Cuoc-Thi-Hat-Tieng-Anh', 'Luôn dành thời gian chuẩn bị chu đáo trước khi lên sân khấu và sở hữu giọng ca mượt mà, Phan Khánh Hà, sinh viên Học viện Báo chí, đã được hội đồng giám khảo trao giải đặc biệt.', 'chung_ket_hat_ta_(5).jpg', 22, '<h1 class=\"Title\">Nữ sinh b&aacute;o ch&iacute; đoạt giải đặc biệt cuộc thi h&aacute;t tiếng Anh</h1>\r\n<h2 class=\"Lead\">Lu&ocirc;n d&agrave;nh thời gian chuẩn bị chu đ&aacute;o trước khi l&ecirc;n s&acirc;n khấu v&agrave; sở hữu giọng ca mượt m&agrave;, Phan Kh&aacute;nh H&agrave;, sinh vi&ecirc;n Học viện B&aacute;o ch&iacute;, đ&atilde; được hội đồng gi&aacute;m khảo trao giải đặc biệt.</h2>\r\n<p class=\"Normal\">hung kết cuộc thi h&aacute;t tiếng Anh Let\'s get loud diễn ra tại H&agrave; Nội v&agrave;o tối 16/1, hai th&iacute; sinh được trao giải đặc biệt (một do hội đồng gi&aacute;m khảo v&agrave; một do kh&aacute;n giả b&igrave;nh chọn), một th&iacute; sinh được trao giải nhất. Tổng gi&aacute; trị giải thưởng l&agrave; 300 triệu đồng.</p>\r\n<p class=\"Normal\">Chọn ca kh&uacute;c&nbsp;<em>The prayer</em>, Kh&aacute;nh H&agrave;, sinh vi&ecirc;n Khoa Quan hệ quốc tế, Học viện B&aacute;o ch&iacute; v&agrave; Tuy&ecirc;n truyền, đ&atilde; khoe được giọng ca ngọt ng&agrave;o, s&acirc;u lắng v&agrave; lối diễn xuất c&oacute; hồn. Phần minh họa v&agrave; h&aacute;t b&egrave; của c&aacute;c em nhỏ c&acirc;u lạc bộ Chim Sơn Ca khiến cho tiết mục của c&ocirc; sinh vi&ecirc;n b&aacute;o ch&iacute; đi v&agrave;o l&ograve;ng người.</p>\r\n<p class=\"Normal\">H&agrave; cho biết h&aacute;t tiếng Anh với c&ocirc; l&agrave; đam m&ecirc;. Ngay từ nhỏ, c&ocirc; đ&atilde; m&agrave;y m&ograve; học h&aacute;t theo c&aacute;c ca sĩ tr&ecirc;n tivi v&agrave; được học th&ecirc;m tại nh&agrave; văn h&oacute;a. Những l&uacute;c rảnh rỗi, H&agrave; đều học tiếng Anh v&agrave; tập h&aacute;t bởi \"muốn l&agrave;m một nh&agrave; ngoại giao th&igrave; trước hết phải giỏi tiếng Anh\".</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/57/chung_ket_hat_ta_(5).jpg\" alt=\"Phan Kh&aacute;nh H&agrave;\" width=\"495\" height=\"353\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Phan Kh&aacute;nh H&agrave; đ&atilde; d&agrave;nh được giải đặc biệt do ban gi&aacute;m khảo b&igrave;nh chọn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Nhận giải đặc biệt do kh&aacute;n giả b&igrave;nh chọn, Nguyễn Duy T&ugrave;ng vỡ &ograve;a trong niềm hạnh ph&uacute;c. Cậu t&acirc;m sự, khi tất cả c&aacute;c bạn đều đ&atilde; c&oacute; giải, chỉ một m&igrave;nh đứng tr&ecirc;n s&acirc;n khấu vẫn \"tay kh&ocirc;ng\", T&ugrave;ng thấy rất c&ocirc; đơn. \"Nếu kh&ocirc;ng phải con trai th&igrave; em đ&atilde; kh&oacute;c v&igrave; tủi th&acirc;n\", T&ugrave;ng t&acirc;m sự.</p>\r\n<p class=\"Normal\">Nhưng anh ch&agrave;ng k&iacute;nh cận Duy T&ugrave;ng đ&atilde; chinh phục được kh&aacute;n giả với sự đa t&agrave;i của m&igrave;nh. Tự đệm đ&agrave;n ghi ta thể hiện ca kh&uacute;c&nbsp;<em>One more Try&nbsp;</em>nhẹ nh&agrave;ng, trữ t&igrave;nh, T&ugrave;ng đ&atilde; tiếp tục khẳng định h&igrave;nh ảnh trẻ trung v&agrave; \"đa di năng\" của m&igrave;nh ở chặng thi cuối.</p>\r\n<p class=\"Normal\">Cuộc thi &ldquo;Let&rsquo;s Get Loud&rdquo; do đ&agrave;i Truyền h&igrave;nh Việt Nam, Tập đo&agrave;n gi&aacute;o dục quốc tế Language Link, Trung t&acirc;m hỗ trợ, ph&aacute;t triển sinh vi&ecirc;n Việt Nam... phối hợp tổ chức. Đ&acirc;y l&agrave; cuộc thi h&aacute;t tiếng Anh được mong chờ d&agrave;nh cho học sinh, sinh vi&ecirc;n H&agrave; Nội.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y - B&aacute; Đ&ocirc;</strong></p>\r\n<p>&nbsp;</p>', 2, 2963, 0, 1, NULL, NULL),
(99, 'Chàng sinh viên có \'bàn tay vàng\'', '', 'Đoạt giải nhất hội thi tay nghề thành phố, Hoàng Văn Tùng đã xuất sắc giành vị trí số 1 tại hội thi tay nghề quốc gia và ASEAN.', 'tung.jpg', 22, '<h1 class=\"Title\">Ch&agrave;ng sinh vi&ecirc;n c&oacute; \'b&agrave;n tay v&agrave;ng\'</h1>\r\n<h2 class=\"Lead\">Đoạt giải nhất hội thi tay nghề th&agrave;nh phố, Ho&agrave;ng Văn T&ugrave;ng đ&atilde; xuất sắc gi&agrave;nh vị tr&iacute; số 1 tại hội thi tay nghề quốc gia v&agrave; ASEAN.</h2>\r\n<p class=\"Normal\">L&agrave; con nh&agrave; n&ocirc;ng ở v&ugrave;ng qu&ecirc; ngh&egrave;o Ngũ Ki&ecirc;n (Vĩnh Tường, Vĩnh Ph&uacute;c), lại l&agrave; anh cả của gia đ&igrave;nh c&oacute; 3 anh em, T&ugrave;ng được bố mẹ kỳ vọng tho&aacute;t ly đồng &aacute;ng, gi&uacute;p đỡ em ăn học.</p>\r\n<p class=\"Normal\">Do kinh tế kh&oacute; khăn n&ecirc;n bố mẹ T&ugrave;ng muốn cậu thi v&agrave;o sư phạm cho đỡ tiền học ph&iacute;. Thiếu một điểm để v&agrave;o ĐH Sư phạm, cậu nộp hồ sơ v&agrave;o Cao đẳng C&ocirc;ng nghiệp v&agrave; dường như đ&acirc;y mới l&agrave; mảnh đất c&oacute; duy&ecirc;n với T&ugrave;ng.</p>\r\n<p class=\"Normal\">V&agrave;o Khoa Cơ kh&iacute;, cứ nửa ng&agrave;y học l&yacute; thuyết tr&ecirc;n lớp, nửa ng&agrave;y T&ugrave;ng lại c&ugrave;ng c&aacute;c bạn v&agrave;o ph&ograve;ng thực h&agrave;nh để học th&ecirc;m. Khi rảnh rỗi, những đồ điện tử trong nh&agrave; cũng được cậu th&aacute;o ra để t&igrave;m t&ograve;i.</p>\r\n<p class=\"Normal\">Nghe thầy chủ nhiệm giới thiệu về cuộc thi tay nghề ở trường, T&ugrave;ng đăng k&yacute; tham gia. \"L&uacute;c đ&oacute; em chỉ nghĩ đ&oacute; l&agrave; s&acirc;n chơi, m&igrave;nh sẽ học hỏi được nhiều điều\", T&ugrave;ng t&acirc;m sự.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/9D/tung.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">T&ugrave;ng l&agrave; sinh vi&ecirc;n xuất sắc gi&agrave;nh huy chương v&agrave;ng hội thi tay nghề từ cấp th&agrave;nh phố đến khu vực Asean.&nbsp;<em>Ảnh: Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Được chọn v&agrave;o đội tuyển dự thi cấp th&agrave;nh phố, T&ugrave;ng mừng lắm, ra sức &ocirc;n luyện c&ugrave;ng c&aacute;c bạn. Niềm vui vỡ &ograve;a khi T&ugrave;ng gi&agrave;nh giải nhất hội thi tay nghề cấp quốc gia. Bố mẹ cậu cũng rớt nước mắt khi hay tin đứa con lớn l&ecirc;n từ dưa c&agrave;, mắm muối \"bỗng dưng\" đạt th&agrave;nh t&iacute;ch xuất sắc.</p>\r\n<p class=\"Normal\">Được chọn l&agrave; đại diện dự hội thi tay nghề ASEAN nghề vẽ v&agrave; thiết kế tr&ecirc;n m&aacute;y t&iacute;nh năm 2010, T&ugrave;ng lại tiếp tục đem về vinh quang khi vượt qua c&aacute;c đối thủ để gi&agrave;nh huy chương v&agrave;ng. T&ugrave;ng cho hay, thi ở trong nước chỉ l&agrave;m một b&agrave;i trong một ng&agrave;y, c&ograve;n thi ở khu vực mỗi th&iacute; sinh phải l&agrave;m ba b&agrave;i trong ba ng&agrave;y.</p>\r\n<p class=\"Normal\">\"Đ&oacute; l&agrave; c&aacute;ch để ban tổ chức kiểm tra sự to&agrave;n diện của th&iacute; sinh th&ocirc;ng qua c&aacute;c b&agrave;i thi. Em thấy m&igrave;nh l&agrave; người may mắn v&igrave; đ&atilde; vững t&acirc;m l&agrave;m tốt cả ba\", T&ugrave;ng t&acirc;m sự.</p>\r\n<p class=\"Normal\">Li&ecirc;n tiếp gi&agrave;nh giải nhất từ cấp th&agrave;nh phố đến khu vực, hiện nay T&ugrave;ng chuẩn bị cho kỳ thi tay nghề thế giới. Ch&agrave;ng trai c&oacute; \"b&agrave;n tay v&agrave;ng\" cho hay, điều quan trọng nhất để chiến thắng kh&ocirc;ng phải giỏi vượt trội so với c&aacute;c th&iacute; sinh m&agrave; l&agrave; yếu tố t&acirc;m l&yacute;. Nếu như t&acirc;m l&yacute; thoải m&aacute;i th&igrave; tư duy sẽ mạch lạc v&agrave; nhanh ch&oacute;ng. Đ&oacute; cũng l&agrave; b&iacute; quyết th&agrave;nh c&ocirc;ng của T&ugrave;ng.</p>\r\n<p class=\"Normal\">Thầy Nguyễn Quốc Tuấn, gi&aacute;o vi&ecirc;n &ocirc;n luyện đội tuyển dự thi tay nghề của ĐH C&ocirc;ng nghiệp đ&aacute;nh gi&aacute;, so với c&aacute;c bạn, T&ugrave;ng kh&ocirc;ng phải l&agrave; học tr&ograve; xuất sắc, nhưng với sự tự tin cậu c&oacute; thể đ&aacute;nh bật tất cả. \"T&ocirc;i giao đề b&agrave;i kh&oacute;, nhiều sinh vi&ecirc;n do dự, nhưng T&ugrave;ng lu&ocirc;n bắt tay v&agrave;o l&agrave;m rất tự tin. Đ&oacute; ch&iacute;nh l&agrave; điều t&ocirc;i mong tất cả sinh vi&ecirc;n của m&igrave;nh c&oacute; được\", thầy Tuấn n&oacute;i.</p>\r\n<p class=\"Normal\">Với những th&agrave;nh t&iacute;ch đạt được, T&ugrave;ng vừa được Th&agrave;nh đo&agrave;n H&agrave; Nội chọn l&agrave; một trong 10 gương mặt trẻ thủ đ&ocirc; ti&ecirc;u biểu, nhận bằng khen của Bộ trưởng Lao động Thương binh v&agrave; x&atilde; hội năm 2010, hai bằng khen của Chủ tịch UBND th&agrave;nh phố H&agrave; Nội năm 2009, 2010.</p>\r\n<p class=\"Normal\">\"Em vừa tốt nghiệp cao đẳng loại kh&aacute; n&ecirc;n được thi li&ecirc;n th&ocirc;ng l&ecirc;n đại học. Thời gian tới, em sẽ cố gắng học tập, t&iacute;ch lũy kiến thức v&agrave; kinh nghiệm để c&oacute; h&agrave;nh trang tốt nhất khi ra trường\", T&ugrave;ng cho hay.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 2, 3, 0, 1, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(100, 'Vũ điệu bốc lửa của các cặp nhảy hàng đầu Việt Nam', 'Vu-Dieu-Boc-Lua-Cua-Cac-Cap-Nhay-Hang-Dau-Viet-Nam', 'Tối 14/1 các ngôi sao khiêu vũ thể thao Khánh Thi, Chí Anh... đã làm nóng nhà thi đấu Trịnh Hoài Đức (Hà Nội) bằng những vũ điệu bốc lửa, lãng mạn.', 'khieuvu7.jpg', 22, '<h1 class=\"Title\">Vũ điệu bốc lửa của c&aacute;c cặp nhảy h&agrave;ng đầu Việt Nam</h1>\r\n<h2 class=\"Lead\">Tối 14/1 c&aacute;c ng&ocirc;i sao khi&ecirc;u vũ thể thao Kh&aacute;nh Thi, Ch&iacute; Anh... đ&atilde; l&agrave;m n&oacute;ng nh&agrave; thi đấu Trịnh Ho&agrave;i Đức (H&agrave; Nội) bằng những vũ điệu bốc lửa, l&atilde;ng mạn.</h2>\r\n<p>Dưới đ&acirc;y l&agrave; một số tiết mục đặc sắc tại \"Li&ecirc;n hoan c&aacute;c ng&ocirc;i sao Khi&ecirc;u vũ Thể thao Việt Nam 2010\"</p>\r\n<p>&nbsp;</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/56/CC/khieuvu7.jpg\" alt=\"Ảnh: L&ecirc; Hiếu.\" width=\"380\" height=\"565\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cặp đ&ocirc;i Kh&aacute;nh Thi - Minh Trường trong điệu Rumba trữ t&igrave;nh. Ảnh:<em>&nbsp;L&ecirc; Hiếu.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em>&nbsp;</em></p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>B&aacute; Đ&ocirc;</strong></p>\r\n<p>&nbsp;</p>', 2, 1080, 0, 1, NULL, NULL),
(101, 'Muôn kiểu chống rét của sinh viên', 'Muon-Kieu-Chong-Ret-Cua-Sinh-Vien', 'Lấy máy sấy sấy cho chăn ấm lên trước khi đi ngủ, thắp bóng điện dây tóc công suất lớn hoặc đổ nước ấm vào chai để ôm là những cách để chống rét của sinh viên Hà Nội.', 'retmoi.jpg', 22, '<h1 class=\"Title\">Mu&ocirc;n kiểu chống r&eacute;t của sinh vi&ecirc;n</h1>\r\n<h2 class=\"Lead\">Lấy m&aacute;y sấy sấy cho chăn ấm l&ecirc;n trước khi đi ngủ, thắp b&oacute;ng điện d&acirc;y t&oacute;c c&ocirc;ng suất lớn hoặc đổ nước ấm v&agrave;o chai để &ocirc;m l&agrave; những c&aacute;ch để chống r&eacute;t của sinh vi&ecirc;n H&agrave; Nội.</h2>\r\n<p class=\"Normal\">10 ng&agrave;y nay r&eacute;t đậm k&eacute;o d&agrave;i, sinh vi&ecirc;n ở k&yacute; t&uacute;c x&aacute; hay thu&ecirc; trọ nh&aacute;o nh&agrave;o t&igrave;m c&aacute;ch giữ ấm. Ra đường th&igrave; quần &aacute;o, mũ m&atilde;o, găng tay che k&iacute;n, ai cũng thực hiện nguy&ecirc;n tắc \"chỉ để hở hai con mắt\" cho khỏi r&eacute;t.</p>\r\n<p class=\"Normal\">\"Chỉ cần hở chỗ n&agrave;o l&agrave; lạnh buốt chỗ đ&oacute; n&ecirc;n bọn em cố gắng tiết kiệm tiền để sắm cho đầy đủ phụ kiện như mũ len, găng tay, bịt tai, khăn, &aacute;o. C&aacute;c bạn ở xa đi học bằng xe m&aacute;y c&oacute; khi c&ograve;n mặc th&ecirc;m &aacute;o mưa, đội mũ bảo hiểm c&oacute; k&iacute;nh chắn gi&oacute; nữa\", Hạnh Hoa (Học viện B&aacute;o ch&iacute;) cho biết.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/BA/retmoi.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">D&ugrave; phải chắt b&oacute;p chi ti&ecirc;u, sinh vi&ecirc;n vẫn cố gắng sắm đủ đồ giữ ấm như bịt tai, găng tay, mũ len. Ảnh:&nbsp;<em>Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Những d&atilde;y trọ thường n&oacute;ng về m&ugrave;a h&egrave;, lạnh về m&ugrave;a đ&ocirc;ng n&ecirc;n c&aacute;c cử nh&acirc;n tương lai đ&atilde; truyền tai nhau c&aacute;ch giữ ấm mới được \"ph&aacute;t minh\". \"Em c&oacute; m&aacute;y sấy t&oacute;c, m&ugrave;a đ&ocirc;ng được sử dụng tối đa v&igrave; hết sấy tay ch&acirc;n cho khỏi c&oacute;ng, trước l&uacute;c đi ngủ n&oacute; c&ograve;n được trưng dụng để sấy chăn, tạo hơi ấm ở b&ecirc;n trong\", Thu C&uacute;c (sinh vi&ecirc;n ĐH Thương mại) kể.</p>\r\n<p class=\"Normal\">Theo C&uacute;c, sau khi sấy chăn phồng l&ecirc;n v&agrave; n&oacute;ng dần, tỏa hơi ấm ra xung quanh. Nếu như trước đ&acirc;y nằm v&agrave;o chăn, c&ocirc; phải xu&yacute;t xoa mất một l&uacute;c để ủ ấm th&igrave; b&acirc;y giờ c&oacute; thể thoải m&aacute;i ngủ đến s&aacute;ng. C&uacute;c đ&atilde; th&ocirc;ng b&aacute;o \"s&aacute;ng kiến\" n&agrave;y cho cả x&oacute;m trọ. Vậy l&agrave; đ&ecirc;m n&agrave;o, chiếc m&aacute;y sấy cũng được cả x&oacute;m chuyền tay nhau.</p>\r\n<p class=\"Normal\">Trời r&eacute;t, mặc năm chiếc &aacute;o ấm m&agrave; Ho&agrave;ng Thảo (ĐH Y H&agrave; Nội) vẫn chưa hết run. Ngồi học b&agrave;i nhưng tay lạnh c&oacute;ng n&ecirc;n Thảo chẳng viết được g&igrave;. Đứng dậy đ&oacute;ng hết cửa sổ, Thảo đem hết c&aacute;c b&oacute;ng đ&egrave;n học của mấy đứa bạn trong ph&ograve;ng ra thắp.</p>\r\n<p class=\"Normal\">\"B&oacute;ng điện d&acirc;y t&oacute;c khi thắp l&ecirc;n sẽ tỏa nhiệt rất ấm. Đứa bạn lớp m&igrave;nh c&oacute; s&aacute;ng kiến bật đ&egrave;n rồi đưa v&agrave;o trong chăn cho ấm, nhưng m&igrave;nh sợ r&ograve; rỉ điện n&ecirc;n kh&ocirc;ng d&aacute;m l&agrave;m\", Thảo chia sẻ.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/BA/retmoi1.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Sấy chăn cho phồng l&ecirc;n v&agrave; ấm n&oacute;ng l&agrave; một trong nhiều chi&ecirc;u giữ ấm của sinh vi&ecirc;n. Ảnh<em>: Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Ở trọ trong ng&otilde; 315 đường Nguyễn Khang (Cầu Giấy), Nguyễn Thị Toan v&agrave; hai người bạn tối n&agrave;o cũng đun một si&ecirc;u nước n&oacute;ng. Trong ph&ograve;ng, Toan d&agrave;nh ri&ecirc;ng một g&oacute;c xếp h&agrave;ng chục vỏ chai lavi. \"Nhớ lại ng&agrave;y ở nh&agrave; mẹ thường đổ nước ấm v&agrave;o chai để chườm chỗ sưng, thế l&agrave; bọn em đun nước, để nguội một ch&uacute;t v&agrave; đổ v&agrave;o chai lavi rồi &ocirc;m ch&uacute;ng khi ngủ. Đ&uacute;ng l&agrave; s&aacute;ng kiến của &ocirc;ng b&agrave; thật hữu hiệu, bọn em ngủ rất ngon l&agrave;nh\", Toan vui vẻ cho hay.</p>\r\n<p class=\"Normal\">C&ocirc; cho biết, trong x&oacute;m c&oacute; một số người đ&atilde; l&agrave;m theo c&aacute;ch n&agrave;y, nhưng được v&agrave;i h&ocirc;m th&igrave; ngại đun nước n&ecirc;n t&igrave;m mua t&uacute;i chườm n&oacute;ng đa năng với gi&aacute; khoảng 80.000-90.000 đồng. Trước khi đi ngủ cắm điện khoảng 5 đến 10 ph&uacute;t, sau đ&oacute; &ocirc;m t&uacute;i đi ngủ sẽ ấm cả đ&ecirc;m.</p>\r\n<p class=\"Normal\">D&ugrave;ng một chăn b&ocirc;ng l&agrave;m đệm, tr&ecirc;n l&agrave; hai chiếc chăn đắp, nhưng \"đ&ocirc;i bạn c&ugrave;ng tiến\" H&ugrave;ng v&agrave; Khoa (Học viện T&agrave;i ch&iacute;nh) vẫn thấy lạnh n&ecirc;n phải nghĩ c&aacute;ch sưởi ấm căn ph&ograve;ng. \"Ch&uacute;ng em vừa bước sang học kỳ mới. Do đăng k&yacute; t&iacute;n chỉ n&ecirc;n học rất vất vả, nếu kh&ocirc;ng cẩn thận th&igrave; thi lại, học lại mệt lắm, lại tốn tiền nữa. Tụi em đ&agrave;nh hy sinh chiếc chậu nh&ocirc;m v&agrave; đi mua &iacute;t củi về đốt\", H&ugrave;ng cho hay.</p>\r\n<p class=\"Normal\">\"D&ugrave; tốn th&ecirc;m ch&uacute;t tiền nhưng được c&aacute;i ấm c&uacute;ng. Cả x&oacute;m chỉ c&oacute; ph&ograve;ng em l&agrave; l&uacute;c n&agrave;o cũng đỏ lửa\", Khoa cười.</p>\r\n<p class=\"Normal\">Bắt đầu từ ng&agrave;y 2/1 v&agrave; dự b&aacute;o k&eacute;o d&agrave;i &iacute;t nhất đến 21/1, đợt r&eacute;t đậm, r&eacute;t hại đang ho&agrave;nh h&agrave;nh ở miền Bắc được đ&aacute;nh gi&aacute; chỉ đứng sau đợt r&eacute;t lịch sử đầu năm 2008 k&eacute;o d&agrave;i 38 ng&agrave;y. Nhiệt độ thấp nhất ở miền Bắc đ&atilde; xuống xấp xỉ -4 độ, tại H&agrave; Nội l&agrave; 8 độ C.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>\r\n<p>&nbsp;</p>', 2, 782, 0, 1, NULL, NULL),
(102, '10 gương mặt chung kết \'Hot VTeen 2010\'', '', 'Sau gần một năm khởi động, cuộc thi tìm kiếm chân dung tuổi teen Việt Nam đã tìm ra các thí sinh xuất sắc. Các bạn sẽ thể hiện tài năng trong đêm chung kết quốc gia tại Nhà văn hóa sinh viên TP HCM, tối 14/1.', 'hotteen-1.jpg', 22, '<h1 class=\"Title\">10 gương mặt chung kết \'Hot VTeen 2010\'</h1>\r\n<h2 class=\"Lead\">Sau gần một năm khởi động, cuộc thi t&igrave;m kiếm ch&acirc;n dung tuổi teen Việt Nam đ&atilde; t&igrave;m ra c&aacute;c th&iacute; sinh xuất sắc. C&aacute;c bạn sẽ thể hiện t&agrave;i năng trong đ&ecirc;m chung kết quốc gia tại Nh&agrave; văn h&oacute;a sinh vi&ecirc;n TP HCM, tối 14/1.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/2E/hotteen-1.jpg\" border=\"1\" alt=\"Đo&agrave;n Hương Linh đến từ H&agrave; Nội.\" width=\"400\" height=\"588\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Sau đ&ecirc;m thi cuối, kết quả chung cuộc sẽ được c&ocirc;ng bố trong đ&ecirc;m Gala Hot VTeen diễn ra tại s&acirc;n khấu Lan Anh v&agrave;o ng&agrave;y 22/1. Trong ảnh l&agrave; Đo&agrave;n Hương Linh - th&iacute; sinh duy nhất đến từ H&agrave; Nội. L&agrave; Ủy vi&ecirc;n BCH đo&agrave;n trường n&ecirc;n nữ sinh rất năng động trong c&aacute;c hoạt động v&agrave; l&agrave; một c&acirc;y văn nghệ của trường với t&agrave;i h&aacute;t, nhảy, đ&agrave;n v&agrave; diễn kịch.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/2E/hotteen-2.jpg\" border=\"1\" alt=\"Ho&agrave;ng Minh Tuấn đến từ trường CĐ Văn h&oacute;a nghệ thuật TP HCM.\" width=\"400\" height=\"563\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Th&iacute; sinh Ho&agrave;ng Minh Tuấn đến từ trường CĐ Văn h&oacute;a nghệ thuật TP HCM. Năng khiếu của Tuấn l&agrave; Beatbox v&agrave; h&aacute;t.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/2E/hotteen-3.jpg\" border=\"1\" alt=\"Huỳnh Bảo sinh vi&ecirc;n trường Ngoại ngữ tin học TP HCM.\" width=\"400\" height=\"570\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Huỳnh Bảo - sinh vi&ecirc;n trường Ngoại ngữ tin học TP HCM. Cậu đặc biệt c&oacute; năng khiếu dẫn chương tr&igrave;nh v&agrave; diễn kịch.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/2E/Phuong-Thao.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"586\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">L&ecirc; Phương Thảo - sinh vi&ecirc;n trường Saigon Tech. C&ocirc; từng g&acirc;y ấn tượng với kh&aacute;n giả v&agrave; ban gi&aacute;m khảo tại v&ograve;ng chung kết khu vực bởi t&agrave;i nấu m&oacute;n s&uacute;p ngay tr&ecirc;n s&acirc;n khấu rồi mời ban gi&aacute;m khảo thưởng thức tại chỗ. C&oacute; nụ cười tươi v&agrave; gương mặt dễ thương, Thảo từng l&agrave;m người mẫu ảnh cho nhiều tạp ch&iacute;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/2E/hotteen-8.jpg\" border=\"1\" alt=\"Trần Thị Kim Anh sinh vi&ecirc;n trường ĐH H&ugrave;ng Vương TP HCM.\" width=\"387\" height=\"556\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Trần Thị Kim Anh - sinh vi&ecirc;n ĐH H&ugrave;ng Vương TP HCM. Oanh tự tin m&igrave;nh c&oacute; nụ cười rất duy&ecirc;n bởi hai chiếc răng khểnh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/2E/hotteen-4.jpg\" border=\"1\" alt=\"Trần Hồng Ngọc học sinh lớp 11 trường PTTH Ph&uacute; Nhuận.\" width=\"400\" height=\"593\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Trần Hồng Ngọc hiện l&agrave; học sinh lớp 11 trường PTTH Ph&uacute; Nhuận. 10 năm liền l&agrave; học sinh giỏi, Hồng Ngọc cũng vừa đạt Huy chương đồng Olympic m&ocirc;n Văn năm 2010. Nữ sinh c&oacute; thể n&oacute;i tiếng Anh lưu lo&aacute;t v&agrave; thể hiện t&agrave;i năng dẫn chương tr&igrave;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/2E/hotteen-9.jpg\" border=\"1\" alt=\"Nguyễn Ngọc Quang Bảo sinh vi&ecirc;n trường ĐH Thể dục thể thao.\" width=\"400\" height=\"583\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nguyễn Ngọc Quang Bảo - sinh vi&ecirc;n trường ĐH Thể dục thể thao. L&agrave; kiện tướng quốc gia về m&ocirc;n bơi lội, Bảo từng tham gia v&agrave; đạt nhiều huy chương tại c&aacute;c giải đấu trong nước v&agrave; quốc tế.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/2E/hong-ngoc.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"588\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nguyễn Hồng Ngọc l&agrave; th&iacute; sinh duy nhất đến từ Đ&agrave; Nẵng. Năng khiếu nổi bật của Ngọc l&agrave; m&uacute;a.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/2E/hotteen-6.jpg\" border=\"1\" alt=\"Tạ B&iacute;ch Ngọc sinh vi&ecirc;n trường CĐ Y tế Cần Thơ.\" width=\"400\" height=\"574\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tạ B&iacute;ch Ngọc l&agrave; sinh vi&ecirc;n trường CĐ Y tế Cần Thơ. Nữ sinh g&acirc;y ấn tượng với kh&aacute;n giả trẻ bởi nụ cười duy&ecirc;n, nước da ngăm đen v&agrave; giọng h&aacute;t hay.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/2E/hotteen-7.jpg\" border=\"1\" alt=\"L&ecirc; Ho&agrave;ng Giang học sinh lớp 12 chuy&ecirc;n Tiền Giang.\" width=\"400\" height=\"585\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Th&iacute; sinh L&ecirc; Ho&agrave;ng Giang l&agrave; học sinh lớp 12 chuy&ecirc;n Tiền Giang. Cậu g&acirc;y ngạc nhi&ecirc;n cho ban gi&aacute;m khảo v&agrave; kh&aacute;n giả bởi bảng th&agrave;nh t&iacute;ch d&agrave;y về c&aacute;c giải thưởng học sinh giỏi xuất sắc cấp huyện, tỉnh. Ngo&agrave;i ra Giang c&ograve;n l&agrave; một ch&agrave;ng trai rất đa t&agrave;i ở c&aacute;c lĩnh vực v&otilde; thuật, diễn kịch v&agrave; m&uacute;a.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>B&igrave;nh Nguy&ecirc;n&nbsp;<br /></strong><em>Ảnh</em><strong>: H. T</strong></p>', 2, 3, 0, 1, NULL, NULL),
(103, 'Miss Teen Diễm Trang khoe vẻ đẹp khỏe khoắn', 'Miss-Teen-Diem-Trang-Khoe-Ve-Dep-Khoe-Khoan', 'Áo thun, quần jeans bó sát, Ngôi sao tuổi teen 2010 tươi cười trẻ trung trên phố Sài Gòn.', '', 22, '<h1 class=\"Title\">Miss Teen Diễm Trang khoe vẻ đẹp khỏe khoắn</h1>\r\n<h2 class=\"Lead\">&Aacute;o thun, quần jeans b&oacute; s&aacute;t, Ng&ocirc;i sao tuổi teen 2010 tươi cười trẻ trung tr&ecirc;n phố S&agrave;i G&ograve;n.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/C9/diem-trang-1.jpg\" border=\"1\" alt=\"L&agrave; sinh vi&ecirc;n năm 2 ĐH Quốc tế TP HCM, nữ sinh trinh phục ban gi&aacute;m khảo về t&agrave;i năng thuyết tr&igrave;nh tiếng Anh lưu lo&aacute;t v&agrave; trở th&agrave;nh chủ nh&acirc;n của danh hiệu Miss teen 2010.\" width=\"400\" height=\"600\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">L&agrave; sinh vi&ecirc;n năm 2 ĐH Quốc tế TP HCM, nữ sinh trở th&agrave;nh người đạt danh hiệu cao nhất cuộc thi Ng&ocirc;i sao tuổi teen 2010 khi chinh phục ban gi&aacute;m khảo bằng t&agrave;i năng thuyết tr&igrave;nh tiếng Anh lưu lo&aacute;t v&agrave; khả năng m&uacute;a ấn tượng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/C9/diem-trang-2.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"584\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Diễm Trang tự nhận nếu chỉ x&eacute;t về vẻ đẹp h&igrave;nh thức c&oacute; lẽ c&ocirc; đ&atilde; kh&ocirc;ng đạt danh hiệu Miss Teen \"v&igrave; rất nhiều bạn trẻ xinh đẹp hơn em\".</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/C9/diem-trang-3.jpg\" border=\"1\" alt=\"Mặc d&ugrave; đạt danh hiệu cao nhất, nhưng Diễm Trang cảm thấy vẫn c&oacute; ch&uacute;t nuối tiếc khi cuộc thi kết th&uacute;c. \" width=\"400\" height=\"582\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Mặc d&ugrave; đạt danh hiệu cao nhất, nhưng Diễm Trang vẫn cảm thấy vẫn c&oacute; ch&uacute;t nuối tiếc khi cuộc thi kết th&uacute;c v&igrave; chưa thực sự tạo được ấn tượng mạnh đối với kh&aacute;n giả ở c&aacute;c v&ograve;ng thi.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/C9/diem-trang-4.jpg\" border=\"1\" alt=\"Nữ sinh cho biết mong muốn sau n&agrave;y của c&ocirc; muốn trở th&agrave;nh một doanh nh&acirc;n th&agrave;nh đạt.\" width=\"480\" height=\"379\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đ&oacute; cũng l&agrave; l&yacute; do khiến c&ocirc; &iacute;t xuất hiện hơn so với những Miss Teen trước đ&oacute; sau khi đăng quang. Nữ sinh cho biết cũng kh&aacute; bận rộn với việc học h&agrave;nh v&agrave; tham gia c&aacute;c hoạt động ngoại kh&oacute;a về kỹ năng sống.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/C9/diem-trang-6.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"577\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Học chuy&ecirc;n ng&agrave;nh Quản trị kinh doanh, Diễm Trang chia sẻ mong muốn của c&ocirc; l&agrave; trở th&agrave;nh một nữ doanh nh&acirc;n th&agrave;nh đạt.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/C9/diem-trang-7.jpg\" border=\"1\" alt=\"Tuy nhi&ecirc;n c&ocirc; cũng kh&ocirc;ng qu&aacute; cứng nhắc trong việc thực hiện ước mơ của m&igrave;nh bằng một con đường, m&agrave; sẽ thử sức ở c&aacute;c lĩnh vực kh&aacute;c khi c&oacute; cơ hội.\" width=\"400\" height=\"600\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tuy nhi&ecirc;n c&ocirc; cho rằng điều đ&oacute; kh&ocirc;ng c&oacute; nghĩa c&ocirc; chỉ c&oacute; thể đi theo một con đường để đến đ&iacute;ch, m&agrave; sẽ thử sức ở c&aacute;c lĩnh vực kh&aacute;c khi c&oacute; cơ hội.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/C9/diem-trang-10.jpg\" border=\"1\" alt=\"Diem trang\" width=\"400\" height=\"592\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Diễm Trang thừa nhận từ khi trở th&agrave;nh Miss Teen c&ocirc; biết c&aacute;ch l&agrave;m điệu v&agrave; ch&uacute; &yacute; nhiều hơn đến vẻ đẹp ngoại h&igrave;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/C9/diem-trang-12.jpg\" border=\"1\" alt=\"Diễm Trang\" width=\"400\" height=\"572\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&ocirc; cũng sẽ \"củng cố\" h&igrave;nh ảnh của m&igrave;nh trong mắt kh&aacute;n giả bởi từng c&oacute; những &yacute; kiến cho rằng Miss Teen năm nay gi&agrave; hơn v&agrave; kh&ocirc;ng đẹp như Miss Teen trước đ&oacute;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/C9/diem-trang-13.jpg\" border=\"1\" alt=\"Diem trang\" width=\"400\" height=\"591\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">\"Một năm đăng quang em tin m&igrave;nh sẽ l&agrave;m tốt vai tr&ograve; của Miss Teen v&agrave; bản th&acirc;n cũng cần phải c&oacute; tr&aacute;ch nhiệm với danh hiệu n&agrave;y\" Trang n&oacute;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/C9/diem-trang-9.jpg\" border=\"1\" alt=\"Diễm Trang\" width=\"480\" height=\"362\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ph&uacute;t nh&iacute; nhảnh của Diễm Trang được nhiếp ảnh Thanh Sơn chộp lại trong l&uacute;c tạo d&aacute;ng tr&ecirc;n phố để thực hiện bộ ảnh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>B&igrave;nh Nguy&ecirc;n</strong></p>', 2, 118, 0, 1, NULL, NULL),
(104, 'Nữ sinh cao đẳng khoe nét duyên', 'Nu-Sinh-Cao-Dang-Khoe-Net-Duyen', '25 gương mặt của CĐ Nguyễn Tất Thành (TP HCM) thể hiện tài năng và vẻ thanh lịch trong cuộc thi tìm Hoa khôi của trường vào cuối tuần qua.', '', 22, '<h1 class=\"Title\">Nữ sinh cao đẳng khoe n&eacute;t duy&ecirc;n</h1>\r\n<h2 class=\"Lead\">25 gương mặt của CĐ Nguyễn Tất Th&agrave;nh (TP HCM) thể hiện t&agrave;i năng v&agrave; vẻ thanh lịch trong cuộc thi t&igrave;m Hoa kh&ocirc;i của trường v&agrave;o cuối tuần qua.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/52/5F/miss-6.jpg\" border=\"1\" alt=\"Phần thể hiện trang phục truyền thống của th&iacute; sinh.\" width=\"400\" height=\"525\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cuộc thi l&agrave; hoạt động h&agrave;ng năm của đo&agrave;n trường nhằm tạo s&acirc;n chơi bổ &iacute;ch, t&ocirc;n vinh vẻ đẹp h&igrave;nh thể, tr&iacute; tuệ v&agrave; phong c&aacute;ch của sinh vi&ecirc;n nữ. Đồng thời tạo điều kiện để c&aacute;c bạn thể hiện khả năng tư duy s&aacute;ng tạo, ứng xử nhanh. Mở m&agrave;n đ&ecirc;m chung kết l&agrave; phần thi trang phục truyền thống v&agrave; dạ hội của c&aacute;c th&iacute; sinh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/52/5F/miss-8.jpg\" border=\"1\" alt=\"Một th&iacute; sinh tự tin tr&igrave;nh diễn trang phục dạ hội.\" width=\"400\" height=\"559\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Th&iacute; sinh L&ecirc; Thị Diễm Th&uacute;y tự tin tr&igrave;nh diễn trang phục dạ hội.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/52/5F/miss-1.jpg\" border=\"1\" alt=\"Top 18 c&ocirc; g&aacute;i xinh đẹp v&agrave; t&agrave;i năng nhất được chọn trong đ&ecirc;m chung kết.\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Top 18 c&ocirc; g&aacute;i xinh đẹp được chọn v&agrave;o phần thi t&agrave;i năng trong đ&ecirc;m chung kết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/52/5F/miss-7.jpg\" border=\"1\" alt=\"\" width=\"472\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c th&iacute; sinh thể hiện t&agrave;i ca h&aacute;t.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/52/5F/miss-4.jpg\" border=\"1\" alt=\"C&aacute;c c&ocirc; g&aacute;i trẻ cũng khuấy động kh&ocirc;ng kh&iacute; bằng c&aacute;c ca kh&uacute;c s&ocirc;i động.\" width=\"480\" height=\"371\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c c&ocirc; g&aacute;i trẻ cũng khuấy động kh&ocirc;ng kh&iacute; bằng ca kh&uacute;c s&ocirc;i động.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/52/5F/miss-2.jpg\" border=\"1\" alt=\"Top 6 nữ sinh lọt v&agrave;o v&ograve;ng ứng xử.\" width=\"480\" height=\"362\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Top 6 nữ sinh lọt v&agrave;o v&ograve;ng ứng xử.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/52/5F/miss-5.jpg\" border=\"1\" alt=\"C&aacute;c hoa kh&ocirc;i chụp ảnh c&ugrave;ng ca sĩ Ng&acirc;n Kh&aacute;nh.\" width=\"480\" height=\"368\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Th&iacute; sinh Ho&agrave;ng Thị Hoa (thứ 4 từ tr&aacute;i qua phải) trở th&agrave;nh Hoa kh&ocirc;i của đ&ecirc;m thi. C&aacute;c người đẹp của trường Nguyễn Tất Th&agrave;nh chụp ảnh c&ugrave;ng ca sĩ Ng&acirc;n Kh&aacute;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/52/5F/miss9.jpg\" border=\"1\" alt=\"Hoa kh&ocirc;i 2009 v&agrave; Hoa kh&ocirc;i 2010 đọ sắc.\" width=\"373\" height=\"547\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hoa kh&ocirc;i 2009 v&agrave; Hoa kh&ocirc;i 2010 đọ sắc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n<br /></strong><em>Ảnh</em><strong>: H. P</strong></p>', 2, 1144, 0, 1, NULL, NULL),
(105, 'Người mẫu 12 tuổi rạng rỡ đầu năm mới', 'Nguoi-Mau-12-Tuoi-Rang-Ro-Dau-Nam-Moi', 'Trong nắng Sài Gòn, Bảo Trân duyên dáng với mái tóc dài búi cao, khi lại tinh nghịch với tóc tém tuổi mới lớn. Người mẫu 12 tuổi này đang gây nên cơn sốt trong giới trẻ.', 'bao-tran-11.jpg', 22, '<h1 class=\"Title\">Người mẫu 12 tuổi rạng rỡ đầu năm mới</h1>\r\n<h2 class=\"Lead\">Trong nắng S&agrave;i G&ograve;n, Bảo Tr&acirc;n duy&ecirc;n d&aacute;ng với m&aacute;i t&oacute;c d&agrave;i b&uacute;i cao, khi lại tinh nghịch với t&oacute;c t&eacute;m tuổi mới lớn. Người mẫu 12 tuổi n&agrave;y đang g&acirc;y n&ecirc;n cơn sốt trong giới trẻ.<br /><br /></h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/50/67/bao-tran-1.jpg\" border=\"1\" alt=\"Từng g&acirc;y x&ocirc;n xao cộng động mạng v&igrave; t&agrave;i diễn thời trang chuy&ecirc;n nghiệp\" width=\"480\" height=\"358\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Từng đạt giải V&agrave;ng cuộc thi Teen Model năm 2009 khi mới 11 tuổi, tận dụng chiều cao vượt trội 1m7, nữ sinh sớm tham gia nhiều hoạt động biểu diễn thời trang với tư c&aacute;ch l&agrave; người mẫu nh&iacute;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/50/67/tran-1.jpg\" border=\"1\" alt=\"Tận dụng chiều cao vượt trội so với bạn b&egrave;, nữ sinh sớm tham gia nhiều hoạt động biểu diễn thời trang với tư c&aacute;ch l&agrave; người mẫu nh&iacute;.\" width=\"400\" height=\"576\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Gần đ&acirc;y Bảo Tr&acirc;n tiếp tục g&acirc;y x&ocirc;n xao cộng đồng mạng khi tham gia diễn thời trang &aacute;o cưới.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/50/67/bao-tran-9.jpg\" border=\"1\" alt=\"hfifjlcv\" width=\"391\" height=\"539\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ngo&agrave;i việc học h&agrave;nh, những ng&agrave;y cuối tuần Bảo Tr&acirc;n tranh thủ đi tập luyện tại lớp người mẫu như l&agrave; một m&ocirc;n giải tr&iacute;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/50/67/bao-tran-3.jpg\" border=\"1\" alt=\"Kh&ocirc;ng chỉ tham gia c&aacute;c hoạt động với tư c&aacute;ch l&agrave; người mẫu \" width=\"390\" height=\"560\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/50/67/bao-tran-4.jpg\" border=\"1\" alt=\"Tr&acirc;n cũng cho biết, đến b&acirc;y giờ vẫn chưa x&aacute;c định được việc chọn đi theo nghề người mẫu bởi v&igrave; c&ograve;n qu&aacute; trẻ để n&oacute;i trước về nghệ nghiệp của m&igrave;nh.\" width=\"480\" height=\"357\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tr&acirc;n cũng cho biết, đến b&acirc;y giờ vẫn chưa x&aacute;c định được việc chọn đi theo nghề người mẫu bởi v&igrave; c&ograve;n qu&aacute; trẻ để n&oacute;i trước về nghề nghiệp của m&igrave;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/50/67/bao-tran-7.jpg\" border=\"1\" alt=\"V&igrave; c&ograve;n qu&aacute; nhỏ, n&ecirc;n Tr&acirc;n được bố mẹ quản l&yacute; rất chặt về thời gian học cũng như việc tham gia c&aacute;c hoạt động biểu diễn.\" width=\"480\" height=\"388\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những chương tr&igrave;nh Tr&acirc;n tham gia đều được ba, duyệt trước. Tuy nhi&ecirc;n c&ocirc; cũng kh&ocirc;ng tr&aacute;nh khỏi những sự cố trong biểu diễn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/50/67/bao-tran-8.jpg\" border=\"1\" alt=\"Những chương tr&igrave;nh Tr&acirc;n tham gia đều được ba, mẹ kiểm duyệt trước.\" width=\"433\" height=\"576\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Trong show diễn thời trang \"Phong c&aacute;ch trẻ\" v&agrave;o cuối th&aacute;ng 11/2010 tại S&agrave;i G&ograve;n, chiếc quạt gi&oacute; l&agrave;m cho chiếc v&aacute;y ngắn của c&ocirc; \"phản chủ\" để lộ v&ograve;ng 3.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/50/67/bao-tran-11.jpg\" border=\"1\" alt=\"hkjlksdf\" width=\"400\" height=\"588\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Sự việc khiến t&ecirc;n tuổi của nữ sinh lớp 7 c&agrave;ng trở n&ecirc;n hot hơn trong diễn đ&agrave;n mạng. Ch&ugrave;m ảnh Bảo Tr&acirc;n do nhiếp ảnh Lavender thực hiện với phần trang phục của Marc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>B&igrave;nh Nguy&ecirc;n</strong></p>', 2, 455, 0, 1, NULL, NULL),
(106, 'Màn tỏ tình ấn tượng trong giá rét 10 độ C', 'Man-To-Tinh-An-Tuong-Trong-Gia-Ret-10-Do-C', 'Trong cái lạnh tê cóng 10 độ C, giữa bãi sông Hồng (Hà Nội), một chàng trai miệt mài xe cát thành hình trái tim lớn, viết tên mình và người yêu lên đó với hy vọng bạn gái nhìn thấy.', 'to_tinh1990-6.jpg', 22, '<h1 class=\"Title\">M&agrave;n tỏ t&igrave;nh ấn tượng trong gi&aacute; r&eacute;t 10 độ C</h1>\r\n<h2 class=\"Lead\">Trong c&aacute;i lạnh t&ecirc; c&oacute;ng 10 độ C, giữa b&atilde;i s&ocirc;ng Hồng (H&agrave; Nội), một ch&agrave;ng trai miệt m&agrave;i xe c&aacute;t th&agrave;nh h&igrave;nh tr&aacute;i tim lớn, viết t&ecirc;n m&igrave;nh v&agrave; người y&ecirc;u l&ecirc;n đ&oacute; với hy vọng bạn g&aacute;i nh&igrave;n thấy.</h2>\r\n<p class=\"Normal\">Ch&agrave;ng trai t&ecirc;n Tuấn Anh, 25 tuổi, ở phường Ph&uacute;c T&acirc;n (Ho&agrave;n Kiếm, H&agrave; Nội). Chiều 6/1, chọn b&atilde;i giữa s&ocirc;ng Hồng dưới ch&acirc;n cầu Long Bi&ecirc;n l&agrave;m địa điểm, Tuấn Anh miệt m&agrave;i vẽ tr&aacute;i tim lớn tr&ecirc;n nền c&aacute;t. Sau nhiều giờ, một tr&aacute;i tim lớn viết t&ecirc;n 2 người hiện l&ecirc;n tr&ecirc;n nền c&aacute;t.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/52/78/to_tinh1990-6.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tuấn Anh vui mừng v&igrave; sản phẩm sắp ho&agrave;n th&agrave;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&ocirc;ng việc sắp ho&agrave;n th&agrave;nh th&igrave; những đợt gi&oacute; lớn li&ecirc;n tục thổi cuốn theo bụi c&aacute;t s&ocirc;ng Hồng, san lấp đi những g&igrave; m&agrave; ch&agrave;ng trai đa t&igrave;nh đang thể hiện. Tuấn Anh lại h&igrave; hụi đ&agrave;o s&acirc;u hơn v&agrave; đắp c&aacute;t nổi cao hơn.</p>\r\n<p class=\"Normal\">Cậu cho biết, chiều bạn g&aacute;i của cậu sẽ qua đ&acirc;y n&ecirc;n hy vọng sẽ nh&igrave;n thấy v&agrave; cảm động. \"Mong l&agrave; trời đừng mưa\", Tuấn Anh hồ hởi.</p>\r\n<p class=\"Normal\">M&agrave;n chuẩn bị tỏ t&igrave;nh l&atilde;ng mạn đ&atilde; thu h&uacute;t sự quan t&acirc;m của nhiều người qua cầu Long Bi&ecirc;n, đặc biệt l&agrave; những bạn trẻ.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Theo&nbsp;<em>C&ocirc;ng an Nh&acirc;n d&acirc;n</em>)</p>\r\n<p>&nbsp;</p>', 2, 2692, 0, 1, NULL, NULL),
(107, 'Chàng trai vàng của Tin học Việt Nam', 'Chang-Trai-Vang-Cua-Tin-Hoc-Viet-Nam', 'Là sinh viên năm thứ 5 của ĐH Khoa học tự nhiên (TP HCM), Trịnh Trần Đăng Khoa đã có 3 năm huấn luyện cho đội tuyển Olympic tin học của ĐH công nghệ thông tin (ĐH Quốc gia TP HCM) và liên tiếp giành giải cao trong các kỳ thi Tin học quốc tế. ', 'dang_khoa.jpg', 22, '<h1 class=\"Title\">Ch&agrave;ng trai v&agrave;ng của Tin học Việt Nam</h1>\r\n<h2 class=\"Lead\">L&agrave; sinh vi&ecirc;n năm thứ 5 của ĐH Khoa học tự nhi&ecirc;n (TP HCM), Trịnh Trần Đăng Khoa đ&atilde; c&oacute; 3 năm huấn luyện cho đội tuyển Olympic tin học của ĐH c&ocirc;ng nghệ th&ocirc;ng tin (ĐH Quốc gia TP HCM) v&agrave; li&ecirc;n tiếp gi&agrave;nh giải cao trong c&aacute;c kỳ thi Tin học quốc tế.&nbsp;<br /><span style=\"font-size: 10px; font-weight: normal;\"><span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">Biết đến lập tr&igrave;nh từ năm lớp 7, khi được bố mẹ cho học lớp&nbsp;<span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">Pascal căn bản ở Trung t&acirc;m Tin học của Th&agrave;nh Đo&agrave;n TP HCM,&nbsp;</span></span>ch&agrave;ng trai c&oacute; d&aacute;ng người mảnh khảnh, nụ cười hiền kh&ocirc; n&agrave;y cho hay, chỉ sau mấy th&aacute;ng học, cậu đ&atilde; giật giải&nbsp;</span></span><span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">trong kỳ thi tin học trẻ kh&ocirc;ng chuy&ecirc;n cấp quận.</span></span></span></h2>\r\n<p class=\"Normal\"><span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">Khoa kể, suốt những năm cấp 2, m&ocirc;n học cậu th&iacute;ch nhất l&agrave; H&oacute;a học v&agrave; Tin học.</span></span><span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">Khi chuẩn bị thi l&ecirc;n cấp 3, cậu băn khoăn kh&ocirc;ng biết n&ecirc;n theo học m&ocirc;n n&agrave;o bởi t&igrave;nh y&ecirc;u hai m&ocirc;n ngang nhau. Chỉ đến khi gi&agrave;nh điểm cao trong kỳ thi v&agrave;o lớp chuy&ecirc;n tin trường phổ th&ocirc;ng năng khiếu ĐH Quốc gia TP HCM, Khoa mới quyết theo c&ocirc;ng nghệ th&ocirc;ng tin.</span></span></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4F/AF/dang_khoa.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đăng Khoa đang sở hữu một bảng th&agrave;nh t&iacute;ch d&agrave;y cộp trong lĩnh vực c&ocirc;ng nghệ th&ocirc;ng tin.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Tại trường phổ th&ocirc;ng năng khiếu, Khoa được nhen nh&oacute;m t&igrave;nh y&ecirc;u v&agrave; niềm đam m&ecirc; với thuật to&aacute;n qua sự tận t&igrave;nh chỉ dạy của thầy&nbsp;<span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">Nguyễn Thanh H&ugrave;ng v&agrave; Nguyễn Thanh Sơn. Từ việc dạy c&aacute;c kiến thức cơ bản, hai thầy đ&atilde; gi&uacute;p Khoa l&agrave;m quen với những b&agrave;i to&aacute;n kh&oacute; v&agrave; c&aacute;ch lập tr&igrave;nh. \"Thậm ch&iacute;, khi em chọn hướng nghi&ecirc;n cứu về l&yacute; thuyết thuật to&aacute;n cũng một phần l&agrave; do ảnh hưởng từ thầy H&ugrave;ng\", Khoa cho hay.</span></span></p>\r\n<p class=\"Normal\"><span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">Trong qu&aacute; tr&igrave;nh học, Khoa luyện tập chung với Phạm Tuấn Vũ, th&agrave;nh vi&ecirc;n trong đội thi ACM/ICPC, Quả cầu v&agrave;ng năm 2009. Ngo&agrave;i ra, cậu c&ograve;n được thầy Trần Minh Triết gi&uacute;p đỡ về c&aacute;c vấn đề thủ tục, kinh ph&iacute; để đi thi ở nước ngo&agrave;i, nhường chỗ l&agrave;m việc của m&igrave;nh ở bộ m&ocirc;n để đội của Khoa c&oacute; chỗ luyện tập chung trước khi thi.</span></span></p>\r\n<p class=\"Normal\">Với Khoa, điều quan trọng&nbsp;<span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">nhất để trở th&agrave;nh một người giỏi trong c&ocirc;ng nghệ th&ocirc;ng tin hay bất kỳ một ng&agrave;nh n&agrave;o kh&aacute;c l&agrave; sự đam m&ecirc;. Chỉ c&oacute; đam m&ecirc;, y&ecirc;u th&iacute;ch c&ocirc;ng việc th&igrave; mới c&oacute; thể vượt qua những l&uacute;c gặp kh&oacute; khăn hay thất bại&hellip;</span></span></p>\r\n<p class=\"Normal\">Học lực kh&aacute; những năm cấp 3, thế nhưng Đăng Khoa nổi tiếng cả trường bởi gi&agrave;nh nhiều giải thưởng Tin học. Đạt giải 3 quốc gia, cậu được&nbsp;<span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">miễn thi tốt nghiệp v&agrave; tuyển thẳng v&agrave;o đại học. Khoa chọn chương tr&igrave;nh ti&ecirc;n tiến của ĐH khoa học tự nhi&ecirc;n (ĐH Quốc gia TP HCM) để c&oacute; điều kiện tốt nhất theo đuổi đam m&ecirc; của m&igrave;nh.</span></span></p>\r\n<p class=\"Normal\"><span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">Li&ecirc;n tục nằm trong top 5 của lớp, những m&ocirc;n m&agrave; Khoa y&ecirc;u th&iacute;ch v&agrave; đạt điểm cao đều li&ecirc;n quan đến To&aacute;n như Calculus (t&iacute;nh to&aacute;n), Linear Algebra (Đại số tuyến t&iacute;nh), Algorithm &amp; Complexity (thuật to&aacute;n v&agrave; độ phức tạp)&hellip;</span></span></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4F/AF/dang_khoa_2.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đăng Khoa (thứ hai từ tr&aacute;i sang) ước mơ trở th&agrave;nh một gi&aacute;o sư về khoa học m&aacute;y t&iacute;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Thời gian ở giảng đường đại học cũng l&agrave; l&uacute;c Khoa li&ecirc;n tiếp đem vinh quang về cho ng&agrave;nh Tin học. Năm 2008, Khoa gi&agrave;nh c&uacute;p bạc si&ecirc;u c&uacute;p Olympic Tin học sinh vi&ecirc;n, giải nhất (vị tr&iacute; thứ 3) cuộc thi lập tr&igrave;nh quốc tế ACM/ICPC.</p>\r\n<p class=\"Normal\">Một năm sau đ&oacute;, cậu đứng vị tr&iacute; thứ 2 cuộc thi lập tr&igrave;nh quốc tế ACM/ICPC tại điểm thi Manila (Philippines). Năm nay, Khoa l&agrave; th&agrave;nh vi&ecirc;n duy nhất của đội tuyển Việt Nam tại chung kết lập tr&igrave;nh quốc tế ACM/ICPC ở Harbin Trung Quốc v&agrave; đạt giải khuyến kh&iacute;ch.</p>\r\n<p class=\"Normal\">Với một loạt th&agrave;nh t&iacute;ch tr&ecirc;n, Đăng Khoa mới vinh dự nhận giải thưởng Quả cầu v&agrave;ng 2010 d&agrave;nh cho những c&aacute; nh&acirc;n xuất sắc trong lĩnh vực c&ocirc;ng nghệ th&ocirc;ng tin.</p>\r\n<p class=\"Normal\"><span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">Khoa chia sẻ, điều quan trọng nhất&nbsp;</span></span><span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">l&agrave; cần chuẩn bị, luyện tập thật tốt khi ở nh&agrave; để tạo t&acirc;m l&yacute; thoải m&aacute;i khi đi thi. Trong l&uacute;c thi th&igrave; cần giữ được sự b&igrave;nh tĩnh để l&agrave;m việc với hiệu quả cao nhất.</span></span></p>\r\n<p class=\"Normal\"><span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">Đang ho&agrave;n th&agrave;nh những th&aacute;ng cuối c&ugrave;ng tại trường đại học, c&ocirc;ng việc ch&iacute;nh của Khoa l&agrave;&nbsp;</span></span><span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">nghi&ecirc;n cứu c&aacute;c b&agrave;i to&aacute;n về tối ưu h&oacute;a dưới sự hướng dẫn của tiến sĩ Đinh B&aacute; Tiến (từng l&agrave;m việc cho NASA) v&agrave;&nbsp;</span></span><span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">huấn luyện cho c&aacute;c bạn trong đội tuyển thi Olympic Tin học sinh vi&ecirc;n của ĐH C&ocirc;ng nghệ th&ocirc;ng tin (ĐH Quốc gia TP HCM).</span></span></p>\r\n<p class=\"Normal\"><span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">\"Việc dạy học rất th&uacute; vị. Đ&oacute; l&agrave; cơ hội để em tăng th&ecirc;m kinh nghiệm về khả năng giảng dạy, truyền đạt kiến thức. Điều đ&oacute; rất c&oacute; &iacute;ch cho c&ocirc;ng việc của em sau n&agrave;y\", Khoa n&oacute;i.</span></span></p>\r\n<p class=\"Normal\"><span style=\"font-family: \'times new roman\', \'new york\', times, serif;\"><span style=\"font-family: \'times new roman\', serif;\">Ước mơ trở th&agrave;nh gi&aacute;o sư về khoa học m&aacute;y t&iacute;nh, mục ti&ecirc;u trước mắt của Khoa l&agrave; t&igrave;m kiếm học bổng đi học tiến sĩ ở Mỹ. \"Em sẽ cố gắng để đạt được mục ti&ecirc;u bởi kiến thức m&ecirc;nh m&ocirc;ng v&ocirc; tận, đi một ng&agrave;y đ&agrave;ng em sẽ học được một s&aacute;ng kh&ocirc;n\", Khoa n&oacute;i.</span></span></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#e8e8ff\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p class=\"Normal\"><strong>Những th&agrave;nh t&iacute;ch nổi bật của quả cầu v&agrave;ng Trịnh Trần Đăng Khoa:</strong></p>\r\n<p class=\"Normal\">Năm 2006: Giải 3 kỳ thi học sinh giỏi quốc gia m&ocirc;n Tin học.</p>\r\n<p class=\"Normal\">Năm 2006-2010: Theo học chương tr&igrave;nh ti&ecirc;n tiến, chương tr&igrave;nh hợp t&aacute;c giữa ĐH khoa học tự nhi&ecirc;n TP HCM với ĐH Portland State (Mỹ) với điểm trung b&igrave;nh t&iacute;nh đến năm thứ 4 l&agrave; 8,77/10.<br /><br />Năm 2008: Gi&agrave;nh c&uacute;p bạc si&ecirc;u c&uacute;p Olympic Tin học sinh vi&ecirc;n, giải nhất (vị tr&iacute; thứ 3) cuộc thi lập tr&igrave;nh quốc tế ACM/ICPC.</p>\r\n<p class=\"Normal\">Năm 2009: C&uacute;p bạc si&ecirc;u c&uacute;p Olympic tin học sinh vi&ecirc;n, vị tr&iacute; thứ 2 cuộc thi lập tr&igrave;nh quốc tế ACM/ICPC tại điểm thi Manila-Philippines.</p>\r\n<p class=\"Normal\">Năm 2010: L&agrave; th&agrave;nh vi&ecirc;n duy nhất của đội tuyển Việt Nam tại chung kết lập tr&igrave;nh quốc tế ACM/ICPC tại Harbin Trung Quốc năm 2010 v&agrave; đạt giải khuyến kh&iacute;ch. Gi&agrave;nh vị tr&iacute; thứ 4 cuộc thi lập tr&igrave;nh quốc tế ICM/ICPC tại điểm thi Kuala Lumpur, Malaysia.</p>\r\n<p class=\"Normal\">Năm 2008, 2009, 2010: Huấn luyện vi&ecirc;n cho đội tuyển dự thi Olympic Tin học sinh vi&ecirc;n của ĐH C&ocirc;ng nghệ th&ocirc;ng tin (ĐH Quốc gia TP HCM).</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 2, 93, 0, 1, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(108, 'Cô gái \'ăn chay vì môi trường\'', '', 'Nhằm hạn chế nguồn khí thải nhà kính, kêu gọi người dân giảm ăn thịt, Trang đã phát động chiến dịch “Ăn chay vì môi trường”. Lần đầu tiên được tổ chức tại Việt Nam, chiến dịch đã thu hút đông đảo bạn trẻ tham gia.', 'trang.jpg', 22, '<h1 class=\"Title\">C&ocirc; g&aacute;i \'ăn chay v&igrave; m&ocirc;i trường\'</h1>\r\n<h2 class=\"Lead\">Nhằm hạn chế nguồn kh&iacute; thải nh&agrave; k&iacute;nh, k&ecirc;u gọi người d&acirc;n giảm ăn thịt, Trang đ&atilde; ph&aacute;t động chiến dịch &ldquo;Ăn chay v&igrave; m&ocirc;i trường&rdquo;. Lần đầu ti&ecirc;n được tổ chức tại Việt Nam, chiến dịch đ&atilde; thu h&uacute;t đ&ocirc;ng đảo bạn trẻ tham gia.</h2>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Chiến dịch &ldquo;Ăn chay v&igrave; m&ocirc;i trường&rdquo; được ph&aacute;t động dưới sự bảo trợ của Trung t&acirc;m sống v&agrave; học tập v&igrave; m&ocirc;i trường v&agrave; cộng đồng (Live&amp;Learn Vietnam). Tại hội thảo sống xanh 2010 diễn ra ng&agrave;y 19/12 vừa qua ở H&agrave; Nội, chiến dịch đ&atilde; đoạt giải &ldquo;S&aacute;ng kiến sống xanh hay nhất&rdquo;.</span></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4F/1C/trang.jpg\" alt=\"\" width=\"373\" height=\"500\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đỗ Thị Thu Trang, c&ocirc; g&aacute;i đ&atilde; đưa ra chiến dịch xanh &ldquo;Ăn chay v&igrave; m&ocirc;i trường\". Ảnh:&nbsp;<em>Văn Nguyễn.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Người đưa ra &yacute; tưởng đặc biệt n&agrave;y l&agrave; Đỗ Thị Thu Trang, sinh vi&ecirc;n thuộc chương tr&igrave;nh cao học quốc tế Huế - Okayama (Nhật Bản), chuy&ecirc;n ng&agrave;nh Khoa học m&ocirc;i trường. Được ph&aacute;t động đầu th&aacute;ng 10/2010, đến nay chiến dịch đang duy tr&igrave; ăn chay cố định v&agrave;o một ng&agrave;y trong th&aacute;ng ở cả 3 miền Bắc - Trung - Nam.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Tốt nghiệp loại giỏi hệ đ&agrave;o tạo cử nh&acirc;n chất lượng cao, chuy&ecirc;n ng&agrave;nh khoa học m&ocirc;i trường, ĐH Khoa học tự nhi&ecirc;n H&agrave; Nội, c&ocirc; sinh vi&ecirc;n qu&ecirc; Ph&uacute; Thọ n&agrave;y l&agrave; một trong số &iacute;t sinh vi&ecirc;n nhận được học bổng thạc sĩ của chương tr&igrave;nh hợp t&aacute;c quốc tế giữa ĐH Okayama với ĐH Huế.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">N&oacute;i về &yacute; tưởng của m&igrave;nh, Thu Trang cho biết: &ldquo;Khi c&ograve;n l&agrave; sinh vi&ecirc;n, m&igrave;nh đ&atilde; c&oacute; &yacute; định l&agrave;m đề t&agrave;i nghi&ecirc;n cứu khoa học về vấn đề ăn chay v&agrave; m&ocirc;i trường, nhưng phải đến khi tham gia diễn đ&agrave;n thanh ni&ecirc;n v&agrave; ph&aacute;t triển bền vững 2010, c&ugrave;ng với qu&aacute; tr&igrave;nh t&igrave;m hiểu kỹ về c&aacute;c hoạt động m&ocirc;i trường ở Việt Nam v&agrave; thế giới, m&igrave;nh mới mạnh dạn tr&igrave;nh b&agrave;y &yacute; tưởng. Thật bất ngờ c&oacute; rất nhiều bạn đồng &yacute; hưởng ứng chiến dịch&rdquo;.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">N&oacute;i nghe c&oacute; vẻ đơn giản nhưng khi bắt tay v&agrave;o thực hiện chiến dịch, Thu Trang v&agrave; c&aacute;c cộng sự mới nhận ra rằng biến đổi kh&iacute; hậu l&agrave; cụm từ rất quen thuộc, nhưng lại c&oacute; qu&aacute; nhiều người kh&ocirc;ng hiểu thực sự về nguy&ecirc;n nh&acirc;n v&agrave; hậu quả của n&oacute;. Th&ecirc;m v&agrave;o đ&oacute; l&agrave; t&acirc;m l&yacute; người d&acirc;n e ngại ăn chay kh&ocirc;ng cung cấp đủ năng lượng v&agrave; dinh dưỡng.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Khi chiến dịch được ph&aacute;t động tại th&agrave;nh phố Huế, Trang đ&atilde; phải rất vất vả mới k&ecirc;u gọi được c&aacute;c c&acirc;u lạc bộ, đội nh&oacute;m t&igrave;nh nguyện c&ugrave;ng tham gia. C&ocirc; cũng đ&atilde; li&ecirc;n lạc với GS.TS triết học Th&aacute;i Kim Lan từ b&ecirc;n Đức về n&oacute;i chuyện để c&aacute;c bạn trẻ hiểu hơn về mục đ&iacute;ch, hiệu quả của việc ăn chay đối với m&ocirc;i trường cũng như sức khỏe con người.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Thu Trang d&ugrave;ng facebook&nbsp;</span><span style=\"color: #000000;\">l&agrave;m c&ocirc;ng cụ ch&iacute;nh để truyền th&ocirc;ng, đồng thời mở những gian h&agrave;ng truyền th&ocirc;ng tại G&ograve; Đống Đa (H&agrave; Nội) để gi&uacute;p mọi người hiểu hơn về chiến dịch.</span></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4F/1C/trang1.jpg\" alt=\"\" width=\"495\" height=\"351\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đ&ocirc;ng đảo bạn trẻ tham gia ăn chay tại Huế. Ảnh:&nbsp;<em>Văn Nguyễn.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Điều l&agrave;m cho c&ocirc; g&aacute;i 24 tuổi n&agrave;y vui nhất l&agrave; ngay khi ph&aacute;t động chiến dịch nh&acirc;n ng&agrave;y 10/10 tại Huế, Đ&agrave; Nẵng, H&agrave; Nội v&agrave; TP HCM đ&atilde; c&oacute; gần 1.700 người tham gia. V&agrave; đến nay, chiến dịch đ&atilde; c&oacute; mặt tại B&igrave;nh Dương, Ph&uacute; Y&ecirc;n, Tiền Giang với sự h&agrave;o hứng tham gia của đ&ocirc;ng đảo bạn trẻ.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">&ldquo;C&oacute; được sự th&agrave;nh c&ocirc;ng bước đầu n&agrave;y l&agrave; sự nỗ lực của c&aacute;c th&agrave;nh vi&ecirc;n trong diễn đ&agrave;n v&agrave; c&aacute;c cộng sự. V&agrave; m&igrave;nh tin với sức trẻ, c&aacute;c bạn sẽ tiếp tục thực hiện trong những th&aacute;ng tiếp theo&rdquo;, Thu Trang bộc bạch.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">N&oacute;i về dự định tiếp theo, Thu Trang cho biết: &ldquo;Hiện tụi m&igrave;nh đang viết một cuốn s&aacute;ch&nbsp;<em>C&aacute;ch nấu c&aacute;c m&oacute;n ăn chay</em>, dự kiến ho&agrave;n th&agrave;nh v&agrave;o th&aacute;ng 4/2011. Tụi m&igrave;nh cũng sẽ tổ chức lớp dạy nấu ăn chay miễn ph&iacute; cho c&aacute;c bạn sinh vi&ecirc;n&rdquo;.</span></p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><span style=\"color: #000000;\"><strong>Văn Nguyễn</strong></span></p>', 2, 3, 0, 1, NULL, NULL),
(109, 'Cô hoa khôi giỏi giang của Học viện Ngoại giao', 'Co-Hoa-Khoi-Gioi-Giang-Cua-Hoc-Vien-Ngoai-Giao', 'Là Miss ứng xử của cuộc thi hoa khôi sinh viên Hà Nội, Ngụy Hải An gây ấn tượng với người tiếp xúc bởi sự sắc sảo và bảng thành tích học tập, hoạt động ngoại khóa đáng nể.', 'Hai_An_3.jpg', 22, '<h1 class=\"Title\">C&ocirc; hoa kh&ocirc;i giỏi giang của Học viện Ngoại giao</h1>\r\n<h2 class=\"Lead\">L&agrave; Miss ứng xử của cuộc thi hoa kh&ocirc;i sinh vi&ecirc;n H&agrave; Nội, Ngụy Hải An g&acirc;y ấn tượng với người tiếp x&uacute;c bởi sự sắc sảo v&agrave; bảng th&agrave;nh t&iacute;ch học tập, hoạt động ngoại kh&oacute;a đ&aacute;ng nể.</h2>\r\n<p>&ocirc;i mắt to tr&ograve;n, m&aacute;i t&oacute;c cắt bằng, Hải An l&uacute;c n&agrave;o cũng nh&iacute; nhảnh, tươi tắn. C&aacute;ch n&oacute;i chuyện h&oacute;m hỉnh của An khiến người đối diện lu&ocirc;n cảm thấy thoải m&aacute;i.</p>\r\n<p class=\"Normal\">Ở Học viện Ngoại giao, bạn b&egrave; kh&ocirc;ng lạ g&igrave; c&ocirc; lớp trưởng, khoa trưởng năng động Hải An. Đỗ thủ khoa đầu v&agrave;o khối D năm 2009, An được bạn b&egrave; t&iacute;n nhiệm bầu l&agrave;m lớp trưởng. Kh&ocirc;ng l&acirc;u sau đ&oacute;, An trở th&agrave;nh một kh&oacute;a trưởng gương mẫu. C&ocirc; t&acirc;m sự, ở vị tr&iacute; n&agrave;y, c&ocirc;ng việc kh&aacute; bận rộn. An l&agrave; đầu mối th&ocirc;ng tin giữa thầy c&ocirc;, nh&agrave; trường v&agrave; c&aacute;c bạn, phải lu&ocirc;n lu&ocirc;n theo d&otilde;i v&agrave; th&ocirc;ng b&aacute;o tin tức về lịch học, lịch thi, t&agrave;i liệu m&ocirc;n học, kết quả, những thay đổi v&agrave; sắp xếp mới.</p>\r\n<p class=\"Normal\">Tham gia t&iacute;ch cực c&aacute;c hoạt động ngoại kh&oacute;a của trường, An c&ograve;n c&oacute; th&agrave;nh t&iacute;ch học tập rất đ&aacute;ng nể. 12 năm l&agrave; học sinh giỏi, An li&ecirc;n tiếp gi&agrave;nh học bổng trong hai kỳ năm nhất đại học. C&ocirc; cho biết, m&ocirc;n học y&ecirc;u th&iacute;ch nhất l&agrave; Văn v&agrave; Sinh. Ng&agrave;y học phổ th&ocirc;ng, An gi&agrave;nh giải nh&igrave; học sinh giỏi cấp quận hai m&ocirc;n Sinh, Văn, giải ba học sinh giỏi cấp th&agrave;nh phố H&agrave; Nội m&ocirc;n Văn. Thi v&agrave;o cấp ba, An đỗ c&aacute;c trường Kim Li&ecirc;n, Chuy&ecirc;n ngữ, Sư phạm, v&agrave; chuy&ecirc;n văn Amsterdam. Kỳ thi đại học, Hải An đỗ thủ khoa khối D Học viện Ngoại giao với 26,5 điểm, trong đ&oacute; To&aacute;n 10 điểm.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4F/16/Hai_An_3.jpg\" alt=\"\" width=\"350\" height=\"480\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đ&ocirc;i mắt to tr&ograve;n, m&aacute;i t&oacute;c cắt bằng, Hải An l&uacute;c n&agrave;o cũng nh&iacute; nhảnh, tươi tắn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Gia đ&igrave;nh kh&ocirc;ng c&oacute; ai l&agrave;m ngoại giao, nhưng bố mẹ đều ủng hộ An hết m&igrave;nh khi c&ocirc; mơ ước l&agrave;m một nh&agrave; ngoại giao giỏi. &ldquo;Bố l&agrave; người rất nghi&ecirc;m khắc, cẩn thận v&agrave; lu&ocirc;n r&egrave;n dũa con c&aacute;i từng ly từng t&iacute;. Mẹ lại l&agrave; người cởi mở, nền n&atilde;, rất y&ecirc;u thương chồng con. Mẹ hy sinh rất nhiều cho gia đ&igrave;nh v&agrave; l&agrave; người bạn chia sẻ với em mọi vui buồn, những &acirc;u lo v&agrave; những mối quan t&acirc;m trong cuộc sống&rdquo;, An n&oacute;i.</p>\r\n<p class=\"Normal\">Dự thi Imiss Thăng Long, đ&oacute; l&agrave; l<span style=\"color: #000000;\">ần đầu ti&ecirc;n An tham dự một cuộc thi về nhan sắc. Khi bắt được c&acirc;u hỏi về n&eacute;t truyền thống, hiện đại của phụ nữ thế kỷ 21,<em>&nbsp;</em>An đ&atilde; l&agrave;m h&agrave;i l&ograve;ng ban gi&aacute;m khảo v&agrave; kh&aacute;n giả với c&acirc;u trả lời sắc sảo: &ldquo;Phụ nữ hiện đại v&agrave; phụ nữ truyền thống đều cần c&oacute; những phẩm chất tốt đẹp m&agrave; nh&acirc;n d&acirc;n ta g&oacute;i gọn trong tứ đức. Phụ nữ hiện đại kh&ocirc;ng chỉ giỏi đối nội m&agrave; c&ograve;n đối ngoại giỏi, lu&ocirc;n lu&ocirc;n chăm lo cho gia đ&igrave;nh, cho người th&acirc;n, biết quan t&acirc;m đến x&atilde; hội v&agrave; cống hiến sức m&igrave;nh cho đất nước. Mặt kh&aacute;c, họ kh&ocirc;ng chỉ c&oacute; vẻ đẹp trời ph&uacute; m&agrave; c&ograve;n biết tự l&agrave;m đẹp, lu&ocirc;n lu&ocirc;n biến đổi để m&igrave;nh trở n&ecirc;n quyến rũ hơn. Nếu như phụ nữ truyền thống học c&aacute;ch đối xử ăn n&oacute;i nhẹ nh&agrave;ng thanh to&aacute;t th&igrave; phụ nữ hiện đại học c&aacute;ch giao tiếp sao cho thanh lịch, tr&iacute; tuệ để ng&agrave;y c&agrave;ng ho&agrave;n thiện&rdquo;.</span></p>\r\n<p class=\"Normal\">Sau cuộc thi, Hải An lại bận rộn khi l&agrave;m t&igrave;nh nguyện vi&ecirc;n cho hội nghị thượng đỉnh ASEAN, tham gia c&acirc;u lạc bộ Miss H&agrave; Nội với nhiều hoạt động từ thiện, nh&acirc;n đạo. Tuy nhi&ecirc;n, c&ocirc; cũng kh&ocirc;ng bỏ qua những đam m&ecirc; của m&igrave;nh l&agrave; hiphop, vẽ, thời trang v&agrave; ca h&aacute;t.</p>\r\n<p class=\"Normal\">L&agrave;m quen với hiphop từ khi c&ograve;n l&agrave; học sinh lớp 11, đặc biệt l&agrave; từ khi tham gia Đội Cheerleaders ở trường Trung học H&agrave; Nội - Amsterdam, mỗi khi sắp xếp được thời gian, An lại tập luyện c&ugrave;ng bạn b&egrave; v&agrave; học hỏi ở c&aacute;c clip tr&ecirc;n mạng. Hiện nay, An tham gia v&agrave;o c&acirc;u lạc bộ hiphop ở trường v&agrave; biểu diễn trong c&aacute;c chương tr&igrave;nh của học viện.</p>\r\n<p class=\"Normal\">&ldquo;Thời gian tới, em sẽ sắp xếp thời gian hợp l&yacute; để vừa học tập tốt, vừa tham gia được nhiều hơn c&aacute;c hoạt động từ thiện. Trước khi l&agrave;m một nh&agrave; ngoại giao giỏi, em muốn l&agrave;m một sinh vi&ecirc;n năng động, thể hiện được bản chất của thanh ni&ecirc;n thế hệ Hồ Ch&iacute; Minh&rdquo;, An cho hay.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>\r\n<p>&nbsp;</p>', 2, 615, 0, 1, NULL, NULL),
(110, '10 gương mặt trẻ tiêu biểu của thủ đô', '10-Guong-Mat-Tre-Tieu-Bieu-Cua-Thu-Do', 'Trở thành phó giáo sư, tiến sĩ khoa học khi mới 34 tuổi, mới học lớp 4 đã là kiện tướng cờ vua hay liên tiếp giành giải nhất các cuộc thi piano quốc tế và châu Á là những thành tích nổi bật của các gương mặt trẻ thủ đô tiêu biểu.', 'tran_van_tan.jpg', 22, '<h1 class=\"Title\">10 gương mặt trẻ ti&ecirc;u biểu của thủ đ&ocirc;</h1>\r\n<h2 class=\"Lead\">Trở th&agrave;nh ph&oacute; gi&aacute;o sư, tiến sĩ khoa học khi mới 34 tuổi, mới học lớp 4 đ&atilde; l&agrave; kiện tướng cờ vua hay li&ecirc;n tiếp gi&agrave;nh giải nhất c&aacute;c cuộc thi piano quốc tế v&agrave; ch&acirc;u &Aacute; l&agrave; những th&agrave;nh t&iacute;ch nổi bật của c&aacute;c gương mặt trẻ thủ đ&ocirc; ti&ecirc;u biểu.</h2>\r\n<p class=\"Normal\">Tối 30/12, tại quảng trường Lăng chủ tịch Hồ Ch&iacute; Minh, 10 gương mặt trẻ ti&ecirc;u biểu của thủ đ&ocirc; đ&atilde; được Th&agrave;nh đo&agrave;n H&agrave; Nội vinh danh v&agrave; trao c&uacute;p lưu niệm.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4E/12/tran_van_tan.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ph&oacute; gi&aacute;o sư, tiến sỹ khoa học Trần Văn Tấn đạt được nhiều th&agrave;nh c&ocirc;ng khi mới 34 tuổi.&nbsp;<em>Ảnh: Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trong số đ&oacute; c&oacute; PGS.TSKH Trần Văn Tấn, c&ocirc;ng t&aacute;c tại bộ m&ocirc;n H&igrave;nh học, khoa To&aacute;n Tin (ĐH sư phạm H&agrave; Nội). Được nh&agrave; nước c&ocirc;ng nhận l&agrave; ph&oacute; gi&aacute;o sư năm 34 tuổi, anh Tấn đ&atilde; nhận được rất nhiều lời mời l&agrave;m việc tại c&aacute;c Viện To&aacute;n học nổi tiếng thế giới. Tuy nhi&ecirc;n, anh đ&atilde; chọn ĐH Sư phạm để cống hiến nhiều hơn cho qu&ecirc; hương.</p>\r\n<p class=\"Normal\">Khi c&ograve;n l&agrave; sinh vi&ecirc;n, Tuấn đ&atilde; gi&agrave;nh giải thưởng sinh vi&ecirc;n nghi&ecirc;n cứu khoa học to&agrave;n quốc, nhận được nhiều bằng khen của Trung ương Đo&agrave;n, th&agrave;nh Đo&agrave;n H&agrave; Nội về th&agrave;nh t&iacute;ch xuất sắc trong học tập v&agrave; hoạt động Đo&agrave;n. Anh c&oacute; 25 b&agrave;i b&aacute;o khoa học được đăng ở Việt Nam v&agrave; thế giới.</p>\r\n<p class=\"Normal\">Th&agrave;nh vi&ecirc;n nhỏ tuổi nhất trong 10 gương mặt trẻ l&agrave; kiện tướng cờ vua Nguyễn Ho&agrave;ng Việt Hải. Mới 9 tuổi, Hải đ&atilde; l&agrave; kiện tướng FIDE của Li&ecirc;n đo&agrave;n cờ vua thế giới, 2 huy chương v&agrave;ng giải v&ocirc; địch cờ vua trẻ Đ&ocirc;ng Nam &Aacute; mở rộng v&agrave; rất nhiều huy chương khi tham dự c&aacute;c giải đấu to&agrave;n quốc.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4E/12/Viet_Hai.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nguyễn Ho&agrave;ng Việt Hải l&agrave; kiện tướng cờ vua khi mới học lớp 4.&nbsp;<em>Ảnh: Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Được bố dạy chơi cờ từ khi c&ograve;n học mẫu gi&aacute;o, năm lớp 1 đi thi quận v&agrave; đoạt giải cao, Hải đ&atilde; lọt v&agrave;o tầm ngắm của thầy Đ&agrave;o Anh Tuấn của đội cờ vua qu&acirc;n đội. Được đ&agrave;o tạo bởi những người c&oacute; chuy&ecirc;n m&ocirc;n, Hải tiến bộ tr&ocirc;ng thấy. Một th&aacute;ng sau khi v&agrave;o đội, Hải li&ecirc;n tiếp gi&agrave;nh hai huy chương v&agrave;ng to&agrave;n quốc. H&egrave; năm lớp 3, khi tham gia giải trẻ Đ&ocirc;ng Nam &Aacute;, em xuất sắc mang về hai huy chương v&agrave;ng.</p>\r\n<p class=\"Normal\">Kh&ocirc;ng chỉ l&agrave; kiện tướng cờ vua, Nguyễn Ho&agrave;ng Việt Hải c&ograve;n l&agrave; học sinh xuất sắc của lớp 4B, trường Tiểu học Khương Mai (Thanh Xu&acirc;n). 4 năm liền l&agrave; học sinh giỏi với điểm tổng kết từ 9,8 đến 10 phẩy. Với Hải, 9 điểm đ&atilde; l&agrave; thấp rồi. \"Em l&agrave; c&aacute;n sự To&aacute;n của lớp, phụ tr&aacute;ch m&ocirc;n To&aacute;n n&ecirc;n cần phải gương mẫu. Th&iacute;ch cờ vua nhưng em muốn l&agrave;m nh&agrave; To&aacute;n học như gi&aacute;o sư Ng&ocirc; Bảo Ch&acirc;u\", Hải cười n&oacute;i.</p>\r\n<p class=\"Normal\">Được coi l&agrave; c&ocirc; b&eacute; v&agrave;ng của piano, Ho&agrave;ng Phạm Tr&agrave; Mi (Khoa piano, Học viện &Acirc;m nhạc quốc gia Việt Nam) đ&atilde; xuất sắc gi&agrave;nh giải nhất cuộc thi piano quốc tế lần thứ nhất mang t&ecirc;n F.Chopin tổ chức tại Singapore. Trong cuộc thi n&agrave;y, Tr&agrave; Mi đ&atilde; vượt qua 145 th&iacute; sinh từ 15 quốc gia để đoạt giải cao nhất.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4E/12/tra_mi.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tr&agrave; Mi được xem l&agrave; c&ocirc; g&aacute;i v&agrave;ng của piano Việt Nam.&nbsp;<em>Ảnh: Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Theo bố mẹ sang Nga học từ nhỏ, Mi đ&atilde; sớm biểu hiện t&agrave;i năng to&aacute;n học v&agrave; piano tr&ecirc;n nước bạn. Trong c&aacute;c cuộc thi to&aacute;n của quận, Mi lu&ocirc;n đọat giải nhất, em c&ograve;n gi&agrave;nh giải 3 to&agrave;n Li&ecirc;n bang Nga khi c&ograve;n l&agrave; học sinh tiểu học.</p>\r\n<p class=\"Normal\">L&ecirc;n 8 tuổi, Mi bắt đầu nhận được c&aacute;c giải thưởng &acirc;m nhạc cấp th&agrave;nh phố. Năm 2008, em gi&agrave;nh giải nh&igrave; piano to&agrave;n Li&ecirc;n bang Nga. Trước khi gi&agrave;nh giải danh gi&aacute; của thế giới, th&aacute;ng 7 vừa qua, Ho&agrave;ng Phạm Tr&agrave; Mi đ&atilde; đoạt huy chương v&agrave;ng tại Li&ecirc;n hoan &acirc;m nhạc ch&acirc;u &Aacute; tổ chức tại Choenan (H&agrave;n Quốc).</p>\r\n<p class=\"Normal\">\"Hiện em l&agrave; học sinh Trung cấp 1 (hệ 11 năm) của Học viện &acirc;m nhạc quốc gia Việt Nam. Em sẽ cố gắng hơn nữa để theo đuổi con đường &acirc;m nhạc\", Tr&agrave; Mi chia sẻ.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>\r\n<p>&nbsp;</p>', 2, 2994, 0, 1, NULL, NULL),
(111, 'Thử tài thiết kế thời trang của sinh viên', 'Thu-Tai-Thiet-Ke-Thoi-Trang-Cua-Sinh-Vien', 'Mới lạ, độc đáo và mang tính ứng dụng cao là tiêu chí dành cho các tác phẩm dự thi \"Hutech Designer 2011\". Năm nay cuộc thi được mở rộng trong phạm vi toàn quốc.', 'thiet-ke-5.jpg', 22, '<h1 class=\"Title\">Thử t&agrave;i thiết kế thời trang của sinh vi&ecirc;n</h1>\r\n<h2 class=\"Lead\">Mới lạ, độc đ&aacute;o v&agrave; mang t&iacute;nh ứng dụng cao l&agrave; ti&ecirc;u ch&iacute; d&agrave;nh cho c&aacute;c t&aacute;c phẩm dự thi \"Hutech Designer 2011\". Năm nay cuộc thi được mở rộng trong phạm vi to&agrave;n quốc.</h2>\r\n<p class=\"Normal\">Cuộc thi d&agrave;nh cho tất cả sinh vi&ecirc;n, học sinh c&aacute;c trường ĐH, CĐ, TCCN v&agrave; c&aacute;c bạn trẻ y&ecirc;u th&iacute;ch thiết kế thời trang trong v&agrave; ngo&agrave;i nước.</p>\r\n<p class=\"Normal\">Với chủ đề \"H&agrave;nh tinh xanh\", th&ocirc;ng điệp ban tổ chức gửi đến c&aacute;c bạn trẻ qua cuộc thi l&agrave; h&atilde;y chung tay bảo vệ m&ocirc;i trường. Đ&acirc;y cũng được coi l&agrave; một trong những ti&ecirc;u ch&iacute; lựa chọn cho c&aacute;c t&aacute;c phẩm dự thi.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4B/5D/thiet-ke-5.jpg\" border=\"1\" alt=\"Một trong những mẫu thiết kế đạt giải cao ở cuộc thi \" width=\"480\" height=\"352\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một trong những mẫu thiết kế đạt giải cao ở cuộc thi \"Hutech Designer 2010\".</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">\"Đ&acirc;y l&agrave; cơ hội để c&aacute;c bạn trẻ m&ecirc; thiết kế thời trang thể hiện sự s&aacute;ng tạo, giao lưu học hỏi ở những người từng đạt giải tại cuộc thi lần trước. Đồng thời c&aacute;c bạn sẽ chứng tỏ khả năng lĩnh hội kiến thức được học từ nh&agrave; trường qua c&aacute;c thiết kế dự thi\", hiệu trưởng ĐH Kỹ thuật C&ocirc;ng nghệ TP HCM Hồ Đắc Lộc, đơn vị tổ chức cuộc thi n&oacute;i.</p>\r\n<p class=\"Normal\">Mẫu vẽ tham gia v&ograve;ng loại của cuộc thi sẽ được nhận từ 2/1 đến 18/2/2011 tại ĐH Kỹ thuật c&ocirc;ng nghệ - 144/24 Điện Bi&ecirc;n Phủ, phường 25, quận B&igrave;nh Thạnh. Mỗi th&iacute; sinh tham dự được quyền gửi từ 5 đến 10 mẫu vẽ. 15 bộ sưu tập tốt nhất của th&iacute; sinh được chọn sẽ c&ocirc;ng bố v&agrave;o ng&agrave;y 10/3 v&agrave; tiến h&agrave;nh may mẫu thật để tranh t&agrave;i ở v&ograve;ng chung kết.</p>\r\n<p class=\"Normal\">Đ&ecirc;m chung kết trao giải sẽ diễn ra v&agrave;o ng&agrave;y 23/4/2011 tại Nh&agrave; thi đấu Phan Đ&igrave;nh Ph&ugrave;ng, TP HCM. Cuộc thi c&oacute; một giải đặc biệt trị gi&aacute; 30 triệu đồng v&agrave; 4 giải phụ gồm: &yacute; tưởng, m&agrave;u sắc, phối hợp chất liệu v&agrave; kỹ thuật.</p>\r\n<p class=\"Normal\">Ban gi&aacute;m khảo cuộc thi l&agrave; một số giảng vi&ecirc;n c&aacute;c trường đại học v&agrave; nh&agrave; thiết kế Sỹ Ho&agrave;ng, si&ecirc;u mẫu H&agrave; Anh.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n</strong></p>', 2, 132, 0, 1, NULL, NULL),
(112, 'Hải Yến đoạt giải hoa khôi sinh viên Hà Nội', 'Hai-Yen-Doat-Giai-Hoa-Khoi-Sinh-Vien-Ha-Noi', 'Không chỉ được Ban giám khảo trao danh hiệu Hoa khôi sinh viên Hà Nội 2010, Hải Yến - sinh viên CĐ Cộng đồng Hà Nội còn là thí sinh được khán giả bình chọn nhiều nhất qua mạng.', 'hoakhoi19.jpg', 22, '<h1 class=\"Title\">Hải Yến đoạt giải hoa kh&ocirc;i sinh vi&ecirc;n H&agrave; Nội</h1>\r\n<h2 class=\"Lead\">Kh&ocirc;ng chỉ được Ban gi&aacute;m khảo trao danh hiệu Hoa kh&ocirc;i sinh vi&ecirc;n H&agrave; Nội 2010, Hải Yến - sinh vi&ecirc;n CĐ Cộng đồng H&agrave; Nội c&ograve;n l&agrave; th&iacute; sinh được kh&aacute;n giả b&igrave;nh chọn nhiều nhất qua mạng.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Image\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4B/1B/hoakhoi19.jpg\" alt=\"\" width=\"495\" height=\"322\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cuộc thi hoa kh&ocirc;i sinh vi&ecirc;n H&agrave; Nội l&agrave; cuộc thi t&ocirc;n vinh vẻ đẹp sinh vi&ecirc;n c&aacute;c trường ĐH, CĐ ngo&agrave;i c&ocirc;ng lập của thủ đ&ocirc;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4B/1B/hoakhoi50.jpg\" alt=\"\" width=\"495\" height=\"308\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Năm nay cuộc thi chọn ra 25 th&iacute; sinh xuất sắc nhất tranh t&agrave;i ở đ&ecirc;m chung kết tối 26/12.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4B/1B/hoakhoi14.jpg\" alt=\"\" width=\"495\" height=\"359\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hoa hậu Việt Nam 2004 Nguyễn Thị Huyền đến dự với tư c&aacute;ch th&agrave;nh vi&ecirc;n Ban gi&aacute;m khảo cuộc thi.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4B/1B/hoakhoi49.jpg\" alt=\"\" width=\"495\" height=\"319\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Top 5 th&iacute; sinh lọt v&agrave;o phần thi ứng xử.</td>\r\n</tr>\r\n<tr>\r\n<td align=\"middle\" valign=\"center\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4B/1B/hoakhoi44.jpg\" alt=\"\" width=\"380\" height=\"632\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Mai Th&ugrave;y Dương (ĐH Nguyễn Tr&atilde;i) l&agrave; th&iacute; sinh mặc &aacute;o d&agrave;i đẹp nhất.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4B/1B/hoakhoi8.jpg\" alt=\"\" width=\"380\" height=\"588\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Danh hiệu &Aacute; kh&ocirc;i 1 v&agrave; ứng xử hay nhất thuộc về Cầm Thu Huyền (CĐ Kinh tế Kỹ thuật).</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4B/1B/hoakhoi69.jpg\" alt=\"\" width=\"380\" height=\"570\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Danh hiệu &Aacute; kh&ocirc;i 2 được trao cho Lương Ngọc Linh (ĐH Nguyễn Tr&atilde;i).</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4B/1B/hoakhoi5.jpg\" alt=\"\" width=\"380\" height=\"570\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hoa kh&ocirc;i Hải Yến (CĐ Cộng đồng H&agrave; Nội) trả lời phần thi ứng xử.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4B/1B/hoakhoi79.jpg\" alt=\"\" width=\"495\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Kh&ocirc;ng chỉ được Ban gi&aacute;m khảo trao danh hiệu Hoa kh&ocirc;i sinh vi&ecirc;n H&agrave; Nội 2010, c&ocirc; sinh vi&ecirc;n CĐ Cộng đồng H&agrave; Nội c&ograve;n được kh&aacute;n giả b&igrave;nh chọn nhiều nhất qua mạng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>L&ecirc; Hiếu</strong></p>', 2, 1536, 0, 1, NULL, NULL),
(113, 'Đại biểu nhỏ tuổi nhất dự đại hội thi đua yêu nước', 'Dai-Bieu-Nho-Tuoi-Nhat-Du-Dai-Hoi-Thi-Dua-Yeu-Nuoc', '4 năm liền là học sinh giỏi, là lớp trưởng, sở hữu bộ huy chương vàng, bạc các môn năng khiếu trong và ngoài nước, Trần Ngọc Diễm Huyền, học sinh lớp 5 trường tiểu học Nam Thành Công (Hà Nội) là đại biểu nhỏ tuổi nhất dự đại hội thi đua yêu nước toàn quốc.', 'huyen01.jpg', 22, '<h1 class=\"Title\">Đại biểu nhỏ tuổi nhất dự đại hội thi đua y&ecirc;u nước</h1>\r\n<h2 class=\"Lead\">4 năm liền l&agrave; học sinh giỏi, l&agrave; lớp trưởng, sở hữu bộ huy chương v&agrave;ng, bạc c&aacute;c m&ocirc;n năng khiếu trong v&agrave; ngo&agrave;i nước, Trần Ngọc Diễm Huyền, học sinh lớp 5 trường tiểu học Nam Th&agrave;nh C&ocirc;ng (H&agrave; Nội) l&agrave; đại biểu nhỏ tuổi nhất dự đại hội thi đua y&ecirc;u nước to&agrave;n quốc.</h2>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Khu&ocirc;n mặt tr&ograve;n trĩnh với hai m&aacute; l&uacute;m đồng tiền, Huyền n&oacute;i chuyện chững chạc hơn so với tuổi của m&igrave;nh. C&ocirc; b&eacute; t&acirc;m sự, do được tham gia c&aacute;c hoạt động cộng đồng từ nhỏ, lại l&agrave; c&aacute;n bộ lớp n&ecirc;n em đ&atilde; học được sự tự tin trong giao tiếp v&agrave; trước đ&aacute;m đ&ocirc;ng.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Năm nay mới 10 tuổi, đang l&agrave; học sinh lớp 5N trường Tiểu học Nam Th&agrave;nh C&ocirc;ng (Đống Đa, H&agrave; Nội), Huyền nổi tiếng v&igrave; th&agrave;nh t&iacute;ch học tập v&agrave; tham gia phong tr&agrave;o s&ocirc;i nổi. Suốt 5 năm l&agrave;m lớp trưởng, năm n&agrave;o Huyền cũng gi&agrave;nh danh hiệu học sinh giỏi xuất sắc với tổng kết 10 phẩy.</span></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4A/7B/huyen01.jpg\" alt=\"\" width=\"374\" height=\"500\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&ocirc; li&ecirc;n đội trưởng trường tiểu học Nam Th&agrave;nh C&ocirc;ng sở hữu một bộ huy chương kh&aacute; đồ sộ. Ảnh:&nbsp;<em>Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">C&ocirc; gi&aacute;o Đỗ Li&ecirc;n Hương, chủ nhiệm lớp phải c&ocirc;ng nhận suốt 19 năm dạy học, học sinh giỏi của c&ocirc; rất nhiều, nhưng đa t&agrave;i v&agrave; tham gia hoạt động nhiều như Huyền th&igrave; chưa c&oacute;. \"M</span><span style=\"color: #000000;\">ới v&agrave;o lớp 1 Huyền đ&atilde; trở th&agrave;nh ng&ocirc;i sao với th&agrave;nh t&iacute;ch học tập đ&aacute;ng nể v&agrave; t&agrave;i năng hơn c&aacute;c bạn. D&ugrave; vậy, em rất h&ograve;a nh&atilde; với bạn b&egrave;, học giỏi nhưng kh&ocirc;ng phải mọt s&aacute;ch, m&agrave; Huyền chơi cũng giỏi&rdquo;, c&ocirc; Hương cho hay.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Huyền đang &ocirc;n luyện trong đội tuyển tiếng Anh v&agrave; Văn để chuẩn bị thi học sinh giỏi th&agrave;nh phố. C&ocirc; b&eacute; cho biết rất th&iacute;ch học hai m&ocirc;n n&agrave;y bởi ước mơ trở th&agrave;nh nh&agrave; ngoại giao. &ldquo;Nhưng mục ti&ecirc;u trước mắt của em l&agrave; cần cố gắng để thi đậu v&agrave;o trường cấp 2 Amsterdam H&agrave; Nội&rdquo;, Huyền cười.</span></p>\r\n<p class=\"Normal\">Lịch học của Huyền d&agrave;y đặc,&nbsp;<span style=\"color: #000000;\">cả ng&agrave;y học ở trường, tối về đi học th&ecirc;m, khi về lại ngồi v&agrave;o b&agrave;n học. Ngay cả thứ bảy, chủ nhật Huyền vẫn kh&ocirc;ng c&oacute; thời gian rảnh. C&ocirc; b&eacute; bật m&iacute; do được r&egrave;n luyện c&aacute;ch l&agrave;m việc với cường độ cao từ nhỏ n&ecirc;n c&oacute; sức khỏe tốt v&agrave; đ&atilde; quen với nếp sinh hoạt ấy, \"kh&ocirc;ng đi học em lại thấy nhớ\".</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">&ldquo;Tr&ecirc;n lớp em nghe giảng v&agrave; cố gắng l&agrave;m hết b&agrave;i tập trong s&aacute;ch gi&aacute;o khoa. Chỗ n&agrave;o kh&ocirc;ng hiểu th&igrave; hỏi c&ocirc; ngay để đỡ mất thời gian học lại&rdquo;, Huyền chia sẻ về kinh nghiệm học tập của m&igrave;nh.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Năm học 2009-2010, Huyền được bầu l&agrave;m đội trưởng li&ecirc;n đội Nam Th&agrave;nh C&ocirc;ng, cũng l&agrave; th&agrave;nh vi&ecirc;n n&ograve;ng cốt trong đội văn nghệ của trường. C&aacute;c m&ocirc;n năng khiếu như h&aacute;t, m&uacute;a, nhảy dance sport, aerobic, bơi lội&hellip;, Huyền đều tham gia v&agrave; nổi trội so với c&aacute;c bạn.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">C&ocirc; b&eacute; kể, ngay từ khi học mẫu gi&aacute;o em đ&atilde; được mẹ đưa đến sinh hoạt tại c&acirc;u lạc bộ Họa My. Ng&agrave;y đầu ti&ecirc;n đến lớp thấy c&aacute;c bạn m&uacute;a rất đẹp, em xin học m&uacute;a v&agrave; lập tức được c&aacute;c c&ocirc; chăm ch&uacute;t bởi người dẻo. Năm 2005, khi Ch&iacute; Anh - Kh&aacute;nh Thy về nước, mẹ Huyền đ&atilde; mời cặp đ&ocirc;i n&agrave;y dạy dance sport cho em. Được v&agrave;i th&aacute;ng, Huyền tham gia cuộc thi khi&ecirc;u vũ thể thao H&agrave; Nội mở rộng lần thứ nhất v&agrave; bất ngờ lọt v&agrave;o chung kết, l&agrave; th&iacute; sinh nhỏ tuổi nhất với thứ hạng 5/6.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Chỉ một năm sau đ&oacute;, Huyền đ&atilde; khẳng định được t&agrave;i năng của m&igrave;nh với m&ocirc;n thể thao n&agrave;y. Em li&ecirc;n tiếp nhận được 2 huy chương v&agrave;ng, một huy chương bạc cấp th&agrave;nh phố, 2 HCV giải khi&ecirc;u vũ thể thao to&agrave;n quốc lần thứ nhất. B&ecirc;n cạnh đ&oacute;, Huyền c&ograve;n d&agrave;nh giải nhất cuộc thi \"b&eacute; khỏe, đẹp, th&ocirc;ng minh\" to&agrave;n miền Bắc v&agrave; to&agrave;n quốc, giải th&iacute; sinh c&oacute; nhiều năng khiếu đặc biệt do b&aacute;o&nbsp;<em>Nhi đồng</em>&nbsp;tổ chức.</span></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4A/7B/huyen2.jpg\" alt=\"\" width=\"350\" height=\"480\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Năm 2007, Huyền d&agrave;nh huy chương bạc khi&ecirc;u vũ quốc tế tại Malaysia d&agrave;nh cho lứa tuổi thiếu nhi.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">C&ocirc; b&eacute; kể: \"Những ng&agrave;y đầu tập luyện em đau nhức khắp người, nhất l&agrave; đ&ocirc;i ch&acirc;n. Mỗi khi tập bằng gi&agrave;y mới, b&agrave;n ch&acirc;n em lại đau r&aacute;t v&igrave; cọ x&aacute;t. C&oacute; những l&uacute;c n&oacute; đỏ ửng l&ecirc;n, xước cả da nhưng sau đ&oacute; th&igrave; chai dần\". C&ograve;n mẹ Huyền, chị Hải Yến, th&igrave; chia sẻ:&nbsp;</span><span style=\"color: #000000;\">&ldquo;Thấy con c&oacute; năng khiếu với dance sport n&ecirc;n t&ocirc;i đầu tư cho ch&aacute;u học. Ch&aacute;u c&ograve;n nhỏ n&ecirc;n đi thi ở đ&acirc;u th&igrave; t&ocirc;i phải thu xếp c&ocirc;ng việc để đi theo&rdquo;.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Năm gặt h&aacute;i nhiều th&agrave;nh c&ocirc;ng nhất của Huyền l&agrave; 2007. C&ocirc; b&eacute; d&agrave;nh 9 huy chương v&agrave;ng, 2 huy chương bạc khu vực v&agrave; quốc tế. Huyền kể, năm đ&oacute; lần đầu ti&ecirc;n khi&ecirc;u vũ quốc tế được tổ chức ở Malaysia. Cặp đ&ocirc;i Ch&iacute; Anh - Kh&aacute;nh Thy đ&atilde; đưa Huyền theo để học hỏi v&agrave; l&agrave;m quen với m&ocirc;i trường thi đấu. Trước khi đi, Huyền đ&atilde; thi lọt v&agrave;o v&ograve;ng chung kết cuộc thi Đồ r&ecirc; m&iacute;.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">&ldquo;Em nghĩ m&igrave;nh sẽ bị loại sớm, về kịp để thi Đồ r&ecirc; m&iacute; nhưng kh&ocirc;ng ngờ lọt được v&agrave;o v&ograve;ng chung kết v&agrave; c&ograve;n d&agrave;nh huy chương bạc. Vậy l&agrave; em lỡ hẹn với cuộc thi tiếng h&aacute;t thiếu nhi v&agrave; phải nộp hồ sơ v&agrave;o năm sau&rdquo;, Huyền cười n&oacute;i.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Ngay sau khi ở Malaysia về, Huyền được mời sang Vi&ecirc;ng Chăn (L&agrave;o) biểu diễn nh&acirc;n kỷ niệm 45 năm th&agrave;nh lập quan hệ hữu nghị Việt - L&agrave;o.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Với nhiều th&agrave;nh t&iacute;ch trong học tập v&agrave; phong tr&agrave;o, Huyền nhận được nhiều học bổng của c&aacute;c tổ chức d&agrave;nh cho học sinh xuất sắc. Năm 2009, Huyền được Chủ tịch UBND H&agrave; Nội tặng danh hiệu \"Người tốt, việc tốt\" v&agrave; l&agrave; đại biểu ưu t&uacute; dự đại hội ch&aacute;u ngoan B&aacute;c Hồ to&agrave;n quốc. Huyền vinh dự được bầu l&agrave; một trong 10 gương mặt xuất sắc.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">&ldquo;C&aacute;ch đ&acirc;y một tuần, nhận tin m&igrave;nh được dự đại hội thi đua y&ecirc;u nước to&agrave;n quốc, em v&ocirc; c&ugrave;ng sung sướng v&agrave; x&uacute;c động, nhất l&agrave; khi biết m&igrave;nh l&agrave; đại biểu nhỏ tuổi nhất. Em c&ograve;n b&eacute; v&agrave; nhất định sẽ cố gắng hết m&igrave;nh trong học tập, d&agrave;nh kết quả cao nhất để xứng đ&aacute;ng với sự kỳ vọng của bố mẹ, thầy c&ocirc;&rdquo;, Huyền n&oacute;i.</span></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#f0f0ff\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Normal\">\r\n<p class=\"Normal\">Ng&agrave;y 27-28/12 tại H&agrave; Nội, 1.500 đại biểu l&agrave; những người ưu t&uacute;, ti&ecirc;u biểu cho phong tr&agrave;o thi đua y&ecirc;u nước của cả nước sẽ tham dự đại hội thi đua y&ecirc;u nước to&agrave;n quốc.</p>\r\n<p class=\"Normal\">Theo Ban tổ chức, trong số đại biểu tham dự, c&oacute; 338 anh h&ugrave;ng lao động, anh h&ugrave;ng lực lượng vũ trang nh&acirc;n d&acirc;n, đại diện tập thể anh h&ugrave;ng lao động, anh h&ugrave;ng lực lượng vũ trang nh&acirc;n d&acirc;n, chiến sĩ thi đua to&agrave;n quốc được phong tặng trong 5 năm qua. Ngo&agrave;i ra c&oacute; 1.001 điển h&igrave;nh ti&ecirc;n tiến tr&ecirc;n c&aacute;c lĩnh vực đời sống, x&atilde; hội.</p>\r\n<p class=\"Normal\">Đại biểu cao tuổi nhất l&agrave; GS, anh h&ugrave;ng lao động Vũ Khi&ecirc;u, 94 tuổi; đại biểu trẻ nhất l&agrave; Trần Ngọc Diễm Huyền, 10 tuổi, học sinh lớp 5, Trường tiểu học Nam Th&agrave;nh C&ocirc;ng (Đống Đa, H&agrave; Nội).</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><span style=\"color: #000000;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></span></p>\r\n<p>&nbsp;</p>', 2, 1541, 0, 1, NULL, NULL),
(114, 'Ông già Noel trượt patin tặng quà trẻ em', 'Ong-Gia-Noel-Truot-Patin-Tang-Qua-Tre-Em', 'Vác bịch quà trên vai, ông già Noel trượt vèo vèo trên phố tặng quà cho các em nhỏ trong đêm Giáng sinh tại Sài Gòn. Phương tiện di chuyển đặc biệt của ông gây chú ý cho đông đảo người đi đường.', 'ong-gia-noel-patin-3.jpg', 22, '<h1 class=\"Title\">&Ocirc;ng gi&agrave; Noel trượt patin tặng qu&agrave; trẻ em</h1>\r\n<h2 class=\"Lead\">V&aacute;c bịch qu&agrave; tr&ecirc;n vai, &ocirc;ng gi&agrave; Noel trượt v&egrave;o v&egrave;o tr&ecirc;n phố tặng qu&agrave; cho c&aacute;c em nhỏ trong đ&ecirc;m Gi&aacute;ng sinh tại S&agrave;i G&ograve;n. Phương tiện di chuyển đặc biệt của &ocirc;ng g&acirc;y ch&uacute; &yacute; cho đ&ocirc;ng đảo người đi đường.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4A/7E/ong-gia-noel-patin-3.jpg\" border=\"1\" alt=\"Xuất ph&aacute;t từ c&ocirc;ng vi&ecirc;n gia định, &ocirc;ng gi&agrave; Noel v&agrave; b&agrave; gi&agrave; Noel c&ugrave;ng nhiều bạn trẻ đ&atilde; di chuyển qua nhiều tuyến đường trong th&agrave;nh phố.\" width=\"400\" height=\"537\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Xuất ph&aacute;t từ C&ocirc;ng vi&ecirc;n Gia Định (quận G&ograve; Vấp, TP HCM), &ocirc;ng gi&agrave; Noel c&ugrave;ng nhiều bạn trẻ di chuyển qua nhiều tuyến đường trong th&agrave;nh phố. Đ&acirc;y l&agrave; hoạt động của C&acirc;u lạc bộ patin TP HCM tự thực hiện để mang niềm vui Gi&aacute;ng Sinh đến c&aacute;c em nhỏ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4A/7E/ong-gia-noel-pa-tin-11.jpg\" border=\"1\" alt=\"Xếp th&agrave;nh h&agrave;ng d&agrave;i di chuyển tr&ecirc;n phố giữa d&ograve;ng xe đ&ocirc;ng đ&uacute;c.\" width=\"480\" height=\"339\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Xếp th&agrave;nh h&agrave;ng d&agrave;i di chuyển tr&ecirc;n phố trong sự hiếu kỳ của người đi đường.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4A/7E/ong-gia-noel-patin-10.jpg\" border=\"1\" alt=\"C&aacute;c em nhỏ ngạc nhi&ecirc;n v&agrave; th&iacute;ch th&uacute; khi được tặng qu&agrave;.\" width=\"400\" height=\"517\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Gặp c&aacute;c gia đ&igrave;nh chở con đi chơi Noel, c&aacute;c bạn dừng lại v&agrave; tặng qu&agrave;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4A/7E/ong-gia-noel-patin-4.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"360\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">K&egrave;m theo qu&agrave; l&agrave; c&aacute;c lời ch&uacute;c b&eacute; ngoan, b&eacute; khỏe khiến c&aacute;c vị phụ huynh cười t&iacute;t mắt.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4A/7E/ong-gia-noel-patin-12.jpg\" border=\"1\" alt=\"C&aacute;c em nhỏ ngạc nhi&ecirc;n v&agrave; th&iacute;ch th&uacute; khi được tặng qu&agrave;.\" width=\"480\" height=\"364\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c em nhỏ ngạc nhi&ecirc;n v&agrave; th&iacute;ch th&uacute; khi được tặng qu&agrave;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4A/7E/ong-gia-noel-patin-5.jpg\" border=\"1\" alt=\"Bị c&aacute;c em nhỏ v&agrave; người đi đường bủa v&acirc;y.\" width=\"480\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&oacute; những l&uacute;c, c&aacute;c &ocirc;ng gi&agrave; Noel bị c&aacute;c em nhỏ v&agrave; người đi đường bủa v&acirc;y, l&agrave;m s&ocirc;i động cả g&oacute;c phố.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4A/7E/ong-gia-noel-patin-8.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"341\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Kh&ocirc;ng bao l&acirc;u 500 phần qu&agrave; đ&atilde; được chia hết. Sau khi ho&agrave;n th&agrave;nh c&ocirc;ng việc, c&aacute;c bạn trẻ của CLB mặc nguy&ecirc;n \"đồng phục\" vui chơi Noel chụp ảnh kỷ niệm với kh&aacute;ch nước ngo&agrave;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4A/7E/ong-gia-noel-patin-9.jpg\" border=\"1\" alt=\"Tạm nghỉ b&ecirc;n đường sau chặng đường d&agrave;i.\" width=\"480\" height=\"325\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cả nh&oacute;m c&oacute; một tối Gi&aacute;ng Sinh rộn r&atilde; v&agrave; &yacute; nghĩa.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n</strong></p>', 2, 2961, 0, 1, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(115, 'Tuyển người yêu đi chơi Noel', 'Tuyen-Nguoi-Yeu-Di-Choi-Noel', '\"Noel sắp đến mà tình yêu tan vỡ nên cần thuê người đi chơi cùng. Bờ vai sẽ được nhận quà do mình chuẩn bị\". Chỉ sau một ngày đăng trên mạng, Hương (23 tuổi) đóng topic vì đã tìm được đối tác trong đêm Giáng sinh.', 'Noel-2.jpg', 22, '<h1 class=\"Title\">Tuyển người y&ecirc;u đi chơi Noel</h1>\r\n<h2 class=\"Lead\">\"Noel sắp đến m&agrave; t&igrave;nh y&ecirc;u tan vỡ n&ecirc;n cần thu&ecirc; người đi chơi c&ugrave;ng. Bờ vai sẽ được nhận qu&agrave; do m&igrave;nh chuẩn bị\". Chỉ sau một ng&agrave;y đăng tr&ecirc;n mạng, Hương (23 tuổi) đ&oacute;ng topic v&igrave; đ&atilde; t&igrave;m được đối t&aacute;c trong đ&ecirc;m Gi&aacute;ng sinh.</h2>\r\n<p class=\"Normal\">Một tuần nay, c&aacute;c diễn đ&agrave;n tr&ecirc;n mạng tr&agrave;n ngập những lời rao tuyển bạn g&aacute;i (bạn trai) đi chơi Noel. Chủ nh&acirc;n đều c&oacute; phần giới thiệu về bản th&acirc;n, nội dung tuyển, y&ecirc;u cầu v&agrave; quyền lợi của người được tuyển. L&yacute; do m&agrave; c&aacute;c bạn đưa ra l&agrave; ng&agrave;y lễ, kh&ocirc;ng muốn ở nh&agrave; nằm ngủ hay đi chơi một m&igrave;nh.</p>\r\n<p class=\"Normal\">Tr&ecirc;n một diễn đ&agrave;n, c&ocirc; g&aacute;i c&oacute; ngoại h&igrave;nh kh&aacute; dễ thương viết: \"T&igrave;nh h&igrave;nh l&agrave; sắp đến Noel rồi m&agrave; em th&igrave; vẫn chưa c&oacute; người y&ecirc;u. Ai chưa c&oacute; người y&ecirc;u th&igrave; đi chơi với em\". H&agrave;ng loạt tin nhắn trả lời với những c&acirc;u hỏi \"Ti&ecirc;u chuẩn thế n&agrave;o? Em đang ở đ&acirc;u? Quyền lợi v&agrave; nghĩa vụ l&agrave; g&igrave;?...\".</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/49/28/Noel-2.jpg\" alt=\"\" width=\"480\" height=\"230\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Lời đăng tuyển của nickname t&ecirc;n Bi.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&oacute; bạn c&ograve;n đưa cả ảnh c&aacute; nh&acirc;n v&agrave; số điện thoại k&egrave;m theo những d&ograve;ng giới thiệu \"Em t&ecirc;n Bi, 21 tuổi. Noel lạnh qu&aacute; n&ecirc;n cần t&igrave;m bạn đi c&ugrave;ng. Ti&ecirc;u chuẩn l&agrave; tr&ecirc;n 22 tuổi, vui vẻ, h&ograve;a đồng, chững chạc\". Khi gọi đến số điện thoại tr&ecirc;n, Bi nhấc m&aacute;y v&agrave; th&ocirc;ng b&aacute;o m&igrave;nh đang ở trong lớp học.</p>\r\n<p class=\"Normal\">Tr&ecirc;n một trang web, c&ocirc; g&aacute;i giới thiệu t&ecirc;n Hương 23 tuổi, đang l&agrave;m gi&aacute;o vi&ecirc;n ở Hải Ph&ograve;ng nhắn: \"Noel sắp đến m&agrave; t&igrave;nh y&ecirc;u th&igrave; tan vỡ n&ecirc;n cần thu&ecirc; người đi chơi c&ugrave;ng\". C&ocirc; đảm bảo bờ vai được thu&ecirc; sẽ kh&ocirc;ng sứt mẻ, được nhận qu&agrave; do c&ocirc; chuẩn bị v&agrave; kh&ocirc;ng phải chịu một khoản ph&iacute; n&agrave;o ph&aacute;t sinh.</p>\r\n<p class=\"Normal\">Ngay sau khi đăng tuyển, c&ocirc; g&aacute;i nhận được rất nhiều sự quan t&acirc;m của cộng đồng mạng. Hơn một ng&agrave;y sau đ&oacute;, c&ocirc; tuy&ecirc;n bố \"đ&oacute;ng topic v&igrave; chuẩn bị hợp đồng với một anh\".</p>\r\n<p class=\"Normal\">Nhiều &yacute; kiến cho rằng đ&acirc;y l&agrave; một c&aacute;ch hay để những người c&ocirc; đơn t&igrave;m đến với nhau. Tuy nhi&ecirc;n, kh&ocirc;ng &iacute;t người &aacute;i ngại bởi \"Noel hay tắc đường, tắc đường th&igrave; kh&ocirc;ng về được nh&agrave;, m&agrave; kh&ocirc;ng về được nh&agrave; th&igrave; phải về nh&agrave;... nghỉ\". Hay c&oacute; người lo xa hơn \"lỡ gặp phải Sở Khanh l&agrave; hết đời lu&ocirc;n\".</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/49/28/bo_vai.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nhiều bạn chia sẻ, thấy người ta c&oacute; đ&ocirc;i, m&igrave;nh lẻ b&oacute;ng, buồn qu&aacute; n&ecirc;n t&igrave;m đến dịch vụ thu&ecirc; người y&ecirc;u. Ảnh minh họa của&nbsp;<em>L&ecirc; Hiếu.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Kh&ocirc;ng chỉ ph&aacute;i đẹp, c&aacute;c boy cũng thi nhau rao tuyển. Hưng, nam sinh vi&ecirc;n ở H&agrave; Nội nhắn: \"Cần tuyển người đi chơi Noel v&agrave; Tết dương lịch. M&igrave;nh đẹp trai nhưng hơi k&eacute;n chọn n&ecirc;n vẫn chưa c&oacute; người y&ecirc;u. Ai quan t&acirc;m th&igrave; nhắn v&agrave;o nick... Quyền lợi m&agrave; c&aacute;c bạn được hưởng l&agrave; đi xem phim, ăn KFC hay t&ugrave;y chọn c&aacute;c nh&agrave; h&agrave;ng ăn kh&aacute;c miễn ph&iacute;. Ngo&agrave;i ra, c&ograve;n được nhận lương cứng 500.000 đồng một ng&agrave;y\".</p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Y&ecirc;u cầu m&agrave; ch&agrave;ng trai n&agrave;y đưa ra l&agrave; bạn g&aacute;i c</span><span style=\"color: #000000;\">&oacute; ngoại h&igrave;nh ưa nh&igrave;n, hiền l&agrave;nh, ăn n&oacute;i dễ thương c&agrave;ng tốt, đặc biệt l&agrave; phải chưa c&oacute; chồng hay bạn trai. Sau v&agrave;i ng&agrave;y đăng tin, bạn trai n&agrave;y x&aacute;c nhận, c&oacute; rất nhiều c&ocirc; g&aacute;i đ&atilde; add nick v&agrave; hỏi chuyện khiến v&ograve;ng \"sơ tuyển\" v&ocirc; c&ugrave;ng nhộn nhịp.</span></p>\r\n<p class=\"Normal\"><strong><span style=\"color: #4f4f4f;\">Tuy nhi&ecirc;n, để tr&aacute;nh rủi ro, kh&ocirc;ng &iacute;t người bỏ tiền thu&ecirc; người y&ecirc;u để đi chơi Noel</span></strong>. Đ&igrave;nh Thắng, một kỹ sư đang l&agrave;m việc tại Cầu Giấy cho biết, c&ocirc;ng việc bận rộn khiến anh chẳng c&ograve;n thời gian cho chuyện ri&ecirc;ng. Noel thấy người ta đi chơi hết, ở nh&agrave; một m&igrave;nh buồn n&ecirc;n đ&atilde; nhờ tới dịch vụ cho thu&ecirc; người y&ecirc;u.</p>\r\n<p class=\"Normal\">\"Bỏ ra tiền triệu để c&oacute; được niềm vui, đ&oacute; cũng l&agrave; sự b&ugrave; trừ th&ocirc;i. B&igrave;nh thường t&ocirc;i l&agrave;m việc nhiều để kiếm tiền n&ecirc;n qu&ecirc;n mất sự c&ocirc; đơn. Những ng&agrave;y lễ Tết mới sực nhớ đến, c&aacute;ch tốt nhất l&agrave; d&ugrave;ng tiền để b&ugrave; đắp sự trống trải đ&oacute;\", Thắng t&acirc;m sự.</p>\r\n<p class=\"Normal\">Trao đổi với&nbsp;<em>VnExpress</em>, gi&aacute;m đốc một c&ocirc;ng ty cho thu&ecirc; người y&ecirc;u cho biết, dịp Noel năm nay, số lượng kh&aacute;ch h&agrave;ng tăng l&ecirc;n đột biến. Do đ&atilde; dự tr&ugrave; từ trước n&ecirc;n c&ocirc;ng ty vẫn đ&aacute;p ứng được nhu cầu của kh&aacute;ch h&agrave;ng.</p>\r\n<p class=\"Normal\">\"Ch&uacute;ng t&ocirc;i đ&atilde; giảm gi&aacute; một nửa cho kh&aacute;ch. Để thu&ecirc; một người đi chơi cả tối Noel, kh&aacute;ch h&agrave;ng phải trả một khoản ph&iacute; khoảng một triệu đồng, đồng thời phải cam kết đảm bảo an to&agrave;n cho người được thu&ecirc;\", vị gi&aacute;m đốc n&agrave;y cho hay.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>\r\n<p class=\"Normal\"><em>* T&ecirc;n nh&acirc;n vật đ&atilde; được thay đổi</em></p>\r\n<p>&nbsp;</p>', 2, 2768, 0, 1, NULL, NULL),
(116, 'Những món quà dễ thương của teen mùa Giáng sinh', 'Nhung-Mon-Qua-De-Thuong-Cua-Teen-Mua-Giang-Sinh', 'Những món quà handmade dễ thương, ý nghĩa đang là lựa chọn của các bạn trẻ dành tặng bạn bè và người yêu dịp Noel.', 'goi_in_hinh_1.jpg', 22, '<h1 class=\"Title\">Những m&oacute;n qu&agrave; dễ thương của teen m&ugrave;a Gi&aacute;ng sinh</h1>\r\n<h2 class=\"Lead\">Những m&oacute;n qu&agrave; handmade dễ thương, &yacute; nghĩa đang l&agrave; lựa chọn của c&aacute;c bạn trẻ d&agrave;nh tặng bạn b&egrave; v&agrave; người y&ecirc;u dịp Noel.</h2>\r\n<p class=\"Normal\">Cả tuần nay, Huy Tuấn (ĐH Quốc gia H&agrave; Nội) h&aacute;o hức l&ecirc;n phương &aacute;n chọn qu&agrave; cho bạn g&aacute;i dịp Noel. Sau hai th&aacute;ng y&ecirc;u nhau, cậu mới tặng cho bạn g&aacute;i những m&oacute;n qu&agrave; nho nhỏ như khăn ấm m&ugrave;a đ&ocirc;ng, tất tay, dịp n&agrave;y cậu muốn d&agrave;nh bất ngờ cho người thương.</p>\r\n<p class=\"Normal\">Hỏi han bậc \"đ&agrave;n anh\" c&oacute; kinh nghiệm, thăm d&ograve; &yacute; kiến c&aacute;c bạn nữ, Tuấn quyết t&igrave;m m&oacute;n qu&agrave; \"đẹp, độc, &yacute; nghĩa\" l&agrave;m cho bạn g&aacute;i x&uacute;c động đến... ph&aacute;t kh&oacute;c. Tuy nhi&ecirc;n, lang thang c&aacute;c phố b&aacute;n đồ lưu niệm suốt một ng&agrave;y m&agrave; Tuấn chưa t&igrave;m ra m&oacute;n n&agrave;o \"độc\".</p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Đến khi người bạn th&ocirc;ng b&aacute;o c&oacute; một cửa h&agrave;ng l&agrave;m gối handmade in h&igrave;nh theo y&ecirc;u cầu, Tuấn mừng rơn v&agrave; ph&oacute;ng một mạch đến đặt ngay chiếc gối h&igrave;nh tr&aacute;i tim c&oacute; in h&igrave;nh hai đứa với lời nhắn \"Ngủ ngon nha c&uacute;n y&ecirc;u\".</span></p>\r\n<p class=\"Normal\">\"Mỗi khi ngủ c&ocirc; ấy lại nh&igrave;n chiếc gối v&agrave; nhớ đến em. Hy vọng lời ch&uacute;c tr&ecirc;n đ&oacute; sẽ l&agrave;m c&ocirc; ấy thấy ấm &aacute;p trong m&ugrave;a đ&ocirc;ng lạnh gi&aacute; n&agrave;y\", Tuấn n&oacute;i.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/48/A8/goi_in_hinh_1.jpg\" alt=\"\" width=\"450\" height=\"320\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">In h&igrave;nh người y&ecirc;u l&ecirc;n gối l&agrave;m qu&agrave; tặng đang l&agrave; \"mốt\" của teen.&nbsp;<em>Ảnh: HT.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">L&agrave; người l&atilde;ng mạn, Phạm H&ugrave;ng (ĐH B&aacute;ch khoa H&agrave; Nội) lu&ocirc;n tạo sự bất ngờ cho bạn g&aacute;i. Dịp Noel n&agrave;y, ngo&agrave;i việc dẫn n&agrave;ng đi chơi, H&ugrave;ng đ&atilde; nghe ng&oacute;ng c&oacute; dịch vụ mới, đ&oacute; cơm hộp santu với những suất ăn được tạo h&igrave;nh theo y&ecirc;u cầu.</p>\r\n<p class=\"Normal\">\"Hai đứa ngồi b&ecirc;n nhau, c&ugrave;ng ăn cơm được tạo h&igrave;nh &ocirc;ng gi&agrave; Noel, ng&ocirc;i nh&agrave; tuyết, c&oacute; những b&iacute;t tất v&agrave; hộp qu&agrave; với lời ch&uacute;c mừng Gi&aacute;ng sinh v&agrave; \"I love you\" th&igrave; c&ograve;n g&igrave; tuyệt vời bằng\", H&ugrave;ng n&oacute;i.</p>\r\n<p class=\"Normal\">Trao đổi với&nbsp;<em>VnExpress</em>, Trưởng nh&oacute;m l&agrave;m cơm hộp santu Hồ Thị Thanh Huyền cho biết, nh&oacute;m c&oacute; 3 người đều l&agrave; sinh vi&ecirc;n. Từ đầu m&ugrave;a đến giờ, cả nh&oacute;m tất bật v&igrave; lượng đặt h&agrave;ng rất lớn. Gi&aacute; mỗi suất từ 120.000 đồng trở l&ecirc;n, bao gồm cả hộp v&agrave; th&igrave;a.</p>\r\n<p class=\"Normal\">\"Bọn em l&agrave;m theo mẫu tự nghĩ ra, đồng thời cũng l&agrave;m theo y&ecirc;u cầu của kh&aacute;ch h&agrave;ng\", Huyền cho hay.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/48/A8/nha_noel.jpg\" alt=\"\" width=\"450\" height=\"320\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ng&ocirc;i nh&agrave; với &ocirc;ng gi&agrave; Noel, đầy tuyết v&agrave; lời ch&uacute;c an l&agrave;nh được giới trẻ săn t&igrave;m.&nbsp;<em>Ảnh: HT.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Ng&ocirc;i nh&agrave; Gi&aacute;ng sinh được phủ đầy tuyết với &ocirc;ng gi&agrave; Noel cũng l&agrave; mẫu qu&agrave; được giới trẻ săn đ&oacute;n. L&ecirc; Minh (ĐH Kinh tế) cho biết, do lang thang ở tr&ecirc;n mạng cậu v&ocirc; t&igrave;nh bắt gặp lời giới thiệu về ng&ocirc;i nh&agrave; Gi&aacute;ng sinh. Xem qua c&aacute;c mẫu, cậu vui sướng đặt l&agrave;m một ng&ocirc;i nh&agrave; đ&ocirc;i với lời ch&uacute;c an l&agrave;nh cho bạn g&aacute;i. Một tr&aacute;i tim xinh xắn l&agrave;m biển chỉ dẫn v&agrave;o ng&ocirc;i nh&agrave; với hai từ \"hạnh ph&uacute;c\".</p>\r\n<p class=\"Normal\">\"Ng&ocirc;i nh&agrave; l&agrave; lời nhắn gửi về một tương lại hạnh ph&uacute;c của hai đứa. C&oacute; những m&oacute;n qu&agrave; gi&aacute; trị rất lớn nhưng &yacute; nghĩa th&igrave; kh&ocirc;ng thể n&agrave;o bằng m&oacute;n qu&agrave; thủ c&ocirc;ng n&agrave;y\", Minh chia sẻ.</p>\r\n<p class=\"Normal\">Thiệp handmade với những h&igrave;nh ảnh &ocirc;ng gi&agrave; Noel, c&acirc;y th&ocirc;ng, tuyết trắng... cũng được giới trẻ t&igrave;m kiếm. Quỳnh Anh (Học viện B&aacute;o ch&iacute;) cho biết, năm n&agrave;o đến Gi&aacute;ng sinh c&ocirc; cũng d&agrave;nh thời gian l&agrave;m thiệp để tặng bạn b&egrave;, người th&acirc;n. Sau buổi học, c&ocirc; ngồi cặm cụi b&ecirc;n đống giấy m&agrave;u cứng, những c&acirc;y b&uacute;t m&agrave;u &oacute;ng &aacute;nh v&agrave; miệt m&agrave;i cắt d&aacute;n.</p>\r\n<p class=\"Normal\">\"D&ugrave; phải thức rất khuya để l&agrave;m em vẫn thấy hạnh ph&uacute;c bởi th&ocirc;ng qua tấm thiệp đ&oacute;, em muốn gửi t&igrave;nh cảm của m&igrave;nh với những người m&igrave;nh thương y&ecirc;u\", Quỳnh Anh chia sẻ.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>\r\n<p>&nbsp;</p>', 2, 176, 0, 1, NULL, NULL),
(117, 'Gửi thiệp Giáng sinh cho người yêu thương', 'Gui-Thiep-Giang-Sinh-Cho-Nguoi-Yeu-Thuong', '“Tặng thiệp Giáng sinh – Rinh quà hấp dẫn” là chương trình do báo điện tử Ngoisao.net tổ chức. Những chiếc thiệp do độc giả sáng tạo hoặc lấy từ mẫu của chương trình sẽ được đăng và được BTC gửi tới e-mail người nhận.', 'Thiep-1.jpg', 22, '<h1 class=\"Title\">Gửi thiệp Gi&aacute;ng sinh cho người y&ecirc;u thương</h1>\r\n<h2 class=\"Lead\">&ldquo;Tặng thiệp Gi&aacute;ng sinh &ndash; Rinh qu&agrave; hấp dẫn&rdquo; l&agrave; chương tr&igrave;nh do b&aacute;o điện tử Ngoisao.net tổ chức. Những chiếc thiệp do độc giả s&aacute;ng tạo hoặc lấy từ mẫu của chương tr&igrave;nh sẽ được đăng v&agrave; được BTC gửi tới e-mail người nhận.</h2>\r\n<p class=\"Normal\">Chương tr&igrave;nh k&eacute;o d&agrave;i tới ng&agrave;y 25/12 nhằm tạo điều kiện cho mọi người cơ hội b&agrave;y tỏ t&igrave;nh cảm của m&igrave;nh với người th&acirc;n, bạn b&egrave;&hellip; nh&acirc;n dịp Gi&aacute;ng sinh. Độc giả truy cập v&agrave;o&nbsp;<a class=\"Normal\" href=\"http://event.ngoisao.net/xmas\">http://event.ngoisao.net/xmas</a>, tự tạo thiệp hoặc chọn theo mẫu sẵn của chương tr&igrave;nh, sau đ&oacute; điền c&aacute;c th&ocirc;ng tin theo y&ecirc;u cầu để l&agrave;m căn cứ trao giải. Thiệp của người gửi sẽ được ban tổ chức đăng tr&ecirc;n website của chương tr&igrave;nh v&agrave; gửi email th&ocirc;ng b&aacute;o đến cho người được nhận thiệp.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/48/49/Thiep-1.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"260\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Những tấm thiệp do bạn đọc tự thiết kế c&oacute; k&iacute;ch thước kh&ocirc;ng qu&aacute; 1.000 x 1.000 pixel v&agrave; dung lượng kh&ocirc;ng qu&aacute; 1 MB. Phần ti&ecirc;u đề của thiệp tối đa 200 k&yacute; tự v&agrave; nội dung của thiệp chỉ 1.000 k&yacute; tự. Thiệp của độc giả sẽ được đăng tr&ecirc;n website của chương tr&igrave;nh v&agrave; được th&ocirc;ng b&aacute;o đến địa chỉ email của người nhận trong thời gian chậm nhất 24h kể từ khi gửi.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"404\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/48/49/Thiep-2.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"270\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">10 thiệp đẹp nhất do ban tổ chức chọn ra sẽ nhận được một phần qu&agrave; của mỹ phẩm Quốc Hưng trị gi&aacute; 2.500.000 đồng. Ngo&agrave;i ra, chương tr&igrave;nh cũng trao giải cho người gửi v&agrave; người nhận thuộc top 10 thiệp được b&igrave;nh chọn nhiều nhất. Nhiều phần qu&agrave; kh&aacute;c l&agrave; phiếu mua h&agrave;ng trị gi&aacute; của c&aacute;c h&atilde;ng mỹ phẩm, thời trang, đồng hồ...</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ngọc Điệp</strong></p>', 2, 2017, 0, 1, NULL, NULL),
(118, 'Hot girl Sài Gòn xinh tươi đón Noel', 'Hot-Girl-Sai-Gon-Xinh-Tuoi-Don-Noel', 'Diện váy đỏ, choàng khăn len trắng, hot girl Bảo Ngọc thích thú khi được đắm mình trong gian đầy màu sắc khi mùa Giáng sinh đang đến gần. \r\n', '250.jpg', 22, '<h1 class=\"Title\">Hot girl S&agrave;i G&ograve;n xinh tươi đ&oacute;n Noel</h1>\r\n<h2 class=\"Lead\">Diện v&aacute;y đỏ, cho&agrave;ng khăn len trắng, hot girl Bảo Ngọc th&iacute;ch th&uacute; khi được đắm m&igrave;nh trong gian đầy m&agrave;u sắc khi m&ugrave;a Gi&aacute;ng sinh đang đến gần.&nbsp;<br /><br /></h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/40/FD/bao-ngoc-noel-1.jpg\" border=\"1\" alt=\"diện chiếc m&aacute;y đỏ Bảo Ngọc l&agrave;m điệu b&ecirc;n c&acirc;y th&ocirc;ng.\" width=\"480\" height=\"320\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Chiếc v&aacute;y đỏ của c&ocirc; c&agrave;ng l&agrave;m t&ocirc; đậm th&ecirc;m m&agrave;u sắc v&agrave; kh&ocirc;ng kh&iacute; Noel rộn r&agrave;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/40/FD/bao-ngoc-noel-3.jpg\" border=\"1\" alt=\"C&ocirc; g&aacute;i trẻ th&iacute;ch những m&oacute; qu&agrave; được đựng trong p&iacute;t tất v&igrave; n&oacute; mang lại nhiều may mắ.n\" width=\"400\" height=\"578\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&ocirc; g&aacute;i trẻ th&iacute;ch th&uacute; khi được trang tr&iacute; cho c&acirc;y th&ocirc;ng bằng những chiếc b&iacute;t tất nhỏ nhiều m&agrave;u sắc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/40/FD/bao-ngoc-noel-2.jpg\" border=\"1\" alt=\"thực hiện bộ ảnh, c&ocirc; chia sẻ muốn c&oacute; những kỷ niệm đẹp m&ugrave;a gi&aacute;ng sinh.\" width=\"400\" height=\"568\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">V&agrave; cả những v&ograve;ng nguyệt quế treo trước cửa ra v&agrave;o.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/40/FD/bao-ngoc-noel-5.jpg\" border=\"1\" alt=\"Chiếc &aacute;o cho&agrave;ng len trắng c&ocirc; th&iacute;ch cảm gi&aacute;c xe lạnh của m&ugrave;a đ&ocirc;ng.\" width=\"466\" height=\"273\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&ocirc; cũng t&ograve; m&ograve; với m&oacute;n đồ trang tr&iacute; lạ lẫm l&agrave;m bằng vỏ trứng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/40/FD/bao-ngoc-noel-9.jpg\" border=\"1\" alt=\"kh&ocirc;ng theo đạo nhưng c&ocirc; rất th&iacute;ch mỗi dịp noel đến.\" width=\"400\" height=\"568\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&ocirc; cho biết rất h&aacute;o hức khi m&ugrave;a Gi&aacute;ng sinh đến v&igrave; sinh nhật của ba m&igrave;nh v&agrave;o đ&uacute;ng ng&agrave;y Noel. Hơn nữa Ngọc cũng th&iacute;ch cảm gi&aacute;c se lạnh của thời tiết, đi dạo phố v&agrave; ngắm những khung cửa được trang tr&iacute; bắt mắt.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/40/FD/bao-ngoc-noel-8.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"488\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">\"Sẽ rất th&uacute; vị nếu như ngồi đọc s&aacute;ch thư th&aacute;i trong một kh&ocirc;ng gian thơ mộng\", c&ocirc; nữ sinh n&oacute;i. Ngọc cho biết thời gian gần đ&acirc;y c&ocirc; g&aacute;c lại mọi hoạt động kh&aacute;c để tập trung v&agrave;o việc học cho năm cuối cấp.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/40/FD/bao-ngoc-noel-10.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Vẻ đẹp trong s&aacute;ng của nữ sinh 17 tuổi, khiến c&ocirc; được nhiều kh&aacute;n giả trẻ y&ecirc;u th&iacute;ch tr&ecirc;n c&aacute;c tạp ch&iacute; tuổi teen.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>B&igrave;nh Nguy&ecirc;n<br /></strong><em>Ảnh</em><strong>: Lavender</strong></p>', 2, 1751, 0, 1, NULL, NULL),
(119, 'Thành đoàn Hà Nội nhận giải tổ chức đoàn xuất sắc ASEAN', 'Thanh-Doan-Ha-Noi-Nhan-Giai-To-Chuc-Doan-Xuat-Sac-Asean', 'Trong ngày thanh niên Đông Nam Á được tổ chức từ 18 đến 21/12, thành đoàn Hà Nội đã nhận giải tổ chức thanh niên xuất sắc khu vực.', 'doan.jpg', 22, '<h1 class=\"Title\">Th&agrave;nh đo&agrave;n H&agrave; Nội nhận giải tổ chức đo&agrave;n xuất sắc ASEAN</h1>\r\n<h2 class=\"Lead\">Trong ng&agrave;y thanh ni&ecirc;n Đ&ocirc;ng Nam &Aacute; được tổ chức từ 18 đến 21/12, th&agrave;nh đo&agrave;n H&agrave; Nội đ&atilde; nhận giải tổ chức thanh ni&ecirc;n xuất sắc khu vực.</h2>\r\n<p class=\"Normal\">Ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ ch&iacute;nh trị của thủ đ&ocirc;, đ&oacute;ng g&oacute;p quan trọng trong đại lễ kỷ niệm 1000 năm Thăng Long, th&agrave;nh đo&agrave;n H&agrave; Nội c&ograve;n được đ&aacute;nh gi&aacute; cao v&igrave; c&oacute; nhiều th&agrave;nh t&iacute;ch trong lĩnh vực bảo vệ m&ocirc;i trường, văn h&oacute;a gi&aacute;o dục, nghề nghiệp, việc l&agrave;m, sức khỏe vị th&agrave;nh ni&ecirc;n, thanh ni&ecirc;n t&igrave;nh nguyện&hellip;</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/47/93/doan.jpg\" alt=\"\" width=\"450\" height=\"320\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Anh Ngọ Duy Hiểu (phải) nhận huy hiệu tổ chức thanh ni&ecirc;n xu&acirc;t sắc.&nbsp;<em>Ảnh: TĐ.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">B&iacute; thư Th&agrave;nh đo&agrave;n H&agrave; Nội Ngọ Duy Hiểu cho biết<em>,&nbsp;</em>rất x&uacute;c động khi được thay mặt c&aacute;c bạn trẻ thủ đ&ocirc; đi nhận giải thưởng. \"Vinh dự n&agrave;y thuộc về gần 600.000 đo&agrave;n vi&ecirc;n thanh ni&ecirc;n H&agrave; Nội chứ kh&ocirc;ng phải một m&igrave;nh th&agrave;nh đo&agrave;n\", anh Hiểu n&oacute;i.</p>\r\n<p class=\"Normal\">Ng&agrave;y Thanh ni&ecirc;n Đ&ocirc;ng Nam &Aacute; do Ủy ban hợp t&aacute;c thanh ni&ecirc;n ASEAN v&agrave; Hội đồng quốc gia thanh ni&ecirc;n Philippines tổ chức tại Manila từ ng&agrave;y 18 đến 21/12. 10 tổ chức thanh ni&ecirc;n được nhận danh hiệu tổ chức Đo&agrave;n xuất sắc, trong đ&oacute; th&agrave;nh đo&agrave;n H&agrave; Nội được chấm điểm cao nhất.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 2, 29, 0, 1, NULL, NULL),
(219, 'Châu Á rộn ràng đón Tết', 'Chau-A-Ron-Rang-Don-Tet', 'Màu đỏ, sắc hồng rực rỡ của hoa, đèn, bánh kẹo, thư pháp, váy áo đã ngập tràn khắp châu Á, xen lẫn giữa những gương mặt lo âu và dòng người hối hả lo cho Tết Nguyên đán đang đến rất gần.', '2 (1).jpg', 22, '<h1 class=\"Title\">Ch&acirc;u &Aacute; rộn r&agrave;ng đ&oacute;n Tết</h1>\r\n<h2 class=\"Lead\">M&agrave;u đỏ, sắc hồng rực rỡ của hoa, đ&egrave;n, b&aacute;nh kẹo, thư ph&aacute;p, v&aacute;y &aacute;o đ&atilde; ngập tr&agrave;n khắp ch&acirc;u &Aacute;, xen lẫn giữa những gương mặt lo &acirc;u v&agrave; d&ograve;ng người hối hả lo cho Tết Nguy&ecirc;n đ&aacute;n đang đến rất gần.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/5C/30/2.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"326\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Trẻ em ở tỉnh An Huy, Trung Quốc, mặc đồ c&oacute; thư ph&aacute;p chữ \"Thỏ\". Năm &acirc;m lịch tới đ&acirc;y ở Trung Quốc được cho l&agrave; năm con Thỏ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/5C/30/3.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"370\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">V&igrave; thế Thỏ hiện diện khắp nơi khi Tết Nguy&ecirc;n đ&aacute;n gần kề. Trong ảnh, hai người đ&agrave;n &ocirc;ng n&oacute;i chuyện ph&iacute;a trước một h&igrave;nh nộm thỏ to tướng tại một trung t&acirc;m thương mại ở Bắc Kinh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/5C/30/4.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tr&ecirc;n s&acirc;n ga Bắc Kinh, cũng như v&ocirc; số nh&agrave; ga kh&aacute;c ở Trung Quốc, người người đang hối hả đổ về qu&ecirc; hương để ăn tết sum họp với gia đ&igrave;nh. Nh&agrave; chức tr&aacute;ch Trung Quốc cho hay sẽ c&oacute; tới 2 tỷ lượt di chuyển tại nước n&agrave;y trước trong v&agrave; sau Tết, tạo th&agrave;nh cuộc di cư lớn nhất của lo&agrave;i người mỗi năm.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/5C/30/5.jpg\" border=\"1\" alt=\"\" width=\"380\" height=\"530\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&acirc;y hoa m&ugrave;a xu&acirc;n được b&agrave;y b&aacute;n ở Jakarta, Indonesia. Một số người nước n&agrave;y cũng ăn tết &acirc;m lịch.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/5C/30/7.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"313\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">B&ecirc;n cạnh h&igrave;nh trang tr&iacute; l&agrave; Thỏ cũng c&oacute; cả h&igrave;nh M&egrave;o. Người Việt Nam ta gọi năm nay l&agrave; năm con m&egrave;o.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/5C/30/8.jpg\" border=\"1\" alt=\"\" width=\"380\" height=\"532\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một nh&acirc;n c&ocirc;ng đang được hai đồng nghiệp giữ để treo đ&egrave;n lồng đ&oacute;n Tết tại Bắc Kinh, ng&agrave;y 21/1.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/5C/30/9.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"321\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những ng&agrave;y n&agrave;y, thỏ l&agrave; con vật nu&ocirc;i được t&igrave;m mua nhiều. Tuy nhi&ecirc;n c&aacute;c nh&agrave; hoạt động v&igrave; quyền động vật e ngại rằng sau Tết sẽ c&oacute; nhiều con thỏ bị bỏ mặc kh&ocirc;ng được chăm s&oacute;c. Trong ảnh l&agrave; những con thỏ dễ thương được b&agrave;y b&aacute;n tr&ecirc;n vỉa h&egrave; ở T&acirc;yJava, Indonesia.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/5C/30/11.jpg\" border=\"1\" alt=\"\" width=\"380\" height=\"487\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c b&agrave; c&aacute;c chị Đ&agrave;i Loan chen ch&acirc;n chọn b&aacute;nh kẹo trong chợ Tết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/5C/30/1.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"371\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một cửa h&agrave;ng b&aacute;n độ trang tr&iacute; Tết ở Đ&agrave;i Loan.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/5C/30/10.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"343\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c em thiếu nhi Đ&agrave;i Loan trang điểm v&agrave; chơi đồ c&oacute; h&igrave;nh ảnh đẹp đẽ của con vật tượng trưng cho năm mới.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Mai Trang</strong>&nbsp;(Ảnh:&nbsp;<em>AFP</em>)</p>', 6, 241, 0, 1, NULL, NULL),
(220, 'Người Trung Quốc nhọc nhằn về quê ăn tết', 'Nguoi-Trung-Quoc-Nhoc-Nhan-Ve-Que-An-Tet', 'Hành lý nặng trĩu vai, dòng người chen chân tại các nhà ga, bến xe ở Trung Quốc để về đoàn tụ cùng gia đình dịp Tết Nguyên đán.', 'cn11.jpg', 22, '<h1 class=\"Title\">Người Trung Quốc nhọc nhằn về qu&ecirc; ăn tết</h1>\r\n<h2 class=\"Lead\">H&agrave;nh l&yacute; nặng trĩu vai, d&ograve;ng người chen ch&acirc;n tại c&aacute;c nh&agrave; ga, bến xe ở Trung Quốc để về đo&agrave;n tụ c&ugrave;ng gia đ&igrave;nh dịp Tết Nguy&ecirc;n đ&aacute;n.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/B6/cn11.jpg\" alt=\"Đ&aacute;m đ&ocirc;ng chen lấn l&ecirc;n xe về qu&ecirc; ăn tết ở tỉnh Chiết Giang, Trung Quốc. Ảnh: China Daily.\" width=\"450\" height=\"307\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đ&aacute;m đ&ocirc;ng chen lấn l&ecirc;n xe về qu&ecirc; ăn tết ở tỉnh Chiết Giang, Trung Quốc. Ảnh:&nbsp;<em>China Daily</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/B6/cn34.jpg\" alt=\"Người đ&agrave;n &ocirc;ng n&agrave;y nặng cả trước cả sau khi tr&ecirc;n lưng l&agrave; h&agrave;nh l&yacute; v&agrave; trước bụng l&agrave; chiếc Tivi từ Chiết Giang về Th&agrave;nh Đ&ocirc;, Tứ Xuy&ecirc;n. Ảnh: Xinhua.\" width=\"380\" height=\"462\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Người đ&agrave;n &ocirc;ng n&agrave;y nặng cả trước cả sau khi tr&ecirc;n lưng l&agrave; h&agrave;nh l&yacute; v&agrave; trước bụng l&agrave; chiếc tivi từ Chiết Giang về Th&agrave;nh Đ&ocirc;, Tứ Xuy&ecirc;n. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/B6/cn32.jpg\" alt=\"Hai phụ nữ mang h&agrave;nh l&yacute; nặng trĩu ra t&agrave;u ở H&agrave;ng Ch&acirc;u, Chiết Giang, để về qu&ecirc; nh&agrave; ở tỉnh V&acirc;n Nam. Ảnh: Xinhua.\" width=\"450\" height=\"302\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hai phụ nữ mang h&agrave;nh l&yacute; nặng trĩu ra t&agrave;u ở H&agrave;ng Ch&acirc;u, Chiết Giang, để về qu&ecirc; nh&agrave; ở tỉnh V&acirc;n Nam. Bộ đường sắt Trung Quốc triển khai th&ecirc;m 350 chuyến t&agrave;u mỗi ng&agrave;y suốt 40 ng&agrave;y trước v&agrave; sau tết. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/B6/cn31.jpg\" alt=\"H&agrave;nh kh&aacute;ch xếp h&agrave;ng chờ l&ecirc;n t&agrave;u ở H&agrave;ng Ch&acirc;u, Chiết Giang.\" width=\"450\" height=\"293\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">H&agrave;nh kh&aacute;ch xếp h&agrave;ng chờ l&ecirc;n t&agrave;u ở H&agrave;ng Ch&acirc;u, Chiết Giang. Khoảng 230 triệu người Trung Quốc đi t&agrave;u về qu&ecirc; dịp tết nguy&ecirc;n đ&aacute;n n&agrave;y. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/B6/cn33.jpg\" alt=\"B&agrave; c&otilde;ng ch&aacute;u ra ga ở H&agrave;ng Ch&acirc;u, Chiết Giang, để về Qu&yacute; Ch&acirc;u.\" width=\"380\" height=\"471\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">B&agrave; c&otilde;ng ch&aacute;u ra ga ở H&agrave;ng Ch&acirc;u, Chiết Giang, để về Qu&yacute; Ch&acirc;u. Wang Zhigang, Thứ trưởng Bộ Đường sắt Trung Quốc, cho rằng để giảm t&igrave;nh trạng khan v&eacute;, nước n&agrave;y cần cải hiện hệ thống đường sắt v&agrave; tăng sức vận chuyển. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/B6/cn35.jpg\" alt=\"Cậu b&eacute; 13 tuổi n&agrave;y c&otilde;ng đống h&agrave;nh l&yacute; to hơn người. Ảnh: Xinhua.\" width=\"450\" height=\"281\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cậu b&eacute; 13 tuổi n&agrave;y c&otilde;ng đống h&agrave;nh l&yacute; to hơn người ở nh&agrave; ga H&agrave;ng Ch&acirc;u. T&igrave;nh trạng khan v&eacute; trở n&ecirc;n nghi&ecirc;m trọng hơn bởi nhiều lao động di cư chủ yếu tập trung tới c&aacute;c th&agrave;nh phố lớn v&agrave; ph&aacute;t triển như Bắc Kinh, Thượng Hải v&agrave; Quảng Ch&acirc;u. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/B6/cn36.jpg\" alt=\"C&aacute;c em b&eacute; cũng theo cha mẹ xếp h&agrave;ng chờ về qu&ecirc;. Ảnh: Xinhua.\" width=\"450\" height=\"274\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c em b&eacute; cũng theo cha mẹ xếp h&agrave;ng chờ về qu&ecirc;. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ngọc Sơn</strong></p>', 6, 1204, 0, 1, NULL, NULL),
(221, 'Yến tiệc đãi Chủ tịch Trung Quốc tại Mỹ', 'Yen-Tiec-Dai-Chu-Tich-Trung-Quoc-Tai-My', 'Đệ nhất phu nhân Mỹ trong bộ dạ hội lộng lẫy đứng bên cạnh Tổng thống Barack Obama đón chào Chủ tịch Trung Quốc Hồ Cẩm Đào tới dự bữa yến tiệc được trưng bày xa hoa tại Nhà Trắng, tối 19/1.', 'ho1.jpg', 22, '<h1 class=\"Title\">Yến tiệc đ&atilde;i Chủ tịch Trung Quốc tại Mỹ</h1>\r\n<h2 class=\"Lead\">Đệ nhất phu nh&acirc;n Mỹ trong bộ dạ hội lộng lẫy đứng b&ecirc;n cạnh Tổng thống Barack Obama đ&oacute;n ch&agrave;o Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o tới dự bữa yến tiệc được trưng b&agrave;y xa hoa tại Nh&agrave; Trắng, tối 19/1.<br /><br /></h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/E2/ho1.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"349\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tổng thống Mỹ Barack Obama, Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o c&ugrave;ng Đệ nhất phu nh&acirc;n Mỹ Michelle Obama tới dự bữa quốc tiệc tại Nh&agrave; Trắng ở Washington DC, h&ocirc;m 19/1.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/E2/ho3.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"296\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đệ nhất phu nh&acirc;n Mỹ Michelle Obama c&ugrave;ng Tổng thống Obama đ&oacute;n ch&agrave;o Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o khi &ocirc;ng tới North Portico ở Nh&agrave; Trắng, trước khi tham dự bữa đại tiệc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/E2/ho6.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"320\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c b&agrave;n tiệc trong ph&ograve;ng Đỏ của Nh&agrave; Trắng được b&agrave;i tr&iacute; sẵn s&agrave;ng cho bữa quốc tiệc đ&oacute;n mừng Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/E2/ho7.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"320\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">B&agrave;n tiệc được sắp đặt c&ocirc;ng phu cho bữa quốc tiệc tại ph&ograve;ng Xanh dương của Nh&agrave; Trắng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/E2/ho8.jpg\" border=\"1\" alt=\"\" width=\"341\" height=\"512\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c nh&acirc;n vi&ecirc;n Nh&agrave; Trắng cẩn thận chuẩn bị b&agrave;n ăn cho bữa đại tiệc đ&oacute;n ch&agrave;o Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/E2/ho9.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"320\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ph&oacute; tổng thống Mỹ Joe Biden n&acirc;ng cốc ch&uacute;c mừng Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o trong bữa trưa tại Bộ Ngoại giao Mỹ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/E2/ho4.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"323\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o c&ugrave;ng Ngoại trưởng Mỹ Hillary Rodham Clinton n&acirc;ng cốc ch&uacute;c mừng trong bữa trưa tại Bộ Ngoại giao Mỹ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/E2/ho12.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"323\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Trước đ&oacute; v&agrave;o buổi s&aacute;ng Tổng thống Mỹ Obama ch&agrave;o mừng Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o trong lễ đ&oacute;n tiếp long trọng tại dinh tổng thống.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/E2/ho11.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"301\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o c&ugrave;ng Tổng thống Mỹ Obama vẫy ch&agrave;o người d&acirc;n tại Lễ đ&oacute;n quốc gia ở Nh&agrave; Trắng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/E2/ho5.jpg\" border=\"1\" alt=\"\" width=\"341\" height=\"512\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tổng thống Mỹ Obama lắng nghe lời ph&aacute;t biểu của Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o trong cuộc gặp mặt với c&aacute;c nh&agrave; l&atilde;nh đạo kinh doanh ở T&ograve;a nh&agrave; văn ph&ograve;ng Eisenhower. &Ocirc;ng Hồ đang c&oacute; chuyến thăm ch&iacute;nh thức cấp nh&agrave; nước d&agrave;i 4 ng&agrave;y tới Mỹ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Song Minh</strong>&nbsp;(Ảnh:&nbsp;<em>AFP</em>)</p>', 6, 1899, 0, 1, NULL, NULL),
(222, 'Đại hội Đảng XI Việt Nam trên báo nước ngoài', 'Dai-Hoi-Dang-Xi-Viet-Nam-Tren-Bao-Nuoc-Ngoai', 'Đại hội lần thứ 11 của Đảng Cộng sản Việt Nam thu hút sự chú ý của báo chí quốc tế. Dưới đây là một số hình ảnh mà các hãng thông tấn nước ngoài ghi lại về sự kiện chính trị quan trọng này. ', '1 (2)_1.jpg', 22, '<h1 class=\"Title\">Đại hội Đảng XI Việt Nam tr&ecirc;n b&aacute;o nước ngo&agrave;i</h1>\r\n<h2 class=\"Lead\">Đại hội lần thứ 11 của Đảng Cộng sản Việt Nam thu h&uacute;t sự ch&uacute; &yacute; của b&aacute;o ch&iacute; quốc tế. Dưới đ&acirc;y l&agrave; một số h&igrave;nh ảnh m&agrave; c&aacute;c h&atilde;ng th&ocirc;ng tấn nước ngo&agrave;i ghi lại về sự kiện ch&iacute;nh trị quan trọng n&agrave;y.&nbsp;<br />&gt;<a class=\"Lead\" href=\"http://vnexpress.net/GL/Xa-hoi/Dai-hoi-Dang-XI/2011/01/3BA258D1/\">&Ocirc;ng Nguyễn Ph&uacute; Trọng đắc cử Tổng b&iacute; thư</a>/&nbsp;<a class=\"Lead\" href=\"http://vnexpress.net/GL/Xa-hoi/2011/01/3BA25947/\">&lsquo;T&ocirc;i kh&ocirc;ng chủ trương l&agrave;m để tạo dấu ấn&rsquo;</a></h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/EB/1.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"436\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tổng b&iacute; thư vừa đắc cử Nguyễn Ph&uacute; Trọng trả lời phỏng vấn của b&aacute;o ch&iacute; trong nước v&agrave; quốc tế sau Đại hội. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/EB/2.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"226\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">(H&agrave;ng 1, tr&aacute;i sang) C&aacute;c ủy vi&ecirc;n Bộ Ch&iacute;nh trị mới đắc cử Nguyễn Tấn Dũng, Nguyễn Ph&uacute; Trọng, Trương Tấn Sang, Nguyễn Sinh H&ugrave;ng c&ugrave;ng c&aacute;c ủy vi&ecirc;n trung ương mới ra mắt đại hội. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/EB/3.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"428\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tổng b&iacute; thư kh&oacute;a X N&ocirc;ng Đức Mạnh (phải) tặng hoa v&agrave; &ocirc;m h&ocirc;n ch&uacute;c mừng t&acirc;n Tổng b&iacute; thư Nguyễn Ph&uacute; Trọng. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/EB/4.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"317\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">To&agrave;n cảnh hội trường Đại hội Đảng Cộng sản Việt Nam lần thứ XI, diễn ra từ 12 đến 19/1 tại H&agrave; Nội. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/EB/5.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"316\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c vị l&atilde;nh đạo của Việt Nam tr&ecirc;n đo&agrave;n chủ tịch đại hội. Từ tr&aacute;i sang: &ocirc;ng Nguyễn Sinh H&ugrave;ng, ph&oacute; thủ tướng; &ocirc;ng Nguyễn Tấn Dũng, thủ tướng; &ocirc;ng L&ecirc; Hồng Anh, bộ trưởng C&ocirc;ng an. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/EB/6.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"254\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c đại biểu giơ cao thẻ đảng vi&ecirc;n trước khi bắt đầu cuộc bỏ phiếu bầu Ban chấp h&agrave;nh trung ương mới. Gần 1.400 đảng vi&ecirc;n Cộng sản tham dự Đại hội XI. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/EB/7.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"344\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một đảng vi&ecirc;n cầm tấm phiếu bầu chờ đến lượt bỏ phiếu ra Ban chấp h&agrave;nh trung ương đảng, gồm gần 200 vị. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/EB/8.jpg\" border=\"1\" alt=\"\" width=\"380\" height=\"531\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đ&acirc;y l&agrave; quang cảnh ngo&agrave;i sảnh của Trung t&acirc;m Hội nghị Quốc gia, nơi diễn ra Đại hội Đảng XI. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/EB/9.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"329\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cảnh vệ c&oacute; vũ trang đứng g&aacute;c b&ecirc;n ngo&agrave;i trung t&acirc;m hội nghị. B&ecirc;n cạnh anh l&agrave; h&agrave;ng loạt băng r&ocirc;n khẩu hiệu ch&agrave;o mừng đại hội. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/EB/11.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"319\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tr&ecirc;n c&aacute;c con phố của H&agrave; Nội chăng nhiều cờ v&agrave; biểu ngữ ch&agrave;o mừng. Một chiếc xe hơi đắt tiền đang lướt đi b&ecirc;n dưới c&aacute;c biểu ngữ. L&atilde;nh đạo để ph&aacute;t triển kinh tế l&agrave; một trong những nhiệm vụ quan trọng của Đảng cầm quyền. Ảnh: AP.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/EB/10.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"319\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Với lực lượng d&acirc;n số đ&ocirc;ng v&agrave; trẻ, Việt Nam c&oacute; nguồn lao động dồi d&agrave;o ở \"độ tuổi v&agrave;ng\". Trong ảnh, nam nữ thanh ni&ecirc;n chụp ảnh kỷ niệm b&ecirc;n c&aacute;c thảm hoa kết th&agrave;nh chữ ch&agrave;o mừng Đại hội XI. Ảnh: AP.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Thanh Mai</strong></p>', 6, 1272, 0, 1, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(223, 'Quân đội Ấn Độ phô diễn sức mạnh', 'Quan-Doi-An-Do-Pho-Dien-Suc-Manh', 'Binh lính thuộc các binh chủng Ấn Độ biểu dương sức mạnh trong cuộc diễu hành tại New Delhi, hôm 15/1, nhằm kỷ niệm 63 năm ngày thành lập quân đội.', 'an1.jpg', 22, '<h1 class=\"Title\">Qu&acirc;n đội Ấn Độ ph&ocirc; diễn sức mạnh</h1>\r\n<h2 class=\"Lead\">Binh l&iacute;nh thuộc c&aacute;c binh chủng Ấn Độ biểu dương sức mạnh trong cuộc diễu h&agrave;nh tại New Delhi, h&ocirc;m 15/1, nhằm kỷ niệm 63 năm ng&agrave;y th&agrave;nh lập qu&acirc;n đội.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/3E/an1.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"265\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Khẩu đội ph&aacute;o khai hỏa mở m&agrave;n cho cuộc diễu h&agrave;nh ho&agrave;nh tr&aacute;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/3E/an2.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"286\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Lực lượng xe tăng diễu h&agrave;nh qua lễ đ&agrave;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/3E/an3.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"282\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">M&agrave;n duyệt binh của lực lượng bộ binh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/3E/an4.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"278\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Binh l&iacute;nh Ấn Độ trong bộ qu&acirc;n phục truyền thống.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/3E/an5.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"381\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c chiến sĩ tạo h&igrave;nh ấn tượng tr&ecirc;n chiếc m&ocirc; t&ocirc; ph&acirc;n khối lớn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/3E/an6.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"386\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một pha biểu diễn mạo hiểm tr&ecirc;n xe m&aacute;y.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/3E/an7.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"269\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Trực thăng qu&acirc;n sự cũng tham gia m&agrave;n diễu h&agrave;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/3E/an8.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"241\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Xe tăng Ấn Độ diễu h&agrave;nh c&ugrave;ng c&aacute;c binh l&iacute;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/3E/an9.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"265\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">M&agrave;n đồng diễn ấn tượng tr&ecirc;n c&aacute;c xe m&ocirc; t&ocirc; ph&acirc;n khối lớn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Song Minh</strong>&nbsp;(Ảnh:&nbsp;<em>Xinhua</em>)</p>', 6, 772, 0, 1, NULL, NULL),
(224, 'Khoảnh khắc ấn tượng của Obama năm 2010', 'Khoanh-Khac-An-Tuong-Cua-Obama-Nam-2010', 'Pete Souza - nhiếp ảnh gia chính thức của Nhà Trắng - đưa người xem trải qua những giây phút riêng tư của người đàn ông quyền lực nhất nước Mỹ - Barack Obama, trong năm 2010.', 'obama1.jpg', 22, '<h1 class=\"Title\">Khoảnh khắc ấn tượng của Obama năm 2010</h1>\r\n<h2 class=\"Lead\"><strong>Pete Souza - nhiếp ảnh gia ch&iacute;nh thức của Nh&agrave; Trắng - đưa người xem trải qua những gi&acirc;y ph&uacute;t ri&ecirc;ng tư của người đ&agrave;n &ocirc;ng quyền lực nhất nước Mỹ - Barack Obama, trong năm 2010.</strong></h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/DB/obama1.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"318\" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class=\"Image\">20/1/2010.</p>\r\n<p class=\"Image\">\"Đ&oacute; l&agrave; đ&ecirc;m sau cuộc bầu cử đặc biệt tại Massachusetts, khi th&agrave;nh vi&ecirc;n đảng Cộng h&ograve;a Scott Brown bất ngờ d&agrave;nh được ghế của nghị sĩ Edward Kennedy tại Nghị viện. Đảng D&acirc;n chủ mất đi 60 số phiếu trong Nghị viện, khiến cho việc th&ocirc;ng qua kế hoạch cải c&aacute;ch y tế gặp kh&oacute; khăn. Sự căng thẳng hiện r&otilde; tr&ecirc;n mặt của Tổng thống v&agrave; c&aacute;c trợ l&yacute;\" - Pete Souza, ph&oacute;ng vi&ecirc;n ảnh ch&iacute;nh thức của Nh&agrave; Trắng.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em>&nbsp;</em></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/DB/obama4.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"319\" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class=\"Image\">29/1/2010.</p>\r\n<p class=\"Image\">Obama nh&igrave;n từ đằng sau khi &ocirc;ng ngồi trong ghế Tổng thống tại ph&ograve;ng Nội c&aacute;c. Miếng thẻ gắn tr&ecirc;n ghế ghi ng&agrave;y &ocirc;ng nhậm chức tổng thống.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/DB/obama5.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"319\" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class=\"Image\">29/1/2010.</p>\r\n<p class=\"Image\">Tổng thống c&oacute; mặt tại Baltimore để ph&aacute;t biểu tại một cuộc họp của Hạ viện. Khi &ocirc;ng chuẩn bị bước v&agrave;o ph&ograve;ng họp, Obama đề nghị Bộ trưởng giao th&ocirc;ng Ray LaHood đi trước để &ocirc;ng n&uacute;p đằng sau.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em><em> </em></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/DB/obama6.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"320\" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class=\"Image\">19/3/2010.</p>\r\n<p class=\"Image\">Sau bữa tối, Tổng thống trở lại ph&ograve;ng Bầu dục để tiếp tục th&uacute;c &eacute;p Hạ viện bỏ phiếu th&ocirc;ng qua dự luật cải c&aacute;ch y tế. Trong những ng&agrave;y trước cuộc bỏ phiếu, Obama thực hiện h&agrave;ng trăm cuộc gọi.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em><em> </em></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/DB/obama7.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"319\" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class=\"Image\">23/3/2010.</p>\r\n<p class=\"Image\">Cận cảnh chữ k&yacute; của Tổng thống Obama th&ocirc;ng qua dự luật cải c&aacute;ch y tế. Tổng thống đ&atilde; k&yacute; văn bản n&agrave;y bằng 22 chiếc b&uacute;t kh&aacute;c nhau.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em><em> </em></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/DB/obama8.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"319\" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class=\"Image\">14/4/2010.</p>\r\n<p class=\"Image\">Tổng thống lắng nghe chăm ch&uacute; c&aacute;c cố vấn cấp cao của m&igrave;nh tại một cuộc họp ở ph&ograve;ng Bầu dục.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> <br />\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Song Minh</strong>&nbsp;(Ảnh: Nh&agrave; Trắng)</p>\r\n</em></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/DB/obama9.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"295\" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class=\"Image\">28/4/2010.</p>\r\n<p class=\"Image\">Tại một qu&aacute;n cafe ở Missouri, Obama chụp ảnh th&acirc;n mật với c&aacute;c nh&acirc;n vi&ecirc;n v&agrave; một người đ&agrave;n &ocirc;ng đ&atilde; l&agrave;m một cử chỉ h&agrave;i hước ph&iacute;a sau Tổng thống.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/DB/obama10.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"317\" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class=\"Image\">31/5/2010.</p>\r\n<p class=\"Image\">Trời đổ mưa trong ng&agrave;y Memorial Day ở Chicago. Khi sấm chớp nổi l&ecirc;n, c&aacute;c nh&acirc;n vi&ecirc;n an ninh khuyến c&aacute;o tổng thống kh&ocirc;ng n&ecirc;n ph&aacute;t biểu. Obama tự m&igrave;nh l&ecirc;n s&acirc;n khấu v&agrave; th&ocirc;ng b&aacute;o với kh&aacute;n giả rằng buổi tr&ograve; chuyện của &ocirc;ng bị ho&atilde;n v&agrave; y&ecirc;u cầu mọi người trở lại xe v&igrave; sự an to&agrave;n. Sau đ&oacute;, &ocirc;ng c&ograve;n l&ecirc;n một số xe bu&yacute;t để cảm ơn mọi người đ&atilde; tới dự v&agrave; xin lỗi v&igrave; kh&ocirc;ng thể ph&aacute;t biểu.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', 6, 1561, 0, 1, NULL, NULL),
(225, 'Bà Clinton ngã trên máy bay', 'Ba-Clinton-Nga-Tren-May-Bay', 'Ngoại trưởng Mỹ Hillary Clinton vấp ngã khi lên máy bay ở Yemen sau khi kết thúc chuyến công cán tại nước này.', 'cl1.jpg', 22, '<h1 class=\"Title\">B&agrave; Clinton ng&atilde; tr&ecirc;n m&aacute;y bay</h1>\r\n<h2 class=\"Lead\">Ngoại trưởng Mỹ Hillary Clinton vấp ng&atilde; khi l&ecirc;n m&aacute;y bay ở Yemen sau khi kết th&uacute;c chuyến c&ocirc;ng c&aacute;n tại nước n&agrave;y.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/55/5B/cl1.jpg\" border=\"1\" alt=\"Ngoại trưởng Mỹ Hillary Clinton. Ảnh: AFP.\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ngoại trưởng Mỹ Hillary Clinton. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Nữ ngoại trưởng Mỹ vẫy tay ch&agrave;o đ&aacute;m đ&ocirc;ng v&agrave; nhiếp ảnh gia khi b&agrave; l&ecirc;n m&aacute;y bay. Khi vừa tới cửa, b&agrave; bị vấp ng&atilde;. Một người đ&agrave;n &ocirc;ng nhanh ch&oacute;ng gi&uacute;p b&agrave; đứng dậy, sau đ&oacute;, đ&egrave;n trong m&aacute;y bay từ từ tối dần.</p>\r\n<p class=\"Normal\">Trợ l&yacute; của nữ ngoại trưởng cho biết kh&ocirc;ng giống như lần ng&atilde; ở Nh&agrave; Trắng, lần n&agrave;y Clinton kh&ocirc;ng bị hề hấn g&igrave;,&nbsp;<em>CNN</em>&nbsp;đưa tin.</p>\r\n<p class=\"Normal\">C&aacute;c ch&iacute;nh trị gia Mỹ hiểu đường l&ecirc;n m&aacute;y bay \"nguy hiểm\" thế n&agrave;o nhất l&agrave; khi c&oacute; v&ocirc; số camera theo từng bước ch&acirc;n của họ. V&igrave; thế, mỗi tổng thống Mỹ tự tạo cho m&igrave;nh một phong c&aacute;ch ri&ecirc;ng. Barack Obama thường nh&uacute;n nhảy. Bill Clinton th&igrave; bước đi thư th&aacute;i. George Bush th&igrave; kh&ocirc;ng rời tay khỏi thanh b&aacute;m - điều m&agrave; Gerald Ford đ&aacute;ng lẽ n&ecirc;n l&agrave;m. Video c&uacute; ng&atilde; của Ford khi &ocirc;ng rời chiếc chuy&ecirc;n cơ Air Force One xuống đất &Aacute;o từng được phối nhạc v&agrave; được lan truyền rộng r&atilde;i tr&ecirc;n mạng.</p>\r\n<p class=\"Normal\">Kh&ocirc;ng chỉ quan chức Mỹ c&oacute; xu hướng vấp ng&atilde; tr&ecirc;n m&aacute;y bay trong l&uacute;c v&ocirc; số con mắt đổ v&agrave;o họ. Tổng thống Ph&aacute;p Nicholas Sarkozy c&oacute; lần lảo đảo su&yacute;t ng&atilde;. Obama chưa từng gặp sự cố tương tự song &ocirc;ng đ&aacute;nh rơi mất chiếc Blackberry khi nh&uacute;n nhảy l&ecirc;n m&aacute;y bay. &Ocirc;ng cũng từng đập đầu v&agrave;o cửa chiếc trực thăng của tổng thống.</p>\r\n<p class=\"Normal\">Tổng thống George W. Bush từng hơn một lần đập đầu v&agrave;o m&aacute;y bay. Đương kim đệ nhất phu nh&acirc;n Michelle Obama cũng gặp sự cố tương tự.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Ninh</strong></p>', 6, 505, 0, 1, NULL, NULL),
(226, 'Lũ lụt kinh hoàng ở Australia', 'Lu-Lut-Kinh-Hoang-O-Australia', 'Lũ quét tràn qua các tỉnh đông bắc Australia hôm qua, làm chết ít nhất 8 người, xe cộ bị trôi tuột trong dòng nước cuốn, cây cối đổ nghiêng ngả và đường phố ngập chìm trong biển nước.', 'flood1.jpg', 22, '<h1 class=\"Title\">Lũ lụt kinh ho&agrave;ng ở Australia</h1>\r\n<h2 class=\"Lead\">Lũ qu&eacute;t tr&agrave;n qua c&aacute;c tỉnh đ&ocirc;ng bắc Australia h&ocirc;m qua, l&agrave;m chết &iacute;t nhất 8 người, xe cộ bị tr&ocirc;i tuột trong d&ograve;ng nước cuốn, c&acirc;y cối đổ nghi&ecirc;ng ngả v&agrave; đường phố ngập ch&igrave;m trong biển nước.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/F0/flood1.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"320\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một chiếc &ocirc;t&ocirc; cố gắng lội qua c&acirc;y cầu ngập nước ở Rocklea, tỉnh Brisbane, h&ocirc;m qua. Những đợt mưa xối xả l&agrave;m to&agrave;n bộ bang Queensland ngập ch&igrave;m trong nước, với hơn 300 mm nước đổ xuống chỉ trong v&ograve;ng 24h, l&agrave;m c&aacute;c con s&ocirc;ng tr&agrave;n nước v&agrave; nhấn ch&igrave;m đường phố, cầu cống.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/F0/flood2.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"335\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Anh Gary Russell nhặt r&aacute;c thải tại s&acirc;n trước nh&agrave; m&igrave;nh ở Rockhampton. H&agrave;ng ngh&igrave;n người d&acirc;n nơi đ&acirc;y bị c&ocirc; lập giữa biển nước.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/F0/flood3.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"323\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những chiếc xe tải hạng nặng nằm chỏng chơ giữa bể nước m&agrave; kh&ocirc;ng thể hoạt động.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/F0/flood4.jpg\" border=\"1\" alt=\"\" width=\"359\" height=\"512\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đường băng ở s&acirc;n bay Rockhampton cũng b&igrave; ch&igrave;m trong nước. Mưa lũ l&agrave;m to&agrave;n bộ s&acirc;n bay v&agrave; đường sắt ở Rockhampton bị t&ecirc; liệt.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/F0/flood5.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"338\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Người d&acirc;n ch&egrave;o thuyền để đi lại. Con s&ocirc;ng Fitzroy bị vỡ đ&ecirc; l&agrave;m nhấn ch&igrave;m gần hết th&agrave;nh phố Rockhampton.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/F0/flood6.jpg\" border=\"1\" alt=\"\" width=\"399\" height=\"512\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Khung cảnh đi&ecirc;u t&agrave;n của một trang trại ngập nước nh&igrave;n từ tr&ecirc;n cao.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/F0/flood7.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"338\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một đường cao tốc ở Toombul, Brisbane bị chặn do bị ngập nặng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/F0/flood8.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"325\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cảnh s&aacute;t đi thuyền giữa đường phố ngập nước để trợ gi&uacute;p người d&acirc;n. C&oacute; tới 200.000 người bị ảnh hưởng bởi đợt lũ lụt nghi&ecirc;m trọng n&agrave;y.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/F0/flood9.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"308\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Người d&acirc;n cố gắng dựng lại tấm biển cảnh b&aacute;o đường ngập lụt sau khi bị con s&ocirc;ng Burnett ở thị trấn Gin Gin nhấn ch&igrave;m.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Song Minh</strong>&nbsp;(Ảnh:&nbsp;<em>AFP</em>)</p>', 6, 325, 0, 1, NULL, NULL),
(227, 'Lễ hội tuổi 20 của thiếu nữ Nhật Bản', 'Le-Hoi-Tuoi-20-Cua-Thieu-Nu-Nhat-Ban', 'Ngày hội trưởng thành được tổ chức hằng năm tại Nhật Bản vào ngày thứ Hai thứ 2 trong tháng một để chúc mừng những cô cậu tròn 20 tuổi.', 'girl1.jpg', 22, '<h1 class=\"Title\">Lễ hội tuổi 20 của thiếu nữ Nhật Bản</h1>\r\n<h2 class=\"Lead\">Ng&agrave;y hội trưởng th&agrave;nh được tổ chức hằng năm tại Nhật Bản v&agrave;o ng&agrave;y thứ Hai thứ 2 trong th&aacute;ng một để ch&uacute;c mừng những c&ocirc; cậu tr&ograve;n 20 tuổi.</h2>\r\n<p class=\"Normal\">Theo một b&aacute;o c&aacute;o của Nhật Bản, c&oacute; khoảng 1.240.000 c&ocirc; g&aacute;i v&agrave; ch&agrave;ng trai sinh năm 1990 v&agrave; đạt độ tuổi trưởng th&agrave;nh đầu năm nay. Đ&acirc;y l&agrave; con số nhỏ nhất kể từ năm 1968 tại đất nước c&oacute; d&acirc;n số gi&agrave; nhất thế giới.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/B4/girl1.jpg\" border=\"1\" alt=\"\" width=\"460\" height=\"351\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c c&ocirc; g&aacute;i diện trang phục kimono truyền thống đến tham dự lễ hội Ng&agrave;y trưởng th&agrave;nh tại một c&ocirc;ng vi&ecirc;n giải tr&iacute; ở Tokyo, h&ocirc;m qua. Ảnh:&nbsp;<em>Xinhua</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/B4/girl2.jpg\" border=\"1\" alt=\"\" width=\"345\" height=\"500\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c c&ocirc; g&aacute;i trong trang phục rực rỡ đứng chụp h&igrave;nh với nhau. Ảnh:<em>Xinhua</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/B4/girl3.jpg\" border=\"1\" alt=\"\" width=\"460\" height=\"306\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Trong ng&agrave;y hội n&agrave;y, những c&ocirc; g&aacute;i mới lớn viết lời ước nguyện l&ecirc;n c&aacute;c miếng thẻ tại một ng&ocirc;i đền để cầu mong lời ước của m&igrave;nh sẽ th&agrave;nh hiện thực. Ảnh:&nbsp;<em>Xinhua</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/B4/girl4.jpg\" border=\"1\" alt=\"\" width=\"460\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hai c&ocirc; g&aacute;i hồ hởi viết lời ước cho m&igrave;nh. Ảnh:&nbsp;<em>Xinhua</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/B4/girl5.jpg\" border=\"1\" alt=\"\" width=\"460\" height=\"317\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một đ&ocirc;i nam nữ chụp ảnh kỷ niệm nh&acirc;n ng&agrave;y trọng đại trong đời. Ảnh:&nbsp;<em>Xinhua</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/B4/girl7.jpg\" border=\"1\" alt=\"\" width=\"460\" height=\"276\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c c&ocirc; g&aacute;i l&ecirc;n t&agrave;u điện ngầm để tới c&ocirc;ng vi&ecirc;n giải tr&iacute;. Con g&aacute;i Nhật Bản c&oacute; thể kết h&ocirc;n ở tuổi 16, v&agrave; con trai ở tuổi 18, nhưng họ chỉ thực sự được coi l&agrave; trưởng th&agrave;nh khi tr&ograve;n 20 tuổi. Ảnh:&nbsp;<em>EPA</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/53/B4/girl6.jpg\" border=\"1\" alt=\"\" width=\"460\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c c&ocirc; g&aacute;i xinh đẹp h&ograve; reo khi chơi tr&ograve; t&agrave;u lượn tại c&ocirc;ng vi&ecirc;n giải tr&iacute;, nh&acirc;n ng&agrave;y hội Trưởng th&agrave;nh. Ảnh:&nbsp;<em>Xinhua</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Song Minh</strong></p>', 6, 2775, 0, 1, NULL, NULL),
(457, 'Doanh nhân không đợi Tết', 'Doanh-Nhan-Khong-Doi-Tet', 'Có lẽ càng trưởng thành, tôi lại càng thấy mệt mỏi với ngày Tết, càng không còn cảm giác hân hoan, mừng rỡ khi nhìn mùa Xuân về.', 'anh-Nguyen-Canh-Binh.jpg', 22, '<h1 class=\"Title\">Doanh nh&acirc;n kh&ocirc;ng đợi Tết</h1>\r\n<h2 class=\"Lead\">C&oacute; lẽ c&agrave;ng trưởng th&agrave;nh, t&ocirc;i lại c&agrave;ng thấy mệt mỏi với ng&agrave;y Tết, c&agrave;ng kh&ocirc;ng c&ograve;n cảm gi&aacute;c h&acirc;n hoan, mừng rỡ khi nh&igrave;n m&ugrave;a Xu&acirc;n về.</h2>\r\n<p class=\"Normal\">C&agrave;ng về những ng&agrave;y cuối năm, H&agrave; Nội v&agrave; TP HCM ng&agrave;y c&agrave;ng đ&ocirc;ng hơn, chật chội hơn, tắc đường nhiều hơn v&agrave; căng thẳng hơn. Dường như tất cả đều cuống cuồng mua sắm, nhậu nhẹt, ch&uacute;c tết v&agrave; tặng qu&agrave;. Giới doanh nh&acirc;n ch&uacute;ng t&ocirc;i cũng bận rộn hơn, hối hả hơn với những mối lo toan mu&ocirc;n thuở: tiền bạc, c&ocirc;ng nợ, thưởng tết, cổ tức, gặp mặt, ch&uacute;c tết, tặng qu&agrave;&hellip; B&acirc;y giờ, những ng&agrave;y n&agrave;y t&ocirc;i nh&igrave;n thấy nhiều mối lo &acirc;u hơn.</p>\r\n<p class=\"Normal\">Cuối năm, c&aacute;c bữa tiệc li&ecirc;n hoan nhiều hơn, li&ecirc;n mi&ecirc;n, với những m&agrave;n d&ocirc; d&ocirc; d&ocirc;, 1&hellip;2&hellip; 3&hellip; d&ocirc;, 1&hellip; 2&hellip; 3&hellip; d&ocirc;, 1&hellip; 2&hellip; 3&hellip; uống khắp c&aacute;c qu&aacute;n s&aacute;. Những buổi trưa, chiều v&agrave; tối li&ecirc;n hoan, ch&uacute;c tụng dường như kh&ocirc;ng dứt. Kh&ocirc;ng chỉ giới doanh nh&acirc;n, c&oacute; lẽ hết thảy mọi người đều như vậy&hellip; Vui cũng l&agrave; thật vui, nhưng mệt v&agrave; lo.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/60/49/anh-Nguyen-Canh-Binh.jpg\" alt=\"\" width=\"450\" height=\"340\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">&Ocirc;ng Nguyễn Cảnh B&igrave;nh b&ecirc;n chậu hoa ng&agrave;y Tết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&oacute; lẽ c&agrave;ng trưởng th&agrave;nh, t&ocirc;i lại c&agrave;ng thấy mệt mỏi với ng&agrave;y Tết, c&agrave;ng kh&ocirc;ng c&ograve;n cảm gi&aacute;c h&acirc;n hoan, mừng rỡ khi nh&igrave;n m&ugrave;a Xu&acirc;n về. Kh&ocirc;ng c&ograve;n kh&ocirc;ng kh&iacute; v&agrave; cảm x&uacute;c của ng&agrave;y thơ ấu với c&aacute;i Tết thiếu thốn nhưng đầy niềm vui, (nhưng c&oacute; lẽ cũng chỉ vui với ch&uacute;ng t&ocirc;i chứ kh&ocirc;ng vui với thế hệ trước&hellip;): xếp h&agrave;ng mua những g&oacute;i qu&agrave; Tết, mang bột v&agrave; trứng đi l&agrave;m b&aacute;nh b&iacute;t quy, với những chai rượu chanh, rượu cam&hellip;</p>\r\n<p class=\"Normal\">Cuối năm v&agrave; trong những ng&agrave;y Tết, tai nạn nhiều hơn. Tất cả dường như uống nhiều hơn, say hơn, đi xe nhanh hơn, trời lạnh hơn, đi khuya hơn&hellip; để rồi v&agrave;o bệnh viện nhiều hơn. Mấy năm trước, đ&uacute;ng những ng&agrave;y Tết, giữa đ&ecirc;m khuya t&ocirc;i chợt giật m&igrave;nh khi c&oacute; tiếng điện thoại b&aacute;o người th&acirc;n tai nạn xe cộ phải v&agrave;o viện, đến b&acirc;y giờ m&agrave; vẫn thấy sợ.</p>\r\n<p class=\"Normal\">Cuối năm, những người xa tổ quốc lại c&oacute; dịp trở về qu&ecirc; hương nhưng rất nhiều sinh vi&ecirc;n, rất nhiều người con trong gia đ&igrave;nh lại kh&ocirc;ng thể về thăm nh&agrave; đ&uacute;ng dịp n&agrave;y v&igrave; phương T&acirc;y nghỉ Noel v&agrave; Tết Dương lịch c&ograve;n ch&uacute;ng ta lại nghỉ Tết &Acirc;m lịch. Khi họ nghỉ th&igrave; ch&uacute;ng ta l&agrave;m việc, c&ograve;n đến khi họ l&agrave;m việc th&igrave; ch&uacute;ng ta lại nghỉ.</p>\r\n<p class=\"Normal\">Với c&aacute;c c&ocirc;ng ty c&oacute; giao dịch với nước ngo&agrave;i như ch&uacute;ng t&ocirc;i, một năm, c&ocirc;ng việc lại bị đ&igrave;nh trệ trong cả hai dịp Tết n&agrave;y. Những ng&agrave;y Noel v&agrave; Tết Dương lịch, ch&uacute;ng t&ocirc;i phải chờ đợi c&aacute;c đối t&aacute;c nước ngo&agrave;i v&agrave; giờ đ&acirc;y, họ lại chờ đợi ch&uacute;ng t&ocirc;i&hellip;</p>\r\n<p class=\"Normal\">Ở Nhật Bản, kể từ khi c&oacute; cuộc Duy T&acirc;n do Thi&ecirc;n ho&agrave;ng Minh Trị thực hiện th&agrave;nh c&ocirc;ng v&agrave;o năm 1868, nước n&agrave;y đ&atilde; thay đổi truyền thống đ&oacute;n mừng Tết Nguy&ecirc;n Đ&aacute;n sang năm mới Dương lịch giống như c&aacute;c nước phương T&acirc;y. Người d&acirc;n Nhật Bản bắt đầu chuẩn bị năm mới từ ng&agrave;y 31/12 của năm cũ v&agrave; chỉ nghỉ 3 - 4 ng&agrave;y m&agrave; th&ocirc;i&hellip; T&ocirc;i nghĩ như vậy hợp l&yacute; v&agrave; thuận tiện hơn. Khi hội nhập với thế giới, ch&uacute;ng ta phải quen với sống th&agrave;nh thị hơn, hiện đại hơn, c&aacute;c th&oacute;i quen v&agrave; phong tục rồi cũng sẽ phải thay đổi. Nhưng c&oacute; những điều cần thay đổi, c&oacute; những điều cần trở lại với phong tục&hellip;</p>\r\n<p class=\"Normal\">Trong những ng&agrave;y Tết, ch&uacute;ng ta n&ecirc;n đi thăm họ h&agrave;ng, gia đ&igrave;nh gần gũi&hellip; kh&ocirc;ng n&ecirc;n đi c&ugrave;ng cơ quan, tổ chức, c&ocirc;ng ty. Gần 20 năm trước, mẹ t&ocirc;i, khi đ&oacute; l&agrave;m ở một trường đại học đ&atilde; n&oacute;i với t&ocirc;i về quy ước Tết kh&ocirc;ng đến nh&agrave; nhau v&igrave; cả một năm đ&atilde; gặp nhau qu&aacute; nhiều rồi.</p>\r\n<p class=\"Normal\">Hơn 10 năm trước, ở nơi l&agrave;m việc cũ, t&ocirc;i c&oacute; th&oacute;i quen đến nh&agrave;, m&ugrave;ng 4, m&ugrave;ng 5 Tết cả ph&ograve;ng rồng rắn đi suốt cả một ng&agrave;y, kh&ocirc;ng đi kh&ocirc;ng được. B&acirc;y giờ t&ocirc;i n&oacute;i với những đồng nghiệp l&agrave; Tết n&ecirc;n d&agrave;nh thời gian cho gia đ&igrave;nh, người th&acirc;n của họ&hellip;</p>\r\n<p class=\"Normal\">Ch&uacute;ng ta n&ecirc;n tặng thiệp ch&uacute;c tết thay cho tặng qu&agrave; Tết. Đến nh&agrave; v&agrave;i người t&ocirc;i thấy qu&agrave; c&aacute;p chồng chất, do kh&ocirc;ng phải m&igrave;nh mua n&ecirc;n kh&ocirc;ng chọn đ&uacute;ng sở th&iacute;ch v&agrave; thường qu&agrave; c&aacute;p mua biếu n&ecirc;n kh&aacute; đắt, l&atilde;ng ph&iacute;. C&oacute; c&ocirc;ng sở, tuần cuối năm, chỉ c&oacute; tặng qu&agrave; v&agrave; nhận qu&agrave; l&agrave; hết ng&agrave;y. C&oacute; những vị sếp cả ng&agrave;y chỉ tiếp kh&aacute;ch đến ch&uacute;c Tết m&agrave; kh&ocirc;ng l&agrave;m được g&igrave;. Ch&uacute;ng ta cũng n&ecirc;n giảm bớt việc nhậu nhẹt qu&aacute; nhiều, chấm dứt t&igrave;nh trạng d&ocirc; d&ocirc; d&ocirc; v&agrave; 100% thường thấy để đảm bảo sức khỏe tốt hơn, bớt tai nạn giao th&ocirc;ng.</p>\r\n<p class=\"Normal\">Ch&uacute;ng ta cũng n&ecirc;n từ bỏ th&oacute;i quen l&igrave; x&igrave; tiền cho trẻ con. Ng&agrave;y xưa, việc l&igrave; x&igrave; chỉ d&ugrave;ng tiền nhỏ, để lấy may. C&ograve;n ng&agrave;y nay, t&ocirc;i thấy nhiều người l&igrave; x&igrave; tiền rất lớn, đ&ocirc;i khi g&acirc;y kh&oacute; xử cho những người kh&aacute;c. Nhận l&igrave; x&igrave; th&agrave;nh th&oacute;i quen xấu tới mức thậm ch&iacute; nhiều đứa trẻ thấy kh&aacute;ch đến l&agrave; chạy ra chỉ chờ tiền mừng rồi lại chạy v&agrave;o nh&agrave;...</p>\r\n<p class=\"Normal\">Gần đ&acirc;y, ch&uacute;ng t&ocirc;i bắt đầu c&oacute; th&oacute;i quen tặng s&aacute;ch thiếu nhi, chọn những cuốn c&oacute; &yacute; nghĩa v&agrave; văn h&oacute;a tặng cho trẻ. V&agrave; điều rất mừng l&agrave; th&oacute;i quen n&agrave;y đang ng&agrave;y được ph&aacute;t triển. T&ocirc;i nghĩ ch&uacute;ng ta cũng n&ecirc;n khuyến kh&iacute;ch lại con trẻ quen với tục lệ Khai b&uacute;t đầu xu&acirc;n, v&agrave; tới thăm đ&igrave;nh ch&ugrave;a theo c&aacute;ch văn minh hơn, văn h&oacute;a hơn.</p>\r\n<p class=\"Normal\">V&agrave; cuối c&ugrave;ng, ch&uacute;ng ta rất n&ecirc;n chuyển về tết Dương lịch để ph&ugrave; hợp với phương T&acirc;y v&agrave; c&oacute; lẽ việc n&agrave;y sẽ l&agrave;m tăng GDP v&agrave; hiệu quả c&ocirc;ng việc. Với những suy nghĩ đ&oacute;, t&ocirc;i nghĩ rằng nếu thực hiện được, một ng&agrave;y n&agrave;o đ&oacute; kh&ocirc;ng xa, ch&uacute;ng ta sẽ c&oacute; những ng&agrave;y Tết vui hơn, an to&agrave;n hơn, &yacute; nghĩa hơn&hellip;</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nguyễn Cảnh B&igrave;nh,</strong>&nbsp;<em>Chủ tịch HĐQT Alpha Book</em></p>', 11, 23, 0, 1, NULL, NULL),
(458, 'Làm CEO khi doanh nghiệp khủng hoảng', 'Lam-Ceo-Khi-Doanh-Nghiep-Khung-Hoang', 'Đã là giám đốc điều hành (CEO), hẳn ai cũng phải đương đầu với vô vàn khó khăn, nhưng khó khăn ấy lớn gấp bội nếu phải chèo lái một công ty mà tương lai của nó chưa chắc chắn.', 'Charles_Ed_Haldeman_Jr_-_CEO_cua_Freddie_Mac_1 (1).jpg', 22, '<h1 class=\"Title\">L&agrave;m CEO khi doanh nghiệp khủng hoảng</h1>\r\n<h2 class=\"Lead\">Đ&atilde; l&agrave; gi&aacute;m đốc điều h&agrave;nh (CEO), hẳn ai cũng phải đương đầu với v&ocirc; v&agrave;n kh&oacute; khăn, nhưng kh&oacute; khăn ấy lớn gấp bội nếu phải ch&egrave;o l&aacute;i một c&ocirc;ng ty m&agrave; tương lai của n&oacute; chưa chắc chắn.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5e/ff/Charles_Ed_Haldeman_Jr_-_CEO_cua_Freddie_Mac_1.jpg\" border=\"1\" alt=\"Ảnh: Charles &ldquo;Ed&rdquo; Haldeman Jr &ndash; CEO của Freddie Mac (Gestalta)\" width=\"450\" height=\"338\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ảnh: Charles &ldquo;Ed&rdquo; Haldeman Jr - CEO của Freddie Mac. Ảnh:&nbsp;<em>Gestalta</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Đ&oacute; ch&iacute;nh l&agrave; t&igrave;nh cảnh của CEO Charles &ldquo;Ed&rdquo; Haldeman Jr của Freddie Mac - một trong hai tập đo&agrave;n cho vay thế chấp phải cầu viện tới sự giải cứu của ch&iacute;nh phủ Mỹ đầu năm 2008, khi cuộc khủng hoảng t&agrave;i ch&iacute;nh đang ở giai đoạn cao tr&agrave;o.</p>\r\n<p class=\"Normal\">Freddie v&agrave; &ldquo;đ&agrave;n anh&rdquo; Fannie Mae của m&igrave;nh đ&atilde; vay tổng cộng 150 tỷ USD của những người nộp thuế để b&ugrave; v&agrave;o khoản lỗ khổng lồ từ hoạt động cho vay thế chấp dưới chuẩn.</p>\r\n<p class=\"Normal\">C&aacute;c ch&iacute;nh trị gia của Đảng Cộng h&ograve;a kết tội hai c&ocirc;ng ty n&agrave;y g&acirc;y ra cuộc khủng hoảng nh&agrave; đất v&agrave; muốn x&oacute;a sổ họ. Kể cả những người vốn ủng hộ mục ti&ecirc;u của Freddie v&agrave; Fannie l&agrave; gi&uacute;p tạo điều kiện cho những người thu nhập thấp c&oacute; thể mua nh&agrave; cũng thừa nhận phải c&oacute; một v&agrave;i thay đổi lớn.</p>\r\n<p class=\"Normal\">Nhưng l&agrave;m thế n&agrave;o để thay đổi tại thời điểm m&agrave; Fannie v&agrave; Freddie đang l&agrave;m trụ cột cho cả thị trường nh&agrave; đất bằng việc bảo đảm cho 90% c&aacute;c khoản vay mới l&agrave; điều hết sức phức tạp v&agrave; mang đậm t&iacute;nh ch&iacute;nh trị.</p>\r\n<p class=\"Normal\">Một kế hoạch của Nh&agrave; Trắng được kỳ vọng sẽ vạch ra những thay đổi đ&atilde; bị ho&atilde;n lại v&agrave; rất c&oacute; thể sẽ chỉ đưa ra một số lựa chọn hơn l&agrave; cải tổ. Rất c&oacute; thể Fannie v&agrave; Freddie sẽ bị để trong t&igrave;nh trạng bấp b&ecirc;nh cho đến sau cuộc bầu cử tổng thống năm 2012 - một viễn cảnh m&agrave; &ocirc;ng Halderman mi&ecirc;u tả l&agrave; &ldquo;trường hợp xấu nhất&rdquo;.</p>\r\n<p class=\"Normal\">&Ocirc;ng đ&atilde; n&oacute;i với tờ&nbsp;<em>Financial Times</em>&nbsp;rằng: &ldquo;T&ocirc;i muốn c&oacute; một quyết định sớm v&igrave; thật kh&oacute; để giữ bộ m&aacute;y hoạt động trong t&igrave;nh trạng như thế n&agrave;y&rdquo;. Do vậy, &ocirc;ng đ&atilde; kh&ocirc;ng đợi ch&iacute;nh phủ h&agrave;nh động. Đo&aacute;n trước sẽ c&oacute; những thay đổi, đặc biệt l&agrave; sự cạnh tranh ng&agrave;y c&agrave;ng tăng, &ocirc;ng đang định h&igrave;nh lại Freddie để tạo ra sự linh hoạt v&agrave; tinh giản bằng c&aacute;ch cắt giảm chi ph&iacute;, n&acirc;ng cấp c&ocirc;ng nghệ v&agrave; thu&ecirc; nhiều cựu CEO phố Wall.</p>\r\n<p class=\"Normal\">Dưới sự chỉ đạo của &ocirc;ng, Freddie đang ng&agrave;y c&agrave;ng thắt chặt c&aacute;c chuẩn mực t&iacute;n dụng, n&acirc;ng ph&iacute; để cải thiện chất lượng c&aacute;c khoản vay. Nhưng ch&iacute;nh điều n&agrave;y lại l&agrave;m phật l&ograve;ng c&aacute;c ng&acirc;n h&agrave;ng - những kh&aacute;ch h&agrave;ng lớn nhất của họ.</p>\r\n<p class=\"Normal\">&Ocirc;ng Halderman n&oacute;i: &ldquo;Ch&uacute;ng t&ocirc;i n&ecirc;n c&oacute; hướng tiếp cận như một th&agrave;nh phần tư nh&acirc;n v&agrave; c&oacute; những người thuộc th&agrave;nh phần n&agrave;y d&ugrave; Freddie kh&ocirc;ng phải l&agrave; một c&ocirc;ng ty tư&rdquo;. Được th&agrave;nh lập sau cuộc Đại suy tho&aacute;i để gi&uacute;p hỗ trợ việc mua nh&agrave;, cả Fannie v&agrave; Freddie đều kh&ocirc;ng tạo ra c&aacute;c khoản vay. Họ mua c&aacute;c t&agrave;i sản thế chấp từ ng&acirc;n h&agrave;ng, giữ lại một số, c&ograve;n lại th&igrave; chứng kho&aacute;n h&oacute;a ch&uacute;ng v&agrave; đem b&aacute;n cho c&aacute;c nh&agrave; đầu tư.</p>\r\n<p class=\"Normal\">Hệ thống n&agrave;y vẫn l&agrave;m việc tốt cho đến khi ng&acirc;n h&agrave;ng bắt đầu cho những người kh&ocirc;ng c&oacute; khả năng trả nợ vay trong thời k&igrave; bong b&oacute;ng nh&agrave; đất cuối thập kỷ trước.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5e/ff/foreclosure-490.jpg\" border=\"1\" alt=\"Ng&acirc;n h&agrave;ng siết chặt c&aacute;c khoản vay sau khủng hoảng t&agrave;i ch&iacute;nh, khiến nhiều gia đ&igrave;nh ở Mỹ phải b&aacute;n nh&agrave; trả nợ.\" width=\"490\" height=\"314\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ng&acirc;n h&agrave;ng siết chặt c&aacute;c khoản vay sau khủng hoảng t&agrave;i ch&iacute;nh, khiến nhiều gia đ&igrave;nh ở Mỹ phải b&aacute;n nh&agrave; trả nợ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Freddie Mac đ&atilde; thắt chặt c&aacute;c chuẩn mực bằng c&aacute;ch từ chối mua c&aacute;c khoản vay bị định mức t&iacute;n dụng dưới chuẩn. Họ cũng tăng ph&iacute; đối với c&aacute;c ng&acirc;n h&agrave;ng để b&ugrave; lại khoản lỗ bị g&acirc;y ra do việc bảo đảm cho c&aacute;c khoản vay ở mức tiền bong b&oacute;ng nh&agrave; đất.</p>\r\n<p class=\"Normal\">V&agrave; c&aacute;c ng&acirc;n h&agrave;ng cũng chẳng ngần ngại b&agrave;y tỏ sự kh&ocirc;ng h&agrave;i l&ograve;ng của m&igrave;nh. Nhưng &ocirc;ng Halderman cũng n&oacute;i với c&aacute;c nh&acirc;n vi&ecirc;n rằng: &ldquo;T&ocirc;i coi trọng chất lượng hơn sự h&agrave;i l&ograve;ng. Cuộc chơi của t&ocirc;i kh&ocirc;ng phải l&agrave; gi&agrave;nh thị phần&rdquo;. Sự thay đổi tr&ecirc;n đ&atilde; gi&uacute;p Freddie thu hẹp khoản lỗ trong qu&yacute; gần nhất về 4,1 tỷ USD từ 6,7 tỷ USD của qu&yacute; 3 năm 2009 v&agrave; 25 tỷ USD của qu&yacute; 3 năm 2008.</p>\r\n<p class=\"Normal\">C&ocirc;ng ty cũng phải đối mặt với những kh&oacute; khăn về khả năng sinh lợi, bao gồm c&aacute;c khoản chi trả cổ tức lớn cho Kho bạc để đổi lấy c&aacute;c cổ phiếu ưu đ&atilde;i được ph&aacute;t h&agrave;nh như một phần của g&oacute;i cứu trợ. Trước đ&acirc;y, &ocirc;ng Halderman từng gi&uacute;p Putnam Investments vượt qua một scandal trong đ&oacute; c&aacute;c gi&aacute;m đốc bị kết tội mua b&aacute;n c&aacute;c khoản tiền của kh&aacute;ch h&agrave;ng một c&aacute;ch kh&ocirc;ng hợp lệ v&agrave; bị phạt 55 triệu USD.</p>\r\n<p class=\"Normal\">C&ograve;n tại Freddie, &ocirc;ng đ&atilde; lấp đầy khoảng trống l&atilde;nh đạo sau sự từ chức của CEO David Moffett th&aacute;ng 3/2009 v&agrave; vụ tự tử một th&aacute;ng trước đ&oacute; của CFO David Kellermann. &Ocirc;ng đ&atilde; bị hết cuộc khủng hoảng nọ đến cuộc khủng hoảng kia bủa v&acirc;y.</p>\r\n<p class=\"Normal\">Nhẹ nh&agrave;ng v&agrave; suy nghĩ thấu đ&aacute;o, &ocirc;ng Halderman c&oacute; sự ki&ecirc;n tr&igrave; của một người tự th&agrave;nh đạt. Khi c&ograve;n l&agrave; một thiếu ni&ecirc;n sống tại Philadelphia, &ocirc;ng đ&atilde; l&agrave;m trong một c&ocirc;ng ty gia đ&igrave;nh chuy&ecirc;n sản xuất bảng đăng k&iacute; xe &ocirc; t&ocirc;. Sau đ&oacute; &ocirc;ng gi&agrave;nh học bổng tại Dartmouth v&agrave; trở th&agrave;nh người đầu ti&ecirc;n trong gia đ&igrave;nh học đại học. Về sau &ocirc;ng c&ograve;n học th&ecirc;m Đại học Luật Harvard nữa.</p>\r\n<p class=\"Normal\">Sự bền bỉ đ&oacute; đ&atilde; được ph&aacute;t huy khi Freddie Mac ra sức y&ecirc;u cầu c&aacute;c ng&acirc;n h&agrave;ng mua lại c&aacute;c khoản vay kh&ocirc;ng đ&aacute;p ứng được y&ecirc;u cầu của bảo l&atilde;nh ph&aacute;t h&agrave;nh. Fannie v&agrave; Freddie cũng đ&atilde; đạt được thỏa thuận đ&ograve;i 2,6 tỷ USD từ ng&acirc;n h&agrave;ng trung ương Mỹ để b&ugrave; lại chi ph&iacute; của phần lớn c&aacute;c khoản vay n&agrave;y.</p>\r\n<p class=\"Normal\">Lời đề nghị mua lại vẫn đang được treo với c&aacute;c ng&acirc;n h&agrave;ng kh&aacute;c, v&agrave; Freddie c&ugrave;ng c&aacute;c tr&aacute;i chủ vẫn đang kiện ng&acirc;n h&agrave;ng trung ương Mỹ về c&aacute;c vi phạm trong qu&aacute; tr&igrave;nh hoạt động v&agrave; trả l&atilde;i c&aacute;c chứng kho&aacute;n đảm bảo bằng t&agrave;i sản thế chấp được gắn m&aacute;c ri&ecirc;ng tư m&agrave; bộ phận T&agrave;i ch&iacute;nh quốc gia của ng&acirc;n h&agrave;ng n&agrave;y bảo l&atilde;nh ph&aacute;t h&agrave;nh.</p>\r\n<p class=\"Normal\">&Ocirc;ng Halderman n&oacute;i: &ldquo;T&ocirc;i sẽ l&agrave;m tất cả những g&igrave; c&oacute; thể để bảo vệ lợi &iacute;ch cho người nộp thuế, kể cả khi điều đ&oacute; l&agrave;m tổn hại đến c&aacute;c mối quan hệ l&agrave;m ăn&rdquo;.</p>\r\n<p class=\"Normal\">Một ưu ti&ecirc;n kh&aacute;c của &ocirc;ng l&agrave; giữ ch&acirc;n nh&acirc;n vi&ecirc;n. &Ocirc;ng rất bực m&igrave;nh với ch&iacute;nh s&aacute;ch đ&oacute;ng băng lương đối với c&aacute;c nh&acirc;n vi&ecirc;n ch&iacute;nh phủ v&igrave; &ocirc;ng tin rằng n&oacute; kh&ocirc;ng c&ocirc;ng bằng với những nh&acirc;n vi&ecirc;n ở cấp dưới.</p>\r\n<p class=\"Normal\">V&agrave; mặc d&ugrave; &ocirc;ng đ&atilde; cố tạo ra m&ocirc;i trường l&agrave;m việc khẩn trương, nhưng đ&ocirc;i l&uacute;c nh&acirc;n vi&ecirc;n của &ocirc;ng lại phản ứng qu&aacute; chậm chạp. Một gi&aacute;m đốc đ&atilde; ph&agrave;n n&agrave;n khi nh&acirc;n vi&ecirc;n của m&igrave;nh mất qu&aacute; nhiều thời gian để trả lời lại một y&ecirc;u cầu: &ldquo;Ch&uacute;ng ta cần c&oacute; một hệ thống phản ứng 24 giờ&rdquo;.</p>\r\n<p class=\"Normal\">Để n&acirc;ng cao nhuệ kh&iacute;, &ocirc;ng Halderman thường xuy&ecirc;n gặp gỡ c&aacute;c nh&acirc;n vi&ecirc;n của m&igrave;nh sau giờ ăn trưa tại qu&aacute;n caf&eacute; của c&ocirc;ng ty v&agrave; khuyến kh&iacute;ch họ để b&agrave;y tỏ sự quan t&acirc;m. Khi một nh&acirc;n vi&ecirc;n hỏi &ocirc;ng khi n&agrave;o ch&iacute;nh s&aacute;ch nh&agrave; đất sẽ thay đổi, &ocirc;ng đ&atilde; trả lời rằng việc Fannie v&agrave; Freddie thống trị thị trường kh&ocirc;ng thể k&eacute;o d&agrave;i m&atilde;i m&atilde;i được, v&agrave; &ldquo;T&ocirc;i kh&ocirc;ng biết liệu việc cạnh tranh c&oacute; đang đến hay kh&ocirc;ng. Nhưng ch&uacute;ng ta cần phải sẵn s&agrave;ng cho việc đ&oacute;&rdquo;.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>H&agrave; Thu</strong>&nbsp;(theo&nbsp;<em>Financial Times</em>)</p>', 11, 1383, 0, 1, NULL, NULL),
(459, 'Người đàn ông biến nước thành vàng', 'Nguoi-Dan-Ong-Bien-Nuoc-Thanh-Vang', 'Không giống như những người xây dựng nên các đế chế nổi tiếng như Warren Buffett hay Bill Gates, Dick Heckmann là một cái tên khá xa lạ đối với phần đông mọi người.', 'heckmann_1.jpg', 22, '<h1 class=\"Title\">Người đ&agrave;n &ocirc;ng biến nước th&agrave;nh v&agrave;ng</h1>\r\n<h2 class=\"Lead\">Kh&ocirc;ng giống như những người x&acirc;y dựng n&ecirc;n c&aacute;c đế chế nổi tiếng như Warren Buffett hay Bill Gates, Dick Heckmann l&agrave; một c&aacute;i t&ecirc;n kh&aacute; xa lạ đối với phần đ&ocirc;ng mọi người.</h2>\r\n<p class=\"Normal\">Nhưng người đ&agrave;n &ocirc;ng 67 tuổi n&agrave;y thực sự l&agrave; một đế vương.</p>\r\n<p class=\"Normal\">&Ocirc;ng n&oacute;i: &ldquo;T&ocirc;i thật sự muốn trở th&agrave;nh một người khổng lồ 800 triệu USD một lần nữa&rdquo;. Trong kinh Cựu ước c&oacute; c&acirc;u n&oacute;i: H&atilde;y mơ những giấc mơ vĩ đại. V&agrave; điều đ&oacute; ho&agrave;n to&agrave;n đ&uacute;ng với Heckmann, &ocirc;ng kh&ocirc;ng chỉ d&aacute;m mơ ước, m&agrave; c&ograve;n ki&ecirc;n tr&igrave; l&agrave;m tất cả mọi việc để biến giấc mơ của m&igrave;nh th&agrave;nh hiện thực.</p>\r\n<p class=\"Normal\">V&agrave;o những năm 1990, Heckmann, với sự trợ gi&uacute;p của một thư k&yacute;, đ&atilde; lập ra Tập đo&agrave;n tinh lọc Mỹ - một c&ocirc;ng ty được ni&ecirc;m yết tr&ecirc;n s&agrave;n chứng kho&aacute;n New York &ndash; chuy&ecirc;n xử l&yacute; nước thải. V&agrave; trong 9 năm sau đ&oacute;, bằng việc th&acirc;u t&oacute;m li&ecirc;n tiếp 260 c&ocirc;ng ty nước đi k&egrave;m với việc ph&aacute;t triển nội bộ, &ocirc;ng đ&atilde; x&acirc;y dựng n&ecirc;n một tập đo&agrave;n h&ugrave;ng mạnh với doanh thu hằng năm l&ecirc;n tới 5 tỷ USD. Năm 1999, &ocirc;ng đ&atilde; b&aacute;n c&ocirc;ng ty m&igrave;nh cho Vivendi với gi&aacute; 8,2 tỷ USD.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5f/96/heckmann_1.jpg\" border=\"1\" alt=\"Ảnh: Dick Heckmann &ndash; người đ&agrave;n &ocirc;ng biến nước th&agrave;nh v&agrave;ng (beloblog)\" width=\"450\" height=\"295\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ảnh: Dick Heckmann &ndash; người đ&agrave;n &ocirc;ng biến nước th&agrave;nh v&agrave;ng (beloblog)</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Khi c&ograve;n đương nhiệm, &ocirc;ng đ&atilde; l&agrave;m cho doanh thu của tập đo&agrave;n tăng gấp đ&ocirc;i sau mỗi năm trong 9 năm li&ecirc;n tiếp v&agrave; gi&aacute; cổ phiếu cũng nhảy vọt từ 0,75 USD l&ecirc;n 33 USD. V&igrave; vậy c&aacute;c nh&agrave; đầu tư đ&atilde; kiếm được một khoản tiền lớn khi chỉ phải bỏ ra v&agrave;i USD ban đầu để mua cổ phiếu của c&ocirc;ng ty n&agrave;y. Heckmann thực sự đ&atilde; biến nước th&agrave;nh v&agrave;ng.</p>\r\n<p class=\"Normal\">Số t&agrave;i sản của &ocirc;ng hiện c&oacute; gi&aacute; trị ước t&iacute;nh l&agrave; 200 triệu USD. Dự &aacute;n cuối c&ugrave;ng của &ocirc;ng, bao gồm cả việc sở hữu một phần đội b&oacute;ng rổ Phoenix Suns, ch&iacute;nh l&agrave; tập đo&agrave;n Heckmann. Th&agrave;nh lập năm 2008 v&agrave; c&oacute; trụ sở tại Palm Desert, California, tập đo&agrave;n n&agrave;y mua v&agrave; x&acirc;y dựng n&ecirc;n c&aacute;c c&ocirc;ng ty trong ng&agrave;nh c&ocirc;ng nghiệp nước.</p>\r\n<p class=\"Normal\">Một lần nữa, Heckmann lại lại bắt tay v&agrave;o c&ocirc;ng việc x&acirc;y dựng đế chế. Chỉ trong v&ograve;ng hơn 2 năm, Heckmann đ&atilde; trở th&agrave;nh &ocirc;ng chủ của c&aacute;c ng&agrave;nh c&ocirc;ng nghiệp li&ecirc;n quan đến nước v&agrave; l&agrave; người chơi thống trị trong việc xử l&yacute; c&aacute;c loại nước li&ecirc;n quan đến năng lượng.</p>\r\n<p class=\"Normal\">C&aacute;c khoản đầu tư v&agrave; hoạt động nổi tiếng của &ocirc;ng bao gồm: sở hữu 100% c&ocirc;ng ty China Water &amp; Drink - một trong những nh&agrave; cung cấp nước lớn nhất cho Coca-Cola Trung Quốc, sở hữu 50% một c&ocirc;ng ty xử l&yacute; nước, một c&ocirc;ng ty li&ecirc;n doanh giữa Tập đo&agrave;n Heckmann v&agrave; c&ocirc;ng ty Energy Transfer Partners L.P, v&agrave; 7% cổ phần của c&ocirc;ng ty Underground Solutions &ndash; một nh&agrave; cung cấp nhựa PVC với nhiều c&ocirc;ng nghệ được cấp bằng s&aacute;ng chế.</p>\r\n<p class=\"Normal\">Heckmann n&oacute;i rằng nước l&agrave; ng&agrave;nh c&ocirc;ng nghiệp ngh&igrave;n tỷ USD v&agrave; l&agrave; nơi duy nhất &ocirc;ng biết rằng m&igrave;nh sẽ kh&ocirc;ng phải chiến đấu với một người khổng lồ nặng 800 pound (363 kg). Heckmann muốn lấp đầy khoảng trống đ&oacute;. &Ocirc;ng nhẩm t&iacute;nh trong v&ograve;ng 5 năm, c&ocirc;ng ty của m&igrave;nh sẽ l&agrave; c&ocirc;ng ty cung cấp nước sạch độc lập lớn nhất nước Mỹ v&agrave; l&agrave; c&ocirc;ng ty duy nhất tại thời điểm đ&oacute; c&oacute; doanh thu h&agrave;ng năm hơn 1 tỷ USD.</p>\r\n<p class=\"Normal\">C&oacute; lẽ điều đ&oacute; sẽ th&agrave;nh sự thật, nhưng &ocirc;ng vẫn c&ograve;n rất nhiều việc phải l&agrave;m. Trong năm đầu ti&ecirc;n hoạt động hết c&ocirc;ng suất (2009), c&ocirc;ng ty đ&atilde; đạt doanh thu 35 triệu USD. Năm 2010, bằng việc mua lại một c&ocirc;ng ty c&oacute; doanh thu 70 triệu USD, doanh thu của Heckmann được n&acirc;ng l&ecirc;n mức 105 triệu USD. &Ocirc;ng kh&ocirc;ng n&oacute;i tới doanh thu của năm 2011, nhưng một số dự b&aacute;o cho thấy c&ocirc;ng ty sẽ c&oacute; lợi doanh thu năm 2011 l&agrave; 140 triệu USD.</p>\r\n<p class=\"Normal\">Tại thời điểm n&agrave;y, Heckmann n&oacute;i c&ocirc;ng ty đang c&oacute; 200 triệu USD tiền mặt v&agrave; kh&ocirc;ng c&oacute; một khoản nợ n&agrave;o. Liệu rằng việc &ocirc;ng c&oacute; thể x&acirc;y dựng n&ecirc;n một đế chế nữa hay kh&ocirc;ng c&ograve;n t&ugrave;y v&agrave;o ph&aacute;n đo&aacute;n của mỗi người. Nhưng &iacute;t nhất th&igrave; vẫn c&oacute; một v&agrave;i c&ocirc;ng ty trong ng&agrave;nh tỏ &yacute; nghi ngờ khi nh&igrave;n v&agrave;o chuyển động của gi&aacute; cổ phiếu: khởi đầu với mức gi&aacute; 8 USD, sau đ&oacute; tăng l&ecirc;n 10,74 v&agrave; hiện đứng ở gi&aacute; 4,96 USD. Tương tự, c&ocirc;ng ty n&agrave;y cũng c&oacute; phải trả một khoản l&atilde;i ngắn hạn từ hơn 3 triệu tr&ecirc;n tổng số 108 triệu cổ phiếu đang được lưu h&agrave;nh của c&ocirc;ng ty.</p>\r\n<p class=\"Normal\">Nhưng Neil Weisman, một cựu gi&aacute;m đốc quỹ đầu tư lại kh&ocirc;ng đồng t&igrave;nh với quan điểm n&agrave;y. &Ocirc;ng kỳ vọng một sự tăng trưởng mạnh v&agrave; cổ phiếu sẽ được giao dịch với gi&aacute; 10 USD trong v&ograve;ng 12 th&aacute;ng tới. &Ocirc;ng n&oacute;i: &ldquo;Nước l&agrave; con đường để đi theo v&agrave; Heckmann ch&iacute;nh l&agrave; c&aacute;ch tốt nhất để l&agrave;m điều đ&oacute;&rdquo;.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>H&agrave; Thu</strong>&nbsp;(theo&nbsp;<em>Huffingtonpost</em>)</p>', 11, 40, 0, 1, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(460, 'Chuyện doanh nhân chạy sô tất niên', 'Chuyen-Doanh-Nhan-Chay-So-Tat-Nien', 'Những ngày giáp Tết, tôi chật vật với các buổi tiệc tất niên. Chỗ nào cũng tình nghĩa hết nên một buổi tối, tôi chạy 2-3 sô là bình thường. Cũng chúc mừng, cụng ly, hát hò liên tục đến... khàn tiếng, hâm hấp sốt mới ra về.', 'Anh-Tuan-Quynh (1).jpg', 22, '<h1 class=\"Title\">Chuyện doanh nh&acirc;n chạy s&ocirc; tất ni&ecirc;n</h1>\r\n<h2 class=\"Lead\">Những ng&agrave;y gi&aacute;p Tết, t&ocirc;i chật vật với c&aacute;c buổi tiệc tất ni&ecirc;n. Chỗ n&agrave;o cũng t&igrave;nh nghĩa hết n&ecirc;n một buổi tối, t&ocirc;i chạy 2-3 s&ocirc; l&agrave; b&igrave;nh thường. Cũng ch&uacute;c mừng, cụng ly, h&aacute;t h&ograve; li&ecirc;n tục đến... kh&agrave;n tiếng, h&acirc;m hấp sốt mới ra về.</h2>\r\n<p class=\"Normal\">C&oacute; những tiệc tất ni&ecirc;n sang trọng, c&oacute; ca sĩ nổi tiếng h&aacute;t gi&uacute;p vui v&agrave; cũng c&oacute; những bữa tất ni&ecirc;n đạm bạc, chỉ l&agrave; một bữa cơm trưa để tiễn c&ocirc;ng nh&acirc;n về qu&ecirc; ăn Tết! Những buổi tất ni&ecirc;n, d&ugrave; sang d&ugrave; h&egrave;n vẫn thấm đượm t&igrave;nh đồng nghiệp.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5e/6e/Anh-Tuan-Quynh.jpg\" alt=\"\" width=\"380\" height=\"321\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Doanh nh&acirc;n Nguyễn Tuấn Quỳnh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Tiệc tất ni&ecirc;n l&agrave; một dịp để t&ocirc;i chia sẻ v&agrave; lắng nghe t&acirc;m sự của nh&acirc;n vi&ecirc;n. Khi m&agrave; kh&ocirc;ng kh&iacute; buổi tiệc th&acirc;n mật, khoảng c&aacute;ch giữa sếp v&agrave; l&iacute;nh như gần lại, người ta dễ mở l&ograve;ng hơn. C&oacute; những người, đ&ocirc;i khi cả năm, t&ocirc;i chỉ gặp một lần v&agrave;o dịp n&agrave;y. Qua c&aacute;c cuộc tr&ograve; chuyện, t&ocirc;i nhận ra rằng, nh&acirc;n vi&ecirc;n để &yacute; sếp rất kỹ. Những b&agrave;i b&aacute;o t&ocirc;i viết, những lần t&ocirc;i xuất hiện tr&ecirc;n truyền h&igrave;nh... được c&aacute;c bạn nh&acirc;n vi&ecirc;n nhắc với sự ngưỡng mộ. C&ograve;n bản th&acirc;n t&ocirc;i th&igrave; ngượng ch&iacute;n người khi nghe như vậy.</p>\r\n<p class=\"Normal\">Kh&ocirc;ng những thế, những lời n&oacute;i tại b&agrave;n tiệc, d&ugrave; l&agrave; l&uacute;c t&ocirc;i đ&atilde; ng&agrave; ng&agrave; say cũng được nh&acirc;n vi&ecirc;n ghi nhận v&agrave; ch&uacute; &yacute;. Sau buổi tiệc, một chị trưởng ph&ograve;ng gặp t&ocirc;i v&agrave; n&oacute;i: Chị rất th&iacute;ch c&aacute;ch t&ocirc;i cư xử trong tiệc tất ni&ecirc;n h&ocirc;m qua, đ&oacute; l&agrave; khi t&ocirc;i n&oacute;i với một bạn nh&acirc;n vi&ecirc;n khi bạn n&agrave;y gọi t&ocirc;i l&agrave; sếp: \"H&atilde;y gọi anh l&agrave; anh th&ocirc;i\".</p>\r\n<p class=\"Normal\">T&ocirc;i c&oacute; nhớ mang m&aacute;ng chuyện n&agrave;y nhưng kh&ocirc;ng ngờ những lời m&igrave;nh n&oacute;i, tưởng v&ocirc; thưởng v&ocirc; phạt lại bị cấp dưới để &yacute; như vậy. Hoặc l&agrave; chỉ cần nh&igrave;n thấy t&ocirc;i suy tư, c&oacute; bạn nh&acirc;n vi&ecirc;n đ&atilde; cầm ly bia đến mời v&agrave; hỏi: Sao nh&igrave;n anh buồn vậy? Lại phải cười, n&oacute;i, reo h&ograve; để kh&ocirc;ng kh&iacute; tiệc tất ni&ecirc;n lu&ocirc;n s&ocirc;i động, để những người đồng nghiệp th&acirc;n y&ecirc;u cảm nhận trọn vẹn t&igrave;nh cảm ấm &aacute;p của một gia đ&igrave;nh thứ hai.</p>\r\n<p class=\"Normal\">Tiệc tất ni&ecirc;n cũng l&agrave; dịp để mọi người tổng kết lại một năm trong kh&ocirc;ng kh&iacute; nhẹ nh&agrave;ng, vui vẻ. Nhờ vậy m&agrave; những nhận x&eacute;t, đ&aacute;nh gi&aacute; cũng ch&acirc;n th&agrave;nh v&agrave; t&iacute;ch cực hơn. C&oacute; lẽ đ&acirc;y cũng l&agrave; dịp tốt nhất để g&oacute;p &yacute; v&agrave; gi&atilde;i b&agrave;y. Với t&acirc;m l&yacute; c&ograve;n những g&igrave; uẩn kh&uacute;c trong l&ograve;ng th&igrave; n&oacute;i ra để năm sau bắt đầu bằng những niềm vui, sự hợp t&aacute;c t&iacute;ch cực, t&ocirc;i cũng đ&atilde; nhận được rất nhiều đ&aacute;nh gi&aacute;, chia sẻ của nh&acirc;n vi&ecirc;n với bản th&acirc;n m&igrave;nh.</p>\r\n<p class=\"Normal\">T&ocirc;i t&acirc;m niệm, đ&atilde; l&agrave;m quản l&yacute; phải l&agrave; một người \"đ&agrave;n anh\" thật sự: Phải bao dung, th&ocirc;ng cảm, biết c&aacute;ch đứng đằng sau nh&acirc;n vi&ecirc;n v&agrave; d&aacute;m chịu tr&aacute;ch nhiệm. V&igrave; vậy, tiệc tất ni&ecirc;n cũng l&agrave; nơi t&ocirc;i thể hiện sự \"đ&agrave;n anh\" của m&igrave;nh. Những sai s&oacute;t, lỗi lầm của nh&acirc;n vi&ecirc;n trong năm vừa qua, sẽ được g&oacute;p &yacute; nhẹ nh&agrave;ng với thiện &yacute; mong muốn nh&acirc;n vi&ecirc;n m&igrave;nh sẽ tốt hơn, ho&agrave;n thiện hơn! T&ocirc;i lớn l&ecirc;n, trưởng th&agrave;nh cũng nhờ những lần như vậy.</p>\r\n<p class=\"Normal\">Tiệc tất ni&ecirc;n cũng l&agrave; nơi để n&oacute;i l&ecirc;n lời cảm ơn. C&oacute; nhiều nh&acirc;n vi&ecirc;n đ&atilde; l&agrave;m việc xuất sắc trong suốt năm qua nhưng t&ocirc;i chưa khen ngợi họ bao giờ. C&oacute; thể l&agrave; &iacute;t c&oacute; cơ hội gặp gỡ ri&ecirc;ng hoặc t&acirc;m l&yacute; đợi đến cuối năm, mới đ&aacute;nh gi&aacute; kết quả l&agrave;m việc một lần n&ecirc;n tại tiệc tất ni&ecirc;n, t&ocirc;i lu&ocirc;n động vi&ecirc;n khen ngợi những đồng nghiệp xuất sắc. T&ocirc;i hiểu c&aacute;i siết chặt tay, lời động vi&ecirc;n, khen ngợi lu&ocirc;n l&agrave; những động lực mạnh mẽ để mọi người nỗ lực v&agrave; phấn đấu hơn nữa trong tương lai. Chợt nhận ra rằng, sao m&igrave;nh tiết kiệm lời khen ngợi trong năm qu&aacute; vậy. Một lần khen ngợi v&agrave;o dịp tất ni&ecirc;n l&agrave; qu&aacute; &iacute;t.</p>\r\n<p class=\"Normal\">Tiệc tất ni&ecirc;n cũng l&agrave; dịp để nh&igrave;n về tương lai. Khi sống trong kh&ocirc;ng kh&iacute; tập thể gắn b&oacute;, đo&agrave;n kết, c&aacute;c mục ti&ecirc;u, kế hoạch của năm sau dường như kh&ocirc;ng c&ograve;n qu&aacute; kh&oacute; để đạt được. Rất nhiều s&aacute;ng kiến, đề xuất thiết thực được đưa ra trong những bữa tiệc như vậy. Dường như, bứt ra khỏi văn ph&ograve;ng với những &aacute;p lực triền mi&ecirc;n, sức s&aacute;ng tạo của con người được ph&aacute;t huy?</p>\r\n<p class=\"Normal\">Cuối năm kh&ocirc;ng chỉ dự tiệc tất ni&ecirc;n ở c&ocirc;ng ty của m&igrave;nh m&agrave; c&ograve;n được c&ocirc;ng ty bạn b&egrave;, đối t&aacute;c mời. Chỗ n&agrave;o cũng t&igrave;nh nghĩa. Đ&atilde; đi chỗ n&agrave;y rồi m&agrave; kh&ocirc;ng dự chỗ kh&aacute;c th&igrave; thể n&agrave;o cũng bị tr&aacute;ch m&oacute;c. Vậy l&agrave; phải r&aacute;ng sắp xếp để đi dự đầy đủ nhất c&oacute; thể được. V&agrave;o tiệc, cứ ngồi nhấp nhỏm để khai mạc tiệc xong, ăn 2, 3 m&oacute;n đầu l&agrave; chạy qua chỗ kh&aacute;c. Tần suất chạy s&ocirc; c&ograve;n hơn ca sĩ ng&ocirc;i sao. Nếu c&aacute;c nơi tổ chức tất ni&ecirc;n gần nhau th&igrave; c&ograve;n đỡ. Kh&ocirc;ng may, ở 2 đầu th&agrave;nh phố th&igrave; chỉ c&oacute; thể đi xe &ocirc;m v&igrave; &ocirc;t&ocirc; th&igrave; kẹt cứng.</p>\r\n<p class=\"Normal\">C&oacute; tiệc tất ni&ecirc;n, kh&ocirc;ng chỉ c&oacute; nh&acirc;n vi&ecirc;n m&agrave; c&ograve;n c&oacute; sự tham dự của người th&acirc;n trong gia đ&igrave;nh. Nh&igrave;n những b&eacute; bụ bẫm trong khi nh&acirc;n vi&ecirc;n m&igrave;nh th&igrave; gầy c&ograve;m, hiểu th&ecirc;m về tr&aacute;ch nhiệm v&agrave; sự giỏi giang của nh&acirc;n vi&ecirc;n. Nhớ lại m&oacute;n tiền thưởng m&agrave; m&igrave;nh sắp trao, chạnh l&ograve;ng v&igrave; thấy n&oacute; &iacute;t ỏi qu&aacute; so với vật gi&aacute; leo thang cũng như g&aacute;nh nặng trong việc chăm lo con c&aacute;i của nh&acirc;n vi&ecirc;n. Lại ngậm ng&ugrave;i.</p>\r\n<p class=\"Normal\">Tất ni&ecirc;n nhiều nghĩa l&agrave; ăn nhiều, uống nhiều, l&agrave; kh&ocirc;ng tập thể dục, l&agrave; tăng c&acirc;n, l&agrave; thiếu ngủ, l&agrave; bệnh tật. Dẫu biết rất r&otilde; như vậy nhưng kh&ocirc;ng tr&aacute;nh được. Một &ocirc;ng anh khuy&ecirc;n l&agrave; phải biết n&oacute;i \"Kh&ocirc;ng\", bỏ tất ni&ecirc;n m&agrave; về chơi với con. N&oacute;i th&igrave; dễ nhưng l&agrave;m sao kh&oacute; qu&aacute;. Nếu như ai đ&atilde; từng chứng kiến sự mong đợi của nh&acirc;n vi&ecirc;n khi c&oacute; mặt sếp dự tất ni&ecirc;n th&igrave; sẽ th&ocirc;ng cảm v&agrave; chia sẽ được. Cũng may, mỗi năm chỉ c&oacute; một lần Tết để m&agrave; tất ni&ecirc;n.</p>\r\n<p class=\"Normal\">Lại h&aacute;t ngh&ecirc;u ngao: \"Tết nhất l&agrave;m chi, ai b&agrave;y, Tết nhất l&agrave;m chi...\".</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nguyễn Tuấn Quỳnh</strong></p>', 11, 2918, 0, 1, NULL, NULL),
(461, 'CEO AIG dù ung thư vẫn làm việc', 'Ceo-Aig-Du-Ung-Thu-Van-Lam-Viec', 'Hôm qua, Tập đoàn Bảo hiểm Mỹ (AIG) cho biết CEO của họ là Robert Benmosche vẫn đủ sức khỏe để tiếp tục công việc dù đang chữa trị ung thư.', '3_benmosche.jpg', 22, '<h1 class=\"Title\">CEO AIG d&ugrave; ung thư vẫn l&agrave;m việc</h1>\r\n<h2 class=\"Lead\">H&ocirc;m qua, Tập đo&agrave;n Bảo hiểm Mỹ (AIG) cho biết CEO của họ l&agrave; Robert Benmosche vẫn đủ sức khỏe để tiếp tục c&ocirc;ng việc d&ugrave; đang chữa trị ung thư.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/5C/D6/3_benmosche.jpg\" border=\"1\" alt=\"\" width=\"235\" height=\"278\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">CEO của AIG, &ocirc;ng Benmosche đang bị ung thư nhưng vẫn l&agrave;m việc. Ảnh:<em>&nbsp;Time.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Tuy&ecirc;n bố ng&agrave;y h&ocirc;m qua của AIG đến sau 3 th&aacute;ng kể từ ng&agrave;y h&atilde;ng tiết lộ t&igrave;nh h&igrave;nh sức khỏe&nbsp;<a class=\"Normal\" href=\"http://vnexpress.net/GL/Kinh-doanh/Kinh-nghiem/2010/10/3BA22195/\">bi quan</a>&nbsp;của CEO. H&atilde;ng bảo hiểm kh&ocirc;ng n&oacute;i r&otilde; &ocirc;ng Benmosche đang mắc loại ung thư n&agrave;o.</p>\r\n<p class=\"Normal\">C&ograve;n vị CEO 66 tuổi cho biết, c&aacute;c b&aacute;c sĩ đ&atilde; đưa ra ph&aacute;c đồ điều trị rất t&iacute;ch cực v&agrave; hiện tại &ocirc;ng cảm thấy khỏe khoắn. \"B&aacute;c sĩ tin rằng t&ocirc;i c&oacute; thể tiếp tục l&agrave;m việc v&agrave; cống hiến ở vị tr&iacute; hiện tại trong khoảng 12 đến 18 th&aacute;ng tới\", &ocirc;ng n&oacute;i trong buổi họp b&aacute;o h&ocirc;m qua.</p>\r\n<p class=\"Normal\">Theo kế hoạch c&oacute; từ trước, Benmosche sẽ nghỉ hưu v&agrave;o năm 2012. Hồi 2006, &ocirc;ng từng quyết định nghỉ hưu sau một thời gian dẫn dắt Tập đo&agrave;n bảo hiểm MetLife. Tuy nhi&ecirc;n, dự định n&agrave;y được tạm thời g&aacute;c lại khi AIG mời &ocirc;ng về l&agrave;m CEO từ th&aacute;ng 8/2009.</p>\r\n<p class=\"Normal\">&Ocirc;ng thay thế&nbsp;<a class=\"Normal\" href=\"http://vnexpress.net/GL/Kinh-doanh/Kinh-nghiem/2009/03/3BA0D75E/\">Edward Liddy</a>, người ngồi chiếc ghế CEO mới được một năm nhưng đ&atilde; kịp tạo ra h&agrave;ng loạt&nbsp;<a class=\"Normal\" href=\"http://vnexpress.net/GL/Kinh-doanh/Kinh-nghiem/2009/03/3BA0D4B4/\">tai tiếng</a>. C&ograve;n dưới sự l&atilde;nh đạo của Benmosche, AIG đ&atilde; trở lại kh&aacute; ngoạn mục sau khi nhận g&oacute;i cứu trợ khẩn cấp khổng lồ trị gi&aacute; 182 tỷ USD từ Ch&iacute;nh phủ Mỹ. Hiện AIG đ&atilde; trả gần hết số nợ khổng lồ n&agrave;y.</p>\r\n<p class=\"Normal\">Tập đo&agrave;n AIG đ&atilde; l&ecirc;n sẵn kế hoạch thay thế nếu Benmosche nghỉ đột ngột, theo đ&oacute; Chủ tịch Robert Miller sẽ l&agrave;m quyền CEO trong khi h&atilde;ng lựa chọn người thay thế ch&iacute;nh thức.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Thanh B&igrave;nh</strong></p>', 11, 1696, 0, 1, NULL, NULL),
(610, 'Thiên đường của các loài chim', 'Thien-Duong-Cua-Cac-Loai-Chim', 'Những cánh rừng ở Belize, Trung Mỹ, là nơi sinh sống của vô số các loài chim, từ diệc hổ cổ trần cho đến tu căng ngọc lục bảo.', 'chim-7.jpg', 22, '<p class=\"Title\">Thi&ecirc;n đường của c&aacute;c lo&agrave;i chim</p>\r\n<p class=\"Lead\">Những c&aacute;nh rừng ở Belize, Trung Mỹ, l&agrave; nơi sinh sống của v&ocirc; số c&aacute;c lo&agrave;i chim, từ diệc hổ cổ trần cho đến tu căng ngọc lục bảo.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E6/E4/chim-7.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"363\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&ograve; đầu gỗ tung c&aacute;nh tr&ecirc;n nền trời xanh. Lo&agrave;i chim n&agrave;y thường kiếm ăn ở v&ugrave;ng nước n&ocirc;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E6/E4/chim1.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"344\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một con diệc hổ cổ trần vươn chiếc mỏ v&agrave; cổ d&agrave;i tr&ecirc;n c&agrave;nh c&acirc;y. Ch&uacute;ng thường l&agrave;m tổ tr&ecirc;n những bụi c&acirc;y gần mặt nước.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E6/E4/chim-8.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"343\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Chim ong ch&acirc;n đỏ l&agrave;m tổ tr&ecirc;n c&acirc;y v&agrave; h&uacute;t mật hoa.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E6/E4/chim-6.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"321\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đ&ocirc;i vẹt đu&ocirc;i d&agrave;i đậu tr&ecirc;n c&agrave;nh c&acirc;y. T&igrave;nh trạng khai th&aacute;c rừng khiến m&ocirc;i trường sinh sống của ch&uacute;ng bị thu hẹp lại. Lo&agrave;i chim n&agrave;y c&oacute; thể sống tới 75 năm.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E6/E4/chim-2.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"340\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Chim tu căng ngọc lục bảo c&oacute; rất nhiều ở Belize. Lo&agrave;i chim n&agrave;y ăn c&ocirc;n tr&ugrave;ng, hoa quả v&agrave; cả trứng của c&aacute;c lo&agrave;i chim kh&aacute;c.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E6/E4/chim-3.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"331\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Để dụ c&aacute;, lo&agrave;i diệc xanh n&agrave;y thường thả c&ocirc;n tr&ugrave;ng hoặc s&acirc;u xuống những chỗ nước n&ocirc;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E6/E4/chim-4.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"342\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Chim aracari cổ đỏ thường sống theo nh&oacute;m khoảng 15 con.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E6/E4/chim-5.jpg\" border=\"0\" alt=\"\" width=\"360\" height=\"477\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">G&otilde; kiến m&aacute; đen kiếm mồi tr&ecirc;n những c&agrave;nh c&acirc;y chết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ngọc Sơn</strong>&nbsp;(theo&nbsp;<em>NYT</em>)</p>', 35, 1380, 0, 1, NULL, NULL),
(611, 'Ảnh động vật trong tuần  ', 'Anh-Dong-Vat-Trong-Tuan', 'Chim làm tổ trong chụp đèn giao thông trên phố. Lạc đà không bướu tung mình qua hàng rào. Dưới đây là những bức ảnh đẹp về thế giới động vật tuần qua.', 'chim.jpg', 22, '<p class=\"Title\">Ảnh động vật trong tuần</p>\r\n<p class=\"Lead\">Chim l&agrave;m tổ trong chụp đ&egrave;n giao th&ocirc;ng tr&ecirc;n phố. Lạc đ&agrave; kh&ocirc;ng bướu tung m&igrave;nh qua h&agrave;ng r&agrave;o. Dưới đ&acirc;y l&agrave; những bức ảnh đẹp về thế giới động vật tuần qua.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E6/29/chim.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"349\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Chim l&agrave;m tổ trong chụp đ&egrave;n giao th&ocirc;ng ở trung t&acirc;m th&agrave;nh phố Leeds, Anh. Ảnh:&nbsp;<em>PA.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E6/29/meo.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"347\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ban nhạc rock của c&aacute;c ch&uacute; m&egrave;o, ng&ocirc;i sao của rạp xiếc American Acro-Cats. Ảnh:<em>AFP/GETTY</em><strong>.</strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E6/29/lac-da.jpg\" border=\"0\" alt=\"lạc đ&agrave;\" width=\"460\" height=\"292\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ch&uacute; lạc đ&agrave; kh&ocirc;ng biếu tung m&igrave;nh qua h&agrave;ng r&agrave;o ở Dorset, Anh. Ảnh:&nbsp;<em>DORSET MEDIA SERVICE</em><strong>.</strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E6/29/lemut.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"312\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hai con vượn c&aacute;o gặm hoa quả trong vườn th&uacute; Bernburg, Đức. Ảnh:&nbsp;<em>REX FEATURES</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E6/29/da-dieu.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"311\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đ&agrave; điểu con 10 ng&agrave;y tuổi theo ch&acirc;n bố trong vườn th&uacute; Berlin, Đức. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E6/29/cho.jpg\" border=\"0\" alt=\"ch&oacute;\" width=\"460\" height=\"312\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ch&oacute; tham gia tr&igrave;nh diễn thời trang ở Bangkok, Th&aacute;i Lan. Ảnh:&nbsp;<em>Reuters.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E6/29/lion.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"313\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Sư tử n&uacute;i đu m&igrave;nh tr&ecirc;n c&acirc;y ở Wisconsin, Mỹ. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ngọc Sơn</strong></p>', 35, 2869, 0, 1, NULL, NULL),
(612, 'Hình ảnh ấn tượng về Ngày Trái đất', 'Hinh-Anh-An-Tuong-Ve-Ngay-Trai-Dat', 'Kể từ năm 1970, Ngày Trái đất được tổ chức thường niên vào 22/4, trong đó sinh viên các trường đại học khắp nước Mỹ tụ họp và tăng cường hiểu biết về sự suy thoái của môi trường. Sự kiện này cũng diễn ra tại một số nước khác.', 'ntd1.jpg', 22, '<p class=\"Title\">H&igrave;nh ảnh ấn tượng về Ng&agrave;y Tr&aacute;i đất</p>\r\n<p class=\"Lead\">Kể từ năm 1970, Ng&agrave;y Tr&aacute;i đất được tổ chức thường ni&ecirc;n v&agrave;o 22/4, trong đ&oacute; sinh vi&ecirc;n c&aacute;c trường đại học khắp nước Mỹ tụ họp v&agrave; tăng cường hiểu biết về sự suy tho&aacute;i của m&ocirc;i trường. Sự kiện n&agrave;y cũng diễn ra tại một số nước kh&aacute;c.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E4/F8/ntd1.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"311\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ch&agrave;ng trai n&agrave;y giả l&agrave;m con ong ngửi hoa mộc lan gần đại học Pace, New York, Mỹ. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E4/F8/ntd2.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">H&agrave;ng ngh&igrave;n học sinh tiểu học v&agrave; trung học cũng tham gia Ng&agrave;y Tr&aacute;i đất năm 1970. Trong ảnh, c&aacute;c nữ sinh New York qu&eacute;t dọn gần đ&agrave;i tưởng niệm ở c&ocirc;ng vi&ecirc;n Union của th&agrave;nh phố. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E4/F8/ntd3.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"318\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Quả cầu m&ocirc; phỏng tr&aacute;i đất c&oacute; chiều cao bằng một t&ograve;a nh&agrave; 5 tầng được dựng ở Washington để kỷ niệm 25 năm Ng&agrave;y Tr&aacute;i đất năm 1995. Ảnh: AFP/Getty.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E4/F8/ntd7.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"318\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Mỹ ph&aacute;t động chiến dịch thu dọn r&aacute;c thải g&acirc;y độc trong gia đ&igrave;nh nh&acirc;n Ng&agrave;y Tr&aacute;i đất năm 1990. Ảnh:&nbsp;<em>Corbis.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E4/F8/ntd4.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"326\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tr&igrave;nh diễn trang phục l&agrave;m từ vỏ hộp ở Seoul năm 1996. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E4/F8/ntd5.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"312\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Paul McCartney v&agrave; Ringo Starr, hai th&agrave;nh vi&ecirc;n ban nhạc huyền thoại The Beatles, h&aacute;t trong chương tr&igrave;nh &acirc;m nhạc nh&acirc;n Ng&agrave;y Tr&aacute;i đất tại Los Angeles năm 1993. Ảnh:&nbsp;<em>Getty.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E4/F8/ntd6.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"328\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">B&eacute; g&aacute;i Nepal trong trang phục m&ocirc; phỏng tr&aacute;i đất tại Katmandu năm 2002. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ngọc Sơn</strong></p>', 35, 1634, 0, 1, NULL, NULL),
(613, 'Ảnh đẹp động vật trong tuần', 'Anh-Dep-Dong-Vat-Trong-Tuan', 'Ếch ngã bổ nhào trên tàu lá. Cá voi quẫy đuôi tung mặt nước bên cạnh nữ huấn luyện viên. Dưới đây là ảnh đẹp về thế giới động vật tuần qua.', 'ech.jpg', 22, '<p class=\"Title\">Ảnh đẹp động vật trong tuần</p>\r\n<p class=\"Lead\">Ếch ng&atilde; bổ nh&agrave;o tr&ecirc;n t&agrave;u l&aacute;. C&aacute; voi quẫy đu&ocirc;i tung mặt nước b&ecirc;n cạnh nữ huấn luyện vi&ecirc;n. Dưới đ&acirc;y l&agrave; ảnh đẹp về thế giới động vật tuần qua.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E2/6D/ech.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"331\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một con ếch trượt tr&ecirc;n t&agrave;u l&aacute; tại vườn th&uacute; El Nispero, Panama. Ảnh:&nbsp;<em>Reuters.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E2/6D/soc.jpg\" border=\"0\" alt=\"\" width=\"360\" height=\"378\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Con s&oacute;c lộn ngược đầu xuống đất sau khi tấn c&ocirc;ng chiếc hộp gỗ đựng đồ cho chim ăn ở Ohio, Mỹ. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E2/6D/chim.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"303\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đại b&agrave;ng biển bụng trắng tung m&igrave;nh khỏi mặt nước ở đảo Langkawi, Malaysia. Ảnh:<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E2/6D/ca-heo.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"302\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute; heo biểu diễn b&oacute;ng nước ở Nuremberg, Đức. Ảnh:<em>&nbsp;AFP/GETTY.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E2/6D/ca.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"301\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nữ huấn luyện vi&ecirc;n đang bơi c&ugrave;ng con c&aacute; voi mang t&ecirc;n Lovey trong chương tr&igrave;nh biểu diễn tại c&ocirc;ng vi&ecirc;n Kamogawa ở Nhật. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E2/6D/tho.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"307\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Con thỏ rừng n&acirc;u th&egrave; lưỡi khi chạy nhảy tr&ecirc;n đồng cỏ ở Slimbridge, Anh. Ảnh:&nbsp;<em>J.S.Lees/WWT</em><strong>.</strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E2/6D/khi.jpg\" border=\"0\" alt=\"\" width=\"360\" height=\"466\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Khỉ con n&eacute;p m&igrave;nh trong l&ograve;ng mẹ trong c&ocirc;ng vi&ecirc;n Kilim Karts Geoforest ở Malaysia. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A0/E2/6D/kangaroo.jpg\" border=\"0\" alt=\"\" width=\"460\" height=\"352\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ch&uacute; kangaroo con t&ecirc;n l&agrave; Tijana đang được nh&acirc;n vi&ecirc;n vườn th&uacute; cho b&uacute; b&igrave;nh ở Belgrade, Serbia. Mẹ của Tijana đ&atilde; bỏ chạy v&agrave; văng cả con ra khỏi t&uacute;i v&igrave; sợ đ&agrave; điểu. Ảnh:&nbsp;<em>Reuters.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em></p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ngọc Sơn</strong></p>', 35, 1475, 0, 1, NULL, NULL),
(614, 'Trực thăng siêu khủng của Nga lộ diện', 'Truc-Thang-Sieu-Khung-Cua-Nga-Lo-Dien', 'Một công ty Nga chế tạo thành công loại trực thăng có khả năng bay rất cao và chống ăn mòn.', 'helicopter.jpg', 22, '<h1 class=\"Title\">Trực thăng si&ecirc;u khủng của Nga lộ diện</h1>\r\n<h2 class=\"Lead\">Một c&ocirc;ng ty Nga chế tạo th&agrave;nh c&ocirc;ng loại trực thăng c&oacute; khả năng bay rất cao v&agrave; chống ăn m&ograve;n.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/67/33/helicopter.jpg\" alt=\"Trực thăng Mil Mi-38-OP. Ảnh: RIA Novosti.\" width=\"450\" height=\"275\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Trực thăng Mil Mi-38-OP. Ảnh:&nbsp;<em>RIA Novosti</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Phi&ecirc;n bản mẫu của loại trực thăng Mil Mi-38-OP do nh&agrave; m&aacute;y sản xuất trực thăng Mil Moscow sản xuất đang được thử nghiệm. C&aacute;c chuy&ecirc;n gia trong nh&oacute;m thiết kế trực thăng khẳng định khả năng hoạt động của n&oacute; l&agrave; \"độc nhất v&ocirc; nhị\", với tầm bay 800 km, chở được 36 người v&agrave; 5 tấn h&agrave;ng h&oacute;a, h&atilde;ng th&ocirc;ng tấn&nbsp;<em>RIA Novosti</em>&nbsp;cho hay.</p>\r\n<p class=\"Normal\">\"Đa số trực thăng của Nga chỉ hoạt động từ 40 tới 45 năm. Mil Mi-38-OP sẽ phục vụ l&acirc;u hơn thế nhiều lần bởi th&acirc;n của ch&uacute;ng được l&agrave;m bằng loại hợp kim chống ăn m&ograve;n\", &ocirc;ng Alexander Samusenko, người đứng đầu bộ phận thiết kế m&aacute;y bay của nh&agrave; m&aacute;y Mil Moscow, khẳng định.</p>\r\n<p>Khoang l&aacute;i của Mil Mi-38-OP được trang bị hệ thống m&aacute;y t&iacute;nh hiện đại nhất. Hệ thống n&agrave;y li&ecirc;n tục th&ocirc;ng b&aacute;o những trục trặc c&oacute; thể xảy ra trong từng bộ phận của trực thăng. Với k&iacute;ch thước lớn v&agrave; khả năng cơ động cực cao, Mil Mi-38-OP c&oacute; thể trở th&agrave;nh \"bệnh viện bay\" khi chiến đấu hoặc tham gia cứu hộ thi&ecirc;n tai.</p>\r\n<p class=\"Normal\">Qu&aacute; tr&igrave;nh thử nghiệm sẽ k&eacute;o d&agrave;i trong v&agrave;i th&aacute;ng, thậm ch&iacute; v&agrave;i năm. Trong lần thử nghiệm tới, Mil Mi-38-OP sẽ hạ c&aacute;nh tr&ecirc;n Everest, ngọn n&uacute;i cao nhất h&agrave;nh tinh. Theo kế hoạch, n&oacute; sẽ được sản xuất h&agrave;ng loạt v&agrave; đưa v&agrave;o sử dụng trong năm 2014.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Minh Long</strong></p>', 36, 1659, 0, 1, NULL, NULL),
(615, 'Kính mắt chống đạn và tự đổi màu', 'Kinh-Mat-Chong-Dan-Va-Tu-Doi-Mau', 'Hải quân Mỹ bắt đầu thử nghiệm loại kính bảo vệ mắt có khả năng chống đạn và tự động thay đổi màu theo điều kiện ánh sáng.', 'k.jpg', 22, '<h1 class=\"Title\">K&iacute;nh mắt chống đạn v&agrave; tự đổi m&agrave;u</h1>\r\n<h2 class=\"Lead\">Hải qu&acirc;n Mỹ bắt đầu thử nghiệm loại k&iacute;nh bảo vệ mắt c&oacute; khả năng chống đạn v&agrave; tự động thay đổi m&agrave;u theo điều kiện &aacute;nh s&aacute;ng.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/65/eb/k.jpg\" alt=\"Một si&ecirc;u k&iacute;nh bảo hộ c&oacute; khả năng tự đổi m&agrave;u do AlphaMicron chế tạo. Ảnh: Fox News.\" width=\"450\" height=\"307\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một si&ecirc;u k&iacute;nh bảo hộ c&oacute; khả năng tự đổi m&agrave;u do AlphaMicron chế tạo. Ảnh:&nbsp;<em>Fox News</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">AlphaMicron - một c&ocirc;ng ty c&oacute; trụ sở tại th&agrave;nh phố Kent, bang Ohio, Mỹ - nghi&ecirc;n cứu v&agrave; chế tạo si&ecirc;u k&iacute;nh mắt đổi m&agrave;u trong gần 13 năm. Hai mắt k&iacute;nh của n&oacute; được gắn m&agrave;n h&igrave;nh tinh thể lỏng (LCD) si&ecirc;u mỏng v&agrave; c&oacute; khả năng uốn cong về một ph&iacute;a. Nhờ đ&oacute; ch&uacute;ng c&oacute; thể thay đổi m&agrave;u sắc theo điều kiện m&ocirc;i trường, như chuyển sang m&agrave;u sẫm khi gặp &aacute;nh s&aacute;ng mạnh v&agrave; trở n&ecirc;n trong suốt khi người sử dụng tiến v&agrave;o v&ugrave;ng tối hoặc c&oacute; b&oacute;ng r&acirc;m,&nbsp;<em>Fox News</em>&nbsp;cho hay.</p>\r\n<p class=\"Normal\">Khi cường độ &aacute;nh s&aacute;ng giảm, một d&ograve;ng điện chạy qua m&agrave;n h&igrave;nh LCD khiến c&aacute;c tinh thể trong đ&oacute; xếp thẳng h&agrave;ng, dẫn đến sự thay đổi của m&agrave;u k&iacute;nh trong v&agrave;i gi&acirc;y. Nếu cường độ &aacute;nh s&aacute;ng tăng, d&ograve;ng điện lại chạy qua m&agrave;n h&igrave;nh LCD khiến c&aacute;c tinh thể quay trở lại trạng th&aacute;i kh&ocirc;ng thẳng h&agrave;ng.</p>\r\n<p class=\"Normal\">&Ocirc;ng Charles Ziervogel, gi&aacute;m đốc bộ phận nghi&ecirc;n cứu của hải qu&acirc;n Mỹ, cho biết, k&iacute;nh bảo hộ chống đạn rất quan trọng đối với l&iacute;nh hải qu&acirc;n, bởi mắt của họ rất dễ bị tổn thương bởi nước, gi&oacute; v&agrave; bụi. Tuy nhi&ecirc;n, loại k&iacute;nh r&acirc;m để l&agrave;m dịu mắt trong m&ocirc;i trường s&aacute;ng lại giảm khả năng nh&igrave;n của binh sĩ hoặc phi c&ocirc;ng trong m&ocirc;i trường tối. Ngược lại, k&iacute;nh trong suốt để binh l&iacute;nh nh&igrave;n r&otilde; hơn trong b&oacute;ng r&acirc;m lại khiến mắt của người sử dụng dễ bị l&oacute;a trong m&ocirc;i trường c&oacute; &aacute;nh s&aacute;ng mạnh.</p>\r\n<p class=\"Normal\">&ldquo;Ch&uacute;ng t&ocirc;i y&ecirc;u cầu AlphaMicron chế tạo ba loại mắt k&iacute;nh: sẫm, s&aacute;ng v&agrave; hổ ph&aacute;ch. Người sử dụng c&oacute; thể thay thế c&aacute;c mắt k&iacute;nh trong tối đa 5 ph&uacute;t&rdquo;, Ziervogel n&oacute;i.</p>\r\n<p class=\"Normal\">Roy Miller, gi&aacute;m đốc kinh doanh v&agrave; tiếp thị của c&ocirc;ng ty AlphaMicron, cho biết, c&ocirc;ng ty chỉ sản xuất 30 si&ecirc;u k&iacute;nh mắt để hải qu&acirc;n Mỹ thử nghiệm. Tất cả ch&uacute;ng đều được l&agrave;m bằng tay. Sau khi qu&aacute; tr&igrave;nh thử nghiệm ho&agrave;n th&agrave;nh, c&ocirc;ng ty sẽ xem x&eacute;t khả năng tung ch&uacute;ng ra thị trường.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Việt Linh</strong></p>', 36, 2763, 0, 1, NULL, NULL),
(616, 'Con người sẽ \'cưỡi\' tên lửa để bay vào vũ trụ', '', 'Hai công ty sản xuất tên lửa hàng đầu thế giới muốn chế tạo loại hỏa tiễn có khả năng đưa người vào không gian mà không cần phi thuyền.', 'ariane.jpg', 22, '<h1 class=\"Title\">Con người sẽ \'cưỡi\' t&ecirc;n lửa để bay v&agrave;o vũ trụ</h1>\r\n<h2 class=\"Lead\">Hai c&ocirc;ng ty sản xuất t&ecirc;n lửa h&agrave;ng đầu thế giới muốn chế tạo loại hỏa tiễn c&oacute; khả năng đưa người v&agrave;o kh&ocirc;ng gian m&agrave; kh&ocirc;ng cần phi thuyền.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/65/9b/ariane.jpg\" alt=\"T&ecirc;n lửa Ariane 5. Ảnh:\" width=\"450\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">T&ecirc;n lửa Ariane 5. Ảnh:&nbsp;<em>sciencephoto.com</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">&Yacute; tưởng về t&ecirc;n lửa trực tiếp đưa người l&ecirc;n vũ trụ được đề xướng bởi c&ocirc;ng ty Alliant Techsystems tại Mỹ v&agrave; Astrium ở ch&acirc;u &Acirc;u,&nbsp;<em>BBC</em>&nbsp;cho biết. Hai c&ocirc;ng ty đ&atilde; gửi &yacute; tưởng của họ tới Cơ quan H&agrave;ng kh&ocirc;ng vũ trụ Mỹ (NASA) v&igrave; ch&iacute;nh phủ Mỹ đang t&igrave;m kiếm những giải ph&aacute;p thương mại nhằm đưa phi h&agrave;nh gia l&ecirc;n Trạm Kh&ocirc;ng gian quốc tế sau khi đội t&agrave;u vũ trụ con thoi của họ ngừng hoạt động cuối năm nay.</p>\r\n<p class=\"Normal\">Liberty, t&ecirc;n của loại t&ecirc;n lửa tương lai, sẽ sử dụng cả nhi&ecirc;n liệu rắn lẫn nhi&ecirc;u liệu lỏng để bay l&ecirc;n. Chiều d&agrave;i của n&oacute; v&agrave;o khoảng 90 m (trong khi t&ecirc;n lửa Ariane 5 của ch&acirc;u &Acirc;u c&oacute; độ d&agrave;i 50 m). Khoang chứa người sẽ nằm ở đầu hỏa tiễn. Liberty c&oacute; thể chở 20 tấn h&agrave;ng h&oacute;a l&ecirc;n quỹ đạo thấp của tr&aacute;i đất - nơi Trạm Kh&ocirc;ng gian quốc tế đang bay.</p>\r\n<p class=\"Normal\">Nhiều nghị sĩ trong quốc hội Mỹ lo ngại NASA sẽ phải chờ rất l&acirc;u trước khi t&agrave;u vũ trụ thế hệ mới ra đời. Nếu điều đ&oacute; xảy ra, c&oacute; lẽ Mỹ sẽ phải phụ thuộc v&agrave;o t&agrave;u Soyuz của Nga trong việc đưa phi h&agrave;nh gia l&ecirc;n vũ trụ tới hết thập kỷ n&agrave;y. Liberty c&oacute; thể trở th&agrave;nh một giải ph&aacute;p \"nhất cử lưỡng tiện\". N&oacute; kh&ocirc;ng chỉ gi&uacute;p NASA giảm chi ph&iacute; d&agrave;nh cho chương tr&igrave;nh chế tạo thế hệ t&agrave;u vũ trụ mới, m&agrave; c&ograve;n r&uacute;t ngắn thời gian phụ thuộc v&agrave;o Nga trong việc đưa nh&agrave; du h&agrave;nh l&ecirc;n vũ trụ. Alliant Techsystems v&agrave; Astrium tin rằng với kinh nghiệm v&agrave; tiềm lực của họ, t&ecirc;n lửa Liberty sẽ cất c&aacute;nh v&agrave;o năm 2013.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Việt Linh</strong></p>', 36, 3, 0, 1, NULL, NULL),
(617, 'Robot sẽ có mạng Internet riêng', 'Robot-Se-Co-Mang-Internet-Rieng', 'Các nhà khoa học châu Âu vừa khởi động một dự án để giúp robot lưu trữ và chia sẻ dữ liệu mà chúng tự thu thập từ thế giới xung quanh.', 'pancake-robot.jpg', 22, '<h1 class=\"Title\">Robot sẽ c&oacute; mạng Internet ri&ecirc;ng</h1>\r\n<h2 class=\"Lead\">C&aacute;c nh&agrave; khoa học ch&acirc;u &Acirc;u vừa khởi động một dự &aacute;n để gi&uacute;p robot lưu trữ v&agrave; chia sẻ dữ liệu m&agrave; ch&uacute;ng tự thu thập từ thế giới xung quanh.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/64/0e/pancake-robot.jpg\" alt=\"Một robot c&oacute; khả năng nấu nướng tại Nhật Bản. Ảnh: weirdthings.com.\" width=\"450\" height=\"383\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một robot c&oacute; khả năng nấu nướng tại Nhật Bản. Ảnh:&nbsp;<em>weirdthings.com.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><em>BBC</em>&nbsp;cho biết, RoboEarth, t&ecirc;n của dự &aacute;n do c&aacute;c nh&agrave; khoa học ch&acirc;u &Acirc;u thực hiện, sẽ tạo ra một nơi m&agrave; c&aacute;c robot c&oacute; thể đưa dữ liệu l&ecirc;n v&agrave; y&ecirc;u cầu trợ gi&uacute;p khi thực hiện một nhiệm vụ n&agrave;o đ&oacute;. C&aacute;c nh&agrave; khoa học tham gia dự &aacute;n hy vọng RoboEarth sẽ gi&uacute;p c&aacute;c robot thực hiện c&aacute;c nhiệm vụ nhanh hơn.</p>\r\n<p class=\"Normal\">Tiến sĩ Markus Waibel, một chuy&ecirc;n gia của Viện C&ocirc;ng nghệ li&ecirc;n bang Thụy Sĩ, cho biết, mục đ&iacute;ch ch&iacute;nh của RoboEarth l&agrave; nghi&ecirc;n cứu c&aacute;c biện ph&aacute;p để robot c&oacute; thể m&atilde; h&oacute;a, trao đổi v&agrave; khai th&aacute;c tri thức nhiều lần.</p>\r\n<p class=\"Normal\">&ldquo;Phần lớn robot hiện nay nh&igrave;n thế giới theo c&aacute;ch ri&ecirc;ng của ch&uacute;ng v&agrave; con người c&oacute; rất &iacute;t ti&ecirc;u chuẩn chung đối với dữ liệu của robot. Thực trạng n&agrave;y khiến c&aacute;c chuy&ecirc;n gia về robot gặp v&ocirc; v&agrave;n kh&oacute; khăn trong nỗ lực chia sẻ th&ocirc;ng tin d&ugrave; họ phải giải quyết c&aacute;c vấn đề giống hệt nhau. RoboEarth sẽ bao gồm kho dữ liệu ki&ecirc;m hệ thống li&ecirc;n lạc&rdquo;, Waibel n&oacute;i.</p>\r\n<p class=\"Normal\">RoboEarth cung cấp những bản đồ về vị tr&iacute; m&agrave; robot hoạt động, m&ocirc; tả những vật thể m&agrave; ch&uacute;ng thấy v&agrave; chỉ dẫn về c&aacute;ch ho&agrave;n th&agrave;nh những nhiệm vụ cụ thể. Cơ sở dữ liệu đ&oacute; giống như trang Wikipedia tr&ecirc;n mạng.</p>\r\n<p class=\"Normal\">&ldquo;Wikipedia l&agrave; thứ m&agrave; lo&agrave;i người sử dụng để chia sẻ tri thức. Mọi th&agrave;nh vi&ecirc;n đều c&oacute; thể bi&ecirc;n tập, đ&oacute;ng g&oacute;p kiến thức v&agrave; truy cập dữ liệu. Một thứ tương tự như thế d&agrave;nh cho robot kh&ocirc;ng hề tồn tại&rdquo;, Waibel ph&aacute;t biểu.</p>\r\n<p class=\"Normal\">Waibel nhận định con người sẽ hưởng nhiều lợi &iacute;ch nếu một robot c&oacute; thể tới một nơi m&agrave; ch&uacute;ng chưa từng thấy, truy cập RoboEarth để tham khảo th&ocirc;ng tin về những vật thể, nhiệm vụ cần l&agrave;m ở nơi đ&oacute;. Chẳng hạn khi tiến v&agrave;o một t&ograve;a nh&agrave; đổ v&igrave; động đất, robot c&oacute; thể truy cập RoboEarth để học c&aacute;ch sử dụng c&aacute;c thiết bị cứu hộ, sơ cứu nạn nh&acirc;n, di chuyển b&ecirc;n trong t&ograve;a nh&agrave;. Việc truy cập RoboEarth sẽ gi&uacute;p ch&uacute;ng tiết kiệm rất nhiều thời gian khi thực hiện c&aacute;c nhiệm vụ m&agrave; con người giao.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Minh Long</strong></p>', 36, 2925, 0, 1, NULL, NULL),
(618, 'Du thuyền bằng sọ dừa', 'Du-Thuyen-Bang-So-Dua', 'Chiếc du thuyền với chất liệu cốt là composite, ốp bên ngoài và các họa tiết trang trí bên trong đều được làm bằng sọ dừa, vừa được hạ thủy trên sông Đà Rằng ở Phú Yên.', 'du-thuyen-1-to.jpg', 22, '<h1 class=\"Title\">Du thuyền bằng sọ dừa</h1>\r\n<h2 class=\"Lead\">Chiếc du thuyền với chất liệu cốt l&agrave; composite, ốp b&ecirc;n ngo&agrave;i v&agrave; c&aacute;c họa tiết trang tr&iacute; b&ecirc;n trong đều được l&agrave;m bằng sọ dừa, vừa được hạ thủy tr&ecirc;n s&ocirc;ng Đ&agrave; Rằng ở Ph&uacute; Y&ecirc;n.</h2>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; chiếc du thuyền đầu ti&ecirc;n tại Ph&uacute; Y&ecirc;n, v&agrave; l&agrave; du thuyền đầu ti&ecirc;n tr&ecirc;n thế giới được l&agrave;m từ sọ dừa, mang t&ecirc;n Lạc Hồng 1.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/62/cb/du-thuyen-1-to.jpg\" border=\"1\" alt=\"Du thuyền Lạc Hồng 1 l&agrave;m bằng sọ dừa. Ảnh: Khoa Thy\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Du thuyền Lạc Hồng 1 l&agrave;m bằng sọ dừa. Ảnh: Khoa Thy</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Lạc Hồng 1 do C&ocirc;ng ty Vinashin Nha Trang thiết kế v&agrave; C&ocirc;ng ty Dịch vụ - Du lịch Lạc Hồng l&agrave;m chủ. Thuyền c&oacute; h&igrave;nh d&aacute;ng một con chim Lạc, d&agrave;i 25m, rộng 5m v&agrave; cao 6,5m (2 tầng phục vụ v&agrave; khoang m&aacute;y). Sức chứa 100 người khi chạy v&agrave; 120 người l&uacute;c dừng đậu.</p>\r\n<p class=\"Normal\">Hầu hết chi tiết trang tr&iacute; đều được thực hiện bằng h&agrave;ng ngh&igrave;n mảnh sọ dừa rất nhỏ gh&eacute;p lại.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/62/cb/du-thuyen-2-to.jpg\" border=\"1\" alt=\"C&aacute;c chi tiết trang tr&iacute; du thuyền đều từ sọ dừa. Ảnh: Khoa Thy\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c chi tiết trang tr&iacute; du thuyền đều từ sọ dừa. Ảnh: Khoa Thy</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&ocirc;ng tr&igrave;nh n&agrave;y nhằm hưởng ứng sự kiện &ldquo;Năm Du lịch quốc gia duy&ecirc;n hải Nam Trung bộ - Ph&uacute; Y&ecirc;n 2011: Du lịch biển đảo&rdquo;. Lạc Hồng 1 sẽ phục vụ du lịch dọc d&ograve;ng s&ocirc;ng Đ&agrave; Rằng.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Khoa Thy</strong></p>', 36, 878, 0, 1, NULL, NULL),
(619, '\'Cây ngửi bom\' giúp ngăn chặn khủng bố', '', 'Khi kẻ khủng bố mang bom vào sân bay, một cây gần hắn đột nhiên chuyển từ màu xanh sang màu trắng và tín hiệu cảnh báo được truyền tới các máy bộ đàm của cảnh sát.', 'plant.jpg', 22, '<h1 class=\"Title\">\'C&acirc;y ngửi bom\' gi&uacute;p ngăn chặn khủng bố</h1>\r\n<h2 class=\"Lead\">Khi kẻ khủng bố mang bom v&agrave;o s&acirc;n bay, một c&acirc;y gần hắn đột nhi&ecirc;n chuyển từ m&agrave;u xanh sang m&agrave;u trắng v&agrave; t&iacute;n hiệu cảnh b&aacute;o được truyền tới c&aacute;c m&aacute;y bộ đ&agrave;m của cảnh s&aacute;t.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5f/3a/plant.jpg\" alt=\"C&acirc;y phong lan trong một s&acirc;n bay.\" width=\"450\" height=\"347\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Phong lan v&agrave; một số loại c&acirc;y kh&aacute;c trong một s&acirc;n bay. Nếu đ&acirc;y l&agrave;\"c&acirc;y ngửi bom\", ch&uacute;ng sẽ chuyển th&agrave;nh m&agrave;u trắng khi ph&aacute;t hiện thuốc nổ TNT trong kh&ocirc;ng kh&iacute;. Ảnh:&nbsp;<em>Flickr</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Đ&oacute; kh&ocirc;ng phải l&agrave; một cảnh trong phim viễn tưởng, m&agrave; c&oacute; thể xảy ra ngo&agrave;i cuộc sống thực.&nbsp;<em>Fox News</em>&nbsp;đưa tin c&aacute;c nh&agrave; khoa học của Đại học Colorado, Mỹ t&aacute;c động tới c&acirc;y cối để ch&uacute;ng chuyển từ m&agrave;u xanh sang m&agrave;u trắng khi ph&aacute;t hiện một lượng nhỏ thuốc nổ TNT trong kh&ocirc;ng kh&iacute;. Kỹ thuật tạo \"c&acirc;y ngửi bom\", được c&ocirc;ng bố tr&ecirc;n tạp ch&iacute; khoa học&nbsp;<em>PloS One</em>&nbsp;v&agrave; do Bộ An ninh nội địa Mỹ t&agrave;i trợ, c&oacute; thể được ứng dụng tại c&aacute;c phi trường trong v&agrave;i năm nữa.</p>\r\n<p class=\"Normal\">&ldquo;&Yacute; tưởng tạo ra c&acirc;y c&oacute; khả năng ph&aacute;t hiện thuốc nổ tới từ thi&ecirc;n nhi&ecirc;n. C&acirc;y cối kh&ocirc;ng thể chạy hay lẩn trốn hiểm họa, v&igrave; thế ch&uacute;ng tạo ra những hệ thống phức tạp để bảo vệ bản th&acirc;n&rdquo;, gi&aacute;o sư June Medford, trưởng nh&oacute;m nghi&ecirc;n cứu của Đại học Colorado, ph&aacute;t biểu.</p>\r\n<p class=\"Normal\">Medford c&ugrave;ng c&aacute;c đồng nghiệp trong khoa Sinh học t&igrave;m ra c&aacute;ch t&aacute;c động tới c&aacute;c hệ thống tự vệ của thực vật để chất diệp lục biến mất khi ch&uacute;ng ph&aacute;t hiện thuốc nổ, khiến to&agrave;n bộ c&acirc;y chuyển từ m&agrave;u xanh sang m&agrave;u trắng. Ngo&agrave;i thuốc nổ thực vật c&ograve;n ph&aacute;t hiện được một số chất kh&aacute;c.</p>\r\n<p class=\"Normal\">Hiện tại nghi&ecirc;n cứu đang ở giai đoạn sơ khai n&ecirc;n c&acirc;y cần tới v&agrave;i giờ để ph&aacute;t hiện h&oacute;a chất, song Medford hy vọng thời gian đ&oacute; sẽ chỉ c&ograve;n v&agrave;i ph&uacute;t trong tương lai. C&acirc;y m&agrave; họ tạo ra c&oacute; độ nhạy đối với thuốc nổ gấp 100 lần trở l&ecirc;n so với những con ch&oacute; nghiệp vụ được đ&agrave;o tạo để t&igrave;m bom.</p>\r\n<p class=\"Normal\">Do những ứng dụng thực tế của &ldquo;c&acirc;y ngửi bom&rdquo;, Bộ Quốc ph&ograve;ng Mỹ quyết định t&agrave;i trợ cho nh&oacute;m Medford 7,9 triệu USD trong ba năm để họ tiếp tục nghi&ecirc;n cứu.</p>\r\n<p class=\"Normal\">&ldquo;C&acirc;y ngửi bom sẽ ph&aacute;t hiện chất nổ v&agrave; bảo vệ binh sĩ của ch&uacute;ng t&ocirc;i khỏi những quả bom được k&iacute;ch nổ từ xa&rdquo;, Linda Chrisey, một chuy&ecirc;n gia thuộc Cơ quan Nghi&ecirc;n cứu Hải qu&acirc;n Mỹ, ph&aacute;t biểu.</p>\r\n<p class=\"Normal\">Mục ti&ecirc;u xa hơn của Medford v&agrave; c&aacute;c đồng nghiệp l&agrave; t&aacute;c động v&agrave;o c&aacute;c hệ thống tự vệ của c&acirc;y để ch&uacute;ng ph&aacute;t hiện cả những chất g&acirc;y &ocirc; nhiễm trong kh&ocirc;ng kh&iacute;.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Minh Long</strong></p>', 36, 3, 0, 1, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `slug`, `summary`, `urlImage`, `users_id`, `content`, `type_id`, `qtyView`, `highLight`, `hide`, `created_at`, `updated_at`) VALUES
(620, 'Kính cứng hơn thép', 'Kinh-Cung-Hon-Thep', 'Các nhà khoa học Mỹ tạo ra loại kính cứng và bền hơn mọi loại vật liệu mà con người từng biết.', 'kinh.jpg', 22, '<h1 class=\"Title\">K&iacute;nh cứng hơn th&eacute;p</h1>\r\n<h2 class=\"Lead\">C&aacute;c nh&agrave; khoa học Mỹ tạo ra loại k&iacute;nh cứng v&agrave; bền hơn mọi loại vật liệu m&agrave; con người từng biết.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5e/1e/kinh.jpg\" alt=\"Ảnh minh họa: blogspot.com.\" width=\"450\" height=\"404\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ảnh minh họa:&nbsp;<em>blogspot.com</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">K&iacute;nh c&oacute; rất nhiều ưu điểm đối với con người. Ch&uacute;ng ta c&oacute; thể nh&igrave;n xuy&ecirc;n qua v&agrave; l&agrave;m sạch n&oacute; dễ d&agrave;ng. Vấn đề phiền to&aacute;i duy nhất đối với k&iacute;nh l&agrave; yếu v&agrave; dễ vỡ. Đ&oacute; l&agrave; l&yacute; do khiến c&aacute;c nh&agrave; khoa học của Viện C&ocirc;ng nghệ California v&agrave; Ph&ograve;ng th&iacute; nghiệm quốc gia Berkeley tại Mỹ theo đuổi ước mơ tạo ra loại k&iacute;nh cứng v&agrave; đ&agrave;n hồi hơn th&eacute;p,&nbsp;<em>Discovery News</em>&nbsp;cho biết.</p>\r\n<p class=\"Normal\">Tr&ecirc;n thực tế, loại k&iacute;nh chịu lực m&agrave; họ tạo ra c&oacute; độ bền lớn hơn tất cả vật liệu m&agrave; con người từng biết. Trong th&agrave;nh phần của n&oacute; c&oacute; Paladi, một kim loại c&oacute; độ cứng lớn để khắc chế t&iacute;nh chất gi&ograve;n cố hữu của thủy tinh. V&igrave; thế m&agrave; c&aacute;c chuy&ecirc;n gia gọi n&oacute; l&agrave; k&iacute;nh kim loại.</p>\r\n<p class=\"Normal\">Khi thử nghiệm, nh&oacute;m nghi&ecirc;n cứu đập k&iacute;nh bằng vật nặng v&agrave; nhận thấy n&oacute; kh&ocirc;ng nứt ho&agrave;n to&agrave;n khi họ gi&aacute;ng mạnh. Robert Richie, một th&agrave;nh vi&ecirc;n trong nh&oacute;m nghi&ecirc;n cứu, khẳng định loại k&iacute;nh mới cứng hơn th&eacute;p v&igrave; n&oacute; c&oacute; khả năng đ&agrave;n hồi tốt khi chịu lực t&aacute;c dụng. V&igrave; thế m&agrave; n&oacute; chỉ cong chứ kh&ocirc;ng vỡ.</p>\r\n<p class=\"Normal\">Phi&ecirc;n bản đầu ti&ecirc;n của k&iacute;nh kim loại chỉ l&agrave; v&agrave;i thanh k&iacute;nh c&oacute; đường k&iacute;nh một mm. Khi c&aacute;c chuy&ecirc;n gia của Viện C&ocirc;ng nghệ California cho th&ecirc;m bạc v&agrave;o hỗn hợp, đường k&iacute;nh của thanh tăng l&ecirc;n 6 mm. K&iacute;nh kim loại cần được l&agrave;m nguội thật nhanh để đảm bảo độ cứng v&agrave; đ&agrave;n hồi. V&igrave; thế m&agrave; đường k&iacute;nh của ch&uacute;ng kh&ocirc;ng thể lớn.</p>\r\n<p class=\"Normal\">Ritchie v&agrave; c&aacute;c đồng nghiệp tin rằng nghi&ecirc;n cứu của họ sẽ dẫn tới sự ra đời của nhiều loại k&iacute;nh c&oacute; độ bền cao hơn.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Minh Long</strong></p>', 36, 2580, 0, 1, NULL, NULL),
(621, 'Bắn hạ máy bay nhờ không khí', 'Ban-Ha-May-Bay-Nho-Khong-Khi', 'Hải quân Mỹ nghiên cứu thành công một loại súng phòng không có khả năng tiêu diệt máy bay nhờ không khí xung quanh.', 'felaser.jpg', 22, '<h1 class=\"Title\">Bắn hạ m&aacute;y bay nhờ kh&ocirc;ng kh&iacute;</h1>\r\n<h2 class=\"Lead\">Hải qu&acirc;n Mỹ nghi&ecirc;n cứu th&agrave;nh c&ocirc;ng một loại s&uacute;ng ph&ograve;ng kh&ocirc;ng c&oacute; khả năng ti&ecirc;u diệt m&aacute;y bay nhờ kh&ocirc;ng kh&iacute; xung quanh.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/5D/9C/felaser.jpg\" alt=\"\" width=\"450\" height=\"273\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những t&agrave;u chiến của hải qu&acirc;n Mỹ sẽ được trang bị s&uacute;ng ph&oacute;ng laser từ điện tử trong kh&ocirc;ng kh&iacute;. Ảnh:&nbsp;<em>Fox News</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><em>Fox News</em>&nbsp;cho biết, c&aacute;c nh&agrave; khoa học của hải qu&acirc;n Mỹ vừa thử nghiệm loại vũ kh&iacute; chống m&aacute;y bay thế hệ mới c&oacute; khả năng tạo ra tia laser từ c&aacute;c điện tử (electron) trong kh&ocirc;ng kh&iacute;. Những tia laser m&agrave; ch&uacute;ng tạo ra c&oacute; c&ocirc;ng suất cực mạnh (l&ecirc;n tới v&agrave;i triệu Watt).</p>\r\n<p class=\"Normal\">&ldquo;Thiết bị ph&oacute;ng tia laser hoạt động đ&uacute;ng như mong đợi của ch&uacute;ng t&ocirc;i&rdquo;, Dinh Nguyen - trưởng nh&oacute;m nghi&ecirc;n cứu thuộc chương tr&igrave;nh Free Electron Laser (FEL) tại Ph&ograve;ng th&iacute; nghiệm quốc gia Los Alamos, Mỹ - ph&aacute;t biểu.</p>\r\n<p class=\"Normal\">Nguyen n&oacute;i nh&oacute;m của &ocirc;ng hy vọng sẽ lập kỷ lục thế giới với loại vũ kh&iacute; laser mới. C&ocirc;ng nghệ FEL tạo ra những ch&ugrave;m laser si&ecirc;u mạnh bằng c&aacute;ch đẩy những luồng hạt điện tử qua nhiều từ trường. Th&ocirc;ng thường, khi tạo tia laser từ kh&iacute; gas, người ta phải d&ugrave;ng một số h&oacute;a chất. Kỹ thuật n&agrave;y sản nhiệt trong qu&aacute; tr&igrave;nh tạo tia laser, khiến c&ocirc;ng suất của tia laser giảm. Nhờ sử dụng điện tử, nh&oacute;m của Nguyen tr&aacute;nh được hiện tượng sản nhiệt trong qu&aacute; tr&igrave;nh tạo laser, nhờ đ&oacute; sức hủy diệt của tia laser tăng mạnh.</p>\r\n<p class=\"Normal\">Những tia laser được tạo ra từ điện tử trong kh&ocirc;ng kh&iacute; ph&aacute;t huy hiệu quả cao nhất đối với c&aacute;c hoạt động ph&ograve;ng kh&ocirc;ng, đặc biệt l&agrave; khi độ ẩm cao hoặc nhiều m&acirc;y. Nh&oacute;m nghi&ecirc;n cứu sẽ cố gắng tạo ra phi&ecirc;n bản mẫu ho&agrave;n chỉnh trước năm 2018. Phi&ecirc;n bản mẫu sẽ c&oacute; khả năng bắn mục ti&ecirc;u ở mọi độ cao, v&agrave;o mọi thời điểm.</p>\r\n<p class=\"Normal\">C&aacute;c t&agrave;u chiến ng&agrave;y c&agrave;ng trở n&ecirc;n yếu thế trước những t&ecirc;n lửa si&ecirc;u thanh bởi hệ thống ph&ograve;ng thủ của t&agrave;u chiến đi sau t&ecirc;n lửa về mặt c&ocirc;ng nghệ.</p>\r\n<p class=\"Normal\">&ldquo;FEL sẽ gi&uacute;p c&aacute;c t&agrave;u chiến Mỹ c&oacute; khả năng tự vệ gần như tức thời bằng tia laser trong mọi điều kiện hải dương tr&ecirc;n khắp thế giới&rdquo;, Quentin Saulter, người quản l&yacute; chương tr&igrave;nh FEL, nhận định.</p>\r\n<p class=\"Normal\">Ngo&agrave;i ra, theo Saulter, do c&aacute;c t&agrave;u chiến tương lai sẽ được trang bị động cơ đẩy điện tử n&ecirc;n vũ kh&iacute; FEL sẽ c&oacute; nguồn cung cấp điện tử dồi d&agrave;o.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Minh Long</strong></p>', 36, 1415, 0, 1, NULL, NULL),
(696, 'Bà bầu liên tiếp phạm tội', 'Ba-Bau-Lien-Tiep-Pham-Toi', 'Thấy ví căng đầy của ông hàng xóm đặt trên bàn, người đàn bà nhanh tay lấy trộm mang về nhà. Đốt hết giấy tờ bên trong, cô đưa tập tiền cho chồng.', 'Hai-Duong-250.jpg', 22, '<h1 class=\"Title\">B&agrave; bầu li&ecirc;n tiếp phạm tội</h1>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/66/16/Hai-Duong-250.jpg\" alt=\"\" width=\"250\" height=\"195\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Trần Thị Hải Đường đang mang bầu 7 th&aacute;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class=\"Lead\">Thấy v&iacute; căng đầy của &ocirc;ng h&agrave;ng x&oacute;m đặt tr&ecirc;n b&agrave;n, người đ&agrave;n b&agrave; nhanh tay lấy trộm mang về nh&agrave;. Đốt hết giấy tờ b&ecirc;n trong, c&ocirc; đưa tập tiền cho chồng.</h2>\r\n<p class=\"Normal\">C&ocirc;ng an th&agrave;nh phố Y&ecirc;n B&aacute;i vừa khởi tố người phụ nữ mang bầu 7 th&aacute;ng Trần Thị Hải Đường (33 tuổi, phường Nguyễn Th&aacute;i Học) về tội trộm cắp t&agrave;i sản. Mới chỉ hơn 20 ng&agrave;y trước, Đường bị t&ograve;a &aacute;n th&agrave;nh phố kết &aacute;n 3 năm t&ugrave; v&igrave; mua b&aacute;n ma t&uacute;y, song được tại ngoại chờ sinh nở.</p>\r\n<p class=\"Normal\">Theo kết quả x&aacute;c minh của c&ocirc;ng an, đầu th&aacute;ng 2 Đường sang nh&agrave; anh L&ecirc; Quốc H&ugrave;ng (h&agrave;ng x&oacute;m) thanh to&aacute;n tiền d&ugrave;ng nước sinh hoạt v&igrave; hai nh&agrave; sử dụng chung. Sau khi trả anh H&ugrave;ng hơn 150.000 đồng, thấy chiếc v&iacute; da căng đầy của chủ nh&agrave; để tr&ecirc;n b&agrave;n uống nước, người phụ nữ đ&atilde; nấn n&aacute; ở lại. Nh&acirc;n l&uacute;c anh H&ugrave;ng đi ra ph&iacute;a sau nh&agrave;, b&agrave; bầu nhanh tay lấy trộm.</p>\r\n<p class=\"Normal\">R&uacute;t gần 10 triệu đồng b&ecirc;n trong v&iacute;, Đường đưa chồng cất giữ. To&agrave;n bộ giấy tờ của anh H&ugrave;ng, c&ocirc; ta đem đốt sạch.</p>\r\n<p class=\"Normal\">Cảnh s&aacute;t cho biết, Đường đ&atilde; khai nhận to&agrave;n bộ h&agrave;nh vi. Người phụ nữ n&agrave;y kh&ocirc;ng nghề nghiệp, nghiện ma t&uacute;y.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tuệ L&acirc;m</strong></p>', 25, 2595, 0, 1, NULL, NULL),
(697, 'Cứu con trai, đôi vợ chồng bị chém chết', 'Cuu-Con-Trai,-Doi-Vo-Chong-Bi-Chem-Chet', 'Nhóm thanh niên xông vào nhà Cường \"đại náo\" khiến cậu và 5 người khác bị thương. Riêng cha mẹ Cường tử vong khi can ngăn đám côn đồ.', 'giet-nguoi.jpg', 22, '<h1 class=\"Title\">Cứu con trai, đ&ocirc;i vợ chồng bị ch&eacute;m chết</h1>\r\n<h2 class=\"Lead\">Nh&oacute;m thanh ni&ecirc;n x&ocirc;ng v&agrave;o nh&agrave; Cường \"đại n&aacute;o\" khiến cậu v&agrave; 5 người kh&aacute;c bị thương. Ri&ecirc;ng cha mẹ Cường tử vong khi can ngăn đ&aacute;m c&ocirc;n đồ.</h2>\r\n<p class=\"Normal\">Hiện, hai can phạm li&ecirc;n quan vụ &aacute;n mạng nghi&ecirc;m trọng khiến hai người chết, 6 người trọng thương đ&atilde; bị C&ocirc;ng an tỉnh Y&ecirc;n B&aacute;i khởi tố, tạm giam về tội giết người. Trong đ&oacute; c&oacute; chủ mưu Ho&agrave;ng Tuấn Anh, 23 tuổi.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/65/6c/giet-nguoi.jpg\" alt=\"\" width=\"350\" height=\"267\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ho&agrave;ng Tuấn Anh tr&uacute; tại khu vực ga Mậu A, huyện Văn Y&ecirc;n, Y&ecirc;n B&aacute;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Theo kết quả x&aacute;c minh, trưa 4/2 (mồng 2 Tết) Ngọc Tiến Cường (x&atilde; M&ocirc;ng Quy, Trấn Y&ecirc;n, Y&ecirc;n B&aacute;i) chơi xu&acirc;n với nh&oacute;m bạn tại khu vực cầu Khe th&igrave; gặp một c&ocirc; bạn học đi c&ugrave;ng Tuấn Anh v&agrave; 3 người kh&aacute;c. Cường tung khăn tr&ecirc;u bạn n&ecirc;n xảy ra va chạm với những thanh ni&ecirc;n đi c&ugrave;ng c&ocirc; g&aacute;i.</p>\r\n<p class=\"Normal\">L&aacute;t sau, nh&oacute;m bạn của c&ocirc; g&aacute;i mang hung kh&iacute; t&igrave;m đến nh&agrave; của Cường. Hai b&ecirc;n tiếp tục x&ocirc; x&aacute;t v&agrave; ẩu đả. Cuộc hỗn chiến giữa hơn 10 thanh ni&ecirc;n đ&atilde; khiến Cường bị đ&acirc;m tr&uacute;ng ngực. Những người v&agrave;o can ngăn cũng bị nh&oacute;m c&ocirc;n đồ \"xuống tay\". Trong số n&agrave;y, bố của Cường tử vong tại chỗ, mẹ chết tr&ecirc;n đường v&agrave;o bệnh viện. Cường c&ugrave;ng em trai v&agrave; 4 người kh&aacute;c bị trọng thương.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/65/6c/Cuong-tren-giuong-benh.jpg\" border=\"1\" alt=\"\" width=\"350\" height=\"269\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đến 11/2 sau nhiều ng&agrave;y điều trị tại bệnh viện, Cường vẫn chưa hay biết tin bố mẹ đ&atilde; qua đời.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trưởng c&ocirc;ng an x&atilde; M&ocirc;ng Quy Trần Văn Chung cho biết: &ldquo;Ở địa phương chưa c&oacute; vụ việc n&agrave;o d&atilde; man đến như vậy. Người l&agrave;ng, người x&atilde; với nhau m&agrave; h&agrave;nh xử kh&ocirc;ng kh&aacute;c trong phim x&atilde; hội đen&rdquo;.</p>\r\n<p class=\"Normal\">C&ocirc;ng an tỉnh Y&ecirc;n B&aacute;i đang tiếp tục truy bắt những người c&ograve;n lại li&ecirc;n quan vụ &aacute;n.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tuệ L&acirc;m</strong></p>', 25, 541, 0, 1, NULL, NULL),
(698, 'Giở trò bậy tại nhà nhân tình', 'Gio-Tro-Bay-Tai-Nha-Nhan-Tinh', 'Khi đến chơi nhà bác họ dịp cuối năm, thiếu nữ mới lớn đã bị Hoàng Thế Đoàn lôi vào giường giở trò đồi bại. Nạn nhân chạy về trong tinh thần hoảng loạn.', 'hiep-dam.jpg', 22, '<h1 class=\"Title\">Giở tr&ograve; bậy tại nh&agrave; nh&acirc;n t&igrave;nh</h1>\r\n<h2 class=\"Lead\">Khi đến chơi nh&agrave; b&aacute;c họ dịp cuối năm, thiếu nữ mới lớn đ&atilde; bị Ho&agrave;ng Thế Đo&agrave;n l&ocirc;i v&agrave;o giường giở tr&ograve; đồi bại. Nạn nh&acirc;n chạy về trong tinh thần hoảng loạn.</h2>\r\n<p class=\"Normal\">Ng&agrave;y 11/2, C&ocirc;ng an th&agrave;nh phố Y&ecirc;n B&aacute;i cho biết vừa k&yacute; quyết định khởi tố, tạm giam Ho&agrave;ng Thế Đo&agrave;n, 53 tuổi, để điều tra h&agrave;nh vi hiếp d&acirc;m trẻ em.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/65/4c/hiep-dam.jpg\" alt=\"\" width=\"250\" height=\"315\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nghi phạm Ho&agrave;ng Thế Đo&agrave;n.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Theo kết quả x&aacute;c minh, ng&agrave;y 31/1 c&ocirc; b&eacute; chưa tr&ograve;n 16 tuổi t&ecirc;n Trinh đến chơi nh&agrave; b&aacute;c họ ở c&ugrave;ng khu. Kh&ocirc;ng gặp người th&acirc;n, c&ocirc; b&eacute; chỉ thấy Đo&agrave;n (quan hệ t&igrave;nh cảm với chủ nh&agrave;) đang dọn dẹp... Nh&acirc;n l&uacute;c nh&agrave; vắng người, &ocirc;ng Đo&agrave;n đ&atilde; khống chế, hiếp d&acirc;m Trinh.</p>\r\n<p class=\"Normal\">Mẹ Trinh cho biết thấy con g&aacute;i hoảng sợ, kh&oacute;c l&oacute;c, m&aacute;u chảy ướt quần chạy về n&ecirc;n đ&atilde; gạn hỏi sự t&igrave;nh. \"T&ocirc;i sang nh&agrave; t&igrave;m Đo&agrave;n, &ocirc;ng ta định bỏ đi n&ecirc;n t&ocirc;i phải nhờ h&agrave;ng x&oacute;m giữ lại. Sau một l&uacute;c quanh co, &ocirc;ng ta nhận đ&atilde; l&agrave;m tr&ograve; bậy với đứa trẻ. Hắn c&ograve;n định tự s&aacute;t bằng dao v&agrave; đập đầu v&agrave;o tường, may những người c&oacute; mặt l&uacute;c đ&oacute; kịp ngăn lại, khống chế giao c&ocirc;ng an\".</p>\r\n<p class=\"Normal\">Theo người mẹ, Trinh kh&ocirc;ng đi học được do t&acirc;m sinh l&yacute; nhiều l&uacute;c kh&ocirc;ng b&igrave;nh thường. C&ograve;n &ocirc;ng Đo&agrave;n đ&atilde; c&oacute; vợ con nhưng c&oacute; quan hệ t&igrave;nh cảm với người phụ nữ kh&aacute;c.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tuệ L&acirc;m</strong></p>', 25, 2320, 0, 1, NULL, NULL),
(699, 'Giết đồng nghiệp thả trôi sông vì thiếu nợ 300.000 đồng', 'Giet-Dong-Nghiep-Tha-Troi-Song-Vi-Thieu-No-300.000-Dong', 'Sau khi sát hại đồng nghiệp của mình, Quân nhét xác anh Tài vào hai bao tải dứa rồi thả sông Hồng. Toàn bộ giấy tờ tùy thân cũng như tài sản của nạn nhân xấu số bị hung thủ tẩu tán.', 'sat-hai.jpg', 22, '<h1 class=\"Title\">Giết đồng nghiệp thả tr&ocirc;i s&ocirc;ng v&igrave; thiếu nợ 300.000 đồng</h1>\r\n<h2 class=\"Lead\">Sau khi s&aacute;t hại đồng nghiệp của m&igrave;nh, Qu&acirc;n nh&eacute;t x&aacute;c anh T&agrave;i v&agrave;o hai bao tải dứa rồi thả s&ocirc;ng Hồng. To&agrave;n bộ giấy tờ t&ugrave;y th&acirc;n cũng như t&agrave;i sản của nạn nh&acirc;n xấu số bị hung thủ tẩu t&aacute;n.</h2>\r\n<p class=\"Normal\">Chiều 9/2, sau nhiều ng&agrave;y điều tra, c&ocirc;ng an H&agrave; Nội đ&atilde; t&igrave;m thấy x&aacute;c được cho l&agrave; của nạn nh&acirc;n Nguyễn Hữu T&agrave;i (29 tuổi ở Hương Sơn, H&agrave; Tĩnh) trong vụ &aacute;n giết người, n&eacute;m s&ocirc;ng Hồng để phi tang.</p>\r\n<p class=\"Normal\">X&aacute;c nạn nh&acirc;n xấu số được ph&aacute;t hiện ở khu vực ven s&ocirc;ng Hồng, thuộc địa phận phường Cự Khối, đoạn giữa cầu Thanh Tr&igrave; v&agrave; cầu Vĩnh Tuy&nbsp;<span style=\"color: #000000;\">đựng trong chiếc bao tải mầu trắng, ch&acirc;n tay được buộc lại bằng sợi d&acirc;y điện.</span></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/63/b4/sat-hai.jpg\" alt=\"Qu&acirc;n tại cơ quan điều tra chiều 9/2.\" width=\"300\" height=\"395\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Qu&acirc;n tại cơ quan điều tra chiều 9/2.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Theo cơ quan điều tra, trước đ&oacute;, T&agrave;i c&oacute; điện về nh&agrave; th&ocirc;ng b&aacute;o sẽ về qu&ecirc; ăn Tết v&agrave;o ng&agrave;y 26 th&aacute;ng chạp. Tuy nhi&ecirc;n, sau v&agrave;i ng&agrave;y t&igrave;m kiếm kh&ocirc;ng thấy b&oacute;ng d&aacute;ng, chiều 4 Tết gia đ&igrave;nh đ&atilde;&nbsp;</span><span style=\"color: #000000;\"><span style=\"color: #000000;\">đến trụ sở cảnh s&aacute;t tr&igrave;nh b&aacute;o.</span></span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\"><span style=\"color: #000000;\">Ngay trong ng&agrave;y h&ocirc;m đ&oacute;, nam thanh ni&ecirc;n khai l&agrave; Trương Mạnh Qu&acirc;n (32 tuổi ở quận Hai B&agrave; Trưng), nh&acirc;n vi&ecirc;n kiểm ng&acirc;n Cục ph&aacute;t h&agrave;nh v&agrave; kho quỹ Ng&acirc;n h&agrave;ng nh&agrave; nước Việt Nam đ&atilde; đến trụ sở PC 45 đầu th&uacute;.</span></span></p>\r\n<p><span style=\"color: #000000;\">\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Theo lời khai v&agrave; t&agrave;i liệu ban đầu, cơ quan điều tra x&aacute;c định, chiều 28/1, sau bữa tất ni&ecirc;n với cơ quan, Qu&acirc;n hẹn anh T&agrave;i đến nh&agrave; để giải quyết nợ nần. Tr&ecirc;n s&acirc;n thượng, Qu&acirc;n hứa trả trước 200.000 đồng (số c&ograve;n lại 300.000 đồng sẽ trả sau) nhưng kh&ocirc;ng được T&agrave;i chấp nhận. Hai b&ecirc;n đ&atilde; xảy ra c&atilde;i cự.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Trong l&uacute;c x&ocirc; s&aacute;t, Qu&acirc;n lấy c&acirc;y x&agrave; c&agrave;y (d&agrave;i khoảng 60cm) đập v&agrave;o g&aacute;y đồng nghiệp khiến nạn nh&acirc;n gục ng&atilde;. Thấy T&agrave;i tỉnh dậy định cầm gỗ chống lại, Qu&acirc;n giằng v&agrave; li&ecirc;n tiếp đập v&agrave;o đầu khiến nạn nh&acirc;n chết tại chỗ.&nbsp;</span><span style=\"color: #000000;\">Sau khi x&aacute;c định T&agrave;i đ&atilde; chết, Qu&acirc;n&nbsp;</span><span style=\"color: #000000;\">d&ugrave;ng d&acirc;y điện tr&oacute;i ch&acirc;n tay người n&agrave;y rồi cho</span><span style=\"color: #000000;\">&nbsp;v&agrave;o 2 lượt bao tải rứa để tr&ecirc;n s&acirc;n thượng.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Cảnh s&aacute;t cho biết, kh&ocirc;ng chỉ lấy một khoản tiền, chiếc điện thoại c&ugrave;ng chiếc xe m&aacute;y của nạn nh&acirc;n, hung thủ c&ograve;n phi tang một số đồ c&aacute; nh&acirc;n của nạn nh&acirc;n xấu số ở nhiều nơi kh&aacute;c nhau.</span></p>\r\n<span style=\"color: #000000;\">\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Ngay trong đ&ecirc;m g&acirc;y &aacute;n, lợi dụng l&uacute;c vắng người, Qu&acirc;n thả x&aacute;c nạn nh&acirc;n từ s&acirc;n thượng tầng 3 xuống đất. Thi thể T&agrave;i được hung thủ chở đến cầu Vĩnh Tuy rồi thả tr&ocirc;i s&ocirc;ng Hồng.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Xong việc, Qu&acirc;n lấy xe m&aacute;y của nạn nh&acirc;n đi l&ograve;ng v&ograve;ng đến s&aacute;ng, rồi ra cơ quan l&agrave;m việc b&igrave;nh thường. Ng&agrave;y cuối c&ugrave;ng của th&aacute;ng 1, hung thủ mang xe m&aacute;y v&agrave; điện thoại của nạn nh&acirc;n b&aacute;n được gần 9 triệu đồng.</span></p>\r\n<p class=\"Normal\">Cơ quan đang điều tra mở rộng vụ &aacute;n.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><span style=\"color: #000000;\"><strong>Ho&agrave;ng Anh</strong></span></p>\r\n</span></span></p>', 25, 2847, 0, 1, NULL, NULL),
(700, 'Nam sinh nổ súng giết người tại vũ trường', 'Nam-Sinh-No-Sung-Giet-Nguoi-Tai-Vu-Truong', 'Cãi vã trong lúc đi xem ca nhạc trong vũ trường, Nhật (18 tuổi) dùng súng hoa cải bắn thẳng vào đối thủ khiến người này chết trên đường đi cấp cứu.', 'vu-truong.jpg', 22, '<h1 class=\"Title\">Nam sinh nổ s&uacute;ng giết người tại vũ trường</h1>\r\n<h2 class=\"Lead\"><strong>C&atilde;i v&atilde; trong l&uacute;c đi xem ca nhạc trong vũ trường, Nhật (18 tuổi) d&ugrave;ng s&uacute;ng hoa cải bắn thẳng v&agrave;o đối thủ khiến người n&agrave;y chết tr&ecirc;n đường đi cấp cứu.</strong></h2>\r\n<p class=\"Normal\">Vụ việc xảy ra tối 8/2 tại vũ trường Heaven. Vũ trường n&agrave;y nằm trong khu&ocirc;n vi&ecirc;n CLB Lao Động, TP Vinh (Nghệ An).</p>\r\n<p class=\"Normal\">Nạn nh&acirc;n bị bắn chết l&agrave; anh Nguyễn Tuấn Trung (28 tuổi) ở phường Hồng Sơn, TP Vinh. Hung thủ được x&aacute;c định l&agrave; Nguyễn Khắc Nhật (18 tuổi) học sinh lớp 11 một trường THPT ở Nghệ An.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/63/6c/vu-truong.jpg\" alt=\"Vũ trường nơi xảy ra sự việc.\" width=\"490\" height=\"364\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Vũ trường nơi xảy ra sự việc. Ảnh:&nbsp;<em>Nguy&ecirc;n Khoa.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Cảnh s&aacute;t cho biết, v&agrave;o thời điểm xảy ra sự việc, nh&oacute;m bạn của anh Trung v&agrave; Nhật c&ugrave;ng nhau xem ca nhạc ở vũ trường. M&acirc;u thuẫn xảy ra khi nh&oacute;m của Trung đứng che khuất tầm nh&igrave;n của nh&oacute;m Nhật.<br /><br />Sau một hồi c&atilde;i v&atilde;, một người trong nh&oacute;m anh Trung đ&atilde; n&eacute;m cốc thủy tinh v&agrave;o mặt một người bạn của Nhật khiến anh n&agrave;y chảy m&aacute;u. Thấy vậy, Nhật d&ugrave;ng khẩu s&uacute;ng dắt trong người bắn thẳng v&agrave;o Trung rồi bỏ ra ngo&agrave;i.</p>\r\n<p class=\"Normal\">Đưa đi cấp cứu nhưng Trung đ&atilde; tử vong. V&agrave;i giờ sau, Nhật cũng bị cảnh s&aacute;t bắt khi đang trốn ở một kh&aacute;ch sạn tr&ecirc;n địa b&agrave;n th&agrave;nh phố Vinh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/63/6c/vu-truong-1.jpg\" alt=\"Cảnh s&aacute;t phong tỏa hiện trường.\" width=\"490\" height=\"368\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cảnh s&aacute;t điều tra ph&iacute;a trong vũ trường. Ảnh:&nbsp;<em>Nguy&ecirc;n Khoa.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Tại hiện trường, cảnh s&aacute;t thu được một khẩu s&uacute;ng c&ugrave;ng với ba vỏ đạn hoa cải.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nguy&ecirc;n Khoa</strong></p>', 25, 2166, 0, 1, NULL, NULL),
(701, 'Kẻ tham gia vụ bắn người trước trung tâm Parkson đầu thú', 'Ke-Tham-Gia-Vu-Ban-Nguoi-Truoc-Trung-Tam-Parkson-Dau-Thu', 'Sau 5 ngày Tết, Lê Trọng Nghĩa - người bị truy nã trong vụ bắn chết người trước khu mua sắm Parkson - đã ra cơ quan điều tra trình diện.', 'sat-thu.jpg', 22, '<h1 class=\"Title\">Kẻ tham gia vụ bắn người trước trung t&acirc;m Parkson đầu th&uacute;</h1>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/62/ef/sat-thu.jpg\" alt=\"L&ecirc; Trọng Nghĩa.\" width=\"250\" height=\"195\" align=\"left\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">L&ecirc; Trọng Nghĩa.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class=\"Lead\">Sau 5 ng&agrave;y Tết, L&ecirc; Trọng Nghĩa - người bị truy n&atilde; trong vụ bắn chết người trước khu mua sắm Parkson - đ&atilde; ra cơ quan điều tra tr&igrave;nh diện.</h2>\r\n<p class=\"Normal\">L&ecirc; Trọng Nghĩa (30 tuổi, quận Cầu Giấy) bị x&aacute;c định li&ecirc;n quan vụ nổ s&uacute;ng bắn chết anh Nguyễn Minh Dương v&agrave;o rạng s&aacute;ng 26/1 tại ng&atilde; tư Th&aacute;i H&agrave; - Ch&ugrave;a Bộc, H&agrave; Nội. Sau gần nửa th&aacute;ng bỏ trốn, tối 7/2 (m&ugrave;ng 5 Tết) Nghĩa đến Ph&ograve;ng cảnh s&aacute;t điều tra tội phạm về trật tự x&atilde; hội, C&ocirc;ng an H&agrave; Nội đầu th&uacute;.</p>\r\n<p class=\"Normal\">Theo kết quả x&aacute;c minh, anh Dương c&oacute; vay chị Ho&agrave;ng Hải H&agrave; (31 tuổi ở Ninh B&igrave;nh) 30 triệu đồng. Sau nhiều lần đ&ograve;i tiền người đ&agrave;n &ocirc;ng 30 tuổi n&agrave;y, chủ nợ chỉ thu về được 5 triệu đồng.</p>\r\n<p class=\"Normal\">Ng&agrave;y 25/1, đến gặp anh Dương tại một kh&aacute;ch sạn ở phố Th&aacute;i H&agrave; nhưng kh&ocirc;ng được trả tiền, H&agrave; kể lại c&acirc;u chuyện cho Nghĩa. Nghĩa liền gọi điện thoại cho Dương.</p>\r\n<p class=\"Normal\">Rạng s&aacute;ng 26/1, Nghĩa rủ Lưu Quang Thạc (31 tuổi ở quận Hai B&agrave; Trưng) đến ng&atilde; tư Th&aacute;i H&agrave; - Ch&ugrave;a Bộc để \"n&oacute;i chuyện\" với Dương. Tại điểm hẹn trước khu trung t&acirc;m mua sắm Parkson, Thạc đ&atilde; bắn s&uacute;ng v&agrave;o ngực phải khiến anh Dương tử vong.</p>\r\n<p class=\"Normal\">Kh&aacute;m người nạn nh&acirc;n, cảnh s&aacute;t thu một khẩu K54 v&agrave; 7 vi&ecirc;n đạn. Trong ph&ograve;ng kh&aacute;ch sạn của Dương, trinh s&aacute;t tiếp tục ph&aacute;t hiện s&uacute;ng Col c&ugrave;ng 19 vi&ecirc;n đạn.</p>\r\n<p class=\"Normal\">Ng&agrave;y 29/1, C&ocirc;ng an Đống Đa ph&aacute;t lệnh truy n&atilde; Nghĩa v&agrave; Thạc.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>H&agrave; Anh</strong></p>', 25, 1155, 0, 1, NULL, NULL),
(702, 'Hại vợ vì bị ngăn cản uống rượu', 'Hai-Vo-Vi-Bi-Ngan-Can-Uong-Ruou', 'Án mạng kinh hoàng xảy ra tại ki-ốt bán quần áo vào ngày giáp Tết sau khi người vợ cằn nhằn chồng liên tục uống rượu suốt buổi tối.', 'dao-to.jpg', 22, '<h1 class=\"Title\">Hại vợ v&igrave; bị ngăn cản uống rượu</h1>\r\n<h2 class=\"Lead\">&Aacute;n mạng kinh ho&agrave;ng xảy ra tại ki-ốt b&aacute;n quần &aacute;o v&agrave;o ng&agrave;y gi&aacute;p Tết sau khi người vợ cằn nhằn chồng li&ecirc;n tục uống rượu suốt buổi tối.</h2>\r\n<p class=\"Normal\">Ng&agrave;y 29/1, C&ocirc;ng an tỉnh Quảng Ninh bắt giữ Nguyễn Văn Cừ (28 tuổi) - người chồng g&acirc;y ra c&aacute;i chết của c&ocirc; vợ Trần Thị Li&ecirc;n.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/61/2d/dao-to.jpg\" alt=\"\" width=\"300\" height=\"338\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cừ cầm dao quắm ch&eacute;m vợ nhiều nh&aacute;t.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Tối 28/1 thấy Cừ &ocirc;m khư khư chai rượu uống li&ecirc;n hồi, chị Li&ecirc;n can ngăn. N&oacute;i m&atilde;i kh&ocirc;ng được, người vợ bế con 8 th&aacute;ng tuổi đi ngủ trước. Bực tức v&igrave; bị cằn nhằn, cộng với men rượu đ&atilde; ngấm, Cừ x&aacute;ch con dao quắm ở g&oacute;c nh&agrave; lao v&agrave;o qu&aacute;t mắng v&agrave; ch&eacute;m vợ.</p>\r\n<p class=\"Normal\">Bị thương, chị Li&ecirc;n cố chạy tho&aacute;t ra ngo&agrave;i nhưng cửa nh&agrave; đ&atilde; kh&oacute;a&hellip; Biết vợ bị thương, Cừ để mặc l&ecirc;n giường đi ngủ. Khoảng 4h s&aacute;ng h&ocirc;m sau, anh ta tỉnh dậy, gọi h&agrave;ng x&oacute;m đưa chị Li&ecirc;n đi cấp cứu. Nạn nh&acirc;n tử vong trong s&aacute;ng 29/1 do vết thương qu&aacute; nặng.</p>\r\n<p class=\"Normal\">Chị Li&ecirc;n v&agrave; Cừ đều c&ugrave;ng qu&ecirc; ở huyện Thuận Th&agrave;nh, Bắc Ninh, thu&ecirc; một ki-ốt b&aacute;n quần &aacute;o tại km 12, x&atilde; Hải Đ&ocirc;ng, M&oacute;ng C&aacute;i được khoảng 2 năm.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Theo&nbsp;<em>Quảng Ninh</em>)</p>', 25, 2028, 0, 1, NULL, NULL),
(703, 'Nữ sinh về quê ăn Tết bị tài xế làm nhục', 'Nu-Sinh-Ve-Que-An-Tet-Bi-Tai-Xe-Lam-Nhuc', 'Ngày 28/1 công an đã bắt lái xe Lê Ngọc Hùng để điều tra tố cáo hiếp dâm một nữ sinh viên khi cô này đi nhờ xe tải từ Đà Nẵng về Thanh Hóa ăn Tết.', 'hiep-dam (1).jpg', 22, '<h1 class=\"Title\">Nữ sinh về qu&ecirc; ăn Tết bị t&agrave;i xế l&agrave;m nhục</h1>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5f/a2/hiep-dam.jpg\" alt=\"\" width=\"250\" height=\"195\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">L&aacute;i xe L&ecirc; Ngọc H&ugrave;ng, 29 tuổi, tr&uacute; tại Y&ecirc;n B&aacute;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class=\"Lead\">Ng&agrave;y 28/1 c&ocirc;ng an đ&atilde; bắt l&aacute;i xe L&ecirc; Ngọc H&ugrave;ng để điều tra tố c&aacute;o hiếp d&acirc;m một nữ sinh vi&ecirc;n khi c&ocirc; n&agrave;y đi nhờ xe tải từ Đ&agrave; Nẵng về Thanh H&oacute;a ăn Tết.</h2>\r\n<p class=\"Normal\">Khoảng 19h ng&agrave;y 27/1, tại Đ&agrave; Nẵng 2 nữ sinh vi&ecirc;n đ&oacute;n &ocirc;t&ocirc; tải để về qu&ecirc; Thanh H&oacute;a, tr&ecirc;n xe c&oacute; 2 t&agrave;i xế. Đến địa phận th&ocirc;n H&agrave; Thanh, x&atilde; Gio Ch&acirc;u, huyện Gio Linh, tỉnh Quảng Trị, một c&ocirc; bị l&aacute;i xe đẩy xuống đường. Người c&ograve;n lại bị t&agrave;i xế H&ugrave;ng giữ lại trong xe để hiếp d&acirc;m.</p>\r\n<p class=\"Normal\">Sau khi giở tr&ograve; đồi bại, anh ta đẩy c&ocirc; n&agrave;y xuống đường. Nạn nh&acirc;n được người d&acirc;n xung quanh gi&uacute;p đỡ đưa đến C&ocirc;ng an huyện Gio Linh tr&igrave;nh b&aacute;o sự việc.</p>\r\n<p class=\"Normal\">S&aacute;ng 28/1, C&ocirc;ng an huyện Gio Linh phối hợp với C&ocirc;ng an tỉnh Quảng B&igrave;nh v&acirc;y bắt được chiếc xe tải tr&ecirc;n khi chạy đến thị trấn Ho&agrave;n L&atilde;o, huyện Bố Trạch, Quảng B&igrave;nh. Tại cơ quan điều tra, t&agrave;i xế 29 tuổi đ&atilde; thừa nhận h&agrave;nh vi hiếp d&acirc;m.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Theo&nbsp;<em>N&ocirc;ng Nghiệp Việt Nam</em>)</p>', 25, 1971, 0, 1, NULL, NULL),
(704, 'Nghi phạm sát hại chủ nhà trọ bị bắt', 'Nghi-Pham-Sat-Hai-Chu-Nha-Tro-Bi-Bat', 'Chiều 24/1, Công an Hà Nội đã bắt Vũ Trọng Tâm - nghi phạm gây ra cái chết thương tâm cho ông chủ nhà trọ Lê Văn Lưu (57 tuổi).', 'giet.jpg', 22, '<h1 class=\"Title\">Nghi phạm s&aacute;t hại chủ nh&agrave; trọ bị bắt</h1>\r\n<h2 class=\"Lead\">Chiều 24/1, C&ocirc;ng an H&agrave; Nội đ&atilde; bắt Vũ Trọng T&acirc;m - nghi phạm g&acirc;y ra c&aacute;i chết thương t&acirc;m cho &ocirc;ng chủ nh&agrave; trọ L&ecirc; Văn Lưu (57 tuổi).</h2>\r\n<p class=\"Normal\">Trước đ&oacute; 3 ng&agrave;y, x&aacute;c nạn nh&acirc;n được ph&aacute;t hiện ở b&ecirc;n tường khu nh&agrave; trọ tại phường Y&ecirc;n H&ograve;a, Cầu Giấy, phần ngực v&agrave; đầu c&oacute; hơn chục vết thương do vật nhọn t&aacute;c động. Trong t&uacute;i quần của &ocirc;ng Lưu, cơ quan điều tra t&igrave;m thấy hơn 100 triệu đồng.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/5C/6C/giet.jpg\" alt=\"T&acirc;m tại cơ quan điều tra chiều 24/1. Ảnh: H&agrave; Anh.\" width=\"300\" height=\"429\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">T&acirc;m tại cơ quan điều tra chiều 24/1. Ảnh:&nbsp;<em>H&agrave; Anh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Kh&aacute;m nghiệm hiện trường, cảnh s&aacute;t t&igrave;m thấy ở b&atilde;i cỏ c&aacute;ch nh&agrave; nạn nh&acirc;n c&oacute; con dao nhọn, một chiếc kh&oacute;a h&igrave;nh chữ U c&oacute; dấu vết m&aacute;u. Lần theo manh mối, người thu&ecirc; trọ t&ecirc;n T&acirc;m, 51 tuổi, h&agrave;nh nghề l&aacute;i xe &ocirc;m bị đưa v&agrave;o \"tầm ngắm\" v&agrave; bị bắt tại một nh&agrave; nghỉ ở huyện Gia L&acirc;m.</p>\r\n<p class=\"Normal\">Bước đầu, tại cơ quan điều tra T&acirc;m khai tối 20/1 khi đi uống rượu về th&igrave; bị &ocirc;ng Lưu bắt gặp. Biết kh&aacute;ch thu&ecirc; trọ hay cờ bạc, chiếc xe m&aacute;y thường ng&agrave;y hay đi đ&atilde; \"cầm cố\" n&ecirc;n chủ nh&agrave; trọ mắng v&agrave; dọa đuổi.</p>\r\n<p class=\"normal\">Tức giận, T&acirc;m lấy chiếc kh&oacute;a h&igrave;nh chữ U đập một nh&aacute;t v&agrave;o g&aacute;y chủ nh&agrave;. Thấy nạn nh&acirc;n vẫn thở, &ocirc;ng ta tiếp tục ra tay...</p>\r\n<p class=\"Normal\">\"Qua dấu vết ch&uacute;ng t&ocirc;i đ&atilde; x&aacute;c định hung thủ phải l&agrave; người quen biết với &ocirc;ng Lưu. Người lạ kh&ocirc;ng thể v&agrave;o được đ&acirc;y v&igrave; &ocirc;ng chủ nh&agrave; trọ c&oacute; nu&ocirc;i ch&oacute;...\", một trinh s&aacute;t n&oacute;i.</p>\r\n<p class=\"normal\">Theo C&ocirc;ng an H&agrave; Nội, sau khi g&acirc;y &aacute;n T&acirc;m đ&atilde; th&aacute;o sim của nạn nh&acirc;n vứt phi tang. Hơn 20 năm trước, người n&agrave;y từng trộm cắp hơn chục triệu đồng của c&ocirc;ng ty ở Y&ecirc;n B&aacute;i rồi bỏ trốn xuống H&agrave; Nội.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>H&agrave; Anh</strong></p>', 25, 2832, 0, 1, NULL, NULL),
(705, 'Nhóm thanh niên giết người ở quán karaoke ra đầu thú', 'Nhom-Thanh-Nien-Giet-Nguoi-O-Quan-Karaoke-Ra-Dau-Thu', '6 thanh niên liên quan vụ hành hung đến chết một khách hát karaoke tại quận Đống Đa, Hà Nội, vừa ra đầu thú. ', 'dung.jpg', 22, '<h1 class=\"Title\">Nh&oacute;m thanh ni&ecirc;n giết người ở qu&aacute;n karaoke ra đầu th&uacute;</h1>\r\n<h2 class=\"Lead\">6 thanh ni&ecirc;n li&ecirc;n quan vụ h&agrave;nh hung đến chết một kh&aacute;ch h&aacute;t karaoke tại quận Đống Đa, H&agrave; Nội, vừa ra đầu th&uacute;.&nbsp;</h2>\r\n<p class=\"Normal\">Nh&oacute;m thanh ni&ecirc;n gồm: Nguyễn Mạnh H&ugrave;ng (15 tuổi, từng đi trường gi&aacute;o dưỡng), Nguyễn Quang Huy (16 tuổi, c&oacute; một tiền sự), Phạm Ho&agrave;ng Giang (16 tuổi), Trần Nam Sơn (18 tuổi), Nguyễn Xu&acirc;n Trường (17 tuổi, từng c&oacute; tiền &aacute;n) v&agrave; Phạm Anh Dũng đều ở quận Ho&agrave;ng Mai v&agrave; Hai B&agrave; Trưng, H&agrave; Nội.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/5A/62/dung.jpg\" alt=\"Phạm Anh Dũng tại cơ quan điều tra.\" width=\"490\" height=\"378\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Phạm Anh Dũng tại cơ quan điều tra.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Tại cơ quan điều tra, H&ugrave;ng, Huy, Giang, Trường v&agrave; Sơn khai đang chơi bi-a ở đường Trương Định th&igrave; nhận điện thoại của Anh Dũng rủ l&ecirc;n một qu&aacute;n h&aacute;t ở tr&ecirc;n phố Nguyễn Khuyến. Khi cả hội đến nơi, Anh Dũng n&oacute;i muốn đ&aacute;nh anh Lưu Đ&igrave;nh Dũng (21 tuổi, ở quận Thanh Xu&acirc;n) đang ngồi trong ph&ograve;ng v&igrave; thấy \"ngứa mắt\".</p>\r\n<p class=\"Normal\">5 thanh ni&ecirc;n n&agrave;y đ&atilde; hưởng ứng v&agrave; gom tiền mua 6 con dao. C&oacute; hung kh&iacute; trong tay, H&ugrave;ng vung dao đe dọa mọi người đang ngồi trong ph&ograve;ng h&aacute;t rồi cả nh&oacute;m lao v&agrave;o đ&aacute;nh anh Đ&igrave;nh Dũng... Anh Dũng cầm dao đ&acirc;m tr&uacute;ng bụng người n&agrave;y. Huy x&ocirc;ng tiếp v&agrave;o g&acirc;y &aacute;n khiến nạn nh&acirc;n gục ng&atilde; b&ecirc;n vũng m&aacute;u, chết tr&ecirc;n đường đi cấp cứu. Trong vụ &aacute;n mạng n&agrave;y, nh&oacute;m c&ocirc;n đồ c&ograve;n ch&eacute;m trọng thương một người kh&aacute;c.</p>\r\n<p class=\"Normal\">Sau khi g&acirc;y &aacute;n, cả bọn tẩu tho&aacute;t. Ch&uacute;ng vứt hung kh&iacute; dưới một hố ga gần s&acirc;n b&oacute;ng Thịnh Liệt.</p>\r\n<p class=\"Normal\">Cảnh s&aacute;t cho biết m&acirc;u thuẫn giữa Anh Dũng v&agrave; nạn nh&acirc;n Đ&igrave;nh Dũng bắt nguồn v&agrave;o tối 16/1 khi cả hai c&ugrave;ng tới dự tiệc sinh nhật của Nguyễn Thu Hường (15 tuổi) tổ chức tại qu&aacute;n karaoke n&agrave;y. Trong l&uacute;c h&aacute;t, thấy chủ bữa tiệc l&agrave; Hường v&agrave; Đ&igrave;nh Dũng &ocirc;m nhau Anh Dũng đ&atilde; rất tức tối.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>H&agrave; Anh</strong></p>', 25, 1202, 0, 1, NULL, NULL),
(706, 'Vạch mặt hung thủ hại hàng xóm', 'Vach-Mat-Hung-Thu-Hai-Hang-Xom', 'Sau khi hại chết bà lão hàng xóm, Tám lấy đôi hoa tai mang về giấu kỹ trong hòm cá nhân. Chân tướng hung thủ bị lật tẩy sau ít tiếng điều tra.', 'an.jpg', 22, '<h1 class=\"Title\">Vạch mặt hung thủ hại h&agrave;ng x&oacute;m</h1>\r\n<h2 class=\"Lead\">Sau khi hại chết b&agrave; l&atilde;o h&agrave;ng x&oacute;m, T&aacute;m lấy đ&ocirc;i hoa tai mang về giấu kỹ trong h&ograve;m c&aacute; nh&acirc;n. Ch&acirc;n tướng hung thủ bị lật tẩy sau &iacute;t tiếng điều tra.</h2>\r\n<p class=\"Normal\">Ng&agrave;y 14/1, Cơ quan điều tra c&ocirc;ng an tỉnh Bắc Giang cho biết đ&atilde; khởi tố v&agrave; bắt tạm giam H&agrave; Văn T&aacute;m (24 tuổi ở huyện Lục Ngan) về h&agrave;nh vi giết người, cướp t&agrave;i sản.</p>\r\n<p class=\"Normal\">Theo cơ quan điều tra, tối v&agrave;i ng&agrave;y trước đ&oacute;, lợi dụng b&agrave; Leo Thị T&acirc;m (63 tuổi) ở nh&agrave; một m&igrave;nh, hung thủ cầm ống tu&yacute;p sắt mai phục trong vườn. Thấy b&agrave; l&atilde;o vừa tắm giặt đi ra s&acirc;n, t&ecirc;n n&agrave;y lao từ đằng sau phang đến khi nạn nh&acirc;n nằm bất động. Sau khi g&acirc;y &aacute;n, T&aacute;m th&aacute;o đ&ocirc;i hoa tai của b&agrave; T&acirc;m mang về giấu ở h&ograve;m c&aacute; nh&acirc;n.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/56/66/an.jpg\" border=\"1\" alt=\"C&aacute;c điều tra vi&ecirc;n lấy lời khai hung thủ.\" width=\"490\" height=\"362\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c điều tra vi&ecirc;n lấy lời khai hung thủ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&ocirc;ng an x&aacute;c định hung thủ phải l&agrave; người quen biết v&agrave; nam thanh ni&ecirc;n 24 tuổi sống cạnh nh&agrave; nạn nh&acirc;n đ&atilde; được triệu tập l&ecirc;n UBND x&atilde;. Tại đ&acirc;y, người n&agrave;y lu&ocirc;n đưa ra những l&yacute; do, dẫn chứng chứng minh m&igrave;nh v&ocirc; can trước c&aacute;i chết của b&agrave; l&atilde;o.</p>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, sau v&agrave;i tiếng biết kh&ocirc;ng qua được mặt c&aacute;c điều tra vi&ecirc;n, T&aacute;m đ&atilde; thừa nhận h&agrave;nh vi của m&igrave;nh. Hung thủ khai do vợ mới sinh con nhỏ, kinh tế gia đ&igrave;nh eo hẹp n&ecirc;n v&agrave;i ng&agrave;y trước khi g&acirc;y &aacute;n c&oacute; đi l&agrave;m đồng ph&aacute;t hiện b&agrave; T&acirc;m c&oacute; đeo đ&ocirc;i khuy&ecirc;n tai bằng v&agrave;ng đ&atilde; nảy sinh &yacute; đồ cướp.</p>\r\n<p class=\"Normal\">Cảnh s&aacute;t cho hay, T&aacute;m mồ c&ocirc;i cha mẹ từ nhỏ. Được sự cưu mang của của người th&acirc;n t&ecirc;n n&agrave;y đ&atilde; ăn học hết THPT, đ&atilde; x&acirc;y dựng gia đ&igrave;nh v&agrave; c&oacute; con nhỏ.</p>\r\n<p class=\"Normal\">Sau 9 tiếng kể từ khi nhận được điện b&aacute;o, C&ocirc;ng an tỉnh Bắc Giang đ&atilde; điều tra r&otilde;. Vụ &aacute;n được kh&aacute;m ph&aacute; trong đợt cao điểm tấn c&ocirc;ng trấn &aacute;p tội phạm, bảo vệ Tết Nguy&ecirc;n đ&aacute;n được C&ocirc;ng an tỉnh ph&aacute;t động từ 4/1 đến 28/2.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đặng Nam - Phi H&ugrave;ng</strong></p>', 25, 2785, 0, 1, NULL, NULL),
(887, '\'Người đàn bà thép\' 9 lần vô địch cử tạ thế giới', '', 'Sue Hollands, 59 tuổi, được mô tả là \"người đàn bà thép\", hay \"bà mẹ khỏe nhất thế giới\" sau khi đoạt chức vô địch cử tạ thế giới ở lứa tuổi trên 50 lần thứ 9 liên tiếp.', 'Sue-hollands-2.jpg', 22, '<h1 class=\"Title\">\'Người đ&agrave;n b&agrave; th&eacute;p\' 9 lần v&ocirc; địch cử tạ thế giới</h1>\r\n<h2 class=\"Lead\">Sue Hollands, 59 tuổi, được m&ocirc; tả l&agrave; \"người đ&agrave;n b&agrave; th&eacute;p\", hay \"b&agrave; mẹ khỏe nhất thế giới\" sau khi đoạt chức v&ocirc; địch cử tạ thế giới ở lứa tuổi tr&ecirc;n 50 lần thứ 9 li&ecirc;n tiếp.</h2>\r\n<p class=\"Normal\">Ở giải v&ocirc; địch cử tạ thế giới 2010 - World Masters Powerlifting Championship - diễn ra ở Pilsen, CH Czech h&ocirc;m 14/10, b&agrave; Hollands đoạt chức v&ocirc; địch hạng c&acirc;n 90 kg. Lần thứ năm li&ecirc;n tiếp b&agrave; bước l&ecirc;n bục nhận HC v&agrave;ng của hạng c&acirc;n n&agrave;y, sau 4 lần li&ecirc;n tiếp được vinh danh ở hạng c&acirc;n 82,5 kg.</p>\r\n<p class=\"Normal\">\"Nếu d&ugrave;ng từ b&agrave; mẹ khỏe nhất thế giới l&agrave; kh&ocirc;ng thật sự ch&iacute;nh x&aacute;c, th&igrave; Hollands xứng đ&aacute;ng l&agrave; b&agrave; mẹ khỏe nhất thế giới trong độ tuổi 50-59\", tờ&nbsp;<em>Sport Mail</em>&nbsp;b&igrave;nh luận sau khi chứng kiến b&agrave; nhẹ nh&agrave;ng nhấc bổng một người đ&agrave;n &ocirc;ng nặng gần 102 kg. Mức c&acirc;n nặng n&agrave;y chỉ xấp xỉ nửa mức tạ 199,76 kg (khoảng 400 pound Anh) m&agrave; Hollands cử th&agrave;nh c&ocirc;ng khi đăng quang ở Czech.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"1\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/22/BE/Sue-hollands-2.jpg\" alt=\"Holland nhẹ nh&agrave;ng nhấc bổng một người đ&agrave;n &ocirc;ng to v&agrave; nặng hơn cả b&agrave;.\" width=\"348\" height=\"506\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Holland nhẹ nh&agrave;ng nhấc bổng một người đ&agrave;n &ocirc;ng to v&agrave; nặng hơn cả b&agrave;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Th&agrave;nh c&ocirc;ng cứ tuần tự đến với Hollands v&agrave; khiến b&agrave; phải ngỡ ng&agrave;ng với ch&iacute;nh bản th&acirc;n. Thời trẻ Hollands chẳng bao giờ h&igrave;nh dung b&agrave; sẽ trở th&agrave;nh một vận động vi&ecirc;n cử tạ nghiệp dư ở c&aacute;i tuổi m&agrave; những phụ nữ kh&aacute;c chỉ d&agrave;nh thời gian để bế ch&aacute;u, hoặc vui th&uacute; điền vi&ecirc;n. V&agrave; hồi xưa ấy, người phụ nữ v&ugrave;ng Dewsbury, Leeds n&agrave;y đương nhi&ecirc;n cũng chẳng hứng th&uacute; g&igrave; với m&ocirc;n cử tạ.</p>\r\n<p class=\"normal\">Nhưng một chuyến thăm t&igrave;nh cờ với người bạn hay đi nh&agrave; thờ c&ugrave;ng tới ph&ograve;ng tập thể h&igrave;nh Steelman Powerlifting Club c&aacute;ch đ&acirc;y 13 năm đ&atilde; l&agrave;m Hollands thay đổi quan niệm về m&ocirc;n thể thao tưởng chỉ d&agrave;nh cho ph&aacute;i mạnh n&agrave;y. Sau khi thử v&agrave;i động t&aacute;c, b&agrave; mẹ ba con v&agrave; l&agrave; một gi&aacute;o vi&ecirc;n tiểu học n&agrave;y nhận ra rằng cử tạ chẳng đến mức kh&ocirc; khan như b&agrave; vẫn nghĩ. Những cuộc n&oacute;i chuyện th&uacute; vị với Barrie Nelson, cựu lực sĩ nổi tiếng v&agrave; l&agrave; chủ ph&ograve;ng tập khi đ&oacute;, khiến Hollands c&agrave;ng th&ecirc;m th&iacute;ch th&uacute; v&agrave; lao v&agrave;o tập luyện cử tạ.</p>\r\n<p class=\"Normal\">Cũng ch&iacute;nh Barrie Nelson (&ocirc;ng mới qua đời năm ngo&aacute;i) đ&atilde; nhận ra khả năng tiềm t&agrave;ng của Hollands v&agrave; thuyết phục b&agrave; tập luyện để đi thi đấu. \"&Ocirc;ng ấy b&agrave;y cho t&ocirc;i c&aacute;ch thực hiện c&aacute;c động t&aacute;c một c&aacute;ch ch&iacute;nh x&aacute;c. Nelson l&agrave; một HLV rất nhiệt t&igrave;nh v&agrave; l&agrave; một nh&acirc;n c&aacute;ch lớn. &Ocirc;ng ấy kh&iacute;ch lệ t&ocirc;i bằng c&aacute;ch đặt c&acirc;u hỏi tại sao cứ phải l&agrave; một người Nga mới c&oacute; thể v&ocirc; địch thế giới v&agrave; giữ c&aacute;c kỷ lục n&agrave;y nọ, tại sao t&ocirc;i, một phụ nữ từ Dewsbury, lại kh&ocirc;ng thể?\", Hollands nhớ lại.</p>\r\n<p class=\"Normal\">Nelson cũng truyền cho Barrie kh&aacute;t vọng chinh phục v&agrave; suy nghĩ kh&ocirc;ng bao giờ tự h&agrave;i l&ograve;ng với những g&igrave; đ&atilde; hoặc đang c&oacute;. \"Tập luyện với &ocirc;ng ấy gi&uacute;p t&ocirc;i c&oacute; th&oacute;i quen lu&ocirc;n muốn đạt th&agrave;nh t&iacute;ch tốt hơn. Đ&oacute; l&agrave; l&yacute; do khiến mức tạ tốt nhất của t&ocirc;i tăng l&ecirc;n theo từng năm v&agrave; th&agrave;nh t&iacute;ch hiện nay cao hơn nhiều so với khi t&ocirc;i bắt thi đấu c&aacute;ch đ&acirc;y 10 năm\", b&agrave; chia sẻ.</p>\r\n<p class=\"Normal\">Hollands cũng khẳng định th&agrave;nh t&iacute;ch v&agrave; phong độ hiện tại của b&agrave; chỉ đơn giản l&agrave; kết quả của việc luyện tập chăm chỉ v&agrave; quyết t&acirc;m, chứ kh&ocirc;ng phải bởi c&aacute;c thức uống đặc biệt, chế độ ăn ki&ecirc;ng hay bất kỳ yếu tố bổ trợ n&agrave;o kh&aacute;c từ b&ecirc;n ngo&agrave;i. \"T&ocirc;i chỉ ăn một c&aacute;ch hợp l&yacute;, chứ chưa tới mức phải ki&ecirc;ng khem g&igrave;\", b&agrave; cho biết. \"Mọi người cứ nghĩ để đạt th&agrave;nh t&iacute;ch như t&ocirc;i đ&atilde; c&oacute;, t&ocirc;i hẳn phải l&agrave; một phụ nữ cơ bắp cuồn cuộn. Tất nhi&ecirc;n t&ocirc;i cũng c&oacute; ch&uacute;t cơ bắp, nhưng kh&ocirc;ng tới mức như c&aacute;c vận động vi&ecirc;n thể h&igrave;nh nữ. T&ocirc;i cũng chỉ l&agrave; một phụ nữ b&igrave;nh thường th&ocirc;i\".</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"1\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/22/BE/sue.jpg\" alt=\"\" width=\"490\" height=\"346\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hollands kh&ocirc;ng c&oacute; b&iacute; quyết g&igrave; đặc biệt ngo&agrave;i tập luyện ki&ecirc;n tr&igrave;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Chồng của Holllands, &ocirc;ng Tony, 66 tuổi v&agrave; c&oacute; thể h&igrave;nh rất mảnh dẻ, chẳng những kh&ocirc;ng phản đối, m&agrave; c&ograve;n ủng hộ mạnh mẽ khi biết vợ muốn thi đấu cử tạ. 10 năm trước, &ocirc;ng c&ograve;n b&iacute; mật l&aacute;i chiếc Fiat Tipo c&agrave; t&agrave;ng đi hơn 1600 km để sang Czech cổ vũ khi b&agrave; Hollands lần đầu tham dự giải v&ocirc; địch thế giới. Mới đ&acirc;y, &ocirc;ng lại b&iacute; mật thực hiện h&agrave;nh tr&igrave;nh ấy, nhưng bằng xe bus, để c&oacute; mặt tại Pilsen chứng kiến khoảnh khắc b&agrave; vợ chiến thắng v&agrave; đoạt HC v&agrave;ng. Tony rất tự h&agrave;o trước những kỳ t&iacute;ch của Hollands.</p>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, kh&ocirc;ng phải tất cả mọi thứ đều l&agrave; m&agrave;u hồng. Cử tạ ở lứa tuổi 50-59 kh&ocirc;ng nằm trong nội dung thi đấu Olympic, v&agrave; v&igrave; thế Hollands kh&ocirc;ng nhận được một xu tiền t&agrave;i trợ hay tiền thưởng, bất chấp bảng th&agrave;nh t&iacute;ch đồ sộ của b&agrave;. B&agrave; thậm ch&iacute; c&ograve;n phải tr&iacute;ch từ những đồng lương hưu để mua sắm trang phục thi đấu v&agrave; tự trang trải chi ph&iacute; trong c&aacute;c chuyến xa nh&agrave; thi đấu. \"T&ocirc;i sẽ cố gắng tiếp tục cho đến chừng n&agrave;o c&oacute; thể, cả về thể lực, lẫn tiền bạc\", Hollands thở d&agrave;i.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Minh Kha</strong></p>', 23, 3, 0, 1, NULL, NULL);
